(window.webpackJsonp=window.webpackJsonp||[]).push([[2126],{2481:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-836-phong-thanh-đai-sup"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-836-phong-thanh-đai-sup"}},[n._v("#")]),n._v(" Chương 836: Phong Thánh Đài sụp")]),n._v(" "),i("hr"),n._v(" "),i("h2",[n._v(" Chương 836 Phong Thánh Đài sụp ")]),i("br"),i("br"),n._v("“Ngươi muốn hiểu như vậy, ta liền thực không có biện pháp...”"),i("br"),i("br"),n._v("Thấy đối phương mỗi lần đều thiêu thứ, giải thích thế nào như thế nào sai, Trương Huyền nhíu mày: “Tất cả mọi người là danh sư, điểm đến là dừng là được, ta đều nhận sai, không cần phải hùng hổ dọa người đi!”"),i("br"),i("br"),n._v("“Láo xược!”"),i("br"),i("br"),n._v("“Như thế nào nói chuyện với Ngũ Sư?”"),i("br"),i("br"),n._v("“Từ đâu tới Dã Tiểu Tử, thật sự là không biết trời cao đất rộng!”"),i("br"),i("br"),n._v("Nghe được lời của hắn, chung quanh một mảnh xôn xao, tất cả mọi người nhanh nổ tung."),i("br"),i("br"),n._v("Nghe xong lớp của Ngũ Sư, cùng với hắn có nửa sư chi nghị, một mình ngươi quy nhất cảnh tiểu tử, không khiêm tốn nghe thì thôi, rõ ràng còn nói ra lời này, tưởng muốn chết sao?"),i("br"),i("br"),n._v("“Bất quá để cho ngươi qua tới giảng giải mà thôi, tại sao là hủng hổ dọa người? Biết là biết, không biết thì là không biết! Giả vờ giả vịt, ra vẻ hiểu biết, đây là danh sư nên có thái độ?”"),i("br"),i("br"),n._v("Ánh mắt như điện, Ngũ Sư lúc trước một bước."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Thất tinh danh sư vượt qua Thánh Vực nhị trọng lực lượng lập tức vung rơi xuống dưới, như là thực chất, đấu đá bốn phía, như nước thủy triều tấn công tới, làm cho người ta hít thở không thông."),i("br"),i("br"),n._v("Làm như nghiên cứu phong Thánh Tâm được Lão Đồ Cổ, đối với Phong Thánh Đài có cảm tình đặc biệt, rất không nhìn nổi vãn bối mạo phạm, gia hỏa này từ khi mình mở mới giảng, liền các loại biểu tình cổ quái, càng là cười nhạo, để cho hắn cũng nhịn không được nữa."),i("br"),i("br"),n._v("Miệt thị hắn, không coi vào đâu, tại Khổng Sư phong thánh địa phương cũng như thế lỗ mãng... Nếu học sinh của hắn, chỉ sợ trực tiếp đuổi học rồi!"),i("br"),i("br"),n._v("Nguyên nhân chính là như thế, thất tinh danh sư mới đúng một cái tứ tinh danh sư, lớn tiếng rầy, cũng không phải là không để ý thân phận, càng không phải là đồ ngốc, mà là muốn để cho tất cả mọi người biết, cái gì là lễ, đối với Khổng Sư tôn trọng lễ!"),i("br"),i("br"),n._v("Bằng không thì, mỗi người đều như vậy, sư Đạo Tôn nghiêm ở đâu?"),i("br"),i("br"),n._v("“Ra vẻ hiểu biết?” Thiên Đạo chân khí một chuyến, tất cả áp lực, tan biến tại vô hình, Trương Huyền giơ lên mắt thấy hướng tên trước mắt, nhịn không được lắc đầu."),i("br"),i("br"),n._v("Ngươi giảng sai rồi, ta đều lười nói, mũ chụp đến thật nhiều."),i("br"),i("br"),n._v("Tuy rằng phiền muộn, bất quá, hắn cũng không phải là một ưa thích người gây chuyện, đành phải ôm quyền: “Được, là tại hạ không hiểu, cử chỉ thất lễ chỗ, xin lượng thứ!”"),i("br"),i("br"),n._v("Hiểu cùng không hiểu, trong nội tâm minh bạch là tốt rồi, không cần phải tranh chấp."),i("br"),i("br"),n._v("Đối phương ý tưởng gì, hắn không muốn để ý tới, nhưng mà cho dù ngươi là tưởng muốn giết gà dọa khỉ, đi tìm người khác, đừng tìm chính mình a!"),i("br"),i("br"),n._v("“Không hiểu nhưng ở chỗ này ăn nói bừa bãi, thật sự là xấu hổ làm người sư!”"),i("br"),i("br"),n._v("Hừ một tiếng, Ngũ Sư hai tay chắp sau lưng, mang theo thất tinh danh sư đặc hữu khí thế: “Ta không muốn nhìn thấy nữa như thế lỗ mãng chi nhân, chính mình đem danh sư phục cởi, xuống núi đi!”"),i("br"),i("br"),n._v("“Cởi danh sư phục? Xuống núi?” Trương Huyền sầm mặt lại."),i("br"),i("br"),n._v("Cởi danh sư phục, đuổi hạ sơn phong, đây là vũ nhục cực lớn rồi."),i("br"),i("br"),n._v("Một khi truyền đi, chính mình danh sư chức nghiệp, chỉ sợ đều muốn bởi vậy chôn vùi."),i("br"),i("br"),n._v("Mình cũng lùi lại nhiều lần rồi, gia hỏa này rõ ràng còn tưởng như thế bức bách, chẳng lẽ lại thật sự coi chính mình trẻ tuổi, dễ khi dễ?"),i("br"),i("br"),n._v("Tưởng muốn uy hiếp mọi người, cũng không cần cầm ta đi?"),i("br"),i("br"),n._v("Chẳng lẽ ta liền lớn lên như vậy làm người ta ghét, ai thấy thậm chí nghĩ giẫm hô mấy cước?"),i("br"),i("br"),n._v("Là một cái mặt giễu cợt, ai cũng muốn dạy dỗ vài câu?"),i("br"),i("br"),n._v("“Không sai!” Lạnh lùng nhìn qua, Ngũ Sư nói: “Để cho ta tự mình đuổi ngươi đi, cũng không phải là chỉ cởi danh sư phục đơn giản như vậy...”"),i("br"),i("br"),n._v("Thấy đối phương thái độ chăm chú, không giống làm trò đùa, Trương Huyền tròng mắt hơi híp, ngẩng đầu lên: “Cho ngươi mặt mũi có phải không? Nói chó má không phải, sai lầm chồng chất, chừa cho ngươi mặt mũi, xin lỗi vạch trần, còn ở nơi này giả bộ cái gì đầu to Sói?”"),i("br"),i("br"),n._v("Đều thừa nhận sai lầm, còn không thuận không dung, như thế nào, không phải muốn ta làm mặt vẽ mặt, mới sảng khoái?"),i("br"),i("br"),n._v("Vậy thì tốt, ta liền để cho ngươi đánh... Ngươi đánh chính là di chuyển sao?"),i("br"),i("br"),n._v("“Ngươi nói cái gì?”"),i("br"),i("br"),n._v("Không có nghĩ đến cái này quy nhất cảnh tiểu tử, dám nói như vậy, Ngũ Sư mặt biến sắc được tái nhợt."),i("br"),i("br"),n._v("“Nói cái gì ngươi không nghe thấy? Khổng Sư lưu lại tâm đắc, là vì nhắc nhở, đốc thúc hậu bối, ngươi xuyên tạc ý tứ không nói, còn ở nơi này ăn nói bừa bãi, đầu độc mọi người, không có chửi mắng ngươi cũng không tệ rồi, cùng ta giả vờ tỏi, giả bộ cái gì? Tỏ ra ngươi rất lợi hại phải không?”"),i("br"),i("br"),n._v("Lông mi nhếch lên, Trương Huyền hừ lạnh."),i("br"),i("br"),n._v("Vốn không muốn nhiều lời, ít xuất hiện là được rồi, không dứt đúng không?"),i("br"),i("br"),n._v("Nể mặt ngươi, thật đúng là đã cho ta nghe không hiểu?"),i("br"),i("br"),n._v("“Láo xược!” Không có nghĩ tới tên này một chút mặt mũi cũng không cho, Ngũ Sư tức giận sắp bạo tạc nổ tung."),i("br"),i("br"),n._v("“Láo xược? Rốt cuộc là ai láo xược? Khổng Sư, lưu lại lòng này được, vốn là vì khảo nghiệm hậu bối, một cùng sở hữu chín nơi sai lầm, còn Phong Thánh Học Hội hội trưởng, các ngươi nghiên cứu nhiều năm như vậy, nghiên cứu cái đồ chơi gì? Cũng biết sai lầm này ở đâu?”"),i("br"),i("br"),n._v("Trương Huyền không có chút nào khách khí."),i("br"),i("br"),n._v("Nếu như mở miệng, vậy sẽ phải phiến đối phương biết đau mới được!"),i("br"),i("br"),n._v("“Chín nơi sai lầm?”"),i("br"),i("br"),n._v("“Khổng Sư tại sao có thể có sai lầm? Quả thực vớ vẩn nhất thiên hạ!”"),i("br"),i("br"),n._v("“Nói vớ nói vẩn...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe nói như thế, không riêng Ngũ Sư khiếp sợ, đã liền chung quanh rất nhiều tu luyện giả, cũng tất cả đều biến sắc, nhịn không được lên tiếng quát lớn."),i("br"),i("br"),n._v("Nói Khổng Sư phạm sai lầm, chẳng khác nào nói với bọn hắn trời sập giống nhau, ai dám tin tưởng?"),i("br"),i("br"),n._v("“Không tin? Vậy thì tốt, ta hiện tại liền nói cho các ngươi một chút này chín nơi sai lầm ở đâu!”"),i("br"),i("br"),n._v("Chẳng muốn cùng đối phương phí miệng, Trương Huyền tiến về phía trước một bước, đi vào vách đá trước mặt, ngón tay chỉ một cái, một đạo chân khí bắn ra, như là quang bút, tìm được nơi thứ nhất sai lầm: “Đây là nơi thứ nhất, ‘yên tĩnh mà không lo, táo mà ý loạn. Bằng chân như vại, chọn Tĩnh Di chi. Tung không tiến tiếp chi lực, cũng có thể Phá chi...’ Lực lượng cùng tinh thần, muốn làm dung hợp làm một, mới có thể yên tĩnh mà thản nhiên, hữu lực vô thần, như là thánh thú, có cậy mạnh, không gì đặc biệt kỳ ngộ, không có khả năng tiến lên. Hữu thần vô lực, là không muốn...”"),i("br"),i("br"),n._v("“Khổng Sư lưu lại cái này lời nói, là muốn cho hậu nhân phát hiện huyền bí trong đó, không nghĩ viển vông, không man được... Mà vị Ngũ Sư này, nhưng kể cho các ngươi thành, chỉ cần tâm tình đầy đủ, không cần lực lượng cũng có thể đột phá... Lợi hại a, đến, đột phá cái cho ta nhìn một chút? Đột phá không đi ra, có tin ta hay không đánh chết ngươi?”"),i("br"),i("br"),n._v("“Ngươi...” Ngũ Sư phát triển sắc mặt đỏ lên."),i("br"),i("br"),n._v("“Nơi thứ hai, ‘sợ hãi ở thiên địa, thần mờ mịt, tâm ưu tư, vẫn còn lực áp vai, phương đắc đi về phía trước...’ Bổn ý nói là, người phải hiểu được sợ hãi, mới có thể cảm giác như đồng lực lượng áp trên bả vai, mới có thể tiến bộ, luôn luôn tự cao tự đại, chỉ biết trì trệ không tiến.”"),i("br"),n._v("“Những lời này, đối với vừa mới bắt đầu tu hành Võ Giả, không có bất kỳ sai lầm, nhưng mà tu luyện tới Thánh Vực, nghịch thiên, nếu như đơn thuần sợ hãi, còn thế nào phát triển?”"),i("br"),i("br"),n._v("“Cường giả chân chính, thần ngăn cản Sát Thần, phật ngăn giết phật, chỉ có không úy kỵ hết thảy tín niệm, mới có thể đột phá như uống nước, thăng cấp như ăn cơm!”"),i("br"),i("br"),n._v("“Nơi thứ ba...”"),i("br"),i("br"),n._v("“Nơi thứ bốn...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Đứng ở trước vách núi, Trương Huyền thanh âm như đao, mỗi một câu đều trực chỉ bản này tâm đắc khuyết điểm, tuy rằng đều là rất nhỏ chỗ thiếu hụt, nhưng nghe đến trong tai mọi người, tất cả đều sắc mặt ngẩn ngơ, nhịn không được run."),i("br"),i("br"),n._v("Trên vách đá văn tự, bọn hắn không thể không hoài nghi tới, chỉ là một nghĩ đến là Khổng Sư lưu lại, liền tự nhiên mà vậy hướng tốt phương hướng suy nghĩ."),i("br"),i("br"),n._v("Giờ phút này bị người thanh niên này ở trước mặt vạch trần, tất cả đều nguyên một đám khuôn mặt trắng bệch, trong lòng tựa như Phiên Giang Đảo Hải... Chẳng lẽ, Khổng Sư thật sự sai rồi?"),i("br"),i("br"),n._v("Chỉ là... Khổng Sư, làm sao sẽ sai!"),i("br"),i("br"),n._v("Hắn thế nhưng là muôn đời chi sư..."),i("br"),i("br"),n._v("Cách đó không xa Ngũ Sư càng là ngây tại chỗ."),i("br"),i("br"),n._v("Thân là thất tinh danh sư, đối phương nói có không sai lầm, vẫn có thể phân biệt, hơn nữa đắm chìm tại nơi này tâm đắc nhiều năm, đối phương vừa nói, lập tức cho hắn cực đại trùng kích, tựa hồ nhiều năm qua kiên trì lý niệm lập tức sụp đổ."),i("br"),i("br"),n._v("“... Đây là nơi thứ chín sai lầm!”"),i("br"),i("br"),n._v("Thấy mọi người tuy rằng không nói chuyện, nhưng đều không khác mấy tin phục lời của hắn, Trương Huyền Nhất cổ tác khí, đem cuối cùng một chỗ chỗ thiếu hụt cũng nói ra."),i("br"),i("br"),n._v("Ô... Ô... Ô... N... G!"),i("br"),i("br"),n._v("Nương theo tiếng nói chấm dứt, trên vách đá văn tự, đột nhiên bộc phát ra tia sáng chói mắt, tựa như nghe hiểu lời của hắn, lúc trước hắn nói chín nơi sai lầm, một hồi nhúc nhích, chín chữ to bay ra, lơ lửng giữa không trung, chiếu rọi bốn phương."),i("br"),i("br"),n._v("“Đại thiện, tin hết sư, không bằng không sư!”"),i("br"),i("br"),n._v("Không có này chín chữ, tâm đắc chín nơi sai lầm, lập tức phát sinh biến hóa, trở nên hoàn mỹ vô khuyết, lại không một chỗ sai lầm."),i("br"),i("br"),n._v("“Này là... Khổng Sư tán thành?”"),i("br"),i("br"),n._v("“Hắn đã nhận được Khổng Sư tán thành?”"),i("br"),i("br"),n._v("“Không phải là Khổng Sư phạm sai lầm, mà là... Khổng Sư lưu cho hậu nhân khảo nghiệm!”"),i("br"),i("br"),n._v("“Đáng tiếc chúng ta đều không nhìn ra, đã liền Phong Thánh Học Hội cũng không còn hiểu rõ...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nhìn đến đây, mọi người ở đâu vẫn không rõ, tất cả đều thân thể cứng đờ, kìm lòng không được quỳ rạp xuống đất."),i("br"),i("br"),n._v("Người thanh niên này nói, liền Khổng Sư đều tán thành, nói rõ chính xác không thể nghi ngờ!"),i("br"),i("br"),n._v("Lúc trước mọi người nghi vấn, Ngũ Sư trào phúng, giờ phút này toàn bộ đã biến thành trò cười."),i("br"),i("br"),n._v("“Đây, chuyện này... Nghiên cứu nhiều năm như vậy đều là sai?”"),i("br"),i("br"),n._v("Cùng mọi người khiếp sợ bất đồng, đám người Ngũ Sư tất cả đều sắc mặt trắng nhợt, không kềm hãm được lui về phía sau mấy bước."),i("br"),i("br"),n._v("Vương sư, Tôn thị cũng khuôn mặt trắng bệch, thất hồn lạc phách, không thể tin được ánh mắt của chính mình cùng lỗ tai."),i("br"),i("br"),n._v("Lời của Trương Huyền, bọn hắn đều có thể phân biệt rõ thật giả, rồi hãy nói... Khổng Sư cũng thừa nhận!"),i("br"),i("br"),n._v("Lưu lại khảo nghiệm, rõ ràng như thế, nhiều năm như vậy nhưng cũng không phát hiện, còn tưởng là thành chính xác nghiên cứu..."),i("br"),i("br"),n._v("Phong Thánh Học Hội, nhiều như vậy danh sư, nhiều lần thảo luận, ngày đêm nghiên cứu... Từ đã cho là khắc sâu hiểu ý tứ của Khổng Sư, bây giờ mới biết..."),i("br"),i("br"),n._v("Quả thực chính là một chuyện cười!"),i("br"),i("br"),n._v("Răng rắc!"),i("br"),i("br"),n._v("Chín chữ to xuất hiện, Vô Địch khí tức cường đại, trấn áp mọi người nói không ra lời, tâm trì thần lay động, tựa như lâm vào đặc thù ý cảnh, không kiềm chế được."),i("br"),i("br"),n._v("Sau lưng vách đá tức thì một hồi kịch liệt lắc lư, tựa hồ hoàn thành nào đó sứ mạng, xuất hiện vết rách."),i("br"),i("br"),n._v("Vết rách càng lúc càng lớn, từ ngón cái rộng hẹp, rất nhanh biến đến mức có thể dung nạp trưởng thành, ngay sau đó “Ầm!” Một tiếng, từ đó nổ tung, đá vụn nhao nhao rơi xuống, kích thích vô số bụi mù."),i("br"),i("br"),n._v("Nhất thiên 3, 324 chữ Khổng Sư tâm đắc, thời gian nháy con mắt bể bột phấn."),i("br"),i("br"),n._v("“Ni mã...”"),i("br"),i("br"),n._v("Sắc mặt trắng nhợt, thân thể nhoáng một cái, Trương Huyền chỉ cảm thấy trước mắt tối om, thiếu chút nữa không có khóc lên."),i("br"),i("br"),n._v("Hắn biết bản này tâm đắc có sai lầm, thật không nghĩ đến, chỉ ra sai lầm, sẽ để cho vách đá sụp đổ."),i("br"),i("br"),n._v("Ngươi bể như thế lừng lẫy, tiêu sái như vậy... Ta làm sao bây giờ?"),i("br"),i("br"),n._v("Trước mắt này ô ép một chút một mảng lớn danh sư, tận mắt thấy ta “hành hung”, sẽ đem ta đang sống đánh chết đấy..."),i("br"),i("br"),n._v("Có muốn hay không như vậy hại?"),i("br"),i("br"),n._v("“Ừ?”"),i("br"),i("br"),n._v("Đang tại bốn phía loạn chuyển, định tìm Khổng Sư phong thánh chân chính điểm Lạc Nhược Hi, nghe được nổ vang, lại càng hoảng sợ, thân thể nhoáng một cái, kiểu thuấn di đến đến bên cạnh."),i("br"),i("br"),n._v("Ngay sau đó liền thấy đứng ở trước vách núi, vẻ mặt mờ mịt, khóe miệng liên tục co giật Trương Huyền."),i("br"),i("br"),n._v("Lông mi nhảy loạn, Lạc Nhược Hi con mắt tối sầm."),i("br"),i("br"),n._v("Ta chỉ đã đi ra một hồi a!"),i("br"),i("br"),n._v("Ngươi, ngươi... Lại đã làm nên trò gì?"),i("br"),i("br"),n._v("Lần này như thế nào liền Phong Thánh Đài, đều làm phế đi?"),i("br"),i("br"),n._v("(Nhìn rất ở thêm nói, nói vị Ngũ Sư này cưỡng ép mất chỉ số thông minh, kỳ thật, rất nhiều người là chưa thấy qua Lão Học Cứu, ta đã thấy. Rõ ràng rất có học vấn, nhưng tiếng người nghe không hiểu, cảm thấy ghi văn học mạng chính là không làm việc đàng hoàng, chính là tai họa Thanh Thiếu Niên. Tận mắt nhìn đến, các ngươi đã biết rõ, Ngũ Sư loại người này, có khối người. Ngoài ra, còn có mọi người nói danh sư xấu vấn đề, ta gần nhất sẽ đem mình ở đại học tự mình trải qua viết ra gởi đến công chúng số, về một vị thầy, làm gương sáng cho người khác chưa hẳn đều là lòng dạ rộng rãi người tốt. Có trùng hợp, nhưng sự thật thường thường so với còn muốn trùng hợp. Cuối cùng, mọi người xem thoải mái là được rồi, không cần phải xoắn xuýt.) )!!"),i("br"),i("br"),n._v("Convert by: TruyenCuaTui (cầu chia sẻ)"),i("br"),i("br"),i("br"),i("br"),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-844"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-846"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);