(window.webpackJsonp=window.webpackJsonp||[]).push([[213],{567:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1169-my-nhan-đi-tam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1169-my-nhan-đi-tam"}},[n._v("#")]),n._v(" Chương 1169: Mỹ nhân đi tắm")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1169: Mỹ nhân đi tắm"),t("br"),t("br"),n._v("Vô Bi lão nhân nhìn rất cẩn thận, vẽ cũng rất cẩn thận, Khổng sư hư tượng xuất hiện vị trí, mười phần rõ ràng, có điều, cũng không xác định tại nào đó một chỗ, mà là một cái phạm vi."),t("br"),t("br"),n._v("Rất mau tới đến trước mặt."),t("br"),t("br"),n._v("Là một cái khe nứt to lớn, tràn đầy hoa râm sắc tảng đá."),t("br"),t("br"),n._v("Loại này tảng đá mười phần hiếm thấy, bình thường còn chưa bao giờ thấy qua."),t("br"),t("br"),n._v("Trương Huyền đang định tiến vào bên trong, tìm kiếm vị trí cụ thể, chỉ thấy trên không Khổng sư hư tượng lắc lư một cái, “Hô!” Thoáng cái biến mất không thấy hình bóng."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Sầm mặt lại."),t("br"),t("br"),n._v("Còn tưởng rằng có thể duy trì thêm một đoạn thời gian, hiện tại xem ra, còn là nghĩ quá đơn giản."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, bức họa này hư ảnh, hẳn là có thể một mực xuất hiện, nhưng... Nguyên bản tác phẩm hội họa bị cứ thế mà cắt mỏng, như thật như ảo ý cảnh, cũng liền tồn tại không được bao lâu."),t("br"),t("br"),n._v("Lại thêm hơn hai nghìn năm thời gian tiêu diệt, có thể kiên trì lâu như vậy, đã coi như là không tệ."),t("br"),t("br"),n._v("“Quên đi, biết tại đây cái vết nứt cũng tốt, cũng không biết ngọn núi này... Là cái gì!”"),t("br"),t("br"),n._v("Biết Khổng sư hư ảnh, xuất hiện tại đây cái vết nứt, về sau gặp được ngọn núi này thời điểm, hoàn toàn có thể tới tìm kiếm."),t("br"),t("br"),n._v("Đương nhiên... Điều kiện tiên quyết là nhận ra ngọn núi này là cái gì, lại tại vị trí nào."),t("br"),t("br"),n._v("Chí ít, hắn đã gặp tất cả sơn mạch, còn không có cùng cái này đồng dạng."),t("br"),t("br"),n._v("“Vị kia mặt nạ đồng xanh, một lòng muốn phá vỡ phong ấn, thậm chí không tiếc hủy đi một cái bát cảnh tác phẩm hội họa, có phải hay không là... Biết mây trắng phía dưới, sẽ có Khổng sư hư ảnh?”"),t("br"),t("br"),n._v("Đang nghĩ hoàn toàn kích hoạt linh tính, rời khỏi nơi này, Trương Huyền đột nhiên trong lòng hơi động."),t("br"),t("br"),n._v("Cái này mặt nạ đồng xanh, không dám đem bộ mặt thật gặp người, bản thân liền khó hiểu không nói, càng không tiếc phá hư trân quý như thế tác phẩm, đều muốn nhìn một chút phong ấn lại mặt đến cùng lưu giữ cái gì..."),t("br"),t("br"),n._v("Chẳng lẽ, đã trước thời hạn biết một chút chuyện?"),t("br"),t("br"),n._v("“Mặc kệ hắn có biết hay không, nhưng nếu như mở ra phong ấn, phát hiện Khổng sư hư ảnh không thấy, khẳng định sẽ có lòng nghi ngờ!”"),t("br"),t("br"),n._v("Trong lòng trầm tư."),t("br"),t("br"),n._v("Mặc dù không nhìn ra vị này mặt nạ đồng xanh thực lực cụ thể cao bao nhiêu, nhưng dám đối Mục hội trưởng đám người, vô lễ như thế, tất nhiên không tầm thường."),t("br"),t("br"),n._v("Nếu là cho cái tên này biết, bởi vì chính mình quan sát, đem Khổng sư hư ảnh làm không có, có thể hay không dưới cơn nóng giận, bắt lấy bản thân, tìm phiền toái?"),t("br"),t("br"),n._v("“Khổng sư sự tình, nên cũng biết người càng ít càng tốt...”"),t("br"),t("br"),n._v("Dính dáng Khổng sư, hơi không cẩn thận, liền sẽ cùng Vô Bi lão nhân đồng dạng, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("“Như vậy đi, ta cho sửa chữa mấy bút...”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền tinh thần khẽ động, ý niệm huyễn hóa ra một cái bút lông, thân thể nhoáng một cái đi tới trên không, lăng không vẽ lên đến."),t("br"),t("br"),n._v("Tránh khỏi phiền phức, cũng chỉ có thể như vậy."),t("br"),t("br"),n._v("Rất nhanh, tác phẩm hội họa làm xong, Trương Huyền thở phào nhẹ nhõm, bàn tay nhẹ nhàng đẩy một cái, đem hắn khắc ở vừa rồi Khổng sư hư tượng xuất hiện địa phương, lúc này mới lui trở về tại chỗ, tinh thần khẽ động, đem bốn phía hoàn toàn thắp sáng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một hồi lay động, biết linh tính đã kích hoạt, cũng không dừng lại, Trương Huyền ý thức rời khỏi cửa, trở lại thân thể."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một ngụm trọc khí phun ra, mở mắt."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Gặp hắn tỉnh lại, mặt nạ đồng xanh vội vàng nhìn lại."),t("br"),t("br"),n._v("Trương Huyền sắc mặt trắng nhợt, thanh âm uể oải: “May mắn không làm nhục mệnh... Bất quá, cũng hao tổn cực lớn, nếu như ngươi có tinh nguyên linh thạch thượng phẩm loại hình, cho ta một trăm, hai trăm, hẳn là có thể để cho ta khôi phục một chút...”"),t("br"),t("br"),n._v("“Một trăm, hai trăm?”"),t("br"),t("br"),n._v("Mặt nạ đồng xanh miệng co lại: “Thứ này ta không có, có điều, thật muốn thành công loại bỏ phong ấn, chắc chắn sẽ không bạc đãi...”"),t("br"),t("br"),n._v("Tinh nguyên linh thạch thượng phẩm thứ này, liên minh đế quốc đều không có bao nhiêu, cái tên này mới mở miệng, một trăm, hai trăm... Thật nói được!"),t("br"),t("br"),n._v("Thấy đối phương không có những này đồ tốt, lắc đầu, Trương Huyền đứng dậy, ngón tay đang vẽ cuốn lên nhẹ nhàng điểm một cái."),t("br"),t("br"),n._v("“Tỉnh lại!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Nương theo lời nói, cuốn sách bỗng nhiên đứng lên, duỗi lưng một cái, hình như một cái lười biếng người, vừa mới tỉnh ngủ."),t("br"),t("br"),n._v("“Đây là... Đem trọn bức họa đều khải linh?”"),t("br"),t("br"),n._v("Mọi người khóe miệng giật một cái."),t("br"),t("br"),n._v("Duỗi hết lưng mỏi, bức tranh giống như là thấy được hoàn cảnh chung quanh, đột nhiên thân thể khẽ động, trực tiếp hướng ra phía ngoài chạy tới."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Lông mày giương lên, mặt nạ đồng xanh lăng không một trảo, không gian chung quanh lập tức bị phong tỏa lên."),t("br"),t("br"),n._v("“Nửa bước xuất khiếu!”"),t("br"),t("br"),n._v("Vừa ra tay, Trương Huyền lập tức nhìn ra thực lực, lại là nửa bước Xuất Khiếu cảnh cường giả."),t("br"),t("br"),n._v("Khó trách dám như thế kiên cường, phần đông hội trưởng, đường chủ cũng không để ý."),t("br"),t("br"),n._v("Bị phong bế, bức tranh trốn không thoát, đành phải lui trở về, tràn đầy oan ức."),t("br"),t("br"),n._v("“Ta tới cùng hắn thương nghị một chút đi!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Huyền đi tới trước mặt, ngón tay đưa tới."),t("br"),t("br"),n._v("Là hắn khải linh, có đặc thù thân thiết cảm giác, gặp ngón tay tới, bức tranh cũng không trốn tránh."),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Trương Huyền một đạo ý niệm truyền lại đi qua."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Tựa hồ nghe đã hiểu hắn ý tứ, bức tranh lắc lư một cái, trên ngọn núi lơ lửng mây trắng, giống như là bị gió táp tịch quyển đồng dạng, không ngừng run rẩy, hình như bất cứ lúc nào cũng sẽ tiêu tán."),t("br"),t("br"),n._v("“Quả nhiên có thể...”"),t("br"),t("br"),n._v("Thấy cảnh này, mặt nạ đồng xanh cùng rất nhiều hội trưởng, đường chủ cũng tất cả đều mắt sáng rực lên, nguyên một đám nắm đấm xiết chặt."),t("br"),n._v(" Loại này bát cảnh thư hoạ bên trên phong ấn, bọn họ lần đầu tiên nghe nói... Có thể tận mắt nhìn thấy kỳ tích, tất cả đều tràn đầy kích động."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Nương theo bức tranh không ngừng lay động, mây trắng cũng run rẩy càng ngày càng lợi hại, thời gian không dài, cũng lại không chịu nổi, giống như là bị xé toang bệnh vảy nến, “Đùng!” Một tiếng, rơi trên mặt đất."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Gặp phong ấn lại trên mặt đất, tất cả mọi người đều đồng loạt nhìn lại."),t("br"),t("br"),n._v("Ngay cả Ngô sư cũng kìm lòng không được nhìn về phía ngọn núi trung tâm, muốn nhìn một chút, đến cùng phía dưới che đậy cái gì, có thể để cho vị này mặt nạ đồng xanh, vội vã như thế."),t("br"),t("br"),n._v("Non xanh nước biếc, chỗ này cảnh tượng, cùng trước đó dường như không có khác nhau quá nhiều."),t("br"),t("br"),n._v("“Đó là cái gì?”"),t("br"),t("br"),n._v("Không biết ai hô lên, lập tức mọi người tập trung thị lực nhìn sang."),t("br"),t("br"),n._v("Chỉ thấy ngọn núi trung tâm một vết nứt bên trong, do vô số khó hiểu tảng đá, xây dựng lên một đống tự nhiên suối nước nóng, mấy cái xinh đẹp cái bóng, đang tại trong đó ngâm."),t("br"),t("br"),n._v("Mấy cái này cái bóng, mặc dù thoạt nhìn mơ hồ, lại có thể nhìn ra, dáng người mỹ lệ, vừa nhìn liền biết, là không được thấy nhiều đại mỹ nữ."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Vốn cho rằng sẽ phong ấn cái gì vật kỳ quái, hoặc là bảo tàng, nhìn thấy lại là mỹ nhân đi tắm... Tất cả mọi người đều ngẩn ngơ."),t("br"),t("br"),n._v("Có chút quá bất ngờ."),t("br"),t("br"),n._v("“Ta hiểu được, Vô Bi lão nhân lúc trước nhìn núi, nhìn thấy một đám thiếu nữ tắm gội, trực tiếp vẽ vào, về sau cảm thấy... Nhìn người ta nữ hài tắm rửa, làm mất thân phận, liền cố ý che lấp!”"),t("br"),t("br"),n._v("“Đúng vậy a, ta liền nói tốt tốt, làm gì đánh lên như thế một khối to miếng vá, phá hủy cả bức họa thần vận... Nguyên lai nguyên nhân ở đây!”"),t("br"),t("br"),n._v("“Ha ha, có thể đem mỹ nhân đi tắm vẽ như vậy giống như đúc, vị này Vô Bi lão nhân, thật đúng là tính tình trung nhân...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người tất cả đều bừng tỉnh hiểu ra, nở nụ cười."),t("br"),t("br"),n._v("Còn tưởng rằng vị này Vô Bi lão nhân, tiêu tốn như thế đại tâm huyết, tiến hành phong ấn, là khóa lại bí mật gì, không nghĩ tới, là che khuất xấu hổ."),t("br"),t("br"),n._v("“Cái này... Không có khả năng, không có khả năng!”"),t("br"),t("br"),n._v("Mặt nạ đồng xanh đợi tại nguyên chỗ, mặt mũi khó tin."),t("br"),t("br"),n._v("Vì bức họa này, hắn bỏ ra không biết bao nhiêu tâm huyết, liền là muốn nhìn đến phong ấn lại bí mật, kết quả... Lại là mỹ nhân tắm rửa... To lớn chênh lệch, để hắn sắp điên cuồng."),t("br"),t("br"),n._v("“Phía dưới này nhất định còn có phong ấn đúng hay không? Khẳng định còn có...”"),t("br"),t("br"),n._v("Vội vàng đi tới thư hoạ trước mặt, cường đại nguyên thần lực lượng, bỗng nhiên lan tràn đi vào, muốn nhìn rõ ràng đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Cảm nhận được nguyên thần áp bức, bức tranh giật nảy mình, vội vàng co rụt lại, trốn đến Trương Huyền sau lưng, không ngừng run rẩy."),t("br"),t("br"),n._v("“Nói cho ta biết, có phải hay không còn có phong ấn...”"),t("br"),t("br"),n._v("Tròng mắt đỏ hoe, mặt nạ đồng xanh bởi vì quá quá khích động, từng đạo kịch liệt hồn lực không ổn định."),t("br"),t("br"),n._v("“Tốt rồi, ngươi nhìn đem Họa Họa dọa đến!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền nhẹ nhàng trấn an thoáng cái bức tranh: “Mọi người cũng đều thấy được, phong ấn lại là mỹ nữ đi tắm, muốn đến là Vô Bi lão nhân, nhìn lén người tắm rửa, không chỗ phát tiết, cuối cùng tuyên chi cùng mực... Mặc dù không đạo đức, nhưng cũng là văn nhân nhà thơ hành vi, không sao! Ngươi cũng đừng kích động.”"),t("br"),t("br"),n._v("Thấy đối phương thái độ biến hóa lớn như thế, rất rõ ràng biết phong ấn lại đồ vật, Trương Huyền âm thầm thở phào nhẹ nhõm."),t("br"),t("br"),n._v("May mắn phản ứng nhanh, tạm thời làm một bức họa, nếu không... Khẳng định muốn lộ ra chân tướng."),t("br"),t("br"),n._v("“Là ta lỗ mãng...”"),t("br"),t("br"),n._v("Nghe nói như thế, mặt nạ đồng xanh không có vừa rồi lỗ mãng, lần nữa khôi phục lý trí, ôm quyền cười một tiếng: “Ta được đến bức họa này về sau, phát hiện cái này phong ấn, vẫn nghĩ sẽ ẩn tàng cái gì, nếu biết, cũng coi như tâm nguyện, làm phiền các vị phí tâm!”"),t("br"),t("br"),n._v("Nói hết bàn tay lăng không một trảo."),t("br"),t("br"),n._v("Trước mắt xuất hiện một đống linh thạch thượng phẩm, chừng hơn vạn nhiều."),t("br"),t("br"),n._v("“Những linh thạch này, là ta lần này làm phiền chư vị thù lao, nhìn đừng ghét bỏ!”"),t("br"),t("br"),n._v("Nói hết, mặt nạ đồng xanh, nhìn về phía Trương Huyền: “Trương sư đã tại thư hoạ bên trên có như vậy nghiên cứu, ta chỗ này có một bản Vô Bi lão nhân lưu lại , kỹ càng viết hắn vẽ tranh kỹ xảo, hi vọng đối ngươi có ích...”"),t("br"),t("br"),n._v("Nói hết lắc đầu, lăng không một trảo, đem khải linh sau bức tranh chộp vào lòng bàn tay, liền ôm quyền: “Chư vị, cáo từ!”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, trực tiếp vọt ra ngoài, thời gian nháy mắt biến mất ở trước mặt mọi người."),t("br"),t("br"),n._v("Hiển nhiên, người mang cự bảo, sợ chọc phiền phức, xoay người trực tiếp rời đi."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhìn xem quyển sách trên tay, Trương Huyền không còn gì để nói."),t("br"),t("br"),n._v("Đối phương lời thề son sắt nói muốn cho bản thân thù lao, cỡ nào trân quý cỡ nào trân quý... Kết quả là cái đồ chơi này!"),t("br"),t("br"),n._v("Suy nghĩ một chút đều tràn đầy tâm tắc."),t("br"),t("br"),n._v("“Họa pháp thập giải... Trương sư, thứ này ngươi có bằng lòng hay không bán ra?”"),t("br"),t("br"),n._v("Cùng hắn im lặng khác biệt, một bên Mông Xung hội trưởng, hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Vô Bi lão nhân, là Thanh Nguyên đế quốc đi ra lợi hại nhất thư hoạ đại tông sư, mặc dù chỉ sống ngắn ngủi ba trăm tuổi, nhưng đối toàn bộ thư họa sư công hội, đều có cực lớn cống hiến."),t("br"),t("br"),n._v("Nhất là lúc tuổi già họa pháp thập giải, còn không có hoàn toàn hoàn thành, người liền mất tích, bởi vậy, người biết cũng không nhiều... Vốn cho rằng sớm đã thất truyền, không nghĩ tới, tại vị này mặt nạ đồng xanh trong tay, hơn nữa còn đưa tặng cho Trương sư."),t("br"),t("br"),n._v("“Bán ra?”"),t("br"),t("br"),n._v("Trương Huyền nhìn một cái lòng bàn tay sách, trực tiếp đem nội dung thu vào tiến thư viện, sách ném tới, một mặt đại khí: “Tặng cho ngươi!”"),t("br"),t("br"),n._v("“Đa tạ Trương sư...”"),t("br"),t("br"),n._v("Mông Xung hội trưởng kích động ngón tay phát run: “Đây chính là giá trị ít nhất năm mươi tinh nguyên linh thạch thượng phẩm bản độc nhất... Thật tặng ta?”"),t("br"),t("br"),n._v("“Ngươi nói cái gì? Năm mươi tinh nguyên linh thạch thượng phẩm?” Trương Huyền nhoáng một cái."),t("br"),t("br"),n._v("“Đúng vậy a!” Mông Xung hội trưởng."),t("br"),t("br"),n._v("“Vậy ta không tặng, bán cho ngươi...” Trương Huyền vội nói: “Đưa tiền đi!”"),t("br"),t("br"),n._v("“...” Mông Xung."),t("br"),t("br"),n._v("“...” Mọi người."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1177"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1179"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);