(window.webpackJsonp=window.webpackJsonp||[]).push([[2130],{2485:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-840-truong-huyen-chinh-la-thay-ta"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-840-truong-huyen-chinh-la-thay-ta"}},[n._v("#")]),n._v(" Chương 840: Trương Huyền chính là thầy ta!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 840: Trương Huyền chính là thầy ta!"),t("br"),t("br"),n._v("“Bị mê hoặc? Ai? Mời ngươi nói ra đến, không cần chửi chó mắng mèo!”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết tính khí nóng nảy, khuôn mặt trầm xuống, đi đầu khẽ nói."),t("br"),t("br"),n._v("“Không sai, chúng ta thập đại trường lão cùng một chỗ, ít nhất cũng phải mấy trăm năm, ngươi ý tứ, là ta sẽ rò rỉ bí mật, vẫn là Triệu viện trưởng, Vệ viện trưởng? Vẫn là tại ngồi các vị?”"),t("br"),t("br"),n._v("Mi viện trưởng cũng đầy là không vui."),t("br"),t("br"),n._v("Đối phương, rõ ràng có chỗ chỉ, không tức giận mới là lạ."),t("br"),t("br"),n._v("“Hai vị viện trưởng không cần phải gấp, ta lại không nói các ngươi, trừ phi thật cùng Dị Linh tộc nhân, có một loại nào đó quan hệ thân mật... Bản thân chột dạ!” Lục Phong cười lạnh."),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, Vệ Nhiễm Tuyết vỗ bàn một cái đứng lên, toàn thân khí tức bỗng nhiên vọt ra: “Có bản lĩnh ngươi lặp lại lần nữa!”"),t("br"),t("br"),n._v("“Lục Phong, chú ý miệng của mình, đang ngồi đều là một viện trưởng, chỗ có danh sư công nhận thập đại trường lão, ngươi nói ai cùng Dị Linh tộc có quan hệ thân mật?”"),t("br"),t("br"),n._v("Triệu Bính Tuất cũng híp mắt lại, cố nén tức giận."),t("br"),t("br"),n._v("“Ta nói chính là ai, một lát nữa chân tướng rõ ràng, tự sẽ biết! Liền sợ là chính các ngươi, đến lúc đó không ngẩng đầu được lên!” Mí mắt khẽ đảo, Lục Phong khẽ nói."),t("br"),t("br"),n._v("“Không cần một lát nữa, ta hiện tại liền nghĩ lãnh giáo một chút ngươi ! Ra chiêu đi!”"),t("br"),t("br"),n._v("Hét dài một tiếng, Vệ Nhiễm Tuyết tay ngọc cuồn cuộn, một đạo sóng khí thẳng tắp bắn ra, Thánh vực nhất trọng đỉnh phong thực lực, không giữ lại chút nào phóng xuất ra."),t("br"),t("br"),n._v("Nàng mặc dù am hiểu kinh hồng chức nghiệp, bản thân sức chiến đấu cũng không thể khinh thường, còn không có ra tay, liền đem không khí chung quanh đè ép dường như sắp bạo tạc."),t("br"),t("br"),n._v("“Vệ viện trưởng thực lực lại tăng trưởng thêm?”"),t("br"),t("br"),n._v("Thấy được nàng xao động mà ra khí tức, mọi người tất cả đều run lên."),t("br"),t("br"),n._v("Bọn họ ở chung được mấy trăm năm, từng người hiểu rõ, Vệ Nhiễm Tuyết thực lực ranh giới cuối cùng ở nơi nào, biết rất nhiều, không nghĩ tới, mới mấy ngày không thấy, lại có đột phá, thậm chí đối kinh hồng có càng sâu lý giải, nhất cử nhất động, đều khiến người ta say mê."),t("br"),t("br"),n._v("“Đã Vệ viện trưởng muốn muốn lĩnh giáo của ta Đại Vô Ngôn thủ, nếu như ta không đáp ứng, há không lộ vẻ thật không tiện?”"),t("br"),t("br"),n._v("Lạnh hừ một tiếng, Lục Phong bàn tay vừa nhấc, một cái cự đại thủ ấn, đứng lơ lửng giữa không trung."),t("br"),t("br"),n._v("Đại Vô Ngôn thủ, Linh cấp đỉnh phong võ kỹ, Lục Phong mạnh nhất tuyệt chiêu!"),t("br"),t("br"),n._v("“Tới đi...”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết ngọc tay vừa lộn, tinh thuần chân khí hội tụ thành trường hồng, thẳng tắp công kích đi qua."),t("br"),t("br"),n._v("Mặc dù gian phòng không lớn, nhưng đi đến Thánh vực, đối lực lượng khống chế đạt đến lông tóc cảnh giới, không dư thừa chút nào lực lượng lãng phí, không cần lo lắng làm bị thương những người khác, thậm chí tiêu diệt gian phòng."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Thấy nàng chủ động công kích, Lục Phong cười lạnh, cũng không đứng dậy, tay phải bỗng nhiên khẽ đảo, trước mặt lơ lửng thủ ấn, bút thẳng bay đi."),t("br"),t("br"),n._v("Oanh!"),t("br"),t("br"),n._v("Chưởng ấn cùng đối phương trường hồng đụng một cái, Vệ Nhiễm Tuyết sắc mặt trắng nhợt, liên tục lui về phía sau mấy bước, con ngươi kìm lòng không được bỗng nhiên co rút lại."),t("br"),t("br"),n._v("“Nửa bước... Thần thức cảnh?”"),t("br"),t("br"),n._v("Thánh vực nhị trọng thần thức cảnh, có thể mở ra thai mắt, sinh ra thần thức, để người tu luyện xem càng xa, xem càng thêm rõ ràng, chiến đấu lại càng dễ phân tích chi tiết, đứng ở thế bất bại."),t("br"),t("br"),n._v("Trước mắt cái này Lục Phong mặc dù còn không có đi đến loại cảnh giới này, nhưng cũng không kém nhiều!"),t("br"),t("br"),n._v("“Khó trách...” Triệu Bính Tuất mấy người cũng đều tất cả đều xiết chặt."),t("br"),t("br"),n._v("Khó trách cái tên này dám nói ra vừa rồi lời kia, không sợ đắc tội người, nguyên lai thực lực đã có đột phá!"),t("br"),t("br"),n._v("“Tính ngươi nhãn lực không tệ!”"),t("br"),t("br"),n._v("Mí mắt vừa nhấc, Lục Phong hừ lạnh một tiếng, trên người triển lộ ra mạnh mẽ vô địch khí tức: “Các ngươi ai còn muốn thử xem của ta Đại Vô Ngôn thủ?”"),t("br"),t("br"),n._v("“Cái này...” Mọi người trầm lặng."),t("br"),t("br"),n._v("Mọi người ở chung đã nhiều năm như vậy, đều đối thủ đoạn của chính mình biết rất rõ ràng, trước mắt vị này đột phá đến nửa bước thần thức cảnh, đã đi tới trước mặt bọn họ, thành danh sư học viện, chính cống người thứ nhất!"),t("br"),t("br"),n._v("“Tốt, đều là học viện danh sư, không có gì có thể cãi lộn!”"),t("br"),t("br"),n._v("Khoát tay áo, Mộc sư bàn tay một trảo, một đạo chân khí xao động bốn phía, đem cả phòng đều che đậy lên."),t("br"),t("br"),n._v("Hắn dùng thủ đoạn đặc thù, không chỉ thanh âm truyền lại không đi ra, ngay cả đưa tin ngọc phù, đều rất khó đem tin tức rò rỉ."),t("br"),t("br"),n._v("“Ta đã xuống cấm chế, có lời gì nói đi!”"),t("br"),t("br"),n._v("Mộc sư thản nhiên nói."),t("br"),t("br"),n._v("“Bẩm báo Mộc sư, tại hạ còn muốn làm một chuyện...”"),t("br"),t("br"),n._v("Thấy mọi người đều bị bản thân chấn nhiếp, Mộc sư cười lạnh, đứng dậy, lông mày giương lên: “Vì phòng ngừa ngoài ý muốn, ta còn muốn mời ra trấn viện Thánh khí... Kim Nguyên đỉnh!”"),t("br"),t("br"),n._v("“Kim Nguyên đỉnh?”"),t("br"),t("br"),n._v("“Danh Sư đường ban cho học viện hộ viện Thánh khí?”"),t("br"),t("br"),n._v("“Đây chính là Thánh khí, mặc dù chỉ là một cái Khí đỉnh, nhưng là chân chính Thánh khí, có tinh thần của mình cùng ý chí, một khi vận chuyển, Thánh vực nhị trọng, tam trọng cường giả đều có thể tuỳ tiện chém giết, Lục Phong muốn làm gì?”"),t("br"),t("br"),n._v("“Tổng bộ ban cho Khí đỉnh thời điểm, từng nói qua, không phải vạn bất đắc dĩ, tuyệt không thể vận dụng, hắn thật chẳng lẽ phát hiện cái gì?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Kim Nguyên đỉnh là danh sư học viện sau cùng át chủ bài, trừ phi gặp được Dị Linh tộc nhân xâm lấn, nếu không, tuyệt không mở ra, hắn hiện tại muốn mời đi ra, làm gì?"),t("br"),t("br"),n._v("Tất cả trưởng lão sắc mặt đồng thời ngưng trọng lên."),t("br"),t("br"),n._v("“Ngươi cũng đã biết Kim Nguyên đỉnh đại biểu cái gì?” Mộc sư cũng híp mắt lại."),t("br"),t("br"),n._v("“Tự nhiên biết, nhưng ta muốn nói sự tình, liên quan quá lớn, hơn nữa người kia có không thể tưởng tượng thủ đoạn, vì phòng ngừa ngoài ý muốn, trước muốn sớm làm ra chuẩn bị, nếu không... Sợ chúng ta nhiều trưởng lão như vậy, đều khó mà chống chọi!”"),t("br"),t("br"),n._v("Hai tay vẫy một cái, Lục Phong trên người tự mang khí phách."),t("br"),t("br"),n._v("Căn cứ Đổng Hân đọc, cái này Trương Huyền, khống chế hai mươi đầu Thánh vực nhất trọng cấp bậc thuộc hạ, còn có thể toả ra liền Tử Dương thú tiền bối, đều khó mà chống lại sát lục chi khí, một khi vạch trần thân phận, đối phương lộ ra nguyên hình, ra tay đánh nhau lời nói, cho dù thập đại trường lão thực lực mạnh mẽ, cũng chưa chắc có thể là đối thủ!"),t("br"),t("br"),n._v("Nếu thực như thế, toàn bộ danh sư học viện, chỉ sợ đều muốn toàn quân bị diệt."),t("br"),t("br"),n._v("“Cái này...” Mọi người nhìn nhau."),t("br"),t("br"),n._v("Đến cùng chuyện gì? Liền danh sư học viện sau cùng át chủ bài, đều muốn xuất ra đến?"),t("br"),n._v("“Biết là tốt rồi, đã ngươi nói nghiêm trọng như vậy, vậy liền cho mời Kim Nguyên đỉnh!” Mộc sư nhẹ gật đầu."),t("br"),t("br"),n._v("Hắn dù sao cũng là ngoại nhân, chỉ là tổng bộ phái tới xác định viện trưởng, không muốn nhúng tay quá nhiều. Nội bộ sự tình, vẫn là nội bộ xử lý tốt hơn."),t("br"),t("br"),n._v("“Đa tạ Mộc sư!”"),t("br"),t("br"),n._v("Gặp hắn đồng ý, Lục Phong gật gật đầu, liền ôm quyền về phía sau đi hai bước đi tới gian phòng một bên, bàn tay ở trên tường nhấn một cái, ngay sau đó thanh âm đám lãng vang lên."),t("br"),t("br"),n._v("“Tại hạ Lục Phong, danh sư học viện đại viện trưởng, cho mời Kim Nguyên đỉnh tiền bối!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nương theo tiếng âm vang lên, ngay sau đó mọi người cảm thấy gian phòng một hồi lay động, nguyên bản bóng loáng bức tường “Kẹt kẹt!” Mở ra, một cái cự đại Khí đỉnh, “Hô!” Bay vào."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Rơi vào gian phòng ở giữa nhất địa phương, phát ra ầm vang vang lên: “Không biết ta đang nghỉ ngơi ư? Chuyện gì?”"),t("br"),t("br"),n._v("Thánh khí, đã có linh trí của mình, có thể cùng nhân loại đồng dạng suy nghĩ."),t("br"),t("br"),n._v("“Học viện xuất hiện Dị Linh tộc Vương Giả, còn hi vọng tiền bối, đến lúc đó ra tay trấn áp!”"),t("br"),t("br"),n._v("Lục Phong ôm quyền."),t("br"),t("br"),n._v("“Dị Linh tộc Vương Giả?”"),t("br"),t("br"),n._v("“Ai?”"),t("br"),t("br"),n._v("Tất cả viện trưởng tất cả đều con ngươi co rụt lại."),t("br"),t("br"),n._v("Dị Linh tộc Vương Giả... Xuất hiện tại danh sư học viện?"),t("br"),t("br"),n._v("Mộc sư vẻ mặt cũng là trầm xuống."),t("br"),t("br"),n._v("Thật có loại này đáng sợ tồn tại, bản thân làm sao không cảm thấy được?"),t("br"),t("br"),n._v("Coi như mình không cảm thấy được... Nàng có lẽ có thể chứ?"),t("br"),t("br"),n._v("“Dị Linh tộc Vương Giả? Ngươi xác định?” Kim Nguyên đỉnh sững sờ."),t("br"),t("br"),n._v("“Chắc chắn 100%!” Lục Phong gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, nếu quả thật xuất hiện loại người này, ta sẽ lập tức đem hắn nuốt luyện hóa, nếu như không phải... Quấy rầy ta đi ngủ, có tin ta hay không đem ngươi nuốt vào đến, luyện thành xỉ than đá?”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh hừ một tiếng."),t("br"),t("br"),n._v("“Tiền bối yên tâm, ta nhất định không cho ngươi cơ hội này...” Lục Phong nở nụ cười."),t("br"),t("br"),n._v("“Tốt, Kim Nguyên tiền bối đã tới, đến cùng chuyện gì xảy ra, mau nói đi!”"),t("br"),t("br"),n._v("Triệu Bính Tuất cũng nhịn không được nữa, nói."),t("br"),t("br"),n._v("“Không cần phải gấp!”"),t("br"),t("br"),n._v("Gật gật đầu, Lục Phong bàn tay vẫy một cái, hướng mọi người giới thiệu: “Cho mọi người giới thiệu một chút, cái này Đổng Hân, là học viện lớp năm học viên, Đổng Hân biết hội trưởng, ta mới vừa thu học sinh thân truyền... Tốt, Đổng Hân, ngươi cùng mọi người nói một chút mười ngày trước nhìn thấy một màn kia, như nói thật đi ra là được, không cần khẩn trương.”"),t("br"),t("br"),n._v("“Vâng!” Gật gật đầu, Đổng Hân hít sâu một hơi, tiến về phía trước một bước, sắc mặt nghiêm túc."),t("br"),t("br"),n._v("Nàng biết đây là một lần kỳ ngộ, không nói cái khác, phát hiện ẩn núp Dị Linh tộc Vương Giả, chỉ điều này, tựu có thể làm cho nàng sau này danh sư chi đường, thuận buồm xuôi gió."),t("br"),t("br"),n._v("“Tại hạ Đổng Hân, danh sư học viện lớp năm học viên, lục tinh hạ phẩm danh sư, lấy danh sư danh dự đảm bảo, phía dưới nói tới, toàn bộ tận mắt thấy, không dám khuyếch đại nửa điểm...”"),t("br"),t("br"),n._v("Đến đến đại sảnh ở giữa, Đổng Hân giơ bàn tay lên, đi đầu lấy danh sư thân phận, phát ra lời thề."),t("br"),t("br"),n._v("Thấy nàng nói như vậy, mọi người tất cả đều nhẹ gật đầu."),t("br"),t("br"),n._v("Danh sư lời thề chí cao vô thượng, không ai dám nói dối, bởi vì một khi vi phạm nội tâm, không cần trừng phạt, tâm cảnh liền sẽ sụp đổ."),t("br"),t("br"),n._v("“Mười ngày trước, ta trong lúc vô tình đi ra ngoài trường một tòa phủ đệ, thấy được một cái Phong Tức trận, đang tò mò chuyện gì xảy ra, liền thấy Tử Dương thú tiền bối xông phá trận pháp, trốn thoát...”"),t("br"),t("br"),n._v("Đổng Hân thanh âm thanh thúy, trật tự tươi mát, đem ngày đó gặp phải sự tình kỹ càng nói một lần."),t("br"),t("br"),n._v("Trong phòng lặng ngắt như tờ, tất cả mọi người đều vẻ mặt khó coi."),t("br"),t("br"),n._v("Nhất là Mi trưởng lão đám người, mày nhăn lại, trong mắt tràn đầy không thể tin được."),t("br"),t("br"),n._v("“Ngươi nói... Trương lão sư, nắm trong tay hai mươi cái Thánh vực cấp bậc Dị Linh tộc nhân thuộc hạ, đem Tử Dương thú tiền bối cuồng ẩu?” Tưởng Thanh Cầm viện trưởng nhịn không được đứng dậy."),t("br"),t("br"),n._v("Hắn nghe qua Trương lão sư diễn tấu nhạc khúc, tâm cảnh thuần khiết, chí thú cao nhã, cho người ta một loại ra nước bùn mà không nhiễm cảm giác, làm sao có thể là Dị Linh tộc nhân?"),t("br"),t("br"),n._v("Liền xem như Dị Linh tộc Vương Giả có thể ngụy trang dung mạo, ngụy trang khí tức, cũng không thể đem tâm cảnh, cũng ngụy giả vờ đi!"),t("br"),t("br"),n._v("Phải biết, đàn có thể đại biểu tâm cảnh, điểm ấy là ai đều không thể che lấp!"),t("br"),t("br"),n._v("“Vâng!” Đổng Hân gật đầu."),t("br"),t("br"),n._v("“Điều đó không có khả năng! Trương lão sư vì người phóng khoáng, làm việc sảng khoái, làm sao có thể là Dị Linh tộc nhân? Quả thực lời nói vô căn cứ! Dù sao ta không tin!”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm hất lên ống tay áo: “Lục Phong, ngươi không phải là cố ý công báo tư thù chứ?”"),t("br"),t("br"),n._v("“Ngươi gọi hắn cái gì? Trương lão sư?” Thấy luôn luôn không để ý tới thế sự Tưởng viện trưởng, đột nhiên tức giận, Lục Phong mày nhăn lại, đồng thời tràn đầy kỳ quái."),t("br"),t("br"),n._v("Trương sư, Trương lão sư, mặc dù xưng hô bên trong chỉ tăng thêm một chữ, nhưng đại biểu hoàn toàn thân phận khác nhau."),t("br"),t("br"),n._v("Cái trước là đối danh sư tôn sùng, mà người sau, thì là học sinh xưng hô."),t("br"),t("br"),n._v("“Không sai, ta hiện tại là Trương lão sư học sinh, ngươi vũ nhục thầy ta, nói hắn là Dị Linh tộc nhân, ngươi cảm thấy ta sẽ tin tưởng?” Hừ một tiếng, Tưởng Thanh Cầm lông mày vung lên."),t("br"),t("br"),n._v("“Lão sư? Ngươi đường đường viện trưởng, nhận một cái học sinh là lão sư?”"),t("br"),t("br"),n._v("Lục Phong suýt chút nữa không điên rồi."),t("br"),t("br"),n._v("Cái tên này có bị bệnh không?"),t("br"),t("br"),n._v("Ngươi không phải cái gì cũng không để ý ư?"),t("br"),t("br"),n._v("Không phải thế ngoại cao nhân ư?"),t("br"),t("br"),n._v("Làm sao thời gian nháy mắt, tựu nhận vị này Trương Huyền vi sư?"),t("br"),t("br"),n._v("Không điên đi!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-848"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-850"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);