(window.webpackJsonp=window.webpackJsonp||[]).push([[2134],{2489:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-843-nguoi-lao-su-la-ai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-843-nguoi-lao-su-la-ai"}},[n._v("#")]),n._v(" Chương 843: Ngươi lão sư là ai?")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 843: Ngươi lão sư là ai?"),i("br"),i("br"),n._v("“Không sai, những ngày này ta không liên hệ chư vị, là bởi vì đến đột phá mấu chốt, không thể phân tâm!”"),i("br"),i("br"),n._v("Tử Dương thú nói: “Nếu như không phải chủ nhân, ta hiện tại vẫn như cũ là Thánh vực nhất trọng đỉnh phong, muốn đột phá, hoàn toàn không có khả năng!”"),i("br"),i("br"),n._v("Nó nói ngược lại là lời nói thật."),i("br"),i("br"),n._v("Trong khoảng thời gian này, trừ bỏ bị đánh, chính là bị đặt ở Thiên Nghĩ Phong sào huyệt bên trong, liền thân thể đều giãn ra không ra, mãnh liệt tức giận, cũng là thuận lợi đột phá cơ sở."),i("br"),i("br"),n._v("Thần Thức cảnh, cùng linh hồn, tâm cảnh có quan hệ, những này tối tăm không mặt trời nhục nhã, để hắn tinh thần trở nên cứng cáp hơn."),i("br"),i("br"),n._v("Nếu như không có cái này trải qua, cho dù có Hồng Viên thú nội đan, có thể hay không đột phá, cũng là không biết chi ngày."),i("br"),i("br"),n._v("Mọi người nói không ra lời."),i("br"),i("br"),n._v("Người tu luyện bế quan, mấy tháng, thậm chí mấy năm đều là chuyện thường, đối phương chẳng qua bế quan nửa cái tháng sau mà thôi, không lâu sau."),i("br"),i("br"),n._v("“Tử Dương thú tiền bối từng là lão viện trưởng thú sủng, vì học viện làm bao nhiêu cống hiến, không cần ta nói, chắc hẳn mọi người cũng đều biết. Trương sư vì để cho nó đột phá, bỏ bao công sức, hao phí tâm huyết... Các ngươi vậy mà vu hãm hắn là Dị Linh tộc nhân! Lục Phong, nếu như hôm nay ngươi giải thích không rõ, cái này tội vu hãm, khẳng định là chạy không thoát, coi như nháo đến Thanh Nguyên đế quốc, ta cũng phụng bồi tới cùng!”"),i("br"),i("br"),n._v("Lông mày giương lên, Triệu Bính Tuất đi đầu nổi lên."),i("br"),i("br"),n._v("Ngươi nói Trương sư đánh Tử Dương thú, là Dị Linh tộc nhân, bây giờ người ta người bị hại đều đi ra bác bỏ tin đồn... Còn có lời gì nói?"),i("br"),i("br"),n._v("Sắc mặt nhăn nhó, Lục Phong thân thể run rẩy, trên đầu nổi gân xanh, buồn bực sắp hộc máu."),i("br"),i("br"),n._v("Vốn cho rằng, lần này nhận được tin tức, mười phần chắc chín có thể để cho vị này Trương Huyền, muốn sống không được muốn chết không xong, nằm mơ đều không nghĩ tới, sẽ có dạng này đảo ngược..."),i("br"),i("br"),n._v("Một bên Đổng Hân, cũng thân thể mềm mại run rẩy, sắp ngất."),i("br"),i("br"),n._v("Nàng tận mắt thấy, đối Phương chỉ huy cấp dưới đối vị này Tử Dương thú đánh, tàn nhẫn vô tình, làm sao trong nháy mắt, liền biến thành giúp nó đột phá?"),i("br"),i("br"),n._v("Đột phá, có như vậy không để ý tới sống chết cuồng ẩu?"),i("br"),i("br"),n._v("“Không đúng, chẳng lẽ là...”"),i("br"),i("br"),n._v("Đột nhiên trong lòng hơi động, một cái ý nghĩ xông ra."),i("br"),i("br"),n._v("Nghĩ đến điểm này, vội vàng ôm quyền: “Mộc sư, Kim Nguyên đỉnh tiền bối, chư vị trưởng lão, tại hạ có một ý tưởng, không biết có nên nói hay không!”"),i("br"),i("br"),n._v("“Hôm nay chỉ vì biết rõ chân tướng, có chuyện cứ nói đừng ngại!”"),i("br"),i("br"),n._v("Mộc sư gật đầu."),i("br"),i("br"),n._v("“Ta tận mắt thấy Trương Huyền phái người đánh Tử Dương thú tiền bối, hiện ở tiền bối nhưng giúp hắn nói chuyện... Có phải hay không là nhận lấy một loại nào đó uy hiếp hoặc là có cái gì việc khó nói? Lại hoặc là, đánh mục đích đúng là vì để cho hắn khuất phục?”"),i("br"),i("br"),n._v("Đổng Hân nói."),i("br"),i("br"),n._v("Trước mấy ngày mới bị đánh chết đi sống lại, hiện tại chẳng những không địch ý, còn nhận chủ, đổi lại ai, đều khó mà tin được."),i("br"),i("br"),n._v("Biến hóa nhanh như vậy, loại trừ thực tình thần phục, còn có một loại, cái kia chính là... Nghĩ một đằng nói một nẻo!"),i("br"),i("br"),n._v("“Ngươi nói là uy hiếp?” Mộc sư cau mày."),i("br"),i("br"),n._v("Tất cả trưởng lão cũng đều sững sờ."),i("br"),i("br"),n._v("Loại tình huống này, cũng không phải là không có qua."),i("br"),i("br"),n._v("Nếu thật là uy hiếp, nói lời cũng không phải là thực tình, liền coi như bọn họ là danh sư, cũng rất khó phân biệt."),i("br"),i("br"),n._v("“Không sai, người người đều biết Tử Dương tiền bối, đối lão viện trưởng, trung thành tuyệt đối, viện trưởng đến cùng sống hay chết, còn không có tin tức xác thật, nó liền nhận người khác làm chủ, ta dù sao không tin!”"),i("br"),i("br"),n._v("Ánh mắt sáng lên, Lục Phong tràn đầy khen ngợi nhìn Đổng Hân một cái, khẽ nói."),i("br"),i("br"),n._v("Lão viện trưởng đi di tích, một mực không về, học viện định tính chính là mất tích, cụ thể có phải hay không chết rồi, cũng không rõ ràng."),i("br"),i("br"),n._v("Thật muốn như vậy, liền ý vị sâu xa."),i("br"),i("br"),n._v("Chủ nhân, chết hay không đều không có xác định, liền nhận những người khác làm chủ, quả thực chính là không thể tưởng tượng."),i("br"),i("br"),n._v("“Có phải hay không là vị này Trương Huyền, nắm trong tay một loại nào đó tin tức, lấy lão viện trưởng sống chết tiến hành uy hiếp, bức bách Tử Dương tiền bối nhận chủ, giúp hắn nói chuyện? Lại hoặc là, là... Dùng một loại nào đó không muốn người biết thủ đoạn đặc thù?”"),i("br"),i("br"),n._v("Trần Thừa Tuần đứng dậy."),i("br"),i("br"),n._v("Hắn cùng Lục Phong là trên một đường thẳng, đã không có cách nào lùi bước."),i("br"),i("br"),n._v("Mộc sư trầm tư."),i("br"),i("br"),n._v("Nếu là bản thân vị lão hữu kia thật không có chết, người khác coi đây là áp chế lời nói, y theo Tử Dương thú trung thành tuyệt đối tính cách, ngược lại là thật có khả năng đổi giọng."),i("br"),i("br"),n._v("“Bởi vì không cách nào phán định có phải hay không chịu đến uy hiếp, cho nên... Tử Dương thú tiền bối lời nói không đủ làm tin!”"),i("br"),i("br"),n._v("Lục Phong nói tiếp."),i("br"),i("br"),n._v("“Ngươi... Đánh rắm!”"),i("br"),i("br"),n._v("Không nghĩ tới nó tự mình bác bỏ tin đồn, biến thành hoang đường như vậy luận chứng, Tử Dương mặt thú sắc đỏ lên, tức giận đến hét lớn: “Ta nói mỗi một câu nói, đều xuất phát từ nội tâm, làm sao lại không thể tin?”"),i("br"),i("br"),n._v("“Tiền bối không nên kích động, Dị Linh tộc nhân nhiều thủ đoạn, am hiểu hơn linh hồn mê hoặc chi thuật, không có cách nào xác nhận, ngươi có phải hay không bị mê hoặc trước đó, lời của ngươi, chúng ta thực sự rất khó phân biệt!”"),i("br"),i("br"),n._v("Lục Phong nói."),i("br"),i("br"),n._v("Thời kỳ Thượng Cổ, vu hồn sư chức nghiệp, từng đầu nhập vào Dị Linh tộc, bởi vậy, thứ hai đối hồn phách nắm giữ cực cao, ngươi trước mấy ngày mới bị đánh, bây giờ lại nói ra hoàn toàn trái lại lời nói, đổi lại ai, đều cảm thấy không quá tin tưởng."),i("br"),i("br"),n._v("“Lục viện trưởng, cái này suy luận thành lập, ta có phải hay không cũng có thể hoài nghi, ngươi đã bị Dị Linh tộc nhân mê hoặc, cố ý phải thêm hại một vị nắm giữ Minh Lý Chi Nhãn danh sư?”"),i("br"),i("br"),n._v("Từ khi đi vào phòng, vẫn không lên tiếng Mạc Cao Viễn đột nhiên mở miệng."),i("br"),i("br"),n._v("“Không sai! Nắm giữ Minh Lý Chi Nhãn danh sư, đối nhân tộc tác dụng bao lớn, có thể tưởng tượng được, Lục viện trưởng một mực nhằm vào, sẽ không phải đã là Dị Linh tộc người chứ?”"),i("br"),i("br"),n._v("Mi trưởng lão cũng vuốt râu, nói."),i("br"),i("br"),n._v("Ngươi nói Tử Dương thú lời nói, không thể tin, cái kia tốt... Lời của ngươi lại có mấy phần có thể tin?"),i("br"),i("br"),n._v("“Các ngươi...”"),i("br"),i("br"),n._v("Không nghĩ tới hai vị này trở tay một chút, liền đem chụp mũ một lần nữa khấu trừ tới, Lục Phong kìm nén suýt chút nữa không nôn ra máu, đành phải khoát tay áo: “Tốt, chúng ta có thể không xoắn xuýt vấn đề này, cái kia... Vị này Trương Huyền, nắm giữ Dị Linh tộc thuộc hạ sự tình, là không có cách nào phản bác chứ? Lúc trước Tử Dương tiền bối bị bắt hiện trường, chúng ta đều đi qua nhìn qua, Dị Linh tộc nhân khí tức, dấu vết, rõ ràng như vậy, là căn bản không che giấu được!”"),i("br"),n._v("Căn cứ Đổng Hân miêu tả, hắn không chỉ đánh Tử Dương thú, còn có một đám Dị Linh tộc nhân thuộc hạ."),i("br"),i("br"),n._v("Đây mới là mấu chốt nhất, trọng yếu nhất."),i("br"),i("br"),n._v("Tại cộng thêm, ở đây chư vị, đều đi dò xét qua, biết những cái kia dấu vết đại biểu cái gì."),i("br"),i("br"),n._v("Mi trưởng lão, Triệu viện trưởng đám người tất cả đều nói không ra lời."),i("br"),i("br"),n._v("Bọn họ đều gặp Lôi Viễn phong chiến đấu dấu vết lưu lại, tại cộng thêm Trương sư tu vi hiện tại, không có người trợ giúp, không thể nào là Tử Dương tiền bối đối thủ."),i("br"),i("br"),n._v("Thật chẳng lẽ có Dị Linh tộc nhân thuộc hạ?"),i("br"),i("br"),n._v("Muốn là như thế này, thật liền phiền toái."),i("br"),i("br"),n._v("Danh sư, là cấm cùng Dị Linh tộc nhân có liên quan, nếu không, có miệng đều giải thích không rõ."),i("br"),i("br"),n._v("“Dị Linh tộc thuộc hạ... Các ngươi nói nhưng mà cái này?”"),i("br"),i("br"),n._v("Biết chuyện ngày hôm nay, không nói rõ ràng, cả đời đều có chỗ bẩn, Trương Huyền cũng không nhiều lời, cổ tay khẽ đảo, một cái khôi lỗi xuất hiện tại trước mặt."),i("br"),i("br"),n._v("“Tin tưởng chư vị nhãn lực, đó có thể thấy được... Đây là dùng Dị Linh tộc nhân luyện chế mà thành khôi lỗi!”"),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("“Đây là lấy người thật luyện chế khôi lỗi!”"),i("br"),i("br"),n._v("Mọi người sững sờ, lập tức toàn bộ gật đầu."),i("br"),i("br"),n._v("Làm lục tinh thượng phẩm danh sư, lại không giống Đổng Hân như thế, nhìn thoáng qua, là khôi lỗi hay là người thật, hoàn toàn có thể nhẹ nhõm nhận ra."),i("br"),i("br"),n._v("“Triệu viện trưởng hẳn phải biết, ta đến học viện trước, từng cùng học viện Lạc Thất Thất, Ngọc Phi Nhi công chúa đám người, tiến vào một chỗ di tích, đồng thời may mắn nhận được Ngô Dương Tử tiền bối truyền thừa!”"),i("br"),i("br"),n._v("Trương Huyền nói tiếp."),i("br"),i("br"),n._v("“Ừm!” Triệu Bính Tuất gật đầu."),i("br"),i("br"),n._v("Chuyện này, lúc trước hắn liền kỹ càng sửa sang lại thành sách, thông cáo những người khác."),i("br"),i("br"),n._v("“Những khôi lỗi này, chính là tiền bối dùng đại thủ pháp luyện chế, ta chẳng qua cơ duyên xảo hợp nhận được mà thôi!”"),i("br"),i("br"),n._v("Trương Huyền cười cười: “Danh sư là không thể cùng Dị Linh tộc nhân có gặp nhau, nhưng đem hắn luyện chế thành khôi lỗi, vì nhân tộc sử dụng, coi như vô công, cũng không nên từng có chứ?”"),i("br"),i("br"),n._v("Mọi người sửng sốt."),i("br"),i("br"),n._v("Vốn cho rằng là Dị Linh tộc nhân, kết quả lại là khôi lỗi, loại vật này, không có mệnh lệnh, căn bản là không có cách hại người."),i("br"),i("br"),n._v("Thu phục nhiều hơn nữa, cũng không ảnh hưởng toàn cục."),i("br"),i("br"),n._v("“Cái này đích xác là vô ý thức khôi lỗi, hơn nữa rèn đúc phương pháp tinh diệu vô song, cùng trong truyền thuyết Ngô Dương Tử thủ pháp cực kỳ tương tự...”"),i("br"),i("br"),n._v("Nhìn kỹ một cái, Mộc sư nhẹ gật đầu."),i("br"),i("br"),n._v("Những khôi lỗi này, là Ngô Dương Tử tại Dị Linh tộc nhân khi còn sống rèn luyện, đã dùng hết đủ loại tài liệu quý giá cùng phương pháp, mỗi một đầu đều giá trị cực cao."),i("br"),i("br"),n._v("“Bất quá... Những khôi lỗi này, trong cơ thể vẫn như cũ ẩn chứa sát lục chi khí, bảo lưu Dị Linh tộc nhân tập tính, coi như Ngô Dương Tử khi còn sống, cũng chưa chắc có thể đem thuần phục? Ngươi lại là như thế nào có thể để cho ngoan ngoãn nghe theo mệnh lệnh?”"),i("br"),i("br"),n._v("Nhìn ra mấu chốt, Mộc sư cau mày."),i("br"),i("br"),n._v("Cái này mặc dù là đầu khôi lỗi, trong cơ thể sát lục chi khí, nhưng không hề yếu, hơn nữa nhìn đến nhân loại, mang theo trời sinh căm thù."),i("br"),i("br"),n._v("Mạnh như thế người, đừng nói Ngô Dương Tử, liền xem như hắn, đều chưa hẳn có thể thuần phục, trước mắt vị này chỉ có Quy Nhất cảnh thanh niên, lại là làm được bằng cách nào?"),i("br"),i("br"),n._v("Trương Huyền cau mày."),i("br"),i("br"),n._v("Để hắn ngoan ngoãn nghe lời, là bởi vì Ngoan Nhân, mà giải thích Ngoan Nhân, liền muốn giải thích Thiên Đạo chi sách, liên quan hắn bí mật lớn nhất, Thiên Đạo thư viện."),i("br"),i("br"),n._v("Bởi vậy... Căn bản không thể nói."),i("br"),i("br"),n._v("“Hơn nữa, những khôi lỗi này, là Dị Linh tộc nhân khi còn sống, đem thân thể nguyên một đám bộ kiện, cứ thế mà bị thay thế a! Không xuất hiện bất kỳ bài xích, nói rõ... Chủ quan bên trên, nguyện ý làm như vậy! Ngô Dương Tử năm đó bị Dị Linh tộc nhân bắt đi, động tĩnh huyên náo rất lớn, Thanh Nguyên đế quốc phân bộ đều có hồ sơ, nếu như hắn thật có để cái này Dị Linh tộc nhân ngoan ngoãn được luyện chế, thậm chí nghe lời bản lĩnh, lại làm sao có thể bị hắn bắt đi, một đi không trở lại?”"),i("br"),i("br"),n._v("Mộc sư nói tiếp."),i("br"),i("br"),n._v("Thân là thất tinh danh sư, nhãn lực kinh người, biết loại này khôi lỗi phương pháp luyện chế, coi như Ngô Dương Tử rất lợi hại, muốn thành công, cũng khó khăn."),i("br"),i("br"),n._v("“Mộc sư nói rất đúng, những này liền xem như khôi lỗi, lại là như thế nào luyện chế thành công? Ngươi loại thực lực này, như thế nào thu phục?”"),i("br"),i("br"),n._v("Nghe được Mộc sư lời nói, Lục Phong ánh mắt sáng lên: “Trừ phi, ngươi là Dị Linh tộc nhân Vương Giả, có thể để cho khôi lỗi ngoan ngoãn nghe lời, cam tâm liền giết, lúc này mới tại sau khi chết, tất cả nghe theo ngươi lời nói!”"),i("br"),i("br"),n._v("Trương Huyền sầm mặt lại."),i("br"),i("br"),n._v("Hắn tự nhiên không phải Dị Linh tộc Vương Giả, nhưng Ngoan Nhân là, thậm chí cũng có thể là trong đó Hoàng giả!"),i("br"),i("br"),n._v("Lúc trước Ngô Dương Tử có thể đem những này Dị Linh tộc nhân luyện chế thành khôi lỗi, chính là cái tên này nguyên nhân."),i("br"),i("br"),n._v("Nhưng mà... Thật muốn nói tỉ mỉ, thư viện bí mật liền khó bảo toàn."),i("br"),i("br"),n._v("“Như thế nào luyện chế thành công, ta thật không biết, đây là Ngô Dương Tử tiền bối gây nên, còn thu phục!”"),i("br"),i("br"),n._v("Chần chờ một chút, Trương Huyền nói: “Là lão sư ta lo lắng ta an toàn, cố ý thuần phục, giao cho ta!”"),i("br"),i("br"),n._v("“Ngươi lão sư?”"),i("br"),i("br"),n._v("Lục Phong hừ lạnh: “Là vị nào danh sư? Nói ra nghe một chút, ta cũng muốn nhìn một chút, là cái nào danh sư, có thể đem nhiều như vậy vô ý thức, chỉ thuần phục Dị Linh tộc Vương Giả khôi lỗi, thuần phục ngoan ngoãn nghe một cái tứ tinh danh sư!”"),i("br"),i("br"),n._v("“Lão sư ta...”"),i("br"),i("br"),n._v("Mặt mũi xoắn xuýt, Trương Huyền còn không có giải thích, một cái thanh âm nhàn nhạt, theo bên ngoài phòng truyền vào."),i("br"),i("br"),n._v("“Là ta!”"),i("br"),i("br"),n._v("Một cỗ cường đại tới cực điểm khí tức, trong nháy mắt đổ xuống mà xuống."),i("br"),i("br"),n._v("(Nhanh đến cuối tháng, cầu hai tấm vé tháng!)"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-851"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-853"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);