(window.webpackJsonp=window.webpackJsonp||[]).push([[2137],{2491:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-846-truong-vien-truong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-846-truong-vien-truong"}},[n._v("#")]),n._v(" Chương 846: Trương viện trưởng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 846: Trương viện trưởng"),t("br"),t("br"),n._v("Tưởng Thanh Cầm nhưng mà tận mắt chứng kiến qua vị này Trương lão sư tốc độ học tập."),t("br"),t("br"),n._v("Ngày đầu tiên cái gì cũng đều không hiểu, ngày thứ hai Ma âm liền đi đến thất tinh, lại học tập đồng dạng chức nghiệp, cũng tốn hao không được bao dài thời gian."),t("br"),t("br"),n._v("“Phụ tu ta cũng không có lo lắng, mấu chốt là thực lực, Trương sư mới Quy Nhất cảnh, mà khảo hạch lục tinh yêu cầu thấp nhất là Tằm Phong cảnh đỉnh phong...” Triệu Bính Tuất nói."),t("br"),t("br"),n._v("Ngũ tinh danh sư, bao gồm Kiều Thiên cảnh, Quy Nhất cảnh, Đạp Hư cảnh."),t("br"),t("br"),n._v("Muốn muốn khảo hạch lục tinh, tu vi thấp nhất đều muốn đạt tới Tằm Phong cảnh đỉnh phong."),t("br"),t("br"),n._v("Trương sư cùng Vưu Hư tỷ thí thời điểm, đã từng rò rỉ qua tu vi, Quy Nhất cảnh mà thôi, khoảng cách cái này yêu cầu thấp nhất, còn có hai cái đại cấp bậc, ba tháng muốn đột phá... Có thể nói hoàn toàn không có khả năng."),t("br"),t("br"),n._v("“Mấy ngày nay đi ra thời điểm, không cẩn thận đột phá, hiện tại đã là Đạp Hư cảnh!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Không cẩn thận?”"),t("br"),t("br"),n._v("Mọi người trừng to mắt."),t("br"),t("br"),n._v("Người khác đột phá Đạp Hư cảnh, tốn hao vô số tinh lực, đủ loại bảo vật hoa vô số kể, ngươi không cẩn thận đã đột phá, nghe thanh âm còn mang theo oan ức..."),t("br"),t("br"),n._v("Có thể hay không chút nghiêm túc?"),t("br"),t("br"),n._v("Kỳ thật bọn họ thật trách oan Trương Huyền, quả thực là không cẩn thận đột phá, vì thế còn buồn bực một thời gian thật dài."),t("br"),t("br"),n._v("“Đột phá Đạp Hư cảnh?”"),t("br"),t("br"),n._v("Cùng mọi người buồn bực khác biệt, Mi trưởng lão sững sờ tại nguyên chỗ, khóe miệng giật một cái: “Trương sư... Tham gia học viên nhập môn khảo hạch thời điểm, mới Hợp Linh cảnh đỉnh phong, vì cứu người, đột phá Kiều Thiên cảnh...”"),t("br"),t("br"),n._v("Nhập môn khảo hạch thời điểm, hắn nghe học sinh báo cáo, vị này Trương Huyền, vì cứu người, sử dụng bí thuật, theo Hợp Linh cảnh đỉnh phong trực tiếp tăng lên một cái đại cấp bậc, đi đến Kiều Thiên cảnh đỉnh phong."),t("br"),t("br"),n._v("Lúc đó vẫn cảm thấy sẽ đối với thân thể lưu lại tai hoạ ngầm, để hắn tốt dễ xử lý... Làm sao mấy ngày không thấy, đến Đạp Hư cảnh?"),t("br"),t("br"),n._v("“Theo tham gia nhập môn khảo hạch đến bây giờ... Vẫn chưa tới hai mươi ngày chứ?”"),t("br"),t("br"),n._v("Không biết ai đích thì thầm một tiếng."),t("br"),t("br"),n._v("Ngay sau đó tất cả mọi người đều trừng trừng nhìn về phía thanh niên trước mắt, giống như là thấy được một đầu quái vật."),t("br"),t("br"),n._v("Theo khai giảng đến bây giờ, tính toán đâu ra đấy, nửa tháng nhiều một chút, theo Hóa Phàm ngũ trọng Hợp Linh cảnh, trực tiếp đi đến bát trọng Đạp Hư cảnh..."),t("br"),t("br"),n._v("Tăng lên tiếp cận bốn cái đại cấp bậc..."),t("br"),t("br"),n._v("Ngươi đây là tu luyện, vẫn là đùa giỡn?"),t("br"),t("br"),n._v("Liền xem như quán đỉnh, cũng không có nhanh như vậy a!"),t("br"),t("br"),n._v("Ngươi tu luyện, không cần củng cố tu vi ư?"),t("br"),t("br"),n._v("Không cần đủ loại cảm ngộ ư?"),t("br"),t("br"),n._v("Đang đang khiếp sợ, cảm thấy có chút phát điên, liền nghe đến cách đó không xa Trương Huyền than thở một tiếng, một mặt hổ thẹn: “Đúng vậy a, gần đây chủ yếu chỉ riêng kiểm tra phụ tu đi, không có thời gian tu luyện, cứ thế tiến bộ chậm...”"),t("br"),t("br"),n._v("Hắn vốn là định, mười ngày học xong mười cái học viện phụ tu, sau đó dùng năm, sáu ngày thời gian, xung kích đến Thánh vực nhất trọng, nửa cái tháng sau liền có thể rời đi."),t("br"),t("br"),n._v("Kết quả, đều đi qua mười bảy, tám ngày, vẫn chỉ là Đạp Hư cảnh sơ kỳ, phụ tu cũng chỉ có năm loại... Thực sự không có gì tốt đắc ý."),t("br"),t("br"),n._v("Mọi người lần nữa nhoáng một cái, nguyên một đám sắp hộc máu."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Cái này còn gọi chậm?"),t("br"),t("br"),n._v("Ngươi cái này gọi chậm, chúng ta những người này tốn hao lớn mấy chục năm mới đột phá một cái tu vi, chẳng phải chậm cùng cứt chó đồng dạng?"),t("br"),t("br"),n._v("Mặt khác, ngươi hổ thẹn cái gì sức lực a?"),t("br"),t("br"),n._v("Phải hổ thẹn, cũng hẳn là là chúng ta đi!"),t("br"),t("br"),n._v("Mặc dù buồn bực ngực đau nhức, nhưng mọi người cũng biết hắn nói sự thật."),t("br"),t("br"),n._v("Trước mắt vị này, không có trước khi đi ra, bảy ngày thời gian khảo hạch năm loại chức nghiệp, mỗi một loại, đều vượt mức hoàn thành, huyên náo gà bay chó chạy... Không chỉ có như thế, còn tại nhàn rỗi trong lúc đó, giảng bài, sinh tử đấu, thậm chí còn lấy cái Huyền Huyền hội, loại tình huống này, vẫn như cũ theo Kiều Thiên cảnh đỉnh phong, đột phá đến Quy Nhất cảnh đỉnh phong..."),t("br"),t("br"),n._v("Được rồi, không nghĩ, càng nghĩ càng thấy đến khó chịu!"),t("br"),t("br"),n._v("Cùng hắn so sánh, bản thân những người này tu luyện, không phải một cái “Chậm” liền có thể hình dung, quả thực chính là phế vật!"),t("br"),t("br"),n._v("Mặc dù buồn bực, nhưng nghĩ đến cái kia vị cao thâm khó dò lão sư, cũng liền giật mình."),t("br"),t("br"),n._v("Lão sư nghịch thiên, lại thêm bản thân thiên phú lợi hại, nếu như tại giống như bọn họ, cũng liền mất mặt, lại thế nào được xưng tụng siêu cấp thiên tài."),t("br"),t("br"),n._v("“Ba tháng, ta cảm thấy lấy Trương sư bản lĩnh, khảo hạch lục tinh có lẽ rất nhẹ nhàng đi!”"),t("br"),t("br"),n._v("Ngực đau không biết bao lâu, một vị trưởng lão nhịn không được nói."),t("br"),t("br"),n._v("“Ta cũng cảm thấy...” Rất nhanh có người gật đầu."),t("br"),t("br"),n._v("Đối người khác mà nói, ba tháng bế quan liền đi qua, thực lực tăng lên cực kỳ bé nhỏ, có thể đối kẻ trước mắt này tới nói, ai ngờ có thể làm được cái gì?"),t("br"),t("br"),n._v("Thậm chí, đột phá đến Thánh vực, cũng chưa biết chừng!"),t("br"),t("br"),n._v("“Cái kia... Liền chớ do dự, ba ngày sau tổ chức kế vị nghi thức!”"),t("br"),t("br"),n._v("Mộc sư đại thủ vẫy một cái."),t("br"),t("br"),n._v("Danh sư học viện tuyển ra viện trưởng, là oanh động nhất đẳng đế quốc đại sự, không thể qua loa."),t("br"),t("br"),n._v("Định tại ba ngày sau, chính là vì có thời gian chuẩn bị, mời khách quý, bố trí sân bãi."),t("br"),t("br"),n._v("Lại nói, còn muốn trước đem cái này trưởng lão viện cho sửa một cái, nếu không, người khác nhìn thấy đường đường danh sư học viện, cùng gặp phải phá dỡ đội đồng dạng, cười đều sẽ chết cười."),t("br"),t("br"),n._v("Có quyết định, những người khác không nói thêm lời, toàn bộ đều gật đầu đồng ý."),t("br"),t("br"),n._v("Kim Nguyên đỉnh mặc dù có thể bác bỏ, dù sao nhưng mà cái Thánh khí, những người khác không cảm thấy không ổn, nó tiếp tục chen vào nói cũng không có ý nghĩa gì."),t("br"),t("br"),n._v("Dù sao bất kể ai làm viện trưởng, địa vị của nó cũng sẽ không biến."),t("br"),t("br"),n._v("Nhìn thấy mọi người vui vẻ quyết định, Trương Huyền mí mắt xoay loạn."),t("br"),t("br"),n._v("Các ngươi tuyển ta làm viện trưởng, ta người trong cuộc này, còn chưa nói đồng ý đây..."),t("br"),t("br"),n._v("Tử Dương thú dường như nhìn ra ý nghĩ của hắn, nhịn không được mở miệng nói: “Chủ nhân, trở thành viện trưởng, có thể tùy ý tại học viện Tàng Thư khố ra vào, muốn nhìn cái gì liền nhìn cái gì!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),n._v(" Chỉ cần có thể đọc sách, viện trưởng này, làm một chút cũng không sao."),t("br"),t("br"),n._v("Thương nghị hết, chuyện còn lại liền đơn giản, phân công nhiệm vụ, đủ loại quá trình, nói tiếp cận một canh giờ, lúc này mới kết thúc."),t("br"),t("br"),n._v("“Viện trưởng, kế vị nghi thức còn chưa bắt đầu, cho nên, tạm thời ngươi còn không thể lại đến biệt viện, chỉ có thể oan ức tạm thời ở lại bên ngoài...”"),t("br"),t("br"),n._v("Thương nghị hết, Mi trưởng lão tràn đầy áy náy nói."),t("br"),t("br"),n._v("Mặc dù thập đại trường lão cùng Mộc sư đồng ý, còn không có tiếp nhận nghi thức, hắn hiện tại liền không phải chân chính viện trưởng, còn không thể sử dụng quyền lực."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Dù sao cũng liền ba ngày thời gian, cũng không vội vã."),t("br"),t("br"),n._v("Lại hàn huyên một hồi, Trương Huyền lấy khảo hạch lục tinh danh sư, mau chóng tăng cao tu vi làm lý do, đem cái gọi là viện trưởng trách nhiệm, toàn bộ ném cho Triệu viện trưởng, Mi trưởng lão đám người, lúc này mới thở phào nhẹ nhõm: “Ta muốn luyện chế một cái bảy cấp đan dược, có thể hay không mượn Kim Nguyên đỉnh tiền bối dùng một chút?”"),t("br"),t("br"),n._v("Đã qua mười ngày, mặc dù chưa kịp đi xem, nhưng Ngụy Như Yên linh hồn, hẳn đã nhận được tẩm bổ, có thể tiến hành xuống mặt động tác."),t("br"),t("br"),n._v("Bởi vậy, việc cấp bách, là luyện chế thỉnh thoảng đan, chắc hẳn những ngày này đi qua, lỗ đan sư mấy người cũng đem bản thân cho thủ pháp toàn bộ học được."),t("br"),t("br"),n._v("“Khụ khụ... Viện trưởng, Kim Nguyên đỉnh tiền bối là cái luyện khí lô đỉnh, không phải đan lô!”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Triệu Bính Tuất vội vàng nói."),t("br"),t("br"),n._v("Luyện khí lô đỉnh cùng đan lô, khác biệt còn là rất lớn."),t("br"),t("br"),n._v("Ngươi thân là lục tinh luyện khí sư kiêm luyện đan sư, chẳng lẽ không nhìn ra?"),t("br"),t("br"),n._v("“Ta biết, nhưng bảy cấp đan dược uy lực quá lớn, Linh cấp đan lô ta sợ không chịu nổi, cũng chỉ có Kim Nguyên đỉnh tiền bối, loại này Thánh khí, mới có thể làm đến!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Mặc dù nghiêm ngặt dựa theo hắn trình tự đi làm, thỉnh thoảng đan khẳng định có thể luyện được, sẽ không nổ lô, nhưng đi qua mấy lần trước luyện đan, hắn biết trình tự là một chuyện, thi hành lên lại là một chuyện khác."),t("br"),t("br"),n._v("Đổi lại Linh cấp đan lô, một khi không chịu nổi bạo tạc, toàn bộ danh sư học viện, hầu như đều vô dụng."),t("br"),t("br"),n._v("Không dám mạo hiểm như vậy."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Triệu Bính Tuất tràn đầy nghi hoặc: “Đan đỉnh cùng Khí đỉnh, chênh lệch rất lớn, như thế nào luyện chế?”"),t("br"),t("br"),n._v("“Không cần lo lắng, Kim Nguyên đỉnh tiền bối mặc dù là Khí đỉnh, nhưng cũng là phong kín lô đỉnh, chỉ cần ở phía trên, thêm cái chuyển đổi trận pháp, hoàn toàn có thể làm thành đan đỉnh sử dụng!”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("Đan đỉnh, Khí đỉnh khác biệt lớn nhất là trong đó nhiệt độ."),t("br"),t("br"),n._v("Thứ hai cao hơn, hơn nữa nương theo thời gian càng lâu, nhiệt độ càng cực nóng."),t("br"),t("br"),n._v("Cái trước thì nóng lạnh biến hóa cực nhanh, lại càng dễ khống chế."),t("br"),t("br"),n._v("Điểm ấy, chỉ cần thêm một cái trận pháp đi vào, liền có thể giải quyết, không tính là quá khó."),t("br"),t("br"),n._v("“Nếu có nắm chắc, ta cùng tiền bối thương nghị một chút...”"),t("br"),t("br"),n._v("Nghe hắn có thể đem Khí đỉnh đổi thành lô đỉnh sử dụng, Triệu Bính Tuất chần chờ một chút, đang muốn tìm Kim Nguyên đỉnh thương nghị một chút, liền nghe đến đầu này Thánh khí hừ tiếng vang lên."),t("br"),t("br"),n._v("“Ta là đường đường chịu trách nhiệm luyện khí Thánh vật, để cho ta luyện đan, nghĩ cũng đừng nghĩ!”"),t("br"),t("br"),n._v("Luyện khí Thánh khí, có sự kiêu ngạo của chính mình, để nó đi luyện đan, thật giống như để vịt đi sinh trứng gà đồng dạng, có thể đáp ứng mới là lạ!"),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Vẻ mặt xấu hổ, Triệu Bính Tuất vội vàng ôm quyền."),t("br"),t("br"),n._v("“Không cần nói nhảm, tính tình của ta ngươi cũng không phải không biết, còn dám nhiều lời, có tin ta hay không nuốt ngươi, đưa ngươi luyện thành xỉ than đá?”"),t("br"),t("br"),n._v("Lạnh hừ một tiếng, Kim Nguyên đỉnh thanh âm oanh minh, trong giọng nói mang theo khó mà che lấp ngạo khí."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được đối phương không có chút nào đường lùi, sắc mặt trắng nhợt, Triệu Bính Tuất đành phải lắc đầu: “Viện trưởng, cái này... Thực sự hết cách rồi, nếu như tiền bối không đồng ý, coi như ngươi có nhiều hơn nữa thủ đoạn, cũng vô dụng...”"),t("br"),t("br"),n._v("Thánh khí khí linh không phối hợp, muốn sử dụng, tương đương với nằm mơ, một khi hơi có ngoài ý muốn, liền vô cùng có khả năng để dược liệu biến thành tro bụi, hoàn toàn lãng phí!"),t("br"),t("br"),n._v("“Ta xem một chút có thể hay không thương nghị một chút!”"),t("br"),t("br"),n._v("Không để ý tới Triệu Bính Tuất, Trương Huyền mấy bước đi tới Kim Nguyên đỉnh gần."),t("br"),t("br"),n._v("Không thể không nói cái này Thánh khí thân hình rất lớn, so với hắn cao hơn phân nửa, khí tức thần thánh phát ra, cho người ta một loại cực mạnh áp bách."),t("br"),t("br"),n._v("Khó trách thành trở thành danh sư học viện trấn viện chi bảo, thực lực quả thực đáng sợ."),t("br"),t("br"),n._v("Sau khi tấn cấp Tử Dương thú, cũng không là đối thủ."),t("br"),t("br"),n._v("“Muốn cho ta cho ngươi luyện đan? Không có cửa đâu! Ta khuyên ngươi, nghĩ như thế nào làm sao quên mất, nếu không, liền coi như bọn họ đồng ý ngươi làm viện trưởng, ta cũng sẽ đem ngươi luyện thành xỉ than đá...”"),t("br"),t("br"),n._v("Gặp hắn đi tới gần, Kim Nguyên đỉnh hừ lạnh."),t("br"),t("br"),n._v("“Tiền bối, ta tới nhưng mà cùng ngươi thương nghị một chút, không cần thiết tức giận!”"),t("br"),t("br"),n._v("Đi tới gần, Trương Huyền bàn tay nhẹ nhàng tại Kim Nguyên trên đỉnh vuốt ve một chút, lập tức, dạo qua một vòng, vừa nói chuyện, một bên tại trên người đối phương đập mấy lần."),t("br"),t("br"),n._v("“Ngươi nói... Trương sư có thể thuyết phục Kim Nguyên đỉnh tiền bối ư?”"),t("br"),t("br"),n._v("Gặp hắn đi qua, Mi trưởng lão đi tới Triệu viện trưởng gần."),t("br"),t("br"),n._v("“Rất khó, Kim Nguyên đỉnh tiền bối tính khí ngươi cũng không phải không biết, năm đó lão viện trưởng mặt mũi cũng không cho, huống chi Trương sư, muốn muốn thuyết phục... Gần như không có khả năng!”"),t("br"),t("br"),n._v("Triệu Bính Tuất lắc đầu."),t("br"),t("br"),n._v("“Ta cũng cảm thấy như vậy...”"),t("br"),t("br"),n._v("Than thở một tiếng, Mi trưởng lão đang muốn nói tiếp, chỉ thấy trước mắt Kim Nguyên đỉnh đột nhiên phát ra một tiếng chói tai oanh minh."),t("br"),t("br"),n._v("Lập tức, thân thể khổng lồ bỗng nhiên nhoáng một cái, dán chặt cách đó không xa Trương Huyền, tựa như chó xù thấy chủ nhân: “Đỉnh Đỉnh gặp qua chủ nhân! Chủ nhân, ngươi để cho ta luyện cái gì, ta liền luyện cái gì, không phải là đan dược ấy ư, ngươi muốn luyện chế bao nhiêu?”"),t("br"),t("br"),n._v("“...” Mi trưởng lão."),t("br"),t("br"),n._v("“...” Triệu viện trưởng."),t("br"),t("br"),n._v("“...” Mọi người khác."),t("br"),t("br"),n._v("Đỉnh Đỉnh a~:) )"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-854"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-856"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);