(window.webpackJsonp=window.webpackJsonp||[]).push([[2138],{2493:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-847-ngoc-phi-nhi-moi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-847-ngoc-phi-nhi-moi"}},[n._v("#")]),n._v(" Chương 847: Ngọc Phi Nhi mời")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 847: Ngọc Phi Nhi mời"),t("br"),t("br"),n._v("Kim Nguyên đỉnh, là chân chính Thánh khí, danh sư học viện thủ hộ giả, cao ngạo tới cực điểm, danh sư học viện các đời viện trưởng, mặc dù có mời nó xuất thủ quyền lợi, nhưng lại chưa bao giờ để hắn nhận qua chủ."),t("br"),t("br"),n._v("Coi như trước đó lão viện trưởng, trước khi mất tích, đột phá lục tinh ràng buộc, đạt đến thất tinh, nhưng đối với nó vẫn như cũ không chút nào biện pháp."),t("br"),t("br"),n._v("Cái tên này ngạo khí không nói, còn đặc biệt lười, ba ngày hai đầu đi ngủ, muốn cho hắn hỗ trợ luyện chế vũ khí, quả thực liền là không thể nào."),t("br"),t("br"),n._v("Hiện tại muốn cho hắn hỗ trợ luyện đan, mọi người vốn cho rằng tuyệt không thể nào làm được, nằm mơ đều không nghĩ tới, biến hóa nhanh như vậy."),t("br"),t("br"),n._v("Còn Đỉnh Đỉnh... Đỉnh em gái ngươi ah!"),t("br"),t("br"),n._v("Ngươi là đường đường Thánh khí, học viện thủ hộ thần... Có thể có chút tôn nghiêm ư?"),t("br"),t("br"),n._v("Lục Phong càng là nhanh muốn điên rồi."),t("br"),t("br"),n._v("Trước đó hắn mời đối phương thời điểm, chảnh chứ cùng ngồi chém gió tự kỷ giống như, còn tuyên bố vài phút muốn đem hắn luyện thành xỉ than đá, hiện tại được rồi, nắm thân đỉnh đi nhờ Trương Huyền, một bộ sợ hắn tức giận bộ dáng..."),t("br"),t("br"),n._v("Người cùng người chênh lệch cũng quá lớn đi!"),t("br"),t("br"),n._v("“Giải quyết!” Đem Kim Nguyên đỉnh thu phục, Trương Huyền cười nhìn qua."),t("br"),t("br"),n._v("Đối phương liền xem như Thánh khí, khẳng định cũng có thiếu hụt, Thiên Đạo thư viện ra tay, trực tiếp tìm tới, sau đó lại hứa hẹn giúp hắn tăng lên phẩm giai, thu phục mười phần đơn giản."),t("br"),t("br"),n._v("“Viện trưởng... Lợi hại!”"),t("br"),t("br"),n._v("Vẻ mặt đỏ lên, nhẫn nhịn nửa ngày, Triệu Bính Tuất lúc này mới giơ ngón tay cái lên."),t("br"),t("br"),n._v("Không phục không được, vị viện trưởng này mỗi lần đều có thể ngoài dự liệu, để cho người ta khiếp sợ, có lẽ, về sau thật có thể dẫn đầu danh sư học viện, càng đi càng xa, thậm chí trở thành bốn đại học viện đứng đầu."),t("br"),t("br"),n._v("“Chủ nhân, cái tên này, một lòng muốn hại ngươi, có muốn hay không để đem hắn luyện thành xỉ than đá?”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh bị thu phục, bắt đầu thay chủ nhân suy nghĩ, một cỗ hung ác ý niệm, nhìn chằm chằm cách đó không xa Lục Phong."),t("br"),t("br"),n._v("Cái tên này trước đó đắc ý bộ dạng, nó nhưng mà nhìn thấy trong mắt, chủ nhân thân vì sư tổ, có thể không so đo cháu trai khuyết điểm, nó có thể không làm được."),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Lục Phong dọa đến mặt như giấy trắng."),t("br"),t("br"),n._v("Kim Nguyên đỉnh là danh sư học viện thủ hộ Thánh khí, địa vị tương đương với lão tổ, thật đem hắn luyện chế thành xỉ than đá, ngay cả Mộc sư đều nói không nên lời cái gì."),t("br"),t("br"),n._v("“Ai là ngươi tiền bối? Gọi gia gia!” Kim Nguyên đỉnh hừ lạnh."),t("br"),t("br"),n._v("“Ta...” Suýt chút nữa không khóc, Lục Phong vội vàng nhìn về phía Mộc sư, muốn cho hắn hỗ trợ chủ trì công đạo."),t("br"),t("br"),n._v("Mộc sư lắc đầu: “Kim Nguyên đỉnh tiền bối nói không sai, ngươi vu hãm danh sư, kẻ xấu danh tiếng, ta sẽ đem chuyện ngày hôm nay, kỹ càng sửa sang lại thành sách, báo cáo tổng bộ, không có gì bất ngờ xảy ra, ngươi luyện đan học viện viện trưởng thân phận khó giữ được, thậm chí danh sư tư chất đều có thể bị thủ tiêu!”"),t("br"),t("br"),n._v("“Hủy bỏ danh sư tư chất?”"),t("br"),t("br"),n._v("Vẻ mặt một chút trắng bệch, Lục Phong chỉ cảm thấy hai chân như nhũn ra, suýt chút nữa té ngã trên đất."),t("br"),t("br"),n._v("Hủy bỏ hắn luyện đan viện trưởng, nằm trong dự liệu, một khi hủy bỏ danh sư tư chất, phấn đấu một đời, đều đem trôi theo nước chảy."),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Mộc sư than thở một tiếng: “Danh sư, tự thân dạy dỗ, làm gương sáng cho người khác, ngươi vì bản thân tư dục, không điều tra rõ sự thật liền tùy ý vu oan, đã vi phạm với ‘Sư’ yêu cầu cùng đạo đức, chuyện này, quái chính ngươi, ta cũng không giúp được!”"),t("br"),t("br"),n._v("Nói xong, lắc đầu."),t("br"),t("br"),n._v("Nếu là nhóm người mình, tin vào hắn, không cho Trương sư phân biệt cơ hội, khẳng định đã ủ ra đại họa."),t("br"),t("br"),n._v("Đem hắn tiếp tục lưu lại, cũng sẽ trở thành danh sư bên trong con sâu làm rầu nồi canh, còn không bằng khai trừ."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Vẻ mặt trở nên trắng bệch, vị này một canh giờ trước còn quát tháo phong vân, cảm thấy viện trưởng chức vị đã tới tay lục tinh thượng phẩm danh sư, nửa bước thần thức cảnh cường giả, giờ phút này, trở nên hồn bay phách lạc, lại không có chút tinh thần."),t("br"),t("br"),n._v("Cùng Trương Huyền đọ sức, hắn thua, thất bại thảm hại."),t("br"),t("br"),n._v("“Trần Thừa Tuần, nghe tin lời của người, không phân phải trái, từ hôm nay trở đi, huỷ bỏ trận pháp viện trưởng chức vụ, chờ tổng bộ xử lý!”"),t("br"),t("br"),n._v("Xử lý xong Lục Phong, Mộc sư lần nữa nhìn về phía Trần Thừa Tuần."),t("br"),t("br"),n._v("Mặc dù vị này Trần viện trưởng không quá lỗi lớn sai, nhưng cùng Lục Phong vu hãm Trương sư, tội danh không nhỏ."),t("br"),t("br"),n._v("“Vâng!” Không dám phản bác, lục ngồi thuần gật đầu."),t("br"),t("br"),n._v("“Còn Đổng Hân, thân là danh sư học viện học viên, không cố gắng học tập, vì tân sinh làm làm gương mẫu, ngược lại, tham dự học viện tranh đấu, trở thành Lục Phong hãm hại Trương sư công cụ; Tại không phân biệt sự thật dưới tình huống, báo cáo học viện, suýt chút nữa ủ thành sai lầm lớn... Từ hôm nay trở đi, hủy bỏ học viên thân phận... Nói cách khác, ngươi bị khai trừ!”"),t("br"),t("br"),n._v("Mộc sư lần nữa vẫy tay."),t("br"),t("br"),n._v("Hiện tại Trương Huyền oan khuất rửa sạch, càng là thành học viện viện trưởng, những này người hãm hại hắn, nhất định phải xử lý, nếu không, về sau ai cũng muốn hãm hại, còn thể thống gì?"),t("br"),t("br"),n._v("Viện trưởng liền muốn có viện trưởng tôn nghiêm cùng thân phận, không phải bất luận kẻ nào đều có thể bêu xấu."),t("br"),t("br"),n._v("Vị này Đổng Hân mặc dù nói là tận mắt nhìn thấy, nhưng Lục Phong, Trần Thừa Tuần sở dĩ chịu đến xử phạt, đều bởi vì tin tức của nàng, thân là danh sư, tại không phân rõ sự tình thật giả dưới tình huống, liền lên báo, bản thân liền có vấn đề."),t("br"),t("br"),n._v("Nhất định phải chịu đến nghiêm trị."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Vẻ mặt u ám, Đổng Hân ôm quyền."),t("br"),t("br"),n._v("Vốn cho rằng tìm được Trương Huyền tay cầm, có thể để cho hắn ăn một bình, kết quả... Không chỉ thành chắt trai bối phận, còn bị khai trừ."),t("br"),t("br"),n._v("Mặc dù rời đi học viện, nàng vẫn như cũ là lục tinh danh sư, nhưng... Những năm này tại học viện kinh doanh quan hệ, tất cả đều biến thành tro bụi, thậm chí có cái này chỗ bẩn, sau này tấn cấp con đường, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("“Chúng ta bốn người, vẫn là... Hồ Yêu Yêu thông minh!”"),t("br"),t("br"),n._v("Than thở một tiếng."),t("br"),t("br"),n._v("Tứ đại học được, cạnh tranh với nhau, bây giờ suy nghĩ một chút, vẫn là Hồ Yêu Yêu thông minh nhất, thành Trương sư tọa hạ học đồ, lắc mình biến hoá, chính là viện trưởng môn sinh... Cái thân phận này, về sau bất kể tu luyện vẫn là khảo hạch đẳng cấp cao hơn, đều đem xuôi gió xuôi nước, những người khác rốt cuộc so ra kém."),t("br"),t("br"),n._v("“Trương viện trưởng, xử lý như vậy, được không?”"),t("br"),t("br"),n._v("Xử lý xong những người này, Mộc sư xoay đầu lại."),t("br"),n._v("Hắn không xưng hô sư thúc, mà là Trương viện trưởng, tỏ rõ lập trường, vừa đến, không bao biện làm thay, thứ hai, không làm việc thiên tư."),t("br"),t("br"),n._v("“Cái này... Được rồi!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Kỳ thật dựa theo ý nghĩ của hắn, đem ba trên thân người linh thạch vơ vét một phen, không xử trí đều được, dù sao cũng làm ầm ĩ không nổi, hiện tại hủy bỏ danh sư tư chất hủy bỏ tư chất, khai trừ khai trừ... Ngược lại nói cái gì đều không cách nào nói."),t("br"),t("br"),n._v("“Ta còn có việc xử lý, liền đi về trước... Đỉnh Đỉnh, đi theo ta đi!”"),t("br"),t("br"),n._v("Biết tiếp tục tiếp tục chờ đợi, sẽ chỉ có chút xấu hổ, Trương Huyền cổ tay rung lên, đem Kim Nguyên đỉnh thu vào trữ vật giới chỉ, cùng mọi người cáo từ."),t("br"),t("br"),n._v("Trở lại chỗ ở, để Tôn Cường đi huyền huyền sẽ đem lỗ đan sư đám người mời đến, bản thân thì đi luyện đan sư học viện."),t("br"),t("br"),n._v("Lúc trước cùng tổng bộ liên hệ, đối phương nói muốn đem thỉnh thoảng thảo đưa tới, tính toán thời gian cũng không còn nhiều lắm."),t("br"),t("br"),n._v("Đi tới luyện đan sư học viện, xe nhẹ đường quen, quả nhiên phát hiện đối phương đáp ứng dược liệu vài ngày trước là đến."),t("br"),t("br"),n._v("Nghe nói đưa người, muốn phải đưa cho hắn, chờ một ngày, đều không đợi được, đành phải hậm hực mà trở lại."),t("br"),t("br"),n._v("Đem thỉnh thoảng thảo cầm tới, đang muốn về biệt viện, đi xem Ngụy Như Yên tình huống, chỉ thấy Lạc Thất Thất tiến lên đón."),t("br"),t("br"),n._v("“Lão sư, ngươi về đến rồi!”"),t("br"),t("br"),n._v("Thấy hắn xuất hiện, Lạc Thất Thất ánh mắt sáng lên."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Nữ hài sau lưng, theo mấy người, chính là Ngọc Phi Nhi, Hình Viễn, Ngô Chấn, Diệp Tiền đám người."),t("br"),t("br"),n._v("Lúc này Ngọc Phi Nhi, Hình Viễn đám người, thực lực đều có cực lớn đột phá, giống như Lạc Thất Thất, đều đạt đến Quy Nhất cảnh."),t("br"),t("br"),n._v("Xem ra Triệu Bính Tuất tẩy cân phạt tủy, đối bọn hắn hiệu quả rất lớn, trong khoảng thời gian này bế quan tu luyện, tiến bộ nổi bật."),t("br"),t("br"),n._v("“Rất không tệ ah!”"),t("br"),t("br"),n._v("Trương Huyền cười cười."),t("br"),t("br"),n._v("Mặc dù trước đó cùng những người này cũng có chút mâu thuẫn, nhưng đã trải qua Địa Cung sống chết, đã có khác tình cảm, lúc này nhìn thấy bọn họ tấn cấp, trong lòng cũng là hết sức vui mừng cùng cao hứng."),t("br"),t("br"),n._v("“Trương Huyền, ta nói qua đi tới sẽ trả ngươi cá cược, đây là ba cái linh thạch thượng phẩm, nhiều, xem như những ngày này đền bù!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi tiến về phía trước một bước, đưa đến một cái hộp ngọc, một loạt ba cái linh thạch thượng phẩm, linh khí xao động, khí tức kinh người."),t("br"),t("br"),n._v("Nàng cùng vị này Trương Huyền đánh cược, một mực không thắng nổi, thậm chí chính mình cũng thành tỳ nữ, mặc dù... Chưa hề làm qua tỳ nữ chức trách, cá cược vẫn là đưa tới."),t("br"),t("br"),n._v("Nàng thân là công chúa, trở lại hoàng cung, tu vi càng là đột phá đến Quy Nhất cảnh, mấy cái linh thạch thượng phẩm, vẫn là rất dễ dàng lấy được."),t("br"),t("br"),n._v("“Vậy ta liền không khách khí...”"),t("br"),t("br"),n._v("Đối với linh thạch loại vật này, Trương Huyền càng nhiều càng tốt, lật bàn tay một cái, liền thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("“Trương sư, ngươi tại học viện sự tình, chúng ta đều nghe nói, rất nhiều viện trưởng, không... Làm khó dễ ngươi chứ?”"),t("br"),t("br"),n._v("Hình Viễn cùng Ngô Chấn nhìn qua."),t("br"),t("br"),n._v("Bọn họ xuất quan, nghe được nhiều nhất chính là vị này Trương sư sự tình, huyên náo mấy cái học viện hoàn toàn tê liệt, thậm chí còn cùng một vị phó viện trưởng tiến hành sinh tử đấu... Có thể nói, vị này Trương sư, ngắn ngủi nửa cái tháng sau tại học viện huyên náo động tĩnh, so với bọn hắn ở đây nhiều năm đều lớn."),t("br"),t("br"),n._v("“Không có!” Nhìn ra đối phương là thật tâm thật ý quan tâm, Trương Huyền cười cười."),t("br"),t("br"),n._v("“Vậy là tốt rồi!”"),t("br"),t("br"),n._v("Nghe nói học viện không làm khó, mọi người thở phào nhẹ nhõm, Ngọc Phi Nhi đi tới gần, răng cắn lấy trên môi, chần chờ một lát: “Phụ hoàng ta muốn muốn gặp ngươi!”"),t("br"),t("br"),n._v("“Phụ hoàng? Hồng Viễn đế quốc, Hoàng đế bệ hạ?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Ngọc Phi Nhi là Lục công chúa, nàng phụ hoàng, tự nhiên là Hồng Viễn đế quốc đương triều bệ hạ."),t("br"),t("br"),n._v("“Vâng!” Ngọc Phi Nhi gật đầu."),t("br"),t("br"),n._v("“Lúc nào?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Mời ngươi... Tham gia dạ tiệc hôm nay!” Ngọc Phi Nhi nói."),t("br"),t("br"),n._v("“Mời ta làm gì?” Trương Huyền tràn đầy kỳ quái."),t("br"),t("br"),n._v("Hắn chính là một cái tứ tinh danh sư, mặc dù tại học viện náo ra chút động tĩnh, còn không đến mức để một cái đế quốc Hoàng đế bệ hạ, tự mình mời chứ?"),t("br"),t("br"),n._v("“Hoài Vương gia tự mình tiễn ngươi một tòa biệt thự, lại muốn thu ta làm nô tỳ, tại học viện huyên náo động tĩnh lớn như vậy, còn đem toàn bộ linh tài các đều dời trống... Phụ hoàng tự nhiên muốn nhìn một chút!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi khẽ nói."),t("br"),t("br"),n._v("Thân làm nhất đẳng đế quốc Hoàng đế bệ hạ, vị này Trương Huyền còn kém đem toàn bộ Đế đô đều phá hủy, nếu như lại không nghe nói, thật sự quá ngu ngốc."),t("br"),t("br"),n._v("Huống chi, đối phương thu nữ nhi của hắn làm nô tỳ, coi như Ngọc Phi Nhi không nói, thân là bệ hạ, cũng có thể điều tra ra."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền vò đầu: “Được rồi, có điều, ta muốn đi luyện đan, nếu như hôm nay có rảnh rỗi, sẽ đi qua một chuyến...”"),t("br"),t("br"),n._v("Đã tại Hồng Viễn đế quốc cư xử, vị này nhất đẳng đế quốc Hoàng đế bệ hạ, liền trốn tránh không khỏi, đi qua gặp một lần, cũng không tính là gì."),t("br"),t("br"),n._v("“Quyết định như vậy đi, tiệc tối chờ ngươi, địa điểm ngay tại hoàng cung, đây là thư mời!”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Ngọc Phi Nhi nhẹ nhàng cười một tiếng, lấy ra một cái thư mời đưa tới."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền tiện tay tiếp nhận, để vào nhẫn."),t("br"),t("br"),n._v("Cùng Ngọc Phi Nhi, Lạc Thất Thất đám người cáo từ, Trương Huyền mới vừa trở lại phủ đệ, chỉ thấy Tôn Cường một mặt gấp gáp tiến lên đón."),t("br"),t("br"),n._v("“Thiếu gia, không xong... Ngươi trong viện trồng trọt gốc kia Thập Diệp hoa, không biết làm sao... Nhan sắc toàn bộ cũng thay đổi!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-855"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-857"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);