(window.webpackJsonp=window.webpackJsonp||[]).push([[2139],{2495:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-848-nguy-nhu-yen-hien-trang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-848-nguy-nhu-yen-hien-trang"}},[n._v("#")]),n._v(" Chương 848: Ngụy Như Yên hiện trạng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 848: Ngụy Như Yên hiện trạng"),t("br"),t("br"),n._v("“Màu sắc thay đổi?” Trương Huyền sững sờ, vội vàng hướng Thập Diệp hoa phương hướng đi đến."),t("br"),t("br"),n._v("Thập Diệp hoa, là Thánh vực dược liệu, thành thục về sau, hoa nở mười đóa, mỗi đóa đều có không giống nhau màu sắc, đối ứng người ba hồn bảy phách."),t("br"),t("br"),n._v("Ngụy Như Yên yếu ớt linh hồn, chính là thả ở trên đây tẩm bổ."),t("br"),t("br"),n._v("Hắn đã đáp ứng Ngụy Trường Phong, muốn chiếu cố nữ nhi của hắn, đột nhiên biến màu sắc, chuyện gì xảy ra?"),t("br"),t("br"),n._v("Rất mau tới đến giữa sân, lập tức nhìn thấy, trong trận pháp ở giữa đến mười phần diễm lệ đóa hoa, tất cả đều biến thành màu sắc đen nhánh, tựa như có người tại trên đóa hoa, giội lên mực nước."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Sắc mặt trầm xuống."),t("br"),t("br"),n._v("Thập Diệp hoa, mười loại màu sắc, mang theo thai nghén linh hồn năng lực, hiện tại toàn bộ biến thành màu đen, rất hiển nhiên xuất hiện biến cố."),t("br"),t("br"),n._v("Ngụy Trường Phong đã qua đời, với tư cách thiếu chủ, đáp ứng đối phương, nếu là cứu không được nữ nhi của hắn, còn có loại nào mặt mũi mệnh danh danh sư?"),t("br"),t("br"),n._v("“Thiếu gia trước khi đi ra, để cho ta chiếu cố đóa hoa này, ta mỗi ngày đều tưới một chút địa mạch linh dịch, mấy ngày nay dáng dấp đều rất tốt, thậm chí buổi sáng thiếu gia trở về thời điểm, ta xem cũng không có vấn đề gì, liền một canh giờ trước... Đột nhiên màu sắc biến thành đen, ta cẩn thận kiểm tra, trận pháp không có cái gì người động, cũng không người đến qua, cụ thể làm sao tình huống, cũng không rõ ràng...”"),t("br"),t("br"),n._v("Tôn Cường mặt mũi lo lắng."),t("br"),t("br"),n._v("“Đột nhiên biến thành đen?”"),t("br"),t("br"),n._v("Trương Huyền cau mày, đem bố trí ở chung quanh trận pháp mở ra, nhìn kỹ một cái, quả thực không người đến qua."),t("br"),t("br"),n._v("Không người đến, liền không khả năng làm cho phá hư, hắn đối Tôn Cường lại mười phần tin tưởng, đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Đi tới Thập Diệp hoa gần, Minh Lý Chi Nhãn nhúc nhích, nhìn sang."),t("br"),t("br"),n._v("Cái này gốc Thập Diệp hoa, trước đó bị hắn địa mạch linh dịch cùng Thiên Đạo chân khí tẩm bổ, nhanh chóng thành thục, sức sống tràn trề, tiên diễm ướt át, mà giờ khắc này, không chỉ đóa hoa biến thành đen, lá cây cũng biến thành hơi khô héo, dường như mắc bệnh gì chứng, bất cứ lúc nào cũng sẽ tử vong."),t("br"),t("br"),n._v("Nhất là trần lộ ra ngoài rễ cây, đã xuất hiện thối rữa cục diện."),t("br"),t("br"),n._v("“Tại sao có thể như vậy?”"),t("br"),t("br"),n._v("Địa mạch linh dịch tẩm bổ, lại thêm Tụ Linh trận hội tụ linh khí, cái này gốc Thánh dược có lẽ càng sống càng tốt mới là, làm sao sẽ thối rữa thành bộ dáng này?"),t("br"),t("br"),n._v("Ngón tay đưa tới, trong đầu thở nhẹ “Thiếu hụt”, một quyển sách xuất hiện ở trước mắt, vội vàng mở ra."),t("br"),t("br"),n._v("Nhìn thoáng qua, vẻ mặt một cái trở nên khó coi: “Lại là trúng độc!”"),t("br"),t("br"),n._v("Căn cứ thư viện thiếu hụt, cái này gốc Thập Diệp hoa sở dĩ như vậy, cũng không phải là nguyên nhân khác, mà là... Trúng độc!"),t("br"),t("br"),n._v("“Ngụy Như Yên Tiên Thiên độc hồn thể, linh hồn đều có mang kịch độc, Thập Diệp hoa mặc dù có thể tẩm bổ linh hồn, nhưng... Cùng hồn phách của nàng, chạm phải thời gian dài, một cách tự nhiên cũng liền trúng phải độc, bị độc thành như vậy! Nếu không có địa mạch linh dịch, cùng linh khí thoải mái, chỉ sợ sớm đã chết rồi, căn bản không kiên trì được mười ngày...”"),t("br"),t("br"),n._v("Xuất hiện loại tình huống này căn do tại trong óc bên trong dạo qua một vòng, Trương Huyền lông mày nhăn thành mụn nhọt."),t("br"),t("br"),n._v("Ngụy Như Yên Tiên Thiên độc thể quá lợi hại, liền linh hồn hắn bên trong Tiên Thiên thai độc đều có thể áp chế, đủ thấy đáng sợ!"),t("br"),t("br"),n._v("Thập Diệp hoa là tẩm bổ linh hồn Thánh dược, nhưng cùng lợi hại như vậy độc thể tiếp xúc, cũng có chút không chịu nổi, xuất hiện loại biến cố này."),t("br"),t("br"),n._v("Trước đó, hắn chỉ nghĩ tẩm bổ linh hồn, cứu chữa Ngụy Như Yên, đưa nàng Tiên Thiên hồn thể quên chuyện, không nghĩ tới, lại lợi hại như thế!"),t("br"),t("br"),n._v("Một khi Thập Diệp hoa tử vong, Ngụy Như Yên linh hồn, chẳng khác nào biến thành cây không rễ, chẳng mấy chốc sẽ tiêu tán!"),t("br"),t("br"),n._v("May mắn Tôn Cường phát hiện kịp lúc, nếu không, người không có cứu sống, chỉ sợ sẽ còn làm hồn phi phách tán."),t("br"),t("br"),n._v("“Xem trước một chút linh hồn tẩm bổ dạng gì!”"),t("br"),t("br"),n._v("Không dám do dự, tinh thần khẽ động, Vu hồn ly thể, cúi đầu hướng ẩn chứa tại đóa hoa bên trong hồn phách nhìn sang."),t("br"),t("br"),n._v("Một lát sau, sắc mặt tái xanh."),t("br"),t("br"),n._v("Thập Diệp hoa tẩm bổ cái khác linh hồn, thời gian mười ngày, tuyệt đối có thể để cho yếu ớt muốn diệt hồn phách, tẩm bổ có thể tỉnh lại, mà Ngụy Như Yên Tiên Thiên hồn thể, thực sự quá cường đại, đem cái này gốc Thánh dược độc chết không nói, tẩm bổ bên trên, căn bản không có quá trợ giúp lớn!"),t("br"),t("br"),n._v("Nói cách khác, Ngụy Như Yên thời khắc này linh hồn trạng thái, mặc dù so mười ngày trước, mạnh mẽ một chút, có thể... Vẫn như cũ vô cùng suy yếu, khoảng cách có thể tỉnh lại, còn kém rất lớn một đoạn."),t("br"),t("br"),n._v("“Dựa theo tình huống nàng bây giờ, muốn tỉnh lại... Ít nhất phải mấy chục vạn gốc Thập Diệp hoa mới có thể làm đến!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt."),t("br"),t("br"),n._v("Dựa theo Ngụy Như Yên tình huống hiện tại đến phân tích, để hắn tỉnh lại lời nói, không có có vài chục vạn cây Thập Diệp hoa, căn bản không có khả năng."),t("br"),t("br"),n._v("Một gốc Thập Diệp hoa, liền huyên náo long trời lở đất, đem y sư học viện đều phá hủy, mấy chục vạn gốc... Làm sao có thể làm đến?"),t("br"),t("br"),n._v("Có thể nói, muốn nhờ vào Thập Diệp hoa cứu người, đã là không thể nào!"),t("br"),t("br"),n._v("“Trừ phi... Sử dụng cấp bậc cao hơn Thánh dược!”"),t("br"),t("br"),n._v("Lông mày vung lên."),t("br"),t("br"),n._v("Thập Diệp hoa, mặc dù là Thánh dược, nhưng cũng chỉ là vừa mới bước vào Thánh vực, dựa theo nhân loại phân cấp lời nói, cùng Thánh vực nhất trọng không có gì khác biệt, kháng độc tính, sức chịu đựng, tự nhiên muốn kém chút."),t("br"),t("br"),n._v("Nếu như sử dụng cấp bậc cao hơn Thánh dược, có lẽ hiệu quả sẽ lớn hơn rất nhiều, cũng không cần khó khăn như vậy."),t("br"),t("br"),n._v("Nhưng mà... Một gốc Thập Diệp hoa, đều như thế khó tìm, huống chi hắn hắn còn sống dược vật!"),t("br"),t("br"),n._v("“Đem linh hồn của nàng, thu nhập thân thể lại nói...”"),t("br"),t("br"),n._v("  Biết tạm thời nghĩ những thứ này cũng vô dụng, để Tôn Cường đem Ngụy Như Yên thân thể mang tới, lúc này mới Vu hồn khẽ động, đem hắn hồn phách theo Thập Diệp hoa bên trong rút ra đi ra."),t("br"),t("br"),n._v("Rời đi đóa hoa hồn phách, giống như trước đây suy yếu, hai mắt nhắm nghiền, để hắn trở lại thân thể, quả nhiên không có tỉnh lại dấu hiệu."),t("br"),t("br"),n._v("“Còn tiếp tục luyện chế Đoạn Tục đan, đan dược luyện tốt, trước tẩm bổ thân thể, thân thể mạnh mẽ, linh hồn cũng có thể tùy theo mạnh lên... Sau đó, lại nghĩ biện pháp tìm kiếm cấp bậc cao hơn Thánh dược!”"),t("br"),t("br"),n._v("Nhìn thấy nữ hài vẫn như cũ bất tỉnh, Trương Huyền xoa xoa lông mày."),t("br"),t("br"),n._v("Có thể để cho vô số lục tinh y sư bó tay toàn tập, Ngụy Trường Phong hao phí tất cả tinh lực... Ngụy Như Yên bệnh tình quả nhiên không có trong tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("Vốn là hắn cho rằng, tìm một gốc còn sống tẩm bổ linh hồn Thánh dược liền có thể để cho tỉnh lại, hiện tại xem ra, vẫn là nghĩ quá đơn giản."),t("br"),t("br"),n._v("Không nói cái khác, liền nói nàng cái này Tiên Thiên có độc hồn phách, liền để vô số Thánh dược, đều khó mà chống chọi."),t("br"),t("br"),n._v("Vu hồn bay ra, lợi dụng hồn phách của mình lần nữa tẩm bổ đối phương một hồi, cảm thấy sinh mệnh lực khôi phục một chút, sẽ không tạm thời tử vong, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Cái này gốc Thập Diệp hoa, mặc dù không có đem hắn cứu tỉnh, nhưng thành công hóa giải bất cứ lúc nào cũng sẽ tử vong khốn cảnh, chỉ cần đem Đoạn Tục đan luyện tốt, thân thể sức sống đi lên, cho dù tạm thời bất tỉnh, cũng nhất định có thể chống đến lại tìm đến cấp bậc cao hơn Thánh dược."),t("br"),t("br"),n._v("“Thiếu gia, Lỗ đan sư bọn họ đã ở đại sảnh chờ!”"),t("br"),t("br"),n._v("Để Tôn Cường phái người tiếp tục chiếu cố Ngụy Như Yên, Trương Huyền thì hướng đại sảnh đi đến."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Đi vào đại điện, gặp hắn đi tới, Lỗ đan sư đám người vội vàng đứng dậy."),t("br"),t("br"),n._v("“Chư vị không cần khách khí, tìm mấy vị đến đây mục đích, không cần ta nhiều lời, cũng đều hiểu, ta hy vọng có thể mượn giúp tay của các ngươi, luyện chế ra bảy cấp Đoạn Tục đan!”"),t("br"),t("br"),n._v("Ngồi xuống, Trương Huyền đi thẳng vào vấn đề."),t("br"),t("br"),n._v("Mọi người mặc dù sớm có suy đoán, nhưng giờ phút này nghe được hắn chính miệng thừa nhận, vẫn là nguyên một đám trầm mặc xuống."),t("br"),t("br"),n._v("Bọn họ đều là lục tinh đỉnh phong luyện đan sư, luyện chế cấp sáu đan dược, không có vấn đề gì, nhưng luyện chế bảy cấp... Hơn nữa còn là không có phối hợp qua mấy người, thực sự quá khó."),t("br"),t("br"),n._v("“Trương sư, không phải chúng ta chối từ, năng lực của chúng ta có hạn, sợ... Khó mà hoàn thành trách nhiệm!”"),t("br"),t("br"),n._v("Lỗ đan sư lắc đầu."),t("br"),t("br"),n._v("Bọn họ không phủ nhận vị này Trương sư, tại luyện đan bên trên trình độ, nhưng... Bảy cấp đan dược, vẫn là bài danh phía trên, hoàn toàn không có khả năng!"),t("br"),t("br"),n._v("“Chư vị không cần lo lắng, cũng không cần có cái gì gánh nặng trong lòng, chỉ cần dựa theo của ta trình tự, từng bước hoàn thành, hẳn là sẽ không xuất hiện vấn đề quá lớn!” Thấy mấy người không có có lòng tin, Trương Huyền nói."),t("br"),t("br"),n._v("Cũng khó trách, bọn họ nhưng mà lục tinh luyện đan sư, tiếp xúc nhiều nhất cũng chính là cấp sáu đan dược."),t("br"),t("br"),n._v("Đoạn Tục đan, bảy cấp khá cao, đã vượt qua có thể tưởng tượng phạm vi."),t("br"),t("br"),n._v("“Chúng ta vẫn là không dám...” Lỗ đan sư lắc đầu."),t("br"),t("br"),n._v("“Đúng vậy a, chúng ta niên cấp lớn như vậy, xung kích qua nhiều lần, đều không thành công, lại chưa bao giờ phối hợp qua, sợ rất khó hoàn thành!”"),t("br"),t("br"),n._v("Mấy vị khác đan sư cũng đồng thời lắc đầu."),t("br"),t("br"),n._v("Thấy mấy người liền một chút lòng tin đều không có, Trương Huyền cau mày."),t("br"),t("br"),n._v("Luyện đan, không chỉ muốn có kỹ xảo, tinh, khí, thần thiếu một thứ cũng không được, nhất là loại này Đoạn Tục đan, có thể chữa trị tứ chi, cụt tay lại nối tiếp, ẩn chứa trong đó nồng đậm sinh cơ, nếu như luyện đan người đều là loại thái độ này, coi như trình tự hoàn toàn đúng, cũng khẳng định không cách nào thành công!"),t("br"),t("br"),n._v("Cái này đã không phải luyện đan thuật vấn đề, mà là tinh thần cùng tín niệm."),t("br"),t("br"),n._v("Thật giống như lúc trước hắn khảo hạch nhất tinh luyện đan sư lúc, Trần Tiêu đan sư bởi vì thân thể nguyên nhân, luyện chế đan dược ẩn chứa tử khí, sau khi phục dụng chẳng những không có hiệu quả, sẽ còn tẩu hỏa nhập ma đồng dạng."),t("br"),t("br"),n._v("Bảy cấp đan dược, đã đạt đến Thánh cấp, dược vật thậm chí có thể sinh ra thuộc về mình thần trí, luyện đan sư tinh thần không đủ... Làm sao có thể thành công?"),t("br"),t("br"),n._v("Dược liệu, hắn chỉ có một bộ, một khi thất bại, không có cơ hội thứ hai, có thể nói, lần này luyện chế, tuyệt không thể sai sót!"),t("br"),t("br"),n._v("“Đã chư vị không có có lòng tin, cái kia... Trương mỗ liền đắc tội!”"),t("br"),t("br"),n._v("Thấy mọi người thái độ như thế, Trương Huyền biết trong thời gian ngắn khẳng định khó khôi phục lòng tin, mà bản thân lại không có thời gian trì hoãn, cổ tay khẽ đảo, một đống dược liệu rơi trên mặt đất, đang là trước kia mấy vị này luyện đan học viện túc lão hỗ trợ chuẩn bị."),t("br"),t("br"),n._v("Làm xong những này, hít sâu một hơi, trực tiếp mở miệng: “Không cần sự tình khác, hiện tại liền bắt đầu luyện đan, cái này là dược liệu, Lỗ đan sư, chiết xuất cỏ linh lăng kim ngân hoa dược dịch, Khâu đan sư dùng lửa nhỏ chân tu hoa, Lưu đan sư...”"),t("br"),t("br"),n._v("Tốc độ nói rất nhanh, mang theo đặc thù ý vị, mỗi người đều an bài một việc."),t("br"),t("br"),n._v("Mấy vị luyện đan sư đang muốn chối từ, đã cảm thấy trong đầu một ngất, thân thể kìm lòng không được dựa theo đối phương phân phó, bắt đầu rèn luyện dược liệu."),t("br"),t("br"),n._v("“Sư ngôn thiên bẩm?”"),t("br"),t("br"),n._v("Nhìn xem không nghe sai khiến bàn tay cùng động tác, mọi người tất cả đều con ngươi co rụt lại."),t("br"),t("br"),n._v("Giờ phút này ngu nữa cũng biết, trước mắt vị này đối bọn hắn thi triển sư ngôn thiên bẩm!"),t("br"),t("br"),n._v("Bọn họ đều là lục tinh thượng phẩm danh sư, bất kể thực lực vẫn là tâm cảnh khắc độ tại danh sư học viện đều dựa vào trước, thế mà trực tiếp trúng chiêu, đổi lại trước đó nghĩ cũng không dám nghĩ!"),t("br"),t("br"),n._v("“Tâm cảnh của hắn khắc độ... Chỉ sợ đã vượt qua 21!”"),t("br"),t("br"),n._v("Nguyên một đám tất cả đều hoảng sợ."),t("br"),t("br"),n._v("Có thể một hơi mệnh mọi người, hơn nữa để sư ngôn thiên bẩm có hiệu quả, nói rõ trước mắt vị này tâm cảnh khắc độ, so với bọn hắn đều mạnh hơn, chỉ sợ đã đạt đến... Thất tinh danh sư cấp bậc!"),t("br"),t("br"),n._v("Một cái chừng hai mươi tuổi tân sinh, tâm cảnh đi đến 21... Coi như Thánh Nhân quý tộc, cũng không có như thế nghịch thiên thiên tài đi!"),t("br"),t("br"),n._v("Có lẽ, bọn họ những này chưa hề phối hợp qua người thật có thể liên hợp lại cùng nhau, sáng tạo ra kỳ tích!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-856"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-858"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);