(window.webpackJsonp=window.webpackJsonp||[]).push([[2143],{2498:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-851-xich-huynh-qua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-851-xich-huynh-qua"}},[n._v("#")]),n._v(" Chương 851: Xích Huỳnh quả")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 851: Xích Huỳnh quả"),t("br"),t("br"),n._v("Địa Long cùng bầu trời giao tiếp địa phương, là một dòng sông, dọc theo học viện chung quanh, chậm rãi chảy xuôi, uốn lượn đến không biết tên bóng tối, một cái không nhìn thấy cuối cùng."),t("br"),t("br"),n._v("Dọc theo thân rồng nhìn lại, hướng về phía trước lan tràn sân nhỏ, có hồ nước, hoa cỏ cây cối tô điểm, dường như nhiều hơn từng mảnh nhỏ tường vân, phụ trợ Địa Long, phá không bay lượn."),t("br"),t("br"),n._v("Nghiên cứu hơn mười phút, Minh Lý Chi Nhãn sử dụng có chút đầu váng mắt hoa, lúc này mới nhịn không được lắc đầu, ngừng lại."),t("br"),t("br"),n._v("Xem ra, đối phương nói tới tam tinh, cũng không phải là nói tinh thần, càng không phải là địa hình bố cục, chỉ riêng nhìn như vậy, là không nhìn ra, cần phải từ từ nghiên cứu mới được."),t("br"),t("br"),n._v("Đã biết Địa Long là cái gì, chỉ cần hạ điểm công phu, tam tinh hẳn là cũng có thể tìm ra."),t("br"),t("br"),n._v("Ngô Dương Tử đã nói ra bảo tàng chỗ trên mặt đất, tự nhiên là hi vọng hậu nhân tìm tới, mà không phải có thể ẩn tàng."),t("br"),t("br"),n._v("Thu hồi tinh thần, đang định bay xuống đi, lần sau có cơ hội lại nghiên cứu, chỉ thấy một bóng người thẳng tắp bay lượn mà tới."),t("br"),t("br"),n._v("“Hồ Yêu Yêu? Lúc này tới đây làm gì?”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn không sợ bóng tối, một cái liền nhận ra được, có điều, đối phương dường như không thấy được không trung hắn, nhanh chóng vọt làm được thân ảnh, đi tới phế tích một cái chỗ cao, tìm kiếm khắp nơi."),t("br"),t("br"),n._v("Mặc dù thực lực đạt đến Tằm Phong cảnh đỉnh phong, nhưng muốn trong bóng đêm tìm tới một người vẫn là không rất dễ dàng, cổ tay khẽ đảo, một cái Dạ Minh Châu xuất hiện, mượn nhờ trong đó ánh sáng, khắp nơi quan sát."),t("br"),t("br"),n._v("“Trương lão sư...”"),t("br"),t("br"),n._v("Nhịn không được la lên."),t("br"),t("br"),n._v("Thân là học đồ, đã đổi giọng xưng hô lão sư."),t("br"),t("br"),n._v("Đang muốn trả lời, Trương Huyền đột nhiên con mắt rơi vào trong tay nàng Dạ Minh Châu bên trên, hào quang nhỏ yếu, phản chiếu tại cách đó không xa trong hồ nước, tựa như một ngôi sao."),t("br"),t("br"),n._v("“Chẳng lẽ...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, vội vàng hướng sân nhỏ mấy cái hồ nước nhìn lại, quả nhiên thấy được Dạ Minh Châu ánh sáng, chiếu ở trong nước, kêu gọi kết nối với nhau, tựa như thiên hà treo ngược."),t("br"),t("br"),n._v("Từ trên nhìn xuống, phế tích hình thành Địa Long, uyển như phi hành ở trong trời đêm, bị tinh thần tô đậm, tự mang một cỗ khí thế."),t("br"),t("br"),n._v("“Tòa phủ đệ này, đi qua sửa chữa lớn, tổng cộng tám cái hồ nước, địa hình đã cùng hơn hai ngàn năm trước không đồng dạng... Tam tinh Địa Long, cần tìm ra ba viên tinh vị trí, nhiều ngược lại không đúng!”"),t("br"),t("br"),n._v("Ngô Dương Tử nhắn lại nói là tam tinh Địa Long, nếu như Dạ Minh Châu phản chiếu tại trong hồ nước ánh sáng là cái gọi là “Tinh”, như vậy hiện nay có tám cái tinh nhiều."),t("br"),t("br"),n._v("Không là đối phương nói sai, mà là biệt viện đi qua sửa chữa lớn, trước kia hình dạng mặt đất đã không tồn tại nữa."),t("br"),t("br"),n._v("“Bất quá, như vậy có thể làm khó người khác, khó không được ta...”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Cái khác người tu luyện, coi như Mộc sư tới, muốn theo cái này tám chỗ trong hồ nước tuyển ra hai ngàn năm trước liền tồn tại ba chỗ, cũng mười phần phiền phức, hắn không giống, Minh Lý Chi Nhãn nhúc nhích, hồ nước có phải hay không niên đại xa xưa, một cái liền có thể nhìn ra."),t("br"),t("br"),n._v("Không lâu sau, liền đã xác định ba cái."),t("br"),t("br"),n._v("Đem trong hồ nước ba cái điểm sáng, nối liền cùng một chỗ, sân nhỏ một cái giao lộ, xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Vị trí này, đang tại phủ đệ chủ điện phía trước, một khỏa to lớn cây tùng, đứng sừng sững trên đó, xem ra không biết đã trải qua bao nhiêu năm tháng."),t("br"),t("br"),n._v("“Xem ra liền tại gốc này cây tùng phía dưới!”"),t("br"),t("br"),n._v("Xác định địa điểm, Trương Huyền thở phào nhẹ nhõm, nhẹ nhàng cười một tiếng, từ không trung bay xuống, lộ tại Hồ Yêu Yêu trước mặt."),t("br"),t("br"),n._v("“Tìm ta chuyện gì?”"),t("br"),t("br"),n._v("“Trương lão sư, ngươi vì tìm bảo tàng, cũng quá liều mạng đi...”"),t("br"),t("br"),n._v("Chỉ hướng chung quanh, Hồ Yêu Yêu khắp khuôn mặt là ngớ ra."),t("br"),t("br"),n._v("Ngươi không phải mới vừa lại đi vào sao? Làm sao vậy thì biến thành như vậy?"),t("br"),t("br"),n._v("Coi như vì tìm Ngô Dương Tử lưu lại bảo tàng, cũng không trở thành, đem trọn cái phủ đệ, đều làm cho thành phế tích đi!"),t("br"),t("br"),n._v("“Không phải là bởi vì bảo tàng, là một chút ngoài ý muốn...”"),t("br"),t("br"),n._v("Trương Huyền xạm mặt lại: “Ngươi không phải là nghe nói phủ đệ sụp đổ, đặc biệt tới nhìn xem a!”"),t("br"),t("br"),n._v("“Không phải, là... Có chuyện, muốn mời lão sư hỗ trợ!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu vội nói."),t("br"),t("br"),n._v("Mặc dù Ngô Dương Tử có lưu lại bảo tàng, nhưng nhiều năm như vậy, nàng vô số tiền bối đều tìm, cái gì tung tích cũng không phát hiện, nàng cũng không có cảm thấy mình có thể tìm tới, cũng không chú ý."),t("br"),t("br"),n._v("“Chuyện gì?”"),t("br"),t("br"),n._v("Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Hồ Yêu Yêu mặc dù ở trước mặt hắn không nổi lên được bất luận cái gì sóng gió, nhưng ở trong học viện tuyệt đối là hô phong hoán vũ cường nhân, lại thêm thủ đoạn rất nhiều, còn có nàng không giải quyết được sự tình?"),t("br"),t("br"),n._v("“Nói thật cho ngươi biết đi, ta cùng Đổng Hân, Long Thương Nguyệt cùng Tiết Chân Dương, phát hiện một chỗ bảo tàng, chỉ muốn lấy được, có hai mươi phần trăm tỷ lệ, giúp chúng ta đột phá đến Bán Thánh...”"),t("br"),t("br"),n._v("Hồ Yêu Yêu cắn răng: “Vốn là, chúng ta bốn người muốn một mình đem bảo vật lấy đi, nhưng mà... Bên trong hai đầu thủ hộ gia hỏa thực sự quá mạnh, chúng ta không phải là đối thủ, cho nên... Hy vọng có thể đạt được ngươi trợ giúp!”"),t("br"),t("br"),n._v("“Ta?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Chuyện này, lúc trước hắn nghe lén nói chuyện thời điểm, nghe qua một lần, Đổng Hân từng nói qua, đại khái tháng này thành thục, tính toán thời gian, quả thực không sai biệt lắm."),t("br"),t("br"),n._v("“Không sai, ngươi muốn thực lực có thực lực, muốn thủ đoạn có thủ đoạn, nếu như nói ai còn có thể giúp chúng ta nhận được món đồ kia, ngoại trừ ngươi, chúng ta thực sự nghĩ không ra...”"),t("br"),t("br"),n._v("Hồ Yêu Yêu lắc đầu."),t("br"),t("br"),n._v("Bốn người bọn họ ở trong học viện mặc dù hô phong hoán vũ, nhưng gặp được cái tên này, liền như là gặp ma, biến đồ đệ biến đồ đệ, biến đồ tôn biến đồ tôn, Đổng Hân rất, biến thành chắt trai không nói, còn trực tiếp bị khai trừ."),t("br"),t("br"),n._v("Long Thương Nguyệt một mực không có hành động, ngược lại may mắn thoát khỏi xuống."),t("br"),n._v(" Vốn là, bốn người có ba cái cùng hắn từng có khúc mắc, thực sự không muốn tìm hắn hỗ trợ, nhưng món đồ kia thành thục sắp đến, một khi không lấy, liền sẽ cho cái kia hai đầu đại gia hỏa lấy đi, đến lúc đó, cái gì cũng không chiếm được, còn muốn đột phá ràng buộc, liền khó hơn!"),t("br"),t("br"),n._v("Suy đi nghĩ lại, vẫn là quyết định, mời một cái."),t("br"),t("br"),n._v("Cái tên này mặc dù là cái phá hư vương, nhưng chân chính năng lực ở nơi đó bày biện, nếu không, cũng không trở thành để bọn hắn bốn đại cao thủ, liên tục hao tổn ba cái."),t("br"),t("br"),n._v("“Là cái gì? Có thể để cho đột phá Bán Thánh tỷ lệ tăng lên hai mươi phần trăm?”"),t("br"),t("br"),n._v("Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Đi qua Phong Thánh đài, nhìn qua vô số đột phá tâm đắc, biết theo Tằm Phong cảnh đến Bán Thánh độ khó, tăng lên hai mươi phần trăm, đối bọn hắn loại thiên tài này tới nói, tỷ lệ đã phi thường lớn."),t("br"),t("br"),n._v("Cắn môi, Dạ Minh Châu phụ trợ bên dưới, Hồ Yêu Yêu da thịt tuyết trắng cùng yêu nghiệt dáng người, rung động lòng người: “Thánh vực dược liệu... Xích Huỳnh quả!”"),t("br"),t("br"),n._v("“Xích Huỳnh quả?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền con mắt nhịn không được tỏa ánh sáng."),t("br"),t("br"),n._v("Thứ này hắn nghe nói qua, quả thực là một loại Thánh vực trái cây, toàn thân đỏ tươi, một khi thành thục, có thể phát ra huỳnh quang, tựa như đom đóm, chiếu sáng bốn phía, mười phần xinh đẹp."),t("br"),t("br"),n._v("Thứ này hấp thu tinh hoa nhật nguyệt sinh trưởng, trăm năm thành thục một lần, mười phần thưa thớt, hơn nữa, không cách nào nhân tạo bồi dưỡng, tuyệt đối là chỉ có thể ngộ mà không thể cầu bảo vật."),t("br"),t("br"),n._v("Nuốt loại này Xích Huỳnh quả, mặc dù không cách nào đột phá Bán Thánh, nhưng quả thực có thể gia tăng đột phá tỷ lệ, khó trách bốn người động tâm, liền xem như hắn, đều tâm tư sục sôi."),t("br"),t("br"),n._v("Hắn tu vi hiện tại mặc dù chỉ là Đạp Hư cảnh sơ kỳ, khoảng cách Bán Thánh còn chênh lệch hơn một cái cấp bậc, nhưng... Lấy tốc độ tu luyện của hắn, chỉ cần tìm được công pháp, cắn răng khổ tu hai, ba canh giờ liền tích lũy đủ."),t("br"),t("br"),n._v("Tại Phong Thánh đài, tổng kết vô số kinh nghiệm, đột phá có lẽ độ khó không lớn, nhưng có thứ này, hiệu quả khẳng định càng tốt."),t("br"),t("br"),n._v("Dù sao, hắn tu luyện Thiên Đạo công pháp, đột phá cần linh khí là lượng lớn, mặc dù tại Vân Vụ lĩnh nhận được không ít địa mạch linh dịch, nhưng đến lúc đó có thể không thể sử dụng, đều cũng khó nói."),t("br"),t("br"),n._v("Dù sao, địa mạch linh dịch linh khí độ tinh thuần, vẫn là chênh lệch một chút, có thứ này nơi tay, có thể nói... Bán Thánh có hi vọng!"),t("br"),t("br"),n._v("“Không sai, chúng ta đi qua quan sát, cây này tổng cộng kết mười khỏa Xích Huỳnh quả, chỉ cần ngươi có thể giúp chúng ta nhận được... Mọi người có thể chia đều, mỗi người hai khỏa!” Hồ Yêu Yêu nói."),t("br"),t("br"),n._v("“Mỗi người hai khỏa?” Trương Huyền đỡ lấy cái cằm: “Tốt, ta đáp ứng!”"),t("br"),t("br"),n._v("Hắn là lâm thời gia nhập, có thể theo bình quân phân phối, cầm tới hai cái cũng cũng không tệ lắm."),t("br"),t("br"),n._v("Đương nhiên, cụ thể làm sao chia, khẳng định muốn xem cống hiến lớn nhỏ, đến lúc đó lại thương nghị cũng không muộn."),t("br"),t("br"),n._v("“Đáp ứng?”"),t("br"),t("br"),n._v("Vốn cho rằng đối phương sẽ công phu sư tử ngoạm, nhiều muốn mấy cái, không nghĩ tới sảng khoái như vậy đồng ý, Hồ Yêu Yêu có chút không dám tin tưởng."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật đầu: “Lúc nào xuất phát?”"),t("br"),t("br"),n._v("“Chỗ kia cách nơi này có chút xa, ngồi Đổng Hân thú sủng, đại khái cần ba ngày thời gian mới có thể đến đạt! Nói cách khác, một cái qua lại, không sai biệt lắm muốn... Bảy, tám ngày thời gian!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu nói: “Nếu như không có chuyện gì lời nói, chúng ta có thể hiện tại liền đi!”"),t("br"),t("br"),n._v("“Bảy, tám ngày?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Hắn ba ngày sau còn muốn kế vị trong sân dài, có cái thịnh đại nghi thức, cần tham gia, khẳng định là đi không được."),t("br"),t("br"),n._v("“Đúng vậy a, đây là Xích Huỳnh quả tại tính toán tốt thời điểm thành thục mới có thể làm đến, nếu không, chỉ sợ còn muốn ở nơi đó các loại mấy ngày, hoàn toàn thành thục mới có thể hái...” Hồ Yêu Yêu nói."),t("br"),t("br"),n._v("“Còn phải đợi?”"),t("br"),t("br"),n._v("Cứ tính toán như thế đến, chẳng phải muốn vượt qua mười ngày nửa tháng?"),t("br"),t("br"),n._v("Thực sự quá chậm!"),t("br"),t("br"),n._v("Lâu như vậy, cũng có thể xung kích Tòng Thánh, chỉ vì Bán Thánh, thực sự không có lời."),t("br"),t("br"),n._v("Hơn nữa, chủ yếu nhất là chậm trễ sự tình."),t("br"),t("br"),n._v("“Ba ngày sau, học viện tân viện trưởng lên ngôi lễ, hiện tại đi lời nói, khẳng định sẽ chậm trễ...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“A, chuyện này ta nghe nói, lão viện trưởng sau khi mất tích, viện trưởng vị trí một mực không công bố, Mộc sư hôm nay tới đây, chính là vì việc này, nhưng mà không biết viện trưởng rơi vào ai trên đầu... Theo ta suy đoán, không phải Lục Phong viện trưởng, chính là Triệu Bính Tuất viện trưởng! Hai người bọn họ khả năng lớn nhất.”"),t("br"),t("br"),n._v("Lên tiếng, Hồ Yêu Yêu lắc đầu, môi đỏ nhếch lên: “Bất quá, cái này cùng chúng ta có quan hệ gì, dù sao lão sư đều không phải là học viện học sinh, còn quản hắn viện trưởng làm gì?”"),t("br"),t("br"),n._v("Trương Huyền cùng Lục Phong viện trưởng huyên náo như thế lớn, đuổi ra học viện, liền chỗ ở đều không có, đường đường yêu nghiệt biết hội trưởng làm sao không hiểu biết!"),t("br"),t("br"),n._v("Ngươi nói ngươi đều không phải là học viện học sinh, còn quan tâm ai làm viện trưởng làm gì?"),t("br"),t("br"),n._v("Cái gì lên ngôi nghi thức, cùng ngươi có nửa xu quan hệ?"),t("br"),t("br"),n._v("“Cái này...” Trương Huyền cười khổ."),t("br"),t("br"),n._v("Thật là có quan hệ, đồng thời quan hệ rất lớn."),t("br"),t("br"),n._v("“Nhanh lên làm quyết định đi, Xích Huỳnh quả ngay tại mấy ngày nay thành thục, nếu như đi muộn ta sợ không kịp!”"),t("br"),t("br"),n._v("Gặp hắn còn suy nghĩ gì viện trưởng kế vị lễ, Hồ Yêu Yêu lắc đầu."),t("br"),t("br"),n._v("“Như vậy đi, đêm nay giờ Tý xuất phát, cũng đừng ngồi Đổng Hân thú sủng, tốc độ không đủ nhanh, ngồi của ta đi, như vậy trong ba ngày hẳn là có thể đánh cái qua lại!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Hắn hiện tại còn muốn đi tham gia yến hội, dò xét tra một chút Thánh vực Bồ Đề thụ có thể hay không cứu chữa Ngụy Như Yên, sau đó mới có thể ra cửa."),t("br"),t("br"),n._v("Đổng Hân thú sủng, nhiều nhất cùng nàng tu vi tương đương, Tằm Phong cảnh mà thôi, tốc độ, ngồi A Tử lời nói, ba ngày lộ trình, nửa ngày nên có thể đi đến."),t("br"),t("br"),n._v("Có thể tiết kiệm không ít thời gian."),t("br"),t("br"),n._v("“Của ngươi thú sủng? Đầu kia Tử Dực Thiên Hùng thú?” Hồ Yêu Yêu nhướng mày: “Tốc độ chỉ sợ còn muốn chậm đi!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-859"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-861"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);