(window.webpackJsonp=window.webpackJsonp||[]).push([[2145],{2499:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-853-yen-hoi-phan-2"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-853-yen-hoi-phan-2"}},[n._v("#")]),n._v(" Chương 853: Yến hội (phần 2)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 853: Yến hội (phần 2)"),t("br"),t("br"),n._v("Chính là Lạc Thất Thất."),t("br"),t("br"),n._v("“Ngươi tới thật đúng lúc!”"),t("br"),t("br"),n._v("Thấy nàng tới, Trương Huyền ánh mắt sáng lên, truyền âm qua: “Ngô Dương Tử tiền bối bảo vật ta tìm được!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đem trước hộp sắt cùng ba kiện bảo vật lấy ra ngoài."),t("br"),t("br"),n._v("Lạc Thất Thất cùng hắn cùng một chỗ nhận được truyền thừa, thậm chí cái trước vẫn là đối phương thân truyền, có quyền biết bảo vật là cái gì, thậm chí chia sẻ."),t("br"),t("br"),n._v("“Tìm được?”"),t("br"),t("br"),n._v("Lạc Thất Thất sững sờ, lập tức hướng trong tay hắn nhìn lại."),t("br"),t("br"),n._v("“Kim tinh huyền thạch?” Ánh mắt sáng lên, Lạc Thất Thất dường như không thể tin được."),t("br"),t("br"),n._v("“Ngươi biết vật này?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Thứ này hắn mặc dù tại thư tịch bên trên nhìn qua, nhưng chưa từng thấy qua vật thật, bởi vậy Ngô Dương Tử không giải thích, hắn đều không rõ ràng, trước mắt cô gái này, chỉ một cái liền nhận ra, chẳng lẽ nhãn lực so với chính mình còn lợi hại hơn?"),t("br"),t("br"),n._v("Phải biết luyện khí bên trên, hắn hết đối đứng tại Hồng Viễn đế quốc đỉnh phong nhất, coi như Triệu Bính Tuất, vị này luyện khí học viện viện trưởng, đều còn kém rất rất xa."),t("br"),t("br"),n._v("“Ta từng gặp một lần, bất quá... Chỉ có lớn chừng hột đào, liền muốn giá... Không ít, như thế lớn, quá trân quý...”"),t("br"),t("br"),n._v("Lạc Thất Thất nhịn không được nói."),t("br"),t("br"),n._v("“Ngươi gặp qua?” Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Dù chỉ là lớn chừng hột đào, cũng nói vị này Lạc Thất Thất gia thế quả thực không đơn giản."),t("br"),t("br"),n._v("“Đúng vậy a!” Lạc Thất Thất nhẹ gật đầu, lại đem còn lại hai dạng đồ vật nhìn một lần, âm thầm may mắn, bản thân không có luyện sai."),t("br"),t("br"),n._v("“Bản này chính xác bí tịch ngươi cầm trước, lúc rảnh rỗi giúp ngươi sửa chữa một cái, càng thích hợp ngươi thể chất!”"),t("br"),t("br"),n._v("Đem chính xác Ngũ Diệu Kim Thân, tiện tay ném cho đối phương, Trương Huyền nói tiếp: “Còn cái này địa đồ, ngươi có thể nhận biết là văn tự gì?”"),t("br"),t("br"),n._v("“Chưa từng thấy qua!” Lạc Thất Thất lắc đầu, một mặt mê man."),t("br"),t("br"),n._v("“Như vậy đi, thứ này, trước lưu tại ta chỗ này, chờ ta nghiên cứu rõ ràng là cái gì, tìm tới địa đồ miêu tả địa phương, mang ngươi cùng đi!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu: “Còn kim tinh huyền thạch, mấy ngày nay nghĩ biện pháp hòa tan, cho ngươi luyện chế một chuôi vũ khí!”"),t("br"),t("br"),n._v("“Đa tạ lão sư!”"),t("br"),t("br"),n._v("Lạc Thất Thất gật đầu, trong mắt tràn đầy cảm kích."),t("br"),t("br"),n._v("Cái này ba món đồ, đồng dạng so đồng dạng trân quý, bản thân vị lão sư này nhưng không có chút nào giấu diếm, đủ thấy nhân phẩm."),t("br"),t("br"),n._v("“Đúng rồi, lão sư, ta tới là tìm ngươi đi tham gia yến hội...”"),t("br"),t("br"),n._v("Không lại thảo luận bảo vật sự tình, Lạc Thất Thất lúc này mới muốn lên mục đích của mình, vội vàng nói."),t("br"),t("br"),n._v("Ngọc Phi Nhi mời tham gia tiệc tối, đưa nàng cùng Hình Viễn đám người cùng nhau mời, sợ lão sư không đi, lại nghe nói phủ đệ sụp đổ tin tức, cố ý tới một chuyến."),t("br"),t("br"),n._v("“Đi thôi!” Muốn từ bản thân còn muốn đi hoàng cung nhìn xem cái gọi là Thánh vực Bồ Đề thụ, Trương Huyền cũng không chối từ, nhẹ gật đầu."),t("br"),t("br"),n._v("Rời đi phế tích, hai người ngồi lên Lạc Thất Thất sớm liền chuẩn bị xong xe ngựa, bay lượn mà ra."),t("br"),t("br"),n._v("Không lâu sau, liền đi tới hoàng cung trước mặt."),t("br"),t("br"),n._v("Không hổ là nhất đẳng đế quốc hoàng cung, nguy nga cao ngất, khí thế kinh người, cao lớn trước cửa cung, hai hàng binh sĩ đứng thẳng, mỗi một cái đều nắm giữ Hóa Phàm lục trọng Kiều Thiên cảnh thực lực, mấy trăm vị tổ hợp lại với nhau, cho người ta một loại cực mạnh cảm giác áp bách."),t("br"),t("br"),n._v("Hoàng cung nội bộ, đủ loại trận pháp lấp lóe, thấp nhất đều đạt đến lục tinh sơ kỳ, không biết bố cục, tùy tiện xông vào, tất nhiên sẽ khốn vào trong đó, lại khó chạy ra."),t("br"),t("br"),n._v("Từng bước cơ quan, coi như Thánh vực nhất trọng cường giả, cũng không dám tùy tiện đặt chân."),t("br"),t("br"),n._v("Có thư mời, không có người ngăn cản, tại một tên hộ vệ dẫn đầu bên dưới, một đường hướng về phía trước, không lâu sau đi tới một tòa cung điện to lớn trước."),t("br"),t("br"),n._v("“Bệ hạ chính là ở đây cử hành yến hội...”"),t("br"),t("br"),n._v("Lạc Thất Thất chỉ về phía trước."),t("br"),t("br"),n._v("“Đúng rồi, đây là cái gì nội dung yến hội? Vì sao phải mời chúng ta?” Lúc này mới nhớ tới, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Ngươi không biết?”"),t("br"),t("br"),n._v("t r"),t("br"),n._v("u y e n c u a ｔ u i . v n  Thấy lão sư cái gì cũng không biết, liền tới tham gia yến hội, Lạc Thất Thất không còn gì để nói."),t("br"),t("br"),n._v("“Ngọc Phi Nhi cho ta thư mời thời điểm, ngươi liền ở bên cạnh, không nói gì, ta làm sao biết?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Sờ một cái cái trán, Lạc Thất Thất tràn đầy im lặng."),t("br"),t("br"),n._v("Chính mình cái này lão sư cái gì cũng tốt, chính là EQ quá thấp."),t("br"),t("br"),n._v("Nhân gia đường đường công chúa, mời ngươi tới tham gia yến hội, bất kể nói thế nào, đều muốn sớm hỏi thăm một chút đi, coi như không hỏi thăm, cũng hẳn là sớm chuẩn bị một cái, có chút tư thái đi... Kết quả, không đi gọi hắn, chỉ sợ còn tại trong phế tích quanh quẩn một chỗ, cái gì cũng không biết."),t("br"),t("br"),n._v("“Là Ngọc Phi Nhi... Sinh nhật yến hội!”"),t("br"),t("br"),n._v("Lạc Thất Thất nói: “Bệ hạ đem trong đế đô tuổi trẻ tài tuấn, cùng bạn tốt của nàng, đều xin đi qua!”"),t("br"),t("br"),n._v("“Sinh nhật yến hội?” Trương Huyền nheo mắt."),t("br"),t("br"),n._v("“Đúng vậy a, ngươi sẽ không liền lễ vật đều không chuẩn bị chứ?” Xem hướng lão sư, Lạc Thất Thất một mặt bất đắc dĩ."),t("br"),t("br"),n._v("“Chuẩn bị cái gì?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Các ngươi lại không có nói là sinh nhật yến hội, cái gì cũng không biết lại tới, làm sao có thể chuẩn bị lễ vật?"),t("br"),t("br"),n._v("Lại nói, nữ hài tử thích gì, hoàn toàn không hiểu, suốt ngày vội vàng cùng con thỏ giống như, cũng không có rảnh đi tìm."),t("br"),t("br"),n._v("“Cái này...” Lạc Thất Thất lắc đầu."),t("br"),t("br"),n._v("Coi như ngươi không biết là sinh nhật yến hội, đường đường Hoàng đế bệ hạ mời, cũng muốn chuẩn bị vài thứ, gia tăng chút cảm tình đi, tay không liền đến... Đoán chừng cũng liền bản thân vị lão sư này, mới có thể làm được."),t("br"),n._v("“Như vậy đi, ta chỗ này có chút bảo bối, không được, ngươi cầm trước...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Lạc Thất Thất lấy ra một chút nữ hài tử ưa thích bảo vật."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, chỉ thấy trên tay của nàng thêm ra bảy, tám dạng đồ vật."),t("br"),t("br"),n._v("Có lớn như vậy bảo châu, tài liệu trân quý chế tạo kẹp tóc, mùi đặc biệt túi thơm... Mỗi một cái đều mười phần trân quý, có giá trị không nhỏ."),t("br"),t("br"),n._v("“Không cần! Dù sao cũng không muốn tham gia, thực sự không được, rời đi chính là, không tính là gì đại sự!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn không có muốn tham gia cái gì sinh nhật yến hội, đến hoàng cung, chỉ là muốn nghiên cứu một chút, Thánh vực Bồ Đề thụ có thể hay không cứu chữa Ngụy Như Yên thôi, nếu như đối phương bởi vì không có lễ vật mà khinh mạn, rời đi chính là, không tính là gì."),t("br"),t("br"),n._v("“Rời đi?”"),t("br"),t("br"),n._v("Nhìn thấy lão sư vẻ mặt thành thật, không giống nói đùa, Lạc Thất Thất đành phải gật đầu: “Tốt a!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi là bạn tốt của nàng, hai người ở chung hồi lâu, biết tính khí cùng bản tính, cũng sẽ không để ý lễ vật gì bất lễ vật, chỉ cần Trương sư người đến, là đủ rồi."),t("br"),t("br"),n._v("Nhấc chân hướng trước mắt đại điện đi đến."),t("br"),t("br"),n._v("Cao ngất điện đường, trang trí tráng lệ, con mắt chỗ đến, cho người ta một loại ung dung hoa quý cảm giác, thật dày thảm, chừng nửa thước nhiều dày, đi lên, xốp mà có co dãn."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, hướng nhìn bốn phía, chỉ thấy đại điện trong ngoài che kín đủ loại trận pháp, có phòng ngự, có tiến công, càng nhiều hơn chính là gia cố."),t("br"),t("br"),n._v("“Cấp sáu đỉnh phong Ổn Cố trận pháp, cấp sáu đỉnh phong Bàn Thạch trận pháp, cấp sáu đỉnh phong Thành Tường Hậu Trọng trận pháp... Làm nhiều như vậy trận pháp ở đây làm gì?”"),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền nháy con mắt, tràn đầy không rõ ràng cho lắm."),t("br"),t("br"),n._v("Cái này đại điện trận pháp, phòng ngự tiến công mặc dù cũng có một chút, nhưng càng nhiều hơn chính là củng cố trận pháp."),t("br"),t("br"),n._v("To lớn như vậy hoàng cung kiến trúc, cơ bản đều là đứng đầu nhất thợ thủ công kiến tạo mà thành, vững chắc bên trên không thể nghi ngờ, coi như lớn hơn nữa chấn động đều khó mà sụp đổ, bây giờ lại vô cớ thêm nhiều như vậy củng cố trận pháp, để cho người ta nhìn ra quái lạ."),t("br"),t("br"),n._v("“Được rồi, dù sao cũng chuyện không liên quan đến ta...”"),t("br"),t("br"),n._v("Lắc đầu, không nghĩ nhiều nữa."),t("br"),t("br"),n._v("Dù sao hắn chính là tới tham gia yến hội, cái yến hội này sảnh, làm nhiều hơn nữa trận pháp, cùng hắn cũng không quan hệ."),t("br"),t("br"),n._v("“Thất Thất, Trương sư, các ngươi đã tới!”"),t("br"),t("br"),n._v("Còn chưa đi đi vào, chỉ thấy Hình Viễn cùng Ngô Chấn tiến lên đón."),t("br"),t("br"),n._v("Hai vị này hôm nay đều mặc lấy mới tinh danh sư bào, xem ra chuyên môn ăn mặc một phen, mười phần có tinh thần, khí chất bất phàm."),t("br"),t("br"),n._v("“Yến hội bắt đầu sao?”"),t("br"),t("br"),n._v("Lạc Thất Thất hỏi."),t("br"),t("br"),n._v("“Còn không có đây, người còn chưa tới đủ...” Hình Viễn cười nói."),t("br"),t("br"),n._v("“Vậy là tốt rồi!” Thấy không đến hết, Lạc Thất Thất thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Đi theo hai người sau lưng, đi vào đại điện."),t("br"),t("br"),n._v("Toàn bộ điện đường chừng bảy, tám trăm mét vuông lớn nhỏ, chỉ có bốn phía bốn cây cột chống đỡ, nóc phòng vô số Dạ Minh Châu bao phủ, ánh sáng loá mắt, tựa như ban ngày."),t("br"),t("br"),n._v("Tìm chỗ ngồi ngồi xuống, lúc này mới phát hiện, trong phòng, đã tới bảy, tám người, đều hai mươi tuổi, quần áo lộng lẫy, khí độ bất phàm."),t("br"),t("br"),n._v("Chỉ nhìn một cách đơn thuần dung mạo cùng cử chỉ, liền biết xuất thân không thấp."),t("br"),t("br"),n._v("“Đây đều là đế quốc quyền quý hậu bối...” Hình Viễn thấp giọng."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Kỳ thật biết là sinh nhật yến hội, hắn liền hiểu được."),t("br"),t("br"),n._v("Hoàng đế bệ hạ mời thanh niên tài tuấn tới, làm không cẩn thận, là muốn cho Ngọc Phi Nhi tuyển một cái môn đăng hộ đối phò mã."),t("br"),t("br"),n._v("Cho dù đối với Hóa Phàm võ giả tới nói, hai mươi tuổi còn sớm, nhưng hoàng gia coi trọng lợi ích, sớm đem hắn đến giúp bản thân trên chiến xa, có thể để cho hoàng quyền càng thêm vững chắc."),t("br"),t("br"),n._v("Những này thanh niên tài tuấn, cứ việc không phải danh sư học viện học viên, nhưng cũng từng cái thực lực bất phàm, trong đó cường đại, thế mà cùng Hồ Yêu Yêu đám người tương đồng, đã là Tằm Phong cảnh đỉnh phong, khoảng cách Bán Thánh chỉ có cách xa một bước."),t("br"),t("br"),n._v("Hai mươi tuổi liền có thực lực như thế, coi như đặt vào danh sư học viện đều là siêu cấp thiên tài!"),t("br"),t("br"),n._v("“Hình Viễn, trong này có lẽ có không ít của ngươi đối thủ cạnh tranh chứ?” Lạc Thất Thất cười nói."),t("br"),t("br"),n._v("Làm làm hảo hữu, biết Hình Viễn một mực theo đuổi Ngọc Phi Nhi công chúa, chỉ bất quá tương vương có giấc mơ, thần nữ vô tình thôi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Hình Viễn liếc qua cách đó không xa Trương Huyền, gặp hắn không lên tiếng, lúc này mới xấu hổ cười một tiếng: “Ta biết mình không xứng với Ngọc Phi Nhi công chúa, sớm liền không có cái kia cái niệm tưởng...”"),t("br"),t("br"),n._v("Hắn là ưa thích Ngọc Phi Nhi, nhưng mà thứ hai đối với hắn hờ hững, ngược lại đối trước mắt vị này Trương sư, dường như có không nói rõ được cũng không tả rõ được tình cảm, suy tư liên tục, vẫn là quyết định từ bỏ."),t("br"),t("br"),n._v("Không nói trước vị này Trương Huyền là ân nhân cứu mạng của hắn, chỉ nói tại danh sư học viện làm ra những sự tình kia, liền xem như mười cái hắn cũng theo không kịp."),t("br"),t("br"),n._v("Cùng cái tên này tranh, thật lo lắng sẽ bị Huyền Huyền hội người, đánh danh sư học viện cửa cũng không tìm tới."),t("br"),t("br"),n._v("“Đã sớm khuyên ngươi từ bỏ, ngươi không phải không nghe, bây giờ có thể nghĩ thông suốt cũng không tệ!”"),t("br"),t("br"),n._v("Gặp hắn nói như vậy, Lạc Thất Thất cười nói."),t("br"),t("br"),n._v("Nàng bạn gái thân yêu thích, nàng biết đến rõ rõ ràng ràng, trong âm thầm cũng khuyên qua trước mắt vị này, chỉ bất quá không sau khi nghe xong."),t("br"),t("br"),n._v("Hiện tại có thể từ bỏ, đối với hắn mà nói, cũng chưa chắc không phải chuyện tốt."),t("br"),t("br"),n._v("“Hình Viễn, bên cạnh ngươi hai vị này rất là lạ mặt ah, không biết là ai? Có thể thuận tiện giới thiệu cho ta một cái?”"),t("br"),t("br"),n._v("Mấy người đang đang nói chuyện, liền nghe đến một cái thanh âm nhàn nhạt vang lên, lập tức nhìn thấy một cái hai mười lăm, mười sáu tuổi thanh niên, bưng chén rượu đi tới, mang trên mặt nụ cười thản nhiên."),t("br"),t("br"),n._v("Người thanh niên này, một thân áo xanh, hai hàng lông mày vung lên, ánh mắt như điện, dung mạo so với Hình Viễn, đều chỉ mạnh không yếu, tự mang một phen quý công tử ý vị."),t("br"),t("br"),n._v("Chính là rất nhiều tài tuấn bên trong, thực lực mạnh nhất một cái, thình lình đạt đến Tằm Phong cảnh đỉnh phong."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-861"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-863"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);