(window.webpackJsonp=window.webpackJsonp||[]).push([[2147],{2502:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-855-thien-chuy-ngung-hon-quyet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-855-thien-chuy-ngung-hon-quyet"}},[n._v("#")]),n._v(" Chương 855: Thiên Chuy Ngưng Hồn quyết")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 855: Thiên Chuy Ngưng Hồn quyết"),t("br"),t("br"),n._v("“Quy Nguyên Nhất Khí đại trận?”"),t("br"),t("br"),n._v("Đứng ở trên tường, nhìn về phía sân nhỏ, Trương Huyền lông mày giương lên: “Lại là cấp bảy trận pháp?”"),t("br"),t("br"),n._v("Cái viện này tại hoàng cung rất nhiều trong đình viện, không tính là rộng rãi nhất, lớn nhất, trận pháp nhưng là lợi hại nhất, đã đạt đến cấp bảy."),t("br"),t("br"),n._v("Loại cấp bậc này trận pháp, vượt ra khỏi Hồng Viễn đế quốc phạm trù, đạt đến phong hào đế quốc phạm vi."),t("br"),t("br"),n._v("“Xem ra, hẳn là hoàng cung nơi quan trọng nhất, gốc kia Thánh vực Bồ Đề thụ liền tại bên trong!”"),t("br"),t("br"),n._v("Mắt sáng lên."),t("br"),t("br"),n._v("Dựa theo Chung Đỉnh Thuần nói, Thánh vực Bồ Đề thụ là Hồng Viễn hoàng thất lập quốc gốc rễ, đã như vậy, có lẽ thủ hộ rất sâm nghiêm, mà cái viện này, vừa vặn phù hợp điều kiện."),t("br"),t("br"),n._v("Có thể hay không cứu chữa Ngụy Như Yên, chỉ có thể tự mình vào xem."),t("br"),t("br"),n._v("“Ta thực lực bây giờ, phá giải cấp bảy trận pháp, cần phải hao phí rất lớn một phen công phu!”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, nhìn một vòng, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Lấy thực lực của hắn bây giờ, muốn phá giải trận pháp này, tìm tới sinh môn, không hề phát giác lách đi vào, cũng có thể làm được, có điều, cho dù có Minh Lý Chi Nhãn tinh tế cân nhắc, cũng cần một, hai canh giờ lâu."),t("br"),t("br"),n._v("Hiện tại người tại hoàng cung, nguy cơ trùng trùng, về thời gian khẳng định không kịp."),t("br"),t("br"),n._v("“Không có biện pháp!”"),t("br"),t("br"),n._v("Vu hồn khẽ động, bẻ gãy một cái nhánh cây, khống chế sức mạnh, thẳng tắp hướng trong viện ném đi."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Dường như tảng đá rơi trên mặt hồ, bên trong đại trận, cảm nhận được ngoại lực đụng đến, bỗng nhiên vận chuyển lại, phát ra liên tiếp thanh minh."),t("br"),t("br"),n._v("Vô số kiếm quang lóe lên, cành cây trong chốc lát liền bị quấy thành phấn vụn."),t("br"),t("br"),n._v("Lại là cái cường đại sát trận!"),t("br"),t("br"),n._v("Không đi quản cành cây kết cục, Trương Huyền hai mắt như điện (giật), nhìn thấy trận pháp vận chuyển, trong đầu thở nhẹ."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Trận pháp không vận chuyển, cùng người không thi triển võ kỹ đồng dạng, Thiên Đạo thư viện là không cách nào sử dụng, kích hoạt trận pháp, liền có thể thuận lợi nhìn ra chỗ mấu chốt."),t("br"),t("br"),n._v("Thức hải xuất hiện một quyển sách, rất mau nhìn hết, đối đại trận này, lập tức rõ như lòng bàn tay."),t("br"),t("br"),n._v("Thân thể nhẹ nhàng nhoáng một cái, tiến vào bên trong, tìm tới vị trí hạch tâm, một cước đạp tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Vận chuyển trận pháp lập tức ngừng lại."),t("br"),t("br"),n._v("Vu hồn mặc dù vô hình vô chất, lại có thể căn cứ ý niệm khống chế vật phẩm, hiện ra hình thái, muốn trận pháp đình chỉ, chỉ có thể sử dụng tuyệt chiêu của hắn, một cước phá trận pháp!"),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm, hướng trong viện bay đi."),t("br"),t("br"),n._v("Sân nhỏ rộng lớn, cùng cái khác sân nhỏ không có khác nhau quá nhiều, chỉ là không có nửa cái bóng người, cũng không có tìm không thấy cái gọi là Bồ Đề thụ, chỉ có mấy cái cổ xưa bia đá đứng sừng sững ở tại chỗ."),t("br"),t("br"),n._v("“Chẳng lẽ đi nhầm?”"),t("br"),t("br"),n._v("Nhìn một vòng, Trương Huyền mày nhăn lại."),t("br"),t("br"),n._v("Dựa theo Chung Đỉnh Thuần lời nói, cái này gốc Thánh vực Bồ Đề thụ, có lẽ kích cỡ rất lớn, hiện ở trong viện trừ một chút bình thường vườn hoa, cũng không xem đến bất kỳ cây cối, chẳng lẽ... Ngọc gia cái này gốc thứ trọng yếu nhất, cũng không tại trận pháp sâm nghiêm nhất địa phương?"),t("br"),t("br"),n._v("Không có thứ này, bố trí cấp bảy trận pháp làm gì?"),t("br"),t("br"),n._v("Vì bao phủ mấy cái này bia đá?"),t("br"),t("br"),n._v("Chẳng lẽ, những bia đá này... So Bồ Đề thụ còn muốn trân quý?"),t("br"),t("br"),n._v("Tràn đầy nghi hoặc, nhịn không được nhìn sang."),t("br"),t("br"),n._v("Bia đá cũ kỹ, không biết đã bao nhiêu năm, thoạt nhìn còn có chút tổn hại, từng hàng chữ lớn điêu khắc trên đó."),t("br"),t("br"),n._v("Những chữ này thể, cũng không phải là thư họa sư điêu khắc, không có loại kia đặc thù ý cảnh, thậm chí kiểu chữ cũng không tính là công chính, có thể chẳng biết tại sao, nhìn lên một cái, cũng làm người ta linh hồn rơi vào trong đó, không cách nào tự kềm chế, phảng phất có loại đặc thù lực lượng."),t("br"),t("br"),n._v("“Thiên Chuy Ngưng Hồn quyết!”"),t("br"),t("br"),n._v("Con mắt rơi vào thứ trên một tấm bia đá, một hàng chữ lớn vào vào mí mắt."),t("br"),t("br"),n._v("“Là hồn phách phương pháp tu luyện?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Còn tưởng rằng là cái gì, không nghĩ tới lại là linh hồn tu luyện pháp quyết."),t("br"),t("br"),n._v("Hồn phách, luôn luôn là trong tu luyện khó khăn nhất, nhất là Vu hồn chức nghiệp thất truyền về sau, người tu luyện, đều lấy chân khí vi tôn, hồn phách người tu luyện rõ ràng xuất hiện thế yếu, cũng không có bao nhiêu pháp quyết lưu truyền."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, hắn Vu hồn, một mực dừng lại tại Quy Nhất cảnh đỉnh phong, không cách nào tiếp tục tu luyện."),t("br"),t("br"),n._v("Thậm chí trước đó Tiên Thiên thai độc, cũng không cách nào áp chế."),t("br"),t("br"),n._v("Vốn cho rằng cái viện này, sẽ có gọi là Thánh vực Bồ Đề thụ, không nghĩ tới, lại là hồn phách phương pháp tu luyện!"),t("br"),t("br"),n._v("Thứ này đối người khác mà nói, không tính là cái gì, muốn tu luyện rất khó, đối với hắn mà nói, có thể là đồ tốt."),t("br"),t("br"),n._v("Theo Mặc Hồn Sinh chỗ nào nhận được Hóa Phàm nhất đến cửu trọng Vu hồn pháp quyết tu luyện, một mực không tìm được thích hợp hồn phách phương pháp tu luyện bổ sung, có lẽ cái này cái gọi là ngàn chùy cô đọng quyết, có thể tạo được tác dụng."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Ánh mắt hướng bia đá quét tới, một quyển sách xuất hiện tại trên giá sách."),t("br"),t("br"),n._v("Hiện tại trong hoàng cung, chỗ nguy hiểm, không có thời gian tu luyện, cũng không có thời gian nhìn kỹ, trước đem hắn thu vào lại nói."),t("br"),t("br"),n._v("Có trước đó tại Phong Thánh đài, sao chép bia đá văn tự kinh nghiệm, ánh mắt chỗ đến, nội dung trên tấm bia đá, còn nguyên khắc sâu vào trong óc, hình thành thư tịch."),t("br"),t("br"),n._v("Rất nhanh, đem mấy tấm bia đá bên trên văn tự toàn bộ ghi chép lại, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Mặc kệ có hữu dụng hay không, lấy trước đi lại nói."),t("br"),t("br"),n._v("Trở về có rảnh rỗi, đang từ từ nghiên cứu."),t("br"),t("br"),n._v("“Người nào?”"),t("br"),t("br"),n._v("Mới vừa thu vào hết, đang định rời đi sân nhỏ, tiếp tục tìm kiếm, liền nghe đến hét lớn một tiếng vang lên, ngay sau đó một cổ lực lượng cường đại thẳng tắp hướng hắn Vu hồn tập kích tới."),t("br"),t("br"),n._v("“Nguy rồi!”"),t("br"),t("br"),n._v("Sầm mặt lại, Vu hồn khẽ động, tránh thoát công kích, vội vàng xoay người nhìn lại, chỉ thấy một người trung niên, ánh mắt lấp lánh nhìn lại."),t("br"),n._v(" Chừng bốn mươi tuổi, một thân lộng lẫy quần áo, khí tức cực kỳ cường đại, cho người ta một loại kịch liệt cảm giác áp bách, nhất là hai mắt, như cùng một cái vòng xoáy, dường như có thể nhìn thấy hắn tồn tại."),t("br"),t("br"),n._v("“Am hiểu linh hồn cường giả...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Nhìn thấy bộ dáng của đối phương, hắn lập tức biết, đối phương cũng không phải là có thể nhìn thấy, mà là am hiểu linh hồn, có thể cảm ứng được hắn tồn tại."),t("br"),t("br"),n._v("Liền cùng lúc trước Vệ Nhiễm Tuyết đồng dạng."),t("br"),t("br"),n._v("Xem ra có thể tọa trấn Hồng Viễn đế quốc, chấn nhiếp vô số đế quốc, vương quốc, không dám dị động, cái này hoàng cung, quả thực không có tưởng tượng đơn giản như vậy, liền xem như Vu hồn, cũng không phải tùy ý ra vào."),t("br"),t("br"),n._v("“Dám xông vào hoàng cung, thật to gan!”"),t("br"),t("br"),n._v("Dường như cảm ứng được hắn chính đang quan sát, quát lạnh một tiếng, trung niên nhân một quyền công kích mà tới."),t("br"),t("br"),n._v("Lực lượng cường đại, trong nháy mắt cuồn cuộn mà tới, đã đạt đến Thánh vực nhất trọng đỉnh phong."),t("br"),t("br"),n._v("Loại này cường giả, xem như Hồng Viễn đế quốc phương diện, cường đại nhất."),t("br"),t("br"),n._v("“Trốn!”"),t("br"),t("br"),n._v("Biết chắc không phải là đối thủ, Trương Huyền không dám do dự, thân thể nhoáng một cái, thẳng tắp hướng ngoài viện vọt tới."),t("br"),t("br"),n._v("“Muốn chạy trốn? Hoàng cung há lại ngươi muốn tới thì tới, muốn đi thì đi địa phương?”"),t("br"),t("br"),n._v("Cảm ứng được cái này lẫn vào trong đó sinh mệnh, muốn chạy trốn, hai hàng lông mày vung lên, trung niên nhân bàn tay lớn vồ một cái."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một cái cự đại chưởng ấn, hạ xuống từ trên trời, đem hết thảy chung quanh đường lui, toàn bộ phong kín."),t("br"),t("br"),n._v("“Hắn mặc dù nhìn không thấy ta, nhưng am hiểu linh hồn, có thể cảm ứng được ta tồn tại... Không trốn thoát được, làm không cẩn thận thực biết bị chém giết...”"),t("br"),t("br"),n._v("Bỗng nhiên dừng lại, ánh mắt ngưng trọng, Trương Huyền toàn thân kéo căng."),t("br"),t("br"),n._v("Lần này nguy cơ mức độ, có thể so gặp được Vệ Nhiễm Tuyết còn có lớn."),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết là học viện danh sư, bất kể nói thế nào, đều sẽ lưu có một ít chỗ trống, mà trước mắt vị này, công kích hung hãn, không lưu tình chút nào, dường như nhất định muốn đem hắn chém giết."),t("br"),t("br"),n._v("Cũng đúng, một cái không biết tên sinh mạng thể, xông vào hoàng cung, đổi lại ai, chỉ sợ đều khó mà an tâm."),t("br"),t("br"),n._v("Thân ảnh nhanh chóng tránh né, có điều, chưởng lực của đối phương quá hùng hậu, vẫn là bị quét trúng một cái, đau rát đau, đã bị thương nhẹ."),t("br"),t("br"),n._v("“Không được, như vậy trốn tránh đi xuống, khẳng định tránh không khỏi!”"),t("br"),t("br"),n._v("Tràn đầy lo lắng, Trương Huyền đáy lòng nhưng lại chưa bối rối, mà là cẩn thận phân tích tình huống trước mắt."),t("br"),t("br"),n._v("Hắn hồn phách lực lượng, chỉ có Quy Nhất cảnh, cùng đối phương Thánh vực nhất trọng đỉnh phong, cách biệt quá xa, căn bản không tránh né được."),t("br"),t("br"),n._v("Một mực tránh né, chỉ có bị đánh chết một con đường có thể đi."),t("br"),t("br"),n._v("“Mượn nhờ trận pháp...”"),t("br"),t("br"),n._v("Mắt sáng lên, Trương Huyền trong nháy mắt có quyết định."),t("br"),t("br"),n._v("Muốn đánh thắng đối phương, nhờ vào thực lực của hắn, khẳng định làm không được, hiện tại chỉ có thể dựa vào trong viện cái này Quy Nguyên Nhất Khí đại trận."),t("br"),t("br"),n._v("Thông qua thư viện, đã biết trận pháp thiếu hụt cùng mấu chốt, muốn thoát thân, chỉ có thể lợi dụng cái này."),t("br"),t("br"),n._v("Muốn đến nơi này, cũng không do dự, thân ảnh nhoáng một cái, đi tới trận pháp tiết điểm trước mặt, mà lúc này, công kích của đối phương đã rơi mà xuống."),t("br"),t("br"),n._v("“Vận chuyển!”"),t("br"),t("br"),n._v("Không dám do dự, bàn chân một điểm."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mới vừa rồi bị hắn dừng lại trận pháp, trong nháy mắt kích hoạt, một trận mê vụ, thời gian nháy mắt liền đem trung niên nhân bao phủ ở bên trong."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Biết đối phương coi như quen thuộc trận pháp, muốn muốn xông ra đến cũng cần một chút thời gian, không dám tiếp tục do dự, Trương Huyền tinh thần nhoáng một cái, thẳng tắp xông ra sân nhỏ, hướng ra phía ngoài gấp vọt mà ra."),t("br"),t("br"),n._v("“Đáng giận...”"),t("br"),t("br"),n._v("Trung niên nhân hiển nhiên không nghĩ tới, đối phương có thể khống chế trận pháp, tức giận đến oa oa kêu loạn, đợi hắn theo trận pháp lúc đi ra, thân ảnh của đối phương đã biến mất không thấy gì nữa, rốt cuộc không cảm ứng được."),t("br"),t("br"),n._v("“Đến cùng là cái gì?”"),t("br"),t("br"),n._v("Đứng tại sân nhỏ bầu trời, nhìn xem đã mất đi tung tích Trương Huyền, trung niên nhân khuôn mặt xanh xám."),t("br"),t("br"),n._v("Mặc dù những bia đá này ghi lại nội dung, huyền diệu cao thâm, đối phương nhìn lên một cái, khẳng định khó có thể lý giải được, không cần lo lắng mất đi, nhưng ra vào hoàng cung như chỗ không người, còn có thể khống chế cấp bảy trận pháp, khó tránh khỏi có chút quá khoa trương!"),t("br"),t("br"),n._v("Nếu không phải trong cái sân này trận pháp vận chuyển, đưa tới hắn hoài nghi, cố ý tới xem một chút, bằng không, đều không thể phát hiện."),t("br"),t("br"),n._v("“Xem ra, hoàng cung an toàn còn cần gia cố...”"),t("br"),t("br"),n._v("Mắt sáng lên."),t("br"),t("br"),n._v("Không biết đối phương là Thánh thú, còn là nhân loại, biện pháp duy nhất chính là gia cố hoàng cung phòng ngự, đợi đối phương xuất hiện lần nữa, trực tiếp chém giết."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Nguy hiểm thật!”"),t("br"),t("br"),n._v("Chạy ra sân nhỏ, Trương Huyền không dám tiếp tục phi hành, mà là tinh thần khẽ động, hướng cách đó không xa một cái tiểu viện chui vào."),t("br"),t("br"),n._v("Những này sân nhỏ trận pháp Minh Lý Chi Nhãn đều có thể xem thấu, không cần lo lắng xúc động, liên tục chạy hết tốc lực vài trăm mét, lúc này mới tránh qua, tránh né đối phương tinh thần lục soát, nhịn không được thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Không hổ là Hồng Viễn đế quốc hoàng cung, vẫn là có am hiểu công kích linh hồn cường giả."),t("br"),t("br"),n._v("Cũng đúng, đã Bồ Đề thụ là tẩm bổ linh hồn, thành viên hoàng thất hồn phách tất nhiên mạnh mẽ, biết một chút trên linh hồn võ kỹ, cũng không tính là gì."),t("br"),t("br"),n._v("Như vậy trải qua, muốn trong hoàng cung tùy ý đi tìm cái gọi là Thánh vực Bồ Đề thụ, liền không quá dễ dàng."),t("br"),t("br"),n._v("“Chơi đùa cái này nửa ngày, yến hội chỉ sợ cũng sắp bắt đầu, không được tìm cơ hội lại tới!”"),t("br"),t("br"),n._v("Biết lần này rút dây động rừng, có Bồ Đề thụ địa phương, tất nhiên phòng ngự càng thêm nghiêm mật, coi như tìm tới, làm không cẩn thận cũng sẽ bị bắt, chần chờ một chút, Trương Huyền quyết định trước trở về rồi hãy nói."),t("br"),t("br"),n._v("Dù sao Ngụy Như Yên tình huống cũng không tệ, trong thời gian ngắn không có nguy hiểm tính mạng, không cần thiết mạo hiểm đi qua."),t("br"),t("br"),n._v("Tinh thần khẽ động, lần nữa nằm ở mái hiên phía trên, Trương Huyền nhìn xem chung quanh lít nha lít nhít, lộng lẫy kiến trúc, nhịn không được vò đầu."),t("br"),t("br"),n._v("“Cái kia... Yến hội đại sảnh, ở đâu ấy nhỉ?”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-863"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-865"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);