(window.webpackJsonp=window.webpackJsonp||[]).push([[2151],{2506:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-859-ngoc-than-thanh-hoai-nghi-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-859-ngoc-than-thanh-hoai-nghi-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 859: Ngọc Thần Thanh hoài nghi 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 859: Ngọc Thần Thanh hoài nghi "),t("br"),t("br"),n._v("“Trương sư chuyên môn vì ta sáng tạo công pháp, ta rất vui vẻ, nhưng... Ta không thể nhận!” Ngọc Phi Nhi lắc đầu."),t("br"),t("br"),n._v("“Ngươi cũng đã biết, công pháp của ta trân quý cỡ nào, chỉ cần tu luyện, hoàn toàn có thể để cho linh hồn ngươi thực lực, gia tăng gấp đôi không chỉ!”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Đây cũng không phải tự khen, liền xem như bản rút gọn linh hồn công pháp, cũng là dung hợp Vu hồn, Thiên Chuy Ngưng Hồn quyết cùng Ma âm, kinh hồng chờ nghề nghiệp bí thuật, Ngọc Phi Nhi thật muốn học tập, trên linh hồn mặt tiến bộ, tuyệt đối là tiến triển cực nhanh."),t("br"),t("br"),n._v("Về sau siêu việt Hoàng đế bệ hạ, thậm chí ra tay với hắn vị kia trung niên nhân, đều ở trong tầm tay."),t("br"),t("br"),n._v("“Chính là bởi vì trân quý, mới không thể nhận!” Ngọc Phi Nhi chần chờ một chút, nói: “Nếu không như vậy, ta lấy tài vật hình thức mua sắm...”"),t("br"),t("br"),n._v("Học được đối phương một mình sáng tạo công pháp, không có hồi báo, chẳng khác nào có nửa sư tình nghĩa, danh sư đại lục, đối thầy trò tình nghĩa xem rất nặng, một khi có, cái khác đều chỉ có thể biến thành hy vọng xa vời."),t("br"),t("br"),n._v("Mà mua sắm, hàng tiền thanh toán xong, liền không liên quan vấn đề này."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Thấy đối phương kiên trì, Trương Huyền không để ý lắm, lắc đầu, ngón tay đối với trán của đối phương nhấn tới."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngọc Phi Nhi chỉ cảm thấy thân thể mềm mại chấn động, một bộ hoàn chỉnh linh hồn pháp quyết tu luyện, đã truyền vào thức hải."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Chỉ nhìn thoáng qua, đôi mắt thanh tú liền trợn tròn, dọa đến không ngừng run rẩy."),t("br"),t("br"),n._v("Bộ công pháp này quả thực quá huyền diệu, so với nàng luyện được những cái kia, cao minh không biết gấp bao nhiêu lần!"),t("br"),t("br"),n._v("“Cái này... Quá trân quý, ta chỉ sợ chi trả không nổi, nhất định phải lên báo phụ hoàng, để hắn giải quyết...”"),t("br"),t("br"),n._v("Khuôn mặt đỏ lên, Ngọc Phi Nhi vội vàng nói."),t("br"),t("br"),n._v("Vốn cho rằng đối phương sáng chế, coi như lại trân quý, cũng sẽ không so gia tộc các nàng tổ truyền công pháp mạnh hơn, nhìn thoáng qua mới biết được, gia tộc mình cái kia bộ công pháp, ném tới trước mặt, đối phương khả năng liền nhặt cũng sẽ không nhặt."),t("br"),t("br"),n._v("Khác biệt thực sự quá lớn."),t("br"),t("br"),n._v("“Tùy tiện đi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đem công pháp truyền ra, cũng coi như báo đáp hấp thu hoàng thất linh khí ân tình, còn đối phương làm sao quyết định, là chuyện của bọn hắn, cùng mình quan hệ không lớn."),t("br"),t("br"),n._v("Bên này trò chuyện, bên kia Thẩm Quân vẻ mặt biến thành màu đen, tức giận đến không ngừng run rẩy, bất cứ lúc nào cũng sẽ bạo tạc, chỉ cảm thấy trên đầu một mảnh thảo nguyên, tùy ý sinh trưởng tốt."),t("br"),t("br"),n._v("Khoảng cách khá xa, hai người lại tại truyền âm, không biết nói thứ gì, chỉ thấy Trương Huyền tại Ngọc Phi Nhi cái trán vỗ, đối phương liền vẻ mặt đỏ bừng..."),t("br"),t("br"),n._v("Trần trụi liếc mắt đưa tình, xem hắn tại không có gì!"),t("br"),t("br"),n._v("“Đáng giận, đáng giận! Liễu Tuyền, trở về giúp ta điều tra thêm, cái tên này đến cùng lai lịch gì...”"),t("br"),t("br"),n._v("Răng cắn khanh khách rung động, Thẩm Quân dường như đem chén rượu trong tay đều muốn bóp nát."),t("br"),t("br"),n._v("Hắn mới từ bên ngoài trở về, Đế đô sự tình không rõ ràng, vị này Trương sư lại không nói ra họ tên, không biết cũng rất bình thường!"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Liễu gia công tử Liễu Tuyền nhẹ gật đầu: “Đoán chừng là danh sư học viện học sinh, vừa rồi lúc ra cửa, phụ thân nói, ba ngày sau, học viện mới viện trưởng kế vị, muốn dẫn ta qua đi gặp việc đời. Đến lúc đó, đúng lúc có thể đi vào danh sư học viện, đến lúc đó, mang mấy người đem cái tên này đánh bên trên một chầu, giúp ngươi hả giận!”"),t("br"),t("br"),n._v("“Tốt, quyết định như vậy đi, ta cũng mang mấy người, nhất định phải đem hắn đánh răng rơi đầy đất...”"),t("br"),t("br"),n._v("đọc ngantruyen.com/"),t("br"),n._v("Thẩm Quân nghiến răng nghiến lợi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bạch thúc rời đi sân nhỏ liền đi thẳng tới Ngọc Thần Thanh vị trí đại điện."),t("br"),t("br"),n._v("“Bẩm báo Thất vương gia, hôm nay là Lục công chúa sinh nhật, bệ hạ mới vừa đi yến hội sảnh...”"),t("br"),t("br"),n._v("Một tên thái giám đi tới."),t("br"),t("br"),n._v("“Tốt, ta bây giờ đi qua...”"),t("br"),t("br"),n._v("Không dám dừng lại ngừng, Bạch thúc vội vã hướng yến hội sảnh chạy như điên, rốt cục ở bên ngoài, đuổi kịp Hoàng đế đội nghi trượng."),t("br"),t("br"),n._v("“Hoàng huynh...”"),t("br"),t("br"),n._v("Gấp vội vàng nghênh đón."),t("br"),t("br"),n._v("“Mộ Bạch, đã xảy ra chuyện gì, gấp gáp như vậy?” Ngọc Thần Thanh nhướng mày."),t("br"),t("br"),n._v("Bạch thúc gọi ngọc Mộ Bạch, là Hồng Viễn đế quốc Thất vương gia, Ngọc Thần Thanh thân đệ đệ."),t("br"),t("br"),n._v("“Là như vậy, vừa rồi ta đi lĩnh hội bia đá...”"),t("br"),t("br"),n._v("Không dám giấu diếm, ngọc Mộ Bạch đem chuyện lúc trước, kỹ càng nói một lần."),t("br"),t("br"),n._v("“Ngươi nói có tương tự Vu hồn bộ dáng đồ vật, tại hoàng cung xuất hiện? Hơn nữa... Luyện Hồn điện bị hủy? Còn có người một hơi xông qua cửa thứ chín, mỗi một quan chỉ dùng hai hơi thở thời gian?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Ngọc Thần Thanh tràn đầy không thể tin được."),t("br"),t("br"),n._v("Thân làm nhất đẳng đế quốc Hoàng đế bệ hạ, đã gặp đại sự, nhiều lắm, nghe xong đối phương, vẫn như cũ cảm thấy giống như là đang nghe thiên thư."),t("br"),t("br"),n._v("Luyện Hồn điện thông đạo độ khó, hắn thân là Ngọc gia đỉnh phong nhất nhân vật, tự nhiên biết rõ."),t("br"),t("br"),n._v("Liền xem như hắn loại thực lực này, kịch liệt chèn ép xuống, đều không đột phá nổi cửa thứ tám..."),t("br"),t("br"),n._v("Mỗi một quan, chỉ cần hai cái hô hấp... Không chỉ có như thế, còn cứ thế mà vọt tới chủ điện, đem linh khí trong đó, hấp thu ánh sáng, thậm chí Tiên Tổ tượng đều làm vỡ nát..."),t("br"),t("br"),n._v("Linh hồn này cái kia mạnh bao nhiêu?"),t("br"),t("br"),n._v("“Đúng!” Ngọc Mộ Bạch gật đầu, tràn đầy lo lắng nhìn qua: “Bệ hạ, có muốn hay không khởi động bảo hộ cung đại trận, đem tên kia cầm ra đến?”"),t("br"),t("br"),n._v("Hồng Viễn đế quốc bảo hộ cung đại trận, liền Thánh vực nhị trọng, tam trọng cường giả đều có thể chống lại, đối phương coi như vô hình vô chất, cũng khẳng định trốn không thoát."),t("br"),t("br"),n._v("“Một khi khởi động đại trận, toàn bộ hoàng cung đều sẽ rơi vào khủng hoảng, đã người kia bị ngươi đuổi đi, cũng không có truy xét đến, có lẽ... Đã rời đi!”"),t("br"),t("br"),n._v("Ngọc Thần Thanh lắc đầu."),t("br"),t("br"),n._v("Bảo hộ cung đại trận, không đến đế quốc sinh tử tồn vong, sẽ không mở ra, một khi vận chuyển, nhất định gây nên đại hỗn loạn, đến lúc đó, coi như hắn vị hoàng đế này, cũng giải thích không rõ."),t("br"),t("br"),n._v("Lại nói, đối phương đến cùng là Vu hồn, vẫn là người mang dị năng Thánh thú, không có hòa hợp tra rõ ràng trước, coi như đại trận khởi động, muốn tìm, cũng chỉ sợ khó mà tìm tới."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("“Ngươi thêm phái nhân thủ, khắp nơi tuần tra, một khi phát hiện không hợp lý, liền lập tức đi, cần phải đem hắn bắt lấy...”"),t("br"),t("br"),n._v("Suy tư một lát, Ngọc Thần Thanh đạo."),t("br"),n._v("“Đúng!” Ngọc Mộ Bạch nhẹ gật đầu."),t("br"),t("br"),n._v("“Tốt, chuyện này ai đều đừng nói cho, một khi có tin tức, lập tức hướng ta bẩm báo, ta hiện tại muốn đi tham gia Phi nhi sinh nhật yến hội!”"),t("br"),t("br"),n._v("Ngọc Thần Thanh khoát tay áo."),t("br"),t("br"),n._v("Mặc dù rất muốn đem cái kia làm cho phá hư gia hỏa bắt lấy, nhưng thân là Đế Vương bệ hạ, càng phải bình tĩnh bình tĩnh."),t("br"),t("br"),n._v("Nói hết không tiếp tục để ý ngọc Mộ Bạch, nhấc chân hướng trước mắt yến hội sảnh đi đến, còn chưa tới đến trước mặt, chỉ thấy sau lưng lão thái giám lông mày nhíu chung một chỗ."),t("br"),t("br"),n._v("“Có chuyện gì, cứ nói đừng ngại!”"),t("br"),t("br"),n._v("Gặp hắn cái dạng này, biết lão gia hỏa này khẳng định là nghĩ đến cái gì, Ngọc Thần Thanh nhìn qua."),t("br"),t("br"),n._v("Vị này lão thái giám, theo hắn không biết đã bao nhiêu năm, thực lực từ lâu đạt đến Thánh vực, đối rất nhiều chuyện luôn luôn nhạy cảm."),t("br"),t("br"),n._v("“Trương sư chỗ đến, gà bay chó chạy, liền phủ đệ của mình đều khó mà may mắn thoát khỏi... Hắn buổi tối hôm nay tới hoàng cung, có thể hay không... Chuyện này cùng hắn có quan hệ?” Lão thái giám nhịn không được nói."),t("br"),t("br"),n._v("Cái này Trương Huyền quá quỷ dị."),t("br"),t("br"),n._v("Chỗ đến, không phải đổ sụp chính là xui xẻo... Hắn hôm nay chạy đến yến hội sảnh, mặc dù là phòng ngừa ngoài ý muốn, chuyên môn để trận pháp sư gia cố..."),t("br"),t("br"),n._v("Nhưng, ai ngờ địa phương khác sẽ sẽ không xuất hiện vấn đề?"),t("br"),t("br"),n._v("Thật giống như hiện tại, yến hội sảnh là không sao, có thể Luyện Hồn điện nhưng xong... Có thể hay không có liên quan gì?"),t("br"),t("br"),n._v("“Hắn?”"),t("br"),t("br"),n._v("Ngọc Thần Thanh cau mày."),t("br"),t("br"),n._v("Đối phương nói mặc dù nghe là lời nói vô căn cứ, có thể nhưng cũng không phải không có đạo lý."),t("br"),t("br"),n._v("Cái tên này nửa tháng này đến náo ra động tĩnh, liền xem như hắn, đều cảm thấy lông mày nhảy loạn."),t("br"),t("br"),n._v("Muốn nói có một người, thật có thể làm được trước đó ngọc Mộ Bạch nói tới tình huống, làm không cẩn thận thật là cái tên này!"),t("br"),t("br"),n._v("“Hắn không phải một canh giờ trước liền đến, hơn nữa không có ra khỏi cửa ư?” Ngọc Thần Thanh hỏi."),t("br"),t("br"),n._v("“Vâng, một mực không có ra khỏi cửa... Bất quá, từng tại nguyên chỗ ngẩn người qua hơn nửa canh giờ!”"),t("br"),t("br"),n._v("Thái giám suy nghĩ một chút, đạo."),t("br"),t("br"),n._v("“Ngẩn người?”"),t("br"),t("br"),n._v("“Ừm, nếu như vừa rồi Thất vương gia, nói thật là Vu hồn lời nói, có phải hay không là ngẩn người đoạn thời gian kia, Vu hồn ly thể...” Thái giám chần chờ một chút, đạo."),t("br"),t("br"),n._v("“Vu hồn ly thể? Ngươi cảm thấy vị này Trương sư, là một vị vu hồn sư?” Ngọc Thần Thanh sầm mặt lại."),t("br"),t("br"),n._v("“Lão nô nhưng mà đoán bừa...” Lão thái giám cười cười, đạo."),t("br"),t("br"),n._v("“Vu hồn sư, thời kỳ Thượng Cổ đầu nhập vào Dị Linh tộc, không phải là bị Danh Sư đường diệt giết sạch, truyền thừa đều đã cắt đứt sao? Cái này Trương Huyền, thân là danh sư, làm sao có thể biết rõ rồi mà còn cố phạm phải, đi học tập cái nghề nghiệp này?”"),t("br"),t("br"),n._v("Ngọc Thần Thanh lông mày nhăn thành mụn nhọt."),t("br"),t("br"),n._v("“Kỳ thật, phải hay không phải, thử một lần liền hiểu biết...” Lão thái giám đạo."),t("br"),t("br"),n._v("“Ừm!” Ngọc Thần Thanh nhẹ gật đầu."),t("br"),t("br"),n._v("Không nghĩ nhiều nữa, hất lên ống tay áo, nhanh chân hướng yến hội sảnh đi vào, vừa vào cửa, lập tức nhìn thấy bản thân quần áo lộng lẫy nữ nhi, đang đứng tại một thanh niên trước mặt, xoay người cho hắn rót rượu."),t("br"),t("br"),n._v("Nữ nhi của hắn, vô cùng tôn quý, kim chi ngọc diệp, cái tên này thế mà để nàng rót rượu, lá gan thật là quá lớn đi!"),t("br"),t("br"),n._v("“Bệ hạ, vị này chính là Trương Huyền... Trương sư!”"),t("br"),t("br"),n._v("Lão thái giám vội nói."),t("br"),t("br"),n._v("“Hắn chính là?”"),t("br"),t("br"),n._v("Ngọc Thần Thanh ánh mắt phát lạnh."),t("br"),t("br"),n._v("Đối diện cái tên này có ý kiến, liền đụng tới, để lửa giận của hắn cọ một cái liền xông tới."),t("br"),t("br"),n._v("“Phi nhi, ngươi đang làm cái gì?”"),t("br"),t("br"),n._v("Sầm mặt lại, hai bước đi tới gian phòng, ánh mắt trầm thấp: “Đường đường công chúa, cho người ta rót rượu, còn thể thống gì?”"),t("br"),t("br"),n._v("Đối phương thu ngươi làm tỳ nữ, là đang vũ nhục ngươi, vũ nhục hoàng thất, ngươi còn tưởng thật?"),t("br"),t("br"),n._v("Một cái nho nhỏ tứ tinh danh sư, cho dù có chút thủ đoạn, có tư cách gì, để ngươi vì hắn rót rượu?"),t("br"),t("br"),n._v("“Phụ hoàng, ngươi đã đến...”"),t("br"),t("br"),n._v("Nhìn thấy phụ thân đến đến, Ngọc Phi Nhi vội vàng đứng dậy hành lễ."),t("br"),t("br"),n._v("“Thẩm Quân (Liễu Tuyền, Hình Viễn...) Tham kiến bệ hạ!”"),t("br"),t("br"),n._v("Bên trong căn phòng những người khác, cũng tất cả đều đứng dậy, đồng loạt cúi đầu."),t("br"),t("br"),n._v("Trương Huyền cũng đứng dậy, mắt nhìn trước người trung niên này một cái, ôm quyền khom người."),t("br"),t("br"),n._v("Mặc dù hắn thân là viện trưởng, địa vị không so với phương thấp thậm chí càng cao hơn một đoạn, nhưng kế vị nghi thức còn không có cử hành, không tiện biểu lộ thân phận, vẫn là đem lễ tiết làm đủ."),t("br"),t("br"),n._v("“Ngươi chính là Trương Huyền?”"),t("br"),t("br"),n._v("Nhìn lại, Ngọc Thần Thanh mắt sáng lên."),t("br"),t("br"),n._v("“Chính là!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Ngọc Thần Thanh không nói thêm gì nữa, xoay người lại đến chủ vị."),t("br"),t("br"),n._v("Mặc dù muốn thử xem cái tên này đến cùng có hay không vu hồn sư chức nghiệp, nhưng thân là Hoàng đế bệ hạ, không có khả năng trực tiếp ra tay."),t("br"),t("br"),n._v("Thật muốn như vậy làm, thân phận thấp không nói, đối Danh Sư đường cũng không cách nào bàn giao."),t("br"),t("br"),n._v("Thậm chí, còn có thể dẫn đến nữ nhi không vui."),t("br"),t("br"),n._v("“Cảm ơn mọi người có thể tới tham gia tiểu nữ sinh nhật yến hội!”"),t("br"),t("br"),n._v("Ngồi xuống, nhìn quanh một tuần, Ngọc Thần Thanh nhẹ nhàng cười một tiếng, mang theo Đế Vương đặc hữu khí thế."),t("br"),t("br"),n._v("“Hôm nay là cái cao hứng thời gian, làm sao có thể không vui? La Phú, đi đem Bạch âm sư mời đến, để nàng vì mọi người dâng lên một khúc!”"),t("br"),t("br"),n._v("“Đúng!” Lão thái giám nhẹ gật đầu, ngầm hiểu, lui ra ngoài."),t("br"),t("br"),n._v("“Bạch âm sư? Hoàng cung lợi hại nhất nhạc sĩ, lục tinh Ma âm sư, nàng có thể tự mình dâng vui, quả thực quá tốt rồi!”"),t("br"),t("br"),n._v("Nghe được Bạch âm sư, tất cả mọi người con mắt lập tức tỏa ánh sáng, hưng phấn mà nguyên một đám vẻ mặt đỏ lên."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-867"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-869"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);