(window.webpackJsonp=window.webpackJsonp||[]).push([[2152],{2507:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-860-ma-am-đoi-khang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-860-ma-am-đoi-khang"}},[n._v("#")]),n._v(" Chương 860: Ma âm đối kháng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 860: Ma âm đối kháng"),t("br"),t("br"),n._v("“Bạch âm sư?” Thấy mọi người hưng phấn như thế, Trương Huyền nhìn về phía một bên Ngọc Phi Nhi."),t("br"),t("br"),n._v("“Bạch âm sư là phụ hoàng từ bên ngoài chuyên môn mời tới Ma âm sư, nghe nói đã đạt đến lục tinh đỉnh phong, diễn tấu nhạc khúc, nghe tới một lần, liền có thể khiến người ta say mê trong đó, linh hồn chịu đến tẩy lễ! Bình thường, chỉ có tiếp đãi đế quốc khác sứ giả, mới có tư cách lắng nghe...”"),t("br"),t("br"),n._v("Ngọc Phi Nhi cũng kích động vẻ mặt thấu đỏ."),t("br"),t("br"),n._v("Mặc dù đơn độc lục tinh Ma âm sư, địa vị so luyện đan sư, luyện khí sư chờ chức nghiệp kém rất nhiều, nhưng ở một chút hoàng cung, vương phủ lại là rất được tôn trọng."),t("br"),t("br"),n._v("Thường xuyên lắng nghe, có thể để cho linh hồn chịu đến thoải mái, trở nên càng thêm cường đại, càng có thể hiển lộ rõ ràng thân phận."),t("br"),t("br"),n._v("“Xem ra là cái biểu diễn hình Ma âm sư!”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Ma âm sư chia làm sát phạt cùng biểu diễn, hai chủng loại hình."),t("br"),t("br"),n._v("Tưởng viện trưởng thân là danh sư, muốn đối kháng Dị Linh tộc nhân, tự nhiên tu chính là sát phạt hình nhạc khúc, loại người này, một khi diễn tấu, kim qua thiết mã, mang theo cường đại công phạt năng lực."),t("br"),t("br"),n._v("Mà biểu diễn hình, người bình thường cũng có thể lắng nghe, có ích vô hại."),t("br"),t("br"),n._v("Nếu không, đều giống như Trương Huyền, đàn tấu, đầy đất lông gà, cũng không cần chiêu đãi đế quốc khác sứ giả, đoán chừng đàn tấu một lần, liền muốn khai chiến, không chết không thôi."),t("br"),t("br"),n._v("Cái này Bạch âm sư, có thể tại hoàng cung diễn tấu, nhiều người trẻ tuổi người đều biết, cũng vì chi hưng phấn, không cần nghĩ, tất nhiên là biểu diễn hình, nghe xong có thể vui vẻ thể xác tinh thần."),t("br"),t("br"),n._v("“Phi nhi, tới!”"),t("br"),t("br"),n._v("Thấy con gái của mình, vẫn như cũ cùng vị này Trương sư nói cười yến yến, Ngọc Thần Thanh trên mặt không vui."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Ngọc Phi Nhi đi tới phụ thân ra tay một bên, ngồi xuống."),t("br"),t("br"),n._v("Lão thái giám La Phú đi ra ngoài không lâu sau, liền đi trở về, đi theo phía sau một cái cô gái mặc áo trắng, dáng người uyển chuyển, dung mạo xinh đẹp."),t("br"),t("br"),n._v("Mặc dù cùng Ngọc Phi Nhi, Lạc Thất Thất đám người so, hơi có không bằng, nhưng cũng được cho trong trăm có một."),t("br"),t("br"),n._v("“Ma âm sư Bạch Huyên gặp qua bệ hạ, Lục công chúa, chư vị tài tuấn!”"),t("br"),t("br"),n._v("Đi vào đại điện, Bạch âm sư doanh doanh cúi đầu, thanh âm như đàn, dễ nghe êm tai, làm cho lòng người bên trong không khỏi quả quyết."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Đối phương có thể đem thanh âm cùng tiếng đàn dung hợp, thi triển ra Ma âm hiệu quả mà không thương tổn người, thật có chút trình độ."),t("br"),t("br"),n._v("“Bạch âm sư không cần khách khí, hôm nay là tiểu nữ sinh nhật, còn muốn làm phiền ngươi diễn tấu một khúc, vì mọi người trợ hứng!”"),t("br"),t("br"),n._v("Ngọc Thần Thanh cười nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Bạch âm sư nhẹ gật đầu, khoanh chân ngồi ở một bên, bàn tay phất một cái, một bộ đàn ngọc xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Thấy nàng lấy ra đàn ngọc, tất cả mọi người đều con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Đây chính là Bạch âm sư ưng vũ đàn đi!”"),t("br"),t("br"),n._v("“Truyền thuyết Bạch âm sư thú sủng, Thanh Vĩ ưng vì cứu nàng mà chết, vì cảm niệm đối phương, dùng đối phương phần đuôi bên trên lông vũ, làm thành dây đàn, đồng thời tự mình phổ một thủ khúc, thanh minh ai oán, nghe xong làm cho lòng người đau!”"),t("br"),t("br"),n._v("“Ta cũng nghe nói, nghe nói cong thành ngày, xuất hiện tiên hạc khóc minh cục diện, Bạch âm sư mới danh tiếng vang xa, toàn bộ Đế đô, vô số cường giả, đều vì nghe nàng một khúc mà cảm giác vinh hạnh!”"),t("br"),t("br"),n._v("“Không nghĩ tới bệ hạ vậy mà đưa nàng mời tới, cái này đầy đủ ta trở về khoe khoang ba ngày!”"),t("br"),t("br"),n._v("“Ba ngày? Đầy đủ ta khoe khoang ba tháng...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy nàng lấy ra đàn ngọc, mọi người toàn bộ đều khó mà kìm lại hưng phấn trong lòng."),t("br"),t("br"),n._v("Chiến đấu hình Ma âm sư, không gặp được chiến sự, cơ bản danh khí không hiện, mà loại này biểu diễn hình, nhạc khúc vừa ra, rất dễ dàng để cho người ta đổ xô tới, vì đó ảnh theo."),t("br"),t("br"),n._v("Nghe được mọi người thảo luận, Bạch âm sư nhẹ nhàng cười một tiếng, ngón tay thon dài tại dây đàn bên trên nhẹ nhàng một nhóm."),t("br"),t("br"),n._v("Leng keng!"),t("br"),t("br"),n._v(" Tiếng đàn lập tức vang lên, giống như là theo Viễn Cổ truyền đến, đâm thẳng ở sâu trong nội tâm."),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Người trong nghề vừa ra tay, liền hiểu biết có hay không, đối phương mặc dù cùng Tưởng viện trưởng so, hơi có không bằng, nhưng đi được là một con đường khác, để cho người ta mơ màng vô cùng."),t("br"),t("br"),n._v("Leng keng, leng keng!"),t("br"),t("br"),n._v("Bạch âm sư ngón tay khẽ vuốt, thanh âm tiếp tục chảy xuôi mà ra, một cái nháy mắt, bên trong đại sảnh mọi người, phảng phất đặt mình vào rộng lớn thảo nguyên, vô số chim tước ở chung quanh kêu to, hương hoa đập vào mặt, dị thường hưởng thụ."),t("br"),t("br"),n._v("Ở đây rất nhiều tài tuấn, toàn bộ cũng nhịn không được nhắm mắt lại, tâm phi thần động."),t("br"),t("br"),n._v("Ngay cả Thẩm Quân, cũng híp mắt lại, say mê trong đó."),t("br"),t("br"),n._v("Có thể trở thành một lớn chức nghiệp truyền thừa xuống, liền xem như hạ cửu lưu, cũng không thể khinh thường."),t("br"),t("br"),n._v("Thật giống như thư họa sư, chỗ cao thâm, mang bút có thể tranh trăm vạn quân mã, liền xem như một chút thực lực cực mạnh người tu luyện, cũng khó có thể chống chọi."),t("br"),t("br"),n._v("Nhìn thấy mọi người tất cả đều rơi vào nhạc khúc bên trong, Bạch âm sư nhẹ nhàng cười một tiếng, nhìn về phía cách đó không xa Trương Huyền."),t("br"),t("br"),n._v("Mới vừa mới tới thời điểm, nàng đã nhận được bệ hạ ý chỉ, thăm dò một cái người trẻ tuổi này."),t("br"),t("br"),n._v("Leng keng, leng keng!"),t("br"),t("br"),n._v("Thanh âm xoay một cái."),t("br"),t("br"),n._v("Nhạc khúc tại đừng người trong tai, vẫn như cũ say mê mê người, có điều, một cỗ to lớn linh hồn áp lực, lập tức lan tràn mà tới."),t("br"),t("br"),n._v("Trương Huyền đang tại cẩn thận lắng nghe, ý định so sánh một chút, loại này biểu diễn hình nhạc khúc cùng sát phạt hình khác nhau ở chỗ nào, lập tức cảm thấy linh hồn run lên, đối phương nhạc khúc mang theo mãnh liệt khiêu khích cùng tiến công."),t("br"),n._v("“Thật to gan!”"),t("br"),t("br"),n._v("Ma âm sư lực lượng tại hồn phách bên trong xoay một cái, công kích của đối phương lập tức tiêu tán thành vô hình, Trương Huyền lông mày nhăn lại, ánh mắt lấp lánh nhìn sang."),t("br"),t("br"),n._v("Thân là Thất Tinh Ma âm sư, tự nhiên biết đối phương tuyệt đối cố ý gây nên."),t("br"),t("br"),n._v("Nhưng mà... Hắn qua tới tham gia yến hội, cùng cái tên này cũng là lần đầu tiên gặp mặt, công kích mình làm gì?"),t("br"),t("br"),n._v("Chẳng lẽ... Đã biết hắn là danh sư học viện viện trưởng, cố ý đánh lén? Muốn đưa mình vào tử địa?"),t("br"),t("br"),n._v("“Có khả năng, rất nhiều người đều nói qua, Hồng Viễn đế quốc không yên ổn, danh sư học viện vì bảo hộ an toàn của ta, thậm chí không tiếc giấu diếm tin tức... Có lẽ, nguy hiểm liền giấu ở chung quanh!”"),t("br"),t("br"),n._v("Sầm mặt lại."),t("br"),t("br"),n._v("Lạc Nhược Hi nói qua, Hồng Viễn thành nhưng thật ra là đại lục cùng vực ngoại điểm kết nối một trong, Dị Linh tộc nhân tùy thời có thể lấy đi vào, danh sư học viện mục đích đúng là vì trấn áp tiết điểm này."),t("br"),t("br"),n._v("Hồ Yêu Yêu cũng mười phần cảnh giác, không tiếc mai danh ẩn tích, còn có Ngô Dương Tử trải qua, mạnh như thế người, đều bị Dị Linh tộc nhân bắt đi... Những người này nói đến Hồng Viễn thành, đều giữ kín như bưng, chỉ sợ, nơi này thật có không ít bí mật không muốn người biết."),t("br"),t("br"),n._v("Cái này Bạch âm sư, mặc dù danh khí rất lớn, để cho người ta kính trọng, nhưng vừa ra tay, liền đối với mình tiến hành linh hồn uy áp, tất nhiên có mưu đồ!"),t("br"),t("br"),n._v("“A! Trừ phi ra tay, dùng thực lực tuyệt đối đối ta nghiền ép, dùng Ma âm đối phó ta... Ngươi suy nghĩ nhiều!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt phát lạnh."),t("br"),t("br"),n._v("Hắn hiện tại chỉ có Đạp Hư cảnh sơ kỳ, đối phương dùng thực lực tuyệt đối nghiền ép lời nói, loại trừ đem Tử Dương thú ném ra, không có cách nào chống chọi, nhưng muốn dùng... Ma âm tiến công, vẫn còn nghĩ quá đơn giản!"),t("br"),t("br"),n._v("Liền Tưởng viện trưởng đều bái hắn làm thầy, ngươi một cái biểu diễn hình Ma âm sư, cũng muốn đối phó, không phải đùa giỡn hay sao?"),t("br"),t("br"),n._v("Cũng không nói chuyện, Trương Huyền cùng mọi người đồng dạng, đem híp mắt lại, giả bộ như đã say mê tại nhạc khúc bên trong, bàn tay thì nhẹ nhàng bưng lên một cái ly rượu không, cầm lấy một bầu rượu, chậm rãi đổ đi vào."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Rượu đi vào chén rượu, dường như Cao Sơn Lưu Thủy, lại giống là trong núi dòng suối nhỏ, thanh âm không lớn, nhưng xen lẫn tại trong, để trước đó tiến công mà đến nhạc khúc, vì đó trì trệ."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Cảm nhận được thanh âm này đúng lúc xuất hiện tại nhạc khúc dính liền chỗ, Bạch âm sư biết gặp cường giả, lông mày giương lên, đánh đàn thủ pháp bỗng nhiên biến đổi."),t("br"),t("br"),n._v("Leng keng, leng keng, leng keng!"),t("br"),t("br"),n._v("Thanh âm tăng nhanh."),t("br"),t("br"),n._v("Mọi người lập tức cảm thấy rộng lớn thảo nguyên, tựa như có thiên quân vạn mã lao đến, bụi mù lượn lờ, mây đen biến ảo, đại địa chấn chiến."),t("br"),t("br"),n._v("“Đến hay lắm!”"),t("br"),t("br"),n._v("Đã sớm phỏng đoán nàng có thể như vậy, Trương Huyền nhẹ nhàng cười một tiếng, bầu rượu cùng trên bàn vừa để xuống, theo tay cầm lên một chiếc đũa, nhẹ nhàng đập vào chén rượu, bầu rượu cùng chứa đựng rau xanh trên mâm."),t("br"),t("br"),n._v("Đinh!"),t("br"),t("br"),n._v("Mặc dù đây đều là gốm sứ, thanh âm đơn điệu, nhưng bên trong có rượu cùng đồ ăn, liền hiện ra thanh âm bất đồng."),t("br"),t("br"),n._v("Đinh đinh đông, đinh đinh đông!"),t("br"),t("br"),n._v("Thanh âm cùng đàn ngọc so, so sánh một loại, nhưng mỗi một tiếng, đều vừa khéo rơi vào đối phương tiếng đàn biến hóa ở giữa, để cho người ta khí tức hỗn loạn."),t("br"),t("br"),n._v("Nếu như đem Ma âm sư tiếng đàn so sánh trong nước gợn sóng, Trương Huyền mỗi một cái, đều đánh vào gợn sóng đáy cốc, không chỉ kéo lấy đối phương tiếng đàn, thậm chí liền trái tim của nàng, đều muốn dựa theo hắn nhịp mà động."),t("br"),t("br"),n._v("“Nguy rồi? Cao thủ!”"),t("br"),t("br"),n._v("Bạch âm sư giờ mới hiểu được gặp một cái đối thủ như thế nào, con ngươi co rút lại, vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Ma âm đối công, nghe rất đơn giản, trên thực tế mười phần nguy hiểm, hơi không cẩn thận, tu vi tận tổn hại, nghiêm trọng, càng có khả năng tại chỗ tử vong."),t("br"),t("br"),n._v("Khẩu Phật tâm xà giết người, càng văn nhã chức nghiệp, càng mang theo lớn lao sát cơ."),t("br"),t("br"),n._v("Thật giống như danh sư, giáo thư dục nhân, làm gương sáng cho người khác, hòa ái dễ gần, một khi nổi giận, thiên địa đều có thể vì đó biến sắc, không người dám chặn!"),t("br"),t("br"),n._v("Ma âm cũng là như thế."),t("br"),t("br"),n._v("Đối phương dùng đũa gõ chén rượu cùng bộ đồ ăn, liền sinh ra uy lực lớn như vậy, nói rõ đối Ma âm nắm giữ, tuyệt đối vượt qua chính nàng, thậm chí cao không cách nào tưởng tượng!"),t("br"),t("br"),n._v("Đổi lại trước kia, gặp được mạnh như thế người, khẳng định không dám nhiều lời, lập tức ôm quyền nhận thua, có thể trốn xa hơn trốn xa hơn, nhưng... Vừa rồi khiêu khích quá ác, đối phương đã xuống tay độc ác, gõ thanh âm, cùng nàng tiếng đàn hoàn mỹ dung hợp lại cùng nhau, dường như chân khí so đấu, đã không cách nào đơn phương đình chỉ!"),t("br"),t("br"),n._v("Nói cách khác, một khi nàng bên này dừng lại, công kích của đối phương, thế tất cùng súc tích hồng thủy đồng dạng, cuộn trào mãnh liệt mà tới, đến lúc đó... Chặn cũng đỡ không nổi, nhẹ thì, khổ học nhiều năm Ma âm đem trôi theo nước chảy, nặng thì, trái tim nhảy ra cổ họng, tại chỗ tử vong!"),t("br"),t("br"),n._v("“Đáng giận!”"),t("br"),t("br"),n._v("Lông mày vung lên, không dám tiếp tục coi thường, hai tay đánh đàn, cả người đứng lên, bởi vì chân khí trong cơ thể vận chuyển quá nhanh, trên đầu bốc lên xuất ra đạo đạo khói trắng."),t("br"),t("br"),n._v("“Ra tay độc ác ư? Vậy thì tốt, ta thành toàn ngươi!”"),t("br"),t("br"),n._v("Thấy đối phương tiếng đàn càng ngày càng mạnh, Trương Huyền híp mắt lại, cũng không động thân, bên trái tay cầm lên mặt khác một chiếc đũa, đối lên trước mắt cái bàn đập tới."),t("br"),t("br"),n._v("Đông đông đông!"),t("br"),t("br"),n._v("Cái bàn là đặc thù gỗ, gõ lên thanh âm ngột ngạt, giống như trống to, nặng nề mà thâm trầm."),t("br"),t("br"),n._v("Mặc dù chỉ là tăng thêm một chiếc đũa, nhưng công kích lực độ, lập tức tăng lên không chỉ một lần."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Bạch âm sư bản thân liền không phải là đối thủ, lúc này Trương Huyền lại tăng thêm thanh âm, như thế nào chịu được, sắc mặt trắng nhợt, một ngụm máu tươi cuồng bắn ra, kìm lòng không được đụng phải đại điện một cái trên trụ đá."),t("br"),t("br"),n._v("“Lui? Lui được không?”"),t("br"),t("br"),n._v("Trương Huyền đũa vạch một cái, trước mắt đĩa lập tức phát ra tựa như chung đỉnh vang lên."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Lần nữa phun máu, Bạch âm sư chân khí trong cơ thể tuôn trào ra, tựa như bị người tại ngực cái đánh một quyền, sau lưng to lớn cột, không thể kiên trì được nữa, bị nàng cứ thế mà đụng gãy, bụi mù theo nóc phòng tản mát mà xuống."),t("br"),t("br"),n._v("Đầy đất bụi mù."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-868"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-870"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);