(window.webpackJsonp=window.webpackJsonp||[]).push([[2158],{2514:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-865-hai-ben-giao-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-865-hai-ben-giao-thu"}},[n._v("#")]),n._v(" Chương 865: Hai bên giao thủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 865: Hai bên giao thủ (phần 2)"),t("br"),t("br"),n._v("Kinh hồn hương là từ mấy trăm loại đặc thù dược liệu luyện chế mà thành, có thể đem Tằm Phong cảnh cường giả, đều mê ngất, nhưng loại này hương liệu, chỉ cần nhen lửa, liền sẽ không khác biệt công kích, hai người này khoảng cách gần như thế, chỉ sợ hai đầu răng kiếm linh hổ còn không có sự tình, bọn họ trước hết ngất đi!"),t("br"),t("br"),n._v("“Các vị thuốc tương sinh tương khắc, chỉ cần có thuốc, liền có phương pháp giải quyết, bọn họ đoán chừng đã phục dụng giải dược, không lo lắng sẽ xảy ra vấn đề!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Các vị thuốc đều là một vật khắc một vật, mặc kệ bao nhiêu lợi hại kịch độc, đều có thể tìm tới giải dược, chớ nói chi là nhưng mà gây nên người mê huyễn đồ vật."),t("br"),t("br"),n._v("“Ừm!” Mọi người gật đầu."),t("br"),t("br"),n._v("“Trương sư, một khi hơi khói vào sơn động, hai đầu răng kiếm linh hổ rất có thể liền bị mê ngất... Chúng ta làm sao bây giờ?” Đổng Hân nhìn qua."),t("br"),t("br"),n._v("Đối phương có kinh hồn hương, lại có Bán Thánh cường giả, một khi hai đầu linh thú bị mê ngất, nhận được Xích Huỳnh quả liền thành tất nhiên, bọn họ coi như muốn cướp cũng đã chậm."),t("br"),t("br"),n._v("“Tiên hạ thủ vi cường... Tốt nhất đuổi tại trước mặt bọn họ đem Xích Huỳnh quả nắm bắt tới tay!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Thiên tài địa bảo, tới trước được trước, bọn họ xuống kinh hồn hương không giả, nhưng chỉ cần mình đám người, trước đem đồ vật cầm tới, coi như đối phương nói ra tiêu đến, cũng không dùng!"),t("br"),t("br"),n._v("“Đuổi ở phía trước?” Mọi người không hiểu."),t("br"),t("br"),n._v("Đối phương đã tại sơn động trước mặt, hơn nữa lại phóng xuất ra kinh hồn hương, bọn họ giờ phút này coi như tiến lên, chỉ sợ không những không chiếm được Xích Huỳnh quả, làm không cẩn thận sẽ còn bị mê ngất, sống chết không khỏi bản thân khống chế."),t("br"),t("br"),n._v("“Các ngươi ở đây an tĩnh mang theo, có biến, liền nghĩ biện pháp rời đi, bảo vệ tốt an toàn của mình, ta đi vòng qua nhìn xem, có thể hay không tìm cơ hội trà trộn vào sơn động!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền bàn giao."),t("br"),t("br"),n._v("“Trà trộn vào đi?”"),t("br"),t("br"),n._v("Sơn động cứ như vậy lớn, hơn nữa hai người đã đi tới cửa động, làm sao trà trộn vào đi?"),t("br"),t("br"),n._v("“Các ngươi đây liền không cần phải để ý đến...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền cũng không giải thích, thân thể nhoáng một cái, hướng ra phía ngoài thoát ra, thời gian nháy mắt biến mất tại trong tầm mắt của mọi người."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Gặp hắn rời đi, Đổng Hân chờ người đưa mắt nhìn nhau, không rõ ràng cho lắm."),t("br"),t("br"),n._v("“Dựa theo Trương sư nói tới đi, hắn nói như vậy, khẳng định có biện pháp...”"),t("br"),t("br"),n._v("Qua nửa ngày, Hồ Yêu Yêu cắn răng, nói."),t("br"),t("br"),n._v("Tình huống trước mắt, bọn họ bó tay toàn tập, chỉ có thể nghe đối phương."),t("br"),t("br"),n._v("Kết hợp đối phương sự tích đến xem, có lẽ thật có thể ngoài dự liệu, đem Xích Huỳnh quả trước thời hạn nắm bắt tới tay."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tránh thoát tầm mắt mọi người, Trương Huyền tìm cái địa phương, khoanh chân ngồi dưới đất, tinh thần khẽ động, Vu hồn xuất thể."),t("br"),t("br"),n._v("Hiện nay loại tình huống này, muốn thần không biết quỷ không hay vào sơn động, chỉ có thể mượn nhờ Vu hồn."),t("br"),t("br"),n._v("Còn đường vòng đi qua, nhưng mà lý do thôi."),t("br"),t("br"),n._v("Lơ lửng không trung, Vu hồn chần chờ một chút, lấy ra một bình Nhất Nguyên Trọng Thủy, để thân thể nuốt, lại từ đan điền lấy ra vài luồng Thiên Đạo chân khí, cái này mới đem thu vào Thiên Nghĩ Phong sào huyệt."),t("br"),t("br"),n._v("Thân thể đã đi đến vô niệm vô tưởng cảnh giới, hoàn toàn có thể thoát ly Vu hồn tự mình tu luyện, vừa vặn về thời gian không lãng phí, hai không chậm trễ."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Khống chế trữ vật giới chỉ, bay lên trời, Vu hồn vòng qua hẻm núi bầu trời, lặng yên không tiếng động hướng trước sơn động mặt rơi tới."),t("br"),t("br"),n._v("Trên vách núi đá đâu đâu cũng có cây cối, một cái trữ vật giới chỉ, xuyên qua trong đó, căn bản không có khả năng phát hiện, Vu hồn lại đạt đến Đạp Hư cảnh đỉnh phong, mượt mà, sung mãn, cho dù có một vị Bán Thánh, cũng không biết đã có người đến sơn động trước mặt."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Vào sơn động, Trương Huyền lúc này mới thở phào nhẹ nhõm, giương mắt hướng phía trong nhìn lại."),t("br"),t("br"),n._v("Sơn động rộng rãi tĩnh mịch, một cái không nhìn thấy cuối cùng, lúc này, đã che kín thật mỏng khói mù, ngửi bên trên một cái, rất dễ dàng rơi vào say mê, không cách nào tự kềm chế."),t("br"),t("br"),n._v("Đổi lại trước kia, gặp được loại này mê vụ, nhất định phải cẩn thận, không dám tiếp xúc, nhưng hồn phách cùng Ngụy Như Yên giao hòa, đã có Tiên Thiên độc hồn thể năng lực, những dược vật này đối với hắn không còn tác dụng."),t("br"),t("br"),n._v("Chậm rãi tiến lên, đi một hồi, quả nhiên thấy một khỏa cỡ khoảng cái chén ăn cơm cổ quái cây cối xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Thân cây uốn lượn, tựa như dây leo, lá cây hiện lên màu vàng kim, phía trên kết rất nhiều nông trái cây màu đỏ."),t("br"),t("br"),n._v("“Còn giống như không thành thục...”"),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền liền biết, khoảng cách thành thục, có lẽ còn có một đoạn thời gian."),t("br"),t("br"),n._v("Xích Huỳnh quả thành thục về sau, sẽ toàn thân biến thành màu đỏ thắm, sau đó tản mát ra huỳnh quang, hiện tại nhưng mà đỏ nhạt, nói rõ cùng Đổng Hân đám người đoán đồng dạng, chí ít còn cần bảy, tám ngày, thậm chí thời gian nửa tháng, mới có thể thành thục."),t("br"),t("br"),n._v("Cây phía trước, nằm sấp một đầu to lớn răng kiếm linh hổ, dài hơn năm thước, cao hơn hai mét, tản mát ra làm người ta hít thở không thông cảm giác áp bách."),t("br"),t("br"),n._v("Còn có một đầu, thỉnh thoảng hướng ra phía ngoài nhìn, giống như có lẽ đã biết bên ngoài có người tới, ánh mắt bên trong mang theo lo lắng, tựa hồ tại do dự, có muốn hay không đem Xích Huỳnh quả hủy đi, sau đó chạy trốn."),t("br"),t("br"),n._v("Lúc này, kinh hồn hương khói mù, còn không có bay vào đến, hai đầu đại gia hỏa, tự nhiên cũng không biết, người bên ngoài, đã sớm chuẩn bị."),t("br"),t("br"),n._v("“Xem ra Đổng Hân tình báo rất chuẩn xác...”"),t("br"),t("br"),n._v("Nhìn thoáng qua, cây ăn quả bên trên quả thực chỉ có mười cái trái cây, hai đầu răng kiếm linh hổ, cũng đều là Tằm Phong cảnh đỉnh phong, Trương Huyền âm thầm gật đầu."),t("br"),n._v("Nữ nhân này, mặc dù sự tình lần trước không thận trọng, nhưng bình thường vẫn là rất đáng tin cậy."),t("br"),t("br"),n._v("Có thể điều tra như thế kỹ càng, chỉ sợ cái sơn động này đi vào không chỉ một lần."),t("br"),t("br"),n._v("Răng kiếm linh hổ mặc dù cẩn thận, tổng còn muốn ăn uống ngủ nghỉ, chỉ cần sơ sẩy, lấy thực lực của nàng, tiến vào bên trong, lặng lẽ dò xét, còn có thể làm được."),t("br"),t("br"),n._v("“Hiện tại hai cái biện pháp, thứ nhất, thả ra Tử Dương thú, đem hai người này tại chỗ chém giết, quang minh chính đại, chiếm hữu Xích Huỳnh quả... Như vậy mặc dù trực tiếp, nhưng rất dễ dàng bại lộ Vu hồn cùng Thiên Nghĩ Phong sào huyệt tồn tại! Thứ hai, nghĩ biện pháp thúc Xích Huỳnh quả, trực tiếp hái xong rời đi, thần không biết quỷ không hay, cũng tránh khỏi cùng hai vị kia danh sư gặp gỡ, ít đi không ít phiền phức...”"),t("br"),t("br"),n._v("Trương Huyền trong lòng suy tư."),t("br"),t("br"),n._v("Đem Tử Dương thú thả ra, giết chết hai người này, thuận tiện nhất, nhưng mà Tử Dương thú từ đâu tới đây, bản thân lại như thế nào trà trộn vào đến, cùng Hồ Yêu Yêu đám người sẽ rất khó giải thích."),t("br"),t("br"),n._v("“Loại thứ hai đi!”"),t("br"),t("br"),n._v("Suy đi nghĩ lại, vẫn là loại thứ hai ổn thoả."),t("br"),t("br"),n._v("Dù sao thứ này, là vật vô chủ, chỉ muốn lấy được, chính là mình, không cần thiết lễ nhượng."),t("br"),t("br"),n._v("“Trước muốn đem hai người này mê đi...”"),t("br"),t("br"),n._v("Có quyết định, Trương Huyền nhẹ nhàng cười một tiếng, Vu hồn lui về phía sau mấy bước, khống chế kinh hồn hương khói bụi, chậm rãi nhẹ nhàng tới."),t("br"),t("br"),n._v("Muốn thần không biết quỷ không hay, trộm đi trái cây, trước hết đem cái này hai đầu đại gia hỏa mê ngất, nếu không, bọn họ tận mắt thấy trái cây biến mất, cũng sẽ đoán được Vu hồn thủ đoạn."),t("br"),t("br"),n._v("Kinh hồn khói khói bụi, lặng yên không một tiếng động, tại Vu hồn khống chế bên dưới, càng thêm nồng đậm, hai đầu răng kiếm linh hổ, đang phòng bị phía ngoài người tu luyện xông tới, căn bản nghĩ không ra, đã đối bọn chúng hạ độc, không hề phát giác bên dưới, ngửi mấy thanh, liền toàn thân lay động, tựa như uống say, rơi vào mê huyễn cảnh giới."),t("br"),t("br"),n._v("Thấy hai đầu đại gia hỏa biến thành bộ dáng này, Trương Huyền biết, đã mất đi sức phán đoán, mấy bước đi tới Xích Huỳnh quả cây trước mặt."),t("br"),t("br"),n._v("Tinh thần khẽ động, Thiên Đạo chân khí hỗn hợp địa mạch linh dịch chảy xuôi đi qua."),t("br"),t("br"),n._v("Hồ Yêu Yêu nói qua, Xích Huỳnh quả còn chẳng biết lúc nào thành thục, hắn liền dám tới, không sợ chậm trễ viện trưởng kế vị nghi thức, chính là bởi vì có này thúc thủ đoạn."),t("br"),t("br"),n._v("Lúc trước thúc qua Thập Diệp hoa, biết phương pháp, địa mạch linh dịch hỗn hợp Thiên Đạo chân khí, chui vào thân cây, không đến mười phút đồng hồ, trước đó nông trái cây màu đỏ, liền mùi thơm nức mũi, trở nên đỏ thẫm cực kỳ, tản mát ra huỳnh quang."),t("br"),t("br"),n._v("“Thành thục!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, bàn tay vạch một cái, đem mười cái Xích Huỳnh quả thu vào hộp ngọc, Trương Huyền lúc này mới thở ra một hơi."),t("br"),t("br"),n._v("Quay đầu nhìn thoáng qua, thấy hai đầu rơi vào mê huyễn răng kiếm linh hồn, vẫn như cũ lảo đảo, còn không có tỉnh lại, Trương Huyền lúc này mới thở phào nhẹ nhõm, mang theo trữ vật giới chỉ, vội vã đi ra ngoài."),t("br"),t("br"),n._v("Mới vừa đi tới cửa động, liền thấy nhen lửa kinh hồn hương hai vị danh sư, chậm rãi hướng bên này đi tới, dường như muốn nhìn một chút, răng kiếm linh hổ có hay không trúng chiêu."),t("br"),t("br"),n._v("Không để ý tới đối phương, Trương Huyền nhẹ nhàng cười một tiếng, khống chế nhẫn, bút thẳng bay ra ngoài, không lâu sau lần nữa trở lại không trung."),t("br"),t("br"),n._v("Một lần nữa trở lại Hồ Yêu Yêu đám người phát hiện địa phương mà không đến được, lúc này mới đem thân thể một lần nữa phóng ra, tinh thần quay về."),t("br"),t("br"),n._v("Vu hồn ăn cắp Xích Huỳnh quả công phu, thân thể đã đem Ngũ Diệu Kim Thân tu luyện đến đệ tam trọng cảnh giới."),t("br"),t("br"),n._v("Đi đến loại cảnh giới này, thân thể lực lượng lần nữa bạo tăng gấp đôi, theo trước đó 8 triệu đỉnh, đạt đến 16 triệu đỉnh!"),t("br"),t("br"),n._v("Cộng thêm hồn lực, chân khí lời nói, đã nắm giữ 53 triệu đỉnh cự lực, so với Bán Thánh đỉnh phong, đều không hề yếu!"),t("br"),t("br"),n._v("(Bán Thánh sơ kỳ 40 triệu đỉnh, trung kỳ 44 triệu đỉnh, hậu kỳ 48 triệu đỉnh, đỉnh phong 52 triệu đỉnh!)"),t("br"),t("br"),n._v("Cảm nhận được trên người lăn mình lực lượng, Trương Huyền hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Đạp Hư cảnh sơ kỳ, liền nắm giữ có thể so với Bán Thánh đỉnh phong sức chiến đấu, nói ra tuyệt đối có thể dọa ngốc một đám người lớn."),t("br"),t("br"),n._v("Đây cũng không phải là thiên tài cùng yêu nghiệt, mà là tuyệt thế phong thái."),t("br"),t("br"),n._v("“Trở về...”"),t("br"),t("br"),n._v("Thở ra một hơi, ngăn chặn hưng phấn trong lòng, Trương Huyền cười nhạt một tiếng, đứng dậy, thân thể nhoáng một cái, hướng vừa rồi Hồ Yêu Yêu đám người vị trí bay vút đi."),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, liền cảm thấy một hồi kịch liệt năng lượng ba động, từng đạo sóng khí không ngừng bắn ra."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Trương Huyền tăng thêm tốc độ, thẳng tắp phóng đi, mấy hơi thở qua đi, lập tức nhìn thấy trước đó mênh mông chỗ, chân khí bay loạn, mấy người đang tại chiến đấu."),t("br"),t("br"),n._v("Tiết Chân Dương, Hồ Yêu Yêu, Đổng Hân còn có Long Thương Nguyệt, đang vây quanh trước đó trong hai người một cái, đủ loại công kích, tầng tầng lớp lớp, mà bị nhốt ở giữa vị này, mặc dù lấy một địch bốn, nhưng không có chút nào tan tác dấu hiệu, ngược lại thành thạo điêu luyện, chèn ép bốn người nhất định phải thi triển tuyệt chiêu mới có thể bảo mệnh."),t("br"),t("br"),n._v("“Là cái kia Tằm Phong cảnh đỉnh phong gia hỏa?”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Nếu như bọn họ vây công cái kia Bán Thánh cấp bậc cường giả, đối phương lấy một địch bốn ngược lại cũng thôi, có thể vây công lại là cùng thực lực bọn hắn tương đồng Tằm Phong cảnh gia hỏa!"),t("br"),t("br"),n._v("Tương đồng thực lực, bốn người liền một cái đều khó mà chiến thắng, thật hay giả?"),t("br"),t("br"),n._v("Hồ Yêu Yêu, Tiết Chân Dương... Mỗi một cái tại danh sư học viện, đều là đại danh đỉnh đỉnh nhân vật thiên tài, cùng cấp bậc có thể đủ thắng quá, có thể đếm được trên đầu ngón tay."),t("br"),t("br"),n._v("Mà bây giờ, bốn người liên thủ, không những không chiếm thượng phong, còn bị khắp nơi áp chế, cái tên này... Có phần quá khỏe khoắn chút!"),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, chỉ thấy vị kia Bán Thánh cường giả, nghiêng dựa vào một cái nham thạch bên trên, ánh mắt lạnh lùng, dường như chút nào đều không lo lắng đồng bạn của mình thất bại."),t("br"),t("br"),n._v("Xem ra, hắn đối với mình vị này đồng bọn, có rất lớn tự tin."),t("br"),t("br"),n._v("“Dừng tay!”"),t("br"),t("br"),n._v("Thấy tiếp tục đánh xuống, Hồ Yêu Yêu đám người tất nhiên ăn thiệt thòi, Trương Huyền cũng nhịn không được nữa, hét lớn một tiếng, thân ảnh nhoáng một cái, xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("(Canh thứ hai đến, xin nguyệt phiếu!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-873"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-875"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);