(window.webpackJsonp=window.webpackJsonp||[]).push([[2160],{2515:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-867-đinh-đinh-toi-phien-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-867-đinh-đinh-toi-phien-nguoi"}},[n._v("#")]),n._v(" Chương 867: Đỉnh Đỉnh, tới phiên ngươi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 867: Đỉnh Đỉnh, tới phiên ngươi! "),t("br"),t("br"),n._v("“Dĩ nhiên không phải, các ngươi thua, năm cái Xích Huỳnh quả, ta nguyện ý lấy năm mươi viên linh thạch thượng phẩm giá cả mua sắm!” Bán Thánh nói."),t("br"),t("br"),n._v("“Năm mươi viên?” Trương Huyền lắc đầu: “Lấy ra năm ngàn viên linh thạch thượng phẩm, ta hiện tại liền có thể bán cho ngươi!”"),t("br"),t("br"),n._v("Vì những này Xích Huỳnh quả, Hồ Yêu Yêu đám người chuẩn bị Nhất Nguyên Trọng Thủy, rất nhiều trận bàn, đều vượt qua cái giá tiền này, năm mươi viên mua năm cái... Thật là nói khoác không biết ngượng."),t("br"),t("br"),n._v("Muốn mua cũng được, thiếu đi năm ngàn viên, nói đều đừng nói!"),t("br"),t("br"),n._v("Bất quá, cẩn thận nói lời, năm mươi viên linh thạch thượng phẩm, cũng là không giá tiền thấp."),t("br"),t("br"),n._v("“Người trẻ tuổi nói chuyện phải thận trọng, ngươi mặc dù thiên tư không sai, thực lực cũng rất tốt, nhưng trong mắt ta, không tính là gì! Coi như danh sư tầm đó không cho phép tùy ý chém giết, nhưng giáo huấn ngươi một trận, để ngươi tu vi toàn bộ phế, còn có thể làm được!”"),t("br"),t("br"),n._v("Híp mắt lại, Bán Thánh thanh âm băng lãnh."),t("br"),t("br"),n._v("“Thế nào, không có tiền đổi uy hiếp?”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Không phải uy hiếp, mà là sự thật, bất kể danh sư hay là cái gì, thực lực còn là trọng yếu nhất!” Lạnh hừ một tiếng, Bán Thánh hai hàng lông mày vung lên, khí tức trên thân giống như kiểu lưỡi kiếm sắc bén bắn ra, đè chung quanh cây cối toàn bộ cúi đầu xuống."),t("br"),t("br"),n._v("Cảm nhận được cỗ khí tức này, Hồ Yêu Yêu đám người sắc mặt biến đổi."),t("br"),t("br"),n._v("Bán Thánh bọn họ cũng đã gặp không ít, nhưng trước mắt vị này, như cùng một chuôi sắc bén bảo kiếm, bất cứ lúc nào cũng sẽ phá không mà xuống, coi như bình thường Tòng Thánh cường giả, đều không có loại thực lực này."),t("br"),t("br"),n._v("“Ngươi ý tứ, nắm tay người nào lớn, ai có đạo lý?” Không để ý tới đối phương ép lên, Trương Huyền mang theo ngoạn vị nhìn qua."),t("br"),t("br"),n._v("“Không sai! Danh sư mặc dù coi trọng quy củ, nhưng quy củ là nắm đấm đến đánh vỡ! Ta khuyên các ngươi thức thời điểm, ngoan ngoãn đem Xích Huỳnh quả giao ra, miễn tổn thương hòa khí, nếu không, không ngại đối với các ngươi tiến hành danh sư khiêu chiến! Đến lúc đó, chẳng những không có linh thạch, làm không cẩn thận còn chỉ có thể nằm trở về.”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Bán Thánh cười lạnh, mang theo khí ngạo nghễ."),t("br"),t("br"),n._v("Danh sư tầm đó mặc dù không thể cướp đoạt, nhưng không có nghĩa là không thể so sánh đấu."),t("br"),t("br"),n._v("Nơi này có hay không cái khác danh sư tọa trấn, lấy thực lực của đối phương, coi như cố ý chèn ép, cũng không có người có thể nói ra cái gì."),t("br"),t("br"),n._v("Dù sao, mọi người tuổi tác không kém nhiều, thực lực không bằng, cũng là ngươi chính mình vấn đề."),t("br"),t("br"),n._v("“Tốt a, đã ngươi nói như vậy, chúng ta cũng hết cách rồi, A Tử, đi qua chơi chết hắn!”"),t("br"),t("br"),n._v("Thấy đối phương quyết ý dùng vũ lực giải quyết, Trương Huyền lắc đầu, hừ một tiếng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Mọi người còn không có kịp phản ứng, một đầu Tử Dương thú liền theo trong sơn cốc bay tới, rơi vào Bán Thánh trước mặt."),t("br"),t("br"),n._v("Vì không tiết lộ Thiên Nghĩ Phong sào huyệt, Trương Huyền đem Tử Dương thú lấy ra thời điểm, bỏ vào một khối nham thạch to lớn về sau, khoảng cách chỉ cần không phải quá xa lời nói, vẫn là có thể tuỳ tiện làm được."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Bán Thánh không nghĩ tới, đột nhiên thêm ra cái Thánh vực nhị trọng Tử Dương thú, biến sắc, bàn chân trên mặt đất một chút, đang muốn bay lên, chỉ thấy một cái thô to bàn tay chụp lại."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Thân thể một cái cứng ngắc, liên lật bảy, tám cái bổ nhào, đâm vào cách đó không xa trên vách núi đá, một ngụm máu tươi cuồng bắn ra."),t("br"),t("br"),n._v("Hắn mặc dù chiến lực kinh người, nhưng Tử Dương thú loại này Thánh thú, trời sinh liền có thể vượt cấp chiến đấu, lại thêm cấp bậc siêu hắn quá nhiều, căn bản ngăn cản không nổi."),t("br"),t("br"),n._v("“Đáng giận!”"),t("br"),t("br"),n._v("Biết trốn không thoát, vẻ mặt trầm thấp suýt nhỏ máu, Bán Thánh cổ tay khẽ đảo, một thanh trường kiếm xuất hiện tại lòng bàn tay, thân thể nhoáng một cái, hóa thành một đạo kiếm mang, thẳng tắp hướng Tử Dương thú bắn tới."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Nhìn thấy động tác của hắn, Trương Huyền nhẫn gật đầu không ngừng."),t("br"),t("br"),n._v("Khó trách cái tên này như thế tự ngạo, quả thực có tư cách kiêu ngạo, chỉ bằng vào một kiếm này, đừng nói Bán Thánh, coi như bình thường Tòng Thánh, chỉ sợ đều khó mà chống chọi."),t("br"),t("br"),n._v("Cho dù là hắn, cùng đối đầu, đoán chừng cũng chỉ có thua thiệt phần."),t("br"),t("br"),n._v("Đáng tiếc... Gặp Tử Dương thú!"),t("br"),t("br"),n._v("Đầu này danh sư học viện Thánh thú, đủ loại chiến đấu gặp qua không biết bao nhiêu lần, làm sao sẽ sợ một vị Bán Thánh, cười nhạt một tiếng, thô to bàn tay lăng không một trảo, một khỏa to cỡ miệng chén đại thụ liền bị nhổ lên, xem như gậy gỗ, đưa tay liền quất tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Đối phương trường kiếm khoảng cách Tử Dương thú còn có bảy, tám mét, liền bị quất trúng đầu, lần nữa bay ngược mà ra, dính vào vách đá, mặt mũi máu tươi."),t("br"),t("br"),n._v("Cả hai căn bản không phải một cái ngang nhau cấp, hoàn toàn không có chiến đấu cần thiết."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Giãy dụa lấy theo trên vách đá nhảy xuống, Bán Thánh phục một viên thuốc, hóa giải một cái thương thế, răng cắn chặt: “Tất cả mọi người là danh sư, ngươi lại làm cho Thánh thú trưởng bối ra tay, có phải hay không làm mất thân phận!”"),t("br"),t("br"),n._v("Hắn thấy, Tử Dương thú mạnh như thế người, khẳng định là bảo vệ bọn hắn tới lấy Xích Huỳnh quả trưởng bối."),t("br"),t("br"),n._v("Giữa đồng bối chiến đấu, ngươi lại làm cho trưởng bối ra tay, truyền đi, cũng không vẻ vang!"),t("br"),t("br"),n._v("“Trưởng bối?” Trương Huyền lắc đầu: “Đây là ta thú sủng, làm sao lại biến thành trưởng bối? Thuần thú sư chiến đấu, chưa nói qua thú sủng không thể nhúng tay đi!”"),t("br"),t("br"),n._v("“Ngươi thú sủng?”"),t("br"),t("br"),n._v("Bán Thánh ngẩn ngơ."),t("br"),t("br"),n._v("Hắn gặp qua không ít thuần thú sư, coi như đi đến Thất Tinh, cũng nhiều nhất thuần phục cao bản thân nhiều một, hai cái đại cấp bậc, trước mắt tiểu tử này, chẳng qua Đạp Hư cảnh, nhưng thuần phục một đầu Thần Thức cảnh Thánh thú..."),t("br"),t("br"),n._v("Cái này... Làm sao có thể?"),t("br"),t("br"),n._v("Đạp hư, Tàm Phong, Bán Thánh, Tòng Thánh, Trục Không, thần thức... Trọn kém năm cái đại cấp bậc!"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền thần sắc thản nhiên, vẫy vẫy tay, Tử Dương thú lập tức đi tới, to lớn đầu lâu cọ lung tung, hiện ra đến mức dị thường thân mật."),t("br"),t("br"),n._v("“Cái này...” Khóe miệng giật một cái, Bán Thánh mặt mũi xấu hổ."),t("br"),t("br"),n._v("Sớm biết, đối phương có cường đại như vậy thú sủng, là lợi hại như vậy thuần thú sư, đánh chết cũng không tìm phiền toái ah!"),t("br"),n._v("Thuần thú sư, luôn luôn cùng thú sủng cùng một chỗ chiến đấu, chuyện này truyền đi, không có người có thể nói cái gì, nhưng mà... Ai có thể nghĩ tới, kẻ trước mắt này, thế mà thuần phục một đầu siêu việt bản thân năm cái đại cấp bậc thú sủng!"),t("br"),t("br"),n._v("Thế thì còn đánh như thế nào?"),t("br"),t("br"),n._v("Lại dám ra tay, tuyệt đối là tìm tai vạ!"),t("br"),t("br"),n._v("“Làm sao? Ngươi không phải muốn dùng danh sư phương thức giao đấu ư? Ta như ngươi mong muốn, thuần thú là ta phụ tu một cái chức nghiệp, để thú sủng ra tay, cũng đại biểu thực lực của ta, tới đi, ngươi có thể thỏa thích ra ba chiêu, ta để A Tử cũng ra ba chiêu, đừng khách khí!”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("Vừa rồi cái tên này không phải phách lối vô cùng, cảm thấy ăn chắc bọn họ sao?"),t("br"),t("br"),n._v("Đã như vậy, vậy thì tới đi, ngươi nếu có thể tiếp lấy A Tử ba chiêu, coi như ta thua!"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Bán Thánh suýt chút nữa một ngụm máu phun ra."),t("br"),t("br"),n._v("Thánh vực nhị trọng Tử Dương thú, thật muốn ra tay, hoàn toàn có thể đem hắn miểu sát, còn ba chiêu... Một chiêu đều không tiếp nổi!"),t("br"),t("br"),n._v("“Thuần thú sư quả thực có thể mang theo thú sủng chiến đấu, nhưng... Chúng ta đã đều là danh sư, quá mức ỷ lại thuần thú sư chức nghiệp, có phải hay không có chút bỏ gốc lấy ngọn?”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Bán Thánh nói: “Ngươi có dám so với ta đấu một trận, chỉ cần không cho vị này Tử Dương tiền bối ra tay, thủ đoạn khác tùy ý tuyển! Chỉ cần trên chiến đấu có thể thắng được ta, chúng ta xoay người rời đi, không nói thêm nữa nửa câu nói nhảm...”"),t("br"),t("br"),n._v("“Thủ đoạn khác tùy ý tuyển?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Không sai!” Bán Thánh cắn răng."),t("br"),t("br"),n._v("Chỉ cần đối phương không mượn Tử Dương thú, nhờ vào thực lực của hắn, muốn chiến thắng mười phần đơn giản."),t("br"),t("br"),n._v("Mặc dù Ngô Hư không phải là đối thủ, nhưng vừa rồi chiến đấu, hắn cũng nhìn, chỉ muốn xuất thủ, ít nhất có chín mươi chín phần trăm nắm chắc."),t("br"),t("br"),n._v("“Thua, bồi thường một trăm viên linh thạch thượng phẩm, cùng ngươi so với một lần trước, cũng không sao!” Trương Huyền nói."),t("br"),t("br"),n._v("Tay không bắt sói, khẳng định không được, muốn so, tự nhiên phải có tiền đặt cược."),t("br"),t("br"),n._v("“Một trăm linh thạch thượng phẩm? Tốt, ngươi nếu thua, năm cái Xích Huỳnh quả!” Gặp hắn đáp ứng, Bán Thánh ánh mắt sáng lên."),t("br"),t("br"),n._v("“Ta còn không có thua qua...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Hừ, thua không thua, không phải dựa vào nói, mà là thực lực!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Bán Thánh lông mày vung lên, trường kiếm trong tay lắc một cái, phát ra thanh thúy vang lên: “Tới đi!”"),t("br"),t("br"),n._v("“Trước không vội, ta lại cùng ngươi xác nhận một chút, ngươi nói chỉ cần ta không cho A Tử ra tay, không dùng được thủ đoạn gì, thắng đều nhận đúng không?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Không sai, có điều, điều kiện tiên quyết là chiến đấu!” Bán Thánh nói."),t("br"),t("br"),n._v("“Ừm, chính là chiến đấu!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi!”"),t("br"),t("br"),n._v("Cười lạnh, Bán Thánh lần nữa khôi phục tự tin, ngạo nghễ đứng thẳng, cả người cùng trường kiếm tựa như dung hợp lại cùng nhau."),t("br"),t("br"),n._v("Không thể không nói, cái tên này đối kiếm lý giải, không thể so với Trương Huyền yếu, cũng đạt tới nhân kiếm hợp nhất Kiếm Tâm cảnh giới, nếu không phải như thế, cũng không có khả năng vượt cấp chiến đấu, một kiếm bắn ra, Tòng Thánh cũng khó khăn anh kỳ phong."),t("br"),t("br"),n._v("“Trương sư... Cẩn thận!”"),t("br"),t("br"),n._v("Thấy đối phương mặc dù bị Tử Dương tiền bối cuồng tát hai cái, thực lực nhưng không có tổn thất quá lớn tổn thương, Hồ Yêu Yêu tràn đầy lo lắng nhìn qua."),t("br"),t("br"),n._v("Nàng không biết Trương Huyền chuẩn xác thực lực, nhưng cấp bậc ở nơi đó bày biện, muốn vượt qua vị này Bán Thánh, khẳng định rất khó."),t("br"),t("br"),n._v("Không chỉ nàng lo lắng, Tiết Chân Dương mấy người cũng mang theo lo lắng."),t("br"),t("br"),n._v("Không là không tin Trương Huyền, mà là đối phương thực sự quá mạnh, bất luận khí tức hay là kiếm ý, đều không phải người bình thường có thể chống lại."),t("br"),t("br"),n._v("“Yên tâm đi, hắn nói qua chỉ cần không cho A Tử ra tay, cái khác hết thảy thủ đoạn đều có thể!”"),t("br"),t("br"),n._v("Thấy vẻ mặt của mọi người, Trương Huyền cười cười, cổ tay khẽ đảo, mí mắt vừa nhấc: “Đỉnh Đỉnh, tới phiên ngươi, đi qua đem cái tên này đánh một trận!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Vừa dứt lời, một cái cự đại Khí đỉnh xuất hiện ở trước mặt mọi người, trong bụng hỏa diễm bốc lên, đốt chung quanh một mảnh cực nóng."),t("br"),t("br"),n._v("“Đây là... Học viện thủ hộ Thánh khí, Kim Nguyên đỉnh?”"),t("br"),t("br"),n._v("Nhận ra được, Hồ Yêu Yêu suýt chút nữa không có ngã sấp xuống."),t("br"),t("br"),n._v("Học viện có một tôn thủ hộ Thánh khí, mặc dù chưa bao giờ thấy qua, lại tại thư tịch bên trên thấy qua, mạnh mẽ vô cùng, Tử Dương thú tiền bối cũng không là đối thủ."),t("br"),t("br"),n._v("Vốn cho rằng, vị này Trương sư, đem Tử Dương tiền bối thuần phục liền đủ nghịch thiên... Làm sao liên cái này hộ viện Thánh khí, đều thuần phục?"),t("br"),t("br"),n._v("Tiết Chân Dương, Long Thương Nguyệt, cũng đều mắt tối sầm lại, suýt ngất."),t("br"),t("br"),n._v("Học viện thủ hộ Thánh khí đều thuần phục..."),t("br"),t("br"),n._v("Khó trách muốn lập hắn làm viện trưởng, không lập cũng không được ah."),t("br"),t("br"),n._v("Thật không để hắn làm, hai thứ này cùng mang đi, danh sư học viện của cải chẳng khác nào bị cuốn hết..."),t("br"),t("br"),n._v("Hồ Yêu Yêu đám người khiếp sợ, đối diện Bán Thánh chỉ cảm thấy trong lòng có một vạn đầu Thần thú bay qua, miệng cùng cá đồng dạng mở ra, nhưng thở không được khí, suýt nín chết."),t("br"),t("br"),n._v("Ngươi thuần phục một đầu Thánh thú ta nhịn, khả năng ngươi là vận khí tốt, gặp được bị thương Thánh thú, có ân với nó... Có thể thuần phục một kiện Thánh khí, hơn nữa lợi hại như thế Thánh khí, đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Bất quá, về thời gian không cho phép hắn buồn bực, đối diện Kim Nguyên đỉnh, nghe được có thể đánh người, hưng phấn mà suýt nhảy lên, một tiếng la hét: “Yên tâm đi chủ nhân, cam đoan hoàn thành nhiệm vụ, nhìn ta Kim Nguyên đỉnh, vài phút đem hắn luyện thành xỉ than đá...”"),t("br"),t("br"),n._v("Ngay sau đó, Bán Thánh thấy hoa mắt, chỉ thấy cái tên này cứ thế mà đánh tới."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Trường kiếm trong tay lập tức bị đụng vỡ nát, còn không có kịp phản ứng, liền bị một cái đụng bay, xương ngực chặt đứt mười mấy cây, sống lưng áp sát vào trên vách đá, đập ra một cái “Đại” chữ cái hố, máu tươi không ngừng cuồng phún."),t("br"),t("br"),n._v("(Lão nhai liều mạng! Hiện tại là bảng nguyệt phiếu thứ mười, tăng lên một cái thứ tự, lão nhai tăng thêm một ngày! Hôm nay tăng thêm ngày đầu tiên, các vị, nguyệt phiếu đâu? Còn xin bầu cho lão nhai! Cảm tạ!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-875"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-877"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);