(window.webpackJsonp=window.webpackJsonp||[]).push([[2161],{2517:function(n,h,c){"use strict";c.r(h);var t=c(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-868-đot-pha-tam-phong-canh"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-868-đot-pha-tam-phong-canh"}},[n._v("#")]),n._v(" Chương 868: Đột phá, Tằm Phong cảnh!")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("br"),c("br"),n._v("Chương 868: Đột phá, Tằm Phong cảnh! (Phần 2)"),c("br"),c("br"),n._v("“Lộc huynh...”"),c("br"),c("br"),n._v("Ngô Hư vội vàng lao đến, theo trên tường đem hắn chụp xuống, cho ăn một cái chữa thương đan dược."),c("br"),c("br"),n._v("“Ngươi, ngươi...”"),c("br"),c("br"),n._v("Nuốt đan dược, vận chuyển một hồi chân khí, Bán Thánh Lộc huynh hơi thanh tỉnh một chút, lần nữa nhìn về phía cách đó không xa Trương Huyền, cảm giác đến sắp khóc."),c("br"),c("br"),n._v("Ngươi một cái Đạp Hư cảnh tiểu gia hỏa, có một đầu Thần Thức cảnh Thánh thú làm thú sủng, thì cũng thôi đi, còn có một cái Thánh khí... Ngươi là Khổng sư con riêng a?"),c("br"),c("br"),n._v("Nếu không làm sao nhiều như vậy đồ tốt?"),c("br"),c("br"),n._v("“Làm sao vậy? Ngươi nói chỉ cần không cần A Tử là được, thủ đoạn khác mặc ta tuyển, ta cũng không có vi phạm quy định!” Trương Huyền nói."),c("br"),c("br"),n._v("“Phốc!”"),c("br"),c("br"),n._v("Một hơi không có đi lên, Lộc huynh lần nữa phun ra một ngụm máu tươi."),c("br"),c("br"),n._v("Chuyện này là sao!"),c("br"),c("br"),n._v("Hắn vốn cho rằng đối phương chỉ có một cái Tử Dương thú, cái khác không có gì, chỉ cần hạn chế, liền có thể nhẹ nhõm đem hắn cuồng loạn một chầu, nằm mơ đều không nghĩ tới... Còn có một cái đỉnh!"),c("br"),c("br"),n._v("F*ck you ah!"),c("br"),c("br"),n._v("Ta là muốn cùng ngươi luận võ, mà không phải cùng ngươi Thánh khí... Quy củ là không có vi phạm, nhưng này làm sao đánh?"),c("br"),c("br"),n._v("Căn bản không có cách nào đánh được không?"),c("br"),c("br"),n._v("“Ta nói, là không mượn bảo vật, không mượn Thánh thú, ngươi có dám cùng ta tỷ thí? Chính là dùng ngươi thực lực bản thân cùng ta chiến đấu...”"),c("br"),c("br"),n._v("Cắn răng một cái, Lộc huynh nói."),c("br"),c("br"),n._v("Sợ đối phương lấy thêm ra lợi hại gì pháp bảo, hắn nói thẳng dùng thực lực bản thân chiến đấu."),c("br"),c("br"),n._v("“A, ngươi muốn cùng ta luận võ?” Trương Huyền mí mắt vừa nhấc."),c("br"),c("br"),n._v("“Không sai, ngươi có dám đáp ứng?” Lộc huynh cắn răng."),c("br"),c("br"),n._v("Hắn luôn luôn kiêu ngạo, kết quả hôm nay chịu đến khuất nhục, so trước đó cả một đời đều nhiều, nếu là không phát tiết ra ngoài, về sau đoán chừng thành liền sẽ dừng bước nơi này."),c("br"),c("br"),n._v("Mặc dù đường đường Bán Thánh khiêu chiến Đạp Hư cảnh có lấy lớn hiếp nhỏ chi ngại, nhưng đối phương thủ đoạn phong phú, tầng tầng lớp lớp, liền xem như hắn, đều cảm thấy chưa chắc có hoàn toàn chắc chắn."),c("br"),c("br"),n._v("“Cá cược gấp bội!” Trương Huyền nhàn nhạt nhìn qua."),c("br"),c("br"),n._v("Muốn cùng hắn luận võ, cũng không phải là không thể được, chỉ cần tiền đủ, mọi chuyện đều tốt thương nghị."),c("br"),c("br"),n._v("“Tốt!”"),c("br"),c("br"),n._v("Mắt sáng lên, Lộc huynh gật đầu."),c("br"),c("br"),n._v("đănｇ nhập http://ngantruyen.com/ để đọctruyện"),c("br"),n._v("“Nhớ kỹ, vừa rồi ngươi thua, đã nợ ta một trăm viên linh thạch thượng phẩm!” Trương Huyền nói."),c("br"),c("br"),n._v("“Yên tâm, chỉ cần ta thua, linh thạch sẽ giao, đương nhiên, ngươi nếu thua, cũng hy vọng có thể thực hiện cá cược!” Lộc huynh khẽ nói."),c("br"),c("br"),n._v("“Không có vấn đề, như vậy đi, ta cũng không khi dễ ngươi, cho ngươi một canh giờ điều tức, đợi thương thế không sai biệt lắm tái chiến đấu, nếu không, ta sợ ngươi thua không thừa nhận!” Trương Huyền nói tiếp."),c("br"),c("br"),n._v("“Ngươi...”"),c("br"),c("br"),n._v("Đối rõ ràng là lời hữu ích, chẳng biết tại sao nghe được hắn trong tai, nhưng giống như là đang cười nhạo, Lộc huynh tức thiếu chút nữa lại phun ra một ngụm máu, chậm cả buổi, lúc này mới hít sâu một hơi: “Tốt!”"),c("br"),c("br"),n._v("Hắn mãnh liệt như vậy sức chiến đấu, là đã trải qua vô số chém giết mới ma luyện đi ra, biết thời khắc mấu chốt không thể hành động theo cảm tính, một canh giờ, mặc dù thương thế không thể hoàn hảo, nhưng có thể để cho sức chiến đấu khôi phục lại 95% trở lên."),c("br"),c("br"),n._v("Đối với hắn mà nói, cái này là đủ rồi!"),c("br"),c("br"),n._v("“Ngô Hư, đưa ngươi tiểu hoàn đan cho ta hai cái!”"),c("br"),c("br"),n._v("Quay đầu nhìn về phía Ngô Hư."),c("br"),c("br"),n._v("“Vâng!” Ngô Hư gật gật đầu, mặc dù có chút không nỡ, nhưng cũng biết Lộc huynh là vì hắn mới bị thương, lấy ra hai viên thuốc đưa tới."),c("br"),c("br"),n._v("Tiểu hoàn đan cấp sáu đan dược, đối Thánh vực khôi phục thương thế đều có cực lớn hiệu quả trị liệu, hai viên thuốc đi xuống, coi như không thể phục hồi, cũng có thể có rất lớn đổi mới."),c("br"),c("br"),n._v("Tiếp nhận tiểu hoàn đan, há miệng nuốt xuống, dược lực trong nháy mắt tại thể nội hòa tan, nguyên bản trắng sắc mặt khôi phục nhanh chóng."),c("br"),c("br"),n._v("“Chủ nhân, cái tên này, có thể chống đỡ ta đụng một cái còn chưa có chết, thực lực... Không đơn giản!”"),c("br"),c("br"),n._v("Nhìn hắn khôi phục thương thế, Kim Nguyên đỉnh nhìn về phía Trương Huyền, Tử Dương thú cũng nhìn lại."),c("br"),c("br"),n._v("Một thú một đỉnh đều cùng vị này Lộc huynh giao thủ qua, mặc dù thoạt nhìn theo chà đạp hài đồng giống như, nhưng chúng nó biết, thực lực của người này không đơn giản, chủ nhân mặc dù cũng không yếu, so sánh cùng nhau, chỉ sợ còn kém không ít."),c("br"),c("br"),n._v("“Ta biết!”"),c("br"),c("br"),n._v("Trương Huyền gật đầu."),c("br"),c("br"),n._v("Thông qua Minh Lý Chi Nhãn cùng Thiên Đạo thư viện, hắn tự nhiên biết thực lực của đối phương cực mạnh, hắn đích xác so ra kém."),c("br"),c("br"),n._v("Bất quá, chân chính chiến đấu cùng lực lượng mạnh mẽ là một chuyện, tùy cơ ứng biến cùng phán đoán, lại là một chuyện khác."),c("br"),c("br"),n._v("Mặc dù hắn tự nhận là chắc chắn sẽ không thua, nhưng để cho an toàn, vẫn là muốn chuẩn bị một chút, nguyên nhân chính là như thế, mới làm cho đối phương khôi phục một canh giờ."),c("br"),c("br"),n._v("Thời gian này, cho đối phương khôi phục thương thế, cũng cho hắn một cái giảm xóc."),c("br"),c("br"),n._v("“Các ngươi nhưng có Đạp Hư cảnh tu luyện công pháp? Càng nhiều càng tốt!”"),c("br"),c("br"),n._v("Trương Huyền nhìn về phía Hồ Yêu Yêu đám người."),c("br"),c("br"),n._v("Nhanh nhất tăng thực lực lên phương pháp, chính là tu luyện."),c("br"),c("br"),n._v("Một canh giờ, đối người khác mà nói, không tính là gì, nhưng đối với hắn mà nói, chỉ cần Thiên Đạo công pháp gom góp, hoàn toàn có thể nhanh chóng tấn cấp!"),c("br"),c("br"),n._v("“Có!”"),c("br"),c("br"),n._v("Hồ Yêu Yêu đám người gật đầu."),c("br"),c("br"),n._v("Các nàng thân là Tằm Phong cảnh cường giả đỉnh phong, Đạp Hư cảnh công pháp, chuẩn bị không ít, vừa đến, tự mình tu luyện, thứ hai, cũng tốt cho học được bọn thuộc hạ giảng giải, mỗi người đều sao chép không dưới mấy trăm quyển."),c("br"),c("br"),n._v("Tập hợp, Trương Huyền con mắt đảo qua, lập tức đem hắn thu nhập trong óc."),c("br"),c("br"),n._v("“Tằm Phong cảnh đây này?”"),c("br"),c("br"),n._v("Tinh thần khẽ động, tạo thành một bản Thiên Đạo thư tịch, Trương Huyền hỏi lần nữa."),c("br"),c("br"),n._v("“Tằm Phong cảnh công pháp bí tịch không nhiều, ta chỉ có hai mươi bản!”"),c("br"),c("br"),n._v("“Ta cũng chỉ có hơn hai mươi bản! Đổng Hân so sánh cẩn thận, có thể có thể so sánh nhiều một ít!”"),c("br"),n._v(" “Ta có một ít, nhưng cũng chỉ có hơn bốn mươi bản!”"),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("Rất nhanh bốn người đem Tằm Phong cảnh công pháp cũng quyên góp một cái, đại khái chừng một trăm bản bộ dạng."),c("br"),c("br"),n._v("Tiếp tục thu vào, sửa soạn xong hết, Trương Huyền cái này mới nhìn sang."),c("br"),c("br"),n._v("Cùng đoán đồng dạng, Đạp Hư cảnh Thiên Đạo công pháp, nhẹ nhõm gom góp, nhưng Tằm Phong cảnh rõ ràng còn kém rất lớn một đoạn."),c("br"),c("br"),n._v("Bất quá, đột phá Tằm Phong cảnh phương pháp, ngược lại là mười phần hoàn chỉnh."),c("br"),c("br"),n._v("Nói cách khác, theo Đạp Hư cảnh sơ kỳ, đến Tằm Phong cảnh sơ kỳ Thiên Đạo công pháp, toàn bộ gom góp!"),c("br"),c("br"),n._v("“Thay ta thủ hộ, ta muốn tu luyện!”"),c("br"),c("br"),n._v("Mặc dù không có sưu tập đến Tằm Phong cảnh đỉnh phong công pháp, có thể tới Tằm Phong cảnh, Trương Huyền cũng cảm thấy rất hài lòng, cười cười, phân phó Hồ Yêu Yêu đám người một câu."),c("br"),c("br"),n._v("“Vâng!”"),c("br"),c("br"),n._v("Mọi người gật đầu, nguyên một đám liếc mắt nhìn nhau, khắp khuôn mặt là cổ quái."),c("br"),c("br"),n._v("Muốn tỷ võ, ngươi lại tu luyện... Cái này chân phật ôm, có phải hay không có chút quá muộn chút?"),c("br"),c("br"),n._v("Trong lòng mặc dù nghi ngờ, nhưng đối phương đã nói, mấy người vẫn gật đầu, đem hắn chặn ở phía sau, nhìn chằm chằm cách đó không xa Ngô Hư, sợ hắn thừa cơ làm loạn."),c("br"),c("br"),n._v("Bất quá, bất kể Ngô Hư hay là Lộc huynh, tại Xích Huỳnh quả bên trên, có chút chỉ vì cái trước mắt, nhưng ở tỷ thí bên trên, hay là mười phần kiêu ngạo, cũng không qua tới quấy rối, tất cả đều toàn lực khôi phục thương thế."),c("br"),c("br"),n._v("Tranh thủ một canh giờ sau, thực lực có thể phát huy đến cực hạn."),c("br"),c("br"),n._v("Thấy bọn họ loại thái độ này, Hồ Yêu Yêu thở phào nhẹ nhõm, đang muốn hỏi thăm Tử Dương tiền bối, Trương sư có thể chiến thắng nắm chắc có bao lớn, liền nghe đến một bên Đổng Hân, kìm lòng không được “Ồ!” Một tiếng."),c("br"),c("br"),n._v("“Làm sao vậy?”"),c("br"),c("br"),n._v("Mấy người tất cả đều nhìn sang."),c("br"),c("br"),n._v("“Các ngươi nhìn... Trương sư!”"),c("br"),c("br"),n._v("Thân thể cứng ngắc, Đổng Hân nhịn không được về phía sau một chỉ."),c("br"),c("br"),n._v("“Trương sư?”"),c("br"),c("br"),n._v("Hồ Yêu Yêu đám người nhìn sang, cũng tất cả đều sững sờ."),c("br"),c("br"),n._v("“Tại sao ta cảm giác... Trương sư thực lực, hình như là... Đạp Hư cảnh trung kỳ?” Long Thương Nguyệt có chút nhịn không được, nói."),c("br"),c("br"),n._v("Vừa rồi Trương sư ra tay, bọn họ nhìn rõ rõ ràng ràng, chỉ có Đạp Hư cảnh sơ kỳ, làm sao mấy người liền thủ ở phía trước, cảnh giác một cái Ngô Hư đám người, cũng cảm giác thực lực của người này, có vẻ như... Đột phá?"),c("br"),c("br"),n._v("“Không phải hình như, mà là... Quả thực là Đạp Hư cảnh trung kỳ!” Hồ Yêu Yêu gật đầu: “Hẳn là Trương sư đột phá Đạp Hư cảnh sơ kỳ đã rất lâu rồi, tích lũy đầy đủ, cũng đến sắp đột phá tình trạng!”"),c("br"),c("br"),n._v("Đột phá một cái tiểu cấp bậc, không tính là gì, nếu như trước đó liền đi đến điểm tới hạn, tu luyện đột phá, là rất đang giấu."),c("br"),c("br"),n._v("Không có gì ngạc nhiên."),c("br"),c("br"),n._v("“Đúng vậy a, hẳn là như vậy, khó trách Trương sư dám đáp ứng cùng đối phương tỷ thí, nguyên lai lập tức liền muốn đột phá đến Đạp Hư cảnh trung kỳ, bất quá... Một cái tiểu cấp bậc chênh lệch không tính là quá lớn đi... A?”"),c("br"),c("br"),n._v("Gật gật đầu, Tiết Chân Dương đang đang lầm bầm lầu bầu, đột nhiên một cái thét lên phát ra."),c("br"),c("br"),n._v("“Ngươi lại làm sao?”"),c("br"),c("br"),n._v("Gặp hắn cái bộ dáng này, Long Thương Nguyệt một mặt không nói."),c("br"),c("br"),n._v("Bất kể nói thế nào, bọn họ đều là danh sư học viện đỉnh phong nhất học sinh, nếm qua đã gặp chủ, làm sao hôm nay đi theo Trương sư sau lưng, nhất kinh nhất sạ, theo không có thấy qua việc đời nhà quê đồng dạng?"),c("br"),c("br"),n._v("“Ta làm sao... Cảm giác Trương sư hình như là Đạp Hư cảnh hậu kỳ? Chẳng lẽ ta nhìn lầm...”"),c("br"),c("br"),n._v("Vẻ mặt trắng bệch, Tiết Chân Dương dùng sức dụi mắt."),c("br"),c("br"),n._v("Vừa rồi hắn nhìn kỹ, Trương sư là Đạp Hư cảnh trung kỳ ah, làm sao hiện tại lại cảm thấy giống như là hậu kỳ? Coi như ảo giác lại lớn, cũng không trở thành đại thành như vậy đi!"),c("br"),c("br"),n._v("“Hậu kỳ? Ngươi nói đùa cái gì, chúng ta mấy người đều nhìn, quả thực là trung kỳ...”"),c("br"),c("br"),n._v("Long Thương Nguyệt cười một tiếng, đang muốn chế giễu hắn gần đây nhãn lực cũng không được, con mắt cũng một cái trợn tròn: “Bọn ta!”"),c("br"),c("br"),n._v("“Dường như... Thật đúng là hậu kỳ!” Hắn cũng nhanh muốn khóc."),c("br"),c("br"),n._v("Thấy hai người này giống như điên, Hồ Yêu Yêu, Đổng Hân vội vàng nhìn lại, không ngừng dụi mắt."),c("br"),c("br"),n._v("Dường như... Trương sư thật đúng là Đạp Hư cảnh hậu kỳ!"),c("br"),c("br"),n._v("Chẳng lẽ, trước đó thật nhìn lầm?"),c("br"),c("br"),n._v("Không đến mức đi!"),c("br"),c("br"),n._v("Bốn người đồng thời nhìn lầm?"),c("br"),c("br"),n._v("Có thể... Hiện tại bất kể thế nào nhìn, đều là Đạp Hư cảnh hậu kỳ chắc chắn, không có một chút vấn đề ah!"),c("br"),c("br"),n._v("“Hình như... Không phải Đạp Hư cảnh hậu kỳ, là... Đạp Hư cảnh đỉnh phong!”"),c("br"),c("br"),n._v("Mấy người khiếp sợ còn không có kết thúc, Đổng Hân dùng thanh âm khàn khàn nói."),c("br"),c("br"),n._v("“Đỉnh phong?”"),c("br"),c("br"),n._v("Chúng người ánh mắt hoa lên, Hồ Yêu Yêu một mặt muốn khóc biểu lộ: “Tại sao ta cảm giác, hắn đang trùng kích Tằm Phong cảnh? Hình như chính là đang trùng kích... Ách, không đúng, đã là Tằm Phong cảnh cường giả...”"),c("br"),c("br"),n._v("Ba người khác lần nữa ngẩn người, quả nhiên phát hiện, sau lưng thanh niên, đã là Tằm Phong cảnh sơ kỳ cường giả!"),c("br"),c("br"),n._v("Theo Đạp Hư cảnh sơ kỳ đến Tằm Phong cảnh sơ kỳ, trọn một cái đại cấp bậc... Cái tên này mới dùng nửa canh giờ... Coi như tận mắt nhìn thấy, đều cảm thấy muốn choáng váng."),c("br"),c("br"),n._v("“Đổng Hân, ngươi theo Đạp Hư cảnh sơ kỳ đến Tằm Phong cảnh sơ kỳ, dùng bao lâu?”"),c("br"),c("br"),n._v("Cảm giác ba suýt rơi trên mặt đất, Hồ Yêu Yêu nhịn không được hỏi."),c("br"),c("br"),n._v("“Hai năm lẻ bốn tháng... Ta loại tu luyện này tốc độ, đã tại danh sư học viện đều sắp xếp thượng đẳng...”"),c("br"),c("br"),n._v("Đổng Hân thanh âm phát khô."),c("br"),c("br"),n._v("Lúc trước hắn hai năm lẻ bốn tháng liền tăng lên một đại cấp bậc, hào hứng phấn chấn, lòng tin mười phần, cảm giác bên dưới anh hùng thiên hạ ngoài ta còn ai..."),c("br"),c("br"),n._v("Nhìn thấy trước mắt thanh niên này mới hiểu được, nàng vậy cũng gọi tốc độ?"),c("br"),c("br"),n._v("Quả thực cùng rùa đen không có khác nhau chút nào!"),c("br"),c("br"),n._v("Khó trách tại trưởng lão viện, cái tên này nói gần đây bận quá, tốc độ tu luyện chậm, lúc ấy còn cảm thấy có phải hay không đang khoác lác, tận mắt thấy mới biết được, căn bản không phải khoác lác, còn khiêm tốn không muốn sống nữa..."),c("br"),c("br"),n._v("Nghĩ đến cái này, nhịn không được run rụt lại."),c("br"),c("br"),n._v("Nếu như chạy tới theo thập đại trường lão nói, Trương sư thật rất khiêm tốn, rất biết điều... Những này trưởng lão, có thể hay không cảm thấy nàng đang khoác lác, trực tiếp đem nàng ném đi?"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-876"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-878"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);