(window.webpackJsonp=window.webpackJsonp||[]).push([[2165],{2520:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-871-toan-bo-đot-pha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-871-toan-bo-đot-pha"}},[n._v("#")]),n._v(" Chương 871: Toàn bộ đột phá")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 871: Toàn bộ đột phá (phần 2)"),t("br"),t("br"),n._v("“Nếu như không nhìn lầm, hai vị này, hẳn là cái gọi là Chiến sư a?”"),t("br"),t("br"),n._v("Thở ra một hơi, trầm tĩnh lại, Trương Huyền mới phát giác được toàn thân mỏi mệt."),t("br"),t("br"),n._v("Cùng đối phương chiến đấu, thoạt nhìn giành được nhẹ nhõm, trên thực tế, nhưng tinh thần cao độ tập trung, chênh lệch một phân một hào đều không được, chỉ chiến đấu thêm vài phút đồng hồ, cả người tựa như móc rỗng đồng dạng."),t("br"),t("br"),n._v("“Tuổi còn trẻ liền có như thế sức chiến đấu, danh sư trang phục lại cùng chúng ta không quá tương đồng, hẳn là...”"),t("br"),t("br"),n._v("Đổng Hân nhẹ gật đầu."),t("br"),t("br"),n._v("Nàng xem qua không ít sách vở, biết Chiến sư cái nghề nghiệp này đáng sợ, trước đó không nghĩ tới nhiều như vậy, lúc này Trương Huyền một nhắc nhở, lập tức hiểu được."),t("br"),t("br"),n._v("Hai cái này, thật là có khả năng chính là!"),t("br"),t("br"),n._v("Nếu không danh sư giao đấu, có rất nhiều phụ tu, rất ít trực tiếp so tu vi."),t("br"),t("br"),n._v("“Chiến sư?”"),t("br"),t("br"),n._v("Còn lại mấy người nhìn nhau, đồng thời gật đầu."),t("br"),t("br"),n._v("Cũng đúng, chỉ sợ cũng chỉ có loại này trong truyền thuyết, sức chiến đấu mạnh nhất danh sư, mới có thực lực như thế!"),t("br"),t("br"),n._v("Cùng cấp bậc bên dưới, lấy một địch bốn, vẫn như cũ để bọn hắn chật vật không chịu nổi."),t("br"),t("br"),n._v("“Trương sư ngay cả Chiến sư đều có thể thắng được nhẹ nhàng như vậy, chẳng phải cũng đạt tới Chiến sư tiêu chuẩn?” Nhớ tới cái gì, Hồ Yêu Yêu nhìn lại."),t("br"),t("br"),n._v("“Nhẹ nhõm?” Trương Huyền cười khổ lắc đầu: “Hắn hạ thủ lưu tình, nếu không... Có thể hay không thắng, còn chưa biết được!”"),t("br"),t("br"),n._v("t r u y e n c"),t("br"),n._v("u at u i n e t  Ngoại nhân thoạt nhìn, hắn giành được là rất dễ dàng, trên thực tế lại không phải như thế!"),t("br"),t("br"),n._v("“Hạ thủ lưu tình?” Mọi người không hiểu."),t("br"),t("br"),n._v("Ngươi dùng cỏ tranh, đối phương dùng tuyệt phẩm Linh khí trường kiếm, đối chiến nhiều như vậy chiêu, một chiêu đều không đánh trả, làm sao có thể là lưu tình?"),t("br"),t("br"),n._v("“Nếu như lần này ta dùng chính là tuyệt phẩm Linh khí, hai người thắng bại, còn tại chưa định chi trời! Dùng cỏ dại, người này tâm cao khí ngạo, tất nhiên sẽ không chiếm vũ khí bên trên tiện nghi, vậy thì hạn chế kiếm pháp thi triển, lại thêm, cố ý chặt đứt gỗ tròn, để lại cho hắn chân khí sắc bén cảm giác... Lúc này mới ép không ngừng lui về phía sau, nếu không, muốn vượt qua, khó!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lần này luận võ, thoạt nhìn nhưng mà chiêu số bên trên tranh đoạt, trên thực tế, còn chưa bắt đầu, liền sử dụng tâm lý chiến."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Mọi người giờ mới hiểu được tới."),t("br"),t("br"),n._v("Vốn là bọn họ đối cỏ dại cũng không để ý, nhưng Trương sư tay nâng cỏ rơi, to cỡ miệng chén gỗ tròn trong nháy mắt chặt đứt, một chiêu này, để hắn cảm nhận được tim đập nhanh."),t("br"),t("br"),n._v("Chắc hẳn vị kia Lộc Thành cũng là như vậy, cái này mới không dám cùng cỏ dại tiếp xúc, từ đó làm cho một chiêu mất đi tiên cơ, lại khó vãn hồi."),t("br"),t("br"),n._v("“Được rồi, không muốn nhiều như vậy, đây là Xích Huỳnh quả, điều chỉnh trạng thái, mau chóng dùng, tranh thủ lại trở về trước, đột phá ràng buộc!”"),t("br"),t("br"),n._v("Điều tức một hồi, cảm giác thân thể khôi phục, Trương Huyền lúc này mới cổ tay khẽ đảo, đem Xích Huỳnh quả lấy ra ngoài."),t("br"),t("br"),n._v("“Trương sư... Thật lấy được?”"),t("br"),t("br"),n._v("“Làm sao sẽ trong tay ngươi?”"),t("br"),t("br"),n._v("Tất cả mọi người nháy con mắt, tràn đầy không thể tin được."),t("br"),t("br"),n._v("Bọn họ một mực tại bên ngoài sơn động trông coi, tận mắt thấy Lộc Thành, Ngô Hư hai người xông đi vào, sau đó thất vọng trở về, chưa bao giờ thấy qua Trương sư đi vào... Xích Huỳnh quả làm sao thật đến trên tay hắn?"),t("br"),t("br"),n._v("“Ta theo sơn cốc đằng sau tìm được một cái khác vào miệng, trước thời hạn lẻn đi vào!”"),t("br"),t("br"),n._v("Trương Huyền thuận miệng nói."),t("br"),t("br"),n._v("Vu hồn sự tình không thể nói, tự nhiên chỉ có thể dùng cái khác lấy cớ, còn sơn động có hay không cái khác vào miệng, hiện tại vật tới tay, đoán chừng cũng không có người đi kiểm tra."),t("br"),t("br"),n._v("“Lợi hại...”"),t("br"),t("br"),n._v("Tất cả mọi người tràn đầy sợ hãi thán phục cùng khen ngợi."),t("br"),t("br"),n._v("Loại này vận khí tốt đều đụng phải, đem hắn mời tới, thật là quá đúng."),t("br"),t("br"),n._v("“Cái này là địa mạch linh dịch, mỗi người một hồ lô, khôi phục thể lực, liền bắt đầu xung kích Bán Thánh!” Cổ tay khẽ đảo, lấy ra bốn hồ lô địa mạch linh dịch đưa tới."),t("br"),t("br"),n._v("Cái này bốn cái hồ lô rõ ràng so bán cho Ngô Hư cái kia không lớn lắm, có điều, tràn đầy linh dịch lời nói, giá trị cũng là không ít, tuyệt đối vượt qua trước đó Nhất Nguyên Trọng Thủy."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Đa tạ Trương sư!”"),t("br"),t("br"),n._v("Trước mắt vị này vừa mới bán ra một hồ lô, bọn họ biết giá cả, nhiều như vậy, tuyệt đối vượt qua năm mươi viên linh thạch thượng phẩm, bây giờ lại trực tiếp đưa cho bọn họ..."),t("br"),t("br"),n._v("Phần ân tình này, quả thực khó mà trả lại!"),t("br"),t("br"),n._v("Hốc mắt tất cả đều đỏ lên, biết đột phá thành công, mới không cô phụ đối phương hi vọng, bốn người lập tức tràn đầy đấu chí."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Có Tử Dương thú cùng Kim Nguyên đỉnh thủ ở chung quanh, cũng không cần lo lắng có người qua tới quấy rối, bốn người khoanh chân ngồi dưới đất, bắt đầu tu luyện."),t("br"),t("br"),n._v("Sau ba canh giờ, tất cả đều đem trạng thái điều đến đỉnh phong nhất, bắt đầu dùng Xích Huỳnh quả."),t("br"),t("br"),n._v("Không hổ là tăng lên Bán Thánh đột phá tỷ lệ Thánh dược, từng nuốt về sau, tinh thần lập tức trở nên càng thêm cường đại, linh hồn cũng càng thêm rõ ràng, Hồ Yêu Yêu tiếp thụ qua Trương Huyền chuyên môn chỉ điểm, đột phá nhanh nhất, đi đầu thành công."),t("br"),t("br"),n._v("Sau đó là Đổng Hân."),t("br"),t("br"),n._v("Cô bé này, đã trải qua trưởng lão viện sự tình, tâm trí so trước kia càng thêm kiên định, theo sát ở phía sau đột phá, thành Bán Thánh cường giả."),t("br"),t("br"),n._v("Lại đằng sau là Tiết Chân Dương, hắn nghe qua Vương Dĩnh đám người giảng giải kiến thức căn bản, tu vi càng thêm vững chắc, mặc dù trong chúng nhân chỉ xếp thứ ba, nhưng nước chảy thành sông, tu vi càng thêm vững chắc."),t("br"),t("br"),n._v("Ba người đều thuận lợi thành công, Long Thương Nguyệt lại không vận khí tốt như vậy, liên tiếp thất bại."),t("br"),t("br"),n._v("Liên tục đánh sâu vào bốn lần, cảm thấy thân khốn mệt mỏi, biết hôm nay lại trùng kích vào đi, làm không trả sẽ chỉ tẩu hỏa nhập ma, đành phải ngừng phía, một mặt mất tinh thần."),t("br"),t("br"),n._v("Tứ đại học hội hội trưởng, vốn là không có cái gì chênh lệch, mà bây giờ, cái khác ba cái đều thành công đột phá, liền hắn không cách nào thành công, trong lòng chênh lệch có thể tưởng tượng được."),t("br"),n._v("“Ba người bọn hắn, không phải Trương sư tọa hạ học đồ, chính là đồ tôn, đồ chắt trai, từng chiếm được chỉ điểm, mà ta... Bởi vì muốn quá nhiều, quá mức kiêu ngạo, đi cũng không gần...”"),t("br"),t("br"),n._v("Cười khổ lắc đầu."),t("br"),t("br"),n._v("Hồ Yêu Yêu đám người, hoặc nhiều hoặc ít đều cùng vị này Trương sư liên quan đến, đều chịu đến qua chỉ điểm của hắn hoặc là ân huệ, mà hắn... Bởi vì lo lắng quá nhiều, không cùng vị này từng có gặp gỡ quá nhiều, kết quả... Liền không cách nào đột phá ràng buộc!"),t("br"),t("br"),n._v("Sớm biết như thế, mặc kệ trở thành đồ tôn hay là đồ chắt trai, cũng tất nhiên lôi kéo quan hệ."),t("br"),t("br"),n._v("“Ngươi kém cũng không phải là thực lực, mà là lòng tin! Ngươi tính cách có chút âm nhu, không bằng Tiết Chân Dương cương mãnh, ưa thích lo trước lo sau, xung kích Bán Thánh ngược lại không bằng mấy người khác!”"),t("br"),t("br"),n._v("Dường như nhìn ra ý nghĩ của hắn, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Muốn nói hắn đối mấy người đột phá có ảnh hưởng hay không, quả thực có."),t("br"),t("br"),n._v("Hắn giảng giải công pháp, giảng giải vấn đề, nói trúng tim đen, nhắm thẳng vào đại đạo, dựa theo phương thức của hắn tu luyện, quả thực lại càng dễ đột phá."),t("br"),t("br"),n._v("Nhưng, xung kích Bán Thánh, không riêng gì tu vi vấn đề, càng quan trọng hơn còn có tinh thần, tâm tính."),t("br"),t("br"),n._v("Hồ Yêu Yêu làm bất cứ chuyện gì quả quyết có quyết đoán, Đổng Hân đã trải qua trưởng lão viện sự tình, cũng trở nên cứng cáp hơn, còn Tiết Chân Dương, từ trước tới nay dũng mãnh, ngược lại là vị này Long Thương Nguyệt, không quả quyết, làm việc ưa thích lo trước lo sau, muốn quá nhiều, ngược lại dẫn đến khó mà thành công."),t("br"),t("br"),n._v("“Trương sư dạy phải!”"),t("br"),t("br"),n._v("Hai mắt tỏa sáng, Long Thương Nguyệt tựa như thể hồ quán đỉnh, bừng tỉnh hiểu ra, lần nữa khoanh chân ngồi xuống, hai hàng lông mày vung lên."),t("br"),t("br"),n._v("Không thành công thì thành nhân!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Chân khí trong cơ thể bị điều động, cuồng bạo khí tức, bay thẳng thức hải."),t("br"),t("br"),n._v("Không biết qua bao lâu, đè ở phía trên ràng buộc, quả nhiên bắt đầu buông lỏng, cuối cùng ầm vang bên trong sập."),t("br"),t("br"),n._v("Bán Thánh, đến!"),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Tử Dương thú, Kim Nguyên đỉnh đứng tại một bên, nhìn thấy chủ nhân cho Long Thương Nguyệt chỉ điểm, tất cả đều âm thầm bội phục."),t("br"),t("br"),n._v("Bọn chúng vị chủ nhân này tuổi không lớn lắm, nhưng đối tu vi cùng nhân tính lý giải, đã đạt đến cao thâm cảnh giới khó lường, nói trúng tim đen, thẳng vào chỗ yếu hại."),t("br"),t("br"),n._v("Càng mấu chốt chính là... Cho người ta chỉ điểm, chưa từng giấu dốt, cho dù là đối thủ còn là địch nhân, đều vô cùng rộng lượng, phần này lòng dạ, đủ để cho vô số danh sư, vì đó tâm phục."),t("br"),t("br"),n._v("“Đa tạ Trương sư!”"),t("br"),t("br"),n._v("Đột phá Bán Thánh, Long Thương Nguyệt lại không còn trước kia kiêu ngạo, quỳ Trương Huyền trước mặt, chân tâm thật ý."),t("br"),t("br"),n._v("“Về đi!”"),t("br"),t("br"),n._v("Tới này mục đích đã hoàn thành, tất cả mọi người đột phá Bán Thánh, cũng không có tiếp tục tiếp tục chờ đợi cần thiết, mấy người lần nữa đạp vào Tử Dương thú sống lưng, thẳng tắp hướng Hồng Viễn thành bay đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lộc Thành, Ngô Hư hai người rời đi sơn cốc, liền ngừng lại."),t("br"),t("br"),n._v("“Lộc huynh, ngươi mới vừa rồi là không phải không thi triển toàn lực...”"),t("br"),t("br"),n._v("Trước đó thời điểm chiến đấu, Ngô Hư vẫn còn tương đối khiếp sợ, dọc đường cẩn thận phỏng đoán một cái, cảm thấy có chút không đúng."),t("br"),t("br"),n._v("Bản thân vị này Lộc huynh có loại thực lực nào, hắn biết đến rất rõ ràng, cực kỳ cường đại chiêu số cũng không thi triển, liền thua... Rất rõ ràng chiến đấu mới vừa rồi, nhường!"),t("br"),t("br"),n._v("“Không có thi triển toàn lực?”"),t("br"),t("br"),n._v("Lắc đầu, Lộc Thành cũng nhịn không được nữa, thân thể nhoáng một cái, một ngụm máu tươi cuồng bắn ra!"),t("br"),t("br"),n._v("“Lộc huynh...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn thế mà thương thế nặng như vậy, vội vàng vịn tìm cái địa phương ngồi xuống, lần nữa cho ăn một viên thuốc, lúc này mới thở phào nhẹ nhõm: “Lộc huynh, ngươi đây là...”"),t("br"),t("br"),n._v("“Ta rất nhiều tuyệt chiêu là không có thi triển đi ra, cũng không phải là không muốn thi triển, mà là... Không thi triển ra được!”"),t("br"),t("br"),n._v("Khôi phục một chút, Lộc huynh lắc đầu."),t("br"),t("br"),n._v("“Không thi triển ra được?” Ngô Hư không hiểu."),t("br"),t("br"),n._v("“Ừm, hắn dùng chính là một cái cỏ tranh, thoạt nhìn đối ta coi thường, để cho ta không tiện cùng hắn liều, mà trên thực tế, chiêu kiếm của hắn, mỗi một cái, đều chuẩn xác cực kỳ đánh vào ta sơ hở chỗ! Để ta chân khí trong cơ thể không cách nào quán thông... Cường đại tuyệt chiêu căn bản là không cách nào thi triển!”"),t("br"),t("br"),n._v("Lộc huynh cười khổ nói, mặc dù tỷ thí sớm đã kết thúc, vừa nghĩ tới đối phương nhãn lực cùng mạnh mẽ, liền không nhịn được con ngươi co rút lại."),t("br"),t("br"),n._v("Không phải hắn không muốn thi triển mạnh đại tuyệt chiêu, mà là... Chân khí trong cơ thể bị đối phương cắt đứt, tuyệt chiêu căn bản tiền để dành không ra."),t("br"),t("br"),n._v("Hình như dòng sông bị phân đoạn chặn đường, đều không thể trôi chảy chảy xuôi, lại làm sao có thể hình thành hồng tai?"),t("br"),t("br"),n._v("“Chân khí không cách nào quán thông?” Ngô Hư sửng sốt."),t("br"),t("br"),n._v("“Đúng vậy a, vị này Trương sư, cường đại không riêng gì vượt cấp khiêu chiến thực lực, càng đáng sợ chính là nhãn lực, hắn phảng phất đối chiêu số của ta, toàn bộ lý giải, toàn bộ rõ ràng, một cái liền có thể nhìn ra thiếu hụt ở nơi nào, sau đó tiến hành chặn đường... Kỳ thật, chiêu thứ nhất ta liền biết bản thân thua, còn lại hơn một trăm chiêu, cũng chỉ là đối phương cố ý nhường cho thôi!”"),t("br"),t("br"),n._v("Lộc huynh lắc đầu."),t("br"),t("br"),n._v("“Cố ý nhường cho? Làm sao có thể?”"),t("br"),t("br"),n._v("Ngô Hư không thể tin được."),t("br"),t("br"),n._v("Hắn người bạn thân này thực lực, biết đến rất rõ ràng, một chiêu liền thua... Bản thân làm sao không có cảm giác được, đối phương tại nhường?"),t("br"),t("br"),n._v("“Ngươi không tin? Vậy ta sẽ nói cho ngươi biết đi! Ta trước đó bị Tử Dương thú cùng cái kia lô đỉnh đánh thành trọng thương, mặc dù đi qua một canh giờ khôi phục, nhưng cũng không hoàn hảo... Điểm ấy, ngươi có lẽ rõ ràng đi!”"),t("br"),t("br"),n._v("Lộc huynh nói."),t("br"),t("br"),n._v("“Ừm!” Ngô Hư gật đầu."),t("br"),t("br"),n._v("Mới vừa rồi bị đánh thời điểm, hắn liền ở bên cạnh, làm sao không biết."),t("br"),t("br"),n._v("“Ta vận dụng bí pháp, đem thương thế trấn áp, chúng ta đồng môn đồng nguyên, cái bí pháp này, chắc hẳn ngươi cũng biết!”"),t("br"),t("br"),n._v("Lộc huynh nói tiếp."),t("br"),t("br"),n._v("“Vâng... Hồ Ba tiền bối lưu lại, Hồ Ba chữa thương pháp!” Ngô Hư lên tiếng."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-879"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-881"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);