(window.webpackJsonp=window.webpackJsonp||[]).push([[2167],{2522:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-873-truong-huyen-đot-cay-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-873-truong-huyen-đot-cay-thuong"}},[n._v("#")]),n._v(" Chương 873: Trương Huyền đốt cây (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 873: Trương Huyền đốt cây (thượng) "),t("br"),t("br"),n._v("“Triệu lão, vị này Trương sư, là ta chuyên môn mời tới hỗ trợ điều trị Bồ Đề thụ, đối dưỡng dược chức nghiệp, cũng có rất sâu trình độ!”"),t("br"),t("br"),n._v("Thấy lão giả ngăn cản, Ngọc Thần Thanh vội vàng giới thiệu."),t("br"),t("br"),n._v("Đây đều là chuyên môn mời tới chuyên nghiệp dưỡng dược người, nếu không phải bọn họ, cái này gốc Thánh vực Bồ Đề thụ khẳng định đã sớm chết."),t("br"),t("br"),n._v("“Điều trị Bồ Đề thụ? Liền hắn? Một cái lông còn chưa mọc đủ đứa bé? Sẽ không lại là cái gì lừa đảo, hại cho chúng ta đánh bạc mạng già đi!”"),t("br"),t("br"),n._v("Liếc qua, Triệu lão trên mặt không vui."),t("br"),t("br"),n._v("“Triệu lão chuyện này, Trương sư là có bản lĩnh lớn người, cùng trước kia những cái kia không giống...” Ngọc Thần Thanh mặt mũi xấu hổ."),t("br"),t("br"),n._v("“Không giống? Hắc hắc, mỗi lần không đều nói như vậy? Kết quả đây?”"),t("br"),t("br"),n._v("Triệu lão hất lên ống tay áo: “Nếu như ngươi không tin tưởng chúng ta lão ca mấy cái, chúng ta đi chính là, không cần thiết xin tới cái này đến cái khác, hại cho chúng ta cố gắng trước đó uổng phí!”"),t("br"),t("br"),n._v("“Không sai, bệ hạ không tin tưởng chúng ta, rời đi là được!”"),t("br"),t("br"),n._v("Cái khác mấy cái lão giả cũng tới đến trước mặt, cả đám đều không quá cao hứng."),t("br"),t("br"),n._v("“Chuyện này, nếu như không tin, ta làm sao có thể đem Ngọc gia trọng yếu nhất Bồ Đề thụ giao cho các ngươi điều trị...” Ngọc Thần Thanh vội vàng giải thích."),t("br"),t("br"),n._v("“Tin tưởng cũng không cần lại mời những này không đứng đắn, lung ta lung tung người, chúng ta sẽ dùng hết toàn lực, còn có thể hay không cứu sống, chỉ có thể dựa vào số trời, ta còn thực sự không muốn, một lần nữa cứu giúp!” Triệu lão khẽ nói."),t("br"),t("br"),n._v("Những năm này Ngọc Thần Thanh mời không biết bao nhiêu dưỡng dược người, trong đó không thiếu mua danh cầu lợi, không những không chữa khỏi, còn làm hại Bồ Đề thụ bất cứ lúc nào cũng sẽ tử vong, là bọn họ những người này đánh bạc tính mạng cứu chữa, mới lấy bảo tồn."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, mỗi lần thấy dẫn người tới, bọn họ đều tràn đầy nhức đầu."),t("br"),t("br"),n._v("Nhất là nhìn thấy vị này, chẳng qua hai mươi tuổi, liền tự xưng dưỡng dược người, còn có rất đào tạo sâu nghệ... Có thể tín nhiệm mới là lạ."),t("br"),t("br"),n._v("Dưỡng dược, mặc dù không có xác lập là đặc thù chức nghiệp, nhưng lại có đặc biệt truyền thừa, hơn nữa cùng y đạo đồng dạng, không riêng gì học được chút lý luận liền có thể làm tốt, càng nhiều hơn chính là thực tiễn."),t("br"),t("br"),n._v("Hai mươi tuổi, coi như vừa ra đời bắt đầu học, lại có thể học tập bao nhiêu?"),t("br"),t("br"),n._v("“Cái này...” Ngọc Thần Thanh mặt mũi xoắn xuýt."),t("br"),t("br"),n._v("Muốn nói Trương sư luyện đan, luyện khí bên trên năng lực, hắn khẳng định không nghi ngờ, nhưng dưỡng dược... Còn thật không dám xác định."),t("br"),t("br"),n._v("Đang không biết trả lời như thế nào, chỉ thấy bên người thanh niên, không để ý tí nào mọi người, thẳng tắp hướng Bồ Đề thụ đi tới, trước cây mặt bố trí trận pháp, một loại trò đùa, căn bản ngăn cản không biết, thời gian nháy mắt liền đến đến trước mặt, đưa tay tại thô to trên cành cây sờ lên."),t("br"),t("br"),n._v("“Ngươi làm gì?”"),t("br"),t("br"),n._v("Không nghĩ tới nói nhiều như vậy, tiểu tử này thế mà còn dám gần phía trước, hơn nữa đi sờ cây, Triệu lão đám người nhất thời nổ tung, nguyên một đám tóc dựng lên, cảm giác đến quyền uy của mình bị khiêu chiến."),t("br"),t("br"),n._v("“Không làm cái gì...”"),t("br"),t("br"),n._v("Sờ qua đại thụ Trương Huyền lắc đầu, nhìn về phía một bên Ngọc Thần Thanh: “Nếu như đám người kia muốn đi, đuổi đi chính là, dù sao cũng không có tác dụng gì...”"),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("“Tiểu tử, ngươi từ nơi nào tới? Hồng Viễn đế quốc, dưỡng dược thế gia cứ như vậy nhiều, ngươi là ai hậu nhân?”"),t("br"),t("br"),n._v("“Tuổi không lớn lắm, giọng điệu không nhỏ...”"),t("br"),t("br"),n._v("Rất nhiều lão giả đồng thời nổ tung."),t("br"),t("br"),n._v("Trước đó Ngọc Thần Thanh mời tới không ít dưỡng dược người, mặc dù cũng rất nhiều quây có danh tiếng, nhưng đối bọn hắn vẫn là rất tôn trọng."),t("br"),t("br"),n._v("Dù sao, Hồng Viễn đế quốc, mấy người bọn hắn xem như đỉnh phong nhất tồn tại... Cái tên này, gặp tiền bối, không vấn an ngược lại cũng thôi, lại còn nói để nhóm người mình rời đi!"),t("br"),t("br"),n._v("Còn nói không có tác dụng gì..."),t("br"),t("br"),n._v("Thật to gan!"),t("br"),t("br"),n._v("“Ta nói chính là sự thật, một cái đơn giản như vậy chứng bệnh, cũng nhìn không ra, còn có thể hay không cứu sống bằng số trời... Các ngươi là tới chữa bệnh, vẫn là muốn cái này gốc Bồ Đề thụ chết sớm một chút?”"),t("br"),t("br"),n._v("Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("Đi tới về sau, hắn dùng Minh Lý Chi Nhãn, liền nhìn ra một vài vấn đề, chẳng qua không dám xác định, lúc này mới tiến lên dùng thư viện kiểm tra."),t("br"),t("br"),n._v("Cùng nhìn thấy không sai biệt lắm."),t("br"),t("br"),n._v("Chứng bệnh không phức tạp, chỉ bằng vào bề ngoài chẩn bệnh, lại là rất khó."),t("br"),t("br"),n._v("“Ngươi...” Triệu lão lông mày giương lên, lạnh hừ một tiếng: “Đơn giản? Ngươi biết cái này gốc Bồ Đề thụ mắc bệnh gì chứng?”"),t("br"),t("br"),n._v("“Coi như ta nói, các ngươi có thể nghe hiểu ư?” Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Đám người kia, ở đây không biết bao lâu, ngay cả cái gì chứng bệnh cũng không biết, tra đều không có điều tra ra, giải thích khẳng định cũng không rõ ràng, cùng hắn lãng phí miệng lưỡi, còn không bằng không đi nhiều lời."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Tức giận suýt nổ, Triệu lão đám người không ngừng run rẩy."),t("br"),t("br"),n._v("Hồng Viễn đế quốc dưỡng dược thế gia, mấy người bọn họ tuyệt đối là đỉnh phong nhất, cường đại nhất, từ trước tới nay đều là xem thường người khác, còn lần thứ nhất bị người như vậy xem thường."),t("br"),t("br"),n._v("Nói có thể nghe hiểu à..."),t("br"),t("br"),n._v("Tiểu tử, ngươi từ nơi nào xuất hiện? Như thế cuồng, như vậy chảnh, nhà người biết ư?"),t("br"),t("br"),n._v("“Tốt, bệ hạ, đừng chậm trễ, nếu không cái này gốc Bồ Đề thụ, sớm muộn gì đều sẽ bị bọn họ giết chết...”"),t("br"),t("br"),n._v("Dọc theo sân nhỏ dạo qua một vòng, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Bị chúng ta giết chết? Tốt, ngươi thử nói xem, làm sao sẽ bị chúng ta giết chết...”"),t("br"),t("br"),n._v("Thấy tiểu tử này, nói chuyện càng ngày càng quá mức, Triệu lão rốt cuộc kìm nén không được, mấy bước đi tới trước mặt."),t("br"),t("br"),n._v("Rất có đối phương nói không đúng, ngay tại trận đem hắn đánh chết khí phách."),t("br"),t("br"),n._v("Cũng khó trách, cường giả Thánh vực, lại thêm dưỡng dược thế gia đỉnh phong nhân vật, sớm đã bị đã quen người khác tôn trọng, coi như Ngọc Thần Thanh bệ hạ, đều lễ ngộ rất nhiều, không dám ác ngôn đối mặt, cái tên này ngược lại tốt, tuổi không lớn lắm, cứ như vậy không kiêng nể gì cả..."),t("br"),t("br"),n._v("Một chút cũng không có đem hắn để vào mắt!"),t("br"),t("br"),n._v("“Các ngươi ở chung quanh bày lên Cửu Cửu Hợp Linh trận pháp, nếu như không nhìn lầm, là cưỡng ép giúp hắn truyền vào linh khí, kích hoạt linh tính a?” Hai tay chắp sau lưng, Trương Huyền thản nhiên nhìn tới."),t("br"),t("br"),n._v("“Ngươi biết Cửu Cửu Hợp Linh trận pháp?”"),t("br"),n._v("Triệu lão sững sờ."),t("br"),t("br"),n._v("Vừa rồi thấy cái tên này nói chuyện khó nghe, còn tưởng rằng giống như trước đây, là cái mua danh cầu lợi chi đồ, không nghĩ tới, liếc mắt liền nhìn ra mục đích của bọn hắn."),t("br"),t("br"),n._v("Cửu Cửu Hợp Linh trận pháp, là dưỡng dược thế gia bất truyền bí pháp một trong, có thể giúp linh tính đánh mất linh dược thậm chí Thánh dược một lần nữa kích hoạt linh tính, để hắn lần nữa toả sáng thanh xuân."),t("br"),t("br"),n._v("Rất nhiều dưỡng dược người cũng không biết."),t("br"),t("br"),n._v("Trước mắt cái này gốc Thánh vực Bồ Đề thụ, đã khô cạn muốn chết, đi qua bọn họ nhiều phương diện dò xét, hẳn là linh tính tiêu tán, lúc này mới tốn hao to lớn đánh đổi, bố trí xuất trận pháp, muốn kích hoạt."),t("br"),t("br"),n._v("Chẳng lẽ, cái này có vấn đề?"),t("br"),t("br"),n._v("“Không chỉ có biết, nếu như ta không nhìn lầm, các ngươi còn có lẽ, tập hợp mười tám loại Thánh dược linh tính, muốn muốn mạnh mẽ truyền vào đi vào, kết quả... Thất bại đi!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Triệu lão cùng mấy vị khác dưỡng dược người liếc mắt nhìn nhau, từng người câm ở."),t("br"),t("br"),n._v("Đối phương nói không sai, bọn họ đích xác tập hợp mười tám loại Thánh dược linh tính, mưu toan cho cái này gốc Bồ Đề thụ bổ sung, kết quả... Thất bại."),t("br"),t("br"),n._v("Chuyện này, chỉ có mấy người bọn họ biết, Hoàng đế bệ hạ đều chưa nói qua, cái tên này làm sao mà biết được?"),t("br"),t("br"),n._v("Chẳng lẽ liền nhìn thoáng qua?"),t("br"),t("br"),n._v("Điều đó không có khả năng ah!"),t("br"),t("br"),n._v("Truyền vào linh tính, mười phần phiền phức, một khi thất bại, không có dấu vết mà tìm kiếm, liền xem như bọn họ, cũng nhìn không ra, kẻ trước mắt này, làm sao có thể nhìn ra?"),t("br"),t("br"),n._v("“Cái này gốc Bồ Đề thụ, bản thân còn sống, linh tính vẫn còn, các ngươi nhưng cưỡng ép truyền vào một cái khác linh tính đi vào, mưu toan đoạt xá... Nó còn có thể khoẻ mạnh, không có bị các ngươi đùa chơi chết, thành tâm lệnh rất lớn. Làm ra loại sự tình này, không biết mất mặt, thế mà còn ở nơi này diễu võ giương oai, cảm thấy có thể cứu sống... Người nào tự tin của các ngươi cùng lá gan?”"),t("br"),t("br"),n._v("Lông mày vung lên, Trương Huyền hơi vung tay cánh tay."),t("br"),t("br"),n._v("“Ngươi... Nói bậy! Chúng ta thông qua đủ loại phương pháp đặc thù đo lường, Bồ Đề thụ linh tính đã biến mất, làm sao có thể còn sống?”"),t("br"),t("br"),n._v("Triệu lão vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Bọn họ mấy vị lão gia hỏa, nhưng mà đo lường nhiều lần, lặp đi lặp lại xác nhận rất nhiều lần, nếu như linh tính còn sống, làm sao có thể không biết?"),t("br"),t("br"),n._v("“Không sai, ta thông qua kim châm trắc linh pháp, thí nghiệm ba lần, đều không có linh tính! Lại nói, nếu như còn có linh tính, cái này gốc Bồ Đề thụ, như thế nào sẽ làm khô, lập tức sẽ tử vong?”"),t("br"),t("br"),n._v("Một cái lão giả đi lên phía trước."),t("br"),t("br"),n._v("“Ta cũng dùng độc môn thanh yên trắc linh pháp khảo nghiệm, không có linh tính, điểm ấy chắc chắn 100%!”"),t("br"),t("br"),n._v("Lại một cái lão giả gật đầu."),t("br"),t("br"),n._v("Thánh dược có linh, đây là người người đều biết, một khi không xác thực nhận linh tính tử vong hoặc là biến mất, liền truyền vào linh tính đi vào, quả thực cùng hắn nói đồng dạng, tương đương với cưỡng ép đoạt xá, đây không phải thuốc chữa, mà là giết thuốc!"),t("br"),t("br"),n._v("Tất cả mọi người là trứ danh dưỡng dược chuyên gia, một khi thuốc chữa không thành phản biến thành giết thuốc, về sau còn thế nào có mặt sống sót?"),t("br"),t("br"),n._v("Mất mặt đều đem mất chết!"),t("br"),t("br"),n._v("“Chúng ta truyền vào linh tính mặc dù thất bại, nhưng lại để nguyên bản khô quắt cây cối một lần nữa đâm chồi, lá cây biến vàng cũng đã nhận được làm dịu, rõ ràng Bồ Đề thụ, đã hướng thật phương hướng đi, chỉ phải kiên trì, tất nhiên có thể cứu sống, không cần thiết nghe một cái tiểu gia hỏa, ở đây ăn nói bừa bãi!”"),t("br"),t("br"),n._v("Lại một cái lão giả khoát tay áo."),t("br"),t("br"),n._v("Nghe được hắn, mọi người gật đầu."),t("br"),t("br"),n._v("Truyền vào linh tính mặc dù thất bại, nhưng lại để trước mắt cái này gốc Bồ Đề thụ lần nữa trôi nổi lục, dường như lần nữa toả sáng lần thứ hai thanh xuân."),t("br"),t("br"),n._v("Đã như vậy, phải nói rõ phương hướng không sai."),t("br"),t("br"),n._v("“Không sai? Lá cây là đổi xanh, rễ cây các ngươi đã kiểm tra không? Nhưng có khôi phục dấu hiệu? Cây XXX các ngươi đã kiểm tra không? Nhưng mà trở nên thoải mái vẫn là càng thêm khô quắt?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người chần chờ."),t("br"),t("br"),n._v("Truyền vào linh tính về sau, rễ cây kiểm tra, đúng là không tốt lắm, thân cây cũng biến thành càng thêm khô quắt, nhưng... Dung hợp mới linh tính, cũng sẽ có loại này thích ứng quá trình, chẳng lẽ không đúng?"),t("br"),t("br"),n._v("“Kim châm trắc linh pháp, có thể đo lường dược vật linh tính, chẳng qua kim châm, tổng cộng bao dài, nhưng đâm vào thân cây nội bộ? Nếu như linh tính giấu ở trong đó, khả năng đo lường đến?”"),t("br"),t("br"),n._v("Không để ý tới mọi người chần chờ, Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lời mới vừa nói vị lão giả kia yên lặng."),t("br"),t("br"),n._v("Kim châm trắc linh pháp, là thông qua kim châm đâm đâm, để linh tính làm ra phản ứng, tới khảo thí hắn tồn tại hay không."),t("br"),t("br"),n._v("Nếu như là một gốc dược liệu, cũng chẳng có gì, như thế lớn cây, quả thực không cách nào đâm thủng."),t("br"),t("br"),n._v("“Thanh yên trắc linh pháp tương đồng đạo lý, có thể khảo thí tỉnh táo linh tính, mà nếu như cái này gốc Bồ Đề thụ linh tính ngủ say đâu? Dùng phương pháp này trắc, có phải hay không cùng tiêu vong đồng dạng?”"),t("br"),t("br"),n._v("Trương Huyền nhìn về phía một vị khác."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Lão giả này cũng nói không ra lời."),t("br"),t("br"),n._v("Linh tính ngủ say, tại thanh yên trắc linh pháp bên trong, quả thực cùng chết không có khác nhau chút nào."),t("br"),t("br"),n._v("“Nói nhiều như vậy vô dụng, vậy ngươi nói một chút, có biện pháp nào có thể cứu sống cái này gốc Bồ Đề thụ! Không nói ra được, tán dóc nhiều hơn nữa đều là uổng công!” Một vị lão giả cắt đứt Trương Huyền lời nói, nói."),t("br"),t("br"),n._v("Những người khác đồng thời gật đầu."),t("br"),t("br"),n._v("Có thể cứu sống mới là trọng yếu nhất, nếu không, nói nhiều hơn nữa đều là giả kỹ năng."),t("br"),t("br"),n._v("“Phương pháp?” Trương Huyền nhẹ nhàng cười một tiếng, xoay đầu lại: “Bệ hạ, làm phiền ngươi phái người tìm mấy thùng dầu cùng hộp quẹt tới...”"),t("br"),t("br"),n._v("“Ta muốn đem gốc cây này cây đốt!”"),t("br"),t("br"),n._v("(Hôm nay vẫn như cũ ba canh! Canh thứ nhất đến! Các vị đại gia thưởng điểm mặt, cho điểm nguyệt phiếu đi, ô ô, nhỏ bé đã quỳ xuống..)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-881"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-883"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);