(window.webpackJsonp=window.webpackJsonp||[]).push([[2168],{2523:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-874-truong-huyen-đot-cay-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-874-truong-huyen-đot-cay-ha"}},[n._v("#")]),n._v(" Chương 874: Trương Huyền đốt cây (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 874: Trương Huyền đốt cây (hạ) (phần 2)"),t("br"),t("br"),n._v("“Đốt?”"),t("br"),t("br"),n._v("“Là để ngươi cứu cây, cũng không phải giết cây!”"),t("br"),t("br"),n._v("“Vớ vẩn, quả thực sai lầm nghiêm trọng! Đem cây giết chết, ai không biết? Còn cần ngươi tới dạy?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được hắn, tất cả mọi người đầu tiên là ngẩn ngơ, lập tức tất cả đều gầm thét."),t("br"),t("br"),n._v("Cái này nha có bị bệnh không!"),t("br"),t("br"),n._v("Đem mọi người nói không đáng một đồng, để hắn cứu chữa, lại muốn đem cây đốt..."),t("br"),t("br"),n._v("Viên này Bồ Đề thụ là hoàng thất Ngọc gia của quý, mấy người liều mạng giữ lại, đều sợ đắc tội bệ hạ, bị chặt đầu, cái tên này thổi đến ngưu khí hống hống, vốn cho rằng có cái gì diệu chiêu, thế mà mở miệng muốn đốt... Đúng là điên!"),t("br"),t("br"),n._v("Ngọc Thần Thanh cũng là nhoáng một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Vốn là Trương sư lời thề son sắt, nói rất nhiều lão giả ngậm miệng không nói gì, hắn cảm thấy khả năng thật là có bản lĩnh, nghe nói như thế mới hiểu được... Ngươi đùa giỡn đâu?"),t("br"),t("br"),n._v("Bồ Đề thụ vốn là sắp phải chết, lại đốt, còn thế nào sống?"),t("br"),t("br"),n._v("“Làm sao vậy? Muốn cứu Bồ Đề thụ liền động tác nhanh lên!” Thấy Ngọc Thần Thanh không nhúc nhích, Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("“Cứu?” Ngọc Thần Thanh khóe miệng giật một cái."),t("br"),t("br"),n._v("Ngươi xác định đây là cứu? Không phải nhìn nó còn có chút cành mầm, muốn hoàn toàn giết chết?"),t("br"),t("br"),n._v("“Không sai!” Gật gật đầu, Trương Huyền hai tay chắp sau lưng, cũng không giải thích."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy không giống nói đùa, Ngọc Thần Thanh chần chờ một chút, xoay người phân phó vị kia gọi La Phú lão thái giám: “Theo Trương sư nói làm!”"),t("br"),t("br"),n._v("Mặc dù cảm giác đối phương đốt cây cử động rất vớ vẩn, nhưng thân là danh sư học viện viện trưởng, lại thêm rất nhiều truyền kỳ sự kiện, để hắn lo nghĩ, vẫn cảm thấy có cần phải tin tưởng."),t("br"),t("br"),n._v("Mấu chốt nhất là, mấy vị này lão giả đã ở đây tiếp cận hai năm, Bồ Đề thụ không có một tia chuyển biến tốt đẹp, có thể nói, tử vong đã đếm ngược, đã Trương sư tự tin như vậy, thua kém hơn lấy ngựa chết làm ngựa sống, cho dù thật chết rồi, cũng có thể nói là mệnh số gây ra, không có biện pháp nào khác."),t("br"),t("br"),n._v("“Bệ hạ nghĩ lại!”"),t("br"),t("br"),n._v("“Đừng nghe cái tên này ăn nói bừa bãi, Bồ Đề thụ đã thoi thóp, lại chịu không được chơi đùa...”"),t("br"),t("br"),n._v("Thấy thật nghe đối phương, để cho người ta lấy dầu lấy lửa, Triệu lão đám người tất cả đều tràn đầy lo lắng, vội vàng tiến lên."),t("br"),t("br"),n._v("Bồ Đề thụ đã cùng bệnh nguy kịch lão nhân, bất cứ lúc nào cũng sẽ bỏ mình, còn muốn đi đốt, ngại chết không đủ nhanh ư?"),t("br"),t("br"),n._v("“Chư vị đừng có gấp, Trương sư chính là danh sư học viện... Cao minh danh sư, đã làm như vậy, tất nhiên có đạo lý của hắn...”"),t("br"),t("br"),n._v("Suýt chút nữa đem thân phận của Trương Huyền nói ra, vừa nghĩ tới tạm thời giữ bí mật, Ngọc Thần Thanh lúc này đổi giọng."),t("br"),t("br"),n._v("“Danh sư kiến thức rộng rãi, tầm mắt rộng lớn không giả, nhưng... Dưỡng dược bên trên, chúng ta mấy người tuyệt đối là Hồng Viễn đế quốc đứng đầu nhất, còn chưa từng nghe nói qua cái gì đốt cây, có thể thuốc chữa!”"),t("br"),t("br"),n._v("Thấy bệ hạ bị người trước mắt này mê hoặc, Triệu lão cũng nhịn không được nữa, trắng như tuyết sợi râu lay động, giận không chỗ phát tiết."),t("br"),t("br"),n._v("Bọn họ là Hồng Viễn đế quốc dưỡng dược thế gia đỉnh phong nhất tồn tại, mỗi cái đều liên quan một cái truyền thừa, qua nhiều năm như vậy, gặp qua chẩn bệnh dược vật rất nhiều ca bệnh, còn chưa từng nghe nói qua, đốt cây có thể thuốc chữa."),t("br"),t("br"),n._v("Cùng uống rượu độc giải khát đồng dạng, cùng dược lý hoàn toàn trái ngược, tuyệt đối là không thể nào thực hiện."),t("br"),t("br"),n._v("“Đúng vậy a, thuật nghiệp hữu chuyên công, liền xem như danh sư, cũng không có khả năng tất cả chức nghiệp đều biết, dù sao, không có người có thể có Khổng sư loại này bản lĩnh!” Lại một cái lão giả cũng nói."),t("br"),t("br"),n._v("Truyền thuyết, Khổng sư không riêng gì danh sư thuỷ tổ, càng là tinh thông rất nhiều chức nghiệp, thậm chí, hiện tại lưu truyền chức nghiệp, có rất nhiều đều là hắn biên soạn, sửa sang lại."),t("br"),t("br"),n._v("Trước mắt vị này, liền xem như rất lợi hại danh sư, nhưng... Danh sư cũng không phải cái gì cũng biết, cũng có khi thất thủ!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được mấy vị lão giả lời nói, Ngọc Thần Thanh trong lòng cũng tràn đầy dao động, có điều, nghĩ tới thân phận của đối phương, vẫn là cắn răng: “Chư vị không cần nói nhiều, ý ta đã quyết, nghe theo Trương sư an bài!”"),t("br"),t("br"),n._v("Đang khi nói chuyện, lão thái giám đã dẫn người đem dầu cùng hộp quẹt mang theo tới."),t("br"),t("br"),n._v("Thấy vị này bệ hạ, thời khắc mấu chốt còn có thể kiên định tín nhiệm bản thân, Trương Huyền thoả mãn nhẹ gật đầu, sắp xếp người đem dầu giội khắp cây đều là, hết thảy chuẩn bị ổn thoả, lúc này mới đem hộp quẹt nhen lửa."),t("br"),t("br"),n._v("“Thiên hạ dưỡng dược gia tộc, không có một vạn chí ít cũng có tám ngàn, cho dược vật chữa bệnh thủ đoạn càng là tầng tầng lớp lớp, số chi không rõ, mấy người các ngươi, tại Hồng Viễn đế quốc được cho không yếu, chẳng lẽ liền đại biểu, đã biết được hết thảy cho dược vật chữa bệnh thủ đoạn?”"),t("br"),t("br"),n._v("Không vội vã chút dầu, Trương Huyền quay đầu nhìn về phía mọi người."),t("br"),t("br"),n._v("Triệu lão đám người yên lặng."),t("br"),t("br"),n._v("Thiên hạ tri thức, đủ loại truyền thừa đâu chỉ mấy vạn, ai dám nói đều gặp?"),t("br"),t("br"),n._v("Coi như năm đó Khổng sư, cũng không dám nói như vậy!"),t("br"),t("br"),n._v("Nếu không, cũng không có khả năng đi ngang qua một cái vắng vẻ quốc gia, bị hai cái tiểu nhi, nói á khẩu không trả lời được."),t("br"),t("br"),n._v("“Bồ Đề thụ linh tính cũng không phải là tử vong, mà là ngủ say, các ngươi không có biết rõ ràng liền ngông cuồng hạ dược, suýt chút nữa đem hắn hại chết không nói, còn làm cả cây cây đều có chút dở dở ương ương...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền không nói thêm lời, ngón tay nhẹ nhàng bắn ra."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Hộp quẹt rơi vào dầu bên trên, lửa lớn rừng rực, lập tức đốt lên."),t("br"),t("br"),n._v("“Thật đốt...”"),t("br"),t("br"),n._v("Triệu lão đám người, nguyên một đám khóe miệng cuồng quất."),t("br"),t("br"),n._v("Cùng cái này gốc Bồ Đề thụ tiếp xúc thời gian dài, đã có tình cảm, nhìn thấy bị đốt, trong lòng vẫn là khó mà dứt bỏ."),t("br"),t("br"),n._v("Ngọc Thần Thanh càng là lông mày nhảy loạn."),t("br"),t("br"),n._v("Hỏa diễm cùng một chỗ, lại không cách nào quay đầu lại, cũng không biết tin tưởng trước mắt vị này, đến cùng nên hay không nên."),t("br"),n._v(" Không để ý tới vẻ mặt của mọi người, nhìn trước mắt hỏa diễm, Trương Huyền thần sắc thản nhiên, thiêu đốt một lát, lông mày giương lên: “Dập lửa!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mấy tên hộ vệ vội vàng tiến lên, từng người chân khí phun ra, đem Bồ Đề thụ bao phủ."),t("br"),t("br"),n._v("Những người này thấp nhất đều nắm giữ Đạp Hư cảnh thực lực, chân khí liên hợp lại cùng nhau, nhẹ nhõm liền đem hỏa diễm dập tắt."),t("br"),t("br"),n._v("Không còn hỏa diễm, mọi người cái này mới nhìn rõ, trước mắt Bồ Đề thụ, đã ngăm đen, tại không có nửa điểm sinh mệnh khí tức."),t("br"),t("br"),n._v("Cũng không giải thích, Trương Huyền hai bước đi tới trước mặt, đối với thân cây liền đánh ra."),t("br"),t("br"),n._v("Liên tục đập ba mươi sáu chưởng, dọc theo thô to thân cây dạo qua một vòng, cái này mới ngừng lại."),t("br"),t("br"),n._v("Lui về phía sau mấy bước đi tới Ngọc Thần Thanh đám người trước mặt, bàn tay giơ lên, nhẹ nhàng bóp một cái."),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Trước đó ngăm đen có chút thành than vỏ cây, phát ra “Kẹt kẹt!” Thanh âm, bỗng nhiên nổ tung."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không biết cái tên này đến cùng trong hồ lô đến cùng bán thuốc gì, Triệu lão đám người đang muốn hỏi thăm, đột nhiên tất cả đều sửng sốt, nguyên một đám đứng tại chỗ, toàn thân cứng ngắc."),t("br"),t("br"),n._v("“Cái này... Làm sao có thể?”"),t("br"),t("br"),n._v("Ngọc Thần Thanh cũng vội vã nhìn sang, lúc này mới phát hiện, nổ tung vỏ cây bên trong, một cái chỉ có lớn bằng cánh tay thân cây, xanh ngắt ướt át, tựa như cây khô mọc ra chồi non, nhưng mà bị bên ngoài mục nát bao vây, không cách nào phát ra tới thôi."),t("br"),t("br"),n._v("Rõ ràng phía ngoài thân cây đã làm xẹp tử vong, làm sao lõi cây bên trong, như thế xanh nhạt?"),t("br"),t("br"),n._v("Tất cả mọi người ngây người, giống như là gặp quỷ, không rõ ràng cho lắm."),t("br"),t("br"),n._v("Gặp qua không ít dược vật chứng bệnh, nhưng loại này kỳ quái sự tình, nhưng chưa từng thấy qua."),t("br"),t("br"),n._v("Thậm chí chưa từng nghe thấy."),t("br"),t("br"),n._v("“Cái này gốc Bồ Đề thụ, cũng không phải là tử vong mà là lâm vào ngủ say, linh tính vì tự vệ, giấu ở lõi cây, để bảo vệ cơ sở... Bên ngoài không còn linh tính chống đỡ, tự nhiên bắt đầu khô héo, cùng tử vong tương tự!”"),t("br"),t("br"),n._v("Thấy mọi người không hiểu, Trương Huyền nhàn nhạt giải thích: “Các ngươi không có phát giác những này, phản lại cảm thấy nó đã chết, cưỡng ép truyền vào linh tính, suýt chút nữa liền đem cái này duy nhất căn cơ hủy đi! Thật muốn thành công, Bồ Đề thụ chính là thật chết rồi...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người chấn động toàn thân."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Linh tính không chết dưới tình huống, cưỡng ép truyền vào linh tính, rất dễ dàng gây nên bắn ngược, làm không cẩn thận chính là ngọc đá cùng vỡ, đồng quy vu tận cục diện."),t("br"),t("br"),n._v("Nói cách khác, may mắn bọn họ không thành công, một khi thành công... Bồ Đề thụ chẳng khác gì là bị bọn họ cứ thế mà trị chết!"),t("br"),t("br"),n._v("“Trương sư, Bồ Đề thụ... Một mực sống được thật tốt, tại sao lại ngủ say?”"),t("br"),t("br"),n._v("Ngọc Thần Thanh nhịn không được nhìn qua, những người khác cũng đầy là nghi ngờ."),t("br"),t("br"),n._v("“Ta đây cũng không biết... Dưới tình huống bình thường, hẳn là chịu đến tổn thương rất nặng, vì bản thân bảo hộ mới sẽ làm như vậy! Đã hai năm trước, xuất hiện loại tình huống này, có phải hay không từng vào lúc đó, phát sinh qua biến cố gì?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thư viện chỉ là giới thiệu thiếu hụt, cũng chưa giải thích xuất hiện loại tình huống này nguyên nhân, liền xem như hắn, cũng không rõ lắm."),t("br"),t("br"),n._v("“Cái này... Không có ah!”"),t("br"),t("br"),n._v("Ngọc Thần Thanh cau mày, lắc đầu: “Hai năm trước... Danh sư học viện lão viện trưởng mất tích, luyện dược học viện Vưu Hư phó viện trưởng từng tới một lần, lấy một cái ‘Cành mầm’ rời đi, nói là muốn bồi dưỡng thử xem, nhìn có thể hay không cảm ứng lão viện trưởng hồn phách... Lúc khác, lại không có những người khác tiếp xúc qua!”"),t("br"),t("br"),n._v("“Vưu Hư?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Cái tên này, lúc trước làm hại Ngụy Trường Phong chết đi, vì để cho Ngụy Như Yên báo thù, còn bị giam giữ tại phủ đệ của mình không có thả ra đây."),t("br"),t("br"),n._v("Tại sao lại cùng cái này gốc Bồ Đề thụ có quan hệ?"),t("br"),t("br"),n._v("“Vâng! Vưu Hư viện trưởng, cũng là dưỡng dược chuyên gia, ta đã từng mời hắn sang đây xem qua, không nhìn ra vấn đề...” Ngọc Thần Thanh nói tiếp."),t("br"),t("br"),n._v("Vưu Hư là học viện dưỡng dược tốt nhất danh sư, hắn cũng mời qua, nhưng cũng không nói xuất cụ thể bệnh gì chứng, tự nhiên là không có cứu chữa thành công."),t("br"),t("br"),n._v("“Cụ thể loại nguyên nhân nào, hiện tại truy cứu cũng mất ý nghĩa, vẫn là trước nghĩ biện pháp để hắn theo trong ngủ mê tỉnh táo rồi nói sau!”"),t("br"),t("br"),n._v("Biết hai năm trước sự tình, kéo tới nhiều hơn nữa, cũng tán dóc không rõ, lại nói cùng hắn quan hệ không lớn, Trương Huyền khoát tay áo, không trong vấn đề này tiếp tục dây dưa."),t("br"),t("br"),n._v("Cũng rõ ràng đạo lý này, Ngọc Thần Thanh không nói thêm lời."),t("br"),t("br"),n._v("Thân thể nhảy lên, lần nữa đi tới Bồ Đề thụ trước mặt, Trương Huyền bàn tay lần nữa ở phía trên sờ soạng một cái, lập tức cúi đầu trầm tư."),t("br"),t("br"),n._v("Một lát sau, cổ tay khẽ đảo, lấy ra một cái to lớn hồ lô rượu, mở ra nắp bình, lập tức một cỗ nồng đậm tới cực điểm linh khí thẳng tắp dâng lên, để cho người ta mê muội."),t("br"),t("br"),n._v("“Địa mạch linh dịch?”"),t("br"),t("br"),n._v("Mấy vị lão giả cùng Ngọc Thần Thanh tất cả đều ánh mắt sáng lên."),t("br"),t("br"),n._v("Bằng mượn nhãn lực của bọn hắn, tự nhiên có thể nhận ra, trong hồ lô là địa mạch linh dịch."),t("br"),t("br"),n._v("Loại này linh dịch ẩn chứa đại địa chi lực, đối dược vật có tác dụng cực lớn, là dưỡng dược hiếm có bảo bối."),t("br"),t("br"),n._v("Đem linh dịch tưới vào xanh nhạt thân cây, nguyên bản trụi lủi trên cây, lập tức mắt trần có thể thấy, mọc ra “Cành mầm”, từng mảnh từng mảnh xanh nhạt lá cây, tùy ý sinh trưởng."),t("br"),t("br"),n._v("“Cái này... Linh tính sống?”"),t("br"),t("br"),n._v("Thấy cảnh này, Ngọc Thần Thanh cùng rất nhiều lão giả tất cả đều kích động nắm đấm xiết chặt."),t("br"),t("br"),n._v("Một lần nữa mọc ra lá cây, hơn nữa tùy ý sinh trưởng, coi như ngu nữa, cũng biết, cái này gốc Bồ Đề thụ ngủ say linh tính, đã hoàn toàn thức tỉnh!"),t("br"),t("br"),n._v("Địa mạch linh dịch, nhiều nhất có thể để cho Thánh dược càng nhanh chóng hơn bổ sung linh khí, có thể nghĩ để linh tính thức tỉnh vẫn là làm không được."),t("br"),t("br"),n._v("Vị này Trương sư đến cùng dùng cái gì thần kỳ thủ đoạn?"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-882"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-884"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);