(window.webpackJsonp=window.webpackJsonp||[]).push([[2170],{2524:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-876-bon-ho-la-danh-su"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-876-bon-ho-la-danh-su"}},[n._v("#")]),n._v(" Chương 876: Bọn họ là danh sư!")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 876: Bọn họ là danh sư!"),i("br"),i("br"),n._v("“Thú đường?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("“Ừm, Thú đường lưu giữ rất nhiều Thánh thú huyết dịch, chỉ cần là cấp bậc đầy đủ thuần thú sư, nguyện ý đánh đổi khá nhiều, hoàn toàn có thể hối đoái!” Thiên Nghĩ Phong Mẫu nói."),i("br"),i("br"),n._v("“Tốt, ta hiện tại liền đi khảo hạch lục tinh thuần thú sư, thuận tiện giúp ngươi hối đoái một chút!”"),i("br"),i("br"),n._v("Nghe được Thánh vực Thiên Nghĩ Phong Mẫu huyết dịch có loại này công hiệu, Trương Huyền lại không chần chờ, rời đi tổ ong, lại liếc mắt nhìn Ngụy Như Yên, phát hiện nàng khôi phục hết sức nhanh chóng, Bồ Đề thụ cũng không bị đến độc hại, lúc này mới thở phào nhẹ nhõm."),i("br"),i("br"),n._v("Rời đi hoàng cung, hướng danh sư học viện đi đến."),i("br"),i("br"),n._v("Khảo hạch thuần thú sư, hối đoái huyết dịch, tự nhiên tìm Mi trưởng lão tương đối dễ dàng, còn Hồng Viễn đế quốc Thú đường, cũng không biết ở nơi nào, chớ nói chi là tìm được."),i("br"),i("br"),n._v("Đi tới thuần thú học viện, tìm tới Mi trưởng lão, chỉ thấy hắn đang mày nhíu lại chặt ngồi tại gian phòng, dường như có cái gì phiền lòng sự tình, cấp bách chờ xử lý."),i("br"),i("br"),n._v("“Viện trưởng!”"),i("br"),i("br"),n._v("Gặp hắn tới, Mi trưởng lão không dám làm lớn, gấp vội vàng nghênh đón."),i("br"),i("br"),n._v("“Mi trưởng lão không cần khách khí, lần này tới, là muốn thi cái lục tinh thuần thú sư...” Trương Huyền trực tiếp nói thẳng ý đồ đến."),i("br"),i("br"),n._v("“Lục tinh thuần thú sư?”"),i("br"),i("br"),n._v("Mi trưởng lão sửng sốt một chút, lập tức cười khổ lắc đầu: “Viện trưởng nói giỡn, ngươi ngay cả Tử Dương tiền bối đều có thể thuần phục, sớm đã đạt đến Thất Tinh tư cách, ta đã hướng tổng bộ xin, cũng đã nhận được trả lời chắc chắn, nhưng mà huy chương còn chưa tới thôi!”"),i("br"),i("br"),n._v("Tử Dương thú đã đi đến Thánh vực nhị trọng Thần Thức cảnh, chỉ có Thất Tinh thuần thú sư mới có thể thuần phục, Trương Huyền thành công, nói rõ đã có loại năng lực này, không cần thiết lại lãng phí thời gian khảo hạch."),i("br"),i("br"),n._v("“Thất Tinh thuần thú sư?” Trương Huyền ngẩn ngơ."),i("br"),i("br"),n._v("Còn muốn lấy tới khảo hạch cái lục tinh, không nghĩ tới, Mi trưởng lão đã hỗ trợ xin Thất Tinh."),i("br"),i("br"),n._v("Như vậy cũng tốt, miễn đi không ít phiền phức."),i("br"),i("br"),n._v("“Vậy xin đa tạ rồi, đã tổng bộ đã đáp ứng, vừa vặn có việc muốn nhờ... Ta muốn hối đoái một chút, Thánh vực Thiên Nghĩ Phong Mẫu huyết dịch!” Trương Huyền nói tiếp."),i("br"),i("br"),n._v("“Thánh vực Thiên Nghĩ Phong Mẫu huyết dịch?” Mi trưởng lão lông mày nhíu một cái."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("“Cái khác Thánh thú huyết dịch, rất tốt thương nghị, giá cả cũng không cao, nhưng... Thiên Nghĩ Phong Mẫu, chỉ sợ không dễ dàng như vậy hối đoái!” Dừng lại một chút, Mi trưởng lão nói."),i("br"),i("br"),n._v("“Không dễ dàng hối đoái?” Trương Huyền nghi ngờ."),i("br"),i("br"),n._v("Đồng dạng là Thánh thú, có lẽ đều không kém nhiều đi, vì cái gì Thiên Nghĩ Phong Mẫu độ khó đại?"),i("br"),i("br"),n._v("“Thiên Nghĩ Phong Mẫu vốn là thưa thớt, có thể đột phá đến Thánh vực tự nhiên càng ít!”"),i("br"),i("br"),n._v("Nhìn ra hắn nghi hoặc, Mi trưởng lão giải thích: “Mấu chốt nhất là, loại này ong mẫu, mang theo chế tạo không gian năng lực, quá mức trân quý... Coi như tổng bộ có, chỉ sợ cũng cần cái giá cực lớn!”"),i("br"),i("br"),n._v("“Cực lớn đánh đổi?”"),i("br"),i("br"),n._v("“Ừm, coi như Trương sư là Thất Tinh thuần thú sư, muốn mua lời nói, chỉ sợ đều phải tốn phí mấy ngàn viên linh thạch thượng phẩm, thậm chí nhiều hơn...” Lắc đầu, Mi trưởng lão nhịn không được cười khổ."),i("br"),i("br"),n._v("Mua sắm Thánh vực Thiên Nghĩ Phong Mẫu tinh huyết đánh đổi thực sự quá lớn, đã lớn đến, bọn họ những người này, căn bản không chịu nổi tình trạng."),i("br"),i("br"),n._v("“Mấy ngàn viên linh thạch thượng phẩm?”"),i("br"),i("br"),n._v("Trương Huyền bờ môi co lại."),i("br"),i("br"),n._v("Thật vất vả theo Ngô Hư đám người nơi đó nhận được mấy trăm linh thạch thượng phẩm, bản cảm thấy là một phương phú hào, nghe được thứ này mới biết được, vẫn như cũ là cái quỷ nghèo."),i("br"),i("br"),n._v("Đây là hắn là Thất Tinh thuần thú sư tình huống, nếu như ngoại nhân đến mua, giá cả ít nhất còn muốn tăng gấp đôi."),i("br"),i("br"),n._v("Thú đường loại hình đặc thù chức nghiệp công hội, đối hắn công hội bên trong thành viên, cấp bậc càng cao, mua sắm càng tiện nghi, đây là quy định."),i("br"),i("br"),n._v("“Kỳ thật... Tốn hao không nổi linh thạch cũng có biện pháp nhận được, nhưng mà... Càng khó!”"),i("br"),i("br"),n._v("Lời nói nói phân nửa, Mi trưởng lão ngừng lại."),i("br"),i("br"),n._v("“Ồ?” Thấy còn có biện pháp, Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Rất đơn giản, cái kia chính là là Thú đường làm ra đầy đủ cống hiến, chỉ cần cống hiến cũng đủ lớn, hoàn toàn có thể đem thứ này ban cho...” Mi trưởng lão nói."),i("br"),i("br"),n._v("“Cống hiến?” Trương Huyền cau mày."),i("br"),i("br"),n._v("“Không sai, cho Thú đường cung cấp hiếm có linh thú, Thánh thú huyết dịch, lại hoặc là hoàn thành Thú đường cấp cho nhiệm vụ... Cũng có thể coi là được cống hiến!” Mi trưởng lão nói tiếp."),i("br"),i("br"),n._v("“Cung cấp hiếm có linh thú huyết dịch?” Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Thú đường thành lập không biết bao nhiêu năm, đại lục ở bên trên có Thánh thú, linh thú, cơ bản đều bị sưu tập qua, có thể xưng là hiếm có, cho dù có, hắn cũng không có thời gian này đi tìm."),i("br"),i("br"),n._v("“Đầu thứ nhất quả thực rất khó, trừ phi vận khí cực giai. Đầu thứ hai, Thú đường rất ít ban bố nhiệm vụ, lại nói coi như ban bố, cũng cơ bản rơi không đến chúng ta nơi này...”"),i("br"),i("br"),n._v("Mi trưởng lão cũng đầy mặt cười khổ."),i("br"),i("br"),n._v("Mỗi một cái truyền thừa nghề nghiệp cống hiến, đều không phải là dễ dàng như vậy kiếm lấy, nếu không, khẳng định sớm bị người đoạt vỡ đầu."),i("br"),i("br"),n._v("“Mi viện trưởng, sắp không chịu đựng nổi nữa, nhanh nghĩ biện pháp đi...”"),i("br"),i("br"),n._v("Đang muốn nói tiếp, liền nghe phía ngoài truyền đến tiếng bước chân dồn dập, một cái lão giả vội vã đi đến."),i("br"),i("br"),n._v("Đi vào phòng, cái này mới nhìn đến Trương Huyền, vội vàng khom người: “Viện trưởng!”"),i("br"),i("br"),n._v("Chính là luyện khí sư học viên viện trưởng, Triệu Bính Tuất."),i("br"),i("br"),n._v("“Triệu trưởng lão, cái gì không kiên trì nổi?”"),i("br"),i("br"),n._v("Trương Huyền kỳ quái."),i("br"),i("br"),n._v("Mới vừa vào cửa chỉ thấy Mi trưởng lão mày nhăn lại, vốn cho rằng là bản thân việc vặt, nghe Triệu Bính Tuất lời nói, chỉ sợ không có đơn giản như vậy."),i("br"),i("br"),n._v("“A, không có việc gì, viện trưởng liền an tâm chờ lấy ngày mai kế vị đại điển, việc vặt chúng ta sẽ trước thời hạn xử lý...”"),i("br"),n._v("Triệu Bính Tuất cười khan một tiếng."),i("br"),i("br"),n._v("“Đúng vậy a, viện trưởng, chúng ta sẽ tự mình xử lý...” Mi trưởng lão cũng liền vội vàng gật đầu."),i("br"),i("br"),n._v("“Đến cùng chuyện gì?” Trương Huyền cau mày."),i("br"),i("br"),n._v("Hai người là một viện trưởng, càng là Thánh vực nhất trọng cường giả đỉnh phong, thực lực mạnh mẽ, nhân vật như vậy, đều mặt mũi ưu sầu, khẳng định chuyện phát sinh không nhỏ."),i("br"),i("br"),n._v("Mặc dù hắn đối rất nhiều chuyện không quan tâm, dù sao cũng là danh sư học viện xuất hiện Nhâm viện trưởng, trách nhiệm trong người, có một số việc vẫn là cần cảm kích."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Gặp hắn hỏi thăm, hai đại trưởng lão liếc mắt nhìn nhau, khắp khuôn mặt là chần chờ."),i("br"),i("br"),n._v("“Tốt a!” Một lát sau, Mi trưởng lão than thở một tiếng: “Là Vân Vụ sơn linh thú, phát sinh thú triều!”"),i("br"),i("br"),n._v("“Vân Vụ sơn? Thú triều?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("“Ừm, Vân Vụ sơn linh thú cùng Thánh thú, từ trước tới nay cùng chúng ta Hồng Viễn thành, không liên quan tới nhau, nước giếng không phạm nước sông, đoạn thời gian trước, không biết chuyện gì xảy ra, đột nhiên tựa như phát điên, khắp nơi vòng vây đi vào sơn mạch đi săn thợ săn cùng người tu luyện.”"),i("br"),i("br"),n._v("Mi trưởng lão nói: “Danh sư học viện nhận được thỉnh cầu, liền phái người đi cảnh cáo, ai ngờ đối phương chẳng những không biết hối cải, ngược lại dốc toàn bộ lực lượng, phát sinh thú triều! Đem học viện danh sư vây công đánh lén. Hết cách rồi, đành phải đem thuần thú học viện tất cả học viên đều phái ra ngoài, tiến hành chống cự, hiện tại hai bên tại Hồng Viễn sơn mạch giao chiến... Đã chiến đấu trọn một ngày, đều có chút không kiên trì nổi!”"),i("br"),i("br"),n._v("“Không kiên trì nổi...” Trương Huyền sầm mặt lại."),i("br"),i("br"),n._v("Vân Vụ sơn Thánh thú, linh thú sở dĩ bạo động, hắn có thể đoán được."),i("br"),i("br"),n._v("Phải cùng hắn trộm đi địa mạch linh dịch liên quan đến."),i("br"),i("br"),n._v("Vốn cho rằng đám người kia, sẽ an tâm đợi tại sơn mạch, không nghĩ tới thế mà vọt ra, còn cùng danh sư học viện mạnh mẽ chống đỡ lên."),i("br"),i("br"),n._v("“Mang ta xem một chút!”"),i("br"),i("br"),n._v("Nắm đấm xiết chặt, Trương Huyền nói."),i("br"),i("br"),n._v("Chuyện này nếu là hắn gây nên, tự nhiên không có khả năng lùi bước."),i("br"),i("br"),n._v("“Viện trưởng... Ngươi không thể đi, ngươi bây giờ còn chưa tiến hành nghi thức, thực lực cũng không đủ, một khi đi qua, bị đối phương biết thân phận, có thể sẽ gặp phải vây công...”"),i("br"),i("br"),n._v("Triệu Bính Tuất đi đầu lắc đầu."),i("br"),i("br"),n._v("Một cái học viện học sinh đều ra ngoài chiến đấu, đại sự như thế, đều không có nói cho Trương Huyền, cũng bởi vì thực lực của hắn bây giờ không đủ, một khi nói ra là viện trưởng, rất dễ dàng gặp phải đánh lén, làm không cẩn thận, lại sẽ xuất xuất hiện viện trưởng biến mất, học viện cục diện hỗn loạn."),i("br"),i("br"),n._v("Cho nên, bọn họ muốn tự mình giải quyết, để hắn an tâm kế vị, không chịu đến bất kỳ ảnh hưởng gì."),i("br"),i("br"),n._v("Lại nói, bọn họ cũng muốn trước thời hạn giải quyết, là mới viện trưởng lưu cái tốt điềm tốt."),i("br"),i("br"),n._v("“Ở đâu, nếu không ta tự mình đi!”"),i("br"),i("br"),n._v("Trương Huyền lông mày giương lên."),i("br"),i("br"),n._v("Tai họa là hắn xông ra tới, không thể để cho học viện học viên vì đó mất mạng."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Gặp hắn thái độ kiên quyết, hai vị trưởng lão lần nữa nhìn nhau, kìm lòng không được than thở một tiếng: “Tốt a, chúng ta mang viện trưởng đi qua, có điều, còn hi vọng viện trưởng, không nên gấp gáp, đi cũng đừng rò rỉ thân phận... Đây cũng không phải là Vân Vụ lĩnh lần thứ nhất thú triều, nhất định có thể có biện pháp trấn áp!”"),i("br"),i("br"),n._v("“Ừm!” Trương Huyền cũng không nhiều lời."),i("br"),i("br"),n._v("Mi trưởng lão đem bản thân hỏa long Thánh thú triệu hoán tới, ba người đạp vào, thẳng tắp hướng Hồng Viễn sơn mạch bay đi."),i("br"),i("br"),n._v("“Tình huống bây giờ như thế nào?”"),i("br"),i("br"),n._v("Đứng tại lưng thú, Trương Huyền nhìn về phía Triệu Bính Tuất."),i("br"),i("br"),n._v("“Cũng không lạc quan... Lần này thú triều, so trước kia đều còn nghiêm trọng hơn, tất cả linh thú cũng giống như có quy luật binh sĩ, tiến hành vây công, học viên của chúng ta mười phần bị động!”"),i("br"),i("br"),n._v("Chần chờ một chút, Triệu Bính Tuất nói."),i("br"),i("br"),n._v("“Có quy luật?”"),i("br"),i("br"),n._v("“Ừm, nếu như không có đoán sai, hẳn là Vân Vụ lĩnh những cái kia Thánh thú cũng xuất động! Chỉ có bọn chúng chỉ huy, mới có thể duy trì như thế sức chiến đấu...”"),i("br"),i("br"),n._v("Triệu Bính Tuất gật đầu."),i("br"),i("br"),n._v("“Thương vong như thế nào?” Trầm mặc hồi lâu, Trương Huyền nhịn không được hỏi."),i("br"),i("br"),n._v("“Rất nghiêm trọng, thuần thú học viện là danh sư học viện lớn thứ tư viện hệ, làm đầu tiên phụ tu, chừng hơn hai vạn người, loại trừ thí luyện, hầu như toàn bộ tham gia chiến đấu, hiện tại bị thương không sai biệt lắm nối gần một nửa, chẳng qua tử vong ngược lại không nhiều, chỉ có mười mấy cái!”"),i("br"),i("br"),n._v("Triệu Bính Tuất nói."),i("br"),i("br"),n._v("“Đã chết mười mấy cái?” Trương Huyền toàn thân cứng ngắc."),i("br"),i("br"),n._v("“Vâng!” Lần này nói chuyện không phải Triệu Bính Tuất, mà là Mi trưởng lão."),i("br"),i("br"),n._v("Đang khi nói chuyện, Thánh thú đã đi tới Hồng Viễn sơn mạch giao chiến chỗ bầu trời, thông qua cửa sổ, Trương Huyền nhìn xuống dưới, lập tức nhìn thấy vô số linh thú, từ trên núi lao xuống mà đến, vô số danh sư học sinh, nghênh đón tiếp lấy, chiến đấu dị thường kịch liệt."),i("br"),i("br"),n._v("Rất nhiều học sinh bị đánh thành trọng thương, nhưng cũng không lùi bước, ngược lại càng thêm dũng mãnh, sống chết đều không e ngại."),i("br"),i("br"),n._v("Trương Huyền thân thể run rẩy."),i("br"),i("br"),n._v("Hắn thấy được hai người quen, đang là trước kia tại Lôi Viễn phong náo mâu thuẫn Phong Ngô cùng hơn ngồi, lúc này hai người máu me khắp người, thứ hai càng là một cánh tay đều chặt đứt, vẫn như trước không có lui về phía sau, ngược lại cắn răng xông về phía trước, hai mắt ửng hồng."),i("br"),i("br"),n._v("Cùng trước đó cùng hắn tranh đấu lúc, vì tư lợi tình cảnh hoàn toàn khác biệt."),i("br"),i("br"),n._v("Còn chứng kiến trước đó tại học viện tranh đấu mấy người, giờ phút này cũng kề vai chiến đấu, sống lưng lẫn nhau dựa sát vào nhau, từng người đem tín nhiệm nhất chỗ lưu cho đồng bạn."),i("br"),i("br"),n._v("“Giữa bọn hắn có thể có mâu thuẫn, có thể tranh đấu, thậm chí có thể làm ra một chút vi phạm đạo đức sự tình, nhưng... Mặt đối nhân tộc nguy nan, sẽ lập tức không chút do dự đoàn kết cùng một chỗ, việc nghĩa chẳng từ tiến lên, dù là bỏ mình, đều sẽ không tiếc!”"),i("br"),i("br"),n._v("Dường như nhìn ra hắn rung động, Mi trưởng lão vẻ mặt nghiêm túc: “Bởi vì đây là sứ mệnh cùng trách nhiệm! Bởi vì, bọn họ có một cái cùng tên...”"),i("br"),i("br"),n._v("“... Danh sư!”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-884"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-886"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);