(window.webpackJsonp=window.webpackJsonp||[]).push([[2172],{2528:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-878-lai-đen-van-vu-linh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-878-lai-đen-van-vu-linh"}},[n._v("#")]),n._v(" Chương 878: Lại đến Vân Vụ lĩnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 878: Lại đến Vân Vụ lĩnh"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Phía dưới tất cả danh sư, mặc dù tràn đầy không cam lòng cùng uất ức, nghe được là danh sư học viện làm ra quyết định, vẫn gật đầu, lui ra chiến trường, lui về phía sau."),t("br"),t("br"),n._v("“Triệu viện trưởng, ngươi lưu lại an bài tất cả học sinh, tạm thời không nên cùng tất cả linh thú phát sinh xung đột, ta cùng Mi trưởng lão đi Vân Vụ lĩnh là được!”"),t("br"),t("br"),n._v("Thấy mọi người rút đi, Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này không được, ta muốn theo viện trưởng cùng đi...”"),t("br"),t("br"),n._v("Nghe được để hắn rời đi, Triệu Bính Tuất tràn đầy lo lắng, vội vàng lắc đầu."),t("br"),t("br"),n._v("Viện trưởng coi như gần đây tiến bộ rất lớn, cũng chỉ có Tằm Phong cảnh sơ kỳ mà thôi, đi tràn đầy Thánh thú Vân Vụ lĩnh, quả thực liền là chịu chết, hắn coi như liều mạng, cũng muốn theo ở phía sau, bảo đảm an toàn."),t("br"),t("br"),n._v("“Thế nào, nhận ta làm viện trưởng, hiện tại liền mệnh lệnh đều không nghe?”"),t("br"),t("br"),n._v("Trương Huyền lông mày giương lên: “Nếu như không nghe, các ngươi hai cái đều chớ đi, một mình ta đi qua!”"),t("br"),t("br"),n._v("“Viện trưởng...” Triệu Bính Tuất tràn đầy lo lắng."),t("br"),t("br"),n._v("“Những học sinh này cũng quả thực cần muốn an bài, Triệu viện trưởng, ngươi đi về trước đi!”"),t("br"),t("br"),n._v("Mi trưởng lão khoát tay áo, ngay sau đó truyền âm: “Ngươi trở về lập tức chuẩn bị, đồng thời đi tìm Mộc sư đám người, thời gian là hai canh giờ, hai canh giờ bên trong chúng ta không ra, trực tiếp công kích... Ta đi theo viện trưởng bên người, dùng hết tính mạng cũng phải bảo đảm hắn hai canh giờ an toàn!”"),t("br"),t("br"),n._v("“Cái này...” Triệu Bính Tuất sửng sốt một chút, lập tức gật đầu: “Được!”"),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Mặc dù không biết đối phương truyền âm nói cái gì, nhưng Trương Huyền cũng không thèm để ý, nói một tiếng, cùng Mi trưởng lão hai người, ngồi Thánh thú thẳng tắp hướng Vân Vụ lĩnh phương hướng bay đi."),t("br"),t("br"),n._v("Triệu Bính Tuất thì cắn răng một cái, vội vã hướng danh sư học viện bay đi."),t("br"),t("br"),n._v("Người trên không trung, nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Viện trưởng, ngươi ngàn vạn không thể có chuyện xảy ra...”"),t("br"),t("br"),n._v("Hắn biết viện trưởng muốn đi đàm phán, là vì phía dưới tất cả danh sư, làm như vậy thoạt nhìn có sai lầm học viện uy nghiêm, nhưng bảo vệ nhân tộc có sinh chi lực."),t("br"),t("br"),n._v("Đem bêu danh trên lưng mình, chỉ vì có thể để cho càng nhiều tên hơn sư không bị thương tổn, phần này lòng dạ cùng khí phách..."),t("br"),t("br"),n._v("Bọn họ đích xác không chọn lầm người!"),t("br"),t("br"),n._v("Trương sư là cái có thể dựa vào danh sư... Một vị lĩnh ngộ sư giả chi tâm chân chính danh sư!"),t("br"),t("br"),n._v("Cùng năm đó Khổng sư đồng dạng, trách trời thương dân, đại nghĩa thiên hạ."),t("br"),t("br"),n._v("Có lẽ, bọn họ thật chứng kiến một cái truyền kỳ sinh ra."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thánh thú tốc độ cực nhanh, chẳng mấy chốc, liền đi tới Vân Vụ lĩnh, Mi trưởng lão đứng tại phía trước cửa sổ, trung khí mười phần, thanh âm vang vọng bốn phương."),t("br"),t("br"),n._v("“Danh sư học viện Mi Trúc, đúng hẹn đến đây, không biết chư vị Vương Giả ở đâu, mong rằng ra gặp một lần!”"),t("br"),t("br"),n._v("“Còn xin ngồi vào đầu này ưng lưng thú bên trên, sẽ đem ngươi mang tới tìm chúng ta, ngươi Thánh thú, vẫn là lưu tại ngoài núi đi! Ta sợ nói không tốt, hãm nó cùng bất nghĩa!”"),t("br"),t("br"),n._v("Một cái thanh âm nhàn nhạt truyền tới."),t("br"),t("br"),n._v("Mi trưởng lão hỏa long Thánh thú, cũng là thú loại, hiện tại người cùng thú đàm phán, một khi xảy ra vấn đề, nó giúp chủ nhân, chẳng khác nào phản bội Thú tộc, giúp Thú tộc, tương đương với phản bội chủ nhân, vô luận như thế nào làm, đều là lưỡng nan."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Bàn giao Thánh thú một tiếng, Mi trưởng lão thân thể nhoáng một cái, theo lưng thú bay đi, lăng không một trảo, chân khí đem Trương Huyền bao vây, rơi vào cách đó không xa một đầu ưng lưng thú bên trên."),t("br"),t("br"),n._v("“Thu!”"),t("br"),t("br"),n._v("Ưng thú một tiếng hí dài, giương cánh bay lên."),t("br"),t("br"),n._v("Cái tên này đã đạt đến Tằm Phong cảnh, một thân xám xanh, thân thể trôi chảy dường như sấm sét."),t("br"),t("br"),n._v("Cùng trước đó Trương Huyền thuần phục đầu kia Thanh Ưng Thú tương tự, chẳng qua càng thêm cường đại."),t("br"),t("br"),n._v("“Viện trưởng, ngươi trước đừng cho thấy thân phận, liền nói... Là ta một cái học sinh! Một khi bọn chúng nổi lên, sẽ nghĩ biện pháp đưa ngươi đưa ra tới...”"),t("br"),t("br"),n._v("  Mi trưởng lão lặng lẽ truyền âm."),t("br"),t("br"),n._v("“Không cần phiền toái như vậy, không cho thấy thân phận làm sao nói?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Cái này... Đám thánh thú này từ trước tới nay cùng chúng ta danh sư học viện không hợp nhau, coi như không phát sinh hai ngày này sự tình, cũng muốn đem hắn san bằng... Chỉ tiếc, đám người kia, một mực núp ở hang ổ không ra, học viện những năm này, phái đi qua thật nhiều người, thậm chí hoàng thất đều phái đi không ít, đều bặt vô âm tín, đoán chừng bị độc thủ!”"),t("br"),t("br"),n._v("Mi trưởng lão nói."),t("br"),t("br"),n._v("“Phái không ít người?” Nhướng mày, Trương Huyền đột nhiên nhớ tới ban đầu ở địa mạch linh dịch trì đằng sau nhìn thấy tất cả thi cốt."),t("br"),t("br"),n._v("Làm không cẩn thận, những cái kia chính là hoàng thất, hoặc là tên thực học viện phái ra đội ngũ một trong."),t("br"),t("br"),n._v("“Vân Vụ lĩnh, quanh năm mây mù, địa hình phức tạp, dễ thủ khó công, đám thánh thú này, ở lại chỗ này, dường như cắm ở nhân loại cổ họng một cây gai, đã sớm muốn diệt trừ, chỉ tiếc... Quá khó!”"),t("br"),t("br"),n._v("Nói đến đây, Mi trưởng lão giống như là nhớ ra cái gì đó, vẻ mặt nghiêm túc: “Hơn nữa, đám này Thánh thú, dường như cũng không phải là chiếm núi làm vua đơn giản như vậy, lão viện trưởng biết một chút, có điều, chưa kịp nói tỉ mỉ liền mất tích! Căn cứ tư liệu lịch sử ghi chép, năm đó Ngô Dương Tử tiền bối mất tích, có vẻ như cũng cùng Vân Vụ lĩnh liên quan đến!”"),t("br"),t("br"),n._v("Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Hắn đối Ngô Dương Tử biết đến rất nhiều, thậm chí còn chiếm được đối phương lưu lại bảo tàng, nếu quả thật cùng cái này Vân Vụ lĩnh liên quan đến, chỉ sợ phía sau cũng liên lụy đến Dị Linh tộc nhân."),t("br"),n._v(" “Chẳng lẽ... Cùng Ngoan Nhân liên quan đến?”"),t("br"),t("br"),n._v("Nhận được Ngô Dương Tử bảo tàng về sau, từng chuyên môn hỏi thăm qua Ngoan Nhân, nhưng mà cái tên này chỉ còn lại có một cái trái tim, rất nhiều ký ức đều biến mất, chỉ biết là Địa Cung sự tình, nhiều cũng không hiểu rõ."),t("br"),t("br"),n._v("Nhưng Ngô Dương Tử mất tích cùng Dị Linh tộc nhân liên quan đến... Chẳng lẽ đám thánh thú này, cũng có liên hệ nào đó?"),t("br"),t("br"),n._v("Có lẽ thật là có khả năng!"),t("br"),t("br"),n._v("Tại Hồng Viễn thành như thế tới gần danh sư học viện chỗ, đem một cái đến gần vô hạn Thánh vực nhị trọng siêu cấp cường giả bắt đi, chỉ bằng vào Dị Linh tộc nhân, chỉ sợ thật khó lấy làm được."),t("br"),t("br"),n._v("“Vâng, nguyên nhân chính là như thế, vì điều tra chân tướng, cũng có cường giả Thánh vực lẻn vào trong đó, đáng tiếc, đồng dạng không thành công, nơi này bảy trăm năm trước ta cũng đã tới... Lúc ấy nếu không phải lão viện trưởng cứu giúp, khả năng đã sớm chết...”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Mi trưởng lão nói."),t("br"),t("br"),n._v("Bảy trăm năm trước, hắn thực lực có thành tựu, cũng muốn dò la xem chỗ này đến tột cùng, lặng lẽ lẻn vào trong đó, kết quả đi không bao xa, liền bị đánh nhanh muốn tử vong."),t("br"),t("br"),n._v("Không phải lão viện trưởng, đâu còn có hắn hôm nay."),t("br"),t("br"),n._v("“Ah!” Không nghĩ tới vị này Mi viện trưởng còn có loại kinh nghiệm này, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Hắn mặc dù đi vào qua một chuyến, nhưng Vân Vụ lĩnh quả thực phi thường kỳ lạ."),t("br"),t("br"),n._v("Nếu không phải nhờ vào Vu hồn, đừng nói trộm đi đối phương địa mạch linh dịch, sợ rằng sẽ giống như Ngụy Trường Phong, bị ngay tại trận đánh chết."),t("br"),t("br"),n._v("“Cho nên, một lát nữa tận lực không cần cho thấy thân phận, nếu không ta sợ sẽ xuất hiện biến cố...” Nói hết nguyên do, Mi trưởng lão nói."),t("br"),t("br"),n._v("“Ừm, đến lúc đó tùy cơ ứng biến đi!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nếu quả thật liên quan nhiều như vậy, Vân Vụ lĩnh Thánh thú biết mình là viện trưởng, làm không cẩn thận thật sẽ nổi điên."),t("br"),t("br"),n._v("Cho dù có Tử Dương thú cùng Kim Nguyên đỉnh, tích tụ như núi Thánh thú, Tòng Thánh, Bán Thánh, muốn chạy trốn, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Hai người nói chuyện công phu, dưới thân ưng thú, bay qua nồng đậm mê vụ, đi tới một chỗ ngọn núi."),t("br"),t("br"),n._v("Trương Huyền nhìn một chút, lại là hắn quen thuộc chỗ, địa mạch linh dịch chỗ trên mặt đất."),t("br"),t("br"),n._v("“Thu!”"),t("br"),t("br"),n._v("Ưng thú minh kêu một tiếng, bút bay thẳng xuống, Mi trưởng lão thân thể nhoáng một cái, rơi trên mặt đất, Trương Huyền theo sát ở phía sau."),t("br"),t("br"),n._v("“Danh sư học viện Mi Trúc mang theo học sinh đã đến, còn xin Vân Vụ lĩnh các vương giả vừa thấy!”"),t("br"),t("br"),n._v("Tiến về phía trước một bước, Mi trưởng lão Thánh vực nhất trọng đỉnh phong khí tức, xao động mà ra."),t("br"),t("br"),n._v("“Mi viện trưởng, đi vào rồi nói sau!”"),t("br"),t("br"),n._v("Thanh âm nhàn nhạt từ tiền phương sơn động truyền đến, ngay sau đó hai đầu Bán Thánh cấp bậc hắc linh báo đi ra, nhìn về phía hai người, làm ra mời tư thế xin mời, giống như là hai tên hộ vệ."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Mi Trúc gật gật đầu, đi đầu đi vào trong."),t("br"),t("br"),n._v("Trương Huyền nhìn quanh hai bên một vòng, phát hiện chung quanh vẫn như cũ có không ít Tòng Thánh, Bán Thánh cấp bậc linh thú thủ hộ, chẳng qua số lượng so với lần trước tới ít nhiều lắm."),t("br"),t("br"),n._v("Xem ra, hẳn là cùng danh sư học viện chiến đấu, bị phái đi ra."),t("br"),t("br"),n._v("Dọc theo sơn động đi vào trong, rất mau tới đến trước đó chứa đựng linh dịch mênh mông hang động, lập tức nhìn thấy mười đầu Thánh vực nhất trọng đỉnh phong Thánh thú ngồi ở trong đó, thấy bọn họ đi vào, nguyên một đám ánh mắt như điện (giật), mang theo lạnh lùng sát cơ."),t("br"),t("br"),n._v("“Mi Trúc, mang theo cái Tằm Phong cảnh học sinh liền tới đây, lá gan không nhỏ!”"),t("br"),t("br"),n._v("Một người trong đó, Thánh vực nhất trọng đỉnh phong hoàng kim Hùng Hổ thú, ánh mắt băng lãnh nhìn qua."),t("br"),t("br"),n._v("Danh sư học viện Mi Trúc, thuần thú học viện viện trưởng, Vân Vụ lĩnh Thánh thú, cũng sớm có nghe thấy."),t("br"),t("br"),n._v("“Ta người đã đến, chúng ta vẫn là thương nghị một chút, danh sư học viện cùng Vân Vụ lĩnh chiến đấu, như thế nào giải quyết tốt hậu quả đi! Như vậy tiếp tục đánh xuống, ta sợ sẽ lưỡng bại câu thương!”"),t("br"),t("br"),n._v("Mi trưởng lão khoát tay áo."),t("br"),t("br"),n._v("“Lưỡng bại câu thương? Lưỡng bại câu thương thì thế nào? Nhân loại các ngươi trộm đi chúng ta địa mạch linh dịch, chặt đứt chúng ta cơ sở, cùng lắm thì liền cá chết lưới rách, giết thống khoái!”"),t("br"),t("br"),n._v("Một đầu Thánh vực nhất trọng đỉnh phong Xích Viêm sư hống nói."),t("br"),t("br"),n._v("“Trộm đi địa mạch linh dịch?” Mi trưởng lão nhướng mày."),t("br"),t("br"),n._v("“Không sai, đây chính là chúng ta chứa đựng địa mạch linh dịch, hiện tại rỗng tuếch, chỉ có nhân loại các ngươi như thế hèn hạ...” Xích Viêm sư nói."),t("br"),t("br"),n._v("Mi trưởng lão quay đầu nhìn thoáng qua, quả nhiên thấy cách đó không xa một cái mênh mông bể tắm, mặc dù không còn đồ vật, vẫn như cũ có thể cảm nhận được trong đó linh khí nồng nặc."),t("br"),t("br"),n._v("Nơi này là Vân Vụ lĩnh trung tâm nhất chỗ, vô số Thánh thú, Tòng Thánh trấn giữ, loại tình huống này, có thể đem linh dịch trộm đi... Đây rốt cuộc là vị cao nhân nào làm?"),t("br"),t("br"),n._v("Phải biết hắn lúc trước thực lực lớn thành, đừng nói nơi này, liền giữa sườn núi cũng chưa tới, liền bị đánh suýt chút nữa gần chết."),t("br"),t("br"),n._v("Có thể đi vào, đồng thời trộm đi đồ vật, còn có thể sống được... Quả thực liền cho người tộc mở miệng ác khí."),t("br"),t("br"),n._v("“Chuyện này ta cũng không biết rõ tình hình!”"),t("br"),t("br"),n._v("Trong lòng tràn đầy bội phục, trên mặt lại không biểu hiện ra ngoài, Mi trưởng lão khoát tay áo: “Các ngươi nói là nhân tộc làm, nhưng có chứng cứ? Là ai?”"),t("br"),t("br"),n._v("“Không biết rõ tình hình? Một câu không biết rõ tình hình liền nghĩ lừa gạt?”"),t("br"),t("br"),n._v("Hoàng kim Hùng Hổ thú hừ một tiếng."),t("br"),t("br"),n._v("“Không phải lừa gạt, các ngươi chưa bắt được chứng cứ, chính là không đầu sổ nợ rối mù, tìm ta cũng vô dụng, ta chỉ đại biểu danh sư học viện! Chúng ta vẫn là nói một chút, song phương chiến đấu đi!”"),t("br"),t("br"),n._v("Mi trưởng lão không trong vấn đề này dây dưa: “Giữa chúng ta trò đùa trẻ con, Danh Sư đường sẽ không quản lý, một khi làm lớn chuyện, sinh linh đồ thán, chỉ sợ các ngươi Vân Vụ lĩnh, cũng liền không có tồn tại cần thiết, sẽ bị tuỳ tiện xóa đi! Điểm ấy không cần ta nhiều lời, các ngươi cũng hẳn là rõ ràng, bằng không thì cũng không có khả năng, nhiều năm như vậy đều bình an vô sự!”"),t("br"),t("br"),n._v("“Trước kia có địa mạch linh dịch, chúng ta có tấn cấp hi vọng, hiện tại không đồng dạng...”"),t("br"),t("br"),n._v("Nghe được hắn, hoàng kim Hùng Hổ thú, thô to móng vuốt vẫy một cái: “Muốn hoà giải cũng được, danh sư học viện chỉ cần đền bù chúng ta, năm vạn viên linh thạch thượng phẩm, cũng hỗ trợ tại Vân Vụ lĩnh kiến tạo năm trăm chỗ cấp sáu tụ linh đại trận, ta liền lập tức đem linh thú rút về tới!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-886"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-888"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);