(window.webpackJsonp=window.webpackJsonp||[]).push([[2173],{2527:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-879-truong-huyen-thu-đoan-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-879-truong-huyen-thu-đoan-thuong"}},[n._v("#")]),n._v(" Chương 879: Trương Huyền thủ đoạn (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 879: Trương Huyền thủ đoạn (thượng)"),t("br"),t("br"),n._v("“Năm vạn viên linh thạch thượng phẩm? Còn năm trăm chỗ đại trận? Làm cái gì mộng đẹp? Đổi điều kiện!”"),t("br"),t("br"),n._v("Mi trưởng lão sầm mặt lại."),t("br"),t("br"),n._v("Quả thực công phu sư tử ngoạm."),t("br"),t("br"),n._v("Năm vạn viên linh thạch thượng phẩm, đem trọn cái danh sư học viện lật một lần, cũng không bỏ ra nổi tới!"),t("br"),t("br"),n._v("Lại nói, danh sư học viện cũng không sợ các ngươi, nhưng mà lo lắng sinh linh đồ thán thôi, thực sự không được, điều động toàn bộ lực lượng, đồng dạng có thể đem một cái Vân Vụ lĩnh san bằng!"),t("br"),t("br"),n._v("Nhưng mà như thế mọi người tổn thất đều rất lớn, làm đất trời oán giận, có thể tránh khỏi, liền tận lực tránh khỏi."),t("br"),t("br"),n._v("“Đổi điều kiện? Không được, liền cái này một cái, đáp ứng liền đáp ứng, không đáp ứng, tiếp tục chiến đấu! Nói thật cho các ngươi biết, linh thú phi hành ta còn không có phái đi ra, một khi bắt đầu, chỉ bằng các ngươi những này không biết bay học viên, có thể ngăn cản? Chỉ muốn đánh vào Hồng Viễn thành, cũng không phải là thương vong mấy chục người đơn giản như vậy, đến lúc đó, nhìn các ngươi như thế nào cùng tổng bộ giải thích, như thế nào cùng phổ thông bách tính giải thích!”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú hừ lạnh nói."),t("br"),t("br"),n._v("Danh sư học viện bảo hộ một phương an toàn, thật nếu để cho đối phương linh thú phi hành, xông vào nội thành trắng trợn chém giết, học viện công tín lực, khẳng định lại nhận nghi ngờ cùng phá hư."),t("br"),t("br"),n._v("Nhân tộc đối Danh Sư đường lòng tin, cũng lại bởi vậy dao động."),t("br"),t("br"),n._v("“Ngươi... Đây là tại uy hiếp?” Mi trưởng lão ánh mắt phát lạnh."),t("br"),t("br"),n._v("Đường đường danh sư học viện, thế mà bị một đám Thánh thú uy hiếp, quả thực sai lầm nghiêm trọng!"),t("br"),t("br"),n._v("Nếu không phải lúc trước tổng bộ nhớ đám này Thánh thú có thể rèn luyện học viên, càng có thể cho tất cả danh sư mang đến cảm giác nguy cơ, để hắn quyết chí tự cường, khẳng định đã sớm ra tay diệt tuyệt!"),t("br"),t("br"),n._v("Làm sao đến mức để hắn diễu võ giương oai."),t("br"),t("br"),n._v("Danh sư không thể nhục."),t("br"),t("br"),n._v("Đây không phải lời nói suông, cũng không phải giả, thật muốn dám nghi ngờ, không ngại ra tay theo đại lục ở bên trên xóa đi."),t("br"),t("br"),n._v("Năm đó Vu hồn nhất mạch, cường đại như thế, cũng không biến mất tại dòng sông lịch sử sao?"),t("br"),t("br"),n._v("Thậm chí độc sư quỷ dị như vậy, đồng dạng không dám đem Độc điện xây dựng ở bên ngoài?"),t("br"),t("br"),n._v("Đang muốn nổi giận, cùng đối phương cá chết lưới rách, liền nghe đến viện trưởng truyền âm tại vang lên bên tai: “Nghĩ biện pháp hỏi ra bọn chúng linh thú phi hành đội ngũ ở nơi nào, thuận tiện để cho ta đi qua!”"),t("br"),t("br"),n._v("Mi trưởng lão sững sờ."),t("br"),t("br"),n._v("Hỏi cái này làm gì? Đi qua? Nhìn linh thú phi hành đội ngũ?"),t("br"),t("br"),n._v("Bất quá, đã viện trưởng yêu cầu, đành phải nhíu nhíu mày, nhìn về phía trước mắt Hoàng Kim Hùng Hổ thú: “Vân Vụ lĩnh cho dù có linh thú phi hành, số lượng cũng có hạn đi, danh sư học viện Thánh giả mấy trăm, ngăn lại những này, không tính là gì! Muốn uy hiếp, cũng muốn xuất ra một chút có thực tế năng lực, nói như vậy, ngươi cảm thấy ta sẽ biết sợ?”"),t("br"),t("br"),n._v("“Số lượng có hạn? Ta Vân Vụ lĩnh, linh thú phi hành trọn vẹn ba ngàn, hơn nữa mỗi một đầu đều cự ly ngắn xông vào cực nhanh, danh sư học viện coi như cường giả Thánh vực phần đông, muốn muốn ngăn cản, cũng không dễ dàng như vậy đi! Chỉ muốn xông ra mấy trăm đầu, liền có thể tạo thành rất lớn phá hư... Thậm chí, chúng ta có thể không xung kích Đế đô, xung kích xung quanh một chút tiểu thành thị, ta không tin các ngươi có nhiều như vậy Thánh giả tọa trấn?”"),t("br"),t("br"),n._v("Cười lạnh, Hoàng Kim Hùng Hổ thú ý uy hiếp, lộ rõ trên mặt."),t("br"),t("br"),n._v("“Ba ngàn đầu?” Mi trưởng lão cười nhạo: “Đoán chừng đều là chút Hóa Phàm nhất, nhị trọng thậm chí còn không đến Hóa Phàm cảnh a, đám ô hợp mà thôi, nhiều hơn nữa, ta danh sư học viện lại có sợ gì!”"),t("br"),t("br"),n._v("Linh thú phi hành, mặc dù có thể phi hành, cũng phải nhìn thực lực."),t("br"),t("br"),n._v("Một chút liền Hóa Phàm đều không có đi đến, thậm chí chỉ có Hóa Phàm hai, tam trọng, coi như biết bay lại như thế nào?"),t("br"),t("br"),n._v("Danh sư học viện tùy tiện phái mấy cái học sinh, liền có thể nhẹ nhõm bắn giết."),t("br"),t("br"),n._v("“Hóa Phàm nhất, nhị trọng? Mi trưởng lão xem ra không biết chúng ta Vân Vụ lĩnh thực lực! Ta ba ngàn linh thú phi hành, đều có Hóa Phàm thất trọng cùng với trở lên thực lực! Ngươi cảm giác danh sư học viện, nhưng có năng lực chặn lại?”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú đạo."),t("br"),t("br"),n._v("“Hóa Phàm thất trọng trở lên? Giọng điệu thật là lớn!” Mi trưởng lão lắc đầu, một mặt không tin."),t("br"),t("br"),n._v("Vân Vụ lĩnh cứ như vậy lớn, làm sao có thể có nhiều như vậy cường đại linh thú phi hành?"),t("br"),t("br"),n._v("Tài nguyên cũng không đủ ah!"),t("br"),t("br"),n._v("đọc truＹện với http://truye"),t("br"),n._v("ncｕatui.net Huống chi, phía dưới cùng tất cả học sinh chiến đấu linh thú, càng là nhiều vô số kể."),t("br"),t("br"),n._v("Vân Vụ lĩnh tài nguyên, có thể nuôi sống nhiều như vậy cường đại linh thú?"),t("br"),t("br"),n._v("“Không tin?” Hoàng Kim Hùng Hổ thú cau mày."),t("br"),t("br"),n._v("“Ngươi nói như thế khoa trương như thế nào để cho ta tin tưởng... Đã các ngươi không có thành ý, ta nhìn cũng không cần nói chuyện!”"),t("br"),t("br"),n._v("Mi trưởng lão vẫy tay, dừng lại một chút: “Đương nhiên, để cho ta tin tưởng cũng rất đơn giản, hoàn toàn có thể phái cái Thánh thú, mang ta người học sinh này đi qua nhìn một chút, thật có nhiều như vậy cường đại linh thú, ta tự sẽ cho các trưởng lão khác đưa tin, thậm chí còn có thể cùng Ngọc Thần Thanh bệ hạ hiệp thương!”"),t("br"),t("br"),n._v("“Nếu ngươi chưa thấy quan tài chưa đổ lệ, vậy thì tốt, Hắc Linh báo các ngươi mang Mi trưởng lão người học sinh này qua đi mở mang kiến thức một chút chúng ta linh thú phi hành quân đoàn!”"),t("br"),t("br"),n._v("Thô to móng vuốt vẫy một cái, Hoàng Kim Hùng Hổ thú hừ một tiếng."),t("br"),t("br"),n._v("Mặc dù nó nói hung mãnh, kỳ thật đối danh sư học viện cũng rất e ngại."),t("br"),t("br"),n._v("Bồi dưỡng được nhiều như vậy danh sư, tàng long ngọa hổ, chỉ đối phó học sinh, còn có tự tin, lão sư cùng tiến lên, coi như dựa dẫm địa thế, cũng khẳng định sẽ bị tiêu diệt!"),t("br"),t("br"),n._v("Thậm chí... Coi như chư nhiều vị lão sư không ra tay, chỉ làm cho học sinh rèn luyện, bọn chúng cũng sẽ tổn thương nặng, bao nhiêu năm nghỉ ngơi lấy lại sức, hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, có thể đàm phán, tận lực đàm phán."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Trước đó thủ tại cửa ra vào hai đầu Bán Thánh cấp bậc Hắc Linh báo, lên tiếng, đi đến."),t("br"),t("br"),n._v("“Mi trưởng lão, ngươi tận lực kéo dài thời gian, không cần tới xung đột, bảo vệ tốt bản thân...”"),t("br"),t("br"),n._v("Truyền âm bàn giao, Trương Huyền đứng dậy đi theo Hắc Linh báo sau lưng, đi ra ngoài."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mặc dù không biết viện trưởng mục đích là cái gì, Mi trưởng lão vẫn là lên tiếng, ngẩng đầu nhìn qua: “Học trò ta hiện tại nhìn, nếu quả thật như các ngươi nói tới như thế, sẽ tình hình thực tế bẩm báo học viện, sau đó lại làm giải quyết! Thật có nhiều như vậy linh thú quân đoàn lời nói, các ngươi nói điều kiện, cũng không phải là không thể đáp ứng...”"),t("br"),t("br"),n._v("“Ồ? Vẫn là Mi trưởng lão dễ nói chuyện, vậy chúng ta liền đợi đến...”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú nhẹ gật đầu."),t("br"),t("br"),n._v("Cái khác Thánh thú cũng không nói thêm gì nữa."),t("br"),n._v(" Có thể không chiến đấu liền có thể nhận được chỗ tốt tự nhiên tốt nhất."),t("br"),t("br"),n._v("Bọn chúng mặc dù thực lực hùng hậu, nhưng cùng Danh Sư đường liều mạng, vẫn là không dám."),t("br"),t("br"),n._v("Sơn động lập tức lâm vào yên tĩnh, Mi trưởng lão thấy kéo dài thời gian thành công, nhịn không được thở phào nhẹ nhõm, đồng thời trong lòng âm thầm nghi ngờ... Viện trưởng phải biết cái nào linh thú phi hành vị trí cụ thể làm gì?"),t("br"),t("br"),n._v("Nhiều như vậy linh thú, danh sư học viện tất cả lão sư đều điều động, đều tràn đầy đau đầu, huống chi một người."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi theo Hắc Linh báo sau lưng, Trương Huyền đi ra sơn động, lập tức nhìn thấy bốn phía mấy chục con Bán Thánh, Tòng Thánh cấp bậc linh thú chạy chung quanh, nhìn chằm chằm."),t("br"),t("br"),n._v("“Hai vị, thực lực của ta thấp... Đám thánh thú này ở chung quanh quanh quẩn một chỗ, sợ sẽ gây bất lợi cho ta... Có thể hay không để bọn chúng lui xuống trước đi?”"),t("br"),t("br"),n._v("Trương Huyền rụt cổ một cái."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Thấy đường đường Tằm Phong cảnh danh sư cư nhiên như thế nhát gan, hai đầu Hắc Linh báo khóe miệng vung lên, tràn đầy cười lạnh."),t("br"),t("br"),n._v("“Lão sư nhất định muốn mang ta tới, ta thật sự sợ hãi, nhìn linh thú phi hành, cũng không phải ta nguyện ý... Hai vị xin thương xót, chỉ cần không để bọn chúng ở chung quanh, cái này hai cái linh thạch thượng phẩm, chính là các ngươi...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lấy ra hai cái linh thạch thượng phẩm, ném tới, Trương Huyền trong mắt tràn đầy cảnh giác nhìn xem bốn phía, dường như tràn đầy không an toàn cảm giác."),t("br"),t("br"),n._v("Tiếp nhận linh thạch, hai đầu Hắc Linh báo con mắt lập tức sáng lên."),t("br"),t("br"),n._v("Bọn chúng nhưng mà Bán Thánh, hưởng thụ tài nguyên không nhiều, hai cái linh thạch thượng phẩm, đã coi như là không nhỏ tài phú."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Đem linh thạch thả lên, hai đầu Hắc Linh báo thô to móng vuốt vung lên, rống lên một tiếng."),t("br"),t("br"),n._v("Chung quanh tất cả Tòng Thánh, Bán Thánh linh thú, nghe được mệnh lệnh, nhao nhao lui về phía sau, thời gian nháy mắt liền biến mất tại ánh mắt."),t("br"),t("br"),n._v("Mặc dù Hắc Linh báo thực lực, không phải trong đó mạnh nhất, thật là Vân Vụ lĩnh mấy đại vương giả người hầu, nói chuyện không có thú dám vi phạm."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Thấy chư thú rời đi, hai đầu Hắc Linh báo ngẩng đầu lên, nhìn về phía Trương Huyền, tràn đầy kiêu ngạo, ý tứ rất đơn giản... Ngươi can đảm nhỏ như vậy, làm sao thành danh sư?"),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Đối với bọn chúng chế giễu, Trương Huyền nhẹ nhàng cười một tiếng, cổ tay khẽ đảo."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một cái thân ảnh giống nhau như đúc xuất hiện ở bên người, hai người đồng thời nhoáng một cái, giống như quỷ mị, thời gian nháy mắt đi tới hai đầu Hắc Linh báo trước mặt, đồng thời một quyền bắn ra!"),t("br"),t("br"),n._v("Bành! Bành!"),t("br"),t("br"),n._v("Tốc độ quá nhanh, hai đầu Hắc Linh báo liền phản ứng đều không có kịp phản ứng, liền bay rớt ra ngoài, nằm trên mặt đất không ngừng run rẩy."),t("br"),t("br"),n._v("“Hoặc là thần phục với ta, hoặc là... Chết!”"),t("br"),t("br"),n._v("Hai vị Trương Huyền, phân biệt đi tới hai đầu Hắc Linh báo trước mặt, bàn chân giẫm lên đầu của đối phương, thần sắc lạnh nhạt nhìn qua."),t("br"),t("br"),n._v("“Rống...”"),t("br"),t("br"),n._v("Hắc Linh báo không có nghĩ tới tên này thái độ chuyển biến nhanh như vậy, rõ ràng chỉ có Tằm Phong cảnh, bọn chúng Bán Thánh đều ngăn cản không nổi, tất cả đều bối rối."),t("br"),t("br"),n._v("Mấy hơi thở qua đi, liền có quyết định, một giọt máu tươi theo mi tâm bay ra rơi vào Trương Huyền trước mặt."),t("br"),t("br"),n._v("Hắc Linh báo... Thần phục!"),t("br"),t("br"),n._v("“Không sai, đem trước chung quanh những Bán Thánh đó, Tòng Thánh cấp bậc linh thú, từng đầu kêu đến, liền nói bên trong Hoàng Kim Hùng Hổ thú triệu hoán!”"),t("br"),t("br"),n._v("Đem hắn thuần phục, Trương Huyền phân phó."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Không dám vi phạm, hai đầu Hắc Linh báo gật một cái to lớn đầu lâu, xoay người đi ra ngoài."),t("br"),t("br"),n._v("Thấy chúng nó rời đi, Trương Huyền tinh thần khẽ động, Tử Dương thú cùng Kim Nguyên đỉnh xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“A Tử, Đỉnh Đỉnh, một lát nữa đem những linh thú này đánh cho ta thần phục...”"),t("br"),t("br"),n._v("“Vâng, chủ nhân!” Tử Dương thú gật đầu."),t("br"),t("br"),n._v("“Hắc hắc, ta thích nhất chuyện như vậy, chủ nhân, không nghe lời không thần phục, có thể hay không cho phép ta luyện thành xỉ than?” Kim Nguyên đỉnh thanh âm bên trong tràn đầy kích động."),t("br"),t("br"),n._v("Làm danh sư học viện trấn viện Thánh khí, nó từ trước tới nay rất rụt rè, có rất ít loại này có thể đại đánh cơ hội xuất thủ."),t("br"),t("br"),n._v("“Có thể!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lần này tới, chính là đồ sát Vân Vụ lĩnh, không thần phục... Chỉ có một con đường có thể đi, cái kia nhất định phải chết!"),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Không lâu sau, một đầu Tòng Thánh cấp bậc linh thú liền bị Hắc Linh báo hô tới, hai phút đồng hồ về sau, quỳ gối Trương Huyền trước mặt, hoàn toàn thần phục."),t("br"),t("br"),n._v("Lại mấy phút đồng hồ sau, ba đầu Bán Thánh cấp bậc linh thú thần phục."),t("br"),t("br"),n._v("Sau mười phút, trước đó ở chung quanh loạn chuyển tất cả Tòng Thánh, Bán Thánh đều đã toàn bộ nghe theo Trương Huyền mệnh lệnh."),t("br"),t("br"),n._v("“Xong rồi, các ngươi còn cùng trước đó đồng dạng, tán ở chung quanh, không nên để cho cái khác thú nhìn ra, Hắc Linh báo, mang ta đi linh thú phi hành nơi đó!”"),t("br"),t("br"),n._v("An bài xong những này, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Những này Tòng Thánh, Bán Thánh so tưởng tượng tốt thuần phục một chút, chỉ có hai đầu không nghe lời, bị Kim Nguyên đỉnh ngay tại trận luyện thành xỉ than."),t("br"),t("br"),n._v("Đi qua lần này giết gà dọa khỉ, cái khác linh thú, lại không dám có chút phản kháng, tất cả đều ngoan ngoãn nghe lời."),t("br"),t("br"),n._v("“Chủ nhân, linh thú phi hành toàn bộ tập trung ở bên kia sơn cốc...”"),t("br"),t("br"),n._v("Nhận chủ nhân, Hắc Linh báo giờ phút này cũng tận tâm tận trách nhiệm, phía trước dẫn đường."),t("br"),t("br"),n._v("Trương Huyền đem Tử Dương thú, phân thân cùng Kim Nguyên đỉnh lần nữa thu vào Thiên Nghĩ Phong sào huyệt, lúc này mới theo ở phía sau, sải bước đi đi qua."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-887"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-889"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);