(window.webpackJsonp=window.webpackJsonp||[]).push([[2174],{2529:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-880-truong-huyen-thu-đoan-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-880-truong-huyen-thu-đoan-ha"}},[n._v("#")]),n._v(" Chương 880: Trương Huyền thủ đoạn (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 880: Trương Huyền thủ đoạn (hạ)"),t("br"),t("br"),n._v("Linh thú phi hành khoảng cách chứa đựng địa mạch linh dịch chỗ, cũng không gần, cưỡi tại Hắc Linh báo sống lưng bên trên một đường chạy như điên, tiếp cận năm phút đồng hồ mới đi đến trước mặt."),t("br"),t("br"),n._v("Là cái to lớn sơn cốc, đường kính chừng hai, ba dặm."),t("br"),t("br"),n._v("Phía trên thung lũng, Trương Huyền Minh Lý Chi Nhãn nhúc nhích, nhìn sang."),t("br"),t("br"),n._v("Cùng Hoàng Kim Hùng Hổ thú nói đồng dạng, quả thực có ba ngàn khoảng cách, hơn nữa những linh thú này sức mạnh, cũng đều đạt đến Hóa Phàm thất trọng trở lên."),t("br"),t("br"),n._v("Cường đại như thế linh thú phi hành quần, nếu quả thật hướng Hồng Viễn thành phóng đi, chỉ sợ thật đúng là không tiện ngăn cản."),t("br"),t("br"),n._v("Coi như về sau toàn bộ có thể chém giết, cũng tất nhiên tạo thành cực tổn thất lớn."),t("br"),t("br"),n._v("“Nhiều như vậy linh thú chất thành một đống, nhất định phải bố trí cái trận pháp, mới có thể không để hắn chạy trốn!”"),t("br"),t("br"),n._v("Lông mày nhăn lại."),t("br"),t("br"),n._v("Linh thú thực sự nhiều lắm, nhất định phải bố trí cái rất cường đại đại trận, mới có thể đem hắn toàn bộ bao phủ, nếu không một khi có chạy trốn, báo tin Hoàng Kim Hùng Hổ thú, Mi trưởng lão liền có khả năng gặp được nguy hiểm."),t("br"),t("br"),n._v("“Cái này địa hình, rất thích hợp cấp sáu Chi Dương huyễn trận! Liền bố trí cái này...”"),t("br"),t("br"),n._v("Hít sâu một hơi, quan sát một cái chung quanh địa hình, tính toán một hồi, một cái trận pháp xuất hiện tại trong óc."),t("br"),t("br"),n._v("Mặc dù không có khảo hạch trận pháp sư đẳng cấp, nhưng đối với trận pháp lý giải, sớm đã đạt đến lục tinh đỉnh phong nhất, căn cứ địa hình suy tính trận pháp, vẫn là rất đơn giản liền có thể hoàn thành."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một đống trận kỳ xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Thân thể nhảy lên, Trương Huyền đi tới tất cả linh thú phi hành bầu trời."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Tất cả linh thú thấy có người tới, tất cả đều phát ra hoảng sợ, đang định bay lên công kích, hai đầu Hắc Linh báo liền chui ra, rống lên một tiếng vang lên: “Vị này là tới cùng Vương Giả đàm phán Trương sư, muốn nhìn một chút chúng ta linh thú phi hành trận doanh, thuận tiện bố trí cái trận pháp, chư vị không nên kinh hoảng, an tâm đợi là được!”"),t("br"),t("br"),n._v("Nói là thú ngữ, tất cả linh thú đều có thể nghe hiểu."),t("br"),t("br"),n._v("Thấy Vương Giả trước mặt người hầu mở miệng, tất cả linh thú lại không có ý kiến, tất cả đều lần nữa nằm sấp xuống dưới."),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Trương Huyền chân khí trong cơ thể rất nhanh vận chuyển, trong tay trận kỳ lơ lửng mà lên, thẳng tắp hướng bốn phía kích bắn đi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Chi Dương huyễn trận thành hình, một đoàn mê vụ lập tức đem tất cả linh thú phi hành bao vây ở bên trong."),t("br"),t("br"),n._v("Trước thời hạn biết hắn muốn bày trận, tất cả linh thú cũng không có kinh hoảng, toàn bộ đều an tĩnh bò tại nguyên chỗ bất động."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, phân thân xuất hiện."),t("br"),t("br"),n._v("Hai người thẳng tắp vọt xuống dưới, đi vào mê vụ."),t("br"),t("br"),n._v("Rống! Rống! Rống!"),t("br"),t("br"),n._v("Phía dưới lập tức vang lên linh thú, liên tiếp rống lên một tiếng."),t("br"),t("br"),n._v("Những linh thú này, sức mạnh mạnh nhất cũng chỉ Tằm Phong cảnh đỉnh phong, căn bản không phải Trương Huyền cùng phân thân đối thủ, liền Tử Dương thú cùng Kim Nguyên đỉnh đều không cần triệu hoán."),t("br"),t("br"),n._v("Mỗi tiến lên mấy bước, liền có một đầu linh thú thuần phục."),t("br"),t("br"),n._v("Hắn bên này thuần thú, trong sơn động, Mi trưởng lão đầu đầy mồ hôi."),t("br"),t("br"),n._v("Theo Trương Huyền rời mở đến bây giờ, đã một canh giờ, đối diện tất cả Thánh thú đã xuất hiện hoài nghi."),t("br"),t("br"),n._v("“Làm sao vẫn chưa trở lại? Ngươi người học sinh này đến cùng đang làm gì?”"),t("br"),t("br"),n._v("Ở giữa nhất Hoàng Kim Hùng Hổ thú, vẻ mặt trầm thấp."),t("br"),t("br"),n._v("Để cái này Mi trưởng lão học sinh, nhìn cái linh thú phi hành quân đoàn, dựa theo tình huống bình thường, hơn mười phút liền có thể đánh cái qua lại, đã lâu như vậy, không thấy tung tích, ngu nữa đều sẽ cảm giác đến có vấn đề."),t("br"),t("br"),n._v("“Ta người học sinh này chỉ có Tằm Phong cảnh, có gì có thể lo lắng?”"),t("br"),t("br"),n._v("Trong lòng lo lắng, trên mặt lại không biểu hiện ra ngoài, Mi trưởng lão nhẹ nhàng cười một tiếng: “Người học sinh này có chút cẩn thận, đoán chừng là tử quan sát kỹ tất cả linh thú sức mạnh và số lượng, chúng ta đều ở nơi này, các ngươi chẳng lẽ còn lo lắng sẽ chạy hay sao?”"),t("br"),t("br"),n._v("“Chạy? Ngươi cái này trưởng lão tại đây, hắn một cái học sinh coi như chạy cũng lật không nổi sóng lớn...”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú lạnh hừ một tiếng: “Xích Viêm sư, ngươi đi qua nhìn một chút, cái tên này đến cùng làm cái quỷ gì? Nhân loại từ trước tới nay gian xảo, tuyệt đối không nên trúng quỷ kế!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Xích Viêm sư liền đi ra ngoài."),t("br"),t("br"),n._v("“Chậm rãi...”"),t("br"),t("br"),n._v("Mi trưởng lão vội vàng đứng lên: “Một cái học sinh mà thôi, làm sao, đường đường Vân Vụ lĩnh Vương Giả, liền nhát gan như vậy?”"),t("br"),t("br"),n._v("“Nhát gan? Không cần phải để ý đến hắn, Xích Viêm sư, đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Mặc kệ hắn kéo dài, Hoàng Kim Hùng Hổ thú to lớn móng vuốt lắc lắc."),t("br"),t("br"),n._v("Thấy Xích Viêm sư lập tức muốn đi ra đi, Mi trưởng lão nhẹ nhàng cười một tiếng, đứng dậy: “Sớm nghe nói Hoàng Kim Hùng Hổ Vương Giả thực lực mạnh mẽ, có dám so với ta đấu một trận? Cũng cho ta lĩnh hội một cái, Thú tộc thực lực cường đại!”"),t("br"),t("br"),n._v("“So với ta đấu?” Không nghĩ tới hắn sẽ đưa ra loại yêu cầu này, Hoàng Kim Hùng Hổ thú nhướng mày."),t("br"),t("br"),n._v("“Thế nào, không dám?” Mi trưởng lão nói."),t("br"),t("br"),n._v("“Có gì không dám!” Lạnh hừ một tiếng, Hoàng Kim Hùng Hổ thú đứng dậy."),t("br"),t("br"),n._v("“Vậy là tốt rồi, vị này Xích Viêm Vương người, không bằng lưu lại quan chiến, ta vượt qua Hoàng Kim Hùng Hổ Vương Giả, không ngại đưa ngươi cũng khiêu chiến một cái!”"),t("br"),t("br"),n._v("Mi trưởng lão mặt mũi ngạo khí."),t("br"),t("br"),n._v("Cùng đối phương khiêu chiến, tự nhiên là vì kéo dài thời gian, mặc dù không biết viện trưởng mục đích là cái gì, nhưng đã an bài nhiệm vụ, liền không thể cản trở."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Xích Viêm sư nhẹ gật đầu."),t("br"),t("br"),n._v("Một người một thú, đều là Hồng Viễn Đế quốc cảnh nội đỉnh phong nhất tồn tại, cả hai giao chiến, tự nhiên so với trước tìm một cái Tằm Phong cảnh tiểu gia hỏa, còn có ý nghĩa nhiều."),t("br"),t("br"),n._v("“Vậy thì tới đi!”"),t("br"),t("br"),n._v("Sợ Hoàng Kim Hùng Hổ thú kịp phản ứng, bản thân kéo dài thời gian phương pháp mất đi hiệu lực, hét dài một tiếng, Mi trưởng lão thân thể nhoáng một cái, đi đầu hướng về phía trước Thánh thú đánh ra."),t("br"),t("br"),n._v("Thánh vực nhất trọng cường giả đỉnh phong sức mạnh toàn bộ bộc phát, cả sơn động đều bị chân khí tràn ngập, bàn tay nhẹ nhàng đè ép, mặt đất nham thạch cũng có chút không chịu nổi, tựa như lúc nào cũng sẽ vỡ ra."),t("br"),t("br"),n._v("“Ha ha, ngươi liền chút thực lực ấy lời nói, hổ thẹn thập đại trường lão xưng hào ah...”"),t("br"),t("br"),n._v("Cảm nhận được lăng không mà đến áp lực, Hoàng Kim Hùng Hổ thú chẳng những không có e ngại, ngược lại một tiếng gào thét, to lớn móng vuốt nâng lên, bổ về đằng trước."),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Không khí tựa như bị xé nứt, gió lốc hiu hiu, phát ra nghẹn ngào thanh âm, Mi trưởng lão công kích trong nháy mắt liền bị hóa giải."),t("br"),t("br"),n._v("Sau một khắc, Hoàng Kim Hùng Hổ thú đuôi lăng không quét tới, dường như roi thép, một khi bị đánh trúng, coi như Thánh vực nhất trọng đỉnh phong thực lực mạnh mẽ, khẳng định cũng muốn trọng thương!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Không dám mạnh mẽ chống đỡ, Mi trưởng lão thân thể nhoáng một cái, tránh thoát khỏi đi."),t("br"),t("br"),n._v("Sưu!"),t("br"),t("br"),n._v("Đúng lúc này, Hoàng Kim Hùng Hổ thú lại một cái móng vuốt quất tới, Mi trưởng lão người trên không trung, không cách nào tránh né, đành phải ra quyền đón đỡ."),t("br"),t("br"),n._v("Quyền, trảo đối đầu, chỉ cảm thấy ngực một buồn bực, bay ngược mà ra, sống lưng nặng nề đụng ở trên vách tường, đập ra một cái hố to."),t("br"),t("br"),n._v("Linh thú, Thánh thú bởi vì tố chất thân thể cùng lực lượng, đều so cùng cấp bậc nhân loại phải cường đại một tia, vị này càng là Vân Vụ lĩnh lợi hại nhất Thánh thú, Mi trưởng lão mặc dù sức mạnh cũng không yếu, muốn mạnh mẽ chống đỡ, cũng có chút làm không được."),t("br"),t("br"),n._v("“Ha ha, đường đường thập đại trường lão, sức mạnh cũng không gì hơn cái này!”"),t("br"),t("br"),n._v("Một chiêu đắc thủ, Hoàng Kim Hùng Hổ thú một tiếng long ngâm hổ gầm, chấn người lỗ tai có chút choáng váng, thân thể khổng lồ, hướng về phía trước chui ra."),t("br"),t("br"),n._v("Rồng theo mây, hổ theo gió."),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú mặc dù không phải lão hổ, nhưng cũng là hổ loại Thánh thú một loại, còn chưa tới từng tới trước mặt, liền giống như một cơn lốc tập qua, Mi trưởng lão kìm lòng không được lui về phía sau, cổ tay khẽ đảo, một thanh trường kiếm xuất hiện tại lòng bàn tay, bỗng nhiên đâm tới."),t("br"),t("br"),n._v("Chỉ bằng vào man lực, khẳng định chống chọi không được đối phương, chỉ có thể sử dụng vũ khí."),t("br"),t("br"),n._v("Sưu sưu sưu!"),t("br"),t("br"),n._v("Trường kiếm kéo ra kiếm hoa, hóa thành từng đạo kiếm mang thẳng tắp hướng về phía trước đâm tới."),t("br"),t("br"),n._v("Dường như biết chiêu số của hắn, Hoàng Kim Hùng Hổ thú cũng không tránh né, hít sâu một hơi, toàn thân da lông lập tức trở nên ngăm đen, tựa như tạo thành đặc thù lân phiến."),t("br"),t("br"),n._v("Đinh đinh đinh!"),t("br"),t("br"),n._v("Kiếm mang rơi vào da lông bên trên, lại bị chặn ở bên ngoài, rốt cuộc không đâm vào được."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Mi trưởng lão sầm mặt lại."),t("br"),t("br"),n._v("Sớm biết cái tên này mạnh mẽ, thật không nghĩ đến cường đại như thế!"),t("br"),t("br"),n._v("Hắn một kiếm, thế mà liền da lông đều không có đâm thủng, cái này phòng ngự có phần cũng quá lợi hại đi!"),t("br"),t("br"),n._v("Khó trách Vân Vụ lĩnh mười Đại Thánh thú, đều đạt đến Thánh vực nhất trọng đỉnh phong, lại đều nghe nó, chỉ loại này phòng ngự, liền có thể để vô số Thánh thú, thậm chí người tu luyện, đều chùn bước."),t("br"),t("br"),n._v("“Hồng Diệp Thập Tam kiếm!”"),t("br"),t("br"),n._v("Biết hơi không cẩn thận liền sẽ bị thương, không dám dừng lại ngừng, một kiếm bị chặn lại, Mi trưởng lão hai hàng lông mày vung lên, cổ tay khẽ đảo, chân khí trong cơ thể lập tức dựa theo đặc thù quỹ tích vận chuyển."),t("br"),t("br"),n._v("Trường kiếm phát ra sắc nhọn vang lên, giống như là bất cứ lúc nào cũng sẽ phá không mà đi."),t("br"),t("br"),n._v("Hồng Diệp Thập Tam kiếm, hắn mạnh nhất võ kỹ tuyệt chiêu!"),t("br"),t("br"),n._v("Lúc trước đầu kia hỏa long Thánh thú, chính là thua ở chiêu này phía dưới, cam tâm tình nguyện thành thú sủng."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Kiếm mang trên không trung hình thành một mảnh lá đỏ, gào thét lên bay tới đằng trước, mang theo xé nát hết thảy lực uy hiếp."),t("br"),t("br"),n._v("“Có chút ý tứ! Bất quá, loại trình độ này liền muốn thắng ta, vẫn là nằm mơ!”"),t("br"),t("br"),n._v("Nhìn thấy hắn thi triển ra như vậy một kiếm, Hoàng Kim Hùng Hổ thú cũng lộ ra vẻ kinh ngạc, lập tức nhẹ nhàng cười một tiếng, móng trước xoay một cái, lộ ra sắc bén ngăm đen móng vuốt, lần nữa bổ xuống."),t("br"),t("br"),n._v("Đinh đinh đinh!"),t("br"),t("br"),n._v("Móng vuốt cùng lá đỏ đối đầu, Hoàng Kim Hùng Hổ thú lui về sau một bước, Mi trưởng lão thì máu tươi phun ra, lần nữa bay ngược mà ra, rơi trên mặt đất, vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Lần này đã dùng hết toàn bộ lực lượng, đã bị trọng thương."),t("br"),t("br"),n._v("Vội vàng xoay người ngồi dậy, nuốt một cái chữa thương đan dược, mới phát giác được thoải mái một chút."),t("br"),t("br"),n._v("“Thế nào? Thực lực của ta, nhưng cũng không tệ lắm?”"),t("br"),t("br"),n._v("Thấy Mi trưởng lão thua, Hoàng Kim Hùng Hổ thú cười ha ha một tiếng, mặc dù cũng bị thương nhẹ, nhưng cùng đối phương so, chênh lệch hơn nhiều."),t("br"),t("br"),n._v("Nói cách khác, tỷ thí lần này, thứ hai căn bản không phải đối thủ."),t("br"),t("br"),n._v("“Lợi hại!” Đứng dậy, Mi trưởng lão muốn không thừa nhận đều không được."),t("br"),t("br"),n._v("Quả thực không phải là đối thủ."),t("br"),t("br"),n._v("Cùng cấp bậc người cùng thú, vẫn là có chênh lệch nhất định."),t("br"),t("br"),n._v("Nếu không, Tử Dương thú cũng sẽ không mệnh danh danh sư học viện người mạnh nhất."),t("br"),t("br"),n._v("“Vậy thì tốt, so cũng so xong, Xích Viêm sư, mau mau đi qua đi, nhìn xem tiểu tử kia đến cùng làm cái quỷ gì, đến bây giờ cũng chưa trở lại, nếu như dám làm ra đối Vân Vụ lĩnh chuyện bất lợi, có thể làm trận giết chết!”"),t("br"),t("br"),n._v("Một lần nữa trở lại tại chỗ ngồi xuống, Hoàng Kim Hùng Hổ thú lần nữa phân phó."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Xích Viêm sư nhẹ gật đầu, xoay người đi ra ngoài."),t("br"),t("br"),n._v("“Cái này... Không vội...”"),t("br"),t("br"),n._v("Vẻ mặt lần nữa biến đổi, Mi trưởng lão đang muốn đi qua ngăn cản, chỉ thấy Hoàng Kim Hùng Hổ thú lạnh lùng nhìn qua: “Thế nào, Mi trưởng lão còn muốn so với ta một trận?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thân thể run lên."),t("br"),t("br"),n._v("Hắn tình huống hiện tại, khẳng định không thể lại so, thật muốn động thủ, tất nhiên sẽ chết ở chỗ này."),t("br"),t("br"),n._v("Mặc dù không biết viện trưởng đang làm cái gì để hắn kéo dài thời gian, nhưng... Hiện tại đã kéo dài không được, làm sao bây giờ?"),t("br"),t("br"),n._v("“Còn không mau đi!”"),t("br"),t("br"),n._v("Cũng nhìn ra hắn kéo dài mục đích, Hoàng Kim Hùng Hổ thú móng vuốt vẫy một cái."),t("br"),t("br"),n._v("“Vâng!” Xích Viêm sư cũng không dừng lại, thân thể nhoáng một cái, thời gian nháy mắt biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("“Viện trưởng...”"),t("br"),t("br"),n._v("Thấy nó biến mất, biết rốt cuộc đuổi không kịp, Mi trưởng lão sắc mặt trắng nhợt, thân thể dường như một bãi bùn nhão: “Ta tận lực...”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-888"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-890"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);