(window.webpackJsonp=window.webpackJsonp||[]).push([[2177],{2532:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-882-hien-te"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-882-hien-te"}},[n._v("#")]),n._v(" Chương 882: Hiến tế")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 882: Hiến tế"),t("br"),t("br"),n._v("Vân Vụ lĩnh mười Đại Thánh thú, mỗi một đầu đều có có thể so với danh sư học viện thập đại trường lão sức chiến đấu!"),t("br"),t("br"),n._v("Mi trưởng lão một cái học sinh, tìm người nào, có thể để cho chín cái Thánh thú cùng đi ra?"),t("br"),t("br"),n._v("Chẳng lẽ đem các trưởng lão khác đều tìm tới?"),t("br"),t("br"),n._v("Không đúng, nếu như là như vậy, bên ngoài nhiều như vậy Bán Thánh, Tòng Thánh, khẳng định đã sớm bẩm báo, không đến mức tin tức gì đều không có!"),t("br"),t("br"),n._v("“Mi Trúc, ngươi đến cùng đang giở trò quỷ gì?”"),t("br"),t("br"),n._v("Tròng mắt hơi híp, Hoàng Kim Hùng Hổ thú nhìn lại."),t("br"),t("br"),n._v("“Ta? Ta một mực tại nơi này, cũng không biết...”"),t("br"),t("br"),n._v("Mi trưởng lão nhanh muốn khóc."),t("br"),t("br"),n._v("Hắn chính là cái đánh xì dầu, phối hợp diễn kịch, viện trưởng muốn làm gì, là thật không rõ ràng..."),t("br"),t("br"),n._v("“Không biết? Tốt nhất đừng cùng ta giở trò gian, ta Vân Vụ lĩnh có thể tại danh sư học viện dưới mí mắt nghị lực nhiều năm như vậy đều không ngã, tuyệt không phải mặt ngoài đơn giản như vậy...”"),t("br"),t("br"),n._v("Thấy đối phương biểu lộ quả thực không biết, Hoàng Kim Hùng Hổ thú lần nữa uy hiếp một tiếng."),t("br"),t("br"),n._v("“Các ngươi linh thú trải rộng toàn bộ Vân Vụ lĩnh, nếu quả thật có thủ đoạn gì, làm sao có thể không biết?”"),t("br"),t("br"),n._v("Mi trưởng lão nói."),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú nhẹ gật đầu."),t("br"),t("br"),n._v("Điểm ấy cũng chính là nó nghi ngờ."),t("br"),t("br"),n._v("Thật muốn có vấn đề, những vương giả kia khẳng định cùng nó nói, để cái khác thú ra ngoài, nói rõ có năng lực giải quyết."),t("br"),t("br"),n._v("“Trước không cần phải để ý đến, chỉ cần đừng để vị này Mi trưởng lão rời đi, danh sư học viện liền khẳng định sẽ sợ ném chuột vỡ bình, coi như không đáp ứng điều kiện, cũng sẽ nghĩ ra biện pháp tốt giải quyết!”"),t("br"),t("br"),n._v("Trong lòng đã tính toán một chút, cảm thấy không có vấn đề gì, Hoàng Kim Hùng Hổ thú đang muốn nói chuyện, chỉ thấy cái thứ nhất đi ra Xích Viêm sư đi đến."),t("br"),t("br"),n._v("Ngay sau đó Thiết Bối Hùng, Hổ Đầu thú, Thiết Xỉ Lang... Cái khác chín đại vương giả cũng đi đến, ở giữa theo một thanh niên, đầu buông xuống, thoạt nhìn mười phần chật vật."),t("br"),t("br"),n._v("Chính là Mi trưởng lão vị kia chạy trốn học sinh."),t("br"),t("br"),n._v("“Lão đại, cái tên này bị chúng ta bắt được...”"),t("br"),t("br"),n._v("Mấy bước đi tới trước mặt, Xích Viêm sư thô to thanh âm khẽ nói."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra? Để các ngươi tất cả đều ra ngoài?”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Là như vậy...”"),t("br"),t("br"),n._v("Hổ Đầu thú đi tới trước mặt, thô to thiết trảo chỉ tay Trương Huyền: “Cái tên này không biết dùng thủ đoạn gì, tìm tới mấy cái lợi hại danh sư, cùng chúng ta ở bên ngoài chiến đấu...”"),t("br"),t("br"),n._v("“Danh sư?” Hoàng Kim Hùng Hổ thú chớp mắt."),t("br"),t("br"),n._v("“Không sai!” Thiết Xỉ Lang cũng đi tới."),t("br"),t("br"),n._v("“Cái gì danh sư? Nhưng mà học viện thập đại trường lão?” Hoàng Kim Hùng Hổ thú tiếp tục hỏi."),t("br"),t("br"),n._v("“Hình như không phải, chúng ta cũng không nhận ra, nhìn dung mạo mười phần lạ lẫm, như vậy đi, ta đem thi thể của bọn hắn lấy ra, cho ngươi xem một chút...”"),t("br"),t("br"),n._v("Thiết Bối Hùng hừ một tiếng, đi tới đối phương không đủ ba mét chỗ."),t("br"),t("br"),n._v("Nói còn chưa dứt lời, vài đầu Thánh thú đồng thời gào thét, đồng loạt ra tay."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Xích Viêm sư, Hổ Đầu thú, Thiết Xỉ Lang, Thiết Bối Hùng, bốn Đại Thánh thú tại Vân Vụ lĩnh Vương Giả bên trong, đều tính toán là mạnh, đồng thời ra tay, lực lượng dường như núi lở đất nứt, giang hà chảy ngược."),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú còn không có kịp phản ứng, liền bị bốn cái móng vuốt đồng thời đánh trúng."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("“Các ngươi chơi cái gì...”"),t("br"),t("br"),n._v("Một tiếng hô, Hùng Hổ thú bay ngược mà ra, sống lưng đụng gãy tận mấy cái thạch nhũ, máu tươi từng ngụm từng ngụm phun ra."),t("br"),t("br"),n._v("Đám thánh thú này đi cùng với nó vượt qua một ngàn năm, tin tưởng lẫn nhau, ai có thể nghĩ tới đột nhiên ra tay."),t("br"),t("br"),n._v("Hơn nữa tàn nhẫn như vậy!"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú một cái liền bản thân bị trọng thương, ngũ tạng lục phủ bị thời gian ngắn khó khôi phục trọng thương."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra...”"),t("br"),t("br"),n._v("Quẳng xuống đất, miệng lớn thở hổn hển, Hoàng Kim Hùng Hổ thú cảm giác toàn thân sức mạnh lần này đánh mất tám chín phần mười, hai mắt đỏ thẫm, nhịn không được gào thét."),t("br"),t("br"),n._v("“Chủ nhân!”"),t("br"),t("br"),n._v("Không để ý tới nó tiếng rống, Xích Viêm sư, Thiết Bối Hùng mấy người thú, đồng loạt xoay người, nhìn về phía cách đó không xa Trương Huyền."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền tại không có trước đó chật vật, hai tay chắp sau lưng, tại phần đông Thánh thú chen chúc xuống đi ra, tựa như một cái cao cao tại thượng Đế Vương."),t("br"),t("br"),n._v("“Vân Vụ lĩnh mười đại vương giả, hiện tại đã chín cái hướng ta thần phục... Hoàng Kim Hùng Hổ thú, ngươi chỉ có một con đường có thể đi, hoặc là, trở thành ta thú sủng, hoặc là... Chết!”"),t("br"),t("br"),n._v("“Thần phục?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Hùng Hổ thú khó mà tin được nhìn về phía chín đại vương giả."),t("br"),t("br"),n._v("Bọn chúng đều là Thánh thú, có thuộc tại sự kiêu ngạo của chính mình, làm sao sẽ thần phục một cái nhân loại, hơn nữa... Vẫn là chỉ có Tằm Phong cảnh tiểu nhân vật?"),t("br"),t("br"),n._v("Không chỉ nó kỳ quái, Mi trưởng lão cũng điên rồi."),t("br"),t("br"),n._v("Còn tưởng rằng viện trưởng thật tới đàm phán, đều làm xong bị người nhục mạ quyết định, không nghĩ tới ra ngoài hơn một canh giờ, đem mười đại vương giả thuần phục chín cái..."),t("br"),t("br"),n._v("Xem ra, phía ngoài linh thú khẳng định cũng khó có thể may mắn thoát khỏi, đều thành thú sủng..."),t("br"),t("br"),n._v("Sớm biết vị này mới viện trưởng thuần thú năng lực rất cường đại, nhưng... Cái này cũng không tránh khỏi quá cường đại đi, quá nghịch thiên đi!"),t("br"),t("br"),n._v("Như vậy thuần phục đi xuống, có thể hay không toàn bộ đại lục ở bên trên Thánh thú, linh thú đều thành một mình ngươi thú sủng?"),t("br"),t("br"),n._v("Nghe theo của ngươi phân phó?"),t("br"),t("br"),n._v("Hắn bên này ngớ ra, Xích Viêm sư mấy người cũng nhìn về phía trước đó lão đại, Vân Vụ lĩnh mạnh nhất Vương Giả."),t("br"),t("br"),n._v("“Thần phục đi, chủ nhân là có đại người có bản lĩnh, thần phục cùng hắn, tuyệt đối so với tại Vân Vụ lĩnh ra không được mạnh!”"),t("br"),n._v("“Đúng vậy a, chúng ta vây ở Vân Vụ lĩnh đều đã nhiều năm như vậy, mặc dù nơi này thành chúng ta lãnh địa, không ai dám đi vào, danh sư học viện đều kiêng kị ba phần, thoạt nhìn rất uy phong, nhưng... Cùng nhốt ở trong lồng khác nhau ở chỗ nào? Dù sao ta là chịu đủ!”"),t("br"),t("br"),n._v("“Không thần phục liền sẽ chết, chớ do dự...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thành Trương Huyền thú sủng, lại không còn trước đó Vương Giả tôn nghiêm, mấy Đại Thánh thú đồng loạt an ủi."),t("br"),t("br"),n._v("“Để cho ta thần phục? Nằm mơ!”"),t("br"),t("br"),n._v("Thấy tất cả thuộc hạ đều đã phản bội, Hoàng Kim Hùng Hổ thú mặt mũi dữ tợn: “Các ngươi không phải thẳng nghi ngờ, vì cái gì nơi này có thể hội tụ nhiều như vậy địa mạch linh dịch, để cho chúng ta nhiều như vậy linh thú, Thánh thú sử dụng mà không suy kiệt ư? Hôm nay ta sẽ nói cho các ngươi biết...”"),t("br"),t("br"),n._v("Gào thét một tiếng, Hoàng Kim Hùng Hổ thú giãy dụa lấy đứng dậy, thân thể nhoáng một cái, nhảy vào cách đó không xa đã làm xẹp bể tắm, thanh âm chậm rãi ra."),t("br"),t("br"),n._v("“Tôn kính Hoàng, xin ban cho ta lực lượng, diệt trừ những này Thú tộc kẻ phản bội...”"),t("br"),t("br"),n._v("Lần này nói không phải nhân loại ngôn ngữ, mà là Thượng Cổ thú ngữ."),t("br"),t("br"),n._v("“Hoàng?”"),t("br"),t("br"),n._v("Nghe được thú ngữ, Trương Huyền lông mày nhíu một cái, một cái dự cảm bất tường theo trong lòng xông ra."),t("br"),t("br"),n._v("Trước đó hắn liền kỳ quái, địa mạch linh dịch như thế hiếm có đồ vật, coi như Vân Vụ lĩnh địa hình đặc thù, muốn ngưng tụ nhiều như vậy, cũng rất không có khả năng, hiện tại xem ra, chỉ sợ không có đơn giản như vậy."),t("br"),t("br"),n._v("Hơn nữa, mấu chốt nhất là, nơi này linh thú, Thánh thú thực sự nhiều lắm."),t("br"),t("br"),n._v("Vật cạnh thiên trạch, bất kỳ chỗ nào, cường giả số lượng đều là có hạn."),t("br"),t("br"),n._v("Nhiều như vậy linh thú, đều có thể có cao như vậy cấp bậc, muốn nói không khiến người ta kỳ quái, cái kia là giả."),t("br"),t("br"),n._v("Mi trưởng lão cũng biến sắc."),t("br"),t("br"),n._v("Hắn là lục tinh đỉnh phong thuần thú sư, mặc dù đối Thượng Cổ thú ngữ, không hoàn toàn biết, nhưng cũng có thể nghe hiểu một chút, lúc này đối phương rõ ràng lại dùng thú ngữ tiến hành một loại nào đó nghi thức, tiến hành một loại đặc thù triệu hoán."),t("br"),t("br"),n._v("Một khi thành công, tất nhiên sẽ xuất hiện biến cố lớn."),t("br"),t("br"),n._v("“Viện trưởng...”"),t("br"),t("br"),n._v("Tràn đầy lo lắng, vội vàng hướng Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Đừng có gấp, khả năng này liên quan trước ngươi nói Ngô Dương Tử tiền bối mất tích bí mật...”"),t("br"),t("br"),n._v("Trương Huyền lông mày vung lên."),t("br"),t("br"),n._v("Thuần phục Xích Viêm sư mấy người thú, tự nhiên cũng hỏi thăm một ít chuyện, những cái thứ này đều chỉ có thể coi là được nhân tài mới nổi, gần một ngàn năm mới trưởng thành, trước đó chư nhiều chuyện, cũng không rõ ràng."),t("br"),t("br"),n._v("Căn cứ sự miêu tả của bọn nó, tất cả bí mật đều khống chế tại đây đầu Hoàng Kim Hùng Hổ thú trên người."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, hắn mới nắm giữ Vân Vụ lĩnh không thể khiêu chiến quyền uy, là danh xứng với thực người thứ nhất."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trong lúc nói chuyện với nhau, linh khí nồng nặc tự hang núi chỗ sâu lăn mình mà đến, tựa như trời sụp đổ, đè lên mọi người không thở nổi."),t("br"),t("br"),n._v("Nguyên bản chứa đựng linh dịch bể tắm, giờ phút này như cùng một cái to lớn tế đàn, Hoàng Kim Hùng Hổ thú tựa hồ tại hiến tế lấy cái gì, thu hút cái này cỗ cường đại uy áp hạ xuống."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Cảm nhận được cỗ lực lượng này, Mi trưởng lão vẻ mặt trắng bệch, cả cái người kìm lòng không được lui về phía sau mấy bước, cắn răng gắng gượng."),t("br"),t("br"),n._v("Thực sự quá cường đại, đâm thẳng tâm linh, coi như hắn là Thánh vực nhất trọng đỉnh phong, đều có chút chống chọi không được."),t("br"),t("br"),n._v("“Viện trưởng...”"),t("br"),t("br"),n._v("Hắn loại thực lực này đều không thể chống cự, viện trưởng làm sao bây giờ?"),t("br"),t("br"),n._v("Vội vàng nhìn về phía trước, chỉ thấy cách đó không xa viện trưởng, lưng eo thẳng tắp, hai hàng lông mày vung lên, lạnh lùng nhìn xem ngay phía trước, hình như không có cảm nhận được áp lực đồng dạng."),t("br"),t("br"),n._v("Mi trưởng lão toàn thân chấn động."),t("br"),t("br"),n._v("Hắn đều chống chọi không được, Xích Viêm sư loại hình Vân Vụ lĩnh Vương Giả, càng là nằm một chỗ, viện trưởng nhưng một chút việc đều không có, cái này sao có thể?"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Khiếp sợ trong lòng còn không có kết thúc, không trung linh khí, giống như là ngưng tụ thành thực chất, một cái hư ảo uyển như sương khói ngưng tụ Thanh Giác Long thú, lơ lửng ở phía trên, cho người ta một loại nghiền nát linh hồn ảo giác."),t("br"),t("br"),n._v("“Ta hậu nhân, kêu gọi ta làm cái gì?”"),t("br"),t("br"),n._v("Hùng vĩ tiếng âm vang lên, nói vẫn như cũ là Thượng Cổ thú ngữ, nhưng cho người ta chủng xuyên việt thời không cảm giác."),t("br"),t("br"),n._v("“Đây là... Thuần túy Thượng Cổ thú ngữ!”"),t("br"),t("br"),n._v("Mi trưởng lão lạnh cả người."),t("br"),t("br"),n._v("Hậu nhân bắt chước thú ngữ, mặc dù phát âm bên trên cũng rất tiếp cận, nhưng không có loại kia xuyên thấu lịch sử cảm giác tang thương, thiếu một chủng Viễn Cổ mà đến chìm xuống đáy."),t("br"),t("br"),n._v("Bên tai nghe được thanh âm này, nặng nề, trầm ổn, chỉ bằng vào thanh âm ngữ điệu, liền có thể để vô số linh thú, Thánh thú thần phục, nghe xong liền biết, cái này hư ảnh, tuyệt đối là theo Thượng Cổ sống sót nhân vật tuyệt thế."),t("br"),t("br"),n._v("Quay đầu nhìn lại, quả nhiên thấy Xích Viêm sư, Hổ Đầu thú mấy người Vương Giả, nằm rạp trên mặt đất run lẩy bẩy, liền một chút lực lượng phản kháng cũng không có."),t("br"),t("br"),n._v("Thánh thú huyết mạch áp chế, ai cũng không có cách nào."),t("br"),t("br"),n._v("Lần nữa hướng viện trưởng nhìn lại, ý định thay đối phương ngăn cản, tốt làm cho đối phương rời đi, lại thấy hắn vẫn đứng tại chỗ, nhìn trước mắt hư ảnh, mày nhăn lại, không biết nghĩ cái gì."),t("br"),t("br"),n._v("“Hoàng, hậu nhân tại này thỉnh cầu ngươi ban cho ta lực lượng, chém giết những này phản đồ!”"),t("br"),t("br"),n._v("Quỳ rạp xuống đất, Hoàng Kim Hùng Hổ thú dùng thú ngữ hô."),t("br"),t("br"),n._v("“Ngươi hiến tế cái gì?”"),t("br"),t("br"),n._v("Hư ảnh thản nhiên nói."),t("br"),t("br"),n._v("“Ta hiến tế vị danh sư này học viện lục tinh đỉnh phong danh sư, thuần thú học viện viện trưởng, lấy máu tươi của hắn đối ngươi tiến hành cung cấp nuôi dưỡng...”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú hí dài."),t("br"),t("br"),n._v("“Lục tinh đỉnh phong danh sư?” Hư ảnh lắc đầu: “Chưa đủ!”"),t("br"),t("br"),n._v("“Còn có hắn vị học viên này...”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú đối với Trương Huyền chỉ tay, nói hết lập tức cảm thấy cái tên này khả năng quá yếu một chút: “Chỉ cần Hoàng ban cho ta lực lượng, ta sẽ lập tức phát động đại quân, săn giết càng nhiều danh sư...”"),t("br"),t("br"),n._v("“Thành giao, nếu như vi phạm với lời hứa của ngươi, ngươi hẳn phải biết hậu quả!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nương theo hư ảnh nhàn nhạt lời nói, một cỗ nồng đậm lực lượng, thẳng tắp hướng Hoàng Kim Hùng Hổ thú truyền vào mà xuống, người sau vốn là trọng thương khí tức, lập tức liên tục tăng lên."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-890"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-892"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);