(window.webpackJsonp=window.webpackJsonp||[]).push([[2179],{2534:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-884-truong-huyen-thue-bien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-884-truong-huyen-thue-bien"}},[n._v("#")]),n._v(" Chương 884: Trương Huyền thuế biến")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 884: Trương Huyền thuế biến"),t("br"),t("br"),n._v("“Vu hồn? Hoàng là thần chỉ, là theo Thượng Cổ sống sót Thánh thú, ngươi dám đối Vân Vụ lĩnh bất lợi, đã xúc phạm nó tôn nghiêm, khẳng định sẽ đem ngươi chém thành muôn mảnh, linh hồn rút đi, báo thù cho ta...”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú hét."),t("br"),t("br"),n._v("Xem ra nó đối cái này cái gọi là Hoàng, mười phần trung thành, thậm chí mang theo nồng đậm tôn sùng."),t("br"),t("br"),n._v("Căn bản không tin tưởng đối phương là Vu hồn, mà đem hắn định nghĩa thành Thần Linh."),t("br"),t("br"),n._v("Trương Huyền lắc đầu, một cái tàn hồn mà thôi, coi như lợi hại hơn nữa, lại có bao nhiêu mạnh?"),t("br"),t("br"),n._v("Thật có đáng sợ như thế, cũng không trở thành thẳng canh giữ ở linh vụ núi, không xuống được."),t("br"),t("br"),n._v("“Nếu như cái này Hoàng, thật có như lời ngươi nói mạnh mẽ, nhìn thấy ngươi bây giờ cái dạng này, khẳng định đã ra tới a?” Trương Huyền nói."),t("br"),t("br"),n._v("“Hoàng là sẽ không bỏ rơi ta, các ngươi đối với ta như vậy, nó nhất định sẽ giết ngươi...” Hoàng Kim Hùng Hổ thú hét."),t("br"),t("br"),n._v("Mặc dù ngoài miệng nói như vậy, nhưng mà trong mắt vẫn là lộ ra khủng hoảng màu."),t("br"),t("br"),n._v("Nó cùng cái này Hoàng, nhưng mà lợi ích quan hệ, nó hiến tế vật phẩm, đối phương ban cho lực lượng, hoặc là địa mạch linh dịch."),t("br"),t("br"),n._v("Coi như thật bị đánh chết, lại chọn một người phát ngôn thôi, không có khả năng xuất thủ cứu giúp, bằng không thì cũng không có khả năng trơ mắt, tùy ý đối phương chiếc kia đại đỉnh, đem hắn đánh thành cái dạng này."),t("br"),t("br"),n._v("Nhìn ra nó khủng hoảng, Trương Huyền biết mình ly gián có tác dụng, tinh thần khẽ động, sư giả chi tâm nhúc nhích, sư ngôn thiên bẩm vang lên: “Vừa rồi nghe nói ngươi muốn đem ta cùng Mi trưởng lão hiến tế, chẳng lẽ những năm gần đây, ngươi cũng là dùng danh sư sinh mệnh đang tiến hành tế tự?”"),t("br"),t("br"),n._v("Cái tên này mới vừa nói, muốn dùng Mi trưởng lão cùng mình hiến tế, còn cộng thêm tất cả danh sư, đối phương lập tức đáp ứng, xem ra loại này giao dịch đã thành ăn ý, tiến hành đã lâu rồi."),t("br"),t("br"),n._v("Kết hợp với trước đó tới đây, nhìn thấy rất nhiều thi cốt, không khó đoán ra cái gọi là tế tự, là dùng danh sư tính mạng tới tiến hành."),t("br"),t("br"),n._v("Loại sự tình này liên quan cực lớn, đối phương tất nhiên sẽ không để cho cái khác Thánh thú biết, chỉ có thể tuân hỏi một chút nhìn xem."),t("br"),t("br"),n._v("Có lẽ liền có thể nhận được muốn biết đáp án."),t("br"),t("br"),n._v("“Ngươi nói bậy...”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú vội vàng lắc đầu, lời nói mới nói phân nửa, lập tức nhìn thấy thanh niên trước mắt, thân thể càng ngày càng thẳng tắp, dường như một tôn vĩ đại Thần Linh, muốn không thừa nhận nhưng cảm thấy làm sao đều nói không ra miệng, tiếng nói xoay một cái, dường như không khống chế nổi miệng, mở miệng nói: “Không sai! Hoàng sẽ thôn phệ danh sư huyết nhục, sau đó ban cho linh dịch, ban cho linh thú thời cơ đột phá!”"),t("br"),t("br"),n._v("Trước đó Trương Huyền sư ngôn thiên bẩm, chỉ đối với nhân loại có tác dụng, linh thú loại hình tác dụng không có lớn như vậy, mà bây giờ lĩnh ngộ sư giả chi tâm, liền xem như người sau, cũng có thể tuỳ tiện bị mê hoặc."),t("br"),t("br"),n._v("“Thôn phệ danh sư huyết nhục?”"),t("br"),t("br"),n._v("Mi trưởng lão nắm đấm xiết chặt, thân thể run rẩy."),t("br"),t("br"),n._v("Vốn cho rằng Vân Vụ lĩnh Thánh thú nhưng mà uy hiếp, không ảnh hưởng đại cục, không nghĩ tới lại là cái ăn người ác ma."),t("br"),t("br"),n._v("Sớm biết như thế, khẳng định sớm đã đem hắn diệt sát."),t("br"),t("br"),n._v("Đối phương lấy danh sư hiến tế, sợ sợ không chỉ duy trì liên tục bao nhiêu năm, không biết bao nhiêu danh sư chết trên tay bọn họ."),t("br"),t("br"),n._v("“Trong sơn động những cái kia bạch cốt, có thể hay không là danh sư di thể?” Trương Huyền cũng vẻ mặt khó coi, hỏi tiếp."),t("br"),t("br"),n._v("“Vâng! Cái kia là bao năm qua tới xông sơn danh sư, đều bị Hoàng thôn phệ huyết nhục, chỉ để lại một đống bạch cốt! Vì báo đáp, nó cũng dùng đại lực lượng cải biến địa hình, tạo thành một cái tự nhiên đại trận, hội tụ địa mạch linh dịch, để Vân Vụ lĩnh linh thú, càng ngày càng mạnh mẽ!”"),t("br"),t("br"),n._v("Hùng Hổ thú nói tiếp."),t("br"),t("br"),n._v("Mặc dù nó không dám trắng trợn săn giết danh sư, nhưng hàng năm thí luyện mất tích, đủ loại nguyên do biến mất không phải số ít, một năm giết chết mười mấy cái, mấy trăm cái, dễ như trở bàn tay."),t("br"),t("br"),n._v("“Cái này Hoàng đã thôn phệ danh sư huyết nhục, tất nhiên không là đơn thuần Vu hồn, nó ở nơi nào?”"),t("br"),t("br"),n._v("Lửa giận trong lòng thiêu đốt, Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("Vu hồn, là linh hồn, muốn sống sót, cần thân thể tẩm bổ, nếu không, rất dễ dàng chịu đến hồn lực ngũ suy ảnh hưởng, càng ngày càng yếu."),t("br"),t("br"),n._v("Lúc trước Mặc Hồn Sinh sở dĩ có thể sống sót vài vạn năm, là bởi vì Cửu Thiên Liên Thai nguyên nhân, không phải cái này thần vật, hắn cũng khẳng định đã sớm biến mất tại dòng sông lịch sử."),t("br"),t("br"),n._v("Có thể sống không biết bao nhiêu năm, còn lấy danh sư huyết nhục làm thức ăn, khẳng định không chỉ có là Vu hồn đơn giản như vậy."),t("br"),t("br"),n._v("“Nó... Ở nơi nào ta cũng không rõ ràng, mỗi lần chỉ có ở đây hiến tế, nó mới có thể xuất hiện...”"),t("br"),t("br"),n._v("Hùng Hổ thú nói."),t("br"),t("br"),n._v("“Ở đây hiến tế?” Trương Huyền cau mày, Minh Lý Chi Nhãn hướng chung quanh nhìn lại."),t("br"),t("br"),n._v("Nơi này trước đó tới qua một lần, cũng chuyên môn nhìn qua một lần, cũng chưa phát hiện cái gì."),t("br"),t("br"),n._v("Cái này “Hoàng” đã xuất hiện ở đây, ở đây tế tự, chẳng lẽ, có chỗ đặc thù gì?"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn chiếu xuống, chung quanh vẫn như cũ không có gì đặc thù, nhưng mà cái bình thường hang núi."),t("br"),t("br"),n._v("“Đúng rồi, vừa rồi tên kia xuất hiện qua, có thể nhìn quỹ tích...”"),t("br"),t("br"),n._v("Đột nhiên nhớ tới một việc, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đối phương là Vu hồn, vô tung vô ảnh, người khác khẳng định điều tra không đến, nhưng hắn có Minh Lý Chi Nhãn, muốn tìm kiếm, hẳn là có thể phát hiện một chút dấu vết để lại."),t("br"),t("br"),n._v("Tinh thần tập trung, trong mắt hoa văn càng ngày càng nhiều, hướng trước đó vị kia “Hoàng” xuất hiện chỗ nhìn sang, quả nhiên thấy được nhàn nhạt quỹ tích hướng trong sơn động lan tràn."),t("br"),t("br"),n._v("“Đỉnh Đỉnh, cái tên này giao cho ngươi... Mặc cho ngươi xử trí!”"),t("br"),t("br"),n._v("Thấy có dấu vết mà lần theo, nên hỏi cũng hỏi xong, Trương Huyền thở phào nhẹ nhõm, nhanh chân hướng trong sơn động đi đến."),t("br"),t("br"),n._v("Đầu này Hoàng Kim Hùng Hổ thú, trợ Trụ vi ngược, săn giết danh sư, đã xúc phạm ranh giới cuối cùng của hắn, tự nhiên không thể lưu lại."),t("br"),t("br"),n._v("“Mặc ta xử trí? Quá tốt rồi... Ta liền ưa thích như vậy!”"),t("br"),t("br"),n._v("Hưng phấn mà hét dài một tiếng, Kim Nguyên đỉnh trực tiếp nhảy lên, thẳng tắp hướng Hùng Hổ thú bay đi."),t("br"),t("br"),n._v("“Ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Nhìn thấy đối phương tràn đầy hưng phấn nhìn chằm chằm bộ dạng, Hoàng Kim Hùng Hổ thú dọa đến toàn thân cứng đờ: “Trương sư, ta nguyện ý nhận ngươi làm chủ nhân, còn xin đừng giết ta, ah...”"),t("br"),n._v("Nói còn chưa dứt lời, liền hô một tiếng, cái này miệng to lớn lô đỉnh, từ trên trời giáng xuống, đối với mặt của nó liền ngồi xuống xuống, phía dưới cuộn trào mãnh liệt lửa than lập tức đem hắn đốt xì xì vang vọng, một cỗ mùi thơm tung bay."),t("br"),t("br"),n._v("“Ừng ực!”"),t("br"),t("br"),n._v("Xích Viêm sư, Thiết Bối Hùng mấy người thú tất cả đều nuốt ngụm nước bọt, nguyên một đám cảm thấy yết hầu phát khô."),t("br"),t("br"),n._v("Mẹ nó."),t("br"),t("br"),n._v("Quá biến thái, may mắn bọn chúng thần phục, nếu không, chỉ sợ khẳng định cùng cái tên này đồng dạng, muốn sống không được, muốn chết không xong..."),t("br"),t("br"),n._v("Nhất là Hùng Hổ thú mặt kia... Trước kia giống như gấu giống như hổ oai phong lẫm liệt, mà bây giờ, cùng con cóc đồng dạng, đều bẹp..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Không để ý tới đánh thú đánh rất vui vẻ Kim Nguyên đỉnh, Mi trưởng lão vội vàng theo sau."),t("br"),t("br"),n._v("Ở đây không dám công khai gọi đối phương là viện trưởng, chỉ có thể lấy “Trương sư” xưng hô."),t("br"),t("br"),n._v("“Cái kia ‘Hoàng’ thực lực cực mạnh, ta nhìn nhiều một chuyện không bằng bớt một chuyện, quay đầu hướng tổng bộ xin, lại làm giải quyết...”"),t("br"),t("br"),n._v("Đi tới trước mặt, vội vàng truyền âm."),t("br"),t("br"),n._v("Vừa rồi cái kia Hoàng, đưa tay ở giữa, liền đem một cái trọng thương Thánh vực nhất trọng tăng lên tới tam trọng, loại năng lực này, thực sự thật là đáng sợ."),t("br"),t("br"),n._v("Viện trưởng tùy tiện đi tìm, hơn nữa còn không đem Kim Nguyên tiền bối mang theo... Hắn sợ sẽ gặp nguy hiểm."),t("br"),t("br"),n._v("Loại sự tình này, tốt nhất xử lý phương pháp, chính là báo cáo tổng bộ, để cấp bậc cao hơn danh sư xuống."),t("br"),t("br"),n._v("“Vị kia Hoàng thấy trung thành thuộc hạ bị giết, tất nhiên chạy trốn, tổng bộ phái người đến, ít nhất phải tốn hao mười ngày nửa tháng, đã không còn kịp rồi!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Tên kia là không trọn vẹn Vu hồn, đối với hắn có cực trợ giúp lớn, sao có thể mượn tay người khác."),t("br"),t("br"),n._v("Một khi tổng bộ danh sư đến rồi, nhiều nhất ban thưởng vài câu, chỗ tốt cũng đừng nghĩ, còn không bằng tự nghĩ biện pháp giải quyết."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mi trưởng lão dừng lại một chút: “Vậy ta để Mộc sư cùng các trưởng lão khác cùng một chỗ tới, mọi người cũng tốt lẫn nhau chiếu ứng...”"),t("br"),t("br"),n._v("“Không cần, ngươi ở lại chỗ này, ta liền vào xem, một khi nguy hiểm, không cần ngươi nói, cũng sẽ tự mình trốn ra được!”"),t("br"),t("br"),n._v("Trương Huyền vung vung tay."),t("br"),t("br"),n._v("Đầu này “Hoàng” thực lực ít nhất cũng phải đi đến Hóa Phàm bốn, ngũ trọng, hắn sở dĩ dám đi tìm, đến một lần có Ngoan Nhân nơi tay, mấu chốt nhất vẫn là trong đầu còn có một bản Thiên Đạo chi sách!"),t("br"),t("br"),n._v("Cái này là trước kia thu Tưởng Thanh Cầm viện trưởng là học sinh thời điểm hình thành, thẳng không có cam lòng sử dụng, coi như tên kia mạnh mẽ đến đâu, còn có thể mạnh đến mức qua Thiên Đạo?"),t("br"),t("br"),n._v("Một sách đi xuống, Ngoan Nhân đều muốn quỳ, chớ nói chi là một cái không trọn vẹn Vu hồn."),t("br"),t("br"),n._v("Đương nhiên, chuyện này ai cũng không thể nói, coi như Mi trưởng lão đối với hắn chân tâm thật ý, cũng không thể tiết lộ."),t("br"),t("br"),n._v("“Viện trưởng...” Gặp hắn không nghe khuyên ngăn, khăng khăng phải vào, Mi trưởng lão vội vàng chặn ở phía trước."),t("br"),t("br"),n._v("“Tốt, ngươi bây giờ ra ngoài, để Xích Viêm sư mấy cái Vương Giả, đem chiến đấu tất cả linh thú gọi trở về, tại Vân Vụ lĩnh tập hợp! Sau đó nói cho Triệu Bính Tuất trưởng lão, đã hoàn thành đàm phán, để tham dự chiến đấu học sinh quay về học viện nghỉ ngơi!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo: “Liên tục chiến đấu thời gian dài như vậy, khẳng định cũng mệt mỏi!”"),t("br"),t("br"),n._v("“Cái này... Vâng!”"),t("br"),t("br"),n._v("Thấy viện trưởng ra lệnh, Mi trưởng lão không dám cự tuyệt, chỉ tốt nhẹ gật đầu."),t("br"),t("br"),n._v("“Đây là hai trăm viên linh thạch thượng phẩm, ngươi điều tra một cái lần này đến cùng chết bao nhiêu học viên, thống kê thành sách, những này liền dùng để trợ cấp đi!”"),t("br"),t("br"),n._v("Than thở một tiếng, Trương Huyền lấy ra một đống linh thạch thượng phẩm."),t("br"),t("br"),n._v("Không nhiều không ít, vừa vặn hai trăm."),t("br"),t("br"),n._v("Mặc dù hắn yêu tài như mạng, nhưng có một số việc phải đi làm."),t("br"),t("br"),n._v("Vân Vụ lĩnh thú triều, là bởi vì hắn trộm lấy địa mạch linh dịch đưa tới, chết nhiều như vậy học viên, hẳn là vì đó làm những gì."),t("br"),t("br"),n._v("Linh thạch không thể vãn hồi đối phương tính mạng, nhưng có thể để cho người nhà trôi qua càng tốt hơn, cũng coi là một loại úy tạ."),t("br"),t("br"),n._v("“Cái này... Viện trưởng, trợ cấp học viên, tự có học viện bỏ ra, sao có thể để ngươi tự móc tiền túi!”"),t("br"),t("br"),n._v("Gặp hắn một cái lấy ra nhiều như vậy linh thạch thượng phẩm, Mi trưởng lão giật nảy mình, vội vàng vẫy tay."),t("br"),t("br"),n._v("Mặc dù đối phương là viện trưởng, nhìn thấy học viên chết, muốn muốn trợ giúp, nhưng cũng không cần bản thân xuất tiền, học viện thậm chí Danh Sư đường, nắm giữ đối ứng chính sách."),t("br"),t("br"),n._v("Những danh sư này, vì nhân tộc mà chết, vì đại nghĩa mà chết, tự nhiên không thể để cho hắn cứ như vậy không có tiếng tăm gì biến mất."),t("br"),t("br"),n._v("“Cầm!” Lông mày vung lên, Trương Huyền khoát tay áo: “Mặc kệ học viện làm thế nào, đây là của ta!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thấy ánh mắt của hắn trong suốt, là chân tâm thật ý muốn đem những linh thạch này đưa cho những cái kia là chiến đấu mà mất mạng học viên, Mi trưởng lão đành phải đón lấy."),t("br"),t("br"),n._v("Đối trước mắt vị này mới viện trưởng bội phục, lần nữa nhiều hơn mấy phần."),t("br"),t("br"),n._v("Cũng chỉ có như vậy chân chính đại công vô tư, xem tiền tài tại không có gì người, mới có tư cách làm viện trưởng đi!"),t("br"),t("br"),n._v("“Về sau ta cũng muốn hướng viện trưởng học tập, có đức độ...”"),t("br"),t("br"),n._v("Trong lòng hơi động, âm thầm làm quyết định."),t("br"),t("br"),n._v("“Tốt, nhanh làm đi, nơi này lưu ta một cái là được, không cần lo lắng...”"),t("br"),t("br"),n._v("Giao phó xong, Trương Huyền khoát tay áo, không nói thêm lời, dọc theo Minh Lý Chi Nhãn nhìn ra lộ tuyến, nhanh chân hướng hang núi chỗ sâu đi đến."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-892"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-894"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);