(window.webpackJsonp=window.webpackJsonp||[]).push([[218],{571:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1173-lai-tien-khuu-ngo-cung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1173-lai-tien-khuu-ngo-cung"}},[n._v("#")]),n._v(" Chương 1173: Lại tiến Khưu Ngô cung")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Cứ việc Trương gia đem vị kia thiên tài nói thiên hoa loạn trụy, thế gian hiếm có, nhưng có Thiên Đạo thư viện, hắn nắm giữ tuyệt đối tự tin, tương đồng cấp bậc bên dưới đụng phải, tuyệt đối có thể ngược đối phương, kêu cha gọi mẹ, hoài nghi nhân sinh."),t("br"),t("br"),n._v("Không làm cái khác, chỉ vì Nhược Hi những năm này chịu đến oan ức!"),t("br"),t("br"),n._v("Ta Trương Huyền nữ nhân, ai dám đụng, ta liền giết chết ai!"),t("br"),t("br"),n._v("“Dương sư, đã vị này Trương sư là học sinh của ngươi, ta sẽ không quấy rầy, cáo lui trước...”"),t("br"),t("br"),n._v("Thấy “Dương sư” híp mắt lại, sát cơ bắn ra, Lạc Thừa Tân run rụt lại, thận trọng nói."),t("br"),t("br"),n._v("Sớm biết vị này Dương sư, tại Trương Huyền trên thân lưu lại cường đại như thế ý niệm, tuyệt đối không đến cảnh cáo..."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("“Thanh niên” khoát tay áo."),t("br"),t("br"),n._v("“Đa tạ Dương sư...”"),t("br"),t("br"),n._v("Lạc Thừa Tân vội vàng xoay người, đi vài bước, ngừng lại, chần chờ phút chốc, lần nữa nhìn qua: “Mong rằng Dương sư có thể cùng Trương sư nói một tiếng, có thể buông tay còn là buông tay đi, tiểu công chúa từ nhỏ đến lớn, chịu khổ quá nhiều, ta thật không muốn xem nàng... Tiếp tục bị thương tổn!”"),t("br"),t("br"),n._v("Nghe nói như thế, “Thanh niên” lông mày nhíu một cái, thấy đối phương chân tâm thật ý, là thật vì Lạc Nhược Hi cân nhắc, lắc đầu: “Đi thôi!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Lạc Thừa Tân thở phào nhẹ nhõm, lúc này mới xoay người rời khỏi."),t("br"),t("br"),n._v("Thấy đối phương đi xa, “Thanh niên” lúc này mới thở phào nhẹ nhõm, trước đó trên thân như là đâm rách thiên địa khí tức, trong nháy mắt tiêu tán, cả người như quả cầu da xì hơi, toàn thân rã rời, dường như không nhấc lên được cánh tay."),t("br"),t("br"),n._v("“Nguy hiểm thật... Suýt chút nữa liền không kiên trì nổi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Vừa rồi để Ngoan Nhân thôn phệ Khổng sư tự tay viết, sinh ra khí tức cường đại, hiệu quả mặc dù không tệ, nhưng trên thực tế không kiên trì được bao lâu."),t("br"),t("br"),n._v("May mắn gần nhất tâm cảnh khắc độ tăng thêm, cùng đối phương không kém nhiều, mới không có bị nhìn ra, nếu không khẳng định lộ tẩy."),t("br"),t("br"),n._v("Đương nhiên, cũng thiệt thòi vừa rồi một đường lao nhanh, nơi này ít ai lui tới, nếu không, cường đại như vậy khí tức rò rỉ, tất nhiên dẫn tới vô số cường giả đến đây."),t("br"),t("br"),n._v("“Mau mau trở về, nếu như cái tên này, một khi phát giác không đúng, lại tới lời nói, không còn nhiều như vậy Khổng sư tự tay viết, sinh ra khí thế...”"),t("br"),t("br"),n._v("Biết bây giờ không phải là ngừng lại thời điểm, Trương Huyền vội vã hướng Chiến sư đường bay lượn mà đi."),t("br"),t("br"),n._v("Thời gian không dài, trở lại chỗ ở của mình, lúc này mới cảm thấy cùng đánh một hồi trận đánh ác liệt, buồn ngủ."),t("br"),t("br"),n._v("Cứ việc mượn nhờ rất nhiều thủ đoạn, đem đối phương chấn nhiếp, nhưng hắn biết, tại đây loại cấp bậc cường giả trước mặt ngụy trang, chắc chắn nhảy múa trên lưỡi đao, bảo hổ lột da, trong lòng tiếp nhận áp lực có thể tưởng tượng được."),t("br"),t("br"),n._v("Nếu không phải tâm lý tố chất quá cứng, khẳng định sớm đã bị đối phương đã nhìn ra."),t("br"),t("br"),n._v("Khoanh chân ngồi dưới đất, điều tức một canh giờ, lúc này mới hoàn toàn khôi phục."),t("br"),t("br"),n._v("“Nhất định phải chạy tới sang năm tháng ba trước đó... Nắm giữ có thể chống lại Trương gia thực lực hoặc là địa vị!”"),t("br"),t("br"),n._v("Nhớ tới trước đó cùng Lạc Thừa Tân đối thoại, Trương Huyền trong lòng liền có nồng đậm cảm giác áp bách."),t("br"),t("br"),n._v("Nghe hắn hơi thở, cái này hôn ước không riêng gì thông gia, còn dính dáng một kiện chuyện rất lớn, không cho cự tuyệt, cho dù Lạc Nhược Hi, là Lạc gia thương yêu nhất tiểu công chúa, cũng không được!"),t("br"),t("br"),t("br"),n._v("Bản thân cứ việc thiên tài, có thể trời nhận danh sư, trời nhận Thánh giả sự tình, không dám tuyên dương, liền đã định trước đối phương không có khả năng chú trọng."),t("br"),t("br"),n._v("Cho nên, muốn quang minh chính đại cưới đi Lạc Nhược Hi, không để cho nàng cùng Mộc sư đồng dạng, hối hận cả đời, chỉ có thể mau chóng tăng thực lực lên cùng địa vị!"),t("br"),t("br"),n._v("Sang năm tháng ba trước đó, nắm giữ cùng Trương gia nói chuyện ngang hàng năng lực!"),t("br"),t("br"),n._v("Như vậy, đối phương mới có thể chú trọng, mới có thể cân nhắc đề nghị của mình."),t("br"),t("br"),n._v("Nếu không, tuyệt không có khả năng vì một cái cái gọi là “Thiên tài”, mà đắc tội đại lục đệ nhất Thánh Nhân quý tộc, đắc tội một cái có thể để cho tượng Khổng sư đều lơ lửng siêu cấp thiên tài."),t("br"),t("br"),n._v("“Còn có khoảng tám tháng... Hiện tại là Thánh vực nhị trọng đỉnh phong, muốn nắm giữ cùng Trương gia bàn điều kiện năng lực, ít nhất đều muốn vượt qua vị này Lạc Thừa Tân, vượt qua Thánh vực thất trọng...”"),t("br"),t("br"),n._v("Trương Huyền trầm tư, có nồng đậm cảm giác áp bách."),t("br"),t("br"),n._v("Cứ việc từ bắt đầu tu luyện, đến bây giờ Thánh vực nhị trọng, chỉ dùng không đến thời gian một năm, tốc độ cực nhanh, nhưng hắn rõ ràng... Tu vi càng về sau càng khó!"),t("br"),t("br"),n._v("Không riêng gì công pháp càng ngày càng ít, càng ngày càng khó tìm, càng quan trọng hơn là, tài nguyên tu luyện thiếu thốn!"),t("br"),t("br"),n._v("Trước đó, một cái hạ phẩm linh thạch, liền có thể để hắn từ võ giả đột phá đến Hóa Phàm, liên tiếp mười cái đại cấp bậc, mà bây giờ... Liền xem như tinh nguyên linh thạch thượng phẩm, cũng chưa chắc có thể để cho tiến bộ rất nhanh."),t("br"),t("br"),n._v("Càng quan trọng hơn là, tu luyện tới Thánh vực, liên lụy đến linh hồn cùng nguyên thần, không riêng gì linh khí đầy đủ liền có thể đột phá, còn cùng tâm tính, tâm cảnh liên quan đến, tuyệt không có khả năng giống như kiểu trước đây một lần mà thôi."),t("br"),t("br"),n._v("Bởi vậy, cứ việc chỉ là sáu cái cấp bậc, nhưng muốn trong vòng tám tháng hoàn thành... Còn là rất khó! Liền xem như hắn, cũng không có bất kỳ nắm chắc nào, chỉ có thể tranh thủ thời gian, nắm chặt thời gian."),t("br"),t("br"),n._v("“Nhanh tu luyện... Nhược Hi, ngươi chờ ta, ta tuyệt sẽ không cho ngươi chịu đến oan ức!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trương Huyền ánh mắt kiên định."),t("br"),t("br"),n._v("Thông qua cùng Lạc Thừa Tân trò chuyện, hắn cũng biết, lúc trước Lạc Nhược Hi có thể đáp ứng hắn, là đã trải qua loại nào dày vò."),t("br"),t("br"),n._v("Đối phương đều quyết định cùng bản thân ở cùng một chỗ, thân là nam nhân, liền không thể phụ lòng."),t("br"),t("br"),n._v("Vô luận như thế nào, cũng phải làm cho nàng quang minh chính đại gả cho bản thân, không nhận mảy may oan ức."),t("br"),t("br"),n._v("Trương gia thật muốn ngăn trở, cái kia... Liền dẫm lên!"),t("br"),t("br"),n._v("Trong lòng có quyết định, tâm niệm càng thêm thông suốt, Trương Huyền chỉ cảm thấy linh hồn của mình, nhận được lột xác, trở nên càng thêm tinh thuần, càng thêm thuần túy."),t("br"),t("br"),n._v("“Tu luyện! Tranh thủ trước hừng đông sáng, xung kích đến Thánh vực tứ trọng...”"),t("br"),t("br"),n._v("Khoanh chân ngồi dưới đất, Trương Huyền đem Thánh vực tam trọng, tứ trọng công pháp, lần nữa hoàn chỉnh nhìn một lần."),t("br"),t("br"),n._v("Trước đó liền sưu tập kết thúc, hiện tại vừa vặn có thời gian, thừa cơ tu luyện một phen."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, từ Sở Thiên Hành trong tay lấy được 17 viên tinh nguyên linh thạch thượng phẩm, xuất hiện tại lòng bàn tay."),t("br"),n._v("Hiện tại hắn tu luyện, bình thường linh thạch đã không có hiệu quả."),t("br"),t("br"),n._v("Bình tâm tĩnh khí, chân khí ở trong người vận chuyển, ý thức trong nháy mắt đi tới nguyên thai trước mặt."),t("br"),t("br"),n._v("Thánh vực tam trọng, Thai Anh cảnh, cũng kêu Nguyên Anh cảnh, là để nguyên thai cùng linh hồn dung hợp, hình thành anh hài."),t("br"),t("br"),n._v("Cái này liên quan, khó khăn nhất liền là vỡ vụn nguyên thai, để hắn phá kén thành bướm, cũng chính là cái gọi là thai vỡ thành anh."),t("br"),t("br"),n._v("Không ít tu luyện giả, đều vây ở cái này, không cách nào đột phá."),t("br"),t("br"),n._v("Có Thiên Đạo công pháp, cứ việc không tồn tại trên việc tu luyện bình chướng, nhưng hắn nguyên thai đã đạt tới hoàn mỹ cấp bậc, bền bỉ vô cùng, muốn phá vỡ, chỉ sợ cũng so với bình thường người muốn khó hơn nhiều."),t("br"),t("br"),n._v("“Linh hồn, ra!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, to lớn hồn thể đi tới nguyên thai trước mặt."),t("br"),t("br"),n._v("Đột phá Thai Anh cảnh, có hai bước."),t("br"),t("br"),n._v("Thứ nhất, đem linh hồn cùng nguyên thai dung hợp, thứ hai, nguyên thai phá kén thành bướm, thai vỡ hóa anh."),t("br"),t("br"),n._v("“Dựa theo Thiên Đạo công pháp bên trên miêu tả, linh hồn đi vào nguyên thai mười phần đơn giản, chỉ cần ở người phía sau bên trên mở ra một cánh cửa, liền có thể tiến vào bên trong, tới dung hợp...”"),t("br"),t("br"),n._v("Cân nhắc công pháp bên trong pháp quyết tu luyện, Trương Huyền tinh thần tập trung, nguyên thai bên trên quả nhiên chậm rãi xuất hiện một cánh cửa."),t("br"),t("br"),n._v("Hồn thể lắc người một cái, trực tiếp hướng phía trong chui vào."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Mới chui một chút, lập tức đứng ở giữa không trung, hai chân không ngừng run rẩy."),t("br"),t("br"),n._v("“Không tốt... Kẹt lại!”"),t("br"),t("br"),n._v("Hắn hồn thể thực sự quá lớn, đi qua phong Thánh về sau, lần nữa trở lại mười mét, nguyên thai cứ việc trong cùng cấp bậc, xem như nghịch thiên tồn tại, có thể... Cùng hồn thể so, còn là kém quá nhiều."),t("br"),t("br"),n._v("Căn bản là không chui vào lọt!"),t("br"),t("br"),n._v("Cho dù linh hồn có thể biến hóa, có thể lớn có thể nhỏ... Cũng có nhất định hạn độ, thật giống như để voi chui vào hạch đào... Căn bản là nhiệm vụ không thể hoàn thành."),t("br"),t("br"),n._v("Vùng vẫy nửa ngày, mới đưa kẹt lại linh hồn rút ra, khóe miệng co giật, Trương Huyền mặt mũi xoắn xuýt."),t("br"),t("br"),n._v("Tinh nguyên linh thạch thượng phẩm có, Thiên Đạo công pháp cũng không có mảy may sai lầm, có thể... Hồn thể quá lớn, chui không lọt nguyên thai, tu luyện thế nào?"),t("br"),t("br"),n._v("Vào không được, liền không có cách nào dung hợp... Chớ nói chi là, thai bị hư hao anh!"),t("br"),t("br"),n._v("“Chuyện này là sao...”"),t("br"),t("br"),n._v("Lại chui mấy lần, đồng dạng bị kẹt lại, còn có một lần suýt chút nữa không có rút ra, Trương Huyền một hồi buồn bực, sắp khóc."),t("br"),t("br"),n._v("Vốn nghĩ bị kích thích, tức giận phấn đấu, một hơi liên tục đột phá hai tầng, đạt tới Nguyên Thần cảnh lại nói... Hiện tại xem ra, nghĩ quá đơn giản."),t("br"),t("br"),n._v("“Thiên Đạo công pháp, là không có bất kỳ cái gì sai lầm, nhưng mà... Ta tu luyện vu hồn sư năng lực, hồn thể mạnh mẽ, đã không thích hợp...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm."),t("br"),t("br"),n._v("Trước kia tu luyện, hồn là hồn, chân khí là chân khí."),t("br"),t("br"),n._v("Mà muốn xung kích Thai Anh cảnh, nhất định phải đem hai thứ này hoàn mỹ dung hợp."),t("br"),t("br"),n._v("Chân khí tu luyện giả, cùng Vu hồn tu luyện giả, là hai cái phương hướng, hơn nữa tu luyện tới cuối cùng, đều có thể đạt tới cực hạn."),t("br"),t("br"),n._v("Dưới tình huống bình thường, không có người có thể khác biệt kiêm tu."),t("br"),t("br"),n._v("Trương Huyền mượn nhờ Thiên Đạo công pháp thành công cải tiến Vu hồn pháp quyết tu luyện, để thân thể cùng linh hồn chung phát triển, giai đoạn trước không có cảm thấy cái gì, nhưng đến loại này cái kia dung hợp thời điểm, liền xảy ra vấn đề."),t("br"),t("br"),n._v("Hai loại đều đạt đến cực hạn... Như là béo bở, lại khó dung hợp!"),t("br"),t("br"),n._v("Tỷ như, rèn luyện một cái phức tạp công cụ, không có toàn thể khung có thể tham khảo, cho dù đơn độc linh kiện chế tạo lại xinh đẹp, hoàn mỹ đến đâu, làm không cẩn thận cũng sẽ lắp ráp không nổi, lớn nhỏ không thích hợp."),t("br"),t("br"),n._v("Hắn nguyên thai cùng hồn thể, như là một cái chỉ có lớn chừng hột đào đai ốc, lại đối ứng một cái đường kính mười mét ốc vít... Có thể chứa bên trên mới là lạ."),t("br"),t("br"),n._v("“Nên làm cái gì?”"),t("br"),t("br"),n._v("Tinh thần rời khỏi đan điền, Trương Huyền sầu muộn."),t("br"),t("br"),n._v("Còn là lần đầu tiên gặp được loại tình huống này."),t("br"),t("br"),n._v("Trước kia, nắm giữ tiền nhân kinh nghiệm tu luyện, trực tiếp sử dụng thư viện dung hợp liền tốt, hiện tại... Không có người gặp được loại sự tình này, cho dù muốn tham khảo cũng không có cách nào."),t("br"),t("br"),n._v("“Khó trách Khổng sư nói, muốn đi càng xa, cần phải đi ra thuộc về mình con đường, một mực kế thừa tiền nhân trí tuệ, mặc dù có thể để cho tiến bộ nhanh hơn, nhưng tương tự sẽ ràng buộc tiến lên phương hướng cùng động lực... Nhìn tới, hắn đã phát hiện ta trong tu luyện tồn tại vấn đề!”"),t("br"),t("br"),n._v("Lúc trước tại Khưu Ngô cung, Khổng sư từng đã nói với hắn, tu luyện muốn đi con đường của mình."),t("br"),t("br"),n._v("Hiện tại mới hiểu được, đối phương khả năng đã nhìn ra hắn vấn đề, mới cho cùng cảnh cáo, đáng tiếc... Bản thân không có coi ra gì."),t("br"),t("br"),n._v("“Đi con đường của mình? Đi như thế nào?”"),t("br"),t("br"),n._v("Loại tình huống này, đã không còn có thể tham khảo mục tiêu, Trương Huyền nhíu mày, trong lòng trầm tư."),t("br"),t("br"),n._v("Đi con đường của mình, nói đến đơn giản, trên thực tế, lại là khó càng thêm khó... Có thể nói, là không thể nào hoàn thành."),t("br"),t("br"),n._v("“Đúng rồi, đi Khưu Ngô cung nhìn một chút, có lẽ có thể tìm tới Khổng sư lưu lại thứ gì, có chút dẫn dắt!”"),t("br"),t("br"),n._v("Trầm tư rất lâu, đều không có tìm tới phương pháp giải quyết, trong lòng hơi động."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một cái thủy tinh cầu xuất hiện tại lòng bàn tay, phía trên điêu khắc đặc thù hoa văn, tựa như bản đồ."),t("br"),t("br"),n._v("Lúc trước luyện hóa thủy tinh, liền đem toàn bộ Khưu Ngô cung thu nhập trong đó, tùy thân mang theo... Chỉ là chưa hề đi vào qua thôi."),t("br"),t("br"),n._v("“Vào xem!”"),t("br"),t("br"),n._v("Thân là chủ nhân, nắm giữ tiến vào phương pháp, tinh thần khẽ động, Trương Huyền trong nháy mắt từ biến mất tại chỗ."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1181"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1183"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);