(window.webpackJsonp=window.webpackJsonp||[]).push([[2180],{2535:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-885-mot-ngon-tay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-885-mot-ngon-tay"}},[n._v("#")]),n._v(" Chương 885: Một ngón tay")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 885: Một ngón tay"),t("br"),t("br"),n._v("Rất nhanh đi tới trước đó tràn đầy xương trắng chỗ, trên mặt đất mặc dù tản mát rất nhiều khôi giáp, nhưng mà nhìn kỹ lại, những này xương cốt bên trên đều có chút tinh tế hoa văn, hẳn là Phong Sư điện nhận được tiên hiền công nhận lưu lại."),t("br"),t("br"),n._v("Nói cách khác đây đều là danh sư."),t("br"),t("br"),n._v("Mỗi một cái danh sư, đều là nhân loại tài phú, Danh Sư đường, tốn hao vô số đại giới bồi dưỡng mà thành, ở đây bị giết nhiều như vậy, huyết nhục tan rã, suy nghĩ một chút đều cảm giác đến đáng sợ."),t("br"),t("br"),n._v("Chỉ từ điểm đó, cũng có thể thấy được Hùng Hổ thú tội lỗi, tội lỗi chồng chất, cho dù chết bên trên một trăm lần, cũng không đủ."),t("br"),t("br"),n._v("“Vì nhân tộc mà hi sinh, vì đạo nghĩa mà không sợ, các ngươi là nhân loại anh hùng, không thể để cho thi cốt một mực phơi bày ở đây!” Trương Huyền thần sắc ảm đạm."),t("br"),t("br"),n._v("Bàn tay tại cách đó không xa mặt đất nhẹ nhàng nhấn một cái, lập tức xuất hiện một cái hố cực lớn, chân khí tuôn ra, đem trên mặt đất hài cốt tụ lại cùng một chỗ, chậm rãi để vào trong đó."),t("br"),t("br"),n._v("Hài cốt đều đã vỡ vụn, không biết là ai, không bằng an táng cùng một chỗ, sinh thời cùng một chỗ chiến đấu, sau khi chết chôn cùng một chỗ, cũng lại không tiếc nuối đi."),t("br"),t("br"),n._v("An táng xong những này hài cốt, Minh Lý Chi Nhãn thôi thúc, Trương Huyền tiếp tục hướng phía trước nhìn lại."),t("br"),t("br"),n._v("“Hoàng” dấu vết lưu lại, biến mất lúc trước hài cốt vị trí phía dưới."),t("br"),t("br"),n._v("“Chẳng lẽ tại đây phía dưới?”"),t("br"),t("br"),n._v("Dưới hài cốt mì là một vùng bình địa, cũng không có bất kỳ cái gì cái hố, đưa tay sờ một cái, cũng không có bất kỳ cái gì cơ quan."),t("br"),t("br"),n._v("Không có cái gì, đi nơi nào?"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhìn thấy dấu vết, chính là từ nơi này biến mất, tuyệt sẽ không sai."),t("br"),t("br"),n._v("“Chẳng lẽ là...” Đột nhiên, một cái ý nghĩ theo trong đầu xông ra."),t("br"),t("br"),n._v("Trương Huyền cổ tay khẽ đảo, một đầu linh thú sôi rơi xuống đất."),t("br"),t("br"),n._v("“Thiên Nghĩ Phong Mẫu, tra nhìn một chút, phía dưới này có hay không chồng chất không gian!”"),t("br"),t("br"),n._v("Chồng chất không gian liên quan không gian pháp tắc, lấy thực lực của hắn bây giờ còn không cách nào dò xét. Thiên Nghĩ Phong Mẫu, trời sinh đối không gian mẫn cảm, có thể nhẹ nhõm phân biệt."),t("br"),t("br"),n._v("Ong mẫu nhẹ gật đầu, cái mũi kề trên mặt đất hít hà, dọc theo chung quanh dạo qua một vòng, đứng dậy."),t("br"),t("br"),n._v("“Chủ nhân, nơi này thật có một cái chồng chất không gian, có điều, không phải rất lớn, có nên đi vào hay không nhìn xem?”"),t("br"),t("br"),n._v("“Quả nhiên!” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Vốn là hắn nhưng mà suy đoán, đã không có ở đây, lại không có phát hiện Địa Cung loại hình kiến trúc, vậy liền có thể là chồng chất không gian, không nghĩ tới quả là thế."),t("br"),t("br"),n._v("“Đương nhiên muốn nhìn!” Không chút do dự."),t("br"),t("br"),n._v("Tới này mục đích, chính là muốn tìm kiếm hổ gấu thú trong miệng vị này “Hoàng”, phát hiện chồng chất không gian lại như thế nào không vào?"),t("br"),t("br"),n._v("Bất quá, nếu như đối phương ở bên trong, lấy thủ đoạn cùng năng lực, khẳng định mười phần nguy hiểm, suy nghĩ một cái, Trương Huyền ngẩng đầu: “Ngươi cũng đừng vào, nói cho ta biết đi vào phương pháp là đủ.”"),t("br"),t("br"),n._v("Cái này chồng chất trong không gian, vô cùng có khả năng tồn tại một vị Thánh vực tứ, ngũ trọng cường giả, Thiên Nghĩ Phong Mẫu, bất quá là Hóa Phàm cửu trọng thôi, tùy tiện đi vào vô cùng có khả năng gặp được nguy hiểm."),t("br"),t("br"),n._v("Loại này không gian, đều có tiến vào phương pháp, không nắm giữ không gian năng lực, không phát hiện được không nói, coi như biết, cũng không thể nào đi vào."),t("br"),t("br"),n._v("Thật giống như lúc trước Phong Thánh đài Khổng sư lưu lại cái kia, không tìm ra tâm đắc sai lầm, là không thể nào mở ra."),t("br"),t("br"),n._v("Trước mắt cái này khẳng định cũng giống vậy, nhưng... Thiên Nghĩ Phong Mẫu đã có thể phát hiện, tất nhiên có tiến vào bên trong phương pháp."),t("br"),t("br"),n._v("“Tốt, ta giúp chủ nhân mở ra cửa vào, trực tiếp đi vào là được!”"),t("br"),t("br"),n._v("Không biết những này trong không gian tồn tại cái gì, thấy chủ nhân không cho nó đi vào, Thiên Nghĩ Phong Mẫu cũng không bắt buộc, gật đầu nói."),t("br"),t("br"),n._v("Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Lại trên mặt đất ngửi một lát, ong mẫu hai tay vồ mạnh một cái."),t("br"),t("br"),n._v("Xoẹt!"),t("br"),t("br"),n._v("Tựa như đồ vật gì bị xé nứt, một cái cao cỡ một người lỗ thủng xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Hít sâu một hơi, Trương Huyền cổ tay khẽ đảo, một quyển sách xuất hiện ở trước ngực, để Ngoan Nhân bảo vệ bản thân, lúc này mới thân thể nhoáng một cái, đi vào."),t("br"),t("br"),n._v("Đi vào lỗ thủng, lập tức thấy rõ tình huống bên trong, quả thực là cái không lớn không gian, đường kính chỉ có bảy, tám mét, so Khổng sư cái kia nhỏ không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Bên trong cũng không nhìn thấy trước đó thấy đầu kia Thanh Giác Long thú, chỉ có một cái không lớn sân khấu, cao cỡ một người, đường kính khoảng nửa mét, cùng tưởng tượng ra bày đầy đồ vật khác biệt, phía trên không có vật gì."),t("br"),t("br"),n._v("Dạo qua một vòng, không hề phát hiện thứ gì, Trương Huyền mày nhăn lại."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn quan sát tuyệt đối sẽ không phạm sai lầm, vị kia “Hoàng” tuyệt đối đã đi vào nơi này, nhưng làm sao cái gì không có?"),t("br"),t("br"),n._v("Liền xem như Vu hồn, cũng không cách nào trốn tránh Minh Lý Chi Nhãn, huống chi bản thân hắn cũng là một cái vu hồn sư, thật muốn có thứ này, tuyệt đối không chỗ che thân."),t("br"),t("br"),n._v("Đang định lại tử quan sát kỹ một vòng, đột nhiên, một cái sắc nhọn thanh âm đâm vào màng nhĩ, tựa như có đồ vật gì phá không đánh tới."),t("br"),t("br"),n._v("Còn không có kịp phản ứng, liền cảm thấy một cỗ lực lượng khổng lồ đột nhiên về phía sau lưng xung kích."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Bên ngoài thân một tầng màng ánh sáng xuất hiện, đang là lúc trước theo Dư huynh Dư Thừa tay ở bên trong lấy được viên kia Kích Không phù."),t("br"),t("br"),n._v("Cái này Kích Không phù có thể ngăn cản Thánh vực nhất trọng cường giả một kích toàn lực, vì an toàn, hắn vẫn luôn sát người cất giữ, chưa hề để vào trữ vật giới chỉ, giờ phút này gặp được nguy hiểm, trước tiên kích hoạt."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Có thể chống đỡ Thánh vực nhất trọng một kích toàn lực Kích Không phù, cùng sau lưng sắc nhọn thanh âm đụng một cái, liền nửa cái hô hấp đều không có chống đỡ, lập tức vỡ vụn ra, lực lượng cuồng bạo, đánh vào trên lưng của hắn, cả người giống như là bị xe lửa đụng vào, kìm lòng không được hướng về phía trước bay ra ngoài."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Nặng nề đụng tại phía trước trên vách tường, đập ra một cái hố to."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Một ngụm máu tươi phun ra."),t("br"),n._v("Mặc dù có Kích Không phù thủ hộ, lần này cũng bị trọng thương."),t("br"),t("br"),n._v("Thiên Đạo vận chuyển chân khí, khôi phục thương thế, Trương Huyền lúc này mới xoay người lại."),t("br"),t("br"),n._v("Trước mắt cũng không có bóng người, cũng không có trước đó Thanh Giác Long thú Vu hồn, chỉ lơ lửng một cái khô quắt ngón tay."),t("br"),t("br"),n._v("Vừa rồi chịu đến công kích, hẳn là cái này ngón tay, thả ra lực lượng."),t("br"),t("br"),n._v("“Một ngón tay, đem Kích Không phù đánh nát, đem ta đả thương?”"),t("br"),t("br"),n._v("Trương Huyền con ngươi co rụt lại."),t("br"),t("br"),n._v("Cảm thụ không ra tay trong ngón tay có hay không có được lực lượng, nhưng có thể đem tuỳ tiện kích thương, có thể nói kinh khủng."),t("br"),t("br"),n._v("“Ngươi chính là Hoàng Kim Hùng Hổ thú trong miệng cái kia ‘Hoàng’ ?” Ổn định hô hấp, thấy đối phương không có tiếp tục công kích, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Hắc hắc, có thể tìm tới nơi này, ngươi cũng rất có bản lĩnh!”"),t("br"),t("br"),n._v("Trong ngón tay một đạo ý niệm truyền tới, nói là Thượng Cổ thú ngữ."),t("br"),t("br"),n._v("Nghe thanh âm, chính là trước đó đầu kia Thanh Giác Long thú Vu hồn."),t("br"),t("br"),n._v("“Xem ra chính là, không nghĩ tới đường đường Thanh Giác Long thú Vu hồn, thế mà giấu ở một ngón tay bên trên, còn sống!”"),t("br"),t("br"),n._v("Trương Huyền mày nhăn lại."),t("br"),t("br"),n._v("Vu hồn không có khả năng đơn độc tồn tại, trước đó hắn liền nghi ngờ, đến cùng chuyện gì xảy ra, nhìn thấy cái này mới biết được, đối phương tất nhiên là gửi lại trong đó, sống lâu như vậy."),t("br"),t("br"),n._v("Thật giống như Mặc Hồn Sinh gửi ở Cửu Thiên Liên Thai bên trên đồng dạng."),t("br"),t("br"),n._v("Chỉ cần có cái tốt vật dẫn, hoàn toàn có thể kiên trì thời gian rất lâu."),t("br"),t("br"),n._v("Nhưng mà ngón tay không phải cơ thể sống, muốn phải gìn giữ sức sống, liền cần hấp thu huyết nhục lực lượng, có lẽ... Đây chính là để Hùng Hổ thú dùng danh sư hiến tế nguyên nhân."),t("br"),t("br"),n._v("Danh sư chân khí tinh thuần, huyết nhục lực lượng hơn xa tại bình thường người tu luyện, đối tử vật tới nói tuyệt đối được cho đại bổ."),t("br"),t("br"),n._v("“Nhãn lực không tệ, trẻ tuổi như vậy liền có loại này hiểu biết, xem ra ngươi không phải bình thường học viên, cũng không phải bên ngoài vị trưởng lão kia học sinh!”"),t("br"),t("br"),n._v("Gặp hắn khẩu vị nói ra lai lịch, ngón tay hừ nhẹ."),t("br"),t("br"),n._v("“Không sai, ta đích xác không phải Mi trưởng lão học sinh...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Có thể đem Vân Vụ lĩnh chín đại vương giả đều thuần phục, quả nhiên có chỗ hơn người, bất quá... Ta thích nhất nuốt chính là ngươi dạng này thiên tài danh sư, chỉ cần nuốt mất, liền có thể đưa tay chỉ huyết nhục bổ đầy, để cho ta lại kéo dài tuổi thọ 50 năm!”"),t("br"),t("br"),n._v("Ngón tay cười hắc hắc, tựa như nắm giữ một đôi tham lam con mắt, muốn đem người nuốt mất."),t("br"),t("br"),n._v("“Kéo dài tuổi thọ 50 năm?” Trương Huyền nhìn qua: “Ngươi lấy thôn phệ danh sư huyết nhục tới kéo dài tuổi thọ? Cái này ngón tay là cái gì, có thể để ngươi tránh né thời gian suy?”"),t("br"),t("br"),n._v("Vu hồn hồn lực ngũ suy bên trong, có thời gian suy, nương theo thời gian trôi qua, cho dù có huyết nhục tiếp tế, hồn lực cũng sẽ dần dần suy yếu."),t("br"),t("br"),n._v("Liền xem như vu hồn sư, cũng không có khả năng lâu dài tồn tại, vĩnh viễn còn sống sót."),t("br"),t("br"),n._v("Mặc Hồn Sinh bị nhốt Cửu Thiên Liên Thai, hoàn toàn tách biệt với thế gian, dù vậy, lực lượng cũng suy giảm lợi hại, Hóa Phàm cửu trọng đỉnh phong lực lượng, mười không còn một."),t("br"),t("br"),n._v("Cái tên này, nhờ vào một ngón tay liền còn sống sót, chẳng lẽ có gì đó cổ quái chỗ?"),t("br"),t("br"),n._v("“Ngươi biết thời gian suy?” Ngón tay nghi ngờ."),t("br"),t("br"),n._v("Không ít người biết vu hồn sư, nhưng đối với hồn lực ngũ suy, biết đến lại không nhiều, dù sao cũng là cái nghề nghiệp này bí mật, kẻ trước mắt này nhưng như thế rõ ràng, chẳng lẽ cũng là một vị vu hồn sư?"),t("br"),t("br"),n._v("“Đương nhiên biết! Không chỉ có biết cái này, còn biết, ngươi cũng không nhận được vu hồn sư truyền thừa, mặc dù lấy Vu hồn hình thái vẫn còn tồn tại, trên thực tế lại là bám vào cộng sinh, mà không phải đoạt xá!”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Đối phương không công kích, thư viện không cách nào kiểm tra, mới vừa mới đối với hắn công kích, sử dụng lực lượng, trong đầu đã hình thành thư tịch, cái này biết công phu, đã mở ra, biến thành kiến thức của mình."),t("br"),t("br"),n._v("Vốn cho rằng cái tên này, nắm giữ vu hồn sư truyền thừa, mới sống lâu như vậy, hiện tại xem ra, căn bản không phải có chuyện như vậy."),t("br"),t("br"),n._v("Chỉ là dùng phương pháp đặc thù, đem hồn phách của mình, cột vào trên ngón tay này, sau đó dựa vào thôn phệ danh sư huyết khí, duy trì sinh mệnh, trên thực tế cùng Vu hồn truyền thừa không có bất cứ quan hệ nào."),t("br"),t("br"),n._v("Có thể để cho hồn phách sống lâu như thế, cái này ngón tay tất nhiên không đơn giản."),t("br"),t("br"),n._v("Chỉ tiếc, đầu này Thanh Giác Long thú hồn phách cùng ngón tay nhưng mà bám vào cộng sinh quan hệ, cũng không phải là đoạt xá, vừa rồi đánh lén là hồn phách, thư viện cũng không biểu hiện ngón tay lai lịch."),t("br"),t("br"),n._v("Nếu không, cũng sẽ không cần cùng đối phương vết mực, trực tiếp một sách đập chết rồi."),t("br"),t("br"),n._v("“Có thể đem lai lịch của ta một cái nói ra, tuổi không lớn lắm, kiến thức đến không ít, bất quá... Càng như vậy, càng giữ lại không được ngươi! Không đến ta chồng chất không gian, ngược lại cũng thôi, đã đi vào, hôm nay liền chớ đi...”"),t("br"),t("br"),n._v("Kinh ngạc một chút, ngón tay lập tức cười lạnh."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngay sau đó một cổ lực lượng cường đại lan tràn mà ra, Trương Huyền trước đó trong sơn động nhìn thấy Thanh Giác Long thú huyễn ảnh lần nữa theo trên ngón tay nổi lên, mang theo nồng đậm sát cơ, tựa hồ muốn hắn nuốt mất."),t("br"),t("br"),n._v("“Thật tốt nói chuyện cùng ngươi, vì cái gì nhất định muốn ăn ta? Ngươi liền xác định có thể thành công?”"),t("br"),t("br"),n._v("Thấy đối phương bất cứ lúc nào cũng sẽ động thủ, Trương Huyền đang định đem Thiên Đạo chi sách ném ra, liền nghe đến bên tai vang lên hung ác thanh âm của người."),t("br"),t("br"),n._v("“Cái tên này giao cho ta đi...”"),t("br"),t("br"),n._v("“Giao cho ngươi?”"),t("br"),t("br"),n._v("Bản thân không mệnh lệnh, Ngoan Nhân cái tên này, từ trước tới nay không chủ động làm việc, làm sao đột nhiên muốn xuất thủ?"),t("br"),t("br"),n._v("Còn không có nghi ngờ bao lâu, liền nghe đến cái tên này thanh âm tiếp tục vang lên."),t("br"),t("br"),n._v("“Đúng... Cây kia là ta xương ngón tay!”"),t("br"),t("br"),n._v("Ps: Gần đây siêu lưu hành giọng nói hồng bao, lão nhai cũng định cho đoàn người phát xuống phúc lợi! Tiếng phổ thông tiêu chuẩn a? Không đúng tiêu chuẩn nhưng mà không giành được ah! Đêm mai tám giờ, lão nhai sẽ tại công chúng kêu đúng lúc phát một đợt giọng nói hồng bao, đoàn người tốc độ Wechat chú ý “Hoành Tảo Thiên Nhai”, đến lúc đó liền có thể đoạt!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-893"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-895"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);