(window.webpackJsonp=window.webpackJsonp||[]).push([[2182],{2537:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-887-hung-ho-thu-tai-phu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-887-hung-ho-thu-tai-phu"}},[n._v("#")]),n._v(" Chương 887: Hùng Hổ thú tài phú")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 887: Hùng Hổ thú tài phú"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Biết bị vây ở thư tịch, lại hết cách rồi, ngón tay lại không còn trước đó phách lối, đành phải mở miệng: “Ta là Ngoan Nhân một đốt xương tay, ngủ say vạn năm mới nắm giữ ý thức, Thanh Giác Long thú là Vân Vụ lĩnh Hoàng giả, ta vì mau chóng thức tỉnh, đem hắn linh hồn bắt đi vào, để hắn dựa theo yêu cầu của ta làm việc...”"),t("br"),t("br"),n._v("Rất nhanh, đem sự tình nói một lần."),t("br"),t("br"),n._v("Cùng trước đó đoán không kém nhiều."),t("br"),t("br"),n._v("Năm đó Ngoan Nhân cùng Khổng sư chiến đấu, đã trải qua cái gì, không người có thể biết, nhưng có thể khẳng định là, cái này đoạn xương ngón tay lưu giữ đi."),t("br"),t("br"),n._v("Ngoan Nhân khi còn sống sớm đã đạt đến nhỏ máu trùng sinh cảnh giới, cái này đoạn xương ngón tay, mặc dù bị thương rất lớn, ngủ say vạn năm, vẫn là ra đời ý thức của mình."),t("br"),t("br"),n._v("Sau đó, trong lúc vô tình bị Vân Vụ lĩnh đầu này Hoàng giả phát hiện."),t("br"),t("br"),n._v("Vì mau chóng tăng thực lực lên, nó liền dùng phương pháp đặc thù bắt cái tên này hồn phách, để hắn kéo dài hơi tàn còn sống."),t("br"),t("br"),n._v("Sau đó lấy cái này tàn hồn khống chế Vân Vụ lĩnh tất cả Thánh thú, lấy hội tụ địa mạch linh dịch, tăng thực lực lên làm đại giá, bắt danh sư, cho nó nuốt!"),t("br"),t("br"),n._v("Làm có thể so với Khổng sư siêu cấp cường giả, giúp Hùng Hổ thú tăng lên cái thực lực vẫn là rất đơn giản, ai biết gặp nắm giữ Thiên Đạo chi sách Trương Huyền, lúc này mới bị bắt, đánh rốt cuộc “Không tàn nhẫn nổi”."),t("br"),t("br"),n._v("Còn Ngô Dương Tử, hơn hai ngàn năm trước, từng có vài đầu Dị Linh tộc nhân đi tới nơi này, tìm xin giúp đỡ."),t("br"),t("br"),n._v("Lúc đó vị này Ngoan Nhân, vừa vặn thôn phệ xong mấy cái danh sư, lâm vào ngủ say, Thanh Giác Long thú biết mục đích của đối phương, phái Thánh thú ra tay, hỗ trợ bắt đi vị này luyện khí đại tông sư."),t("br"),t("br"),n._v("Thanh Giác Long thú mặc dù chỉ là một đạo tàn hồn, nhưng bị Ngoan Nhân nắm trong tay nhiều năm như vậy, cũng biết không ít bí mật, ngay sau đó nói cho Dị Linh tộc nhân... Về sau, đám người kia, mới tại Địa Cung khôi phục trái tim, để lần thứ nhất thu phục cái kia kẻ hung hãn có tự mình ý thức."),t("br"),t("br"),n._v("Có thể nói, trước đó trái tim có thể tỉnh lại, bái trước mắt cái này chỗ ngón tay ban cho."),t("br"),t("br"),n._v("“Trương sư, để cho ta nuốt cái tên này, thực lực của ta khẳng định có thể khôi phục càng nhiều...”"),t("br"),t("br"),n._v("Đem nên hỏi thì hỏi một lần, Trương Huyền còn có một số việc không nghĩ thông suốt, đang muốn tử cân nhắc tỉ mỉ, liền nghe đến vang lên bên tai truyền âm."),t("br"),t("br"),n._v("Nói chuyện đang là lần đầu tiên thu phục trái tim kia."),t("br"),t("br"),n._v("“Nuốt?”"),t("br"),t("br"),n._v("“Không sai! Nếu ta đoán không lầm, Ngoan Nhân lúc trước mặc dù bị giết, khẳng định cũng lưu không ít hậu chiêu, giống chúng ta loại này nắm giữ đơn độc ý thức cũng không ít, muốn lần nữa khôi phục đỉnh phong, chỉ bằng vào hấp thu linh khí, lại đến mười vạn năm cũng làm không được... Biện pháp duy nhất chính là, tìm tới những này phân tán ra tới ý thức, nguyên một đám thôn phệ!”"),t("br"),t("br"),n._v("Trái tim nói: “Chỉ cần thôn phệ đầy đủ, coi như khôi phục không được toàn bộ thực lực, cũng chắc chắn sẽ không kém hơn quá nhiều!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nhìn thấy cái này xương ngón tay cũng tự xưng Ngoan Nhân, hắn liền đoán ra những thứ này."),t("br"),t("br"),n._v("  Linh thú bên trong có một loại bồi dưỡng cường giả phương pháp, cùng cái này không sai biệt lắm, bình thường sẽ nuôi dưỡng rất nhiều, để hắn tự giết lẫn nhau, cuối cùng còn dư lại chính là người mạnh nhất."),t("br"),t("br"),n._v("Hung ác người biết mình hẳn phải chết, trước thời hạn lưu lại một tay, hẳn là lưu lại trái tim, ngón tay kẻ như vậy rất nhiều, mà muốn hoàn toàn phục sinh, cũng chỉ có thể đem những này (tụ) tập hợp lại cùng nhau, nguyên một đám thôn phệ."),t("br"),t("br"),n._v("“Tốt, chỉ cần hiệu trung với ta, không chỉ riêng này cái, ta còn sẽ giúp ngươi tìm tới cái khác Ngoan Nhân, để ngươi trở nên càng ngày càng mạnh!” Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Đối với hắn mà nói ai là Ngoan Nhân râu ria, chỉ cần có thể hiệu trung bản thân, giúp mình làm việc là tốt rồi."),t("br"),t("br"),n._v("Trái tim này Ngoan Nhân, đã đi theo ở bên người đã lâu rồi, cũng đã trải qua không ít chuyện, nếu như có thể hoàn toàn thuần phục, tự nhiên tốt nhất."),t("br"),t("br"),n._v("Còn cái kia ngón tay, tàn sát nhiều như vậy danh sư, nuốt huyết nhục của bọn hắn, coi như có thể thần phục, cũng tuyệt đối sẽ không dùng."),t("br"),t("br"),n._v("“Hiệu trung?”"),t("br"),t("br"),n._v("Trái tim trầm lặng."),t("br"),t("br"),n._v("Mặc dù bị vây ở thư tịch bên trong, nhưng ý thức của nó trước sau là người nào rong ruổi thiên địa, thậm chí cùng Khổng sư chiến đấu qua Ngoan Nhân, để hắn hoàn toàn hiệu trung, vẫn còn có chút độ khó."),t("br"),t("br"),n._v("“Không sai!” Cũng không nhiều lời, Trương Huyền hai tay chắp sau lưng, tùy ý nó tự mình lựa chọn."),t("br"),t("br"),n._v("Nắm giữ Thiên Đạo thư viện, hắn đã có ngạo khiếu thiên địa vốn liếng, đối phương thần phục cũng chỉ là dệt hoa trên gấm mà thôi, không thần phục, cũng có thể đồng dạng xông ra một vùng trời mới."),t("br"),t("br"),n._v("“Tốt, ta đồng ý!”"),t("br"),t("br"),n._v("Không có trầm lặng bao lâu, trái tim liền mở miệng đáp ứng."),t("br"),t("br"),n._v("Không đáp ứng không được ah, nó tận mắt chứng kiến qua người thanh niên này tất cả thủ đoạn cùng năng lực, so trong truyền thuyết những cái kia cửu tinh danh sư, đều kinh diễm hơn."),t("br"),t("br"),n._v("Có lẽ vị này, thật có thể dẫn nó đi càng xa, thậm chí vượt qua... Khổng sư!"),t("br"),t("br"),n._v("Đáp ứng, hoàn toàn khôi phục, thậm chí trở nên càng mạnh; Không đáp ứng, một mực bị vây ở thư tịch bên trong vĩnh viễn không vươn mình lên được, chỉ cần không ngốc, đều sẽ làm ra lựa chọn chính xác nhất."),t("br"),t("br"),n._v("“Hiến tế linh hồn đi!” Thấy đối phương đáp ứng, Trương Huyền nói."),t("br"),t("br"),n._v("Chỉ có linh hồn hiến tế, đối phương sống chết, tại ý niệm của mình trong khống chế, mới có thể đối với đối phương hoàn toàn tin tưởng."),t("br"),t("br"),n._v("Nếu không chỉ bằng vào vài câu lời thề cùng hứa hẹn, một khi phản bội, liền xem như hắn đều không chịu đựng nổi."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Cũng không do dự, trái tim một cỗ ý niệm lập tức truyền tới, rơi vào Trương Huyền mi tâm, lóe lên chui vào thức hải."),t("br"),t("br"),n._v("Cảm nhận được cỗ này ý niệm, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Trước kia nhưng mà đem trái tim này trấn áp, để hắn nghe theo bản thân phân phó, mà bây giờ là hoàn toàn thần phục, có đạo này hiến tế linh hồn, chẳng khác nào hoàn toàn đem hắn khống chế, cùng thuần phục thú sủng đồng dạng, đối mệnh lệnh của mình, vô điều kiện nghe theo, không dám phản bác."),t("br"),t("br"),n._v("“Chủ nhân!”"),t("br"),t("br"),n._v("Linh hồn hiến tế tương đương nhận chủ, trái tim lại không còn trước đó cao ngạo, hạ thấp thân thể."),t("br"),t("br"),n._v("“Gọi thiếu chủ đi!” Trương Huyền khoát tay áo."),t("br"),n._v(" “Vâng, thiếu chủ!” Trái tim lên tiếng."),t("br"),t("br"),n._v("“Như thế nào thôn phệ? Cần ta giúp thế nào bề bộn?” Trương Huyền nói."),t("br"),t("br"),n._v("“Bẩm báo thiếu chủ, ta thực lực bây giờ còn không bằng hắn, nếu như ngươi có thể đem quyển kia Khổng sư tự tay viết, ta mượn dùng một chút, liền có thể bảo đảm đem hắn hoàn toàn thôn phệ!” Trái tim nói."),t("br"),t("br"),n._v("“Mượn dùng Khổng sư tự tay viết?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Nhận được quyển này Khổng sư tự tay viết, dễ dàng ấy ư, lần trước ngụy trang Dương Huyền, đã bị nuốt lấy một phần ba, hiện tại thế mà còn băn khoăn."),t("br"),t("br"),n._v("“Thiếu gia, Dị Linh tộc nhân nuốt nhân loại tăng thực lực lên, cái kia ngón tay, càng là nuốt danh sư, bất kể ý niệm, vẫn là thực lực, đều so ta mạnh hơn. Không mượn Khổng sư tự tay viết lời nói, khẳng định không phải là đối thủ, làm không cẩn thận sẽ còn bị hắn ăn đi!”"),t("br"),t("br"),n._v("Trái tim nói."),t("br"),t("br"),n._v("“Tốt a, bản này Khổng sư tự tay viết chỉ còn lại có hai phần ba chữ viết, ngươi liền nói cho ta biết cần phải mượn bao nhiêu, mới có thể đem hắn thôn phệ?” Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này... Ta cũng không xác định!” Trái tim chần chờ."),t("br"),t("br"),n._v("“Như vậy đi, chờ một lúc, ta trước đem cái này ngón tay đánh thành trọng thương, sau đó ngươi lại ra tay, hẳn là có thể giúp ta tiết kiệm một chút.” Suy nghĩ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Ở bên ngoài, hắn khẳng định không phải cái này ngón tay đối thủ, nhưng bị vây ở Thiên Đạo chi sách bên trong, Thiên Đạo chân khí chính là vũ khí đáng sợ nhất."),t("br"),t("br"),n._v("Làm xong quyết định, cũng không nhiều lời, Trương Huyền đem Thiên Đạo chi sách cầm trong tay, một đạo chân khí lập tức rót chuyển vào."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Bị chân khí tập kích, ngón tay đau liên tục lăn lộn, không lâu sau liền không có thanh âm."),t("br"),t("br"),n._v("Thấy cảnh này, trái tim kéo ra, vội vàng xông lên, một cỗ to lớn chém giết ý niệm phun ra ngoài, tại thiên đạo chi sách bên trong đối đầu."),t("br"),t("br"),n._v("Không biết qua bao lâu, ngón tay rốt cục cũng ngừng lại, lại không còn trước đó kiêu căng khó thuần, an tĩnh nằm tại trang sách bên trong."),t("br"),t("br"),n._v("“Thiếu chủ!”"),t("br"),t("br"),n._v("Lại qua một đoạn thời gian, trong ngón tay một đạo ý niệm vang lên."),t("br"),t("br"),n._v("Trái tim hoàn toàn đem nuốt, để cái này cái gọi là “Ngoan Nhân”, thành bản thân tiếp tế."),t("br"),t("br"),n._v("“Thiếu chủ, vừa đem cái tên này thôn phệ, hai cỗ ý niệm xung đột lẫn nhau, ta có thể muốn ngủ say một đoạn thời gian, bên trong Thanh Giác Long thú tàn hồn, đã thay ngươi xóa đi ý thức, có thể nhẹ nhõm luyện hóa, dùng để tẩm bổ Vu hồn!”"),t("br"),t("br"),n._v("Hung ác thanh âm của người tiếp tục vang lên."),t("br"),t("br"),n._v("Ngay sau đó một đạo tinh thuần hồn phách lực lượng, theo ngón tay truyền ra."),t("br"),t("br"),n._v("Cảm nhận được cỗ này linh hồn tinh thuần độ, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Cái này cái gọi là Vân Vụ lĩnh “Hoàng”, mặc dù chỉ là một đạo tàn hồn, nhưng đã đạt đến Thánh vực tứ, ngũ trọng cảnh giới, hồn phách cực mạnh, chỉ cần thật tốt luyện hóa, hồn lực của mình tất nhiên có thể có tăng lên rất nhiều."),t("br"),t("br"),n._v("Bất quá, bây giờ không phải là luyện hóa thời điểm, còn có rất nhiều sự tình muốn đi xử lý."),t("br"),t("br"),n._v("Nhìn thoáng qua Khổng sư tự tay viết, thấy lại bị nuốt mất hơn ba trăm cái chữ, không sai biệt lắm một phần ba, Trương Huyền lông mày nhảy lên, đem hắn thu vào nhẫn."),t("br"),t("br"),n._v("Người khác nhận được Khổng sư tự tay viết, cũng làm Thánh làm bảo, giống như hắn tao đạp như vậy, đoán chừng trong thiên hạ duy nhất cái này một phần."),t("br"),t("br"),n._v("Rời đi chồng chất không gian, phân phó Thiên Nghĩ Phong Mẫu đến đây thôn phệ."),t("br"),t("br"),n._v("Đem cái này giết hại tất cả danh sư “Hoàng” chém giết, về sau Vân Vụ lĩnh đem lại không đủ để thành uy hiếp, cũng coi như đối với mấy cái này chết đi danh sư, có bàn giao."),t("br"),t("br"),n._v("Chồng chất không gian không lớn, lại thêm Thiên Nghĩ Phong Mẫu thực lực cũng có cực lớn tăng tiến, chỉ dùng không đến nửa canh giờ, liền thôn phệ sạch sẽ."),t("br"),t("br"),n._v("Thu vào tổ ong, Trương Huyền đi ra ngoài."),t("br"),t("br"),n._v("Một lần nữa trở lại trước đó chứa đựng linh dịch chỗ, chỉ thấy Hoàng Kim Hùng Hổ thú nằm trên mặt đất, đã bị đánh thành bánh thịt, chết không thể chết lại."),t("br"),t("br"),n._v("Kim Nguyên đỉnh gặp hắn tới, lập tức một mặt lấy lòng tiến lên đón."),t("br"),t("br"),n._v("“Chủ nhân, cái tên này đã đánh chết, đồ tốt không ít...”"),t("br"),t("br"),n._v("Cười hắc hắc, một cái trữ vật giới chỉ theo Kim Nguyên trong đỉnh bay tới, rơi vào Trương Huyền lòng bàn tay."),t("br"),t("br"),n._v("“Trữ vật giới chỉ?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, lập tức bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Thánh thú đã có thể mở ra trữ vật giới chỉ, cái tên này làm Vân Vụ lĩnh Vương Giả, sống ít nhất một ngàn năm, lại chém giết không biết bao nhiêu cường giả, sưu tập không biết bao nhiêu bảo bối."),t("br"),t("br"),n._v("Trước đó một mực không nghĩ tới, không nghĩ tới Kim Nguyên đỉnh thay hắn đã suy nghĩ kỹ."),t("br"),t("br"),n._v("Chủ nhân chết, trữ vật giới chỉ liền không có cấm chế, ngón tay một điểm, một giọt máu tươi xâm nhập trong đó, Trương Huyền lập tức nhìn thấy trong đó không gian."),t("br"),t("br"),n._v("So với hắn nhẫn lớn gấp bội, đường kính đủ mấy trăm mét, đủ loại bảo vật tích tụ như núi, chỉ riêng linh thạch trung phẩm, liền có ngàn vạn nhiều."),t("br"),t("br"),n._v("Linh thạch thượng phẩm chất thành một đống, cũng có mấy trăm cái."),t("br"),t("br"),n._v("Các loại dược liệu, đan dược, trân quý khoáng thạch, càng là đếm mãi không hết."),t("br"),t("br"),n._v("Vũ khí cũng đủ có mấy trăm, đi đến Linh cấp tuyệt phẩm, liền có ít nhất ba, bốn mươi kiện, thậm chí Bán Thánh cấp bậc, cũng có trọn vẹn ba kiện!"),t("br"),t("br"),n._v("“Lợi hại...”"),t("br"),t("br"),n._v("Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Trùng sinh đến nay, hắn cần cù chăm chỉ kiếm tiền, liền mảy may đều không buông tha, vốn cho rằng đã giá trị bản thân không ít, cùng cái này Vân Vụ lĩnh đỉnh phong Vương Giả so, vẫn là kém thật lớn một đoạn."),t("br"),t("br"),n._v("Cũng khó trách, Vân Vụ lĩnh lúc trước nhưng mà liền Ngô Dương Tử đại tông sư đều bắt đi, người sau bảo bối, khẳng định không ít vơ vét."),t("br"),t("br"),n._v("Mặc dù hai ngàn năm trước, đầu này Hoàng Kim Hùng Hổ thú khả năng còn chưa ra đời, nhưng làm đỉnh phong nhất Vương Giả, Thanh Giác Long thú người phát ngôn, rất thật tốt đồ vật vẫn là truyền thừa xuống."),t("br"),t("br"),n._v("Ps: Gần đây siêu lưu hành giọng nói hồng bao, lão nhai đêm nay cho đoàn người phát xuống phúc lợi! Tiếng phổ thông tiêu chuẩn a? Không đúng tiêu chuẩn nhưng mà không giành được ah! Tám giờ tối hôm nay chuẩn, lão nhai sẽ tại công chúng kêu đúng lúc phát một đợt giọng nói hồng bao, đoàn người tốc độ Wechat chú ý “Hoành Tảo Thiên Nhai”, đến lúc đó liền có thể đoạt!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-895"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-897"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);