(window.webpackJsonp=window.webpackJsonp||[]).push([[2183],{2538:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-888-nghiep-chuong-ah"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-888-nghiep-chuong-ah"}},[n._v("#")]),n._v(" Chương 888: Nghiệp chướng ah!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 888: Nghiệp chướng ah!"),t("br"),t("br"),n._v("Những này nhưng mà tất cả bảo vật một bộ phận, trọng yếu nhất chính là, còn nắm giữ nhiều viên Thánh thú nội đan."),t("br"),t("br"),n._v("Thánh thú tuổi thọ mặc dù so với nhân loại càng dài, càng xa xưa, nhưng cuối cùng sẽ chết, lại thêm chiến đấu tổn thương, nhiều năm tích luỹ xuống, trữ vật giới chỉ bên trong Thánh thú nội đan, đã đạt đến mức làm người nghe kinh hãi, chừng mấy trăm miếng nhiều!"),t("br"),t("br"),n._v("Mỗi một viên đều có giá trị không nhỏ, thật muốn bán ra lời nói, tuyệt đối là cái giá trên trời."),t("br"),t("br"),n._v("Đem bên trong tất cả bảo vật đều nhìn một lần, Trương Huyền tràn đầy sợ hãi thán phục."),t("br"),t("br"),n._v("Cùng cái tên này so sánh, lúc trước hắn cái gọi là giàu có, thật cùng nhà quê không có gì khác nhau, nghèo không có cách nào gặp người."),t("br"),t("br"),n._v("“Làm đến như vậy nhiều đồ tốt, ngươi công lao không nhỏ! Cần ta làm sao ban thưởng?”"),t("br"),t("br"),n._v("Tràn đầy cao hứng, Trương Huyền nhìn về phía cách đó không xa Kim Nguyên đỉnh."),t("br"),t("br"),n._v("Cái này viên trữ vật giới chỉ, khẳng định không có tùy thân mang theo, nếu không phải cái tên này thẩm vấn, có lẽ liền bỏ qua, Kim Nguyên đỉnh bình thường có chút trách trách vù vù, thời khắc mấu chốt, vẫn là rất đáng tin."),t("br"),t("br"),n._v("“Hắc hắc, chỉ cần chủ nhân đừng quên giúp ta tăng thực lực lên là tốt rồi...”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh nói."),t("br"),t("br"),n._v("“Yên tâm đi!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cái tên này nhưng mà Thánh cấp hạ phẩm vũ khí, chỉ cần hắn luyện khí trình độ lại tăng, hoàn toàn có thể giúp một tay tăng lên cấp bậc."),t("br"),t("br"),n._v("Lúc trước có thể thuận lợi đem hắn thuần phục, cũng là bởi vậy hứa hẹn ở bên trong, nếu không coi như Trương Huyền, nắm giữ đối phương thiếu hụt, đường đường danh sư học viện hộ viện Thánh khí cũng không có khả năng dễ dàng như thế liền đáp ứng!"),t("br"),t("br"),n._v("Có uy hiếp, có chỗ tốt, mới có thể để cho thuộc hạ trung thành không hai, nếu như nhưng mà uy hiếp mà không có trông đợi cùng tiền đồ, thời gian lâu dài, sớm muộn cũng sẽ sinh ra dị tâm."),t("br"),t("br"),n._v("Cùng Kim Nguyên đỉnh đi ra sơn động, lập tức nhìn thấy Xích Viêm sư, Hổ Đầu thú mấy người Vương Giả tiến lên đón."),t("br"),t("br"),n._v("“Thế nào?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Bẩm báo chủ nhân, tất cả đang tại chiến đấu linh thú, đã tập hợp xong xuôi, đều tại chân núi, chờ đợi chủ nhân!”"),t("br"),t("br"),n._v("Xích Viêm sư vội nói."),t("br"),t("br"),n._v("Trải qua chuyện lúc trước, bọn chúng đối trước mắt vị này tuổi không lớn lắm chủ nhân tất cả đều khăng khăng một mực, không dám tiếp tục sinh ra hai lòng."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!” Trương Huyền nhẹ gật đầu, nhanh chân hướng chân núi đi tới."),t("br"),t("br"),n._v("Vân Vụ lĩnh linh thú, chỉnh tề xếp tại chân núi, lít nha lít nhít đủ có mấy vạn nhiều, Trương Huyền đi tới trước mặt, lập tức đưa tới bạo động, nguyên một đám mở to hai mắt nhìn không rõ ràng cho lắm."),t("br"),t("br"),n._v("Bọn chúng đang cùng danh sư học viện chiến đấu, liền bị gọi đến nơi này, đến cùng chuyện gì xảy ra, Vương Giả cùng nhân loại lại là thế nào đàm phán, một mực đều không biết."),t("br"),t("br"),n._v("“Linh thú muốn tăng thực lực lên, không chỉ muốn hấp thu tinh hoa nhật nguyệt, linh khí, còn cần học tập nhân loại, tiến hành tu luyện...”"),t("br"),t("br"),n._v("Đi tới trước mặt, Trương Huyền cũng không nói nhảm, âm thanh vang dội vang lên."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hắn bên này đến chân núi, Mi trưởng lão cũng cuối cùng đã tới Lôi Viễn phong."),t("br"),t("br"),n._v("Theo hai người đi Vân Vụ lĩnh đàm phán đến bây giờ đã tiếp cận hai canh giờ, Triệu Bính Tuất trưởng lão đã xem tất cả học sinh đều tập trung tới, cũng đem Mộc sư cùng với cái khác mấy đại trưởng lão, mang theo tới."),t("br"),t("br"),n._v("Thấy chỉ có một mình hắn tới, viện trưởng không thấy tung tích, mọi người tất cả đều vẻ mặt khó coi."),t("br"),t("br"),n._v("Triệu Bính Tuất nhịn không được, tiến lên đón: “Viện trưởng đây, hắn thế nào?”"),t("br"),t("br"),n._v("“Mi Trúc, ngươi sao có thể để viện trưởng đi mạo hiểm như vậy, một khi xảy ra chuyện, như thế nào xứng đáng được tất cả thầy trò?” Vệ Nhiễm Tuyết đôi mắt thanh tú trừng trừng, hận không thể đem trước mắt Mi trưởng lão ăn."),t("br"),t("br"),n._v("Nghe được Triệu Bính Tuất trở về nói viện trưởng vì ngăn cản chiến đấu, giảm bớt học viên tổn thương, đi tới Vân Vụ lĩnh, tất cả mọi người điên rồi, không có bị dọa chết tươi."),t("br"),t("br"),n._v("Vân Vụ lĩnh là địa phương nào?"),t("br"),t("br"),n._v("Linh thú, Thánh thú tung hoành, nhân loại chỉ muốn đi vào, cơ bản đều là có đi không về, hiện tại thật vất vả có một vị mới viện trưởng, nếu như vì vậy mà chôn vùi, bọn họ tuyệt đối đều là lịch sử tội nhân, ai cũng rửa sạch không sạch."),t("br"),t("br"),n._v("“Ta...” Mi trưởng lão mặt mũi cười khổ."),t("br"),t("br"),n._v("Viện trưởng muốn làm cái gì, coi như muốn ngăn trở, có thể ngăn cản đến động ư?"),t("br"),t("br"),n._v("“Tốt, đều đừng nói nữa, ngươi tại sao trở lại, sư thúc hiện tại ở đâu? Vân Vụ lĩnh tình huống thế nào?”"),t("br"),t("br"),n._v("Cắt đứt mọi người chất vấn, Mộc sư nhìn lại: “Ta nghe nói Vân Vụ lĩnh mười đại vương giả, không có một cái nào dễ sống chung hạng người, đối với nhân loại tràn đầy cừu thị, tùy tiện đi qua đàm phán, khẳng định gặp không ít phiền phức đi!”"),t("br"),t("br"),n._v("Những người khác cũng đồng loạt nhìn lại."),t("br"),t("br"),n._v("“Phiền phức...” Mi trưởng lão không biết như thế nào mở miệng."),t("br"),t("br"),n._v("Phiền phức là có, chẳng qua không phải bọn họ."),t("br"),t("br"),n._v("Xoắn xuýt một hồi, đem ngôn ngữ tổ chức một cái, Mi trưởng lão mới mở miệng lần nữa."),t("br"),t("br"),n._v("“Tình huống là như vậy, mười đại vương giả cùng chúng ta hẹn nhau tại một cái sơn động, trao đổi như thế nào giải quyết tốt hậu quả sự tình, viện trưởng... Đi ra ngoài một chuyến, sau đó, sau đó... Mười đại vương giả, liền có chín cái biến thành của hắn thú sủng... Ba ngàn linh thú phi hành, cũng tất cả đều nhận hắn làm chủ! Nói cách khác, hiện tại loại trừ đang tại chiến đấu những linh thú này, Vân Vụ lĩnh tất cả Thánh thú, Tòng Thánh, Bán Thánh, tất cả đều là viện trưởng tài sản riêng...”"),t("br"),t("br"),n._v("Còn chưa nói xong, nghe được chung quanh không còn thanh âm, nghi ngờ ngẩng đầu nhìn lại, chỉ thấy tất cả trưởng lão cùng Mộc sư tất cả đều mở to hai mắt nhìn, nguyên một đám tròng mắt rớt xuống đất, giống như điên."),t("br"),t("br"),n._v("Mặc kệ ai nghe được tin tức này, đoán chừng cũng sẽ có tương đồng biểu lộ."),t("br"),t("br"),n._v("Chạy về đi đàm phán, để người ta tất cả linh thú, Thánh thú toàn bộ thuần phục..."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Ngươi đây không phải đàm phán, mà là đi đầu nhân gia hang ổ đi!"),t("br"),t("br"),n._v("Có muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("“Sự thật chính là như thế...” Biết mọi người không tin, Mi trưởng lão một mặt bất đắc dĩ."),t("br"),t("br"),n._v("Nói thật, nếu không phải tận mắt nhìn thấy, liền xem như hắn đều không thể tin được đây là sự thực."),t("br"),t("br"),n._v("Vân Vụ lĩnh linh thú tai hoạ ngầm, từ khi danh sư học viện thành lập đến nay liền tồn tại, không biết bao nhiêu vị viện trưởng, đều nghĩ qua phương pháp, kết quả đều không công mà lui."),t("br"),n._v(" Vị này Trương viện trưởng ngược lại tốt, còn không có tham gia kế vị lễ, liền đem người ta hang ổ mang!"),t("br"),t("br"),n._v("Không chỉ đứt rễ, còn đem tất cả linh thú, Thánh thú thuần phục, tương đương với cho danh sư học viện tăng thêm một sự giúp đỡ lớn."),t("br"),t("br"),n._v("Cộng thêm toàn bộ Vân Vụ lĩnh lực lượng, học viện thực lực tổng hợp, trực tiếp tăng lên, tại Thanh Nguyên đế quốc cấp dưới tứ đại học viện bên trong, có lẽ cũng có thể tăng lên một cái thứ tự."),t("br"),t("br"),n._v("“Viện trưởng hiện tại đi nơi nào?” Nghe xong như thế truyền kỳ sự tình, Triệu Bính Tuất hai mắt choáng váng."),t("br"),t("br"),n._v("“Hắn để cho ta về tới trước an bài học viên, bản thân còn lưu tại Vân Vụ lĩnh!” Mi trưởng lão nói."),t("br"),t("br"),n._v("“Chung Đỉnh Thuần, ngươi trước mang những học viên này, đi y sư học viện điều trị; Tu Trường Thanh, ngươi để võ kỹ học viện học viên, tùy thời chuẩn bị sẵn sàng chiến đấu. Các trưởng lão khác cùng ta cùng đi Vân Vụ lĩnh!”"),t("br"),t("br"),n._v("Biết mình vị sư thúc này, còn tại Vân Vụ lĩnh, Mộc sư phân phó."),t("br"),t("br"),n._v("Trọng thương học viên nhu cầu cấp bách cứu chữa, y sư học viện không thể đổ trách nhiệm cho người khác, lúc này, vạn nhất linh thú công kích lần nữa, võ kỹ học viện chiến lực mạnh nhất, khẳng định muốn xông đi lên."),t("br"),t("br"),n._v("“Vâng!” Chung Đỉnh Thuần, Tu Trường Thanh đồng thời gật đầu."),t("br"),t("br"),n._v("“Đi thôi!” Mộc sư gật đầu, mang theo tất cả trưởng lão thẳng tắp hướng Vân Vụ lĩnh bay đi."),t("br"),t("br"),n._v("“Mau nhìn, đó là cái gì?”"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, mọi người liền thấy vô số linh thú, tích tụ chân núi, nguyên một đám nằm rạp trên mặt đất, ánh mắt bên trong mang theo nhu hòa, lại không còn trước đó hung ác cùng dữ tợn."),t("br"),t("br"),n._v("Linh thú phía trước nhất, một thanh niên đạp ở một vị Thánh vực Vương Giả sống lưng bên trên, thanh âm nhàn nhạt vung rơi xuống dưới, bốn phía linh khí hội tụ, uyển nguyên một đám đóa hoa, từ không trung tản mát, nghe tất cả linh thú, như si như say."),t("br"),t("br"),n._v("Ngũ tinh danh sư mới có năng lực nói tụ linh khí, miệng phun hoa sen!"),t("br"),t("br"),n._v("“Đây là... Tại cho linh thú giảng bài?”"),t("br"),t("br"),n._v("Tất cả danh sư ngẩn ngơ."),t("br"),t("br"),n._v("Gặp qua cho học sinh, người tu luyện giảng bài, còn là lần đầu tiên gặp cho linh thú giảng bài."),t("br"),t("br"),n._v("Mấu chốt nhất là, những linh thú này thế mà nguyên một đám nghiêm túc nghe giảng, thậm chí còn có chút dùng cái mũi, móng vuốt trên mặt đất làm bút ký..."),t("br"),t("br"),n._v("“... Tốt, chương trình học hôm nay liền tới đây, chư vị tất cả giải tán đi!”"),t("br"),t("br"),n._v("Đang đang khiếp sợ, liền nghe đến Trương Huyền thanh âm truyền tới."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Nghe nói như thế, tất cả linh thú chẳng những không có tản ra, ngược lại tất cả đều giương đầu lên sọ, thanh âm xông thẳng lên trời, ngay sau đó từng đầu từ dưới đất bò dậy, từng người mi tâm hiện lên một giọt máu tươi, thẳng tắp hướng Trương Huyền bay tới."),t("br"),t("br"),n._v("“Bà mẹ nó...”"),t("br"),t("br"),n._v("Nhìn xem đầy trời máu tươi, muốn đem hắn chôn, Trương Huyền thân thể nhoáng một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Ta nhưng mà tùy tiện giảng bài, lại không để các ngươi nhận chủ..."),t("br"),t("br"),n._v("Lại nói, coi như nhận chủ, cũng không trở thành cướp tới đi, tiếp cận mười vạn đầu linh thú, mỗi người một giọt máu... Đầy đủ làm cái bể bơi..."),t("br"),t("br"),n._v("Hắn phát điên, Mộc sư, tất cả trưởng lão càng là tất cả đều muốn điên rồi."),t("br"),t("br"),n._v("Viện trưởng không chỉ thu phục mười đại vương giả, liền Vân Vụ lĩnh tất cả linh thú, đều thuần phục."),t("br"),t("br"),n._v("Mười vạn linh thú, ra lệnh một tiếng, coi như Hồng Viễn đế quốc dạng này nhất đẳng đế quốc, cũng muốn trong nháy mắt rơi vào tay giặc!"),t("br"),t("br"),n._v("“Một cái để nhiều như vậy linh thú nhận chủ, giải quyết nhiều như vậy lớn tai hoạ ngầm... Cái này, cái này... Công lao lớn không có cách nào cân nhắc, ta phải bẩm báo Thú đường!”"),t("br"),t("br"),n._v("Mi trưởng lão run rẩy."),t("br"),t("br"),n._v("Bài học phía dưới, Vân Vụ lĩnh tất cả linh thú trực tiếp nhận chủ, có thể nói tiền vô cổ nhân, hậu vô lai giả... Công lao thực sự quá lớn!"),t("br"),t("br"),n._v("Lại thêm thuần phục mười đại vương giả, vô số Tòng Thánh, Bán Thánh, giải quyết học viện tai hoạ ngầm..."),t("br"),t("br"),n._v("Muốn trước khi nói, hắn nhưng mà bình thường Thất Tinh thuần thú sư, không có bất kỳ cái gì cống hiến, mà bây giờ công lao chi đại... Đều không dám nghĩ!"),t("br"),t("br"),n._v("Thật đúng là không ra tay thì thôi, vừa ra tay... Làm cho tất cả mọi người nổi điên!"),t("br"),t("br"),n._v("“May mắn ta thông minh, hỗ trợ xin Thất Tinh thuần thú sư, không có để hắn tới khảo hạch, nếu không toàn bộ thuần thú học viện... Ai biết lại biến thành dạng gì!”"),t("br"),t("br"),n._v("Khiếp sợ đồng thời, Mi trưởng lão cũng thở dài một hơi."),t("br"),t("br"),n._v("Nhìn thấy luyện khí học viện, y sư học viện, luyện đan sư học viện hình, sợ cái này mới Nhâm viện trưởng, sẽ tới khảo hạch thuần thú sư, cho nên liền trước thời hạn xin, hiện tại xem ra đây tuyệt đối là sáng suốt nhất cách làm."),t("br"),t("br"),n._v("Nếu không, toàn bộ học viện tất cả linh thú, Thánh thú, đều nhận hắn làm chủ... Cái khác thuần thú sư còn không vỡ tổ ah!"),t("br"),t("br"),n._v("“Đi qua đi...”"),t("br"),t("br"),n._v("Đem khiếp sợ trong lòng toàn bộ ngăn chặn, mọi người lúc này mới đồng loạt bay đi."),t("br"),t("br"),n._v("Mới đi đến trước mặt, liền nhìn thanh niên mãn thị muốn khóc biểu lộ."),t("br"),t("br"),n._v("“Gặp qua viện trưởng!” Mọi người ôm quyền."),t("br"),t("br"),n._v("“Các ngươi đã tới... Những linh thú này, đều điên rồi sao? Nhất định muốn nhận ta làm chủ... Mi trưởng lão, ngươi là thuần thú học viện viện trưởng, có thể có biện pháp nào cắt đứt ư?”"),t("br"),t("br"),n._v("Giống như là thấy được cứu tinh, Trương Huyền hiện cả ra trước mắt thấu đỏ nhìn qua."),t("br"),t("br"),n._v("Hắn thật chỉ là giảng bài, muốn cho đám người kia nghe lời mà thôi, thật không muốn cho hắn nhận chủ ah!"),t("br"),t("br"),n._v("“Cắt đứt? Không có...” Mi trưởng lão liền vội vàng lắc đầu."),t("br"),t("br"),n._v("Những linh thú này, người khác muốn phục tùng một đầu cũng khó khăn, một cái toàn bộ thuần phục, thế mà còn bộ biểu tình này... Viện trưởng, ngươi làm như vậy, sẽ bị cái khác thuần thú sư đánh chết!"),t("br"),t("br"),n._v("Có nghe hay không, Trương Huyền một mặt bi phẫn, chỉ cảm thấy một ngụm máu tươi suýt phun ra, tiếng hô nhịn không được vang lên."),t("br"),t("br"),n._v("“Nghiệp chướng ah!”"),t("br"),t("br"),n._v("PS: Đại hài tử cảm cúm, trẻ con viêm phổi nằm viện, lão nhai tận lực duy trì không đứt chương, thời gian đổi mới liền không nhất định, thật có lỗi. Ta tình nguyện ta bị bệnh cũng không muốn hài tử chịu tội, mọi người thứ lỗi!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-896"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-898"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);