(window.webpackJsonp=window.webpackJsonp||[]).push([[2185],{2540:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-890-lao-vien-truong-nguyen-nhan-cai-chet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-890-lao-vien-truong-nguyen-nhan-cai-chet"}},[n._v("#")]),n._v(" Chương 890: Lão viện trưởng nguyên nhân cái chết")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 890: Lão viện trưởng nguyên nhân cái chết"),t("br"),t("br"),n._v("Trở lại chỗ ở của mình, Trương Huyền cái này mới phát giác được toàn thân mệt mỏi."),t("br"),t("br"),n._v("Theo tìm kiếm Xích Huỳnh quả trở về đến bây giờ, gần hai ngày hai đêm, hầu như một cái đều không có nghỉ ngơi, nhất là tại Vân Vụ lĩnh thuần phục tất cả linh thú Thánh thú, đối hao tổn vô hình cực lớn."),t("br"),t("br"),n._v("Nếu không phải hồn thể to lớn, căn bản không kiên trì nổi, dù vậy, trầm tĩnh lại, cũng cảm thấy đầu từng đợt kịch liệt đau đớn."),t("br"),t("br"),n._v("“Ngoan Nhân ngủ say phía trước, đem đầu kia Thanh Giác Long thú Vu hồn, giúp ta luyện chế ra một lần, không còn tự mình ý thức, vừa vặn có thể khôi phục tổn thương hồn phách!”"),t("br"),t("br"),n._v("Thở ra một hơi, Trương Huyền cổ tay khẽ đảo, một cái tinh khiết hồn phách xuất hiện ở trước mắt, tinh thần khẽ động, Vu hồn ly thể."),t("br"),t("br"),n._v("Vận chuyển pháp quyết, đối lên trước mắt hồn phách, thôn phệ đi qua."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Thanh Giác Long thú tinh thuần hồn phách, hóa thành cùng nhau ấm áp dòng suối, tư dưỡng hắn bị hao tổn Vu hồn, để hắn trở nên càng thêm cường đại, càng thêm mượt mà, càng thêm óng ánh."),t("br"),t("br"),n._v("Không lâu sau, liền hoàn toàn khôi phục, hơn nữa trở nên càng thêm lớn mạnh."),t("br"),t("br"),n._v("Thanh Giác Long thú Vu hồn, mặc dù chỉ là không trọn vẹn một bộ phận, cũng không hoàn chỉnh, nhưng dù sao đạt đến Thánh vực ngũ trọng, lực lượng tinh thuần, linh thạch thượng phẩm so sánh cùng nhau, đều kém xa tít tắp."),t("br"),t("br"),n._v("“Đáng tiếc không có Tằm Phong cảnh pháp quyết tu luyện... Nếu không, Vu hồn tất nhiên có thể tiến thêm một bước!”"),t("br"),t("br"),n._v("Thôn phệ một hồi, cảm thấy đạt đến bão hòa, Trương Huyền đành phải lắc đầu đem cái này tinh thuần hồn phách, một lần nữa thu vào."),t("br"),t("br"),n._v("[ truyeｎ cu"),t("br"),n._v("a tui ʘʘ vn ]  Hắn hiện tại Vu hồn, chỉ có Hóa Phàm bát trọng Đạp Hư cảnh công pháp, Tằm Phong cảnh còn không có sửa sang lại, coi như muốn tu luyện, cũng không có cách nào."),t("br"),t("br"),n._v("Trừ phi tăng lớn hồn phách thể tích, nhưng thật muốn làm như vậy, chỉ sợ lại sẽ xuất hiện tại Hóa Thanh trì cục diện khó xử, thân thể cùng hồn thể không xứng, đến lúc đó thực lực chẳng những không có tăng trưởng, ngược lại sẽ trở nên yếu hơn."),t("br"),t("br"),n._v("“Đáng tiếc Vân Vụ lĩnh là linh thú chỗ, không có nhân loại tu luyện công pháp, nếu không, tối nay đến có thể xung kích Bán Thánh thử xem...”"),t("br"),t("br"),n._v("Hoàng Kim Hùng Hổ thú trữ vật giới chỉ bên trong, nắm giữ đủ loại bảo vật, duy chỉ có không có tu luyện công pháp."),t("br"),t("br"),n._v("Linh thú, Thánh thú pháp quyết tu luyện, cùng nhân loại khác biệt, coi như giữ lại cũng vô dụng, cho nên, căn bản là không có chứa đựng."),t("br"),t("br"),n._v("Nếu không công pháp gom góp, khoảng cách hừng đông còn có một số thời gian, hoàn toàn có thể trùng kích vào Bán Thánh thử xem."),t("br"),t("br"),n._v("“Được rồi, hừng đông chính là kế vị đại điển, chờ thành viện trưởng, tất cả thư tịch có thể tự do quan sát, hoàn toàn có thể đem Bán Thánh, Tòng Thánh, cùng còn lại công pháp toàn bộ sưu tập tới...”"),t("br"),t("br"),n._v("Thở ra một hơi, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Đổi lại trước đó, không có công pháp, khẳng định Vu hồn ly thể, tìm kiếm khắp nơi, mà bây giờ lĩnh ngộ sư giả chi tâm, lại thêm ngày mai liền trở thành chân chính viện trưởng, không cần thiết lại tiếp tục chơi đùa."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Đang định nghỉ ngơi một chút, liền nghe đến ngoài cửa sổ Ngụy Như Yên tiếng âm vang lên."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Nhướng mày, Trương Huyền đi ra ngoài."),t("br"),t("br"),n._v("Để hắn vì phụ thân báo thù, chẳng lẽ còn không nhịn xuống tay hay sao?"),t("br"),t("br"),n._v("“Lão sư, vừa rồi ta muốn chém giết Vưu Hư, hắn nói ra một số việc, liên quan danh sư học viện, ta không dám làm chủ, chuyên tới để hướng ngươi bẩm báo!” Ngụy Như Yên nói."),t("br"),t("br"),n._v("“Chuyện gì!” Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Vị này Vưu Hư, trước đó liền cảm thấy có vấn đề, đã trải qua Bồ Đề thụ trúng độc chuyện này, càng cảm thấy không có đơn giản như vậy."),t("br"),t("br"),n._v("Bất quá, nếu là Ngụy Như Yên cừu nhân, tự nhiên do nàng xử trí, không nghĩ tới thật để cho hắn hỏi một số việc."),t("br"),t("br"),n._v("“Hình như liên quan lão viện trưởng chân chính nguyên nhân cái chết...” Ngụy Như Yên vẻ mặt nghiêm túc: “Bất quá, hắn không có nói tỉ mỉ, mong rằng lão sư đi qua tự mình hỏi thăm!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu, vừa định đi, chần chờ một chút, tinh thần khẽ động, Tử Dương thú xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Cái tên này đã từng là lão viện trưởng thú sủng, đối hắn trung thành tuyệt đối, gượng ép lão viện trưởng nguyên nhân cái chết, khẳng định muốn để nó chính tai nghe nói."),t("br"),t("br"),n._v("Hai người một thú, rất mau tới đến Vưu Hư vị trí sân nhỏ."),t("br"),t("br"),n._v("Thời khắc này Vưu Hư, vẻ mặt trắng bệch, giống như là gặp quái vật, thân thể không ngừng run rẩy."),t("br"),t("br"),n._v("“Ngươi cho hắn hạ độc?” Nhìn thấy đối phương bộ dáng này, Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Thân là dùng độc mọi người, hắn tự nhiên có thể nhìn ra, thời khắc này Vưu Hư, cũng không phải là đơn thuần sợ hãi, mà là thân trúng kịch độc."),t("br"),t("br"),n._v("Ngụy Như Yên là Tiên Thiên độc hồn thể, muốn độc chết một cái đã phong ấn tu vi danh sư, vẫn là dễ như trở bàn tay."),t("br"),t("br"),n._v("“Vâng!” Ngụy Như Yên gật đầu."),t("br"),t("br"),n._v("Mặc dù đối thể chất của mình, không hề biết hiểu rất rõ, nhưng có thể đem người hạ độc chết sự tình, vẫn là rất rõ ràng."),t("br"),t("br"),n._v("Cái tên này nếu là hại cha mình tử vong hung thủ, tự nhiên không thể tuỳ tiện buông tha, muốn dùng sức dằn vặt mới được."),t("br"),t("br"),n._v("Nhưng mà không nghĩ tới nhanh như vậy chịu không nổi dằn vặt, nói ra để nàng đều khiếp sợ không thôi bí mật, lúc này mới vội vàng qua đến tìm kiếm lão sư, muốn cho hắn tiến hành quyết định."),t("br"),t("br"),n._v("“Có phải hay không... Không đúng? Vậy ta lần sau... Không dám!” Sợ lão sư tức giận, Ngụy Như Yên nhìn qua."),t("br"),t("br"),n._v("“Không có việc gì, làm không tệ!” Cười cười, Trương Huyền thoả mãn nhẹ gật đầu."),t("br"),t("br"),n._v("Biết mình sở trường là cái gì, lại giỏi về lợi dụng, mới có tư cách làm học sinh của mình."),t("br"),t("br"),n._v("“Vưu phó viện trưởng, chúng ta lại gặp mặt, lão viện trưởng đến cùng là chuyện gì xảy ra? Nói ra đi!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, sư ngôn thiên bẩm vận chuyển, Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Hắn hiện tại tâm cảnh khắc độ, đã đạt đến 2 1.1, có thể so với thất tinh danh sư, lại thêm sư giả chi tâm, hoàn toàn có thể nhẹ nhõm, để hắn ngoan ngoãn nói thật."),t("br"),t("br"),n._v("“Là ta nhất thời hồ đồ!” Ánh mắt dần dần mê ly, Vưu Hư nhịn không được mở miệng."),t("br"),t("br"),n._v("“Hai năm trước, lão viện trưởng triệu tập trong học viện tất cả trưởng lão, nói phát hiện một chỗ di tích thời thượng cổ, cái này di tích khả năng dính dấp một cái to lớn bí mật, cần tiến đến dò xét. Lúc ấy ta cùng hơn hai mươi vị trưởng lão, nhận được mệnh lệnh, cùng một chỗ đi tới!”"),t("br"),t("br"),n._v("“Ai biết vừa tiến vào di tích không lâu, liền gặp không gian phong bạo, đem mọi người thổi tan, ta lâm vào một chỗ chồng chất không gian bên trong, bị Dị Linh tộc nhân bắt lấy...”"),t("br"),n._v(" “Bọn họ cho ta một bình dược vật, buộc ta cho lão viện trưởng đám người sử dụng...”"),t("br"),t("br"),n._v("“Ta thật sự không có ý định hại người, nhưng mà bọn họ tại ta trên thân đã hạ rất lợi hại nguyền rủa, ta cũng là bị ép buộc...”"),t("br"),t("br"),n._v("Vưu Hư kỹ càng giảng giải một lần, vừa nói bên cạnh khóc ròng ròng."),t("br"),t("br"),n._v("“Nhưng mà trước ngươi dùng để so với ta đấu cái kia độc dược?” Trương Huyền vẻ mặt khó coi."),t("br"),t("br"),n._v("“Vâng!” Vưu Hư gật đầu."),t("br"),t("br"),n._v("“Đáng giận...”"),t("br"),t("br"),n._v("Răng cắn đến khanh khách vang vọng, Tử Dương thú tròng mắt đỏ hoe, thân thể khổng lồ không ngừng run rẩy, tức giận đến suýt nổ."),t("br"),t("br"),n._v("Lão chủ nhân đối với hắn tốt như vậy, không nghĩ tới, thế mà bị hạ độc hại chết!"),t("br"),t("br"),n._v("Nếu như không phải cái tên này, coi như chỗ kia di tích thật sự có nguy hiểm, cũng không có khả năng toàn quân bị diệt, đến cuối cùng cũng chưa trở lại."),t("br"),t("br"),n._v("“Thân là danh sư, không bảo vệ đồng loại, nhưng quy thuận Dị Linh tộc nhân, trợ Trụ vi ngược, tàn sát đồng bạn, ngươi còn có mặt mũi nào sống sót?”"),t("br"),t("br"),n._v("Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Đối phương cái kia độc dược, hắn tự mình thử qua, không có giải dược lời nói, liền xem như Thánh vực nhất trọng cường giả đỉnh phong cũng có thể nhẹ nhõm độc chết."),t("br"),t("br"),n._v("Lão viện trưởng thực lực mặc dù không yếu, nhưng cũng không có đi đến Thánh vực nhị trọng, trúng độc sau lại bị Dị Linh tộc nhân vây công, làm sao có thể sống được xuống?"),t("br"),t("br"),n._v("Có thể nói, vị này Vưu Hư, quả thực liền là nhân tộc bại hoại, danh sư bên trong sỉ nhục!"),t("br"),t("br"),n._v("“Hoàng thất Thánh vực Bồ Đề thụ, có phải hay không cũng bị ngươi hạ độc?” Trách mắng một tiếng, Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("“Vâng!” Vưu Hư gật đầu."),t("br"),t("br"),n._v("“Vì cái gì?”"),t("br"),t("br"),n._v("Thánh vực Bồ Đề thụ, cùng Dị Linh tộc không có bất kỳ quan hệ gì, nhưng mà hoàng thất dùng để tăng tiến linh hồn đồ vật, tại sao phải hạ độc hại chết?"),t("br"),t("br"),n._v("“Nguyên nhân cụ thể, ta thật không biết, là bọn họ muốn ta làm như vậy, chỉ cần hoàn thành, liền giúp ta kéo dài 50 năm tuổi thọ...” Vưu Hư không dám giấu diếm."),t("br"),t("br"),n._v("Hắn thân trúng kịch độc, thực lực đã trăm không còn một, hiện tại lại trúng Trương Huyền sư ngôn thiên bẩm, rất nhanh liền đem biết đến toàn bộ nói ra."),t("br"),t("br"),n._v("Vị này Vưu Hư, tuổi thọ đến cuối cùng, càng già càng sợ chết, bị Dị Linh tộc nhân bắt lấy về sau, hứa hẹn giúp hắn kéo dài tuổi thọ, lại thêm đủ loại uy hiếp, tự nhiên là hoàn toàn hàng phục."),t("br"),t("br"),n._v("Mặc dù ở sâu trong nội tâm, cũng biết như vậy là không đúng, nhưng càng đi càng xa, đã không cách nào quay đầu lại."),t("br"),t("br"),n._v("Còn lão viện trưởng chỗ đi di tích, hắn vừa tiến vào, liền bị Dị Linh tộc nhân bắt đi, tình huống cụ thể cũng nói không rõ ràng, hỏi một hồi, cũng không hỏi ra như thế về sau."),t("br"),t("br"),n._v("Độc chết Bồ Đề thụ, là bị mệnh lệnh, cụ thể nguyên do, nhưng mà cái khôi lỗi, cũng không có tư cách biết."),t("br"),t("br"),n._v("“Chủ nhân, có thể hay không để cho ta giết cái này nhân tộc bại hoại?”"),t("br"),t("br"),n._v("Thấy thẩm vấn xong, Tử Dương thú cũng nhịn không được nữa, cắn răng nói."),t("br"),t("br"),n._v("“Hắn nhất định phải chết, nhưng mà liên quan nhiều như vậy, không thể chết tại chúng ta trên tay! Ta sẽ để Mộc sư đem hắn mang về tổng bộ... Yên tâm đi, Danh Sư đường đối với phản đồ xử phạt, là mười phần nghiêm khắc, hắn đem tiếp nhận so tử vong còn muốn đau trải qua!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Nếu như cái này Vưu Hư, không liên quan những vấn đề này, đã giết thì đã giết, nhưng mà liên quan lão viện trưởng chết đi, cùng Dị Linh tộc, liền không thể đơn thuần giết sạch chi."),t("br"),t("br"),n._v("Khẳng định muốn báo cáo Danh Sư đường tổng bộ, để cho bọn họ tới tiến hành xử lý."),t("br"),t("br"),n._v("“Tốt!” Tử Dương thú gật đầu."),t("br"),t("br"),n._v("Mặc dù rất muốn giết cái tên này, nhưng mà thân là lão viện trưởng thú sủng, đối danh sư quy củ biết rất nhiều, đem hắn chém giết, đối cái tên này tới nói, thực sự quá tiện nghi."),t("br"),t("br"),n._v("Đưa vào tổng bộ, vĩnh viễn điêu khắc lấy sỉ nhục, mới là lớn nhất trừng phạt."),t("br"),t("br"),n._v("“Ngươi thấy thế nào?”"),t("br"),t("br"),n._v("Trương Huyền hướng Ngụy Như Yên nhìn lại."),t("br"),t("br"),n._v("“Hết thảy nghe theo lão sư an bài!” Ngụy Như Yên gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi!” Trương Huyền nhẹ gật đầu: “Ta hiện tại liền liên hệ Mộc sư!”"),t("br"),t("br"),n._v("Nói xong, đem Tôn Cường kêu tới, dặn dò một tiếng."),t("br"),t("br"),n._v("Người sau lập tức đi ra ngoài, không đến mười phút đồng hồ, Mộc sư liền bay tới, rơi vào trong viện."),t("br"),t("br"),n._v("“Sư thúc!”"),t("br"),t("br"),n._v("Vừa thấy mặt, Mộc sư ôm quyền."),t("br"),t("br"),n._v("“Vị này Vưu Hư, phản bội nhân tộc, mưu hại danh sư học viện lão viện trưởng, hi vọng ngươi có thể mang về tổng bộ thẩm vấn!”"),t("br"),t("br"),n._v("Trương Huyền chỉ tay."),t("br"),t("br"),n._v("“Phản bội nhân tộc?” Mộc sư con ngươi co rụt lại: “Sư thúc xác định?”"),t("br"),t("br"),n._v("Cái tội danh này cực lớn, nhất là đối với danh sư, một khi xác lập, hủy một đời không nói, còn sẽ gặp phải tất cả mọi người phỉ nhổ."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, ta lập tức xử lý!”"),t("br"),t("br"),n._v("Mộc sư biết chuyện này không dám trì hoãn, bàn tay một trảo, đem Vưu Hư dùng chân khí vây khốn nhấc lên: “Ta hiện tại liền dẫn hắn quay về tổng bộ, sư thúc kế vị lễ, khả năng liền không tham gia được...”"),t("br"),t("br"),n._v("Viện trưởng đã xác nhận, kế vị lễ nhưng mà cái nghi thức thôi, hắn tham gia không tham gia đều râu ria."),t("br"),t("br"),n._v("Trọng yếu là trước đem cái này nhân tộc phản đồ mang về."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Mộc sư không nói thêm lời, thân thể nhảy lên, thẳng tắp hướng nơi xa bay đi, rất nhanh biến mất tại bầu trời đêm."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-898"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-900"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);