(window.webpackJsonp=window.webpackJsonp||[]).push([[2186],{2541:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-5-đai-tieu-thu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-5-đai-tieu-thu"}},[n._v("#")]),n._v(" Chương 5: Đại tiểu thư")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Nghe nói không? Tào Hùng lão sư mới vừa rồi cùng học viện thứ nhất đếm ngược Trương Huyền lão sư tỷ thí!”"),i("br"),i("br"),n._v("“Bọn họ tỷ thí? Vậy cái này Trương Huyền lão sư không phải chờ thua sao?”"),i("br"),i("br"),n._v("“Vẫn đúng là không phải, Trương Huyền lão sư thắng! Hắn tùy tiện chỉ điểm một người học viên, lại để cho sức mạnh chợt tăng trăm phần trăm!”"),i("br"),i("br"),n._v("“Tăng cường trăm phần trăm? Một lần chỉ điểm? Coi như Lục Tầm lão sư cũng không làm được đi! Thật hay giả?”"),i("br"),i("br"),n._v("“Rất nhiều người đều thấy được, làm sao có thể là giả?”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Trong phòng ăn không ít người đem hai người tỷ thí thấy được, từng cái từng cái hưng phấn nghị luận."),i("br"),i("br"),n._v("“Một lần chỉ điểm, tăng cường trăm phần trăm sức mạnh?” Triệu Nhã tú mũi nhíu lên, tràn đầy xem thường: “Cái tên này khẳng định là vận khí!”"),i("br"),i("br"),n._v("Triệu Nhã là Thiên Huyền vương quốc thành thị lớn thứ ba, bạch ngọc thành thành chủ con gái, từ nhỏ liền được hài lòng giáo dục, lần này Hồng Thiên học viện mục đích chủ yếu chính là bái Lục Tầm vi sư."),i("br"),i("br"),n._v("Không nghĩ tới còn chưa có đi tìm, liền nghe đến mọi người nghị luận Lục Tầm lão sư cũng không bằng, đương nhiên không tin."),i("br"),i("br"),n._v("“Vận khí? Ta cảm thấy không phải vận khí, đây chính là trăm phần trăm tăng phúc! Chỉ dựa vào vận khí e sợ không được đi!” Ngồi ở nàng cách đó không xa một người học viên, không nhịn được phản bác."),i("br"),i("br"),n._v("“Không phải vận khí? Ngươi đừng cho là ta không biết cái này Trương Huyền lão sư, lần trước thầy giáo sát hạch được không điểm, kém chút liền bị khai trừ! Ngươi nói người như thế có thể có bản lãnh gì? Không tin ta hiện tại liền đi vạch trần cho các ngươi nhìn!”"),i("br"),i("br"),n._v("Triệu Nhã tính cách luôn luôn nôn nóng, nghe có người khen thứ nhất đếm ngược lão sư, lập tức nhịn không được."),i("br"),i("br"),n._v("“Tốt! Vừa vặn chúng ta cũng nghĩ nhìn, cái này lão sư có phải hay không chỉ là hư danh!”"),i("br"),i("br"),n._v("Nhất thời lại có hai cái học viên đứng dậy."),i("br"),i("br"),n._v("Ba người đi ra căng tin, tìm người hỏi thăm một chút, rất mau tìm đến Trương Huyền vị trí lớp học, đẩy cửa đi vào."),i("br"),i("br"),n._v("“Ngươi chính là Trương Huyền lão sư?”"),i("br"),i("br"),n._v("Đi vào lớp học, lập tức nhìn thấy ngồi ở chủ vị thanh niên, giờ khắc này chính một mặt ngốc cười, vô luận từ phương diện nào nhìn, đều không giống một vị lợi hại lão sư, thậm chí cảm giác lên đều có chút không bình thường."),i("br"),i("br"),n._v("“Là ta!”"),i("br"),i("br"),n._v("Thấy có học sinh đi vào,"),i("br"),i("br"),n._v("Trương Huyền đình chỉ tiếp tục quan sát ngày đến thư viện, giương mắt nhìn sang."),i("br"),i("br"),n._v("“Nghe nói ngươi vừa vượt qua Tào Hùng lão sư, chỉ điểm một vị học viên, để hắn có trăm phần trăm sức mạnh tăng phúc?” Triệu Nhã một bộ không tin dáng dấp: “Vậy thì tốt, ngươi cũng giúp ta nhìn, nhìn ngươi chỉ điểm một chút, cũng có thể hay không để cho thực lực của ta tăng lên dữ dội gấp đôi!”"),i("br"),i("br"),n._v("“Không rảnh!”"),i("br"),i("br"),n._v("Trương Huyền xua tay."),i("br"),i("br"),n._v("Lão sư có sư đạo tôn nghiêm, cũng không phải trêu chọc, ngươi muốn nhìn liền nhìn, muốn cho ta chỉ điểm liền chỉ điểm."),i("br"),i("br"),n._v("Lại nói, ngươi cái kia thái độ tính là gì? Lão tử lại không nợ ngươi một phần."),i("br"),i("br"),n._v("“Không rảnh? Ngươi bây giờ không phải là nhàn rỗi sao?” Triệu Nhã rõ ràng nhìn ra đối phương qua loa, cắn răng hừ nói."),i("br"),i("br"),n._v("Vừa cùng bằng hữu nói muốn tới vạch trần đối phương, ngay cả nhìn cũng chưa từng nhìn liền đi ra ngoài, mất mặt là nàng."),i("br"),i("br"),n._v("“Ta muốn vời học sinh, không có rảnh cùng một cái không biết trời cao đất rộng bé gái làm loạn!” Trương Huyền lạnh nhạt nói."),i("br"),i("br"),n._v("“Ngươi...” Triệu Nhã tức giận tú mục xoay loạn."),i("br"),i("br"),n._v("Nàng là thành chủ con gái, bản thân lại cực kỳ đẹp đẽ, đi tới nơi nào đều bị người ngưỡng mộ ánh mắt, cái tên này ngược lại tốt, không chỉ từ chối tự mình, còn nói tự mình làm loạn, quả thực đáng ghét!"),i("br"),i("br"),n._v("Coi như là lão sư, cũng không thể tha thứ!"),i("br"),i("br"),n._v("“Ngươi muốn như thế nào mới có thể giúp ta chỉ điểm?” Răng ngà cắn chặt, Triệu Nhã nói."),i("br"),i("br"),n._v("“Bái ta làm thầy!” Trương Huyền lạnh nhạt nói, một mặt cổ quái nhìn xưa nay."),i("br"),i("br"),n._v("Thấy đối phương nhìn thằng ngốc như thế ánh mắt, Triệu Nhã tức giận một cái lảo đảo: “Tốt, ta bái ngươi làm thầy, bất quá... Ngươi muốn lung tung chỉ điểm, chỉ cho ta điểm sai rồi, có tin ta hay không vậy thì đem ngươi gạt người xiếc lộ ra ánh sáng?”"),i("br"),i("br"),n._v("“Triệu Nhã!”"),i("br"),i("br"),n._v("“Không thể, một khi bái ông ta làm thầy, ngươi liền không thể lại bái Lục Tầm lão sư...”"),i("br"),i("br"),n._v("Cùng với nàng tới hai cái bằng hữu, nghe nàng trực tiếp đáp ứng, kém chút không dọa ngất đi qua, vội vã khuyên can."),i("br"),i("br"),n._v("Nghe được hai cái bằng hữu khuyên can, Triệu Nhã do dự."),i("br"),i("br"),n._v("Quy củ của học viện nàng là biết đến, lạy người khác vi sư, muốn lại bái một vị khác, nhất định phải lui đi chương trình học, mà một khi làm như thế, một cái khác lão sư cơ bản cũng sẽ không thu lấy, huống chi Lục Tầm dạng này đứng đầu giáo sư."),i("br"),i("br"),n._v("“Không dám? Không dám liền đi ra ngoài đi, không muốn làm lỡ ta làm ăn... Chiêu thu mới học sinh!” Trương Huyền vung vung tay."),i("br"),i("br"),n._v("“Ai nói ta không dám?”"),i("br"),i("br"),n._v("Nguyên bản còn đang do dự, nghe được Trương Huyền lời này, Triệu Nhã lập tức vỡ tổ, tú mi hơi giương: “Ta hiện tại liền bái ngươi làm thầy! Thân phận xác nhận đi!”"),i("br"),i("br"),n._v("“Thái độ quá kém, ngươi muốn bái, ta còn không muốn thu đây!” Trương Huyền xua tay."),i("br"),i("br"),n._v("Chiêu học sinh đương nhiên muốn vời nghe lời, giống trước Vương Dĩnh như vậy, hắn liền khá là yêu thích, trước mắt cái này, vừa đến rồi cùng ăn thuốc súng giống như vậy, coi như thiếu học sinh, cũng không muốn."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Không nghĩ tới tự mình cắn răng muốn bái ông ta làm thầy, hắn còn không thu, Triệu Nhã tức giận bốc khói trên đầu."),i("br"),i("br"),n._v("Bất quá, đặc biệt tới vạch trần đối phương, hiện tại thật muốn tức giận xoay người rời đi, chẳng phải được đối phương ý?"),i("br"),i("br"),n._v("Khả năng cái tên này vừa muốn đem tự mình đánh đuổi, như vậy lời nói dối cũng không cần bị vạch trần."),i("br"),i("br"),n._v("Nhất định không thể để cho ngươi được toại nguyện!"),i("br"),i("br"),n._v("“Hừ, quá mức trước tiên nhận sai, chờ vạch trần ngươi diện mục chân thật về sau, xem ta như thế nào giáo huấn ngươi!”"),i("br"),n._v(" Nghĩ tới đây, Triệu Nhã đè nén lửa giận trong lòng, khẽ mỉm cười, lộ ra trắng nõn hàm răng: “Lão sư chớ trách, là tiểu nữ tử thất lễ! Ta là thành tâm bái ngươi làm thầy, kính xin lão sư nhận lấy!”"),i("br"),i("br"),n._v("“Này còn tạm được!” Thấy nàng thái độ thay đổi, Trương Huyền lúc này mới gật gù: “Muốn bái ta làm thầy cũng được, đem căn phòng này quét dọn sạch sẽ, ta không cần có một chút tro bụi, còn có phía ngoài phòng vệ sinh cũng thu thập một chút, bồn cầu cọ rửa sạch sẽ, ta kiểm tra hợp lệ, để cho ta hài lòng, sẽ thu ngươi làm học sinh!”"),i("br"),i("br"),n._v("“Chớ quá mức...”"),i("br"),i("br"),n._v("Triệu Nhã phát điên."),i("br"),i("br"),n._v("Nàng là ai? Thành chủ con gái, thiên kim đại tiểu thư, từ nhỏ đến lớn, mười ngón không dính nước mùa xuân, cái tên này lại làm cho nàng quét tước vệ sinh? Còn... Thu thập phòng vệ sinh? Tẩy bồn cầu?"),i("br"),i("br"),n._v("Ngươi sắp điên a?"),i("br"),i("br"),n._v("“Như thế thô thiển sống cũng không làm được, vậy thì đi thôi, môn hạ ta không muốn lười biếng, người vô dụng!” Trương Huyền xua tay."),i("br"),i("br"),n._v("Khà khà, nha đầu, muốn cùng ta chơi, ngươi còn non lắm!"),i("br"),i("br"),n._v("“Ai nói ta vô dụng? Ta gọi ngay bây giờ quét, liền cọ rửa!” Triệu Nhã cắn răng, xoay người liền mang tới cái chổi cùng cây lau nhà, ở trong phòng thu thập."),i("br"),i("br"),n._v("“Triệu Nhã, nếu không chúng ta thì thôi...”"),i("br"),i("br"),n._v("“Ta nhìn hắn khả năng thật không có bản lãnh gì, là cố ý làm khó dễ ngươi đây...”"),i("br"),i("br"),n._v("Cùng đi hai cái bằng hữu, thấy đường đường thành chủ Đại tiểu thư, thật sự quét tước vệ sinh, toàn giật nảy mình, vội vàng khuyên can."),i("br"),i("br"),n._v("“Các ngươi đi ra ngoài trước chờ, ta Triệu Nhã lớn như vậy còn không có bị thiệt thòi, ngày hôm nay ta hãy cùng hắn gây gổ lên!” Triệu Nhã lông mày giương lên."),i("br"),i("br"),n._v("“Chuyện này...”"),i("br"),i("br"),n._v("Hai vị này bằng hữu, biết Triệu Nhã tính khí, thấy nàng không cách nào khuyên can, tất cả đều hai mặt nhìn nhau, không biết nên làm gì."),i("br"),i("br"),n._v("“Đi thông báo Diêu Đại quản gia đi, hắn không phải cũng tới sao? Nếu như cho Đại quản gia biết một sư tư sát hạch thứ nhất đếm ngược gia hỏa, sai khiến tiểu thư lau bồn cầu, nhất định sẽ ra tay ngăn cản!”"),i("br"),i("br"),n._v("Nhìn nhau một hồi, một người bạn nghĩ ra biện pháp."),i("br"),i("br"),n._v("Triệu Nhã loại thân phận này, tuy rằng đến đây học viện không nguy hiểm gì, vẫn có người chuyên đưa tới, mà vị này tiễn đưa, chính là Diêu Đại quản gia, hiện tại nên còn không có rời đi."),i("br"),i("br"),n._v("“Tốt, chúng ta vậy thì đi tìm!”"),i("br"),i("br"),n._v("Hai người ăn nhịp với nhau, cũng không do dự, xoay người hướng học viện đi ra ngoài."),i("br"),i("br"),n._v("Triệu Nhã vị đại tiểu thư này cưỡng tính khí tới, tuy rằng chưa bao giờ trải qua việc nặng, vừa mới bắt đầu còn có chút luống cuống tay chân, bất quá thời gian không dài, liền triệt để học được, rất nhanh, bên trong gian phòng bên ngoài đều bị đánh quét sạch sành sanh, liền ngay cả phía ngoài phòng vệ sinh cũng rửa sạch khiết cực kỳ."),i("br"),i("br"),n._v("“Không sai!” Trương Huyền thoả mãn gật đầu."),i("br"),i("br"),n._v("“Hiện tại xong chưa, có thể thu ta vì là học sinh, chỉ điểm tu vi chứ?”"),i("br"),i("br"),n._v("Làm xong việc, Triệu Nhã hàm răng cắn “Khanh khách!” Vang vọng, mặt ngọc vặn vẹo, chỉ lo nhất thời không nhịn được, đem kẻ trước mắt này đánh."),i("br"),i("br"),n._v("“Hừm, thân phận nghiệm chứng đi!”"),i("br"),i("br"),n._v("Phảng phất không thấy đối phương hận không thể lột hắn da ánh mắt, Trương Huyền tiện tay ném ra một cái ngọc bài."),i("br"),i("br"),n._v("Hít sâu một hơi, Triệu Nhã nhỏ máu nhận chủ, xác nhận thân phận."),i("br"),i("br"),n._v("“Trương lão sư, ta hiện tại là học sinh của ngươi, có thể hay không chỉ cho ta điểm một, hai?”"),i("br"),i("br"),n._v("Thấy mục đích đạt đến, lập tức liền có thể vạch trần trước mắt cái này tiểu nhân chân chính sắc mặt, Triệu Nhã cố nén nội tâm hưng phấn, nói."),i("br"),i("br"),n._v("“Ngươi trước tiên đánh một bộ võ kỹ, để cho ta nhìn cơ sở làm sao!” Trương Huyền xua tay."),i("br"),i("br"),n._v("“Rõ!”"),i("br"),i("br"),n._v("Triệu Nhã cũng không phí lời, hai tay ôm một cái, bắt đầu đánh quyền."),i("br"),i("br"),n._v("Hô hô hô hô!"),i("br"),i("br"),n._v("Tiếng gió rít gào, kình lực mười phần, đều là nữ tử, nàng bất luận khí lực vẫn là chiêu số, đều so với Vương Dĩnh càng nhanh nhẹn hơn, có sức mạnh, vừa nhìn liền biết không ít bỏ công sức."),i("br"),i("br"),n._v("Trương Huyền nhìn nàng triển khai võ công, tinh thần nhưng câu Thông Thiên đến thư viện, quả nhiên, thư viện nhẹ nhàng chấn động, một quyển sách rơi xuống."),i("br"),i("br"),n._v("Bìa ngoài chính viết “Triệu Nhã” hai chữ."),i("br"),i("br"),n._v("“Triệu Nhã, bạch ngọc thành thành chủ con gái, võ giả một tầng tụ hơi thở cảnh đỉnh cao!”"),i("br"),i("br"),n._v("“Công pháp tu luyện: Bạch Ngọc Tố Nữ Công!”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Khuyết điểm: Hai mươi bảy nơi, một, tính cách kích động dễ tức giận, cùng bạch ngọc công bình thuận ấm áp dễ chịu vi phạm, không cách nào phát huy thực lực mạnh nhất; Thứ hai...”"),i("br"),i("br"),n._v("Thư tịch cùng trước nhìn thấy như thế, ghi chép Triệu Nhã trong tu luyện sai lầm chỗ cùng thiếu hụt."),i("br"),i("br"),n._v("Nhìn một chút, Trương Huyền một mặt quái lạ."),i("br"),i("br"),n._v("Oành! Oành! Oành!"),i("br"),i("br"),n._v("Chỉ chốc lát, Triệu Nhã đem một bộ quyền pháp đánh xong, đôi bàn tay trắng như phấn sờ một cái, xoay người rơi phòng học phụ trách phép đo lực trên vách đá!"),i("br"),i("br"),n._v("Một tiếng vang giòn, mặt trên cho thấy con số."),i("br"),i("br"),n._v("110!"),i("br"),i("br"),n._v("“Không tệ, không tệ!” Trương Huyền không tự chủ được gật đầu."),i("br"),i("br"),n._v("Còn không có tiến vào học viện hệ thống học tập, liền có thể một quyền đánh ra loại sức mạnh này, vị này Triệu Nhã không hổ là thành chủ con gái, thực lực phi phàm."),i("br"),i("br"),n._v("“Tốt, giúp ta chỉ điểm đi!”"),i("br"),i("br"),n._v("Một bộ võ kỹ đánh xong, mặt không đỏ không thở gấp, Triệu Nhã nhìn sang."),i("br"),i("br"),n._v("Convert by: Hoang Chau"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-8"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-10"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);