(window.webpackJsonp=window.webpackJsonp||[]).push([[2187],{2542:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-81-ta-muon-lui-khoa-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-81-ta-muon-lui-khoa-thuong"}},[n._v("#")]),n._v(" Chương 81: Ta muốn lùi khóa (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Hồng Thiên học viện, Lục Tầm lớp học."),t("br"),t("br"),n._v(" Làm như học viện minh tinh giáo sư, lớp học của hắn dài rộng có tới mấy trăm mét, khác nào một cái to lớn sân đá banh, bên trong học sinh đứng thành một hàng xếp hàng, càng là có mấy trăm."),t("br"),t("br"),n._v(" “Không hổ là Lục lão sư, ta ở bên ngoài nhưng là nghe nói, vô số học viên đều lấy bái ngươi làm vinh, từng cái từng cái đánh vỡ đầu!” Nhìn trong lớp nhiều như vậy học viên, một ông già không tự chủ được vuốt râu, nhìn về phía cách đó không xa một người thanh niên."),t("br"),t("br"),n._v(" Thanh niên hai mươi sáu, bảy tuổi dáng dấp, một thân áo xanh, cả người đứng tại chỗ giống như đâm thủng bầu trời cây lao, mang theo ngạo nhân khí chất."),t("br"),t("br"),n._v(" Cho tới ông lão, nếu như Thượng Bân ở đây nhất định có thể nhận ra, chính là Hồng Thiên lâu hậu trường chưởng quỹ, Hồng Hạo trưởng lão!"),t("br"),t("br"),n._v(" “Trưởng lão quá khen, ta chỉ là tận ta bản năng, bồi dưỡng học sinh thôi, hết thảy đều là bọn họ nâng đỡ!” Thanh niên cười nói."),t("br"),t("br"),n._v(" Lời nói mặc dù nói được khiêm tốn, trong thần thái lại tràn đầy ngạo nghễ cùng tự tin."),t("br"),t("br"),n._v(" “Này, có phải là quá khen, xem những học sinh này chất lượng liền biết rồi, ta có thể nghe nói, lần này nhập học sát hạch 100 người đứng đầu học viên, chỉ báo danh ngươi nơi này, đồng thời bị thu làm môn hạ, liền vượt qua bảy mươi, ngươi hơn 200 vị tân sinh, có vẻ như không có thấp hơn xếp hạng năm trăm trở xuống đi!” "),t("br"),t("br"),n._v(" Hồng Hạo trưởng lão vuốt râu, cười nói."),t("br"),t("br"),n._v(" Ngày hôm qua hắn mới vừa nghe được số này căn cứ thời điểm, cũng là sợ hết hồn."),t("br"),t("br"),n._v(" Vị này Lục Tầm tuổi không lớn lắm, lại thành toàn bộ học viện chói mắt nhất minh tinh lão sư, hầu như đem tân sinh bên trong tốt nhất mầm, đều một lưới bắt hết."),t("br"),t("br"),n._v(" Loại này thành tích, coi như toàn bộ học viện sáng tạo nhiều năm như vậy, cũng là rất hiếm thấy."),t("br"),t("br"),n._v(" “Học sinh đồng ý báo ta chỗ này, ta cũng rất vinh hạnh!” Nghe được đường đường võ giả thất trọng trưởng lão khen bản thân, Lục Tầm lão sư tràn đầy kiêu ngạo, lập tức xoay đầu lại: “Hồng Hạo trưởng lão luôn luôn rất bận, ngày hôm nay làm sao tới chỗ của ta? Có phải là có chuyện gì hay không muốn ta hỗ trợ? Chỉ cần có thể làm được, tất nhiên dùng hết khả năng!” "),t("br"),t("br"),n._v(" Hắn không tin thực lực như vậy trưởng lão, là chạy tới tán gẫu."),t("br"),t("br"),n._v(" “Muốn nói có việc, trả lại xác thực muốn phiền phức ngươi một hồi, ta cùng phụ thân ngươi là bạn cũ, từ nhỏ nhìn ngươi lớn lên, biết ngươi làm người ngay thẳng, đặc biệt muốn cho ngươi hỗ trợ điều tra một vị lão sư!” "),t("br"),t("br"),n._v(" Hồng Hạo trưởng lão nói."),t("br"),t("br"),n._v(" “Không muốn nhấc lên phụ thân ta, hắn chính là cái lão gàn bướng!” Nghe được đối phương nói chuyện cảm tình, Lục Tầm hơi nhướng mày: “Nói đi, là vị nào lão sư?” "),t("br"),t("br"),n._v(" “Trương Huyền!” Hồng Hạo trưởng lão nói."),t("br"),t("br"),n._v(" Của hắn Hồng Thiên tửu lâu bởi vì vì cái này Trương lão sư nháo trò, hiện tại chuyện làm ăn hầu như là số không, trong lòng từ lâu lửa giận thiêu đốt, nếu không là tự mình thân phận, e sợ đều vọt tới trên cửa đánh đập đối phương!"),t("br"),t("br"),n._v(" “Trương Huyền? Ngươi nói người thầy giáo kia sát hạch được không điểm, đem Triệu Nham Phong dạy tẩu hỏa nhập ma Trương Huyền?” "),t("br"),t("br"),n._v(" đọｃ ngantruyen.com"),t("br"),n._v(" Lục Tầm nhìn sang."),t("br"),t("br"),n._v(" Đối với vị này Trương lão sư một ít “Hào quang” sự tích, hắn cũng là có nghe thấy, lại nói, hắn còn thu lấy người ta học viên, làm sao không rõ ràng."),t("br"),t("br"),n._v(" “Chính là!” Hồng Hạo trưởng lão gật đầu."),t("br"),t("br"),n._v(" “Đây là một rác rưởi lão sư, sớm muộn đều sẽ bị khai trừ, làm sao, trưởng lão cùng hắn có mâu thuẫn?” Lục Tầm tràn đầy kỳ quái."),t("br"),t("br"),n._v(" Một cái là đủ để cạnh tranh viện trưởng lâu năm trưởng lão, một cái là bất cứ lúc nào cũng sẽ bị khai trừ lão sư, hai người tám gậy tre đều đánh không được, tại sao có thể có liên hệ?"),t("br"),t("br"),n._v(" “Một chút ít sự tình mà thôi! Ta nghe nói phòng giáo dục cho tối hậu thư, chỉ cần cái này học kỳ chiêu không tới học sinh, liền khai trừ giáo sư tư cách, trục xuất học viện! Vì lẽ đó, hi vọng Lục lão sư phiền phức một hồi, nhìn có thể hay không đem của hắn mấy học sinh thu được môn hạ của ngươi!” "),t("br"),t("br"),n._v(" Hồng Hạo trưởng lão nói ra mục đích của chính mình."),t("br"),t("br"),n._v(" Tới đây trước hắn liền tỉ mỉ hỏi thăm Trương Huyền một ít chuyện, thậm chí bao gồm cái này học kỳ chiêu thu năm học sinh sự tình."),t("br"),t("br"),n._v(" Đối phương là lão sư, có thân phận bảo vệ, hắn không cách nào làm sao, nhưng một khi mất đi tư cách, còn không mặc cho xâu xé?"),t("br"),t("br"),n._v(" “Thu đến môn hạ của ta?” Lục Tầm không nghĩ tới đối phương tìm hắn, là chuyện này."),t("br"),t("br"),n._v(" “Đúng đấy, làm như Hồng Thiên học viện minh tinh giáo sư, muốn nói ai có thể làm được điểm ấy, cũng chỉ có ngươi! Chỉ cần ngươi để lộ ra ý này, chỉ sợ hắn cái kia mấy cái học viên, sẽ lập tức lùi khóa, tranh nhau chen lấn gia nhập môn hạ của ngươi!” Hồng Hạo trưởng lão cười nói: “Giáo huấn một cái có học viên lão sư, sẽ có rất nhiều phiền phức, không có học sinh, liền đơn giản có thêm!” "),t("br"),t("br"),n._v(" “Ừm...” Lục Tầm do dự."),t("br"),n._v("  “Không cái gì có thể do dự, ngươi lẽ nào đã quên Triệu Nham Phong? Nếu như mặc cho cái này Trương Huyền tiếp tục dạy học sinh, nhất định sẽ giẫm lên vết xe đổ, làm lỡ bọn họ một đời! Ngươi đem bọn họ chiêu thu lại đây, cũng là vì bọn họ cân nhắc, cứu bọn họ!” "),t("br"),t("br"),n._v(" Hồng Hạo trưởng lão tận dụng mọi thời cơ."),t("br"),t("br"),n._v(" “Được rồi, chuyện này ta đáp ứng rồi, ta ngày hôm nay sẽ thả ra tin tức, có ý định chiêu thu bọn họ bên dưới mấy cái học viên!” Lục Tầm gật đầu."),t("br"),t("br"),n._v(" “Liền như thế định...” "),t("br"),t("br"),n._v(" Hồng Hạo trưởng lão ánh mắt sáng lên, trong lòng tràn đầy hưng phấn."),t("br"),t("br"),n._v(" Lục lão sư đáp ứng, hắn đã có thể rõ nét dự kiến, cái này Trương Huyền theo thất vọng đến tuyệt vọng mặt."),t("br"),t("br"),n._v(" Hừ, để hắn Hồng Thiên tửu lâu sắp đóng cửa, hắn sẽ từ từ chơi đùa đối phương, để cho cảm thấy chân chính khủng hoảng!"),t("br"),t("br"),n._v(" Đang tại âm thầm cao hứng, thông suốt muốn bước kế tiếp làm sao thu thập đối phương, liền thấy một cái tân sinh đi tới."),t("br"),t("br"),n._v(" “Lục lão sư!” "),t("br"),t("br"),n._v(" Đi tới qua trước mặt, bái ngã xuống đất."),t("br"),t("br"),n._v(" “Vương Nham, chuyện gì?” "),t("br"),t("br"),n._v(" Nhìn thấy thiếu niên này, Lục Tầm mỉm cười gật đầu."),t("br"),t("br"),n._v(" Vị này Vương Nham, xem như là khóa này tân sinh trung thiên tư tốt nhất mấy vị một trong, càng là Vương gia nhị trưởng lão tôn tử, địa vị tôn sùng, có thể thu được học viên như thế, coi như là hắn, cũng không nhịn được có chút tự hào."),t("br"),t("br"),n._v(" “Lục lão sư, ta... Ta...” "),t("br"),t("br"),n._v(" Đến chính là mới từ học tâm tháp trở về Vương Nham, giờ khắc này quỳ trên mặt đất, đầy mặt xoắn xuýt."),t("br"),t("br"),n._v(" “Có phải là có cái gì tu luyện tới vấn đề khó? Nói thẳng không sao cả!” Thấy hắn ngập ngừng ấp úng dáng vẻ, Lục Tầm hào khí mở miệng."),t("br"),t("br"),n._v(" “Lục lão sư học thức uyên bác, là toàn bộ học viện nổi danh nhất giáo sư, có cái gì khó đề trực tiếp hỏi đi, hắn nhất định có thể giúp ngươi giải quyết!” Hồng Hạo trưởng lão cũng vuốt râu, cười nói."),t("br"),t("br"),n._v(" “Vậy ta liền nói...” "),t("br"),t("br"),n._v(" Vương Nham cắn răng một cái: “Ta muốn... Lùi đi của ngươi khóa, mong rằng Lục lão sư tác thành!” "),t("br"),t("br"),n._v(" “Lùi của ta khóa?” "),t("br"),t("br"),n._v(" Lục Tầm một cái lảo đảo, con mắt một hồi trợn tròn, tràn đầy không thể tin được."),t("br"),t("br"),n._v(" Người khác đều tranh đoạt muốn đi vào lớp học của hắn, cái tên này lại có thể... Muốn lùi khóa?"),t("br"),t("br"),n._v(" Thật hay giả?"),t("br"),t("br"),n._v(" Tại Hồng Thiên học viện truyền thụ nhiều năm như vậy, còn chưa bao giờ từng gặp phải!"),t("br"),t("br"),n._v(" “Phải!” Lời nói nói ra, Vương Nham thở phào nhẹ nhõm, liền vội vàng gật đầu."),t("br"),t("br"),n._v(" “Ngươi có phải là có cái gì khó nói chi ẩn, hoặc là bị ai uy hiếp?” Lục Tầm không nhịn được nói."),t("br"),t("br"),n._v(" “Không có, học sinh là tự nguyện!” Vương Nham nói."),t("br"),t("br"),n._v(" “Ngươi có biết, lui của ta khóa, toàn bộ học viện, lại không có cái khác lão sư dám thu ngươi?” Nghe hắn tự nguyện, Lục Tầm không nhịn được tiếp tục nói."),t("br"),t("br"),n._v(" Hắn là học viện nổi danh nhất lão sư, học sinh liền của hắn khóa đều lùi, các lão sư khác làm sao còn dám thu?"),t("br"),t("br"),n._v(" “Chỉ cần có thể lùi đi của ngươi khóa, ta cũng có thể đi Trương Huyền lão sư nơi đó bàng thính, đây là một cơ hội, mong rằng Lục lão sư tác thành!” Vương Nham cúi người xuống."),t("br"),t("br"),n._v(" “Lùi của ta khóa, đi Trương Huyền lão sư nơi đó bàng thính?” "),t("br"),t("br"),n._v(" Lục Tầm nhất thời táo bón giống nhau, trợn mắt ngoác mồm, cảm giác sắp điên rồi."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-89"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-91"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);