(window.webpackJsonp=window.webpackJsonp||[]).push([[2188],{2543:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-891-bon-đai-đe-quoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-891-bon-đai-đe-quoc"}},[n._v("#")]),n._v(" Chương 891: Bốn đại đế quốc")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 891: Bốn đại đế quốc"),t("br"),t("br"),n._v("Hồng Viễn hoàng cung."),t("br"),t("br"),n._v("Thấy Trương Huyền rời đi, lại không có xuất hiện sụp đổ, phá hư, Ngọc Thần Thanh thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Vị này tân nhiệm Trương viện trưởng, thiên phú, phụ tu, ánh mắt, hiểu biết, nhân phẩm... Đều rất tốt, duy nhất thiếu hụt chính là rất thích phá hủy."),t("br"),t("br"),n._v("Mỗi lần tới, đều sợ mất mật, sợ đem hắn hoàng cung phá hủy."),t("br"),t("br"),n._v("Lần này chẳng những không có phá nhà cửa, ngược lại liền cứu sống Bồ Đề thụ, xem như rất ra ngoài ý định."),t("br"),t("br"),n._v("“Bệ hạ!”"),t("br"),t("br"),n._v("Mới vừa trở lại ngự thư phòng, lão thái giám La Phú liền đi đến."),t("br"),t("br"),n._v("“Ừm, Vân Vụ lĩnh bên kia thế nào?” Ngọc Thần Thanh nhìn lại."),t("br"),t("br"),n._v("Vân Vụ lĩnh gây động tĩnh lớn như vậy, với tư cách Hồng Viễn đế quốc Hoàng đế bệ hạ, tự nhiên cũng thời khắc chú ý, thậm chí đều đã mệnh lệnh binh sĩ chuẩn bị xong, tùy thời đều có thể tiến công."),t("br"),t("br"),n._v("“Bẩm báo bệ hạ, Vân Vụ lĩnh cùng danh sư học viện đã ngưng chiến!” La Phú vội vàng ôm quyền."),t("br"),t("br"),n._v("“Ngưng chiến?” Ngọc Thần Thanh cau mày."),t("br"),t("br"),n._v("Lúc ban ngày, còn đánh hừng hực khí thế, không ai nhường ai, làm sao một cái ngừng?"),t("br"),t("br"),n._v("“Mi trưởng lão tự mình đi Vân Vụ lĩnh đàm phán, về sau chiến đấu liền kết thúc...” La Phú nói."),t("br"),t("br"),n._v("Đi Vân Vụ lĩnh, Trương Huyền một mực theo hỏa long Thánh thú trên lưng không có xuống, lại thêm thân phận giữ bí mật, hầu như không có người biết hắn cũng đi."),t("br"),t("br"),n._v("“Đàm phán?” Ngọc Thần Thanh nhìn qua: “Ai trước nâng?”"),t("br"),t("br"),n._v("Song phương giao chiến ai trước đưa ra, liền đại biểu đã nhận thua, ở thế yếu."),t("br"),t("br"),n._v("“Học viện bên này!” La Phú gật đầu."),t("br"),t("br"),n._v("“Học viện?” Ngọc Thần Thanh sửng sốt."),t("br"),t("br"),n._v("Đường đường danh sư học viện, hướng một đám linh thú nhận thua?"),t("br"),t("br"),n._v("Còn muốn Mi trưởng lão tự mình đi qua?"),t("br"),t("br"),n._v("Cái này... Có chút quá không thể tưởng tượng nổi đi!"),t("br"),t("br"),n._v("“Đúng vậy a, đàm phán là tại Vân Vụ lĩnh, cụ thể tin tức còn không có tìm hiểu đi ra, không qua, lúc chiều, cùng danh sư học viện giao chiến tất cả linh thú cũng đã lui về!”"),t("br"),t("br"),n._v("La Phú nói."),t("br"),t("br"),n._v("Linh thú cơ cảnh, đối với nhân loại cùng với bài xích, lại thêm Vân Vụ lĩnh từ trước tới nay vùng cấm của nhân loại, liền xem như Hồng Viễn đế quốc hoàng thất, cũng rất khó điều tra tin tức."),t("br"),t("br"),n._v("“Có phải là vì ngày mai viện trưởng kế vị đại điển, tiến hành một loại nào đó thỏa hiệp đi!” Chần chờ một chút, Ngọc Thần Thanh gật đầu."),t("br"),t("br"),n._v("Hừng đông chính là viện trưởng kế vị nghi thức, mặc dù thông báo vội vàng, nhưng học viện phạm vi bao phủ bên trong tất cả nhất đẳng đế quốc, khẳng định vẫn là sẽ phái người trước tới tham gia, nếu như nhìn thấy nhiều như vậy linh thú đối với nhân loại tiến công, sợ rằng sẽ đối tân viện trưởng mất đi lòng tin."),t("br"),t("br"),n._v("“Hẳn là đi... Bất quá, chúng ta đã phái người lẻn vào Vân Vụ lĩnh, đàm phán nội dung cùng tin tức, không có gì bất ngờ xảy ra viện trưởng kế vị đại điển trước đó, có thể truyền lại trở về!”"),t("br"),t("br"),n._v("La Phú nói."),t("br"),t("br"),n._v("“Ừm!” Ngọc Thần Thanh nhẹ gật đầu, đang muốn nói chuyện, chỉ thấy một cái tiểu thái giám vội vã đi đến."),t("br"),t("br"),n._v("“Bệ hạ!” Thái giám quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Đã xảy ra chuyện gì?” Biết đối phương muộn như vậy, không trải qua triệu hoán liền đi vào, khẳng định là có chuyện quan trọng, Ngọc Thần Thanh nhướng mày."),t("br"),t("br"),n._v("“Hồng Trì, Nguyên Cương, Bạch Thác, Trì Diêu các loại bốn cái đế quốc Hoàng đế bệ hạ, đã đến Càn Hòa điện!” Tiểu thái giám vội nói."),t("br"),t("br"),n._v("“Đến? Bọn họ tới cũng thật là nhanh!” Ngọc Thần Thanh sững sờ, lập tức đứng dậy: “Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Hồng Viễn danh sư học viện, mặc dù treo chính là Hồng Viễn đế quốc tên, trong đó học sinh, nhưng bao gồm xung quanh mười cái nhất đẳng đế quốc."),t("br"),t("br"),n._v("Hồng Trì, Nguyên Cương, Bạch Thác, Trì Diêu bốn cái, chính là cách nơi này gần đây quốc gia."),t("br"),t("br"),n._v("Nghe được tân viện trưởng kế vị, đế quốc cao nhất người cầm quyền, tất cả đều tự mình chạy tới, để bày tỏ bày ra tôn trọng."),t("br"),t("br"),n._v("Đi vào đại điện, quả nhiên thấy bốn vị trung niên nhân ngồi tại khách tọa bên trên, khí tức như mây, nguyên một đám thực lực bất phàm."),t("br"),t("br"),n._v("Có thể trở thành nhất đẳng đế quốc cao nhất người cầm quyền, thực lực ít nhất đều đạt đến Thánh vực nhất trọng đỉnh phong."),t("br"),t("br"),n._v("“Chử huynh, Hàn huynh, Thân huynh, Nhiếp huynh!” Ngọc Thần Thanh từng cái làm lễ ra mắt: “Ngắn ngủi ba ngày không đến, liền chạy tới, dọc đường khẳng định rất vất vả đi!”"),t("br"),t("br"),n._v("Mặc dù bốn đại đế quốc, cách nơi này tương đối gần, nhưng không đến ba ngày liền chạy tới, khẳng định cũng hao tốn cực Đại Chu gãy."),t("br"),t("br"),n._v("“Tân viện trưởng kế vị, chúng ta làm sao có thể không đến?”"),t("br"),t("br"),n._v("“Học viện là chúng ta cái này hơn mười nhất đẳng đế quốc ổn định cơ sở, như thế thịnh hội, không tự mình tham gia, chẳng phải quá thua thiệt?”"),t("br"),t("br"),n._v("“Lão viện trưởng mất tích đều hơn hai năm, rốt cục nghênh đón tân viện trưởng, tự nhiên muốn gặp một lần!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mấy vị trung niên nhân, đều cười cười."),t("br"),t("br"),n._v("Bọn họ tại từng người đế quốc, đều nói một không hai, không ai dám phản bác, nhưng cùng danh sư học viện so, vẫn là chênh lệch một chút."),t("br"),t("br"),n._v("Nhận được mời, lập tức ném xuống tất cả sự vụ, chạy tới."),t("br"),t("br"),n._v("“Đúng rồi, chúng ta vừa tới, không biết tân viện trưởng là vị nào? Triệu Bính Tuất trưởng lão vẫn là Lục Phong trưởng lão?”"),t("br"),t("br"),n._v("Hàn huyên qua đi, một người trung niên mở miệng hỏi."),t("br"),t("br"),n._v("Chính là Hồng Trì nhất đẳng đế quốc Hoàng đế bệ hạ, chử đã."),t("br"),t("br"),n._v("Nghe được hắn, những người khác cũng toàn bộ đều đem ánh mắt tập trung tới, mới vừa tới đến Hồng Viễn thành, hơn nửa đêm liền đến đến hoàng cung, tự nhiên là vì tuân hỏi rõ ràng, chuẩn bị cẩn thận hạ lễ."),t("br"),t("br"),n._v("Nếu không, cho luyện khí học viện trưởng lão đưa vật liệu, không những không có lưu lại ấn tượng tốt, làm không cẩn thận còn biết hoàn toàn ngược lại."),t("br"),t("br"),n._v("“Đều không phải là!” Nghe được suy đoán của bọn hắn, Ngọc Thần Thanh cười khổ lắc đầu."),t("br"),t("br"),n._v("Lúc trước, hắn cũng là cho rằng, thật muốn xuất hiện tân viện trưởng, tất nhiên theo hai người này tuyển ra thứ nhất, mà kết quả lại là..."),t("br"),t("br"),n._v("“Không phải?”"),t("br"),t("br"),n._v("Bốn vị Hoàng đế bệ hạ, tất cả đều sững sờ, liếc mắt nhìn nhau: “Cái kia không biết là vị nào trưởng lão, mong rằng Ngọc huynh, trước thời hạn báo cho, chúng ta cũng tốt có chuẩn bị!”"),t("br"),t("br"),n._v("Chử đã vội nói."),t("br"),n._v(" “Cái này...” Ngọc Thần Thanh chần chờ."),t("br"),t("br"),n._v("Tân viện trưởng tin tức, trước mắt mà nói, còn không có công bố ra ngoài, hắn mặc dù trước thời hạn biết, cũng là dựa vào quan hệ."),t("br"),t("br"),n._v("“Làm sao? Ngày mai lễ tổ chức, liền biết, chẳng lẽ, Ngọc huynh còn không nguyện ý tiết lộ?” Một vị trung niên nhướng mày."),t("br"),t("br"),n._v("“Ngược lại không phải không nguyện ý tiết lộ, học viện đã không có trước thời hạn nói ra, tự nhiên có lý do khác... Ta vượt quá chức phận, làm thay việc của người khác, trước thời hạn nói, là đối học viện không tôn kính...” Ngọc Thần Thanh lắc đầu."),t("br"),t("br"),n._v("“Xem ra Ngọc huynh là không tin chúng ta nha! Ngươi ta đám người thân phận, sớm đã cùng danh sư học viện khóa lại, chặt chẽ không thể tách rời, chẳng lẽ còn cho là chúng ta có thể tiết lộ tin tức hay sao?”"),t("br"),t("br"),n._v("Biết hắn lo lắng, chử đã lắc đầu: “Chúng ta muốn biết cũng chỉ là sớm chuẩn bị hạ lễ mà thôi!”"),t("br"),t("br"),n._v("“Cái này... Cũng được!”"),t("br"),t("br"),n._v("Biết không nói, đối phương khẳng định sẽ đủ loại nghi kỵ, làm không cẩn thận, còn biết dẫn đến hai cái đế quốc không vui, Ngọc Thần Thanh không chần chờ quá lâu, liền gật đầu: “Chuyện này là học viện cơ mật, mong rằng chư vị tại kế vị đại điển trước đó, giữ miệng giữ mồm!”"),t("br"),t("br"),n._v("Trước mắt mấy vị, đều là một nước chi chủ, khẳng định cũng rõ ràng tiết lộ tin tức tính nghiêm trọng, thật muốn tân viện trưởng xuất hiện cái gì bất trắc, Danh Sư đường tất nhiên truy xét đầu nguồn, ai tiết lộ phong thanh, liền sẽ chờ lấy bị hủy diệt."),t("br"),t("br"),n._v("Bởi vậy, coi như cùng bọn hắn nói, cũng không khẩn yếu, nhưng vẫn là muốn trước thời hạn nói rõ ràng."),t("br"),t("br"),n._v("“Cái này yên tâm!”"),t("br"),t("br"),n._v("“Chúng ta đều nhiều năm như vậy giao tình, không cần hoài nghi!”"),t("br"),t("br"),n._v("Mấy người đồng thời nói."),t("br"),t("br"),n._v("“Vậy thì tốt, căn cứ ta được đến tin tức, cái này tân viện trưởng, là Trương Huyền Trương sư!”"),t("br"),t("br"),n._v("Bàn tay lớn vồ một cái, đem trong cung điện cấm chế toàn bộ kích hoạt, Ngọc Thần Thanh truyền âm qua."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("“Trương sư?”"),t("br"),t("br"),n._v("Bốn vị Hoàng đế, tất cả đều liếc mắt nhìn nhau, nguyên một đám tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Danh sư học viện thập đại trường lão, bọn họ tất cả đều nghe qua, cũng rất nhiều đều gặp, nhưng... Cái này Trương Huyền từ nơi nào xuất hiện?"),t("br"),t("br"),n._v("“Chẳng lẽ vị này... Trương sư là theo tổng bộ tới?”"),t("br"),t("br"),n._v("Chử đã nhịn không được nói."),t("br"),t("br"),n._v("Những người khác cũng kỳ quái."),t("br"),t("br"),n._v("Danh sư học viện bồi dưỡng danh sư, đại biểu cho nhân tộc tương lai, trách nhiệm to lớn, cái này cái gọi là viện trưởng, cũng không phải cái gì người muốn làm liền có thể làm."),t("br"),t("br"),n._v("Vị này Trương Huyền, trước đó chưa từng nghe qua tên, có thể là theo tổng bộ trực tiếp nhảy dù mà tới."),t("br"),t("br"),n._v("Nhưng mà... Tổng bộ, không phải bình thường không can dự phân bộ sự tình ư?"),t("br"),t("br"),n._v("Làm sao lần này trực tiếp phái một cái danh sư tới, còn gánh Nhâm viện trưởng?"),t("br"),t("br"),n._v("“Cái này... Hắn không phải từ tổng bộ tới, cũng không phải học viện trưởng lão, mà là... Năm nay mới vừa thi vào học viện tân sinh!”"),t("br"),t("br"),n._v("Thấy mọi người tràn đầy kỳ quái, Ngọc Thần Thanh giải thích nói."),t("br"),t("br"),n._v("“Tân sinh?”"),t("br"),t("br"),n._v("“Mới vừa thi vào tân sinh... Để hắn làm viện trưởng?”"),t("br"),t("br"),n._v("Tứ đại Hoàng đế bệ hạ, tất cả đều sững sờ, nguyên một đám giống như gặp quỷ đồng dạng."),t("br"),t("br"),n._v("Danh sư học viện, là nhân loại trụ cột vững vàng, tại tất cả mọi người trong lòng, đều không thể vũ nhục... Để một cái mới vừa tiến vào học viện tân sinh, làm viện trưởng?"),t("br"),t("br"),n._v("Ngươi đùa giỡn a?"),t("br"),t("br"),n._v("“Ta biết tin tức này, để mọi người rất khó tiếp nhận, nhưng đây chính là sự thật!” Ngọc Thần Thanh cười khổ một tiếng."),t("br"),t("br"),n._v("Nói thật, hắn lần đầu tiên nghe được tin tức này thời điểm, cũng đầy mặt không thể tin được."),t("br"),t("br"),n._v("Nhưng tận mắt chứng kiến đến Trương sư tất cả thủ đoạn về sau, lúc này mới tâm phục khẩu phục, bội phục Mộc sư đám người thấy xa cùng quyết đoán."),t("br"),t("br"),n._v("“Vị này Trương sư, đến cùng lai lịch gì?”"),t("br"),t("br"),n._v("Một lát sau, thực sự nhịn không được, một vị trung niên hỏi."),t("br"),t("br"),n._v("“Trương sư là một vị tứ tinh danh sư, nay tuổi chưa qua 20 tuổi! Mặc dù tuổi tác không lớn, nhưng có luyện khí sư, luyện đan sư, y sư, Ma âm sư, kinh hồng sư... Năm cái lục tinh chức nghiệp, còn tinh thông thuần thú, trận pháp cùng dưỡng dược, là một vị không biết siêu cấp thiên tài!”"),t("br"),t("br"),n._v("Ngọc Thần Thanh nói."),t("br"),t("br"),n._v("“Năm cái lục tinh chức nghiệp? Coi như phụ tu chức nghiệp nhiều hơn nữa, hắn cũng chỉ là cái tứ tinh danh sư, có tư cách gì, trở thành viện trưởng?”"),t("br"),t("br"),n._v("“Đúng vậy a, thế giới này chính là không bao giờ thiếu thiên tài, chỉ cần không có đi đến mức nhất định, muốn làm viện trưởng này, chỉ sợ cũng không có tư chất đi!”"),t("br"),t("br"),n._v("“Trên đường tới ta liền nghe nói, học viện vì ngày mai lễ, ủy khúc cầu toàn hướng Vân Vụ lĩnh linh thú thỏa hiệp! Sẽ không lão viện trưởng mất tích, toàn bộ danh sư học viện, đều trở nên tập tục không đúng a?”"),t("br"),t("br"),n._v("“Chuyện này ta cũng nghe nói, đường đường danh sư học viện, thế mà hướng một đám linh thú đàm phán, truyền đi còn thể thống gì! Hiện tại càng làm cho một cái tứ tinh danh sư là viện trưởng... Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Bốn vị nhất đẳng đế quốc Hoàng đế bệ hạ, tất cả đều khuôn mặt xanh xám."),t("br"),t("br"),n._v("Vân Vụ lĩnh linh thú cùng danh sư học viện giao chiến sự tình, bọn họ cũng nghe nói, đồng thời còn nghe nói nhân loại chủ động đàm phán."),t("br"),t("br"),n._v("Vốn cho rằng liền đủ hoang đường, không nghĩ tới còn có càng hoang đường sự tình!"),t("br"),t("br"),n._v("Để một cái miệng còn hôi sữa, chỉ có hai mươi tuổi tiểu gia hỏa làm viện trưởng?"),t("br"),t("br"),n._v("Đây là muốn làm gì?"),t("br"),t("br"),n._v("Thấy bốn vị bệ hạ bộ dáng này, Ngọc Thần Thanh không biết như thế nào mở miệng."),t("br"),t("br"),n._v("Nếu như chỉ nghe vị này Trương sư niên kỉ kỷ cùng cấp bậc, quả thực có thể như vậy muốn, nhưng tận mắt thấy, mới sẽ biết vị này thoạt nhìn không lớn Trương sư, đến cùng có bao nhiêu lợi hại."),t("br"),t("br"),n._v("“Nhất định phải đem chuyện này hỏi rõ ràng, nếu không, chúng ta Hồng Trì đế quốc liền không nhận vị viện trưởng này!”"),t("br"),t("br"),n._v("“Chúng ta Nguyên Cương đế quốc cũng không nhận!”"),t("br"),t("br"),n._v("“Ta Bạch Thác đế quốc đồng dạng không nhận...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mấy vị Hoàng đế bệ hạ, đồng thời đứng dậy, nguyên một đám lông mày vung lên."),t("br"),t("br"),n._v("(Dùng di động đánh ra một chương, trước truyền bên trên, hôm nay sẽ có chương 2: Giữa tháng, mọi người đừng quên ném nguyệt phiếu ah!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-899"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-901"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);