(window.webpackJsonp=window.webpackJsonp||[]).push([[2189],{2544:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-892-khao-hach-luc-tinh-danh-su-can-đieu-kien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-892-khao-hach-luc-tinh-danh-su-can-đieu-kien"}},[n._v("#")]),n._v(" Chương 892: Khảo hạch lục tinh danh sư cần điều kiện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 892: Khảo hạch lục tinh danh sư cần điều kiện"),t("br"),t("br"),n._v("Những thứ này đế quốc mặc dù không có quyền quản lý viện trưởng bổ nhiệm, nhưng là có thể bỏ phiếu tán thành hoặc là bác bỏ, nếu quả thật như thế không đáng tin cậy, hoàn toàn có thể nghi ngờ, thậm chí báo cáo, để càng cao danh hơn sư đường tới xử lý."),t("br"),t("br"),n._v("Dù sao, như thế lớn học viện vận chuyển, cần phải hao phí cực lớn, rất nhiều kinh phí, đều là cấp dưới nhất đẳng đế quốc không ràng buộc cung cấp."),t("br"),t("br"),n._v("Nói cách khác, những thứ này đế quốc hàng năm đều sẽ lấy ra rất lớn một khoản kinh phí, cung cấp cho học viện sử dụng, một cái không xứng chức viện trưởng, rất dễ dàng để học viện sụp đổ, tự nhiên không phải bọn họ nguyện ý nhìn thấy."),t("br"),t("br"),n._v("Một khi tất cả cấp dưới đế quốc liên hợp bẩm lên, coi như tổng bộ, cũng khẳng định sẽ nghiêm túc cân nhắc."),t("br"),t("br"),n._v("Danh Sư đường, mặc dù cao cao tại thượng, bảo hộ nhân tộc an định đoàn kết, nhưng cũng sẽ cùng cấp dưới tất cả quốc gia giữ quan hệ tốt, nếu không, lòng người không hướng, đứng càng cao, té càng đau."),t("br"),t("br"),n._v("Đồng dạng một màn, cũng phát sinh ở Hồng Viễn đế quốc Danh Sư đường."),t("br"),t("br"),n._v("Nhìn trước mắt tràn đầy chất vấn chư Đa đường chủ, Mạc Cao Viễn xoa mi tâm, không biết giải thích như thế nào."),t("br"),t("br"),n._v("Hồng Trì, Nguyên Cương, Bạch Thác, Trì Diêu bốn đại đế quốc Hoàng đế bệ hạ tới đến Hồng Viễn thành, hắn có Danh Sư đường phân bộ, tự nhiên cũng phái người đến."),t("br"),t("br"),n._v("Ba vị đường chủ một vị Phó đường chủ!"),t("br"),t("br"),n._v("Nghe được đường đường học viện, thế mà để một vị tứ tinh danh sư làm viện trưởng, tất cả đều vỡ tổ."),t("br"),t("br"),n._v("Cùng đế quốc khác biệt, bọn chúng cùng danh sư học viện, chỉ thấp nửa cấp bậc, nói cách khác, mỗi một vị đường chủ, đều cùng thập đại trường lão địa vị tương đương."),t("br"),t("br"),n._v("Lại thêm cùng thuộc danh sư, hoàn toàn có tư cách đưa ra nghi ngờ."),t("br"),t("br"),n._v("“Mặc kệ cái này tân nhiệm viện trưởng đến cùng có năng lực gì, dù sao ta cái thứ nhất không phục,  bên trên xem ta như thế nào để hắn khó xử!”"),t("br"),t("br"),n._v("“Nho nhỏ tứ tinh danh sư đã muốn làm viện trưởng, cũng quá không đem tất cả danh sư để ở trong mắt!!”"),t("br"),t("br"),n._v("“Viện trưởng không phải tốt như vậy làm, không nhưng cái này danh ngạch cũng sẽ không rỗng trọn hai năm dài đằng đẵng!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mấy vị đường chủ nguyên một đám ánh mắt lóe lên."),t("br"),t("br"),n._v("Với tư cách cùng thập đại trường lão địa vị tương đồng đường chủ, nói đối viện trưởng vị trí này chưa từng có ý nghĩ, cái kia là giả, nếu như là một cái đức cao vọng trọng, thực lực cao thâm danh sư tới làm, tất nhiên nói không nên lời cái gì, thậm chí còn có thể âm thầm bội phục, nhưng một cái chỉ có 20 tuổi tứ tinh danh sư... Có thể công nhận mới là lạ!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Danh sư học viện."),t("br"),t("br"),n._v("Tất cả bị thương học viên chất thành một đống, nguyên một đám sắc mặt tái xanh."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Bên trong một cái năm thứ tư học viên bỗng nhiên vỗ bàn một cái."),t("br"),t("br"),n._v("“Đường đường danh sư học viện, vậy mà hướng linh thú nhận thua, quả thực chính là sỉ nhục!”"),t("br"),t("br"),n._v("“Nghe nói là tân viện trưởng kế vị, không thể xuất hiện bạo động... Nếu không phía trên mặt mũi không dễ nhìn.”"),t("br"),t("br"),n._v("“Vớ vẩn, vì tân viện trưởng mặt mũi, đem chúng ta nhân tộc mặt mũi đưa chỗ nào?”"),t("br"),t("br"),n._v("“Quả thực liền ném đi danh sư tôn nghiêm! Ta tình nguyện cùng đối phương chiến tử tại sa trường, cũng không nguyện ý ở đây tham gia cái gì kế vị đại điển!”"),t("br"),t("br"),n._v("“Nam nhi nhiệt huyết, theo trở thành danh sư bắt đầu từ thời khắc đó, liền biết trên người trách nhiệm cùng gánh nặng, vì mình trên mặt mũi rạng rỡ, quả thực là chạy tới đàm phán, quả thực mất mặt xấu hổ!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người lên cơn giận dữ."),t("br"),t("br"),n._v("Vân Vụ lĩnh chuyện phát sinh, Trương Huyền cũng không để các trưởng lão khác truyền ra ngoài, những thứ này tham dự chiến đấu học viên, cũng không biết tất cả linh thú, Thánh thú, đã hàng phục, còn tưởng rằng học viện cho rằng đàm phán, vì tân viện trưởng kế vị có thể an ổn, đã hướng tất cả linh thú nhận thua."),t("br"),t("br"),n._v("Nếu không, trước đó thế tới hung hăng, hận không thể đem người toàn bộ lôi kéo linh thú, vì cái gì cam tâm tình nguyện lui binh?"),t("br"),t("br"),n._v("“Đúng rồi, các ngươi nhưng biết tân viện trưởng là ai?”"),t("br"),t("br"),n._v("Không biết ai hỏi."),t("br"),t("br"),n._v("“Không biết!” Mọi người đồng thời lắc đầu."),t("br"),t("br"),n._v("Học viện đem tân viện trưởng tin tức phong bế rất nghiêm, liền coi như bọn họ là học sinh, cũng toàn bộ không biết rõ tình hình."),t("br"),t("br"),n._v("“Quản hắn là ai! Hướng linh thú đàm phán, xây trường đến nay liền không có qua, loại người này, căn bản không đáng tôn trọng! Như vậy, ngày mai nghi thức mở ra thời điểm, chúng ta không bằng tới một lần biểu tình, để vị này tân viện trưởng, chính miệng nói một chút, đến cùng đáp ứng Vân Vụ lĩnh cái gì tang quyền nhục viện điều kiện!”"),t("br"),t("br"),n._v("Một người nói."),t("br"),t("br"),n._v("“Tốt, cứ làm như thế, ta cũng muốn nghe xem, đến cùng hứa hẹn những cái kia linh thú chỗ tốt gì, mới có thể ngoan ngoãn lui về Vân Vụ lĩnh, lại không quấy rối!”"),t("br"),t("br"),n._v("Mặc dù không biết tân viện trưởng là ai, nhưng chỉ bằng vào chuyện này, liền không cách nào làm cho người nắm giữ hảo cảm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không biết kế vị lễ còn chưa bắt đầu, liền để vô số người cừu thị, Trương Huyền truyền thụ Ngụy Như Yên pháp quyết tu luyện, an bài xong tu luyện, ngày đã sáng rõ."),t("br"),t("br"),n._v("Đang định nghỉ ngơi một chút, ăn chút điểm tâm, chỉ thấy Mi trưởng lão trước tới bái phỏng."),t("br"),t("br"),n._v("“Viện trưởng, đây là kế nhiệm lễ quá trình, ngươi nhìn một lần, làm quen một chút!”"),t("br"),n._v(" Nói đưa tới một cái ngọc phiến, phía trên viết đầy chữ viết."),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền lông mày nhịn không được nhảy một cái."),t("br"),t("br"),n._v("Cái này kế nhiệm nghi thức, quả nhiên không như trong tưởng tượng đơn giản như vậy, không chỉ phải tiếp nhận toàn trường thầy trò trả lời, còn muốn trước mặt mọi người tế bái học viện tất cả Tiên Tổ, nhận được sau khi tán thành, mới có thể tiếp nhận thầy trò ý niệm gia trì, ngưng tụ viện trưởng lệnh, trở thành chân chính viện trưởng."),t("br"),t("br"),n._v("Cuối cùng, càng là muốn lên một tiết công khai khóa."),t("br"),t("br"),n._v("Đủ loại quá trình xuống, mười phần rườm rà, ít nhất phải chơi đùa hơn nửa ngày."),t("br"),t("br"),n._v("Bất quá, muốn trở thành loại này thủ hộ mười mấy nhất đẳng đế quốc học viện viện trưởng, không trải qua những thứ này, khẳng định rất khó để phục chúng."),t("br"),t("br"),n._v("“Đáp Vấn hội, là học viện thầy trò, đối tân viện trưởng khảo hạch, chỉ cần biểu hiện tốt, có thể đề cao công nhận độ. Bình thường tình huống là học sinh đại biểu, lão sư đại biểu cùng với khác Danh Sư đường đặt câu hỏi hoặc là khiêu chiến, viện trưởng cấp cho trả lời chắc chắn, cơ bản đều là tu luyện, hoặc là phụ tu nghề nghiệp. Lấy Trương sư đối đủ loại chức nghiệp lý giải, cùng đối tu luyện nhận thức, hẳn là rất nhẹ nhàng!”"),t("br"),t("br"),n._v("Gặp hắn không nói lời nào, sợ không hiểu, Mi trưởng lão cười giải thích."),t("br"),t("br"),n._v("Vị này Trương viện trưởng học sinh, đều có thể giảng bài nói tất cả mọi người như si như say, làm lão sư lại càng không cần phải nói."),t("br"),t("br"),n._v("Kỳ thật đây chính là cho tân viện trưởng một cái biểu hiện ra cơ hội, làm cho tất cả mọi người đều biết, tuyển ra tân viện trưởng, nắm giữ chân chính bản lĩnh, để cho người ta tâm phục khẩu phục."),t("br"),t("br"),n._v("“Còn tế bái Tiên Tổ, Trương sư thiên phú mạnh như vậy, mặc dù còn không phải lục tinh danh sư, nhưng đã có sáu loại phụ tu chức nghiệp, khảo hạch thông qua nhưng mà vấn đề thời gian, nhận được tổ tiên công nhận, hẳn không phải là việc khó!”"),t("br"),t("br"),n._v("Mi trưởng lão nói tiếp."),t("br"),t("br"),n._v("“Cuối cùng viện trưởng lệnh ý niệm gia trì, ngược lại là khó khăn nhất. Cường đại tới đâu danh sư, cũng không có khả năng làm cho tất cả mọi người đều công nhận, có người bội phục, tự nhiên cũng liền có người chống đối, lão viện trưởng năm đó uy vọng cao như vậy, cũng chỉ là ngưng tụ hoàng kim viện trưởng lệnh.”"),t("br"),t("br"),n._v("“Hoàng kim viện trưởng lệnh?” Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Ừm, làm danh sư học viện viện trưởng, nhận được công nhận thầy trò càng nhiều, ngưng tụ ý niệm càng nhiều, hình thành viện trưởng lệnh, uy lực cũng lại càng lớn, cấp bậc cũng liền càng cao. Bình thường chia làm bạch kim, hoàng kim, thanh đồng cùng cương thiết bốn cái cấp bậc!”"),t("br"),t("br"),n._v("Mi trưởng lão nói."),t("br"),t("br"),n._v("“Đi đến thanh đồng cấp bậc, đại khái là để học viện một nửa thầy trò, theo ở sâu trong nội tâm công nhận, bội phục, hoàng kim cấp bậc, thì là sáu mươi phần trăm khoảng chừng! Bạch kim thì là bảy mươi phần trăm, danh sư học viện xây trường đến nay, gần vạn năm, ngưng tụ ra bạch kim viện trưởng lệnh, chỉ có hai cái!”"),t("br"),t("br"),n._v("“Một cái là chúng ta danh sư học viện người sáng lập, Mục Khải Mục sư! Mục sư vì người chân thành, đợi sống như con, năm đó bộc phát chiến đấu, xung phong đi đầu, thân trúng hơn một trăm ba mươi chỗ vết thương, mà không lui về phía sau, thậm chí còn tán hết gia sản, cứu chữa học viên... Lúc này mới nhận được như thế người công nhận!”"),t("br"),t("br"),n._v("“Còn vị thứ hai, thì là bốn ngàn năm trước Mạc Lưu Chân viện trưởng, nói đến vị này Mạc viện trưởng, quả nhiên là kinh tài tuyệt *** diệu toàn bộ thời kì, năm đó sáng lập ra kỳ tích, cẩn thận nói đến, chỉ sợ so với viện trưởng ngươi, đều không hề yếu!”"),t("br"),t("br"),n._v("Nói đến vị này Mạc Lưu Chân viện trưởng, Mi trưởng lão tràn đầy bội phục."),t("br"),t("br"),n._v("“Ồ?” Trương Huyền có chút hiếu kỳ."),t("br"),t("br"),n._v("Hắn đi tới danh sư học viện, liền chơi đùa đủ rồi, chẳng lẽ vị này Mạc viện trưởng, cũng giống vậy chơi đùa?"),t("br"),t("br"),n._v("“Hắn không có viện trưởng như thế chơi đùa...”"),t("br"),t("br"),n._v("Nhìn thấy ánh mắt của hắn, Mi trưởng lão đoán được ý nghĩ, mới nói một câu, hơi đỏ mặt, tràn đầy xấu hổ."),t("br"),t("br"),n._v("Hắn một trưởng lão, nói viện trưởng chơi đùa, thật sự là lớn bất kính."),t("br"),t("br"),n._v("Bất quá, thấy đối phương cũng không tức giận, lúc này mới thở phào nhẹ nhõm, tiếp tục nói: “Hắn đi tới học viện, là phá kỷ lục! Học viện ghi chép núi, đến bây giờ đều là hắn lưu lại, hậu thế bốn ngàn năm, không có người có thể siêu việt...”"),t("br"),t("br"),n._v("“Ghi chép núi?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Tới học viện đã lâu như vậy, còn chưa hề nghe người ta nói qua."),t("br"),t("br"),n._v("“Trương sư không biết cũng rất bình thường, ghi chép núi là lục tinh danh sư khảo hạch, từ khi khai giảng đến nay, còn không người xung kích lục tinh, cũng vẫn không có mở ra...”"),t("br"),t("br"),n._v("Mi trưởng lão cười cười: “Lần trước mở ra, vẫn là nửa năm trước, Hồ Yêu Yêu bọn họ!”"),t("br"),t("br"),n._v("“Hồ Yêu Yêu?”"),t("br"),t("br"),n._v("“Đúng vậy a, dựa theo Danh Sư đường quy củ, tu vi chỉ có đi đến Bán Thánh khoảng chừng, mới có tư cách khảo hạch lục tinh danh sư, nếu không, nhiều nhất nhưng mà nửa bước lục tinh. Bọn họ nhưng lấy được lục tinh huy chương, chính là bởi vì, tại ghi chép núi, xông qua cửa thứ ba!”"),t("br"),t("br"),n._v("Mi trưởng lão gật gật đầu: “Ghi chép núi, chính là kiểm tra tu vi cùng sức chiến đấu chỗ, có thể xông đến cửa thứ ba, nói đã có đến gần vô hạn Bán Thánh sức chiến đấu, lại thêm trước đó tất cả khảo hạch tất cả đều thông qua, tự nhiên cũng liền được huy chương!”"),t("br"),t("br"),n._v("“Ah!” Trương Huyền nhẹ gật đầu, nhịn không được nhìn qua: “Khảo hạch lục tinh danh sư, loại trừ sáu loại đi đến lục tinh phụ tu, còn cần gì?”"),t("br"),t("br"),n._v("Hắn hiện tại đã có sáu loại đi đến lục tinh phụ tu, thậm chí trong đó hai loại, cũng đều đạt đến thất tinh, dựa theo phụ tu lời nói, đã đầy đủ khảo hạch lục tinh danh sư. (Luyện khí sư, y sư, luyện đan sư, kinh hồng đạt đến lục tinh, Ma âm cùng thuần thú thì là thất tinh)"),t("br"),t("br"),n._v("Bất quá, hẳn là còn có một số điều kiện, cần chuẩn bị."),t("br"),t("br"),n._v("“Phụ tu, tâm tình khắc độ còn có thực lực, đây là khảo hạch danh sư ba loại cơ sở!”"),t("br"),t("br"),n._v("Mi trưởng lão nói: “Phụ tu viện trưởng đã đủ rồi, tâm tình khắc độ, cần muốn đạt tới 18. 0 trên đây, viện trưởng có thể sử dụng sư ngôn thiên bẩm tuỳ tiện mê hoặc Vưu Hư, nói rõ tâm tình cũng không là vấn đề. Chủ yếu nhất là thực lực!”"),t("br"),t("br"),n._v("“Muốn muốn khảo hạch lục tinh danh sư, cần thực lực ít nhất đi đến Tằm Phong cảnh đỉnh phong, viện trưởng nhưng mà sơ kỳ, còn kém một chút! Đương nhiên, nếu có thể ở ghi chép núi xông qua cửa thứ ba, đồng dạng có tư cách khảo hạch!”"),t("br"),t("br"),n._v("“Cái này ba loại cơ sở đủ rồi, còn lại chính là đối danh sư rèn luyện hàng ngày khảo nghiệm! Loại này khảo nghiệm, chủng loại rất nhiều, đủ có mấy ngàn chủng, thường thấy nhất, chính là giảng bài, học sinh cùng chỉ điểm!”"),t("br"),t("br"),n._v("“Đương nhiên, nếu như viện trưởng muốn khảo hạch lục tinh danh sư lời nói, hẳn là độ khó rất lớn, dù sao, thân phận bây giờ ở đây bày biện, tất nhiên có không ít người nhìn chằm chằm, lựa chọn đơn giản không dễ dàng phục chúng... Đoán không sai, có thể là độ khó lớn nhất... Tấn thăng đế quốc!”"),t("br"),t("br"),n._v("Mi trưởng lão chần chờ một chút, nói."),t("br"),t("br"),n._v("“Tấn thăng đế quốc?” Trương Huyền cau mày, tràn đầy không hiểu."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-900"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-902"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);