(window.webpackJsonp=window.webpackJsonp||[]).push([[219],{570:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1174-dien-khong-thien-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1174-dien-khong-thien-thu"}},[n._v("#")]),n._v(" Chương 1174: Diễn Không thiên thư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Sau một khắc, đã đi tới Khưu Ngô cung bia đá trước mặt."),t("br"),t("br"),n._v("Thân bia to lớn toàn bộ từ linh thạch thượng phẩm điêu khắc mà thành, lúc trước để tâm hắn động không ngừng, muốn trộm đi, hiện tại... Loại cấp bậc này linh thạch, đối với hắn đã không còn tác dụng gì nữa, bởi vậy, cũng mất lực hấp dẫn."),t("br"),t("br"),n._v("“Chữ này... Đã đạt đến như thật như ảo cảnh giới!”"),t("br"),t("br"),n._v("Con mắt rơi vào “Khưu Ngô cung” ba chữ to bên trên."),t("br"),t("br"),n._v("Trước đó, thư hoạ chức nghiệp không cao, nhìn không ra ưu khuyết, hiện tại xem ra, ba chữ này, tuyệt đối đạt đến bát cảnh."),t("br"),t("br"),n._v("Mặc dù đơn giản kiểu chữ, cho người cảm giác, không bằng một bộ thư hoạ, nhưng thấy được lâu, vẫn như cũ sẽ để cho chìm đắm trong đó, không cách nào tự kềm chế."),t("br"),t("br"),n._v("“Ừm? Không đúng, chữ này... Làm sao có chút không đúng?”"),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền đang nghĩ rời khỏi, đột nhiên nhướng mày."),t("br"),t("br"),n._v("Vừa mới bắt đầu nhìn, ba chữ này, không có gì đặc thù cảm giác, nhưng nhìn đến lâu, chân khí trong cơ thể di động, vậy mà tùy theo vũ động, hình như bất cứ lúc nào cũng sẽ bắn tung toé mà ra."),t("br"),t("br"),n._v("“Là Đại Bi Thiên Ma chưởng vận hành phương pháp...”"),t("br"),t("br"),n._v("Cảm thụ chân khí, tại ba chữ to chiếu rọi quỹ tích vận hành, Trương Huyền có chút sững sờ."),t("br"),t("br"),n._v("Đại Bi Thiên Ma chưởng, là hắn cùng Khưu Ngô cổ thánh học, thấy thế nào ba chữ này sẽ dẫn tới phản ứng?"),t("br"),t("br"),n._v("“Thử một chút!”"),t("br"),t("br"),n._v("Không tại áp chế vận chuyển chân khí, Trương Huyền bàn tay nhẹ nhàng dựng thẳng lên, đối trên vách đá nhẹ nhàng quét tới."),t("br"),t("br"),n._v("Đạt tới Thiên Đạo cấp bậc Đại Bi Thiên Ma chưởng, đã đạt đến mức độ nặng nhẹ tùy tâm cảnh giới, mặc dù cùng bia đá tiếp xúc, nhưng lại chưa để người sau chịu đến tổn hại."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Mới cùng bia đá tiếp xúc, đột nhiên, ba chữ to, lập tức toả ra ánh sáng chói lọi, Trương Huyền một cái lảo đảo, lập tức từ biến mất tại chỗ."),t("br"),t("br"),n._v("Mở mắt ra, phát hiện đi vào một cái không gian khác, thoạt nhìn không lớn, trước mắt lít nha lít nhít đủ loại phù văn."),t("br"),t("br"),n._v("“Diễn Không thiên thư?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, một nhóm chữ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Những phù văn này, tổ hợp lại với nhau, tạo thành một bộ đặc thù tu luyện công pháp, gọi là Diễn Không thiên thư."),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền liền biết mười phần cao xa, vượt xa quá hắn tu luyện bất kỳ pháp quyết nào."),t("br"),t("br"),n._v("“Chẳng lẽ... Đây là Khưu Ngô cổ thánh truyền thừa?”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt."),t("br"),t("br"),n._v("Lúc trước cổ thánh tượng đá, bị hắn tiến công biến mất, chưa kịp nói mình truyền thừa ở đâu, vốn cho rằng, lại không thể có thể được đến, không nghĩ tới... Thế mà giấu ở cái này vừa vào cửa liền có thể nhìn thấy trong tấm bia đá!"),t("br"),t("br"),n._v("Đi vào di tích, người người đều nghĩ đến đi tìm bí ẩn nhất vị trí, khả năng nằm mộng cũng nghĩ không ra, thật ra thì vật trân quý nhất, liền giấu ở chỗ dễ thấy nhất!"),t("br"),t("br"),n._v("Bất quá, cái không gian này, cần Đại Bi Thiên Ma chưởng mới có thể kích hoạt, đi tới bia đá trước mặt, cho dù phát hiện đặc thù, không biết cái này chiêu, khẳng định cũng không cách nào tiến đến."),t("br"),t("br"),n._v("Chớ nói chi là nhận được truyền thừa."),t("br"),t("br"),n._v("“Diễn Không thiên thư, lấy Đại Diễn số lượng tiến hành suy tính, đem trời, không gian tháo gỡ ra đến, tiến hành khống chế...”"),t("br"),t("br"),n._v("Phía trên phù văn mặc dù không nhận ra, nhưng mà con mắt đảo qua, lập tức rõ ràng ý tứ trong đó."),t("br"),t("br"),n._v("“Bộ công pháp này, cùng Đại Bi Thiên Ma chưởng đồng dạng, đồng dạng chia làm ngũ trọng, đạt tới đệ ngũ trọng, liền có thể đứng tại thế giới chi đỉnh, so ta đều không hề yếu...”"),t("br"),t("br"),n._v("Từng nét bùa chú đi vào trong óc, Trương Huyền con mắt càng ngày càng sáng."),t("br"),t("br"),n._v("Lúc đầu, nắm giữ Thiên Đạo công pháp, đối Khưu Ngô cổ thánh truyền thừa, cũng không thèm để ý, nhưng đem phù văn xem hết, mới biết được bộ công pháp này mạnh mẽ, nghiêm túc tu luyện, đối không gian sẽ nắm giữ rất mạnh lực khống chế, thậm chí... Có thể tiện tay sắp đặt không gian gấp, rốt cuộc không cần bị động như thế."),t("br"),t("br"),n._v("Cùng người chiến đấu, lực lượng tuyệt đối sẽ bạo tăng không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Đem bộ này Diễn Không thiên thư, tu luyện tới nhị trọng đỉnh phong, gặp lại trước đó vị kia Lạc Thừa Tân cho dù đánh không lại, cũng hoàn toàn có thể chạy trốn...”"),t("br"),t("br"),n._v("Tràn đầy kích động, vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Lạc Thừa Tân là cho đến trước mắt, ra tay với hắn người mạnh nhất, Thánh vực thất trọng thực lực, cho dù dùng hết tất cả thủ đoạn, đều chiến thắng có điều, thậm chí trốn đều trốn không thoát... Thật là muốn tu luyện cái này Diễn Không thiên thư liền không đồng dạng!"),t("br"),t("br"),n._v("Không nói đạt tới ngũ trọng, chỉ cần đạt tới đệ nhị trọng đỉnh phong, liền có thể tuỳ tiện giãy giụa đối phương áp bức, thuận lợi bỏ trốn!"),t("br"),t("br"),n._v("Bất quá, bộ công pháp này, đối chân khí, lực lượng cũng có rất cao yêu cầu, muốn tu luyện đến đệ nhị trọng đỉnh phong, không có Xuất Khiếu cảnh đỉnh phong thực lực, hầu như làm không được."),t("br"),t("br"),n._v("“Nhìn một chút có thể hay không dung hợp thành Thiên Đạo công pháp!”"),t("br"),t("br"),n._v("Rất nhanh, đem đầy trời phù văn đều nhìn một lần, thuận tiện thu vào đi vào thư viện, Trương Huyền thở ra một hơi."),t("br"),t("br"),n._v("Những ngày gần đây, thu vào công pháp, bí tịch, số lượng đã đạt đến mức độ kinh người, có lẽ, có thể cùng cái này Diễn Không thiên thư thuận lợi dung hợp."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Một tiếng hô khẽ, một quyển sách xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Ngón tay ở phía trên một điểm, kiến thức chảy vào trong óc."),t("br"),t("br"),n._v("“Tạo thành đệ nhất trọng Thiên Đạo công pháp, đệ nhị trọng... Còn kém rất xa, còn lại càng không cần phải nói...”"),t("br"),t("br"),n._v("Rất mau nhìn xong, Trương Huyền trên mặt mừng rỡ."),t("br"),t("br"),n._v("Loại tình huống này, nằm trong dự liệu."),t("br"),t("br"),n._v("Mặc dù Diễn Không thiên thư là một bộ pháp quyết, nhưng nương theo cấp độ khác biệt, đối ứng phương pháp tu luyện cũng tất nhiên khác biệt."),t("br"),t("br"),n._v("Hắn trong khoảng thời gian này sưu tập công pháp, cấp bậc cao nhất, cũng chỉ là Thánh vực trung kỳ, tại Thanh Nguyên đế quốc xem như rất lợi hại, nhưng muốn dung hợp Diễn Không thiên thư còn kém quá xa."),t("br"),n._v(" Có thể đem đệ nhất trọng dung hợp hoàn chỉnh, thế là tốt rồi."),t("br"),t("br"),n._v("“Tu luyện một chút...”"),t("br"),t("br"),n._v("Tràn đầy kích động, đem đệ nhất trọng công pháp, thuộc làu trong lòng, rời khỏi bia đá không gian, tìm cái địa phương, lấy ra tinh nguyên linh thạch thượng phẩm, bắt đầu tu luyện."),t("br"),t("br"),n._v("Diễn Không thiên thư, là một loại mượn nhờ không gian lực lượng, tăng thực lực lên công pháp, không thuộc về võ kỹ, cũng không thuộc về tăng thêm tu vi công pháp."),t("br"),t("br"),n._v("Tu luyện tới đệ ngũ trọng, có thể thuần thục nắm giữ không gian, xuyên qua trong đó, ngày đi ngàn vạn, hơn trăm triệu cây số, đều không thành vấn đề."),t("br"),t("br"),n._v("Ngũ trọng công pháp, tổng cộng chia làm năm cái cảnh giới, theo thứ tự là, súc địa, xuyên qua, nắm giữ, sáng tạo cùng hủy diệt."),t("br"),t("br"),n._v("“Đệ nhất trọng, tu luyện tới cực hạn, có thể Súc Địa Thành Thốn, vừa sải bước ra khoảng trăm thước... Cùng người chiến đấu, hoàn toàn có thể làm được, trong nháy mắt tránh né hoặc là tiến công, chạy trốn cũng sẽ thuận lợi rất nhiều!”"),t("br"),t("br"),n._v("Phương pháp tu luyện ở trong lòng chảy xuôi, Trương Huyền chân khí trong cơ thể vận chuyển, rất nhanh lâm vào đặc thù ý cảnh, đối không gian lĩnh ngộ, cũng càng ngày càng nhiều."),t("br"),t("br"),n._v("Không biết qua bao lâu, con mắt mở ra, đứng dậy, tiến về phía trước một bước bước ra."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Đã xuất hiện tại trăm mét có hơn."),t("br"),t("br"),n._v("“Thành công?”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Tu luyện Diễn Không thiên thư cũng không thể để tu vi tăng thêm bao nhiêu, nhưng lại có thể để cho hắn đối không gian lĩnh ngộ, trở nên mạnh hơn, phối hợp thêm Hành Giả Vô Cương thân pháp, có thể để cho cả người trở nên xuất quỷ nhập thần, như quỷ giống như mị!"),t("br"),t("br"),n._v("“Loại tốc độ này cùng đối không gian lĩnh ngộ, cùng Điền phó đường chủ đều có thể một trận chiến...”"),t("br"),t("br"),n._v("Trước đó thực lực của hắn, cứ việc có thể vượt cấp chiến đấu, gặp được Điền phó đường chủ loại này nửa bước Xuất Khiếu cảnh cường giả, vẫn là không cách nào thắng được, có điều, học tập Diễn Không thiên thư liền không đồng dạng, đối không gian có rõ ràng hơn nhận biết, đối phương cho dù dùng nguyên thần áp bức, cũng có thể tuỳ tiện giãy giụa, thậm chí thần không biết quỷ không hay đi tới trước mặt, cấp cho một kích trí mạng."),t("br"),t("br"),n._v("“Càng quan trọng hơn là, học tập Thiên Đạo Diễn Không thiên thư đệ nhất trọng, ta đã có thể sắp đặt ra một chút không gian trận pháp, loại trận pháp này, mặc dù không cách nào cùng không gian gấp đồng dạng, dung nạp vật phẩm, lại có thể cắt đứt quan sát, thậm chí, đem đồ vật... Áp súc!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Trước đó, hồn thể quá lớn, không cách nào tan vào nguyên thai, nhưng... Hiện tại chỉ cần nghĩ biện pháp sắp đặt một cái không gian trận pháp, đem hắn áp súc, liền có khả năng nhẹ nhõm đi vào cửa, hoàn thành cả hai dung hợp."),t("br"),t("br"),n._v("“Công pháp thật là tốt, nhưng tiêu hao... Cũng không tránh khỏi quá lớn chút...”"),t("br"),t("br"),n._v("Cảm thụ hết biến hóa, nhìn xem đầy đất bã vụn, Trương Huyền xoa xoa mi tâm."),t("br"),t("br"),n._v("Vừa rồi chỉ tu luyện nhất trọng Diễn Không thiên thư, thậm chí còn không có đạt tới đỉnh phong nhất, thật vất vả lấy được 17 viên tinh nguyên linh thạch thượng phẩm, liền hoàn toàn tiêu hao sạch sẽ."),t("br"),t("br"),n._v("Bộ công pháp này, có thể làm cho người nhận Ngộ Không ở giữa, sức chiến đấu bạo tăng, là hiếm có tuyệt học... Nhưng đối linh thạch tiêu hao, cũng là một kiện rất khủng bố sự tình."),t("br"),t("br"),n._v("Vốn còn nghĩ, nhiều như vậy tinh nguyên linh thạch thượng phẩm, hẳn là có thể để kỳ trùng đánh Thai Anh cảnh đỉnh phong, kết quả, còn chưa bắt đầu, liền sử dụng hết, tưởng tượng liền tràn đầy tâm tắc."),t("br"),t("br"),n._v("“Nhất định phải nghĩ phương pháp kiếm tiền...”"),t("br"),t("br"),n._v("Đứng dậy, thân thể nhoáng một cái, rời đi Khưu Ngô cung."),t("br"),t("br"),n._v("Linh hồn đi vào nguyên thai phương pháp giải quyết, hiện tại trọng yếu nhất chính là, nghĩ biện pháp kiếm lấy tinh nguyên linh thạch thượng phẩm!"),t("br"),t("br"),n._v("Không có tiền thời gian... Một ngày cũng không tốt qua ah!"),t("br"),t("br"),n._v("“Đi nơi nào kiếm?”"),t("br"),t("br"),n._v("Trở lại chỗ ở, Trương Huyền cũng không cuống cuồng đi ra ngoài, mà là cúi đầu trầm tư."),t("br"),t("br"),n._v("Muốn kiếm tiền, cũng muốn tìm được trước địa phương mới được, Thanh Nguyên đế quốc hoàng thất, mới lấy ra 17 viên, Chiến sư đường cũng chỉ có mười cái..."),t("br"),t("br"),n._v("Nơi nào mới có thể tìm được càng nhiều?"),t("br"),t("br"),n._v("“Hỏi một chút Hình đường chủ!”"),t("br"),t("br"),n._v("Biết mình đối Thanh Nguyên thành không quen, nghĩ đến nhiều hơn nữa cũng vô dụng, Trương Huyền đứng dậy, đi ra khỏi phòng."),t("br"),t("br"),n._v("Lúc này, trời đã sáng choang, dương quang tự nhiên, cứ việc chỉ là buổi sáng, đã có chút cực nóng."),t("br"),t("br"),n._v("Tìm tới Hình đường chủ, chỉ thấy hắn đã bắt đầu tu luyện Lưu Ly Luyện Tâm kinh, mặc dù chỉ là mấy ngày ngắn ngủi, nguyên thần lại trở nên càng thêm tinh thuần, khí tức cũng càng thêm hùng hậu."),t("br"),t("br"),n._v("“Hình đường chủ, Hứa trưởng lão bọn họ thế nào? Còn nghe lời?”"),t("br"),t("br"),n._v("Trương Huyền biết đối phương tiếp tục tu luyện đi xuống, hẳn là không bao lâu, liền có thể kinh nghiệm xuất khiếu kiếp, xung kích Xuất Khiếu cảnh sơ kỳ, cười cười, hỏi."),t("br"),t("br"),n._v("Hôm qua Hứa trưởng lão đám người bị Chiến sư đường mang đi, dựa theo tình huống bình thường, cũng đã dàn xếp lại."),t("br"),t("br"),n._v("“Bọn họ rất nghe lời, bất quá... Hôm qua gặp Tôn Cường quản gia, toàn bộ đem hắn nhận được Huyền Huyền hội...”"),t("br"),t("br"),n._v("Hình đường chủ cười khổ."),t("br"),t("br"),n._v("Đám này độc sư, là rất nghe lời, lúc đầu đã thu xếp tốt, nhưng không biết tại sao gặp Trương sư quản gia Tôn Cường..."),t("br"),t("br"),n._v("Tôn Cường thực lực, cũng không thế nào, nhưng chẳng biết tại sao, Độc điện mọi người, nguyên một đám cung kính ghê gớm."),t("br"),t("br"),n._v("Ngay sau đó, người quản gia này, mời đối phương gia nhập, một đám độc sư... Liền thành Huyền Huyền hội thành viên."),t("br"),t("br"),n._v("Đương nhiên, đồng dạng thuộc về Chiến sư đường một phần tử."),t("br"),t("br"),n._v("“Dù sao về các ngươi Chiến sư đường quản lý, lẫn nhau học tập, trao đổi lẫn nhau, cũng cũng không tệ lắm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Như thế... Đúng, Trương sư, cái khác phong hào đế quốc Chiến sư đường, mấy ngày nay liền sẽ tới đây tiến hành trao đổi... Ngươi nhìn có rảnh hay không, cho trong đường Chiến sư nhóm, hơn mấy tiết khóa...”"),t("br"),t("br"),n._v("Hình đường chủ nói."),t("br"),t("br"),n._v("“Cái khác phong hào đế quốc Chiến sư đường? Trao đổi?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Đúng, không có gì bất ngờ xảy ra, trong ba ngày liền có thể đi tới!” Hình đường chủ nhẹ gật đầu."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1182"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1184"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);