(window.webpackJsonp=window.webpackJsonp||[]).push([[2190],{2545:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-893-day-bao-truong-huyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-893-day-bao-truong-huyen"}},[n._v("#")]),n._v(" Chương 893: Dạy bảo Trương Huyền?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 893: Dạy bảo Trương Huyền?"),t("br"),t("br"),n._v("Hắn muốn khảo hạch chính là danh sư, cùng đế quốc tám sào tre cũng đánh không đến!"),t("br"),t("br"),n._v("Danh sư khảo hạch trước kia trải qua đều là giáo thư dục nhân, chỉ điểm tu vi, cùng đế quốc loại này cơ cấu quyền lực, hoàn toàn là hai khái niệm."),t("br"),t("br"),n._v("Thấy hắn nghi ngờ, Mi trưởng lão cười cười: “Viện trưởng theo vương quốc cùng nhau đi tới, cũng biết quốc cùng quốc tầm đó khác biệt lớn nhất là cái gì?”"),t("br"),t("br"),n._v("“Khác nhau?” Trương Huyền sững sờ, nhớ lại một chút, nói: “Quốc cùng quốc tầm đó lớn nhất khác biệt là Danh Sư đường đẳng cấp!”"),t("br"),t("br"),n._v("Phong hào đế quốc, nhất đẳng đế quốc, nhị đẳng đế quốc, phong hào vương quốc, nhất đẳng vương quốc, nhị đẳng vương quốc... Những quốc gia này lớn nhất khác biệt là tổng hợp quốc lực."),t("br"),t("br"),n._v("Mà tạo thành quốc lực chênh lệch nguyên nhân căn bản là Danh Sư đường."),t("br"),t("br"),n._v("Danh sư giáo thư dục nhân, cấp bậc càng cao danh sư, giáo dục đi ra đệ tử càng cường đại, kéo theo hiệu ứng cũng liền càng kịch liệt."),t("br"),t("br"),n._v("Không nói cái khác, nếu như cho hắn lúc trước vị trí Thiên Huyền vương quốc, một cái nắm giữ lục tinh danh sư Danh Sư đường, cả quốc gia khẳng định sẽ có biến hóa nghiêng trời lệch đất, trở thành nhất đẳng đế quốc nhưng mà vấn đề thời gian."),t("br"),t("br"),n._v("Danh Sư đường kéo theo hiệu ứng thật sự là quá lớn, không chỉ bồi dưỡng học sinh, càng quan trọng hơn còn khả năng hấp dẫn càng nhiều cường giả đến đây!"),t("br"),t("br"),n._v("Một chỗ có danh sư đường, còn có thể thu hút cái khác tất cả chức nghiệp, Thú đường, luyện đan sư công hội, luyện khí sư công sẽ... Tất cả chức nghiệp đầy đủ, cao thủ lớp lớp, quốc gia muốn không cường đại cũng khó khăn."),t("br"),t("br"),n._v("Lúc trước Thiên Huyền vương quốc, nhưng mà cái bất nhập lưu vương quốc, nhưng có danh sư đường lời nói, hoàn toàn có thể tấn cấp cấp hai vương quốc."),t("br"),t("br"),n._v("“Không sai, chính là Danh Sư đường đẳng cấp!”"),t("br"),t("br"),n._v("Mi trưởng lão nhẹ gật đầu: “Danh Sư đường cấp bậc càng cao, quốc gia liền càng cường đại, đây đã là đại lục quy định bất thành văn, rất nhiều vương quốc, đế quốc, đều lấy Danh Sư đường làm trung tâm, thành lập quốc đô! Thật giống như Hồng Viễn thành, sở dĩ có thể ngật đứng không ngã, chủ yếu bởi vì nơi này có cái danh sư học viện.”"),t("br"),t("br"),n._v("Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Danh Sư đường mặc dù mặc kệ quốc gia hưng suy, nhưng mà có danh sư đường chỗ, nhất định cường giả như rừng, ngoại nhân không dám tùy tiện xâm phạm."),t("br"),t("br"),n._v("“Bình thường danh sư khảo hạch, nhưng mà đối một người mỗi tiếng nói cử động, nhất cử nhất động tiến hành kiểm tra đối chiếu sự thật, ngươi khác biệt, thân là học viện viện trưởng, không riêng gì người, còn liên quan toàn bộ học viện.”"),t("br"),t("br"),n._v("Thấy hắn hiểu được, Mi trưởng lão tiếp tục giải thích: “Cái gọi là tấn thăng đế quốc, kỳ thật chính là, để ngươi lựa chọn một cái phong hào vương quốc, nhờ vào cá nhân cố gắng, một lần nữa tổ kiến Danh Sư đường, để hắn thuận lợi tấn thăng làm đế quốc! Khảo hạch này không riêng gì năng lực cá nhân, càng là đối với toàn cục nắm chắc cùng đem khống, độ khó cực lớn!”"),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Trương Huyền da mặt nhảy lên, nhịn không được nhìn lại: “Cái này... Hiện tại không làm viện trưởng này còn kịp không?”"),t("br"),t("br"),n._v("“...” Mi trưởng lão."),t("br"),t("br"),n._v("“Sớm biết làm cái viện trưởng phiền toái như vậy, liền không nên đáp ứng, chuyện này là sao...” Trương Huyền mặt mũi u oán."),t("br"),t("br"),n._v("Vốn cho rằng trong sân dài chừng lấy chuyện gì đều không cần quản, còn có thể đi tàng thư quán tùy ý đọc sách, lúc này mới đáp ứng."),t("br"),t("br"),n._v("Ai biết, không chỉ kế vị đại điển phiền toái như vậy, khảo hạch lục tinh danh sư đều muốn gia tăng độ khó... Sớm biết như thế, làm cái này phá viện trưởng làm gì?"),t("br"),t("br"),n._v("Còn không bằng an ổn khảo hạch cái lục tinh danh sư, đem tu vi tấn cấp đến Thánh vực, xoay người rời đi... Cái này tốt, bị trói tại học viện cái này xe nát bên trên!"),t("br"),t("br"),n._v("Thật là một mất đủ thành thiên cổ hận ah!"),t("br"),t("br"),n._v("Trương Huyền vô cùng buồn phiền."),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Nhanh sặc nước bọt mà chết, Mi trưởng lão một mặt im lặng nhìn qua: “Mặc dù khảo hạch lục tinh danh sư độ khó gia tăng, một khi khảo hạch thông qua, cơ sở cũng sẽ càng thêm vững chắc, khảo hạch cấp bậc cao hơn danh sư cũng sẽ dễ dàng không ít! Nguyên nhân chính là như thế, không ít thiên phú cao danh sư, đều nguyện ý xin độ khó càng lớn khảo hạch, tới rèn luyện bản thân.”"),t("br"),t("br"),n._v("Người khác có thể lên làm viện trưởng, sẽ cao hứng tìm không thấy phương hướng, vị này Trương sư ngược lại tốt, thế mà mặt mũi u oán..."),t("br"),t("br"),n._v("Nếu như ngươi bộ dáng này, bị Lục Phong biết, không biết có thể hay không một ngụm lão huyết phun ra, tươi sống tức chết."),t("br"),t("br"),n._v("tＲuy cậ"),t("br"),n._v("p http://ngantruyen.com/ để đọc truyện  Vì viện trưởng này, trả giá nhiều như vậy, rút cuộc lấy giỏ trúc mà múc nước, ngươi trắng trắng nhận được, còn không tình nguyện..."),t("br"),t("br"),n._v("“Dễ dàng khảo hạch cấp bậc cao hơn danh sư?”"),t("br"),t("br"),n._v("“Vâng, nếu như viện trưởng có thể thông qua tấn thăng đế quốc khảo nghiệm, về sau khảo hạch thất tinh danh sư thời điểm, chỉ cần ba loại cơ sở đầy đủ, liền có thể trực tiếp xin! Không cần lại tiến hành... Giáo thư dục nhân, chỉ điểm tu vi mấy người khảo hạch!”"),t("br"),t("br"),n._v("Mi trưởng lão giải thích: “Nói cách khác, chỉ cần có thể hoàn thành cái này khảo nghiệm, chẳng khác nào nắm giữ thông hướng thất tinh danh sư giấy thông hành, lấy sau tiếp tục tích lũy tu vi, rèn luyện tâm tình, hoàn thành phụ tu là được!”"),t("br"),t("br"),n._v("“Còn có loại chuyện tốt này?” Nghe nói như thế, nguyên bản buồn bực Trương Huyền ánh mắt đột nhiên sáng lên."),t("br"),t("br"),n._v("Mỗi lần danh sư khảo hạch, đều cần đủ loại quy tắc một đống lớn, thông qua cái khảo hạch này, về sau có thể trực tiếp trở thành thất tinh danh sư lời nói, tương đương với tiết kiệm thật nhiều phiền phức, giá trị tuyệt đối."),t("br"),t("br"),n._v("“Đúng vậy a!” Mi trưởng lão gật đầu."),t("br"),t("br"),n._v("Mặc dù thoạt nhìn, tiết kiệm được khảo hạch thất tinh danh sư trình tự, nhưng tấn thăng một cái đế quốc, cần không riêng gì năng lực, ánh mắt, cùng chỉ điểm, càng quan trọng hơn vẫn là thời gian!"),t("br"),t("br"),n._v("Để một cái phong hào vương quốc tấn thăng làm đế quốc, dù chỉ là bất nhập lưu, cũng không phải một hai người cao thủ, liền có thể giải quyết, mà là hùng hậu nội tình!"),t("br"),t("br"),n._v("Tăng lên nội tình, tất nhiên cần tốn thời gian rất lâu, năm đó Mạc Lưu Chân viện trưởng, liền lựa chọn cái khảo hạch này, vì tấn thăng đế quốc, cho dù hắn loại thiên phú này, cũng đầy đủ hao tốn ba năm thời gian!"),t("br"),t("br"),n._v("“Tốt, ta quyết định, liền dùng tấn thăng đế quốc tới khảo hạch lục tinh danh sư!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“...” Mi trưởng lão lần nữa không nói."),t("br"),t("br"),n._v("Làm sao cảm giác chính mình cái này viện trưởng, làm sự tình như thế trò đùa đâu?"),t("br"),t("br"),n._v("Đây chính là khảo hạch lục tinh danh sư bên trong khó khăn nhất, rất nhiều người tránh không kịp, muốn cự tuyệt liền cự tuyệt, nói đáp ứng liền đáp ứng, có thể hay không lo lắng nhiều một hồi?"),t("br"),t("br"),n._v("Coi như thật đồng ý, cũng nhăn cái lông mày, trên mặt thuận tiện tới điểm làm khó biểu lộ, ý tứ ý tứ a?"),t("br"),t("br"),n._v("Ngươi ngược lại tốt, trực tiếp điểm đầu đáp ứng, mặt không đổi sắc, có phải hay không không có ý thức được cái khảo hạch này độ khó?"),t("br"),t("br"),n._v("Đang muốn nhắc nhở một chút, lập tức lắc đầu."),t("br"),n._v(" Vị này tân nhiệm viện trưởng, mỗi lần cũng có thể làm ra làm người ta nghẹn họng nhìn trân trối sự tình, sáng tạo ra làm người ta khó có thể tin kỳ tích."),t("br"),t("br"),n._v("Thật giống như trước đó Vân Vụ lĩnh, hắn cho rằng thật là quá khứ đàm phán, rút cuộc, là đi qua mang nhân gia hang ổ!"),t("br"),t("br"),n._v("Trong mắt hắn khó khăn nhất tấn thăng đế quốc, có lẽ tại trong mắt đối phương, không đáng kể chút nào."),t("br"),t("br"),n._v("Ngẩng đầu nhìn một chút thời gian, biết kế vị đại điển cần phải chuẩn bị từ sớm, không trong vấn đề này tiếp tục dây dưa: “Viện trưởng, đi học viện đi, Triệu Bính Tuất trưởng lão cùng Thiên Công viện, đã vì ngươi chuyên môn làm theo yêu cầu một bộ y phục, tốt nhất hiện tại tại quá khứ thử xem có vừa người không!”"),t("br"),t("br"),n._v("“Còn làm quần áo?” Sửng sốt một chút, Trương Huyền cũng không nhiều lời."),t("br"),t("br"),n._v("Viện trưởng kế vị đại điển, long trọng mà tráng lệ, mặc một bộ bình thường danh sư phục tự nhiên không được."),t("br"),t("br"),n._v("Cách Khai Phủ để, hướng học viện đi đến."),t("br"),t("br"),n._v("Lúc này danh sư học viện, người ta tấp nập, Hồng Viễn thành tất cả gia tộc, xung quanh các đại đế quốc... Vô số cường giả hội tụ một tiết, tuyệt đối là hiếm có rầm rộ."),t("br"),t("br"),n._v("Vì phòng ngừa người khác phát hiện gây nên phiền toái không cần thiết, hai người ngồi Thánh thú, trực tiếp đi vào trưởng lão viện."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Danh sư học viện, dòng người nhúc nhích, khắp nơi đều là bóng người."),t("br"),t("br"),n._v("“Nhược Hoan, tin tức của ngươi linh thông, nhưng biết tân viện trưởng là ai?”"),t("br"),t("br"),n._v("Trên đường phố rộng rãi, một thanh niên nhịn không được hỏi."),t("br"),t("br"),n._v("Trước mặt của hắn, là cái thanh niên áo trắng, tay cầm quạt xếp, có vẻ ngọc thụ lâm phong, một bộ thế gia công tử diện mạo."),t("br"),t("br"),n._v("Chính là cùng Trương Huyền cùng một chỗ theo vạn quốc liên minh tới Nhược Hoan công tử."),t("br"),t("br"),n._v("Mà hắn đối diện tra hỏi, cũng là Trương Huyền lão bằng hữu, Huyền Huyền hội người sáng lập một trong... Tống Siêu!"),t("br"),t("br"),n._v("“Ta chỗ nào có thể biết!”"),t("br"),t("br"),n._v("Nhược Hoan công tử lắc đầu: “Tân viện trưởng là học viện bí mật lớn nhất, đoán chừng chỉ có thể chờ đợi kế vị lễ bắt đầu mới hiểu!”"),t("br"),t("br"),n._v("Mặc dù Huyền Huyền hội nhân viên phần đông, chiếm gần toàn bộ học viện một nửa học viên, nhưng đối với tân viện trưởng là ai, cũng là không có dò xét điều tra ra."),t("br"),t("br"),n._v("“Là ai cũng tốt, tuyệt đối không nên là Lục Phong trưởng lão... Hắn và hội trưởng có mâu thuẫn, một khi thành viện trưởng, chúng ta Huyền Huyền hội, tám chín phần mười sẽ bị giải tán!”"),t("br"),t("br"),n._v("Tống Siêu mang trên mặt lo lắng."),t("br"),t("br"),n._v("“Đúng vậy a, chỉ có thể trông đợi không phải hắn đi!”"),t("br"),t("br"),n._v("Lắc đầu, Nhược Hoan công tử đang muốn tiếp tục nói chuyện, chỉ thấy một thanh niên vội vã chạy tới: “Nhược Hoan, ngươi cũng đã biết hội trưởng hiện tại ở đâu?”"),t("br"),t("br"),n._v("Là Huyền Huyền hội một cái thành viên, năm thứ ba học sinh."),t("br"),t("br"),n._v("“Gấp gáp như vậy tìm hội trưởng làm gì?” Nhược Hoan công tử cau mày."),t("br"),t("br"),n._v("Nói thật, vị hội trưởng này, thật đúng là vung tay chưởng quỹ, hắn cũng thật nhiều ngày không gặp, từ trước tới nay thần long thấy đầu mà không thấy đuôi, thật không biết đi nơi nào."),t("br"),t("br"),n._v("“Là như vậy, vừa rồi ta chuẩn bị đi diễn võ trường, chiếm chỗ ngồi chờ viện trưởng kế vị đại điển, ai biết... Còn chưa tới chỗ, liền nghe đến mấy cái vẻ mặt khả nghi gia hỏa, đang khắp nơi hướng người hỏi thăm hội trưởng tung tích, xem ra không có ý tốt...”"),t("br"),t("br"),n._v("Bốn phía nhìn thoáng qua, thanh niên thấp giọng."),t("br"),t("br"),n._v("“Hỏi thăm hội trưởng tung tích?” Nhược Hoan công tử nhướng mày."),t("br"),t("br"),n._v("“Vâng!” Thanh niên liền vội vàng gật đầu."),t("br"),t("br"),n._v("Toàn bộ học viện, nối gần một nửa thầy trò, đều là Huyền Huyền hội người, mấy người này hỏi thăm “Trương Huyền”, tự nhiên đưa tới chú ý."),t("br"),t("br"),n._v("“Mang ta đi nhìn xem!” Nhược Hoan công tử bàn giao, lập tức dừng lại một chút: “Trước không vội, đi đem Ưng Cần bọn họ gọi tới!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thanh niên vội vã mà đi, không lâu sau, Ưng Cần, bột mì đám người liền theo ở phía sau đi tới."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!” Gặp người đến đủ, Nhược Hoan công tử cười cười, để thanh niên dẫn đường."),t("br"),t("br"),n._v("Chỉ chốc lát, quả nhiên thấy bảy, tám cái quần áo cổ quái gia hỏa, một mặt cảnh giác đi trong sân, gặp được người liền hỏi thăm Trương Huyền tung tích."),t("br"),t("br"),n._v("Bất quá, Trương sư hiện tại liền hắn cũng không tìm tới, những người này tự nhiên càng không cách nào tìm kiếm."),t("br"),t("br"),n._v("Chậm rãi tới gần, đem lỗ tai dựng thẳng lên, lập tức nghe được đối phương cố ý đè thấp đối thoại."),t("br"),t("br"),n._v("“Liễu đại thiếu để chúng ta tới dạy bảo cái này Trương Huyền, người tìm không thấy làm sao bây giờ? Cái tên này sẽ không, trước thời hạn biết tin tức, sợ tới mức giấu rồi a?”"),t("br"),t("br"),n._v("Trong đám người một cái tên mặt thẹo, tràn đầy xoắn xuýt."),t("br"),t("br"),n._v("Hỏi thật nhiều người, đều biết Trương Huyền, nhưng cũng không biết ở đâu, để bọn hắn tràn đầy đau đầu."),t("br"),t("br"),n._v("“Dạy bảo? Xem ra là kẻ địch!”"),t("br"),t("br"),n._v("Nghe vào trong tai, Nhược Hoan công tử cùng Tống Siêu đám người liếc mắt nhìn nhau, đồng thời nhẹ gật đầu."),t("br"),t("br"),n._v("Dám giáo huấn bọn họ hội trưởng, đây không phải muốn chết sao?"),t("br"),t("br"),n._v("Đã như vậy, vậy liền để các ngươi mở mang kiến thức một chút..."),t("br"),t("br"),n._v("Chúng ta Huyền Huyền hội lực lượng!"),t("br"),t("br"),n._v("Tin hay không trực tiếp dọa chết các ngươi nha?"),t("br"),t("br"),n._v("(Trong nhà có chuyện xảy ra, không cách nào bộc phát, nhưng mà tháng này nguyệt phiếu liên quan hàng năm tổng bảng, hi vọng các vị có nguyệt phiếu đều ném một chút rất nhai, chờ hài tử trị hết bệnh, về nhà liền bộc phát. Đa tạ!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-901"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-903"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);