(window.webpackJsonp=window.webpackJsonp||[]).push([[2193],{2548:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-896-hoc-sinh-o-giua-chien-đau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-896-hoc-sinh-o-giua-chien-đau"}},[n._v("#")]),n._v(" Chương 896: Học sinh ở giữa chiến đấu")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 896: Học sinh ở giữa chiến đấu"),i("br"),i("br"),n._v("“Cứu mạng?”"),i("br"),i("br"),n._v("Vị này tân viện trưởng, lợi dụng sư ngôn thiên bẩm, để hắn trước mặt mọi người mất mặt, chẳng những không có nổi giận, ngược lại cảm ơn ơn cứu mệnh của hắn..."),i("br"),i("br"),n._v("Tận mắt nhìn thấy, đều để mọi người không hiểu ra sao."),i("br"),i("br"),n._v("Sự tình biến hóa, làm cho người rất không thể tưởng tượng nổi."),i("br"),i("br"),n._v("“Tại hạ nóng lòng cầu thành, trong tu luyện xuất hiện chỗ nhầm lẫn, từ đó làm cho chân khí trong cơ thể chạy không thoải mái, Hổ Dương huyệt mơ hồ phát đau, thận lực suy kiệt, vốn cho rằng không phải cái đại sự gì, chỉ cần về sau thêm chút chú ý liền có thể giải quyết!”"),i("br"),i("br"),n._v("Thấy mọi người là mê man, Liêu Thông đường chủ lắc đầu, tiến về phía trước một bước: “Đi qua Trương sư chỉ điểm mới biết được, cái này đã thương tới thân thể căn bản. Tự đánh hai bàn tay, mở ra phần cổ long khê huyệt, xích dương huyệt! Huyệt vị mở ra, trước đó ràng buộc, lập tức không trở ngại. Sau đó lại tiến hành chồng cây chuối, để chân khí trong cơ thể, trôi chảy lưu thông... Mới một lần hành động giải quyết tai hoạ ngầm!”"),i("br"),i("br"),n._v("“Ta nghi ngờ Trương viện trưởng, phát động khiêu chiến, hắn nhưng không so đo những thứ này, giúp ta chữa thương... Chân chính danh sư lòng dạ! Từ hôm nay trở đi ta Nguyên Cương đế quốc Danh Sư đường, ủng hộ hắn là viện trưởng, đến chết không thay đổi!”"),i("br"),i("br"),n._v("Giơ bàn tay lên, Liêu Thông đường chủ nói."),i("br"),i("br"),n._v("“Còn có thể như vậy?”"),i("br"),i("br"),n._v("“Đánh hai cái bạt tai, sau đó chồng cây chuối, liền đem tai hoạ ngầm giải quyết...”"),i("br"),i("br"),n._v("Dưới đài một hồi xôn xao, nhiều người đưa mắt nhìn nhau, nếu không phải tận mắt thấy trước đó làm khó, thật hoài nghi có phải hay không vị này Liêu đường chủ tại cùng đối phương hát đôi."),i("br"),i("br"),n._v("“Cái này Trương viện trưởng...”"),i("br"),i("br"),n._v("Hoàng đế ngồi vào bên trong, chử đã nuốt ngụm nước bọt: “Nhìn cách thật là có chút thực lực!”"),i("br"),i("br"),n._v("Trước đó hắn cùng mấy vị đường chủ đồng dạng tràn đầy nghi ngờ, cảm thấy người trước mắt này còn quá trẻ, lên làm viện trưởng tất nhiên là có nguyên nhân khác, nhìn thấy liên tục giải quyết hai vị đường chủ nghi ngờ, cấp tốc mau lẹ, không dây dưa dài dòng, nghiễm nhiên có mọi người phong độ, trong lòng không khỏi xuất hiện biến hóa."),i("br"),i("br"),n._v("“Tiếp tục hướng xuống xem đi, ta vẫn cảm thấy quá trẻ tuổi...”"),i("br"),i("br"),n._v("Một vị khác Hoàng đế bệ hạ, lắc đầu."),i("br"),i("br"),n._v("Mặc dù tuổi tác không phải danh sư nhân tố quyết định, nhưng cũng ảnh hưởng cực lớn, không có tuế nguyệt chìm xuống đáy, tâm tính, phẩm đức, đều khó tránh khỏi có chỗ tùy tiện, để cho người ta không dám tin đảm nhiệm."),i("br"),i("br"),n._v("“Trương sư tâm cảnh khắc độ, liền Liêu đường chủ cũng không sánh nổi, tại hạ bội phục.”"),i("br"),i("br"),n._v("Đang khi nói chuyện, lại có một cái lão giả đứng lên: “Bất quá, muốn trở thành viện trưởng còn nhất định phải có giáo thư dục nhân bản lĩnh, tại hạ bất tài, chỉ điểm qua mấy cái học sinh, hơn nữa hôm nay đều đến nơi này. Không biết Trương sư học sinh ở đâu, có thể hay không để bọn hắn lẫn nhau luận bàn thoáng cái, cũng tốt chứng minh, giảng bài ưu khuyết?”"),i("br"),i("br"),n._v("“Học sinh luận bàn?”"),i("br"),i("br"),n._v("“Đề nghị này đủ hung ác!”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe nói như thế, chung quanh một mảnh xôn xao."),i("br"),i("br"),n._v("Lão sư chức trách là giảng bài."),i("br"),i("br"),n._v("Phán xét ưu khuyết trực tiếp nhất thủ đoạn, chính là giáo dục học sinh, học sinh càng mạnh, nói rõ càng kịch liệt."),i("br"),i("br"),n._v("Đường đường danh sư học viện viện trưởng, không có truyền thụ học sinh năng lực, nói dễ nghe đi nữa, đều là uổng công."),i("br"),i("br"),n._v("Có thể nói vị lão giả này, so phía trước hai cái ác hơn!"),i("br"),i("br"),n._v("Nếu như nói Ngũ Nguy Thiên, Liêu Thông chỉ là đánh mặt lời nói, cái tên này tương đương sẽ người ép lên ngõ cụt."),i("br"),i("br"),n._v("Vị này Trương sư, là thiên tài, năng lực bản thân rất mạnh, nhưng... Chỉ có hai mươi tuổi, thực lực lại chỉ có Tằm Phong cảnh, có thể có bao nhiêu lợi hại học sinh?"),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Vị lão giả này vừa dứt lời, một người trung niên đứng lên, hai tay chấn động, một cỗ hùng hậu hơi thở xông thẳng lên trời."),i("br"),i("br"),n._v("Lại là cái Thánh vực nhất trọng hậu kỳ cường giả!"),i("br"),i("br"),n._v("Loại thực lực này, coi như tại danh sư học viện, đều tuyệt đối là trưởng lão cấp bậc."),i("br"),i("br"),n._v("“Viện trưởng... Vừa rồi cái kia là Bạch Thác đế quốc Danh Sư đường Tiết Thương Vân đường chủ! Vị này nếu như không nhìn lầm, hẳn là hắn học sinh thân truyền, Hồng Lâm... Cùng hắn học tập đã vượt qua một trăm năm, ba mươi lăm năm trước liền đạt đến Thánh vực nhất trọng...”"),i("br"),i("br"),n._v("Triệu Bính Tuất vội vàng truyền âm."),i("br"),i("br"),n._v("“Giáo dục vượt qua một trăm năm?” Trương Huyền không còn gì để nói."),i("br"),i("br"),n._v("Cũng điên rồi, Thánh vực nhất trọng hậu kỳ, lại cùng một vị đường chủ học tập vượt qua trăm năm, bất kể thực lực còn là ánh mắt, khẳng định đều là cực cao, có thể nói, cùng cấp bậc vô địch."),i("br"),i("br"),n._v("Đổi lại yêu cầu khác, có lẽ còn có thể từ chối thoáng cái, loại học sinh này ở giữa tỷ thí, liên quan sư Đạo Tôn nghiêm cùng lão sư cơ sở năng lực, căn bản từ chối không mất."),i("br"),i("br"),n._v("Nói cách khác, đối phương khiêu chiến, làm danh sư, kiêm học viện viện trưởng, tiếp cũng phải tiếp, không tiếp cũng phải tiếp, không thể lui về phía sau cũng không cách nào lui về phía sau."),i("br"),i("br"),n._v("Không hổ là một phương Danh Sư đường người mạnh nhất vật, cả đám đều không phải đèn đã cạn dầu."),i("br"),i("br"),n._v("Trong lòng đang cảm khái, gọi là Hồng Lâm trung niên nhân liền nhìn lại, hai tay ôm quyền: “Tại hạ nguyện cùng viện trưởng học sinh một trận chiến, không biết hắn (nàng) bọn họ người ở chỗ nào?”"),i("br"),i("br"),n._v("“Chúng ta tại đây!” Vừa dứt lời, mấy cái thanh âm thanh thúy vang lên."),i("br"),i("br"),n._v("Mọi người đồng loạt nhìn lại, lập tức nhìn thấy Vương Dĩnh, Trịnh Dương đám người đứng lên."),i("br"),i("br"),n._v("Bọn họ là học viện khách tọa trưởng lão, tân viện trưởng kế vị lễ, tự nhiên không thể vắng mặt."),i("br"),i("br"),n._v("Lúc này Vương Dĩnh, đã đột phá Hợp Linh cảnh đạt đến Kiều Thiên cảnh."),i("br"),i("br"),n._v("Mười sáu mười bảy tuổi Kiều Thiên cảnh, đi đến bất kỳ địa phương nào, đều được cho tuyệt đỉnh thiên tài, nhưng thật muốn cùng vị này Hồng Lâm tỷ thí, còn là kém thật lớn một đoạn."),i("br"),i("br"),n._v("“Không có hi vọng chiến thắng...”"),i("br"),i("br"),n._v("Võ kỹ học viện viện trưởng Tu Trường Thanh, lắc đầu."),i("br"),i("br"),n._v("Vương Dĩnh đám người là hắn mời mời tới, làm khách tọa trưởng lão, đối thực lực của bọn hắn biết đến rất rõ ràng."),i("br"),i("br"),n._v("Cơ sở đánh rất kiên cố, cùng cấp bậc được cho hàng đầu, nhưng Hồng Lâm loại này cường giả Thánh vực thực sự quá cường đại, coi như giảm thấp xuống tu vi, nhờ vào nhãn lực cùng kiến thức, cũng có thể nhẹ nhõm đem hắn nghiền ép!"),i("br"),i("br"),n._v("Sáu cái đại cấp bậc chênh lệch, căn bản không phải cơ sở vững chắc hoặc đối vũ khí chân ý lý giải sâu, liền có thể bù đắp."),i("br"),i("br"),n._v("“Các ngươi là viện trưởng học sinh? Ha ha, ta có thể đem tu vi áp chế đến giống như các ngươi, cùng các ngươi chiến đấu!”"),i("br"),i("br"),n._v("Thấy Vương Dĩnh đám người đi tới, mạnh nhất không qua cầu Thiên Cảnh, Hồng Lâm nhịn không được cười to một tiếng."),i("br"),n._v("Loại thực lực này, một hơi có thể thổi chết mấy cái, không cần nghĩ, cái này tỷ thí, hắn thắng chắc!"),i("br"),i("br"),n._v("“Vậy thì tới đi!” Vương Dĩnh hét dài một tiếng, đi đầu nhảy lên đài cao, Hồng Lâm nở nụ cười, cũng đi theo."),i("br"),i("br"),n._v("Thấy hai người giương cung bạt kiếm, lập tức liền muốn chiến đấu, Trương Huyền đang định bàn giao hai câu, liền nghe một cái thanh âm nhàn nhạt vang lên."),i("br"),i("br"),n._v("“Vương Dĩnh sư tỷ, thân là lão sư thân truyền, tự mình ra tay, làm mất thân phận, còn là ta cái này giảng bài học sinh làm thay đi!”"),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Thanh âm kết thúc, một bóng người già nua rơi vào trên đài, đối với Trương Huyền khom người đến cùng, hai tay ôm thực: “Học sinh gặp qua lão sư!”"),i("br"),i("br"),n._v("“Ừm!” Nhìn thấy đối phương, Trương Huyền ánh mắt sáng lên, cười khổ một tiếng, làm sao đem hắn quên?"),i("br"),i("br"),n._v("Không là người khác, chính là Ma âm viện viện trưởng, Tưởng Thanh Cầm."),i("br"),i("br"),n._v("Đây chính là bái sư, chân chính giảng bài học sinh, hoàn toàn có thể thay thế Vương Dĩnh chiến đấu."),i("br"),i("br"),n._v("“Đây không phải Tưởng viện trưởng ư?”"),i("br"),i("br"),n._v("“Thập đại trường lão một trong Tưởng viện trưởng là Trương sư học sinh?”"),i("br"),i("br"),n._v("“Đùa giỡn a? Làm sao có thể?”"),i("br"),i("br"),n._v("“Không biết, danh sư chú trọng nhất sư đạo truyền thừa, Tưởng viện trưởng càng là lục tinh đỉnh phong danh sư, làm sao có thể loạn nhận lão sư? Loại sự tình này đã mở miệng, liền tuyệt sẽ không đùa giỡn!”"),i("br"),i("br"),n._v("“Mặc dù biết thật sự, nhưng vì cái gì khó mà tin được đâu?”"),i("br"),i("br"),n._v("“Ai nói không phải, Trương sư thuần phục lão viện trưởng thú sủng... Hiện tại Tưởng viện trưởng càng là học sinh của hắn, thế nào cảm giác giống như là đang nằm mơ!”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Trương Huyền thu Tưởng Thanh Cầm là học sinh, chưa hề truyền ra ngoài, loại trừ thập đại trường lão không người biết được, dưới đài tất cả thầy trò, lần đầu tiên nghe nói tin tức này, tất cả đều nguyên một đám mắt lớn trừng mắt nhỏ."),i("br"),i("br"),n._v("Một cái tứ tinh danh sư thu lục tinh đỉnh phong danh sư là học sinh, coi như tận mắt nhìn thấy, đều cảm thấy có phải hay không trò đùa."),i("br"),i("br"),n._v("“Tưởng viện trưởng...”"),i("br"),i("br"),n._v("Dưới đài Tiết Thương Vân đường chủ thân thể nhoáng một cái, thiếu chút nữa ngất đi."),i("br"),i("br"),n._v("Còn tưởng rằng thực lực của đối phương thấp, có thể có Kiều Thiên cảnh học sinh, cũng rất không tệ, rút cuộc thoáng cái toát ra một cái thực lực so với hắn đều cường đại hơn thập đại trường lão, còn thế nào đánh?"),i("br"),i("br"),n._v("Đang mặt mũi buồn bực, liền nghe đến trên đài Tưởng Thanh Cầm nhẹ nhàng cười một tiếng, thanh âm phá không truyền đến."),i("br"),i("br"),n._v("“Ngươi là Tiết Thương Vân đường chủ học sinh? Ha ha, ta có thể đem tu vi áp chế đến giống như ngươi, cùng ngươi chiến đấu!”"),i("br"),i("br"),n._v("Nghe nói như thế, đối diện Hồng Lâm, nheo mắt."),i("br"),i("br"),n._v("Đây là hắn vừa rồi đối Trương sư tất cả học sinh nói, đối phương một chữ không đổi đưa trở về."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Biết không cách nào tránh né, Hồng Lâm cắn răng một tiếng la lên: “Vậy thì tới đi!”"),i("br"),i("br"),n._v("Đang khi nói chuyện, lòng bàn tay nhiều ra một thanh trường kiếm."),i("br"),i("br"),n._v("Ầm!"),i("br"),i("br"),n._v("Vạch ra một đạo kiếm mang, thẳng tắp hướng về phía trước đâm tới."),i("br"),i("br"),n._v("Không thể không nói, cái tên này đối với kiếm pháp lý giải, cực kỳ cao minh, đã đạt đến nhân kiếm hợp nhất cảnh giới, coi như không thể cùng Trương Huyền so sánh, nhưng cùng Vương Dĩnh đám người so cũng không hề yếu."),i("br"),i("br"),n._v("Nói cách khác, thật để cho Vương Dĩnh đám người đi lên tỷ thí lời nói, muốn chiến thắng, hầu như không có khả năng."),i("br"),i("br"),n._v("Coi như không nói nhãn lực, kiến thức, chỉ nói đối phương cứu sống mấy trăm năm kinh nghiệm chiến đấu, cũng không phải là mấy vị này không có trải qua sinh tử chiến học viên, có thể so sánh."),i("br"),i("br"),n._v("Bất quá, chút năng lực nhỏ nhoi ấy, đối Tưởng viện trưởng tới nói, liền không coi vào đâu, nhẹ nhàng cười một tiếng, lòng bàn tay nhiều làm ra một bộ đàn ngọc, ngón tay dựng ở phía trên nhẹ nhàng phất một cái, một đạo lanh lảnh tiếng đàn, lập tức vang lên."),i("br"),i("br"),n._v("Leng keng!"),i("br"),i("br"),n._v("Bình bạc chợt phá, chuông đồng vang minh."),i("br"),i("br"),n._v("Hồng Lâm còn không có kịp phản ứng, phía trước kiếm mang liền vỡ vụn thành từng mảnh, ngay sau đó giống như đánh bay gián đồng dạng, “Hô!” Theo trên đài cao bay ra ngoài, hóa thành một tia sáng, biến mất tại trước mắt mọi người."),i("br"),i("br"),n._v("Xem ra coi như không chết, cũng gần như muốn nằm lên đã nhiều ngày."),i("br"),i("br"),n._v("“Thất Tinh Ma âm sư?”"),i("br"),i("br"),n._v("Tiết Thương Vân chợt đứng lên."),i("br"),i("br"),n._v("Dựa theo tình huống bình thường, coi như Tưởng viện trưởng cùng Hồng Lâm thực lực có khoảng cách, nhưng muốn cùng cấp bậc tuỳ tiện đem hắn đánh bại cũng là rất khó làm được, trừ phi hắn đối tiếng đàn lý giải đã đạt đến đệ thất cảnh!"),i("br"),i("br"),n._v("Loại cấp bậc này Ma âm sư, hắn gặp gỡ cũng chỉ có thể trốn có thể trốn bao xa, trốn bao xa, tuyệt không dám chọi cứng."),i("br"),i("br"),n._v("Trước kia Tưởng viện trưởng, tại thập đại trường lão bên trong, không lộ ra trước mắt người đời, nhận vị này Trương Huyền vi sư, ngược lại cũng thôi, loại thực lực này vậy mà cũng nhận..."),i("br"),i("br"),n._v("Nổi điên làm gì?"),i("br"),i("br"),n._v("Đang đang nghi ngờ, cảm thấy không thể nào hiểu được thời điểm, liền nghe đến cách đó không xa, cùng là thập đại trường lão một trong Vệ Nhiễm Tuyết tràn đầy buồn bực bực tức tiếng vang lên."),i("br"),i("br"),n._v("“Đáng giận, để cái tên này ra danh tiếng! Trương sư đối ta có nửa sư tình nghĩa, ta cũng coi như nửa người môn sinh... Đối phó loại người này, giảng bài học sinh đều lãng phí, ta một cái nửa sư tình nghĩa người đi lên là đủ rồi...”"),i("br"),i("br"),n._v("“Vệ viện trưởng cũng là học sinh của hắn?” Tiết đường chủ mắt tối sầm lại."),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Học viện tất cả thầy trò cũng tất cả đều nhoáng một cái, lần nữa nhìn về phía trên đài Trương Huyền, nguyên một đám khóe miệng cuồng quất."),i("br"),i("br"),n._v("Đại ca... Học viện trấn viện Thánh thú là ngươi thú sủng chúng ta nhịn; Thành lập Huyền Huyền hội, để học viện hơn phân nửa học sinh trở thành hội viên, cũng nhịn; Truyền thụ luyện đan, để mấy vạn luyện đan học viện học sinh, nắm giữ nửa sư tình nghĩa, cũng có thể nhịn..."),i("br"),i("br"),n._v("Nhưng thập đại trường lão ít nhất hai vị là ngươi học sinh... Cái này tuyệt bức không thể nhịn đi!"),i("br"),i("br"),n._v("Danh sư học viện là nhà ngươi mở sao?"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-904"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-906"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);