(window.webpackJsonp=window.webpackJsonp||[]).push([[2195],{2551:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-898-danh-su-hoc-vien-khiep-so-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-898-danh-su-hoc-vien-khiep-so-ha"}},[n._v("#")]),n._v(" Chương 898: Danh sư học viện khiếp sợ (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 898: Danh sư học viện khiếp sợ (hạ)"),t("br"),t("br"),n._v("“Không phải nói cùng đối phương đàm phán, lại sẽ không xuất hiện thú triều sao? Tại sao có thể như vậy?”"),t("br"),t("br"),n._v("“Nhiều như vậy linh thú, Thánh thú, đem chúng ta bao vây, căn bản ngăn cản không nổi!”"),t("br"),t("br"),n._v("“Cái này xong!”"),t("br"),t("br"),n._v("“Chỉ sợ là danh sư học viện xây trường đến nay lớn nhất nguy cơ, so bốn ngàn năm trước, Mạc Lưu Chân viện trưởng tại vị lúc, gặp phải nguy cơ đều muốn lớn...”"),t("br"),t("br"),n._v("Qua hồi lâu, mọi người mới thanh âm yên lặng nói ra."),t("br"),t("br"),n._v("Nhiều như vậy Thánh thú, linh thú dốc toàn bộ lực lượng, coi như danh sư học viện, cuối cùng có thể chiến thắng, khẳng định cũng là thắng!"),t("br"),t("br"),n._v("Mười vạn học sinh, có thể còn sống sót tuyệt đối không cao hơn một phần ba."),t("br"),t("br"),n._v("Nếu thực như thế, tuyệt đối có thể ghi chép lịch sử!"),t("br"),t("br"),n._v("Mới vừa lời thề son sắt mà nói, lại sẽ không phát sinh thú triều, liền xuất hiện loại tình huống này, coi như vị này tân viện trưởng kế vị thành công, cũng không có bất kỳ cái gì lực uy hiếp, thậm chí còn có thể biến thành người khắp thiên hạ trò cười!"),t("br"),t("br"),n._v("“Thông qua vừa rồi biểu hiện, đó có thể thấy được vị này Trương sư, thật có chút bản lĩnh, nhưng gặp được loại sự tình này, ai cũng không thể cứu vãn, chỉ có thể trách hắn xui xẻo!”"),t("br"),t("br"),n._v("Một vị Hoàng đế bệ hạ nói."),t("br"),t("br"),n._v("“Đúng vậy a! Bất kể như thế nào ứng đối, làm được khá hơn nữa, thương vong tận lực giảm bớt... Cũng vô dụng! Kế vị đại điển bị nhiều như vậy linh thú, đường hoàng vây quét, bất kể thế nào làm, đều sẽ trở thành danh sư học viện, thất bại nhất viện trưởng, không có cái thứ hai!”"),t("br"),t("br"),n._v("Chử Dĩ cũng lắc đầu, nhìn về phía trên đài Trương Huyền, đầy là đồng tình."),t("br"),t("br"),n._v("Thông qua chuyện vừa rồi, hắn đối vị này tuổi trẻ tân viện trưởng, có chút bội phục hứng thú, đáng tiếc tạo hóa trêu ngươi, lại ưu tú thiên tài, gặp được nhiều như vậy Thánh thú, linh thú, cũng không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("Đang tại cảm khái, suy đoán đối phương như thế nào giải quyết vấn đề này, liền nghe đến chung quanh ồn ào, đột nhiên không dừng được, tất cả mọi người hô hấp dồn dập, dường như xảy ra đại sự gì."),t("br"),t("br"),n._v("“Làm sao vậy?” Nhướng mày, nhìn về phía cách đó không xa Ngọc Thần Thanh."),t("br"),t("br"),n._v("“Tự xem đi!” Vẻ mặt bạc màu, Hồng Viễn đế quốc vị hoàng đế bệ hạ này không trả lời, mà là hướng lên chỉ tay."),t("br"),t("br"),n._v("“Nhìn cái gì?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Chử Dĩ gấp vội ngẩng đầu, cái nhìn thoáng qua, con ngươi kìm lòng không được thu hẹp, thân thể cứng ngắc tại chỗ, yết hầu phát khô: “Cái này, cái này... Là cái gì?”"),t("br"),t("br"),n._v("Không chỉ hắn như vậy, toàn bộ học viện tất cả thầy trò, trong nháy mắt, tất cả đều lặng ngắt như tờ, phảng phất gặp quỷ đồng dạng."),t("br"),t("br"),n._v("Triệu Bính Tuất cũng ngẩng đầu lên, liền thấy bầu trời bên trong phi hành tất cả Thánh thú, linh thú, chẳng biết lúc nào, đã biến đổi trận hình, từ xa nhìn lại, vậy mà tại trên không bày ra một hàng chữ lớn, che khuất bầu trời, lan tràn hơn mười dặm, khí thế rộng rãi!"),t("br"),t("br"),n._v("Kiểu chữ rồng bay phượng múa, ngân câu thiết họa, tựa như thư họa sư lưu lại mặc bảo, mang theo đặc thù ý vị."),t("br"),t("br"),n._v("“Chúc mừng chủ nhân, trở thành danh sư học viện viện trưởng, công lao trác thế, tên lưu Thiên Cổ... Bọn chúng chẳng lẽ, chẳng lẽ không phải tới quấy rối, đối nghịch? Mà là tới chúc mừng?”"),t("br"),t("br"),n._v("Đọc lên phía trên chữ viết, Chử Dĩ thanh âm trống rỗng, cả người gỗ."),t("br"),t("br"),n._v("Vốn cho rằng nhiều như vậy linh thú, Thánh thú, đem bọn hắn vây vào giữa, tất nhiên sẽ phát động công kích mãnh liệt, hoặc là thừa cơ lấy muốn chỗ tốt... Nằm mơ đều không nghĩ tới, không những không có làm những thứ này, ngược lại mở thành kiểu chữ, tiến hành chúc mừng."),t("br"),t("br"),n._v("Chuyện gì xảy ra?"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nghi ngờ trong lòng còn chưa kết thúc, trên không ba vị Vân Vụ lĩnh Vương Giả, đột nhiên đồng thời ôm quyền, mặt hướng trên đài cao Trương Huyền, khom người rốt cuộc: “Thanh Trúc Địa Long, Bạch Nhĩ thú, Thiết Lân thú, chúc mừng chủ nhân, trở thành danh sư học viện viện trưởng, đặc biệt chuẩn bị Vân Vụ hoa trà, mười vạn ba ngàn hai trăm cân, xem như hạ lễ!”"),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Nương theo tiếng nói kết thúc, không bên trong lập tức bay tới, ba mươi đầu Bán Thánh cấp bậc linh thú phi hành, mỗi một cái móng vuốt lên đều nắm lấy một cái to lớn cái rương, chậm rãi đặt ở đài cao, nhẹ nhàng mở ra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Lập tức trà mùi thơm khắp nơi, từng đoàn từng đoàn gấm đám sương mù, theo trong rương bay lên, đủ mọi màu sắc, chiếu rọi được toàn bộ đài cao như mộng như ảo, giống như tiên cảnh."),t("br"),t("br"),n._v("“Đây là...”"),t("br"),t("br"),n._v("Ngọc Thần Thanh ánh mắt trừng suýt rơi ra, bờ môi phát run: “Chí Tôn cấp bậc Vân Vụ hoa trà?”"),t("br"),t("br"),n._v("“Vân Vụ hoa trà? Liền ngươi cho chúng ta đưa cái chủng loại kia?”"),t("br"),t("br"),n._v("Chử Dĩ nhịn không được nói."),t("br"),t("br"),n._v("Làm lân cận đế quốc bệ hạ, cũng hưởng thụ qua Vân Vụ hoa trà, chỉ bất quá số lượng cực kỳ ít ỏi, coi như địa vị kính trọng, cũng không uống qua mấy lần."),t("br"),t("br"),n._v("Dù vậy, tư vị kia, chiếc kia cảm giác, vẫn như cũ để cho người ta cả đời đều khó mà quên được."),t("br"),t("br"),n._v("Nghĩ tới liền gốc lưỡi nước miếng."),t("br"),t("br"),n._v("“Đưa cho các ngươi loại kia?”"),t("br"),t("br"),n._v("Ngọc Thần Thanh lắc đầu: “Ngươi suy nghĩ nhiều, cho các ngươi chỉ là cấp thấp nhất bình thường cấp bậc, cùng cái này so sánh, rác rưởi cũng không bằng!”"),t("br"),t("br"),n._v("“Rác, rác rưởi cũng không bằng?”"),t("br"),t("br"),n._v("Chử Dĩ cùng cái khác mấy đại đế quốc Hoàng đế bệ hạ, vẻ mặt trong nháy mắt biến xanh xám."),t("br"),t("br"),n._v("“Đừng hiểu lầm, không phải ta muốn đưa các ngươi kém, mà là loại cấp bậc này Vân Vụ hoa trà, liền coi như chúng ta hoàng cung... Cũng không có!”"),t("br"),t("br"),n._v("Thấy mấy người biểu lộ, biết chắc là hiểu lầm, Ngọc Thần Thanh vội vàng giải thích."),t("br"),t("br"),n._v("“Không có?” Mấy người sững sờ."),t("br"),t("br"),n._v("Hồng Viễn đế quốc quyền thế người thứ nhất, cũng có không có có đồ vật?"),t("br"),t("br"),n._v("“Dạng này Vân Vụ hoa trà, mỗi một gốc đều ít nhất sống 200 năm trở lên, sau đó đi qua địa mạch linh dịch rèn luyện, nung trọn ba năm mới có thể hoàn thành... Hoàng thất chúng ta những cái kia lá trà, đều không cao hơn ba năm, thuộc về kém nhất loại kia...”"),t("br"),t("br"),n._v("Ngọc Thần Thanh cười khổ."),t("br"),t("br"),n._v("Đồng dạng là Vân Vụ hoa trà, đối phương lấy ra cái này, mới chính thức gọi trà, bọn họ cái kia, đoán chừng liền sợi cỏ cũng không bằng."),t("br"),n._v("“Các ngươi hoàng thất đều cầm không ra được trân quý lá trà, đám thánh thú này thế mà, thế mà đưa mười vạn cân làm hạ lễ?”"),t("br"),t("br"),n._v("Chử Dĩ cùng mấy vị Hoàng đế, nhìn nhau một cái, các tự hiểu là cổ họng phát lạnh, có chút nói không ra lời."),t("br"),t("br"),n._v("Vân Vụ hoa trà giá cả, bọn họ nhưng mà biết đến, coi như Ngọc Thần Thanh trong miệng nói tới bình thường nhất... Đều giá trị liên thành, vạn kim khó mua!"),t("br"),t("br"),n._v("Một cái đưa ra mười vạn cân làm hạ lễ..."),t("br"),t("br"),n._v("Thủ bút này có phần cũng quá lớn đi!"),t("br"),t("br"),n._v("Khiếp sợ trong lòng còn không có kết thúc, một cái trong sáng tiếng cười, lại vang lên, chấn toàn bộ học viện, phát ra kịch liệt tiếng vang."),t("br"),t("br"),n._v("“Xích Viêm sư, Thiết Bối Hùng, Hổ Đầu thú, cũng chúc mừng chủ nhân, trở thành danh sư học viện viện trưởng, đặc chuẩn chuẩn bị linh dược, Thánh dược, ba mươi rương, xem như hạ lễ!”"),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Thanh âm kết thúc, mười đầu Xích Viêm sư, mười đầu Thiết Bối Hùng, mười đầu Hổ Đầu thú song song đi tới, từng người trên lưng, chở đi một cái hai mét vuông cái rương, đặt ở trên đài cao, nhẹ nhàng mở ra, lập tức mùi thuốc xông vào mũi, lan tràn mười dặm."),t("br"),t("br"),n._v("“Đây là... Minh Hoa Thánh dược, Hồng Diệp Thánh dược, Thải Cúc Thánh dược... Nhiều như vậy Thánh dược, liền xem như tìm khắp toàn bộ y sư học viện, cũng không bỏ ra nổi tới đi!”"),t("br"),t("br"),n._v("“Đâu chỉ y sư học viện, không riêng gì Thánh dược, còn có Thanh Diệp nhành hoa, Phiêu Hương diệp, Mộc Tu linh thảo... Những linh dược này, mỗi một cái đều thưa thớt vô cùng, ta hoài nghi Hồng Viễn đế quốc Đại Dược Vương, cũng không có khả năng có!”"),t("br"),t("br"),n._v("“Các ngươi nhìn, vậy có phải hay không Thiên Kim hoa? Năm đó ta hao tốn trọn mười năm, đi khắp hai mươi cái nhất đẳng đế quốc, đều không có mua được một gốc, nơi này... Lại có hơn phân nửa cái rương?”"),t("br"),t("br"),n._v("“Linh Lung Phệ Tâm thảo? Loại dược liệu này phối trí thành rượu ngon, có thể để người ta sống mơ mơ màng màng, thậm chí quên mất thích nhất người... Thưa thớt vô cùng, ta lúc còn trẻ, làm quên mất một phần tình cảm, chuyên môn tìm kiếm, hao tốn ba năm mới tìm được một gốc chỉ có ba tháng lớn, đây đều là... Mấy trăm năm?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy trong rương xuất hiện các loại dược vật, luyện đan sư học viện cùng y sư học viện tất cả danh sư, tất cả đều nhanh muốn điên rồi."),t("br"),t("br"),n._v("Những dược liệu này, mỗi một cây đều giá trị cực cao, vạn kim khó cầu, đối phương một cái lấy ra ba mươi cái rương, hơn nữa mỗi một cây năm số đều đầy đủ..."),t("br"),t("br"),n._v("Chỉ vì xem như hạ lễ..."),t("br"),t("br"),n._v("Đây là đem toàn bộ Vân Vụ lĩnh đều dời trống a?"),t("br"),t("br"),n._v("“Thiết Xỉ Lang, Thanh Tị Tượng, Hồng Hỏa Viên, cũng chúc mừng chủ nhân, trở thành danh sư học viện viện trưởng, đặc chuẩn chuẩn bị các loại khoáng thạch ba mươi rương, coi như hạ lễ!”"),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Ba mươi rương linh dược, Thánh dược rung động còn không có hoàn toàn tiêu hóa, cuối cùng ba đầu Vương Giả, cũng tiến về phía trước một bước, thanh âm hùng hồn vang lên."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mười đầu Thiết Xỉ Lang, mười đầu Thanh Tị Tượng, mười đầu Hồng Hỏa Viên, cũng tới đến đài cao, từng người đem sống lưng lên cái rương, từng cái mở ra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Linh khí xao động, ánh sáng muôn màu."),t("br"),t("br"),n._v("Các loại trân quý khoáng thạch, rất nhiều người nổi tiếng không thấy được một mặt bảo vật, xuất hiện ở trước mắt mọi người."),t("br"),t("br"),n._v("“Đây cũng là... Thanh Ngân thiết a? Nghe nói loại này sắt, so huyền thiết còn cứng rắn hơn, luyện thành vũ khí, phía trên tự nhiên mang có một loại màu xanh hoa văn, vô cùng sắc bén, không dính một giọt máu... Vô số người cầu một hai mà không thể được, một rương này, đủ có vài chục tấn a?”"),t("br"),t("br"),n._v("“Ta nhìn thấy Huyết Ấn khoáng thạch, dùng thứ này bôi lên vũ khí, lại khó mà hòa tan cương thiết, đều có thể tuỳ tiện hóa thành nước... Năm đó ta đi luyện khí sư công hội mua sắm, cấp bậc quá thấp, sửng sốt không có tư cách... Cho Trương sư một cái rương?”"),t("br"),t("br"),n._v("“Những cái kia là kim cương, cái phải thật tốt điêu khắc, hoàn toàn có thể biến thành đẹp nhất kim cương... Coi như giá trị không bằng cái kia mấy thứ, cũng không cần một cái rương, một cái rương đưa đi!”"),t("br"),t("br"),n._v("“Những thứ này sẽ không phải là tuyết đọng đá a? Trời sinh âm hàn, bội ở trên người, có thể để cho thuộc tính âm hàn người tu luyện làm ít công to...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Những quáng thạch này vừa xuất hiện, luyện khí sư học viện chỗ có danh sư đều điên rồi."),t("br"),t("br"),n._v("Thân là luyện khí sư, nhận biết đủ loại khoáng thạch, biết trân quý mức độ, trước mắt cái này ba mươi rương, cộng lại giá trị, coi như vơ vét toàn bộ danh sư học viện, thậm chí Hồng Viễn đế quốc hoàng thất đều không bỏ ra nổi tới!"),t("br"),t("br"),n._v("Mà bây giờ chỉ vì xem như Trương sư, trở thành viện trưởng hạ lễ!"),t("br"),t("br"),n._v("“Chu Thần Thanh, ngươi không phải nói ký cái gì không bình đẳng hiệp nghị, chúng ta danh sư học viện đền bù rất nhiều, mới để cho Vân Vụ sơn linh thú triệt binh ư?”"),t("br"),t("br"),n._v("“Đúng vậy a, những vật này, coi như đem danh sư học viện lật cái lộn chổng vó lên trời cũng không bỏ ra nổi tới nha!”"),t("br"),t("br"),n._v("“Mấu chốt... Còn là người ta đưa tới!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Vừa rồi đứng lên tất cả học viên, đồng loạt nhìn về phía trước Chu Thần Thanh."),t("br"),t("br"),n._v("Trước đó cái tên này nói, học viện vì để cho tân danh sư kế vị, không xuất hiện gợn sóng, bồi thường không biết bao nhiêu tiền, tang quyền nhục viện... Bọn họ mới lòng đầy căm phẫn, đi theo đi ra nghi ngờ."),t("br"),t("br"),n._v("Nhưng... Một màn trước mắt lại là Vân Vụ lĩnh tất cả Thánh thú linh thú, không những không muốn cái gì, còn tới tặng lễ... Thậm chí quý giá như thế!"),t("br"),t("br"),n._v("Nhìn đến nơi này, ngu nữa cũng biết, cái gọi là đưa bảo bồi thường, tang quyền nhục viện, tuyệt đối là giả..."),t("br"),t("br"),n._v("“Ta, ta... Làm sao biết?”"),t("br"),t("br"),n._v("Thân thể run rẩy, Chu Thần Thanh buồn bực nhanh muốn khóc."),t("br"),t("br"),n._v("Chạy đến người ta hang ổ đi đàm phán, đổi lại ai đều sẽ cảm giác được, khẳng định là yếu thế mới như vậy, nói một chút nội dung, tất nhiên cũng là đánh mất tôn nghiêm, mất mặt xấu hổ..."),t("br"),t("br"),n._v("Ai có thể nghĩ tới, nói hùng tráng như vậy, như thế cao điệu?"),t("br"),t("br"),n._v("Như vậy để cho người ta sụp đổ cùng điên cuồng..."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Đây thật là đàm phán? Mà không phải đi cướp đoạt?"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-906"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-908"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);