(window.webpackJsonp=window.webpackJsonp||[]).push([[2196],{2550:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-899-moi-tien-to"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-899-moi-tien-to"}},[n._v("#")]),n._v(" Chương 899: Mời Tiên Tổ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 899: Mời Tiên Tổ"),t("br"),t("br"),n._v("“Không đúng! Các ngươi có chú ý đến hay không, vừa rồi bọn chúng nói cũng không phải là chúc mừng Trương sư trở thành viện trưởng, mà là chúc mừng chủ nhân...”"),t("br"),t("br"),n._v("Thời gian rất lâu lặng ngắt như tờ về sau, không biết ai hô lên."),t("br"),t("br"),n._v("Trước đó khiếp sợ thực sự quá lớn, cho tới bây giờ mới phản ứng được."),t("br"),t("br"),n._v("“Đúng a! Nói là chúc mừng chủ nhân!”"),t("br"),t("br"),n._v("“Thánh thú nói chủ nhân, chỉ có một khả năng... Thú sủng!”"),t("br"),t("br"),n._v("“Các ngươi nói là, Trương sư đem... Vân Vụ lĩnh Vương Giả đều thuần phục? Bọn chúng lúc này mới đem hang ổ đều chuyển tới, xem như hạ lễ?”"),t("br"),t("br"),n._v("Một viên đá dấy lên ngàn cơn sóng, toàn bộ danh sư học viện tất cả đều xôn xao!"),t("br"),t("br"),n._v("Có thể trở thành danh sư học viện học viên, không có một cái nào là kẻ ngu, một cái nháy mắt, liền hiểu được."),t("br"),t("br"),n._v("Lập tức, nguyên một đám giống như là gặp quỷ đồng dạng nhìn về phía trên đài cao cái kia vẻ mặt thản nhiên, chỉ có hai mươi tuổi thanh niên, đều cảm thấy đầu óc suýt nổ."),t("br"),t("br"),n._v("Nhất là thuần thú sư học viện tất cả học viên, tất cả đều tràn đầy phát điên."),t("br"),t("br"),n._v("Bọn họ thuần phục một đầu linh thú, ít nhất phải tốn hao mấy năm, sớm chuẩn bị các loại bảo vật, mỹ thực, hợp ý, bỏ đi tôn nghiêm nịnh bợ, tựa như cầu gia gia, cáo nãi nãi..."),t("br"),t("br"),n._v("Trên đài vị này ngược lại tốt, bất tri bất giác thuần phục Tử Dương thú, còn đem Vân Vụ lĩnh Vương Giả đều thuần phục..."),t("br"),t("br"),n._v("Có dám hay không lại khoa trương điểm?"),t("br"),t("br"),n._v("Đang tại choáng váng, liền nghe đến bầu trời, trên đất mấy vạn linh thú, đồng thời quỳ mọp xuống đất, nguyên một đám nhìn về phía trên đài thanh niên, đồng loạt hô lên tiếng tới."),t("br"),t("br"),n._v("“Hống hống hống hống rống rống...”"),t("br"),t("br"),n._v("“Là Thượng Cổ thú ngữ!” Trong đám người một cái lớn tuổi thuần thú sư nói."),t("br"),t("br"),n._v("“Thượng Cổ thú ngữ? Nói cái gì ý tứ?” Người chung quanh vội vàng hỏi."),t("br"),t("br"),n._v("Tinh thông Thượng Cổ thú ngữ không nhiều, liền xem như danh sư học viện tất cả danh sư, cũng không phải tất cả mọi người hiểu."),t("br"),t("br"),n._v("“Ta chỉ có thể nghe hiểu một bộ phận đơn giản...”"),t("br"),t("br"),n._v("Cái này lớn tuổi thuần thú sư chần chờ một chút: “Hình như là đang nói... Chúc mừng chủ nhân, trở thành danh sư học viện viện trưởng...”"),t("br"),t("br"),n._v("“Chủ nhân? Sẽ không những linh thú này... Cũng bị Trương sư thuần phục, trở thành hắn thú sủng đi?”"),t("br"),t("br"),n._v("“Hình như là thật...”"),t("br"),t("br"),n._v("“Thuần phục Vân Vụ lĩnh Vương Giả, thuận tiện đem người ta tất cả linh thú đều thuần phục?”"),t("br"),t("br"),n._v("“Hội trưởng, a, không, viện trưởng chỗ đến, quả thực không có một ngọn cỏ ah...”"),t("br"),t("br"),n._v("Mọi người lần nữa cảm thấy mí mắt xoay loạn."),t("br"),t("br"),n._v("Mới vừa nói xong, còn dám lại khoa trương một chút không, liền thấy cảnh này..."),t("br"),t("br"),n._v("Viện trưởng, ngươi xác định ngươi thật chỉ là cái tứ tinh danh sư, không phải Khổng sư ngụy trang?"),t("br"),t("br"),n._v("Thậm chí coi như Khổng sư, cũng chưa nghe nói qua, lúc còn trẻ, một hơi thuần phục mấy vạn con linh thú cùng Thánh thú đi."),t("br"),t("br"),n._v("Lần nữa nhìn về phía đài cao thanh niên, giống như là thấy được quái vật."),t("br"),t("br"),n._v("“Nhìn tới cái gọi là đàm phán, là viện trưởng tự mình đi, căn bản không phải chúng ta tưởng tượng cái chủng loại kia, trả giá rất lớn đánh đổi, nén giận, mà là... Đem người ta hang ổ mang.”"),t("br"),t("br"),n._v("Thuần thú học viện tất cả tham gia chiến đấu học viên, một vị đi đầu kịp phản ứng."),t("br"),t("br"),n._v("“Đúng vậy a! Chạy tới trực tiếp thuần phục, mười đại vương giả, tất cả linh thú, không một may mắn thoát khỏi... Có loại năng lực này còn cần đến đàm phán cái gì!”"),t("br"),t("br"),n._v("“Buồn cười, chúng ta còn luôn luôn nghi ngờ! Khó trách vừa rồi Triệu trưởng lão nói về sau lại không cần lo lắng Vân Vụ lĩnh thú triều. Tất cả linh thú thú đều biến thành thú sủng, làm sao có thể lại phát sinh thú triều?”"),t("br"),t("br"),n._v("“Danh sư học viện thành lập trên vạn năm tới đều không thể giải quyết Vân Vụ lĩnh, Trương sư còn không có kế vị, liền trước thời hạn giải quyết, đổi thành ta là thập đại trường lão, không chọn hắn làm viện trưởng, chọn ai?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, tất cả đều là cười khổ."),t("br"),t("br"),n._v("Trước đó nghe được cùng Vân Vụ lĩnh đàm phán tin tức, cả đám đều cảm thấy khẳng định là làm xảy ra điều gì tang quyền nhục viện sự tình."),t("br"),t("br"),n._v("Hiện tại xem ra phi thường không phải, còn cho người tộc rất thở một hơi."),t("br"),t("br"),n._v("Tin tức một khi truyền đi, khẳng định sẽ làm cho cả Thanh Nguyên phong hào đế quốc, đều trở nên khiếp sợ."),t("br"),t("br"),n._v("“Đưa chúng nó tất cả đều thuần phục...”"),t("br"),t("br"),n._v("Nhìn lên bầu trời, cùng chung quanh tất cả linh thú, Chu Thần Thanh hốc mắt đều đỏ."),t("br"),t("br"),n._v("Hắn mục tiêu lớn nhất, là thay người nhà báo thù, mà bây giờ tất cả linh thú đều hoàn toàn quy thuận, tương đương với đại thù đã đến báo!"),t("br"),t("br"),n._v("“Viện trưởng, cám ơn!” Liền ôm quyền, quỳ rạp xuống đất, sùng bái chi ý, phát ra từ thành tâm, không có nửa điểm giả tạo."),t("br"),t("br"),n._v("“Tốt, tất cả đi xuống đi!”"),t("br"),t("br"),n._v("Nhận lấy tất cả hạ lễ, Trương Huyền xoa xoa mi tâm."),t("br"),t("br"),n._v("Linh thú, Thánh thú làm cho ra một màn này, hắn trước đó là hoàn toàn không biết!"),t("br"),t("br"),n._v("Nếu là biết, khẳng định đã sớm khuyên can, làm sao có thể tùy ý bọn chúng như vậy quấy rối."),t("br"),t("br"),n._v("Mặc dù làm như vậy vì cho hắn tăng thể diện, nhưng vẫn là lòng tràn đầy buồn bực."),t("br"),t("br"),n._v("Tiền tài không để ra ngoài, ngươi đem vật này lặng lẽ cho ta thật tốt, tất cả đều vui vẻ, xem như hạ lễ đưa tới, toàn bộ học viện người đều biết, muốn buồn bực thanh âm phát đại tài, đã không có khả năng."),t("br"),t("br"),n._v("“Vâng!” Xích Viêm sư nhẹ gật đầu, mang theo tất cả linh thú, Thánh thú chậm rãi lui xuống."),t("br"),t("br"),n._v("Nhìn thấy đầy trời đầy đất linh thú Thánh thú, ngay ngắn trật tự, nghiêm ngặt dựa theo mệnh lệnh thi hành, chỗ có danh sư nhìn nhau, nguyên một đám lòng tràn đầy bội phục."),t("br"),n._v("Có này viện trưởng, lo gì học viện không chấn hưng!"),t("br"),t("br"),n._v("“Tốt, nếu như mọi người không còn cái khác nghi vấn, chúng ta liền tiến hành tiếp theo hạng tế bái Tiên Tổ!”"),t("br"),t("br"),n._v("Thấy mọi người thái độ theo trước đó nghi ngờ biến thành tôn sùng, Triệu Bính Tuất cười cười, nói."),t("br"),t("br"),n._v("“Chúng ta thừa nhận Trương sư là viện trưởng, lại không nghi vấn!” Mọi người đồng loạt nói."),t("br"),t("br"),n._v("Trên đài vị này Trương viện trưởng, bất kể nhân phẩm, sư đức, giáo dục, lòng dạ, đối tu luyện lý giải, đều không có kẽ hở, khiến cho người kính nể, nâng hỏi tiếp cũng là tự rước lấy nhục, còn không bằng như vậy không tiến hành nữa."),t("br"),t("br"),n._v("“Vậy thì tốt, mời Tiên Tổ!” Triệu Bính Tuất gật đầu, cất cao giọng nói."),t("br"),t("br"),n._v("Viện trưởng kế vị, mời Tiên Tổ, là danh sư là học viện lệ cũ cùng truyền thừa."),t("br"),t("br"),n._v("Mục đích là nhận được, danh sư học viện trước đó tất cả viện trưởng công nhận, nghe cùng Phong Sư điện hiệu quả, có chút tương tự, mà trên thực tế nếu không."),t("br"),t("br"),n._v("Phong Sư điện hướng Danh Sư đường tất cả kiệt xuất Tiên Tổ quỳ lạy, nhận được công nhận càng nhiều, đã định trước sau này thành tựu càng cao."),t("br"),t("br"),n._v("Mời Tiên Tổ thì là, lịch đại viện trưởng tàn lưu lại ý niệm, tiến hành công nhận chọn lựa, bị những viện trưởng này ý thức, bị bọn họ công nhận số lần càng nhiều, tại học viện lấy được quyền hạn cũng lại càng lớn, lấy được chỗ tốt cũng lại càng lớn."),t("br"),t("br"),n._v("Danh sư học viện cũng không phải là mặt ngoài nhìn thấy đơn giản như vậy, liên lụy đến càng sâu tầng đồ vật, liền xem như Triệu Bính Tuất trưởng lão loại thân phận này, cũng không có tư cách dọ thám biết."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Kèm theo Triệu Bính Tuất lời nói, bầu trời lập tức tối sầm lại, đài cao bốn phía một cái to lớn đại trận chậm rãi vận chuyển, chiếu rọi chung quanh sáng tối chập chờn, như cùng một đóa to lớn mây đen bao phủ xuống."),t("br"),t("br"),n._v("“Đây là có chuyện gì?” Rất nhiều học viên trước đến giờ chưa thấy qua cảnh tượng như thế này, nguyên một đám nghi ngờ không thôi."),t("br"),t("br"),n._v("Không phải nói mời Tiên Tổ ư? Làm sao đột nhiên, trời tối đi?"),t("br"),t("br"),n._v("“Nhìn tới các ngươi có chỗ không biết, lần trước viện trưởng kế vị, ta đúng lúc cũng tại, chính là loại tình cảnh này!”"),t("br"),t("br"),n._v("Một trưởng lão vuốt vuốt sợi râu, giải thích nói: “Cái gọi là Tiên Tổ, trên thực tế là tất cả viện trưởng lưu ở trong học viện ý niệm, thuộc về hồn phách loại hình, không cách nào dưới ánh mặt trời hoạt động, chỉ có khởi động trận pháp, đem mặt trời che đậy, mới có thể đem hắn mời đến.”"),t("br"),t("br"),n._v("“Hồn phách? Chẳng lẽ là Vu hồn?” Một người học viên không chịu nổi trong lòng hiếu kỳ, nhịn không được hỏi."),t("br"),t("br"),n._v("“Vu hồn? Cái này sao có thể, Vu hồn chi thuật, vì sống, đem người đoạt xá, thậm chí đem người sống luyện chế thành Vô Hồn Kim Nhân, vô nhân đạo, ta danh sư đường đường chính chính, cuồn cuộn đại khí, làm sao có thể dùng phương pháp của bọn hắn!” Vị trưởng lão này trên mặt lộ ra vẻ khinh thường."),t("br"),t("br"),n._v("“Cái kia...” Học viên không hiểu."),t("br"),t("br"),n._v("Theo hắn biết, chỉ có Vu hồn, mới sẽ như thế, chỉ có thể ở trong âm u hành động, cần che đậy ánh nắng."),t("br"),t("br"),n._v("“Những thứ này Tiên Tổ, là lịch đại viện trưởng ngưng tụ ra sư đạo ý niệm, căn bản không phải Vu hồn loại kia tiểu đạo có thể so sánh được! Không tin các ngươi nhìn...”"),t("br"),t("br"),n._v("Vừa dứt lời, liền nghe một hồi dồn dập gió minh, ngay sau đó nhìn thấy một cái lão giả nhàn nhạt hư ảnh xuất hiện tại đài cao."),t("br"),t("br"),n._v("Lão giả này mặt chữ quốc, hai đầu lông mày mang theo một cỗ khí khái hào hùng, cho người ta một loại thôn phệ thương khung khí phách."),t("br"),t("br"),n._v("Cùng Vu hồn âm lãnh hoàn toàn khác biệt, trái lại mang theo Hạo Nhiên Chính Khí."),t("br"),t("br"),n._v("Nhìn cách đó không xa lão giả này thân ảnh, Trương Huyền cau mày."),t("br"),t("br"),n._v("Trước đó, hắn cùng học viên khác ý nghĩ đồng dạng, cũng cảm thấy cái gọi là Tiên Tổ có thể là Vu hồn, nếu không danh sư học viện thành lập trên vạn năm, lợi hại hơn nữa danh sư cũng không có khả năng sống lâu như thế."),t("br"),t("br"),n._v("Nhìn xem vị lão giả này, mới biết được hẳn là cùng Khổng sư Phong Thánh đài, lưu lại ý niệm, không có sai biệt, không phải Vu hồn."),t("br"),t("br"),n._v("Mặc dù cụ thể là chuyện gì xảy ra, hắn cũng không làm rõ ràng được, nhưng cái này hồn phách đường đường chính chính, không có một tia âm hàn, muốn so Vu hồn tốt hơn nhiều."),t("br"),t("br"),n._v("Đương nhiên, có lợi có hại, thực lực khẳng định muốn so Vu hồn yếu rất nhiều, cũng không phải hoàn chỉnh hồn phách."),t("br"),t("br"),n._v("Vu hồn sống sót chính là mình hoàn chỉnh ý thức, mà cái này chỉ là danh sư một đạo ý niệm, cùng trong tay hắn Ngoan Nhân có chút tương tự."),t("br"),t("br"),n._v("“Nhìn dáng vẻ của hắn cùng khí phách, hẳn là danh sư học viện vị thứ nhất viện trưởng, Mục Khải Mục sư!”"),t("br"),t("br"),n._v("Cùng Trương Huyền xem kỹ khác biệt, dưới đài tất cả trưởng lão cùng danh sư tất cả đều hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Danh sư học viện khai phái tổ sư, hiển hóa hình thái, đổi lại ai cũng sẽ kích động vô cùng!"),t("br"),t("br"),n._v("Mặc dù chỉ là một cái tàn hồn, dù sao cũng ẩn chứa Tiên Tổ ý thức."),t("br"),t("br"),n._v("Hô hô hô hô!"),t("br"),t("br"),n._v("Vị này Mục Khải tổ sư xuất hiện không lâu, trên đài cao, hư ảnh lay động, rất nhanh lại xuất hiện mấy chục người ảnh."),t("br"),t("br"),n._v("Cho dù thoạt nhìn có chút phai mờ, nhưng cho người ta một loại áp lực cường đại."),t("br"),t("br"),n._v("Với tư cách học viên cùng trưởng lão, học viện lịch sử là nhất định phải nhớ rõ, có thể trở thành viện trưởng, mỗi một cái đều có vượt xa cái khác danh sư năng lực, là vô số học viên, sùng bái thần tượng."),t("br"),t("br"),n._v("Đột nhiên nhìn thấy nhiều như vậy thần tượng đồng thời xuất hiện, toàn bộ diễn võ trường gần như sắp muốn vỡ tổ."),t("br"),t("br"),n._v("“Cái kia hẳn là, đời thứ bảy viện trưởng, Lưu Xương Nguyên viện trưởng a? Hắn sáng lập ra , vẫn là ta học tập mục tiêu...”"),t("br"),t("br"),n._v("“Cái kia hẳn là mệnh danh tất cả viện trưởng bên trong, thiên phú đệ nhất Mạc Lưu Chân viện trưởng đi! Quả nhiên khí chất bất phàm, chỉ nhìn ánh mắt, tựa như là bất cứ lúc nào cũng sẽ phá toái hư không!”"),t("br"),t("br"),n._v("“Mạc Lưu Chân viện trưởng là mạnh mẽ, đáng tiếc, vì xung kích bát tinh danh sư, đi vào Địa quật, lại không có trở về, trở thành Danh Sư đường một tổn thất lớn...”"),t("br"),t("br"),n._v("“Thần tượng của ta là thứ bảy mươi sáu đời Trúc Huyền Thanh viện trưởng, hắn mặc dù một đời đều không có đột phá lục tinh đỉnh phong, đi đến thất tinh, nhưng am hiểu mười hai chủng, phụ tu chức nghiệp, đồng thời đều mỗi một loại chức nghiệp, đều tiến hành kỹ càng miêu tả, cho học viện lưu lại trân quý tài phú...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tất cả danh sư, nguyên một đám ánh mắt nóng như lửa."),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Tiếng nghị luận bên trong, trên đài cao thân ảnh, tổng cộng xuất hiện trọn 102 cái."),t("br"),t("br"),n._v("“102? Tại sao không có đời trước viện trưởng thân ảnh?” Trương Huyền nhịn không được cau mày."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-907"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-909"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);