(window.webpackJsonp=window.webpackJsonp||[]).push([[2197],{2552:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-900-đanh-cac-nguoi-mot-tran-đuoc-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-900-đanh-cac-nguoi-mot-tran-đuoc-chu"}},[n._v("#")]),n._v(" Chương 900: Đánh các ngươi một trận được chứ?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 900: Đánh các ngươi một trận được chứ?"),t("br"),t("br"),n._v("Mỗi một thời đại viện trưởng đều lưu lại ý niệm, coi như đời trước lão viện trưởng, ngoài ý muốn mất tích, đến nay tung tích không rõ, nhưng loại này lưu truyền hậu thế đồ vật, hẳn là cũng sẽ gửi ở học viện đi!"),t("br"),t("br"),n._v("Làm sao lại không có?"),t("br"),t("br"),n._v("“Lão viện trưởng... Công lao không đủ, còn chưa kịp đi , liền mất tích. Đương nhiên cũng không có lưu lại ý niệm...”"),t("br"),t("br"),n._v("Nhìn ra hắn nghi hoặc, Triệu Bính Tuất truyền âm nói."),t("br"),t("br"),n._v("“Công lao?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Mỗi một vị danh sư, căn cứ Danh Sư đường ghi chép, đều có thể hình thành công lao... Mà chỉ có đi đến nhất định hạn mức, mới có thể mở ra viện trưởng lăng!”"),t("br"),t("br"),n._v("“Đối nhân tộc hữu ích sự tình, làm ra càng nhiều, công lao lại càng lớn! Giảng bài, để càng nhiều học sinh tiến bộ, là công lao; Chém giết Dị Linh tộc nhân, loại bỏ tai hoạ ngầm, là công lao; Sáng chế công pháp mới, võ kỹ, truyền thụ cho người, lớn mạnh nhân tộc, cũng là công lao...”"),t("br"),t("br"),n._v("Thấy hắn không hiểu, Triệu Bính Tuất cười cười: “Viện trưởng thu phục Vân Vụ lĩnh tất cả linh thú, Thánh thú, xóa bỏ đại tai nạn, trên thực tế chính là công lao lớn, không nói cái khác, điều này liền đầy đủ mở ra viện trưởng lăng!”"),t("br"),t("br"),n._v("Vân Vụ lĩnh vẫn là danh sư học viện tai hoạ ngầm, đã trải qua hơn một trăm vị viện trưởng, đều không có giải quyết triệt để. Trước mắt vị này chẳng những hóa giải, còn không lưu bất luận cái gì tai hoạ ngầm, công lao to lớn, cùng mở trường học tổ sư so, còn hơi có không bằng, nhưng cũng tuyệt đối có thể chói lọi Thiên Cổ, lưu danh bách thế."),t("br"),t("br"),n._v("“Ta công lao đầy đủ?” Trương Huyền ngẩn ngơ, một mặt thẫn thờ."),t("br"),t("br"),n._v("“Đúng vậy a!” Triệu Bính Tuất cười khổ gật đầu."),t("br"),t("br"),n._v("Cái khác viện trưởng, vì kiếm lấy công lao, chịu mệt nhọc, cẩn trọng, làm ra các loại có ích cho người làm sự tình, rút cuộc đều kiếm lấy không được bao nhiêu."),t("br"),t("br"),n._v("Mà trước mắt vị này, đoán chừng hắn là lịch đại viện trưởng bên trong, một cái duy nhất không biết công lao là cái gì, cái đồ chơi này là đủ rồi."),t("br"),t("br"),n._v("Chuyện này, nếu để cho lịch đại những viện trưởng kia biết, không thông báo không sẽ trực tiếp hộc máu."),t("br"),t("br"),n._v("Giữa người và người chênh lệch thực sự quá lớn."),t("br"),t("br"),n._v("Không biết trong lòng đối phương suy nghĩ, Trương Huyền tiếp tục hỏi: “Người viện trưởng kia lăng là cái gì...”"),t("br"),t("br"),n._v("“Viện trưởng lăng, là ôn dưỡng viện trưởng ý niệm chỗ, sau khi chết thi thể cũng sẽ chôn giấu trong đó, nói cách khác, là lịch đại danh sư học viện viện trưởng, ngủ yên chỗ.”"),t("br"),t("br"),n._v("“Chính là viện trưởng nghĩa địa...”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Nghe đối phương giọng điệu, liền xem như viện trưởng nghĩa địa, cũng không phải bất kỳ một cái nào viện trưởng đều có thể đi vào, còn cần công lao."),t("br"),t("br"),n._v("“Cái này nghĩa địa hẳn là có cái gì đặc thù đi...”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Chỉ là cái bình thường nghĩa địa lời nói, chỉ cần trở thành viện trưởng, liền có tư cách đi vào, mà cái này còn có yêu cầu, tất nhiên không có tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("Lời nói mới nói phân nửa, chỉ thấy cách đó không xa tất cả viện trưởng ý niệm, đồng loạt nhìn qua."),t("br"),t("br"),n._v("“Ngươi là khóa này viện trưởng?” Một cái thanh âm uy nghiêm vang lên."),t("br"),t("br"),n._v("Nói chuyện chính là học viện khai phái danh sư, Mục Khải."),t("br"),t("br"),n._v("Trong ánh mắt của hắn, cũng không có bởi vì Trương Huyền tuổi tác tiểu mà lộ ra coi thường ánh sáng, ngược lại ôn tồn lễ độ, mang theo tán thưởng hứng thú."),t("br"),t("br"),n._v("“Có thể trẻ tuổi như vậy, liền đạt được tất cả học viên cùng trưởng lão công nhận, nhìn tới nhất định có chỗ hơn người.”"),t("br"),t("br"),n._v("Khai sáng một cái danh sư học viện người, ánh mắt đương nhiên không phải người bình thường có thể so sánh được."),t("br"),t("br"),n._v("Người khác chỉ có thấy được Trương Huyền tuổi còn nhỏ, không chịu nổi trách nhiệm, nhưng vị này khai phái tổ sư, nhưng nhìn ra tiềm lực của hắn."),t("br"),t("br"),n._v("Trẻ tuổi như vậy, liền có thể để tất cả thầy trò bội phục, tiến hành mời Tiên Tổ nghi thức, làm sao có thể đơn giản?"),t("br"),t("br"),n._v("“Mặc dù có trận pháp duy trì, chúng ta có thể xuất hiện ở nơi này thời gian cũng có hạn, dùng ba câu nói tới giới thiệu bản thân, biểu hiện ra cường hạng cùng năng lực, ba câu qua đi... Chúng ta căn cứ như lời ngươi nói nội dung, đánh giá công nhận hay không.”"),t("br"),t("br"),n._v("Mục Khải trong mắt mang theo mỉm cười, tiếp tục nói."),t("br"),t("br"),n._v("“Ba câu nói?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Còn tưởng rằng sẽ cùng Phong Sư điện đồng dạng, tiến hành quỳ lạy, thu hoạch được công nhận, hiện tại xem ra, cũng không có đơn giản như vậy."),t("br"),t("br"),n._v("Là để nói chuyện, trên thực tế nhưng càng khó."),t("br"),t("br"),n._v("Đại biểu tất cả tiền bối đối khảo nghiệm của hắn, tuyệt đối không thể ăn nói lung tung."),t("br"),t("br"),n._v("Đang nghĩ ngợi trả lời như thế nào, liền nghe đến Triệu Bính Tuất truyền âm vang lên."),t("br"),t("br"),n._v("“Viện trưởng, nhất định phải cân nhắc tốt, nói như thế nào. Nhận được Tiên Tổ ý niệm công nhận càng nhiều, trở thành viện trưởng về sau, có quyền hạn cũng lại càng lớn.”"),t("br"),t("br"),n._v("“Quyền hạn?”"),t("br"),t("br"),n._v("“Ừm, mặc dù có chút chỗ, liền xem như viện trưởng cũng không cách nào tiến vào cùng khống chế, nhất định phải có đầy đủ quyền hạn.”"),t("br"),t("br"),n._v("“Ah!” Trương Huyền nhẹ gật đầu, hiểu được."),t("br"),t("br"),n._v("Viện trưởng chỉ là tất cả thầy trò đề cử đi ra, tạm thời quản lý tất cả công việc danh sư, cũng không phải là biến thành học viện chủ nhân, tự nhiên phải có cái gọi là quyền hạn."),t("br"),t("br"),n._v("Quyền hạn càng lớn, đối học viện khống chế cũng liền càng mạnh."),t("br"),t("br"),n._v("“Để hai mươi phần trăm Tiên Tổ công nhận, liền có tư cách kế nhiệm viện trưởng, thu hoạch được học viện sơ khai quyền hạn. Bốn mươi phần trăm, thu hoạch được trung cấp quyền hạn. Sáu mươi, cấp cao quyền hạn, vượt qua tám mươi phần trăm, thì nhận được quyền hạn tối cao, có thể điều động học viện tất cả đại trận, quan sát bí ẩn nhất thư tịch, thậm chí không cần công lao, đều có thể tự do ra vào viện trưởng lăng.”"),t("br"),t("br"),n._v("“Quan sát bí ẩn nhất thư tịch?” Trương Huyền mắt sáng rực lên."),t("br"),n._v(" Sở dĩ đáp ứng làm viện trưởng này, chính là vì đọc sách, nghe đối phương giọng điệu, thiết trí quyền hạn, chỉ cần không có đi đến, khẳng định vẫn là có chút sách là không thấy được."),t("br"),t("br"),n._v("Đã như vậy, vì cái gì không tranh thủ quyền hạn tối cao?"),t("br"),t("br"),n._v("Bất quá, muốn lấy được cái này, nhất định phải tại ba câu nói phía trong, thu hút tất cả viện trưởng ý niệm, làm cho đối phương thừa nhận bản thân."),t("br"),t("br"),n._v("Cái này cũng không có trong tưởng tượng dễ dàng."),t("br"),t("br"),n._v("Mặc dù vị kia khai phái tổ sư Mục Khải, nhìn thấy tuổi của nàng, khen ngợi rất nhiều, nhưng cũng không phải là tất cả tiền bối, đều là nghĩ như vậy."),t("br"),t("br"),n._v("Dù sao tuổi của hắn, tu vi, tên sư cấp bậc bên trên, thoạt nhìn cũng không tính là xuất sắc, cùng những người khác so, còn kém quá nhiều."),t("br"),t("br"),n._v("Ba câu nói bên trong liền để hắn đổi mới, khó!"),t("br"),t("br"),n._v("“Trước đó lão viện trưởng, nhận được phần trăm bao nhiêu công nhận?” Nhịn không được hỏi."),t("br"),t("br"),n._v("“Lão viện trưởng kế vị thời điểm, là lục tinh danh sư đỉnh phong, vừa mới đi đến tiêu chuẩn, chỉ lấy được 22% công nhận, miễn cưỡng thông qua!”"),t("br"),t("br"),n._v("Triệu Bính Tuất truyền âm tới."),t("br"),t("br"),n._v("“Lịch đại viện trưởng thu hoạch được sơ khai quyền hạn, tương đối dễ dàng, trung cấp liền có chút khó khăn. Nhìn chung lịch sử, hơn một trăm cái viện trưởng nhận được loại này quyền hạn, không cao hơn 30 vị! Nhận được cấp cao quyền hạn không cao hơn mười vị. Thu hoạch được quyền hạn tối cao, có thể cũng chỉ có lúc trước khai phái tổ sư một người!”"),t("br"),t("br"),n._v("“Chỉ có một người?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Nhìn tới so tưởng tượng còn muốn phức tạp."),t("br"),t("br"),n._v("Muốn cho hơn một trăm cái từng làm qua viện trưởng cường giả, toàn bộ công nhận, theo ở sâu trong nội tâm khen ngợi, quả thực rất khó."),t("br"),t("br"),n._v("Lòng người như mặt, trong mắt ngươi hoàn mỹ, có lẽ ở trong mắt người khác, là chán ghét nhất."),t("br"),t("br"),n._v("Thật giống như có chút danh sư tinh thông mười mấy dạng phụ tu, hơn nữa từng cái đều đạt đến rất sâu cảnh giới, dựa theo đạo lý, hẳn là sẽ nhận được rất nhiều người tôn kính cùng tôn sùng."),t("br"),t("br"),n._v("Nhưng cũng có rất nhiều người cho rằng, danh sư hẳn là đa hoa tâm nghĩ tại giáo thư dục nhân bên trên, phụ tu càng nhiều, nói rõ dùng đang giáo dục bên trên tâm tư càng ít, không đáng tôn kính."),t("br"),t("br"),n._v("“Đúng vậy a! Thu hoạch được công nhận, viện trưởng số lượng càng ít càng dễ dàng, đến bây giờ đã trải qua hơn một trăm vị viện trưởng, muốn thu hoạch được càng quyền cao hơn hạn, liền càng ngày càng khó!” Triệu Bính Tuất tiếp tục nói."),t("br"),t("br"),n._v("Muốn lấy được một hai người công nhận, vẫn tương đối dễ dàng, chỉ cần biểu hiện được đầy đủ ưu tú, liền có thể làm được, có thể nghĩ để hơn một trăm người, đồng thời có ấn tượng tốt, vậy liền khó khăn."),t("br"),t("br"),n._v("Cho nên ba câu nói lựa chọn cực kỳ trọng yếu."),t("br"),t("br"),n._v("Nên nói cái gì?"),t("br"),t("br"),n._v("Trương Huyền trầm tư."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài tất cả danh sư, nghe được Mục Khải Tiên Tổ yêu cầu về sau, cũng ý thức được độ khó, nguyên một đám nhìn về phía đài cao, ánh mắt lộ ra vẻ lo lắng."),t("br"),t("br"),n._v("“Ngươi cảm thấy Trương sư sẽ nói cái gì?” Một vị học viên nhịn không được nhìn về phía bên cạnh bạn tốt."),t("br"),t("br"),n._v("“Trương sư đại tài, muốn nói gì, thật đúng là không đoán ra được, chẳng qua lấy năng lực của hắn, để tất cả tiền bối công nhận hẳn không phải là việc khó gì đi!” Hắn cái này cái hảo hữu, là Trương Huyền fan cuồng, đối tân viện trưởng tự tin hơn gấp trăm lần."),t("br"),t("br"),n._v("“Chỉ sợ không dễ dàng như vậy, năm đó kinh tài tuyệt diễm Mạc Ngôn Thanh viện trưởng, nghe nói cũng chỉ để 71% ý niệm công nhận, Trương sư thiên phú mặc dù không kém gì hắn, mà dù sao không có hoàn toàn triển lộ ra, nếu như lại cho cái mấy chục năm, có lẽ có thể nhẹ nhõm siêu việt, nhưng bây giờ ta sợ, không dễ dàng như vậy!” Bên cạnh một vị đối viện sử hiểu rất rõ danh sư nói."),t("br"),t("br"),n._v("“Ngược lại cũng đúng là, khẳng định là không dễ dàng như vậy, ta nghe trước khi nói lão viện trưởng tại mời Tiên Tổ cái này một cái khâu bên trên, thành tích liền không cao, đều không cao hơn hai mươi lăm phần trăm.” Lại một vị học viên nói."),t("br"),t("br"),n._v("Lão viện trưởng có thể thuần phục Tử Dương thú, thành công hơn đột phá thất tinh danh sư cảnh giới, đủ để chứng minh thiên phú và năng lực."),t("br"),t("br"),n._v("Loại này cường giả, cũng chỉ được đến không đủ hai mươi lăm phần trăm công nhận, đủ thấy chuyện này độ khó bao lớn."),t("br"),t("br"),n._v("“Muốn lấy được công nhận, liên quan rất nhiều phương diện, thực lực, nhãn duyên, vận khí thiếu một thứ cũng không được, ba câu nói rất khó biểu hiện ra một người năng lực, liền xem Trương sư, xử lý như thế nào! Nói rất hay, có thể được đến không tưởng tượng được thu hoạch, không tốt, đoán chừng cũng liền miễn cưỡng đạt tiêu chuẩn thôi!”"),t("br"),t("br"),n._v("Một vị trưởng lão nghe được mấy người đối thoại lắc đầu, đột nhiên chỉ về phía trước: “Mau nhìn, Trương sư chuẩn bị nói!”"),t("br"),t("br"),n._v("Mọi người đồng loạt hướng đài cao nhìn lại, quả nhiên thấy Trương Huyền ngẩng đầu lên, ánh mắt lấp lánh nhìn về phía trước mắt, hơn một trăm vị tiền bối danh sư ý niệm!"),t("br"),t("br"),n._v("Cùng Triệu Bính Tuất truyền âm, tất cả danh sư thảo luận, thoạt nhìn thời gian dài, trên thực tế chẳng qua mười mấy hô hấp thôi."),t("br"),t("br"),n._v("Tu sửa viện bộ dạng như thế nhanh liền làm xong quyết định, muốn nói gì, tất cả mọi người ngừng thở, nhịn không được trong lòng hiếu kỳ."),t("br"),t("br"),n._v("Triệu Bính Tuất cũng vuốt râu, nhìn lại."),t("br"),t("br"),n._v("Nên nói đều nói rồi, hắn muốn lần nữa nhìn xem, vị này Trương sư như thế nào sáng tạo kỳ tích, để cho người ta lau mắt mà nhìn."),t("br"),t("br"),n._v("“Ta tên là Trương Huyền!”"),t("br"),t("br"),n._v("Trương Huyền cười cười, trước tiến hành tự giới thiệu."),t("br"),t("br"),n._v("Nghe lời này, tất cả viện trưởng ý niệm, đồng thời nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc dù lãng phí một câu, nhưng bày tỏ đối tôn trọng của bọn hắn, cũng biểu lộ thân phận của mình, trung quy bên trong cách, không có chút nào thiếu niên đắc chí tùy tiện."),t("br"),t("br"),n._v("Càng cho thấy vô cùng tự tin, không có chút nào lo lắng, nhìn tới cái này cái trẻ tuổi viện trưởng, không đơn giản!"),t("br"),t("br"),n._v("Ngay tại tất cả viện trưởng ý niệm, đối với hắn câu nói đầu tiên biểu hiện vẫn là rất hài lòng thời điểm, chỉ thấy đối diện thanh niên, có chút lúng túng gãi đầu một cái, tràn đầy thật không tiện nhìn qua."),t("br"),t("br"),n._v("“Cái kia... Đã các ngươi đều là danh sư học viện lịch đại viện trưởng, hẳn là đều lòng dạ rộng lớn, ung dung rộng lượng... Cùng các ngươi thương lượng một chút, để cho ta đánh các ngươi một trận, được chứ?”"),t("br"),t("br"),n._v("“Đánh một trận?”"),t("br"),t("br"),n._v("Đang vểnh tai, muốn nghe hắn có thể nói cái gì nội dung tất cả danh sư trưởng lão, tất cả đều ngẩn ngơ."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-908"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-910"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);