(window.webpackJsonp=window.webpackJsonp||[]).push([[22],{377:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-996-lang-le-len-vao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-996-lang-le-len-vao"}},[n._v("#")]),n._v(" Chương 996: Lặng lẽ lẻn vào")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 996: Lặng lẽ lẻn vào"),t("br"),t("br"),n._v("Độc điện từ trước tới nay lấy dùng độc trình độ luận cao thấp, mọi người bình thường luận bàn cũng đều là dùng độc, kẻ trước mắt này, một lời không hợp liền đem người đá bay... Khó tránh khỏi có chút quá bạo lực đi."),t("br"),t("br"),n._v("“Tôn độc sư, ngươi... Quá lỗ mãng!”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, nhớ tới cái gì, Lý Viên vẻ mặt trở nên có chút khó coi: “Bọn họ đều là Đoàn Nhân sư huynh thuộc hạ, đem hắn đả thương, người sau khẳng định sẽ mất hứng...”"),t("br"),t("br"),n._v("“Đúng vậy a, Đoàn sư huynh từ trước tới nay bao che khuyết điểm, hơn nữa tính cách dữ dằn, đắc tội hắn người, phiền phức lớn rồi...”"),t("br"),t("br"),n._v("Một cái khác thanh niên, cũng đồng dạng khuôn mặt khó coi."),t("br"),t("br"),n._v("Vừa rồi nếu là vị này Tôn độc sư, ẩn nhẫn một chút, cùng lắm thì làm cho đối phương độc một chút, chuyện cũng liền đi qua, hiện tại được rồi, đem người đánh thành như vậy, Đoàn sư huynh tất nhiên sẽ tràn đầy không vui... Đến lúc đó đến tìm phiền phức, ai có thể chống đỡ nổi sao?"),t("br"),t("br"),n._v("“Đoàn sư huynh?” Thấy bọn họ sợ đến như vậy, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Đoàn Nhân sư huynh là Minh Chân phó điện chủ thân truyền đệ tử, sớm đã đạt đến Thánh vực nhất trọng hậu kỳ, càng là lục tinh cấp bậc độc sư... Tại Độc điện thứ hai bối tu luyện giả bên trong, thuộc về mạnh nhất, bởi vậy bị chúng ta tôn xưng một tiếng sư huynh. Cái này Lưu Húc, đúng là hắn trung thành nhất thuộc hạ...”"),t("br"),t("br"),n._v("Lý Viên vội vàng giải thích."),t("br"),t("br"),n._v("Độc điện điện chủ cùng tất cả trưởng lão, thuộc về đệ nhất bối phận, phía sau đệ tử, học sinh thì thuộc về cùng một cái bối phận."),t("br"),t("br"),n._v("Vị này Đoàn Nhân, tại thứ hai bối bên trong xa xa dẫn trước, sớm đã đạt đến bọn họ ngước nhìn tình trạng."),t("br"),t("br"),n._v("“Thánh vực nhất trọng hậu kỳ, lục tinh độc sư? Chẳng lẽ hắn còn có thể bởi vì vì một cái thuộc hạ, tìm ta phiền phức hay sao?”"),t("br"),t("br"),n._v("Thấy mấy người sợ đến như vậy, Trương Huyền cười lắc đầu."),t("br"),t("br"),n._v("Coi như đối phương lại không nói đạo lý, cũng phải không nể mặt sư thì cũng nể mặt phật đi!"),t("br"),t("br"),n._v("Bản thân là Hứa trưởng lão ân nhân cứu mạng, xem như đệ nhất bối trưởng lão bằng hữu, đối phương coi như phách lối nữa, một cái vãn bối, còn có thể bởi vì vì một cái thuộc hạ, làm khó bản thân?"),t("br"),t("br"),n._v("“Tôn độc sư có chỗ không biết, lần trước Ngô Hải Nguyên sư huynh, bởi vì cái này Lưu Húc không hiểu quy củ, ra tay giáo huấn một trận, kết quả, Đoàn sư huynh dưới cơn nóng giận, cứ thế mà đem hắn độc câm, đến bây giờ đều không thể nói chuyện...”"),t("br"),t("br"),n._v("Trên mặt nhanh muốn khóc lên biểu lộ, Lý Viên vội vàng nói: “Phải biết Ngô Hải Nguyên sư huynh, cũng là một vị lục tinh độc sư, tu vi càng là đạt đến Thánh vực nhất trọng...”"),t("br"),t("br"),n._v("“Còn có việc này?” Trương Huyền có chút kỳ quái, nghi ngờ nhìn qua: “Để một cái thuộc hạ, đắc tội cùng cấp bậc độc sư... Thật hay giả?”"),t("br"),t("br"),n._v("Khó trách hắn không tin."),t("br"),t("br"),n._v("Vừa rồi cái này Lưu Húc, hắn nhìn kỹ, chỉ có Hóa Phàm bát trọng Đạp Hư cảnh thực lực, đoán chừng nhiều nhất là cái ngũ tinh độc sư, loại người này toàn bộ Độc điện vừa nắm một bó to, để kẻ như vậy, đắc tội cùng cấp bậc lục tinh độc sư, vị này Đoàn sư huynh, bao che khuyết điểm có chút bảo vệ quá khó mà tin nổi đi!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hắn không hiểu, Lý Viên chần chờ một chút, cắn răng một cái: “Ngươi vừa tới khả năng không biết... Vị này Lưu Húc, không riêng gì Đoàn sư huynh thuộc hạ, vẫn là hắn... Trai lơ!”"),t("br"),t("br"),n._v("“Trai lơ?” Trương Huyền không giải: “Đoàn sư huynh... Là nữ?”"),t("br"),t("br"),n._v("Trai lơ chính là ăn bám, chẳng lẽ vị này Đoàn Nhân, là nữ tử?"),t("br"),t("br"),n._v("Danh sư đại lục, nam tôn nữ ti, nữ cũng là muốn xưng hô “Sư huynh”, thật giống như Hồ Yêu Yêu được xưng là “Học trưởng” đồng dạng."),t("br"),t("br"),n._v("Bất quá, Lưu Húc lớn lên xấu như vậy, có thể bị vừa ý, ánh mắt cái kia có bao nhiêu kém!"),t("br"),t("br"),n._v("“Là... Nam!”"),t("br"),t("br"),n._v("Lý Viên khuôn mặt xấu hổ: “Bởi vì dùng độc vật quá nhiều, định hướng đã có vấn đề...”"),t("br"),t("br"),n._v("“Khụ khụ...”"),t("br"),t("br"),n._v("Trương Huyền một hồi ác hàn, bị nước bọt sặc một cái."),t("br"),t("br"),n._v("Một cái nam nhân ưa thích một nam nhân khác, còn vì hắn không tiếc đắc tội cùng cấp bậc độc sư... Suy nghĩ một chút để cho người ta lông tơ chợt nổi lên."),t("br"),t("br"),n._v("Cũng khó trách."),t("br"),t("br"),n._v("Độc sư cái nghề nghiệp này ngày ngày cùng kịch độc làm bạn, có đôi khi để phối chế mới độc, thậm chí tự mình thí nghiệm... Đừng nói thay đổi định hướng, coi như thích linh thú, Thánh thú, thậm chí đại thụ, tảng đá, thực vật, đều vô cùng có khả năng."),t("br"),t("br"),n._v("Phải biết, rất nhiều độc dược, sẽ cho người sinh ra ảo giác, cái này Lưu Húc hắn thấy, xấu xí không chịu nổi, có lẽ tại uống thuốc độc vật sau Đoàn sư huynh trong mắt, coi như không tệ."),t("br"),t("br"),n._v("“Đúng vậy a, loại tình huống này ở đây không tính là hiếm thấy, ngươi đem Đoàn sư huynh trai lơ đánh không rõ sống chết, hắn biết tin tức tất nhiên nổi giận, một khi đến tìm phiền phức... Liền nguy rồi!”"),t("br"),t("br"),n._v("Lý Viên tràn đầy lo lắng."),t("br"),t("br"),n._v("Độc điện, người nào không biết Đoàn sư huynh đáng sợ, nguyên nhân chính là như vậy, không ai dám đắc tội, vị này ngược lại tốt, nói còn chưa dứt lời, liền đem Lưu Húc đánh thành như vậy, chuyện đã đến không cách nào vãn hồi tình trạng."),t("br"),t("br"),n._v("Mặc dù Hứa trưởng lão địa vị cực cao, nhưng đồng dạng cũng chỉ là lục tinh độc sư, không thể so với Đoàn sư huynh cao bao nhiêu, mấu chốt nhất là, nghe nói người sau chỉ cần thực lực tiến thêm một bước, liền có khả năng trở thành trưởng lão, lại không về mặt thân phận chênh lệch."),t("br"),t("br"),n._v("“Hắn muốn đi qua tìm phiền toái, tìm đi!”"),t("br"),t("br"),n._v("Duỗi lưng một cái, Trương Huyền không thèm để ý chút nào."),t("br"),t("br"),n._v("Cái gì Đoàn sư huynh, hắn căn bản không quan tâm."),t("br"),t("br"),n._v("Đối phương thật muốn tìm phiền toái, không ngại đóng cửa thả Tử Diệp vương, náo cái long trời lở đất."),t("br"),t("br"),n._v("Dù sao Độc điện đã đã tìm được, thư tịch ngay tại cách đó không xa, đối mặt một đám ngày ngày nhớ độc chết người khác gia hỏa, không cần thiết làm người tốt."),t("br"),t("br"),n._v("“Thế nhưng là...”"),t("br"),t("br"),n._v("Thấy hắn không thèm quan tâm, Lý Viên đám người liếc mắt nhìn nhau, từng người cười khổ, đang không biết nên ứng đối ra sao, chỉ thấy một cái vẻ mặt u ám thanh niên đi nhanh tới."),t("br"),t("br"),n._v("“Đoàn sư huynh...”"),t("br"),t("br"),n._v("Nhìn người nọ, mấy sắc mặt người đồng thời thay đổi."),t("br"),t("br"),n._v("Thật sự là nhắc Tào Tháo, Tào Tháo đến!"),t("br"),t("br"),n._v("Bọn họ chỗ ở, không tính là quá lớn, trước mắt vị này Tôn độc sư, đem Lưu Húc một quyền đánh bay, không ít người đều thấy được, tin tức truyền lại rất nhanh, xem ra, Đoàn Nhân sư huynh không chỉ biết, đã chạy đến!"),t("br"),t("br"),n._v("Cái này xong... Muốn chạy trốn, cũng không kịp."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Mấy bước đi tới trước mặt, Đoàn Nhân sư huynh, thân thể nhảy lên, đem trên chạc cây mang theo Lưu Húc bẻ xuống, cho ăn chút thuốc, ánh mắt trở nên băng hàn vô cùng."),t("br"),t("br"),n._v("“Đoàn sư huynh, ta muốn cùng hắn tỷ thí dùng độc, hắn nhưng đối ta đánh lén... Ngươi phải làm chủ cho ta!”"),t("br"),n._v("Tỉnh lại, Lưu Húc một ngón tay cách đó không xa Trương Huyền, nghiến răng nghiến lợi."),t("br"),t("br"),n._v("“Yên tâm đi!”"),t("br"),t("br"),n._v("Ánh mắt híp lại, Đoàn Nhân đứng dậy, lạnh lùng nhìn về phía Trương Huyền, trong mắt sát ý như là thủy triều: “Ta sẽ để hắn trả giá thật lớn!”"),t("br"),t("br"),n._v("“Đoàn sư huynh, vị này Tôn độc sư, là Hứa trưởng lão ân nhân cứu mạng...”"),t("br"),t("br"),n._v("Thấy hắn nổi giận, Lý Viên cắn răng vội vàng tiến lên giải thích."),t("br"),t("br"),n._v("Hứa trưởng lão đem người giao cho bọn hắn, mới đi đến chỗ ở liền bị độc chết lời nói, bọn họ cũng có trách nhiệm rất lớn."),t("br"),t("br"),n._v("“Cút!”"),t("br"),t("br"),n._v("Lông mày giương lên, Đoàn Nhân một tiếng trách mắng."),t("br"),t("br"),n._v("Lộc cộc lộc cộc lộc cộc lộc cộc!"),t("br"),t("br"),n._v("Bị một cỗ sóng khí xung kích, Lý Viên sắc mặt trắng nhợt, liên tục lui về sau bảy, tám bước, một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Hắn chỉ có Đạp Hư cảnh thực lực, cùng đối phương loại này cường giả Thánh vực kém quá nhiều, căn bản ngăn cản không nổi."),t("br"),t("br"),n._v("“Ta mặc kệ ngươi là cái này Hứa trưởng lão người nào, tổn thương người của ta, liền muốn trả giá đắt.”"),t("br"),t("br"),n._v("Đi tới trước mặt, Đoàn Nhân nhìn chằm chằm Trương Huyền, trên mặt không có chút nào tình cảm: “Hiện tại cho ngươi hai con đường đi, thứ nhất, quỳ xuống cho Lưu Húc xin lỗi, tùy ý hắn xử trí! Thứ hai, ta tự mình ra tay, để ngươi muốn sống không được, muốn chết không xong...”"),t("br"),t("br"),n._v("Người người đều biết Lưu Húc là mặt của hắn đầu, cái tên này thế mà cũng dám động thủ, rõ ràng là không đem hắn để vào mắt, không giáo huấn một lần, như thế nào lập uy?"),t("br"),t("br"),n._v("“Nếu như đều không chọn đâu?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Khó trách Hứa trưởng lão nói, Độc điện không yên ổn, muốn hắn cẩn thận, hiện tại xem ra, quả là thế."),t("br"),t("br"),n._v("Không biết trời cao đất rộng đồ đần thực sự quá nhiều..."),t("br"),t("br"),n._v("Vốn còn muốn khiêm tốn một chút, nhưng đối phương bộ này muốn ăn đòn bộ dáng... Không đánh đều có lỗi với chính mình!"),t("br"),t("br"),n._v("“Đều không chọn? Ngươi cái này là muốn chết...”"),t("br"),t("br"),n._v("Quát lạnh một tiếng, Đoàn Nhân thân thể nhoáng một cái, liền xuất hiện tại Trương Huyền trước mặt, năm ngón tay mở ra, đối với hắn bắt tới."),t("br"),t("br"),n._v("Đầu ngón tay của hắn, chân khí bắn nhanh, đồng thời một đạo đặc thù bột phấn, di tán mà đến, dường như chỉ cần ngửi bên trên một cái, hoặc là dính đến trên da, liền sẽ trúng độc, vạn kiếp bất phục."),t("br"),t("br"),n._v("Đây là hắn tiêu tốn vô số tâm huyết chế biến kịch độc, Thánh vực nhất trọng cường giả đỉnh phong dính vào, đều không chịu nổi."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Đối mặt hắn độc phấn cùng chân khí, Trương Huyền lắc đầu, một mặt thương hại."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Bàn tay đưa ra ngoài, không có bất kỳ cái gì chiêu số, đưa tay quất tới."),t("br"),t("br"),n._v("Nhìn thấy động tác của hắn, Đoàn Nhân đầu tiên là cảm thấy khinh thường, lập tức con ngươi thu hẹp, vội vàng đem lực lượng toàn thân tập trung lại đối kháng."),t("br"),t("br"),n._v("Bất quá, đợi khi hắn phản ứng kịp, đã chậm, cùng vừa rồi Lưu Húc đồng dạng, vù vù cũng không kịp, liền một đầu cắm ở trên chạc cây, ngất đi."),t("br"),t("br"),n._v("Mặc dù hắn chỉ có Tòng Thánh cấp bậc, nhưng Thánh vực nhị trọng Liêu Tùng cũng không là đối thủ, cái tên này chẳng qua Thánh vực nhất trọng hậu kỳ mà thôi, gảy gảy ngón tay liền diệt, không đáng kể chút nào."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lý Viên đám người tất cả đều sắc mặt trắng nhợt, thân thể run rẩy."),t("br"),t("br"),n._v("Quá dọa người!"),t("br"),t("br"),n._v("Đoàn Nhân sư huynh thực lực, bọn họ thế nhưng là thấy tận mắt, phối hợp độc dược, coi như Thánh vực nhị trọng sơ kỳ cường giả, gặp gỡ cũng không dám mạnh mẽ chống đỡ, vị này Tôn độc sư ngược lại tốt, không nói hai lời, một bàn tay liền hút choáng..."),t("br"),t("br"),n._v("Thực lực cái kia mạnh bao nhiêu?"),t("br"),t("br"),n._v("“Được rồi, ta muốn đi về nghỉ!”"),t("br"),t("br"),n._v("Chẳng muốn giải thích, Trương Huyền khoát tay áo, nhìn về phía Lý Viên một cái: “Nếu như còn có người qua tới quấy rối, trước thời hạn gọi ta một tiếng!”"),t("br"),t("br"),n._v("Nói xong nhấc chân hướng sân nhỏ đi đến."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Lý Viên đám người mau vội vàng gật đầu."),t("br"),t("br"),n._v("Tiến vào viện, Trương Huyền tiện tay liền đem viện cửa đóng lại, tìm cái linh khí hội tụ chỗ, khoanh chân ngồi xuống."),t("br"),t("br"),n._v("“Đã đến Độc điện, cũng chớ do dự, mau chóng nhìn xong thư, nghĩ biện pháp cứu chữa Ngụy Như Yên đi!”"),t("br"),t("br"),n._v("Mặc dù Như Yên tình huống hiện tại, còn không có đạt tới không thể cứu vãn tình trạng, nhưng cũng rất nguy hiểm, đã đến Độc điện, liền không cần tuân thủ danh sư quy củ của học viện, mau chóng đem thư nhìn xong, tìm kiếm cứu chữa phương pháp."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Tinh thần khẽ động, Vu hồn theo trong cơ thể chui ra."),t("br"),t("br"),n._v("Trực tiếp nghênh ngang vọt tới đối phương tàng thư điện đọc sách, sẽ dẫn tới rất nhiều phiền phức, dù sao Vu hồn đọc sách tốc độ cùng bản thân đi qua đồng dạng, không cần thiết phiền phức."),t("br"),t("br"),n._v("Nhẹ nhàng khẽ động, Trương Huyền hướng lên bầu trời bay ra ngoài."),t("br"),t("br"),n._v("Mặc dù là ban ngày, nhưng hắn tu luyện chính là trời đạo Vu hồn, không bị ảnh hưởng chút nào, lại thêm tẩm bổ Ngụy Như Yên linh hồn về sau, hồn phách cũng nắm giữ kháng độc tính, coi như hòn đảo này bầu trời cầm giữ có độc khí, đối với hắn mà nói, cũng không tính là gì."),t("br"),t("br"),n._v("Thời gian không dài, liền đến đến trên không, cúi đầu hướng phía dưới nhìn sang."),t("br"),t("br"),n._v("“Hứa trưởng lão nói, tàng thư điện thuộc về tam đại điện đường một trong, đã như vậy, khẳng định là cao nhất, huy hoàng nhất kiến trúc...”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, đảo qua tất cả che đậy, ba cái cao ngất điện đường, xuất hiện ở tầm mắt."),t("br"),t("br"),n._v("(Vì minh chủ hoa tuyết giám tăng thêm!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1004"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1006"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);