(window.webpackJsonp=window.webpackJsonp||[]).push([[220],{573:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1175-cung-len-đi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1175-cung-len-đi"}},[n._v("#")]),n._v(" Chương 1175: Cùng lên đi")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Có bao nhiêu Chiến sư đường, bọn họ... Tinh nguyên linh thạch thượng phẩm nhiều hay không?” Ánh mắt sáng lên, Trương Huyền vội vàng nhìn tới."),i("br"),i("br"),n._v("Hắn hiện tại thiếu tiền đều thiếu điên rồi, nếu là đến đây trao đổi phong hào đế quốc Chiến sư đường, mười phần giàu có lời nói, ngược lại là có thể suy nghĩ chút biện pháp."),i("br"),i("br"),n._v("“Tổng cộng ba cái, cùng chúng ta Chiến sư đường, không kém bao nhiêu, lại thêm tiêu hao, sẽ không có bao nhiêu tinh nguyên linh thạch thượng phẩm đi!”"),i("br"),i("br"),n._v("Thấy trước mắt vị này, mới mở miệng liền là linh thạch, Hình đường chủ cười khổ lắc đầu."),i("br"),i("br"),n._v("“Không kém bao nhiêu?”"),i("br"),i("br"),n._v("“Đúng vậy a, liên minh đế quốc cấp dưới phong hào đế quốc, tổng cộng tám cái, trong đó bốn cái, bất kể quốc lực còn là Danh Sư đường, cấp bậc đều so với chúng ta cái này cao hơn một chút, thuộc về một cái tầng lớp. Còn lại bốn cái, bao quát chúng ta Thanh Nguyên đế quốc ở bên trong, thuộc về một cái khác cấp bậc. Lần này tới trao đổi, chính là người sau, ý định tuyển chọn nhân tài, đi bí mật xông phong hào đế quốc tu hành, tổng cộng hai mươi cái danh ngạch!”"),i("br"),i("br"),n._v("Hình đường chủ giải thích nói: “Trước kia chúng ta Thanh Nguyên Chiến sư đường, chỉ có thể nhận được hai cái danh ngạch, thành tích một mực lót đáy, lần này cùng Huyền Huyền hội trao đổi, mọi người được lợi rất nhiều, hy vọng có thể tranh thủ thêm mấy cái!”"),i("br"),i("br"),n._v("“Đi bí mật xông đế quốc tu hành? Nơi đó có cái gì tu luyện Thánh địa?” Trương Huyền khó hiểu."),i("br"),i("br"),n._v("Lúc trước đi Huyễn Vũ đế quốc, là bởi vì bọn họ Hóa Thanh trì, đối linh hồn tu luyện có trợ giúp, chẳng lẽ cái này bí mật xông đế quốc, cũng có tương tự Thánh địa?"),i("br"),i("br"),n._v("“Muốn nói Thánh địa, quả thực có một cái! Bí mật xông đế quốc, có một chỗ Kiếm Trì, ẩn chứa trong đó vô số kiếm ý kiếm pháp, nếu như có thể tiến vào học tập, đối với chiến đấu lực sẽ có cực lớn trợ giúp! Bởi vậy, cái này danh ngạch, đối với chúng ta Thanh Nguyên Chiến sư đường tới nói, mười phần quan trọng, lấy được càng nhiều, liên minh Chiến sư đường tổng bộ cho ban thưởng, cũng sẽ càng nhiều!”"),i("br"),i("br"),n._v("Hình đường chủ nói."),i("br"),i("br"),n._v("“Ban thưởng?” Trương Huyền con mắt lập tức sáng lên."),i("br"),i("br"),n._v("“Đúng, lần trước trao đổi, nhận được đệ nhất sùng xa phong hào đế quốc, liền được tổng bộ, tổng giá trị hơn trăm tinh nguyên linh thạch thượng phẩm ban thưởng!”"),i("br"),i("br"),n._v("Hình đường chủ nói tiếp."),i("br"),i("br"),n._v("“Hơn trăm tinh nguyên linh thạch thượng phẩm?” Trương Huyền tràn đầy kích động."),i("br"),i("br"),n._v("Nếu có nhiều như vậy tinh nguyên linh thạch thượng phẩm, hoàn toàn có thể nhẹ nhõm đột phá đến Thai Anh cảnh đỉnh phong, thậm chí đều có thể xung kích Nguyên Thần cảnh đỉnh phong thử một chút."),i("br"),i("br"),n._v("“Đúng!” Hình đường chủ nhẹ gật đầu: “Thật ra thì, thật ra thì loại này trao đổi, cùng quân đội luyện binh có chút tương tự, từ mỗi cái cấp bậc lựa ra lợi hại nhất binh vương tiến hành quyết đấu, lấy được thắng lợi càng nhiều, tổng bộ tự nhiên cũng không keo kiệt, đối cấp dưới Chiến sư đường cùng Chiến sư tới nói, cũng là một loại khích lệ!”"),i("br"),i("br"),n._v("Trương Huyền hiểu được."),i("br"),i("br"),n._v("Nếu như đem Chiến sư so sánh Danh Sư đường quân đội, mỗi bên quân đội ở giữa tiến hành đại bỉ bính, cũng là nên."),i("br"),i("br"),n._v("Không có so sánh, liền không có áp lực, bất kể quân đội còn là quân kỷ đều sẽ trở nên tan rã."),i("br"),i("br"),n._v("“Nếu như Trương sư có thể giúp một tay chỉ điểm, thật muốn thu hoạch được ban thưởng, ta có thể hối đoái thành tinh nguyên linh thạch thượng phẩm, phân ra một nửa cho ngươi! Ngươi cũng biết, quyền hạn của ta chỉ có thể vận dụng một nửa, nhiều lại không được...” Hình đường chủ nói."),i("br"),i("br"),n._v("Trước mắt vị này, không chỉ sức chiến đấu mạnh mẽ, cùng cấp bậc vô địch, càng quan trọng hơn là, đối với chiến đấu cùng võ kỹ đã hiểu, đạt đến mức làm người nghe kinh hãi."),i("br"),i("br"),n._v("Mặc dù chỉ điểm rất ít, lại mỗi một câu nói, đều để người hiểu ra, bừng tỉnh hiểu ra, chân do hắn chỉ điểm lời nói, Thanh Nguyên thành Chiến sư đường, có lẽ thật có thể vượt qua trước kia thành tích, đạt tới một cái độ cao mới."),i("br"),i("br"),n._v("Nguyên nhân chính là như vậy, gặp hắn tới, mới nhịn không được mở miệng."),i("br"),i("br"),n._v("“Thành giao!”"),i("br"),i("br"),n._v("Lời còn chưa nói hết, liền nghe đối diện thanh niên, đáp ứng thanh âm vang lên."),i("br"),i("br"),n._v("Nói đùa."),i("br"),i("br"),n._v("Hiện tại thiếu liền là linh thạch, đừng nói một nửa ban thưởng, liền xem như một phần ba, cũng đáng giá hao tốn sức lực."),i("br"),i("br"),n._v("“Cứ quyết định như vậy đi, không có gì bất ngờ xảy ra, còn lại ba cái Chiến sư đường người, trong ba ngày liền sẽ đi tới... Trương sư cần làm sao huấn luyện, ta có thể để cho Chiến sư nhóm vô điều kiện phối hợp!”"),i("br"),i("br"),n._v("Gặp hắn đáp ứng, Hình đường chủ thở phào nhẹ nhõm, hỏi."),i("br"),i("br"),n._v("“Ngươi nói cái này trao đổi, đều cần cấp bậc gì tu luyện giả? Tham gia trận đấu người có thể hay không chọn xong?”"),i("br"),i("br"),n._v("Trương Huyền hỏi."),i("br"),i("br"),n._v("“Cái này trao đổi, cần hai mươi vị tu luyện giả, từ Tằm Phong cảnh bắt đầu, mãi cho đến Thần Thức cảnh đỉnh phong, vừa vặn hai mươi cái tiểu cấp bậc... Người chúng ta trước đó liền chọn xong...”"),i("br"),i("br"),n._v("Hình đường chủ gật đầu."),i("br"),i("br"),n._v("Từ Tằm Phong cảnh bắt đầu, Bán Thánh, Tòng Thánh, Trục Không cảnh, Thần Thức cảnh, năm cái đại cảnh giới, mỗi cái cấp bậc đều có sơ kỳ, trung kỳ, hậu kỳ, đỉnh phong bốn cái tiểu cấp bậc, cộng lại vừa vặn hai mươi cái đẳng cấp."),i("br"),i("br"),n._v("Mỗi cái cấp bậc đều có người mạnh nhất."),i("br"),i("br"),n._v("“Người ở chỗ nào? Mang ta đi nhìn một chút!”"),i("br"),i("br"),n._v("Đã nhân tuyển tốt rồi, còn lại cũng đơn giản, chỉ cần nhìn một chút mỗi người am hiểu nhất phương diện nào đi nữa, sau đó lại chỉ điểm thêm cùng sử dụng, liền có thể phát huy ra mạnh nhất hiệu quả."),i("br"),i("br"),n._v("“Tại phòng luyện võ...”"),i("br"),i("br"),n._v("Cười cười, Hình đường chủ vội vàng phía trước dẫn đường."),i("br"),i("br"),n._v("Phòng luyện võ ngay tại võ kỹ điện lân cận, là Chiến sư nhàn nhã luận bàn địa phương, đại sảnh rộng lớn vô cùng, trung tâm súc lập bốn cái tỷ thí đài, còn chung quanh, thì xây dựng khán đài cùng tĩnh thất, dùng để để Chiến sư xem cuộc chiến cùng nghỉ ngơi."),i("br"),i("br"),n._v("Còn không có tiến vào bên trong, liền nghe đến tiếng gió rít gào, dường như có người tại tỷ thí, ngay sau đó là đủ loại la lên cùng phát tiết thanh âm."),i("br"),i("br"),n._v("Đi vào cửa lớn, quả nhiên thấy một cái trong đó tỷ thí trên đài, hai vị Chiến sư tại luận võ, lực lượng hùng hồn bốn phía xao động, hoa mỹ chiêu số, một cái tiếp theo một cái, để cho người ta không kịp nhìn."),i("br"),i("br"),n._v("Còn lại cái này có mười cái Chiến sư vây quanh ở bốn phía, mỗi khi nhìn thấy lợi hại tuyệt chiêu sử dụng ra, liền phát ra hưng phấn mà la lên."),i("br"),i("br"),n._v("Trương Huyền nhìn một cái, phát hiện bên trong thế mà còn có hai cái người quen, chính là ở bên trong tức điện gặp phải Thạch Hạo, cùng võ kỹ điện thi triển Đại Tinh La Chỉ Lực Tiêu Đàm."),i("br"),i("br"),n._v("Hai người này, tại mỗi người điện đường đều là đỉnh phong nhất tồn tại, có thể bị tuyển chọn, không gì đáng trách."),i("br"),i("br"),n._v("“Đường chủ, Trương sư!”"),i("br"),i("br"),n._v("Nhìn thấy bọn họ đi tới, mọi người ngừng lại, nguyên một đám ôm quyền khom người, thái độ cung kính."),i("br"),i("br"),n._v("“Ừm, không cần khách khí, Trương sư chắc hẳn không cần ta giới thiệu, cũng đều quen biết đi!”"),i("br"),n._v("Đi tới cái trước mặt, Hình đường chủ nói."),i("br"),i("br"),n._v("Mọi người gật đầu."),i("br"),i("br"),n._v("Trương Huyền mấy ngày nay tại từng cái điện đường, gây ra động tĩnh, hầu như đem trọn cái Chiến sư đường đều xích mích."),i("br"),i("br"),n._v("Một người phá tất cả thông đạo ghi chép, hơn nữa mỗi một cái đều đạt đến để cho người ta khó mà với tới tình trạng, để cho người ta không thể không phục."),i("br"),i("br"),n._v("“Lần này trao đổi, Chiến sư đường mười phần chú trọng, cho nên, chuyên môn mời hắn, cho mọi người tiến hành chỉ điểm, hi vọng chư vị có thể đủ tốt tốt phối hợp!” Hình đường chủ nói tiếp."),i("br"),i("br"),n._v("“Đúng!”"),i("br"),i("br"),n._v("Nghe được Trương Huyền cho bọn hắn chỉ điểm, mọi người con mắt tất cả đều sáng lên."),i("br"),i("br"),n._v("Huyền Huyền hội thực lực, bọn họ nhìn ở trong mắt, có thể đem một cái bình thường danh sư học viện bình thường danh sư, chỉ điểm so cùng cấp bậc Chiến sư đều mạnh mẽ, phần này năng lực, đã có thể nói kinh khủng."),i("br"),i("br"),n._v("Có thể được đến chỉ điểm của hắn, không cần nghĩ, thực lực tất nhiên có thể có được bay vọt về chất."),i("br"),i("br"),n._v("“Trương sư, ngươi tới nói vài câu đi!”"),i("br"),i("br"),n._v("Hình đường chủ cười cười."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, Trương Huyền đi tới mọi người trước mặt, cũng không nói chuyện, cổ tay khẽ đảo, một sợi dây thừng xuất hiện tại lòng bàn tay: “Làm phiền Hình đường chủ đem ta hai tay trói lên!”"),i("br"),i("br"),n._v("“Trói?”"),i("br"),i("br"),n._v("Hình đường chủ sững sờ."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Mặc dù không biết trước mắt vị này làm như vậy đến mục đích là cái gì, có điều, đã như vậy yêu cầu, Hình đường chủ cũng không nhiều lời, lúc này dây thừng thắt nút, đem hắn hai tay tại sau lưng, trói cùng một chỗ, trói lại chặt chẽ vững vàng."),i("br"),i("br"),n._v("Tranh một chút, phát hiện không cách nào giãy giụa, Trương Huyền lúc này mới một mặt thoả mãn lần nữa xoay người lại, thân thể nhoáng một cái, đi tới phụ cận trên lôi đài."),i("br"),i("br"),n._v("“Đem tu vi đều áp chế đến Tằm Phong cảnh sơ kỳ, sau đó... Cùng lên đi!”"),i("br"),i("br"),n._v("Hừ một tiếng, Trương Huyền thân thể nhoáng một cái, Thánh vực nhị trọng đỉnh phong lực lượng thẳng tắp hạ thấp, rất nhanh rơi vào Tằm Phong cảnh sơ kỳ, an tĩnh đứng tại tỷ thí đài một góc."),i("br"),i("br"),n._v("“Cùng tiến lên?”"),i("br"),i("br"),n._v("“Trương sư có ý tứ là... Trói lại hai tay, cùng chúng ta nhiều người như vậy cùng một chỗ chiến đấu?”"),i("br"),i("br"),n._v("“Không phải chiến đấu, mà là đơn đấu chúng ta hai mươi người...”"),i("br"),i("br"),n._v("“Trương sư thực lực ta bội phục, nhưng trói lại hai tay, còn muốn chiến thắng chúng ta liên thủ... Khó tránh khỏi có chút quá bất cẩn đi!”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nhìn thấy Trương Huyền động tác, nghe được lời nói này, tất cả mọi người vẻ mặt đỏ lên, híp mắt lại."),i("br"),i("br"),n._v("Bất kể nói thế nào, bọn họ đều là Chiến sư đường tuyển ra, cùng cấp bậc đứng đầu nhất tồn tại."),i("br"),i("br"),n._v("Cho dù cùng vị này Trương Huyền, khả năng còn kém một chút, đơn đả độc đấu thắng không nổi, một người chọn một đàn, còn trói lại hai tay... Khó tránh khỏi có chút quá xem thường người đi!"),i("br"),i("br"),n._v("“Làm sao? Không dám?”"),i("br"),i("br"),n._v("Thấy nói dứt lời, mọi người mặc dù tức giận, lại không lên đây, Trương Huyền mí mắt vừa nhấc: “Vậy dạng này, ta ở đây lưu lại hai cái dấu chân, nếu như các ngươi nhiều người như vậy cùng một chỗ xông lại, có thể để cho ta rời khỏi tại chỗ... Đồng dạng coi như ta thua!”"),i("br"),i("br"),n._v("Trong khi nói chuyện, bàn chân trên mặt đất nhẹ nhàng giẫm mạnh."),i("br"),i("br"),n._v("Tỷ thí đài cứng rắn mặt đất liền xuất hiện hai cái bóng loáng dấu chân, tựa như điêu khắc ở phía trên."),i("br"),i("br"),n._v("“Thật là tinh thuần chân khí...”"),i("br"),i("br"),n._v("Mọi người con ngươi co rụt lại."),i("br"),i("br"),n._v("Tỷ thí đài bởi vì phải thừa nhận chiến đấu dư âm, dùng hầu như đều là kiên cố nhất nham thạch, mặt khác còn tại phía trên tăng thêm đủ loại phong ấn, bình thường Tằm Phong cảnh sơ kỳ, cho dù toàn lực công kích, cũng chưa chắc có thể ở phía trên lưu lại dấu vết, đối phương bàn chân không nhúc nhích, thân không hoảng hốt vai không dao động, liền giẫm ra rõ ràng như thế ấn ký..."),i("br"),i("br"),n._v("Chân khí tinh thuần, chi hùng hậu, quả thực nghe rợn cả người."),i("br"),i("br"),n._v("“Trương sư chân khí hùng hậu mức độ, ta là bội phục, có điều, không cần hai tay, hai chân không di động... Liền muốn vượt qua chúng ta, chỉ sợ cũng không dễ dàng như vậy đi!”"),i("br"),i("br"),n._v("Sau khi hết khiếp sợ, một vị Chiến sư hừ một tiếng, thân thể nhoáng một cái, nhảy lên đài cao: “Như vậy, ta tới trước thử một chút, nếu như như ngươi vậy có thể vượt qua ta, chúng ta cùng một chỗ bên trên cũng không muộn!”"),i("br"),i("br"),n._v("Chính là Thạch Hạo!"),i("br"),i("br"),n._v("Vị này vì phá nội tức thông đạo ghi chép, cưỡng chế chế tu vi hai mươi năm không đột phá, bị Trương Huyền tiến công về sau, một lần đột phá, hiện tại đã là Thánh vực nhị trọng trung kỳ cao thủ."),i("br"),i("br"),n._v("Gặp hắn lên đài, mọi người tất cả đều nhẹ gật đầu."),i("br"),i("br"),n._v("Tất cả mọi người là Chiến sư đường Chiến sư, đối với vị này Thạch Hạo thực lực, còn là mười phần tin phục, từ hắn ra tay, vừa vặn có thể thăm dò một chút, vị này Trương sư trói lại hai tay, cầm cố lại hai chân, đến cùng có thể phát huy ra mạnh cỡ nào sức chiến đấu."),i("br"),i("br"),n._v("Kẽo kẹt, kẽo kẹt!"),i("br"),i("br"),n._v("Đi tới trên đài, Thạch Hạo cũng không nhiều lời, trong cơ thể khí tức áp chế, thời gian nháy mắt cùng Trương Huyền đồng dạng, liền biến thành Tằm Phong cảnh sơ kỳ."),i("br"),i("br"),n._v("“Trương sư, xin chỉ giáo!”"),i("br"),i("br"),n._v("Áp chế hết tu vi, Thạch Hạo hai mắt nheo lại, cánh tay một dài, chân khí tuôn ra, đem trọn cái luận võ đài đều bao phủ ở bên trong, sau một khắc, hướng Trương Huyền lao đến."),i("br"),i("br"),n._v("Người chưa đến, lực lượng đã đến."),i("br"),i("br"),n._v("Long trời lở đất, đại địa nổ vang."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1183"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1185"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);