(window.webpackJsonp=window.webpackJsonp||[]).push([[2204],{2559:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-906-khong-chi-nhin-mot-cai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-906-khong-chi-nhin-mot-cai"}},[n._v("#")]),n._v(" Chương 906: Không chỉ nhìn một cái")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 906: Không chỉ nhìn một cái"),i("br"),i("br"),n._v("“Nhìn một cái?”"),i("br"),i("br"),n._v("Mọi người tất cả đều cau mày."),i("br"),i("br"),n._v("Có ý tứ gì?"),i("br"),i("br"),n._v("Hỏi ngươi tại sao thua, ý tứ rất rõ ràng, thua ở cái nào chiêu số, hoặc là công pháp gì xuống... Không muốn nói, không nói thì thôi, như vậy tình thơ ý hoạ làm gì?"),i("br"),i("br"),n._v("Còn “Nhìn ngươi một cái...”, có phải hay không ngay sau đó sẽ còn nói “Lại cũng không thể quên mất dung nhan của ngươi...”"),i("br"),i("br"),n._v("Đây là danh sư đại lục rất lưu hành một bài tái ngoại khúc mục, không ít người đều biết hát, bây giờ tại hỏi ngươi chính sự, một cái khôi lỗi, không hảo hảo trả lời, sửa sang những thứ này ca từ, làm gì?"),i("br"),i("br"),n._v("Mi trưởng lão đám người lắc đầu."),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Thấy vẻ mặt của mọi người, khôi lỗi một mặt muốn khóc biểu lộ: “Ta nói chính là hắn nhìn ta một cái... Ta liền thua!”"),i("br"),i("br"),n._v("“Nhìn một cái?”"),i("br"),i("br"),n._v("Mọi người ngẩn ngơ."),i("br"),i("br"),n._v("Nghe nói qua một chiêu bại trận, một chưởng, một kiếm định thắng thua, có thể chưa nghe nói qua, liếc mắt nhìn liền nhận thua!"),i("br"),i("br"),n._v("“Ý của ngươi là nói, hôm qua tới vượt quan vị kia, không có ra tay với ngươi, mà là nhìn một cái, liền để ngươi chủ động nhận thua?”"),i("br"),i("br"),n._v("Tu Trường Thanh giống như là rõ ràng cái gì, nhịn không được hỏi."),i("br"),i("br"),n._v("“Đúng...”"),i("br"),i("br"),n._v("Khôi lỗi gật đầu, thấy có người lý giải bản thân, kích động suýt chút nữa khóc."),i("br"),i("br"),n._v("“Đúng?”"),i("br"),i("br"),n._v("Hai mặt nhìn nhau, Triệu Bính Tuất, Mi trưởng lão đám người tràn đầy mê man."),i("br"),i("br"),n._v("Ngươi là khôi lỗi, phụ trách thủ hộ núi ghi chép tồn tại, viện trưởng nhìn ngươi một cái, liền nhận thua? Coi như muốn lấy lòng, cũng không cần làm rõ ràng như thế đi!"),i("br"),i("br"),n._v("Như vậy còn có cái gì công bằng công chính có thể nói?"),i("br"),i("br"),n._v("“Có thể hay không đem hôm qua chiến đấu tình huống cẩn thận miêu tả một cái?” Cùng ý nghĩ của mọi người không giống, Tu Trường Thanh hỏi."),i("br"),i("br"),n._v("“Được!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, khôi lỗi cũng không chậm trễ, đem trước chiến đấu tình cảnh, lần nữa thuật lại một lần."),i("br"),i("br"),n._v("Nhìn thấy Trương Huyền, nó chủ động tiến hành công kích, liền tại sắp tới đối phương trước mặt thời điểm, thanh niên nhẹ nhàng cười một tiếng, hai mắt ánh sáng, tại hư không một chỗ giao hội."),i("br"),i("br"),n._v("Mọi người cũng chưa tỉnh đến có cái gì khác biệt, nhưng khôi lỗi nhưng giống như gặp quỷ đồng dạng, sợ tới mức vội vàng lui về phía sau, bởi vì tốc độ quá nhanh, tiến lên lực lượng cùng lui về phía sau lực lượng xung kích, để hắn thiên chuy bách luyện thân thể, phát sinh kịch liệt va chạm, còn không có ra tay liền bị trọng thương!"),i("br"),i("br"),n._v("“Đây là... Khôi lỗi cấu tạo bên trên thiếu hụt?”"),i("br"),i("br"),n._v("Cái khác viện trưởng còn đang nghi ngờ, đối phương là không phải là nói có chút mơ hồ, Thiên Công học viện viện trưởng, thân thể nhoáng một cái, suýt chút nữa ngã sấp xuống."),i("br"),i("br"),n._v("Khôi lỗi là Thiên Công vật phẩm, cùng nhân loại cấu tạo khác biệt, viện trưởng ánh mắt tập trung chỗ, chính là cái này khôi lỗi, cấu tạo bên trong lớn nhất thiếu hụt, chiến đấu, chỉ cần ngón tay ở phía trên nhẹ nhàng điểm một cái, liền có thể để cho biến thành phế phẩm, lại không năng lực chiến đấu!"),i("br"),i("br"),n._v("Thậm chí, sau đó liền chữa trị đều làm không được!"),i("br"),i("br"),n._v("Nói cách khác, tại khôi lỗi công kích trong nháy mắt, viện trưởng không chỉ tìm được đối phương chiêu thức bên trong thiếu hụt, càng là tìm được cấu tạo bên trong lỗ thủng!"),i("br"),i("br"),n._v("Nhìn một cái không có ra tay, là đang cảnh cáo, là đang nhắc nhở, nếu như còn không thức thời, liền vô cùng có khả năng đối mặt chia năm xẻ bảy kết cục..."),i("br"),i("br"),n._v("Nguy hiểm như thế, khôi lỗi tự nhiên không dám liều, kết quả là dẫn đến lực lượng xung đột lẫn nhau, còn chưa ra tay, liền đã bị thương..."),i("br"),i("br"),n._v("Nói đến đơn giản, làm lại là rất khó."),i("br"),i("br"),n._v("Núi ghi chép bên trong khôi lỗi, là vô số tiền bối nghiên cứu không biết bao nhiêu năm tháng, càng là dung hợp rất nhiều danh sư kinh nghiệm chiến đấu, mới hình thành."),i("br"),i("br"),n._v("Nói đến kinh nghiệm chiến đấu, so với thân kinh bách chiến binh sĩ, đều mạnh hơn rất nhiều."),i("br"),i("br"),n._v("Mà còn vạn mảnh lá cây có mọi loại, coi như tương đồng tài liệu, tương đồng Thiên Công sư luyện chế đồng dạng khôi lỗi, thiếu hụt địa điểm cùng bày ra phương thức, cũng là không hoàn toàn giống nhau."),i("br"),i("br"),n._v("Như tình huống như vậy xuống, còn có thể một cái nhìn ra lỗ thủng, đồng thời tìm tới luyện chế bên trong vấn đề... Đây là cái gì nhãn lực? Lại có bao nhiêu mạnh chiến đấu ứng biến kỹ xảo?"),i("br"),i("br"),n._v("“Còn có thể như vậy?”"),i("br"),i("br"),n._v("“Nghe nói qua một chiêu liền đem tầng thứ nhất khôi lỗi đánh bại... Có thể chưa từng nghe qua, liếc mắt nhìn, còn chưa ra tay, bản thân liền thua...”"),i("br"),i("br"),n._v("Hiểu rõ chuyện gì xảy ra, tất cả mọi người nắm tóc."),i("br"),i("br"),n._v("Có thể trở thành danh sư học viện thập đại trường lão, ở đây mỗi một vị, đều sống chẳng được 500 năm, các loại chiến đấu gặp qua không ít, cũng kinh nghiệm không ít."),i("br"),i("br"),n._v("Gặp qua thực lực quá mạnh, tuyệt đối nghiền ép, gặp qua một chiêu, quét ngang vạn quân, không người có thể địch... Nhưng cho tới bây giờ chưa thấy qua, liếc mắt nhìn, kẻ địch liền bản thân trọng thương!"),i("br"),i("br"),n._v("Nhưng bây giờ, sự thật liền xuất hiện ở trước mắt!"),i("br"),i("br"),n._v("Trước đó bọn họ liền vẫn cảm thấy, viện trưởng đi vào núi ghi chép, khẳng định muốn náo ra người khác làm không được sự tình, nhưng nghĩ tới nhiều nhất cũng chính là vượt quan về thời gian vượt qua Mạc Lưu Chân viện trưởng... Hiện tại xem ra, vẫn là nghĩ tới quá đơn thuần..."),i("br"),i("br"),n._v("Người ta qua ải không dùng võ kỹ, không cần chiêu số, chỉ bằng vào “Nhìn” liền có thể hoàn thành... Những người khác, ai còn có thể làm được?"),i("br"),i("br"),n._v("“Đi tầng thứ hai đi...”"),i("br"),i("br"),n._v("Đè nén nội tâm rung động, không tiếp tục để ý vẫn như cũ có chút nghĩ không thông, bị đả kích có chút tuyệt vọng khôi lỗi, Triệu Bính Tuất đám người vội vã hướng tầng thứ hai đi đến."),i("br"),i("br"),n._v("Cùng tầng thứ nhất đồng dạng, phụ trách thủ quan khôi lỗi, không có hình tượng chút nào co quắp ngồi dưới đất, ánh mắt đờ đẫn, tựa như đấu bại gà trống."),i("br"),i("br"),n._v("“Hắn... Nhìn ta hai lần, sau đó ta liền bị thương...”"),i("br"),i("br"),n._v("Khôi lỗi giải thích."),i("br"),i("br"),n._v("Mọi người khóe miệng giật một cái, vội vã đi tới tầng thứ ba."),i("br"),i("br"),n._v("“Hắn nhìn ta ba lần...”"),i("br"),i("br"),n._v("Cái thứ ba khôi lỗi buồn rười rượi."),i("br"),i("br"),n._v("“Tằm Phong cảnh sơ kỳ thực lực, chiến thắng Bán Thánh cấp bậc khôi lỗi, vượt qua một cái đại cấp bậc, thế mà còn là không có ra tay, chỉ nhìn ba lần?”"),i("br"),n._v("Tất cả mọi người đều nhoáng một cái."),i("br"),i("br"),n._v("Bọn họ suy đoán qua Trương sư thực lực rất mạnh, qua ải hết sức dễ dàng, nhưng cho tới bây giờ không nghĩ tới, chỉ nhìn mấy lần, ngay cả qua tam quan, mấu chốt nhất là, còn đả kích tất cả khôi lỗi, lại không có chiến đấu lòng tin."),i("br"),i("br"),n._v("Cái này qua ải trôi qua, cũng không tránh khỏi quá mây trôi nước chảy, quá dễ dàng!"),i("br"),i("br"),n._v("Lúc trước bọn họ xông núi ghi chép, thế nhưng là bị đánh thành đầu heo, suýt chút nữa sắp ngỏm rồi, người ta chỉ nhìn vài lần, khôi lỗi nguyên một đám lại không có tự tin... Chênh lệch có muốn hay không như thế lớn?"),i("br"),i("br"),n._v("“Tầng thứ tư là đi đến Bán Thánh hậu kỳ khôi lỗi, một liên lụy đến ‘Thánh’, mặc kệ là Bán Thánh vẫn là Tòng Thánh, liền hoàn toàn khác nhau, có lẽ... Không phải nhìn ba lần, bồn lần liền có thể giải quyết...”"),i("br"),i("br"),n._v("Thấy bầu không khí ngột ngạt, tất cả mọi người tràn đầy ngột ngạt, Mi trưởng lão nhịn không được nói."),i("br"),i("br"),n._v("Mặc dù mọi người cũng có này cảm giác, nhưng không ai dám mở miệng."),i("br"),i("br"),n._v("Mấy vị đại biểu danh sư học viện, tầm mắt, thực lực đỉnh phong nhất trưởng lão, tại vị viện trưởng này trên người, liền trước đến giờ không có phán đoán chính xác qua, coi như muốn nói, cũng nói không nên lời như thế về sau."),i("br"),i("br"),n._v("“Đi lên xem một chút đi...”"),i("br"),i("br"),n._v("Lắc đầu, biết nghĩ đến nhiều hơn nữa, cũng vô dụng, tất cả trưởng lão đồng loạt hướng núi ghi chép tầng thứ tư đi đến."),i("br"),i("br"),n._v("Nơi này so ba tầng trước càng rộng rãi, là cái phạm vi hơn mười trượng đài cao, một cái cao lớn khôi lỗi, nằm rạp trên mặt đất, không nhúc nhích, đầu lệch trên mặt đất, ánh mắt đờ đẫn, nhìn biểu tình có chút sống không thể yêu."),i("br"),i("br"),n._v("“Viện trưởng sẽ không nhìn ngươi... Bồn lần, liền biến thành như vậy đi?”"),i("br"),i("br"),n._v("Tu Trường Thanh nhịn không được, đi tới trước mặt."),i("br"),i("br"),n._v("Cái tên này cùng trước mấy quan đồng dạng, chẳng lẽ, bị viện trưởng dùng thủ đoạn giống nhau đánh bại?"),i("br"),i("br"),n._v("“Bồn lần?”"),i("br"),i("br"),n._v("Giãy dụa lấy bò lên, khôi lỗi lắc đầu, xấu hổ muốn chui vào kẽ đất: “Không có nhìn nhiều như vậy... Chỉ liếc ta một cái, sau đó nói một câu ‘Nằm xuống’ sau đó, sau đó ta liền thành như vậy...”"),i("br"),i("br"),n._v("“Liếc một cái? Nói câu ‘Nằm xuống’ ?”"),i("br"),i("br"),n._v("Mọi người ngươi nhìn ta, ta nhìn ngươi, tất cả đều cảm thấy lẫn lộn."),i("br"),i("br"),n._v("Vừa rồi nhìn nhiều vài lần, ánh mắt rơi vào chỗ, đều là khôi lỗi thiếu sót cùng chỗ hiểm, để lực lượng không cách nào phát huy, tự động nhận thua... Coi như không nghĩ ra, nhưng cũng có thể hiểu được."),i("br"),i("br"),n._v("Cái này... Liếc một cái, nói nằm xuống, ngươi liền nằm xuống... Cũng quá nghe lời đi!"),i("br"),i("br"),n._v("“Không đúng...”"),i("br"),i("br"),n._v("Những người khác mê hoặc, chỉ có Tưởng Thanh Cầm nghĩ đến cái gì, con ngươi co rụt lại: “Chẳng lẽ... Thanh âm bên trong mang theo ma âm?”"),i("br"),i("br"),n._v("Có thể một câu nói liền để có thể so với Bán Thánh hậu kỳ khôi lỗi, nằm rạp trên mặt đất, loại trừ ma âm, thực sự nghĩ không ra thủ đoạn khác."),i("br"),i("br"),n._v("“Đúng mang theo ma âm, mà còn nhắm thẳng vào chỗ yếu hại của ta... Ta ngăn cản không nổi!”"),i("br"),i("br"),n._v("Khôi lỗi tràn đầy thất lạc."),i("br"),i("br"),n._v("Thực lực của đối phương, cũng không phải là rất mạnh, thậm chí ở trong mắt nó còn có chút thấp, có thể vừa tiến đến, liếc mắt nhìn, trách mắng một câu, nó liền nằm xuống... Trong lòng tràn đầy cảm giác bị thất bại."),i("br"),i("br"),n._v("“Thẳng vào chỗ yếu hại? Ma âm sư mạnh nhất là công kích linh hồn... Ngươi là khôi lỗi, hồn phách một chút cũng không có, làm sao công kích?” Tu Trường Thanh tràn đầy không hiểu."),i("br"),i("br"),n._v("Các trưởng lão khác cũng đều có loại này nghi ngờ."),i("br"),i("br"),n._v("Ma âm sư sở dĩ để cho người ta kiêng kị, là bởi vì bỏ qua vật chất phòng ngự, nhắm thẳng vào linh hồn... Khôi lỗi tuy có linh tính, lại là linh dược, linh thú loại hình đồ vật hội tụ mà thành, không hề đơn độc hồn phách, coi như Tưởng Thanh Cầm thi triển thất tinh cấp bậc ma âm, cũng là đàn gảy tai trâu, làm sao lại một câu nói, liền để hắn nằm trên mặt đất?"),i("br"),i("br"),n._v("“Của hắn ma âm, cũng không phải là công kích linh hồn, mà là để thân thể của ta, sinh ra cộng hưởng... Chỉ cần dám tiếp tục tiến công, liền sẽ trực tiếp nứt ra, biến thành bụi phấn...”"),i("br"),i("br"),n._v("Lắc đầu, khôi lỗi trong mắt mang theo kính sợ cùng sợ hãi."),i("br"),i("br"),n._v("“Cộng hưởng?”"),i("br"),i("br"),n._v("Tất cả trưởng lão khóe miệng co giật."),i("br"),i("br"),n._v("Loại công kích này chân thực tồn tại, đối phó khôi lỗi mười phần hữu hiệu, nhưng mà điều kiện tiên quyết là... Nhất định phải đối luyện chế khôi lỗi tất cả tài liệu, lý giải cực sâu, cùng rèn luyện sau hình thành mới vật chất, biết vô cùng kỹ càng mới được."),i("br"),i("br"),n._v("Nếu không, chuẩn bị nhiều hơn nữa, ma âm lại vang lên sáng lên, không thể đúng bệnh hốt thuốc, cũng là uổng công."),i("br"),i("br"),n._v("Đi tới tầng thứ tư, liếc mắt nhìn khôi lỗi, liền biết cái tên này tài liệu là cái gì, đồng thời dùng dạng gì ma âm đối phó... Có muốn hay không khoa trương như vậy?"),i("br"),i("br"),n._v("Viện trưởng, ngươi thật sự là tới khiêu chiến? Không phải tới du ngoạn, tùy tiện tiêu diệt mấy cái khôi lỗi?"),i("br"),i("br"),n._v("“Tầng tiếp theo khôi lỗi, đã đạt đến Bán Thánh đỉnh phong, mà còn tinh thông kỹ xảo chiến đấu, cùng cấp bậc, liền xem như ta, muốn vượt qua, đều không dễ dàng như vậy, viện trưởng... Có lẽ liền không có dễ dàng như vậy!”"),i("br"),i("br"),n._v("Lại hỏi thăm vài câu, biết rõ ràng tầng này đến cùng xảy ra chuyện gì, Triệu Bính Tuất nói."),i("br"),i("br"),n._v("“Xem một chút đi!”"),i("br"),i("br"),n._v("Biết ở đây suy đoán vô dụng, mấy người tiếp tục hướng phía trước, rất mau tới đến tầng thứ năm."),i("br"),i("br"),n._v("Vẫn như cũ cùng tầng thứ tư đồng dạng, một cái khôi lỗi nằm trên mặt đất, động một cái cũng không thể động, có còn hay không là thỉnh thoảng tính run rẩy."),i("br"),i("br"),n._v("“Chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Đi tới trước mặt, Thiên Công học viện viện trưởng, đem hắn thật vất vả chữa trị, vội vàng hỏi."),i("br"),i("br"),n._v("“Vị kia... Nói với ta hai tiếng ‘Nằm xuống’, lần thứ nhất ta không có nghe, lần thứ hai liền thành như vậy...”"),i("br"),i("br"),n._v("Khôi lỗi nói."),i("br"),i("br"),n._v("“Hai tiếng?”"),i("br"),i("br"),n._v("“Nhìn tới cái này liên quan viện trưởng xác thực không có bên trên một cửa ải trôi qua dễ dàng, trọn vẹn nói hai tiếng, mới để cho hắn nằm xuống...”"),i("br"),i("br"),n._v("“Cửa ải tiếp theo khôi lỗi nắm giữ Tòng Thánh thực lực, viện trưởng chỉ có Tằm Phong cảnh, cũng không thể hô ba tiếng, liền đem nó đánh bại a?”"),i("br"),i("br"),n._v("“Vì cái gì không thể? Cái này mấy tầng nhìn thấy, ngươi chẳng lẽ còn có hoài nghi ư?”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được khôi lỗi nói, tất cả trưởng lão khóe miệng quất loạn, cũng nhịn không được nữa, vội vã hướng tầng thứ sáu đi tới."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-914"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-916"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);