(window.webpackJsonp=window.webpackJsonp||[]).push([[2206],{2560:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-908-buon-buc-khoi-loi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-908-buon-buc-khoi-loi"}},[n._v("#")]),n._v(" Chương 908: Buồn bực khôi lỗi")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 908: Buồn bực khôi lỗi"),t("br"),t("br"),n._v("“Đặc thù, có thể có cái gì đặc thù?”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết khó hiểu."),t("br"),t("br"),n._v("“Nói thật cho ngươi biết đi, tầng thứ tám là cũng không phải là khôi lỗi, mà là lúc trước Mạc Lưu Chân viện trưởng, dùng bản thân xương sườn làm thành một cái phân thân”"),t("br"),t("br"),n._v("Chần chờ một chút, Triệu Bính Tuất nói."),t("br"),t("br"),n._v("“Phân thân?” Vệ Nhiễm Tuyết ngẩn ngơ. “Không sai, năm đó Mạc Lưu Chân viện trưởng đi đến Hóa Phàm cửu trọng đỉnh phong, lần nữa vượt quan, cùng lần thứ nhất chỉ xông đến tầng thứ bảy khác biệt, đến tầng thứ tám, hơn nữa... Còn đánh bại tầng này khôi lỗi, cảm thấy có rất lớn sơ hở, liền tự mình ra tay, lấy ra bản thân một cây xương sườn, mượn nhờ địa tâm tinh thạch, dựa theo mình tới thực lực cùng kỹ xảo chiến đấu, chế tạo lần nữa tầng này thủ hộ giả!”"),t("br"),t("br"),n._v("“Địa tâm tinh thạch? Loại kia địa tâm mới có thể đản sinh trân quý khoáng thạch?”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết cau mày."),t("br"),t("br"),n._v("Loại này tinh thạch, nàng nghe nói qua, là một loại không thể thấy nhiều khoáng thạch, phẩm chất cứng rắn, ẩn chứa cực nóng năng lượng, dùng để rèn luyện thân thể, luyện chế khôi lỗi, có hiệu quả."),t("br"),t("br"),n._v("Thứ này nghe nói tại mấy ngàn năm trước liền bị tiêu hao hầu như không còn, rất khó tìm đến một cái, Mạc Lưu Chân thế mà dùng hắn luyện chế khôi lỗi... Suy nghĩ một chút đều cảm thấy xa xỉ."),t("br"),t("br"),n._v("Coi như nhưng mà xem như tài liệu chính, mạnh mẽ cũng không thể bỏ qua!"),t("br"),t("br"),n._v("E là cho dù là Tòng Thánh đỉnh phong, Thánh vực nhất trọng cường giả cũng có thể thắng được đi."),t("br"),t("br"),n._v("“Không chỉ có như thế, còn đem ý niệm của mình, lưu ở trong đó... Nói cách khác, cửa thứ tám đã không phải là bình thường khôi lỗi, mà là cùng năm đó Mạc Lưu Chân viện trưởng giống nhau như đúc, lại phối hợp không biết đau đớn, hầu như phòng ngự vô địch thân thể, coi như lúc trước Mạc Lưu Chân, đều rất khó vượt qua, huống chi viện trưởng!”"),t("br"),t("br"),n._v("Triệu Bính Tuất tràn đầy cười khổ."),t("br"),t("br"),n._v("Không phải không tin viện trưởng, mà là viện trưởng thực lực bây giờ chỉ có Hóa Phàm cửu trọng sơ kỳ, có chút quá thấp."),t("br"),t("br"),n._v("Mà lưu lại cái này khôi lỗi thời điểm, Mạc Lưu Chân viện trưởng, đã là Hóa Phàm cửu trọng đỉnh phong, khoảng cách Bán Thánh, cũng không xa."),t("br"),t("br"),t("br"),n._v("Đồng dạng là siêu cấp thiên tài, chênh lệch tiếp cận một cái đại cấp bậc, người sau càng là không biết mệt mỏi không biết đau đớn quái vật, khó trách hắn không có một chút lòng tin."),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết nói không ra lời, nếu thật là như vậy, viện trưởng muốn thắng... Quả thực quá khó, thậm chí có thể nói, đã không có khả năng thành công."),t("br"),t("br"),n._v("“Nhiều lời không ích gì, đi lên xem một chút, liền sẽ biết!” Tu Trường Thanh khoát tay áo."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mọi người nhẹ gật đầu, đồng thời hướng tầng thứ tám, đi tới."),t("br"),t("br"),n._v("Tầng này đồng dạng mười phần yên tĩnh, không có bất kỳ cái gì chiến đấu khí tức, có điều, khu vực mấp mô, khắp nơi đều vỡ vụn không chịu nổi, vừa nhìn liền biết, đã trải qua một trận thảm liệt chiến đấu."),t("br"),t("br"),n._v("“Chúng ta tới đã chậm, chiến đấu có lẽ đã sớm kết thúc...” Bốn phía nhìn thoáng qua, Triệu Bính Tuất nói."),t("br"),t("br"),n._v("Làm danh sư, nhãn lực kinh người, có thể nhẹ nhõm nhìn ra trận chiến đấu này, phát sinh ở một ngày trước đó, nói cách khác, một ngày trước chiến đấu cũng đã có kết quả."),t("br"),t("br"),n._v("Vội vàng đi tới phòng ốc ở giữa, tách ra tìm kiếm, cũng không nhìn thấy nửa cái bóng người, cũng không có phát hiện vỡ vụn linh kiện."),t("br"),t("br"),n._v("“Người nào thắng?” Hai mặt nhìn nhau."),t("br"),t("br"),n._v("Nếu như cùng bên trên một tầng đồng dạng, đầy đất mảnh vỡ cùng linh kiện, không cần nghĩ cũng biết, tất nhiên là viện trưởng, thành công đem khôi lỗi đánh bại!"),t("br"),t("br"),n._v("Nhưng bây giờ khu vực sạch sẽ, loại trừ những này chiến đấu dấu vết không có cái gì, chẳng lẽ viện trưởng thua?"),t("br"),t("br"),n._v("Nhưng thật phải thua... Khôi lỗi đâu?"),t("br"),t("br"),n._v("Làm sao cũng không thấy?"),t("br"),t("br"),n._v("“Nơi này có máu!” Đột nhiên, Vệ Nhiễm Tuyết ngừng lại, thần sắc có chút lo lắng."),t("br"),t("br"),n._v("Mọi người đi tới gần, quả nhiên thấy khu vực, lưu lại một vũng máu, rõ ràng bị thương không nhẹ."),t("br"),t("br"),n._v("“Là viện trưởng lưu lại...” Mọi người nắm đấm xiết chặt."),t("br"),t("br"),n._v("Cái này khôi lỗi tuy là Mạc Lưu Chân viện trưởng, dùng xương sườn làm thành phân thân, lại là không có huyết dịch... Chảy đầy đất, nói rõ khẳng định viện trưởng không phải là đối thủ, bị tại chỗ đả thương."),t("br"),t("br"),n._v("Trước bảy tầng qua đều rất đơn giản, tầng này nhưng như thế, muốn thông qua cái này liên quan, quả thực rất khó."),t("br"),t("br"),n._v("“Mặc dù bị thương, nhưng mọi người không cần lo lắng quá mức, tầng này khôi lỗi, nắm giữ Mạc Lưu Chân viện trưởng ý niệm, hẳn là sẽ không đối viện trưởng lạnh lùng hạ sát thủ!”"),t("br"),t("br"),n._v("Thấy mọi người đều có chút lo lắng, Triệu Bính Tuất giải thích."),t("br"),t("br"),n._v("Trước bảy tầng khôi lỗi, đều là bình thường linh tính, chỉ biết là chiến đấu cùng thủ quan, cũng không có tình cảm của nhân loại."),t("br"),t("br"),n._v("Tầng này Mạc Lưu Chân viện trưởng lưu lại ý niệm, tự nhiên có hắn bản tính tư duy, biết Trương sư thân phận về sau, hẳn là sẽ không lạnh lùng hạ sát thủ!"),t("br"),t("br"),n._v("Cứ như vậy, viện trưởng chính là an toàn, chí ít sinh mệnh năng nhận được bảo hộ."),t("br"),t("br"),n._v("“Mau nhìn!”"),t("br"),t("br"),n._v("Đang tại phỏng đoán, không biết viện trưởng đến cùng ở đây đã trải qua cái gì, liền nghe đến thư hoạ học viện viện trưởng một tiếng kinh hô."),t("br"),t("br"),n._v("Vội vàng nhìn lại, chỉ thấy ngón tay hắn chỉ hướng lên phía trên nóc nhà, một mặt ngớ ra."),t("br"),t("br"),n._v("Theo phương hướng nhìn lại, mọi người cũng tất cả đều sửng sốt."),t("br"),t("br"),n._v("Chỉ thấy nóc phòng trên vách tường, một bóng người kề sát ở phía trên, thật sâu rơi vào trong đó, đập ra một cái “Đại” chữ, hai mắt nhắm nghiền, hẳn là đã đã hôn mê."),t("br"),t("br"),n._v("“Là Mạc Lưu Chân viện trưởng luyện chế thành khôi lỗi...”"),t("br"),n._v("Triệu Bính Tuất nhận ra được, vội vàng nói."),t("br"),t("br"),n._v("Biết là ai, tất cả trưởng lão không dám do dự, toàn bộ bay đến không trung, đem hắn theo trên tường đào lên."),t("br"),t("br"),n._v("Chung Đỉnh Thuần cùng Thiên Công viện viện trưởng, đi tới gần, hỗ trợ điều trị, một lát sau, chậm rãi tỉnh lại."),t("br"),t("br"),n._v("Nhìn thấy mọi người, khôi lỗi tức giận sắc mặt đỏ lên, chửi ầm lên: “Đáng giận... Trước đó đến vượt quan cái kia vô sỉ gia hỏa đến cùng là ai?”"),t("br"),t("br"),n._v("“Vô sỉ gia hỏa?”"),t("br"),t("br"),n._v("Mọi người nghe được Mạc Lưu Chân viện trưởng ý niệm, như thế đánh giá viện trưởng, tất cả đều khóe miệng giật một cái."),t("br"),t("br"),n._v("Căn cứ lịch sử ghi chép, vị viện trưởng này, thiên phú vô song, tính khí cùng bản tính, lại hết sức trầm ổn, nguyên nhân chính là như thế, mới có thể bị hậu thế vô số học viên sùng bái, tên lưu truyền thiên cổ."),t("br"),t("br"),n._v("Mệnh danh vạn năm qua, học viện danh xứng với thực người thứ nhất!"),t("br"),t("br"),n._v("Tại sao như vậy trầm ổn người, một chút phong độ đều không có, ngược lại chửi ầm lên, kích động như thế?"),t("br"),t("br"),n._v("“Cái tên này cùng ta giao đấu, căn bản không phải đối thủ!”"),t("br"),t("br"),n._v("Mạc Lưu Chân viện trưởng lưu lại khôi lỗi, một mặt tức giận: “Đánh mấy chiêu, ta biết tiếp tục đánh xuống, hắn khẳng định không cách nào chiến thắng, liền để hắn thức thời rời đi... Ai ngờ cái tên này lại hết sức nhiệt tình nói đối ta rất sùng bái, muốn ta tay nắm tay truyền thụ cho hắn võ kỹ, càng là nắm tay của ta, đủ loại ca ngợi chi từ liên tục không ngừng...”"),t("br"),t("br"),n._v("Khôi lỗi khắp khuôn mặt là hối hận, trong mắt đều là trúng kế thần sắc: “Nghe hắn nói như vậy, thân là danh sư tự nhiên việc nhân đức không nhường ai, thuận miệng chỉ chọn hắn vài câu...”"),t("br"),t("br"),n._v("“Ai ngờ cái tên này lôi kéo tay của ta bất luận thế nào không thả, không phải nói muốn tiếp xúc nhiều thần tượng... Ta nhất thời không sai, trúng quỷ kế, vốn cho rằng thật là đối ta sùng bái, ai ngờ, ai ngờ...”"),t("br"),t("br"),n._v("Khôi lỗi càng nói càng tức, cả người đều nhanh muốn nổ tung."),t("br"),t("br"),n._v("“Ai ngờ làm sao vậy?”"),t("br"),t("br"),n._v("Thấy hắn nói đến một nửa, Vệ Nhiễm Tuyết có chút nhịn không được."),t("br"),t("br"),n._v("“Ai ngờ cái này vô sỉ gia hỏa, căn bản không phải cái gì sùng bái, càng không phải là muốn cho giáo ta hắn, mà là... Mượn nhờ thân thể ta bên trong địa tâm tinh thạch tiến hành tu luyện!”"),t("br"),t("br"),n._v("“Các ngươi có lẽ đó có thể thấy được, ta là địa tâm tinh thạch luyện chế mà thành... Nếu như cái tên này điên cuồng hấp thu, tất nhiên có thể phát giác, nhưng hắn... Từng tia điều lấy lực lượng, hơn nữa ngoài miệng nói cho dễ nghe, ta hoàn toàn không có phát giác, hầu như đem trong cơ thể tinh thạch đại bộ phận năng lượng đều bị thôn phệ...”"),t("br"),t("br"),n._v("Khôi lỗi coi như dung hợp nhân loại ý niệm, cũng chỉ là khôi lỗi, đối thân thể khống chế không nhạy cảm như vậy."),t("br"),t("br"),n._v("Giống như Vô Hồn Kim Nhân, nhưng mà cái có thể đi lại, thi triển lực lượng thân thể mà thôi, không chiến đấu, căn bản không biết thân thể xảy ra chuyện gì."),t("br"),t("br"),n._v("“Không có tinh thạch, nhiều nhất phòng ngự của ta kém một chút, có thể... Cái tên này không biết chuyện gì xảy ra, thân thể lực lượng nhưng trong nháy mắt tăng nhiều, lần nữa chiến đấu, một quyền liền đem ta đánh bay... Không có phòng ngự, thực lực đối phương lại mạnh... Cho nên, ta liền bị tại chỗ chấn choáng, khảm nạm đến nóc nhà...”"),t("br"),t("br"),n._v("Khôi lỗi nổi giận đùng đùng đem chuyện đã xảy ra nói một lần."),t("br"),t("br"),n._v("Sớm biết cái tên này là muốn trộm hắn địa tâm tinh thạch tu luyện, đánh chết cũng sẽ không tin vào đối phương ah! Cái này được rồi, phòng ngự hạ thấp, sức chiến đấu yếu bớt không nói... Bị đánh đến như là một bãi bùn nhão."),t("br"),t("br"),n._v("Tu luyện một đời, thấy qua vô số danh sư, trước đến giờ chưa thấy qua vô sỉ như vậy!"),t("br"),t("br"),n._v("Càng nghĩ càng thấy đến uất ức, nhìn thấy tất cả trưởng lão, mới nhịn không được phát ra bực tức."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe xong chuyện gì xảy ra, tất cả trưởng lão ngươi nhìn ta, ta nhìn ngươi, tất cả đều nguyên một đám da mặt giật giật."),t("br"),t("br"),n._v("Xem ra viện trưởng vừa mới bắt đầu quả thực không phải là đối thủ, về sau, tiếp xúc đối phương trong cơ thể địa tâm tinh thạch, tu luyện cái gì võ kỹ, lúc này mới thực lực đại tiến, thành công đem hắn đánh bại..."),t("br"),t("br"),n._v("Làm như vậy, trong chiến đấu, không tính là gì, dù sao sống chết đều có thể vứt bỏ, có thể dùng để vượt quan... Quả thực không quá nói."),t("br"),t("br"),n._v("Nhưng nếu là nói thẳng, mượn nhờ đối phương địa tâm tinh thạch, đoán chừng không những không cho, còn sẽ ra tay tại chỗ đem hắn đánh đánh chết tươi."),t("br"),t("br"),n._v("“Được rồi, chiến đấu bản thân liền là ngươi sống ta chết, không cho phép khiêm nhượng, hắn có thể làm như vậy, nói rõ cũng không phải là khô khan cố chấp người, loại người này bình thường sẽ không bám vào một khuôn mẫu, đi được càng xa!”"),t("br"),t("br"),n._v("Đem trong lòng uất ức nói ra, khôi lỗi tâm tình tốt hơn nhiều, khoát tay áo."),t("br"),t("br"),n._v("Danh sư, mặc dù làm gương sáng cho người khác, coi trọng thân phận, nhưng cũng không thể quá mức khô khan, nếu không, như thế nào sửa cũ thành mới, càng đi càng xa?"),t("br"),t("br"),n._v("Không nói cái khác, coi như lúc trước Khổng sư, cũng không phải cố chấp hạng người, ngược lại sẽ biết nghe lời phải, năm đó ba lần Địa quật, ngụy trang thành Dị Linh tộc nhân, lẫn vào trong đó, cuối cùng thậm chí bị phong vương, phong Hoàng, suýt chút nữa làm bọn họ diệt tộc..."),t("br"),t("br"),n._v("Danh sư, nếu là tự lo thân phận, nhất vị khô khan, tuân theo tiền nhân ý niệm, không có sáng tạo, không có to gan nếm thử, nhân tộc cũng sẽ không ngắn ngủi mấy vạn năm, liền phát triển huy hoàng như vậy."),t("br"),t("br"),n._v("“Tiền bối có thể nghĩ thông suốt là tốt rồi...”"),t("br"),t("br"),n._v("Nghe được hắn nói như vậy, Triệu Bính Tuất đám người thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Vị này ẩn chứa Mạc Lưu Chân viện trưởng ý niệm, trước một cái càng là bọn họ viện trưởng, Thần Tiên đánh nhau, phàm nhân gặp nạn, giúp ai nói chuyện đều không thích hợp, hắn có thể tự mình lý giải, vừa vặn tỉnh nhóm người mình khuyên bảo."),t("br"),t("br"),n._v("“Tên kia đem ta đánh bại, khẳng định là đi vào tầng thứ chín, tầng thứ chín, là cấm địa, các ngươi coi như cầm trong tay Trưởng Lão lệnh, cũng không cách nào đi vào, vẫn là ở lại chỗ này đi!”"),t("br"),t("br"),n._v("Gật gật đầu, khôi lỗi đứng dậy, hướng bên trên nhìn một chút, nói."),t("br"),t("br"),n._v("Tầng thứ chín, cho dù có Trưởng Lão lệnh, cũng không cách nào đi vào, chỉ có chỗ dựa chiến đấu chân chính lực, xông qua thủ quan người, mới có thể thành công..."),t("br"),t("br"),n._v("Coi như lúc trước hắn, đều không làm được."),t("br"),t("br"),n._v("“Đúng vậy a!” Triệu Bính Tuất đám người gật đầu, quy tắc bọn hắn cũng đều biết, vốn cho rằng đến tầng này liền có thể tìm tới viện trưởng, hiện tại xem ra, viện trưởng so với bọn hắn trong tưởng tượng đi còn xa."),t("br"),t("br"),n._v("“Không đúng... Tiền bối, thực lực của ngươi, giống như cũng không là Tòng Thánh đỉnh phong...”"),t("br"),t("br"),n._v("Mọi người đang tại cảm khái, Thiên Công học viện viện trưởng nhìn ra cái gì, đột nhiên mở miệng."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-916"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-918"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);