(window.webpackJsonp=window.webpackJsonp||[]).push([[2209],{2565:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-83-hung-hang-chu-hong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-83-hung-hang-chu-hong"}},[n._v("#")]),n._v(" Chương 83: Hung hăng Chu Hồng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Trương lão sư không có sao chứ!” "),t("br"),t("br"),n._v(" Lớp học bên trong, Triệu Nhã, Vương Dĩnh mấy người trong mắt mang theo lo lắng."),t("br"),t("br"),n._v(" Tào Hùng lão sư xin học tâm tra hỏi, đối phó Trương lão sư, chuyện này tuy rằng không làm đến sôi sùng sục lên, nhưng lấy tư cách học sinh, vẫn là nghe đến một chút tin tức."),t("br"),t("br"),n._v(" “Yên tâm đi, Trương lão sư khóa chúng ta đều nghe xong, khâm phục không thôi, lưu truyền đi khẳng định cũng đã sớm chịu phục, không thể xảy ra vấn đề!” Trịnh Dương nói."),t("br"),t("br"),n._v(" Hắn kỳ thực cũng coi như năm học sinh bên trong, không phục nhất Trương lão sư, nhưng đi qua hai tiết khóa ở chung, dĩ nhiên phục sát đất, coi như hiện tại Vương Siêu muốn thu hắn, đều sẽ trực tiếp từ chối."),t("br"),t("br"),n._v(" Chính mình cũng như vậy, lưu truyền đi khẳng định cũng gần như."),t("br"),t("br"),n._v(" Loại này độ tín nhiệm, làm sao có khả năng thất bại?"),t("br"),t("br"),n._v(" Ầm!"),t("br"),t("br"),n._v(" Đang đang nói chuyện, gian phòng cửa sắt bị người từ bên ngoài đá văng, tiếp theo một người thiếu niên tràn đầy tự kiêu đi vào."),t("br"),t("br"),n._v(" “Các ngươi kính yêu Trương Huyền lão sư đâu? Để hắn đi ra!” "),t("br"),t("br"),n._v(" Hai tay chắp ở sau lưng, thiếu niên mí mắt nhấc lên."),t("br"),t("br"),n._v(" Nói tới Trương Huyền lão sư, hắn cố ý nhấn mạnh, trên nét mặt không những không có một chút nào tôn trọng, còn mang theo cười nhạo mùi vị."),t("br"),t("br"),n._v(" “Ngươi là ai? Nơi này không hoan nghênh ngươi, kính xin rời đi!” Thấy đối phương nói tới Trương lão sư, không có một chút nào cung kính, còn đạp cửa đi vào, Trịnh Dương sắc mặt khó coi."),t("br"),t("br"),n._v(" “Trịnh Dương, không muốn lỗ mãng, hắn là nhập học sát hạch xếp hạng thứ bốn Chu Hồng!” "),t("br"),t("br"),n._v(" Nhận ra thiếu niên này, Viên Đào mập mạp thân thể liên tục lay động, kéo Trịnh Dương nhỏ giọng."),t("br"),t("br"),n._v(" “Chu Hồng?” "),t("br"),t("br"),n._v(" Không riêng Trịnh Dương, liền ngay cả Triệu Nhã mấy người cũng không nhịn được sầm mặt lại."),t("br"),t("br"),n._v(" Danh tự này bọn họ trước đều nghe nói qua, thực lực từ lâu đạt được Tụ Tức cảnh đỉnh phong, bất cứ lúc nào cũng sẽ đột phá, mạnh mẽ đến cực điểm."),t("br"),t("br"),n._v(" Có thể tại nhập học sát hạch bên trong xếp tới thứ tư, nói rõ bất luận thực lực vẫn là thiên phú, đều xa không phải bọn họ có thể so sánh."),t("br"),t("br"),n._v(" “Có thể nhận ra ta, vẫn tính có chút kiến thức, đây là chúng ta Lục Tầm lão sư hạ chiến thiếp, muốn tại nửa tháng phía sau tân sinh thi đấu bên trên, cùng hắn khiêu chiến sư giả bình trắc, các ngươi ai thay hắn nhận?” "),t("br"),t("br"),n._v(" Thấy mọi người nhận ra bản thân, khóe miệng giương lên, thiếu niên dưới mắt không còn ai, một bộ cao cao tại thượng dáng dấp."),t("br"),t("br"),n._v(" Vị này chính là theo Lục Tầm lớp học đi tới Chu Hồng."),t("br"),t("br"),n._v(" Trương Huyền hắn đã sớm nghe nói qua, giáo viên sát hạch được không điểm kém cỏi nhất lão sư mà thôi, làm như thiên chi kiêu tử, Lục Tầm môn sinh đắc ý, tự nhiên có chút không lọt mắt."),t("br"),t("br"),n._v(" “Sư giả bình trắc? Lục Tầm lão sư?” "),t("br"),t("br"),n._v(" Mọi người sắc mặt biến đổi."),t("br"),t("br"),n._v(" Sư giả bình trắc, chủ thể tuy rằng khiến cho bọn họ, nhưng là lão sư dạy học trình độ so với, quan hệ giáo sư thể diện, rất ít người cử hành, Lục Tầm học viện tiếng tăm to lớn nhất lão sư, môn hạ học sinh, cao thủ như mây, làm sao sẽ khiêu chiến Trương lão sư?"),t("br"),t("br"),n._v(" “Không sai, Trương Huyền sau lưng làm mờ ám, trêu đến Lục lão sư không cao hứng, dự định giáo huấn hắn một trận, chiến thiếp ta thả xuống, các ngươi một lúc nữa đưa cho hắn, nếu như không dám nhận, kịp lúc chịu thua, mình tới Lục lão sư lớp học xin lỗi, không phải vậy, sẽ chờ mất mặt xấu hổ đi!” "),t("br"),t("br"),n._v(" Vung tay lên, Chu Hồng đem chiến thiếp tiện tay vứt tại trên bàn, xoay người liền muốn rời khỏi."),t("br"),t("br"),n._v(" “Chậm đã!” "),t("br"),t("br"),n._v(" Còn chưa đi ra đại môn, Trịnh Dương chặn ở mặt trước: “Ngươi đá văng chúng ta lớp học đại môn, xưng hô Trương lão sư mở miệng không có một chút nào cung kính, hiện tại nhất định phải xin lỗi, bằng không, đừng trách ta không khách khí!” "),t("br"),t("br"),n._v(" Làm như học sinh, muốn bảo vệ lão sư tôn nghiêm, cái tên này không có một chút nào cung kính, vứt xuống chiến thiếp liền muốn đi, sao có thể mặc cho hắn như vậy tùy ý."),t("br"),t("br"),n._v(" “Muốn cho ta xin lỗi? Muốn xem ngươi có bản lãnh này hay không!” "),t("br"),t("br"),n._v(" Một tiếng cười gằn, Chu Hồng xem thường nhìn sang, một chân đá ra."),t("br"),t("br"),n._v(" Trịnh Dương thương pháp lợi hại, quyền cước bên trên bình thường, lại thêm lên thực lực kém xa đối phương, còn không phản ứng lại, liền bị đá trúng ngực, bay ngược ra ngoài, trùng điệp ngã xuống đất."),t("br"),t("br"),n._v(" “Lớn mật!” "),t("br"),t("br"),n._v(" Triệu Nhã cũng tức giận sắc mặt khó coi, một tiếng khẽ kêu, vọt tới."),t("br"),n._v("  Nàng cùng Chu Hồng đều là Tụ Tức cảnh đỉnh phong, bất quá, sức chiến đấu so với đối phương hơi có không bằng, liên tục mấy chiêu, tương tự bị đánh trúng vai, liên tiếp lui về phía sau."),t("br"),t("br"),n._v(" Tiếp theo Vương Dĩnh, Viên Đào cũng xông lại, liền trong bọn họ thực lực mạnh nhất Triệu Nhã đều không phải là đối thủ, lại làm sao có khả năng chống đỡ được."),t("br"),t("br"),n._v(" Chỉ chốc lát, bốn người liền đều chịu chút tổn thương, từng cái từng cái đầy mặt phẫn nộ, lại không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v(" Bọn họ tuy rằng cùng Trương Huyền học không ít cao thâm lý luận, nhưng thời gian tu luyện quá ngắn, căn bản không phải vị này nhập học kiểm tra người thứ bốn đối thủ."),t("br"),t("br"),n._v(" “Một đám rác rưởi!” "),t("br"),t("br"),n._v(" Đánh bại mọi người, Chu Hồng quăng một cái ống tay áo: “Đây chỉ là trừng phạt nho nhỏ! Dám khiêu chiến chúng ta Lục lão sư, liền muốn nghĩ đến kết quả này quay đầu lại nói cho Trương Huyền lão sư, không dám đáp ứng cũng sắp chút nhận sai, bằng không” "),t("br"),t("br"),n._v(" “Bằng không cái gì?” "),t("br"),t("br"),n._v(" Đang muốn nói hết lời, liền nghe đến một cái thanh âm nhàn nhạt theo bên ngoài phòng truyền vào."),t("br"),t("br"),n._v(" Trương Huyền mang theo lưu truyền đi, đi vào."),t("br"),t("br"),n._v(" “Bằng không” "),t("br"),t("br"),n._v(" Mặc kệ trước mắt cái này Trương Huyền có phải là rác rưởi, dù sao cũng là lão sư, có sư đạo tôn nghiêm, thấy hắn nhìn sang, tuy rằng không có nổi giận, Chu Hồng vẫn là rụt cổ một cái, cắn răng hừ nói: “Bằng không sẽ chờ sư giả bình trắc thua trận đi!” "),t("br"),t("br"),n._v(" “Là thua là đánh bạc, còn chưa dùng tới ngươi đến bình luận! Trở lại nói cho Lục Tầm, chiến thiếp ta nhận!” "),t("br"),t("br"),n._v(" Trương Huyền quăng một cái ống tay áo."),t("br"),t("br"),n._v(" Kỳ thực hắn có chút buồn bực."),t("br"),t("br"),n._v(" Thật vất vả đem Hoàng Ngữ, Bạch Tốn lừa đi, dự định trở về nói tiếp một hồi khóa, không nghĩ tới còn không vào nhà liền nghe đến cái tên này gầm rú."),t("br"),t("br"),n._v(" Lục Tầm khiêu chiến bản thân"),t("br"),t("br"),n._v(" Ngươi mấy trăm học sinh, liền một cái Vương Nham phải cho ta làm dự thính, không đến mức trở mặt đi!"),t("br"),t("br"),n._v(" Hơn nữa coi như để học sinh tiễn đưa chiến thiếp lại đây, xấu dễ tìm cái đáng tin, cái tên này, người không lớn còn còn càn rỡ không được nếu không là tự tin thân phận, từ lâu một cái tát hô chết rồi!"),t("br"),t("br"),n._v(" Còn có thể ở lại chỗ này diễu võ dương oai?"),t("br"),t("br"),n._v(" “Như vậy tốt nhất, cáo từ!” Chu Hồng liền muốn rời khỏi."),t("br"),t("br"),n._v(" “Đừng đi!” Trịnh Dương giẫy giụa về phía trước, sốt ruột nhìn sang: “Trương lão sư, hắn mở miệng ngạo mạn, đánh hỏng chúng ta đại môn, càng động thủ đả thương chúng ta, nếu như như vậy thả hắn rời đi, sau đó ai còn không đến bặt nạt” "),t("br"),t("br"),n._v(" “Làm sao? Vừa nãy ta đánh được không đủ? Hừ! Không đủ thực lực, khẩu khí còn rất lớn, đồ điếc không sợ súng!” "),t("br"),t("br"),n._v(" Ngước đầu đầu lâu xem thường lướt qua Trịnh Dương, Triệu Nhã mấy người một chút, Chu Hồng cười gằn: “Liền các ngươi cái chỗ chết tiệt này, tựu cái này mấy học sinh, môn có muốn hay không cũng không đáng kể, các ngươi còn tưởng rằng có người sẽ đến trộm khóa? Đừng nằm mơ! Còn có, muốn giữ lại ta muốn xem bản lĩnh, ngày hôm nay ta liền đứng ở chỗ này, mấy người các ngươi nếu như có thể lưu lại, liền động thủ đi!” "),t("br"),t("br"),n._v(" Nói đến đây, dừng lại một chút, ôm quyền nhìn về phía Trương Huyền: “Trương lão sư, ngươi thân là giáo sư, còn không đến mức hạ mình đối với ta một học sinh ra tay đi!” "),t("br"),t("br"),n._v(" Lão sư có lão sư tôn nghiêm cùng thân phận, thật ra tay với học sinh, sẽ mất mặt xấu hổ, bị người phỉ nhổ."),t("br"),t("br"),n._v(" Cũng chính là cái gọi là binh đối với binh tướng đối tướng, một cái Tướng quân coi như đánh thắng một người lính, cũng không ló mặt a."),t("br"),t("br"),n._v(" Hắn tính chính xác Trương Huyền sẽ không xuất thủ, còn lại mấy cái học viên, không một cái có thể đánh thắng được hắn, không uý kỵ tí nào."),t("br"),t("br"),n._v(" “Trương lão sư” "),t("br"),t("br"),n._v(" Nhìn thấy hắn lớn lối như thế, Triệu Nhã, Trịnh Dương mấy người tức giận sắc mặt trở nên trắng, từng cái từng cái nắm đấm xiết chặt, hận không thể vọt thẳng quá khứ."),t("br"),t("br"),n._v(" Bất quá, bọn họ cũng biết, xác thực không phải cái này Chu Hồng đối thủ, vừa nãy đã thất bại thảm hại, động thủ nữa, chỉ có thể càng khiến người ta xem thường."),t("br"),t("br"),n._v(" “Các ngươi muốn dạy dỗ hắn?” "),t("br"),t("br"),n._v(" Không để ý tới cái này tự cho là đúng gia hỏa, Trương Huyền nhìn sang."),t("br"),t("br"),n._v(" “Vâng!” "),t("br"),t("br"),n._v(" Mấy người đồng thời gật đầu."),t("br"),t("br"),n._v(" Thấy vẻ mặt của bọn họ, Trương Huyền tùy ý khoát tay áo một cái, vẻ mặt thành thật: “Bất kể nói thế nào đều là Lục lão sư học sinh, đánh chết không tốt bàn giao! Như vậy đi, Viên Đào thực lực ngươi thấp nhất, cũng nhất có chừng mực, đi qua đem hắn đánh thành đầu heo, thuận tiện để hắn đem cửa bồi thêm là được, đừng quá tàn nhẫn! Mặt khác, lúc tỷ đấu nhất định phải công bằng, công chính, chút đến mới thôi! Không cần tổn thương nữa hòa khí!” "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-91"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-93"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);