(window.webpackJsonp=window.webpackJsonp||[]).push([[2211],{2564:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-912-nguyen-thai-phan-cap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-912-nguyen-thai-phan-cap"}},[n._v("#")]),n._v(" Chương 912: Nguyên thai phân cấp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 912: Nguyên thai phân cấp"),t("br"),t("br"),n._v("Trong nháy mắt, Trương Huyền liền đối quyển sách này nội dung, rõ rõ ràng ràng."),t("br"),t("br"),n._v("Phía trước kỹ càng giới thiệu, Khổng sư phong Thánh đi qua, trong đó đại bộ phận kinh nghiệm, đều cùng Phong Thánh đài lưu lại tâm đắc có chút tương tự, chỉ là càng thêm kỹ càng, không chỉ có cảm thụ, còn có các loại trong hiện thực hành động."),t("br"),t("br"),n._v("“Phức tạp như vậy?”"),t("br"),t("br"),n._v("Trương Huyền lông mày dần dần nhăn lại."),t("br"),t("br"),n._v("Vốn cho rằng cái gọi là Phong Thánh giải, là một loại đơn thuần công pháp, chỉ cần theo trình tự tu luyện, liền có thể để thực lực tăng nhiều, trên thực tế cũng không có dễ dàng như vậy."),t("br"),t("br"),n._v("Để ông trời phong làm Thánh Nhân, không chỉ muốn chân khí mạnh, lực lượng mãnh liệt, càng quan trọng hơn là... Liền tâm thần cùng một chỗ tu luyện."),t("br"),t("br"),n._v("Cái gọi là tu luyện tâm thần, trên thực tế chính là rèn luyện tâm trí."),t("br"),t("br"),n._v("Loại này tu hành, không thể cho người tăng lên sức chiến đấu, nhưng có thể khiến người ta lòng dạ càng rộng rãi, xem càng xa, đi cao hơn."),t("br"),t("br"),n._v("Chỉ có tâm trí đi đến bao dung vạn vật, dung nạp thiên địa thời điểm, đan điền tự nhiên cũng sẽ tùy theo trở nên rộng lớn, có thể dung nạp càng nhiều chân khí."),t("br"),t("br"),n._v("“Tằm Phong cảnh, cũng gọi Tàng Thai cảnh, Thai Tàng cảnh hoặc là Kim Đan cảnh. Ý là tích trữ tu vi, tại thể nội ngưng tụ nguyên thai (Kim Đan), một khi thành công, cùng bản thân dung hợp làm một, liền có thể đột phá ràng buộc, thành linh thành Thánh...”"),t("br"),t("br"),n._v("Thấy hắn lần nữa mặc vào trước đó quần áo, nghiêm túc đọc sách, Mục sư khôi lỗi lại không xoắn xuýt, giải thích một câu, hỏi: “Ngươi đã tu luyện đến cấp độ này, chắc hẳn đối cái này cảnh có rất sâu lý giải.”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tằm Phong cảnh, trên thực tế là đem chân khí toàn thân, ngưng tụ thành thai, hóa mà thành đan, nói trắng ra là, là đem chân khí loại khí thể này hình dạng đồ vật, ngưng tụ thành thể rắn."),t("br"),t("br"),n._v("Như vậy, thân thể liền có thể dung nạp càng nhiều chân khí, một khi kết thành nguyên thai, hoàn toàn có thể tàng thai thành Thánh, đi đến cảnh giới càng cao hơn."),t("br"),t("br"),n._v("Mặc dù không có sưu tập đến vượt qua Tằm Phong cảnh cao hơn Thiên Đạo công pháp, nhưng chân khí trong cơ thể hắn đã bắt đầu hướng ở giữa hội tụ, tại đan điền tạo thành chân khí vòng xoáy, chỉ phải cố gắng tu luyện, ngưng kết ra nguyên thai, chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("“Bình thường tu luyện, chỉ cần dần dần từng bước, cố gắng tu luyện, trong cơ thể tại chân khí, rất nhanh liền có thể ngưng kết, nhưng... Đây chẳng qua là kém nhất một loại nguyên thai, cũng chính là cái gọi là tam đẳng nguyên thai!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi nói."),t("br"),t("br"),n._v("“Tam đẳng nguyên thai? Nguyên thai còn phân cấp cấp?” Trương Huyền mặt mũi nghi ngờ."),t("br"),t("br"),n._v("Đây chính là chưa từng nghe nói qua."),t("br"),t("br"),n._v("Coi như tại học viện sưu tập không ít nghề nghiệp thư tịch, đều chưa hề thấy qua tương tự giới thiệu."),t("br"),t("br"),n._v("“Đương nhiên sẽ phân chia đẳng cấp, chỉ bất quá, rất ít người biết thôi!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi lắc đầu: “Đẳng cấp này, là lúc trước Khổng sư một vị học sinh nói ra, vốn định báo cho tất cả tu luyện giả, lại bị Khổng sư ngăn lại! Giữ kín không nói ra, đến mức người biết càng ngày càng ít!”"),t("br"),t("br"),n._v("“Khổng sư ngăn lại?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Khổng sư không phải vạn thế chi sư ư? Đối nhân tộc có cống hiến to lớn, sáng lập danh sư chức nghiệp... Đem truyền thừa phát triển tại toàn bộ nhân tộc, nhân vật như vậy, làm sao lại cố ý che lấp tin tức?"),t("br"),t("br"),n._v("“Khổng sư làm như vậy, là vì nhân loại!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi cảm khái một tiếng, cũng không để Trương Huyền nghi ngờ bao lâu, tiếp tục nói: “Chúng ta bình thường tu luyện giả, chỉ cần chịu cố gắng, cơ bản đều có thể ngưng tụ ra tam đẳng nguyên thai, nhị đẳng nguyên thai liền khó khăn! Không chỉ cần đại lượng tu luyện tài liệu, càng cần hơn có người chỉ điểm, xuất hiện một tia sai lầm đều sẽ dẫn đến thất bại trong gang tấc.”"),t("br"),t("br"),n._v("“Nói cách khác, muốn ngưng tụ nhị đẳng nguyên thai, tốn hao đánh đổi, ít nhất là tam đẳng nguyên thai hơn gấp mười lần... Cái giá như thế này, nhân tộc xúi trả không nổi không nói! Mấu chốt nhất là, cũng không phải là người người đều có thể thành công, còn cần cực cao thiên tư cùng tâm tính, một khi thất bại, nhẹ thì nguyên thai hủy hết, tu vi bị phế, nặng thì, tại chỗ bỏ mình, Thần Tiên khó cứu...”"),t("br"),t("br"),n._v("Mục sư khôi lỗi lắc đầu: “Đẳng cấp tin tức, không biết ngược lại cũng thôi, một khi biết, vô số danh sư đều sẽ vì tăng lên sức chiến đấu, mạo hiểm nếm thử... Mà hậu quả của việc làm như vậy, sẽ chỉ tăng thêm nhân tộc gánh nặng, để vô số người tẩu hỏa nhập ma, cuối cùng tử vong! Mà không phải bồi dưỡng được càng rất mạnh hơn người.”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền cuối cùng nhẹ gật đầu."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Muốn xung kích cấp bậc cao hơn nguyên thai, tự nhiên muốn trả giá đắt!"),t("br"),t("br"),n._v("Rất nhiều người bị giới hạn thiên tư, cưỡng ép đi làm, chẳng những không có hiệu quả, ngược lại sẽ hại hắn."),t("br"),t("br"),n._v("Thật giống như rắn nuốt voi, rõ ràng chỉ có dây thừng lớn nhỏ, nhưng không phải muốn ăn một đầu mấy thước voi, không thành công ngược lại cũng thôi, làm không cẩn thận rất dễ dàng đem bản thân tươi sống đùa chơi chết."),t("br"),t("br"),n._v("Cái gọi là nguyên thai đẳng cấp đã là như thế."),t("br"),t("br"),n._v("Biết tin tức này về sau, không để cho đi làm, rõ ràng còn có một loại cảnh giới không có đi đến, trên tâm cảnh sẽ xuất hiện thiếu hụt, tự giác không bằng người... Từ đó hạn chế sau này thành tựu."),t("br"),t("br"),n._v("Biết lại sẽ xuất hiện nguy hiểm..."),t("br"),t("br"),n._v("đọc ngantruyen.com/"),t("br"),n._v("Người không biết không tội, có đôi khi vô tri, cũng là một niềm hạnh phúc."),t("br"),n._v(" “Nguyên thai đẳng cấp cao, chỉ đại biểu cùng cấp bậc sức chiến đấu mạnh mà thôi... Mà tốn hao đánh đổi, có thể đủ bồi dưỡng được Thánh vực nhị trọng, thậm chí tam trọng cường giả! Như vậy đánh đổi, chỉ làm cho người tăng lên nguyên thai một cái tiểu cấp bậc... Đổi lại ai, đều hẳn phải biết như thế nào lấy hay bỏ!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi tiếp tục nói: “Nhất là Khổng sư loại này là nhân tộc tương lai suy nghĩ vạn thế chi sư! Lại nói, danh sư chức nghiệp chủ yếu nhất năng lực, là bồi dưỡng càng nhiều nhân loại đi đến đỉnh phong, dạy học mới là đệ nhất, cùng cấp bậc sức chiến đấu, coi như yếu một ít, cũng không ảnh hưởng toàn cục.”"),t("br"),t("br"),n._v("“Đúng vậy a!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Bí mật này thực sự quá kinh thế hãi tục, một khi truyền đi, tất nhiên phá vỡ tu luyện giới, mà làm như vậy, loại trừ sẽ cho người mang đến áp lực tâm lý cùng gánh nặng... Cũng không có bất kỳ cái gì chỗ tốt."),t("br"),t("br"),n._v("Cùng hắn như vậy, còn không bằng không nói, làm cho tất cả mọi người cũng không biết chuyện này."),t("br"),t("br"),n._v("Huống chi, danh sư chủ yếu nghề nghiệp là “Dạy” cùng “Truyền thừa”, cũng không phải đánh nhau, liền xem như tam đẳng nguyên thai cũng không tính là gì."),t("br"),t("br"),n._v("Y theo đối công pháp, võ kỹ lý giải, tất cả chức nghiệp, vẫn như cũ là việc nhân đức không nhường ai đệ nhất, không có người có thể so sánh."),t("br"),t("br"),n._v("“Ngươi nói bình thường tu luyện giả đều chỉ là tam đẳng nguyên thai, cái kia... Nhị đẳng nguyên thai người là dạng gì?” Hiểu mục đích của đối phương, Trương Huyền lại không xoắn xuýt, tiếp tục hỏi."),t("br"),t("br"),n._v("“Nhị đẳng nguyên thai, đại bộ phận đều là Chiến sư!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi nói."),t("br"),t("br"),n._v("“Chiến sư?”"),t("br"),t("br"),n._v("“Ừm! Chiến sư không cần học tập phụ tu, chuyên tâm chiến đấu, sẽ có càng nhiều tinh lực dùng tại võ học trên việc tu luyện, lại thêm Danh Sư đường cùng toàn bộ nhân tộc không ràng buộc cung cấp, cùng bản thân thiên phú, ngưng tụ nhị đẳng nguyên thai, cũng không khó!”"),t("br"),t("br"),n._v("Nói đến đây, Mục sư khôi lỗi dừng lại một chút, tiếp tục nói: “Lúc trước Khổng sư tại danh sư chức nghiệp bên trong cứ thế mà tách ra một cái Chiến sư, thật ra thì chính là vì truyền thừa nguyên thai phân cấp!”"),t("br"),t("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Khó trách lúc trước gặp phải vị kia Ngô Hư, đồng dạng là Tằm Phong cảnh đỉnh phong, một người nhưng có thể ép tới Hồ Yêu Yêu, Tiết Chân Dương đám người bó tay toàn tập, không phải là bởi vì Đổng Hân đám người thiên phú không đủ, mà là nguyên thai đẳng cấp... Chưa tới!"),t("br"),t("br"),n._v("Như vậy trải qua, bất kể chân khí độ tinh thuần vẫn là đối lực lượng khống chế, cũng không bằng đối phương, bị đánh bại, cũng là rất bình thường."),t("br"),t("br"),n._v("“Chiến sư cũng chỉ là nhị đẳng nguyên thai... Cái kia nhất đẳng đâu?”"),t("br"),t("br"),n._v("Trương Huyền hỏi lần nữa."),t("br"),t("br"),n._v("Chiến sư cường đại như thế, cũng chỉ là nhị đẳng, nhất đẳng chẳng phải càng thêm đáng sợ?"),t("br"),t("br"),n._v("“Nhất đẳng nguyên thai, chỉ ở trong truyền thuyết tồn tại qua, đã rất lâu không có người thành công qua! Dù sao ta cũng chưa từng thấy qua, nghe nói năm đó Khổng sư, khả năng chính là... Đương nhiên, cụ thể như thế nào, thư tịch không có ghi chép, ta cũng không dám vọng kết luận...”"),t("br"),t("br"),n._v("Mục sư khôi lỗi giải thích."),t("br"),t("br"),n._v("“Không có người thành công qua?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Đúng vậy a! Nguyên nhân chính là như vậy, ta hoài nghi nhất đẳng nguyên thai, chính là... Trở thành trời nhận Thánh Nhân nguyên nhân chủ yếu! Cho nên, cho tới nay, ta đều muốn trở thành liền nhất đẳng nguyên thai, vì thế hao tốn không dưới mấy trăm năm... Kết quả, đến cuối cùng, cũng chỉ ngưng tụ ra nhị đẳng thượng thượng phẩm nguyên thai, vẫn không thể nào thành công!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi mặt mũi cười khổ."),t("br"),t("br"),n._v("Vì một cái không biết kết quả giấc mơ, lãng phí một cách vô ích mấy trăm năm, sinh mệnh nối gần một nửa, đổi lại ai, đều sẽ hối tiếc không thôi."),t("br"),t("br"),n._v("“Nhị đẳng thượng phẩm?”"),t("br"),t("br"),n._v("“Ừm, nguyên thai không chỉ chia làm một, hai, ba các loại, trong đó mỗi một các loại bên trong, còn phân là hạ phẩm, thượng phẩm, tuyệt phẩm bốn cái cấp bậc! Ta có thể ngưng tụ ra loại này, cũng là hao tốn rất lớn đánh đổi, nắm giữ cửu tử nhất sinh kinh nghiệm, lãng phí không biết bao nhiêu thời gian!”"),t("br"),t("br"),n._v("“Sau khi thành công, mặc dù có thể vượt cấp chiến đấu, nhưng khi đó thực lực không bằng ta, thiên tư không bằng ta cùng thế hệ người cạnh tranh... Đều đã đạt đến Thánh vực tứ trọng, ngũ trọng, xem đến nơi này, mới biết mình sai! Ngay lúc đó ta chỉ là cái bình thường Thánh vực... Đã bỏ qua tốt nhất tấn thăng niên kỉ kỷ, cả đời lại không cách nào tấn cấp cảnh giới cao hơn, cuối cùng dừng bước tại nhất trọng đỉnh phong... Trong lòng hối hận có thể tưởng tượng được!”"),t("br"),t("br"),n._v("“Thẳng đến đây, mới hiểu được Khổng sư vì cớ gì ý giấu diếm tin tức này... Thiên tư không đủ, coi như có thể thành công, cũng là phí thời gian thời gian! Thử nghĩ một hồi, Thánh vực tứ trọng cường giả đối với nhân loại tác dụng lớn, vẫn là một cái Thánh vực nhất trọng đỉnh phong tác dụng lớn? Kết quả không cần suy nghĩ nhiều!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi ánh mắt bên trong mang theo vẻ hối tiếc."),t("br"),t("br"),n._v("Cường giả Thánh vực có tuổi thọ hạn chế, dài nhất cũng sống không quá hai ngàn năm, hắn đem trẻ tuổi nhất bảy, tám trăm năm đều lãng phí ở tấn cấp nguyên thai, bỏ qua rất hoàng kim lúc tu luyện ánh sáng, đợi thân thể cơ năng hạ thấp, lại nghĩ đột phá, tự nhiên không làm được."),t("br"),t("br"),n._v("Mà lúc này, cùng hắn năm đó cạnh tranh, thực lực thiên phú cũng không bằng hắn người, đều nhao nhao đi đến Thánh vực tứ ngũ trọng... Nguyên thai cao cấp đến đâu thì có ích lợi gì? Cùng cấp bậc vô địch thì có ích lợi gì? Chẳng lẽ còn có thể vượt qua mấy cái cấp bậc chiến đấu?"),t("br"),t("br"),n._v("Rõ ràng là không thể nào."),t("br"),t("br"),n._v("Cho nên, tổng kết đến cuối cùng, thiên tư không đủ, chính là lãng phí thời gian."),t("br"),t("br"),n._v("“Nguyên nhân chính là biết những thứ này, ta chuyên môn làm ra cái kỷ lục này núi, đối với chiến đấu lực tiến hành khảo hạch, chỉ không đạt tới Bán Thánh, mà còn có thể đủ thắng quá Thánh vực nhất trọng sơ kỳ ta, mới có tư cách biết tin tức này, có hi vọng... Thành công cô đọng cấp bậc cao hơn nguyên thai... Bởi vì loại thiên phú này, so ta đều mạnh hơn, có lẽ có thể thành công, hoàn thành ta chưa hoàn thành sự tình!”"),t("br"),t("br"),n._v("Mục sư ánh mắt lấp lánh nhìn lại."),t("br"),t("br"),n._v("“Ngươi chỉ có Tằm Phong cảnh sơ kỳ, liền có thể đem ta chiến thắng, đồng thời một đường đi tới nơi này, đối với chiến đấu lý giải, chiến lực mạnh mẽ, đều là vài vạn năm chưa từng xuất hiện một vị... Nếu như có thể tu luyện cái này Phong Thánh giải, có lẽ liền có thể ngưng tụ ra cấp bậc cao hơn nguyên thai, có hi vọng xung kích một chút trời nhận Thánh giả!”"),t("br"),t("br"),n._v("“Ta? Có hi vọng?”"),t("br"),t("br"),n._v("Nghe được đối phương, cùng nhìn thấy của hắn hừng hực ánh mắt, Trương Huyền sững sờ."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-920"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-922"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);