(window.webpackJsonp=window.webpackJsonp||[]).push([[2212],{2567:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-913-tu-luyen-phong-thanh-giai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-913-tu-luyen-phong-thanh-giai"}},[n._v("#")]),n._v(" Chương 913: Tu luyện Phong Thánh giải")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 913: Tu luyện Phong Thánh giải"),t("br"),t("br"),n._v("Không nghĩ tới trước mắt vị này đối với hắn thế mà tin tưởng như vậy."),t("br"),t("br"),n._v("Nhất đẳng nguyên thai tuy rất tốt, nhưng muốn cùng đối phương đồng dạng, cần tu luyện cái mấy trăm năm, đánh chết cũng sẽ không đi làm."),t("br"),t("br"),n._v("Hắn 30 tuổi trước đó, không đến cửu tinh danh sư, Tiên Thiên thai độc liền sẽ phát tác, không sống tới lâu như vậy, ngưng tụ cao cấp đến đâu cái khác nguyên thai thì có ích lợi gì?"),t("br"),t("br"),n._v("Cho nên, vị này Mục sư mặc dù đem nhất đẳng nguyên thai nói đến thiên hoa loạn trụy, mà trên thực tế hắn cũng không có hứng thú quá lớn, có thể thành công tốt nhất, không thành công, cũng không có cái gì."),t("br"),t("br"),n._v("“Không sai, lấy thiên phú của ngươi hoàn toàn có hi vọng, đương nhiên, chỉ có thiên phú còn chưa đủ, muốn thành công còn cần cơ duyên xảo hợp cùng ngộ tính!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi gật đầu."),t("br"),t("br"),n._v("Danh sư đại lục trong lịch sử, kinh tài tuyệt diễm thiên tài, như cá diếc sang sông, nhiều vô số kể, mà có thể tu luyện thành nhất đẳng nguyên thai, chưa từng nghe nói qua."),t("br"),t("br"),n._v("Thiên phú tuy quan trọng, vận khí, ngộ tính cũng không thể thiếu."),t("br"),t("br"),n._v("“Vận khí không cách nào khống chế, ngộ tính ngã là có thể đo lường. Như vậy, ngươi trước học tập bản này Phong Thánh giải, nếu có thể ở trong vòng ba ngày, đem chân khí trong cơ thể hội tụ vào một chỗ, không yêu cầu nhiều, chỉ cần bảy cỗ chân khí tụ tập, không sụp đổ, liền có rất lớn hi vọng xung kích vượt qua nhị đẳng thượng phẩm nguyên thai, làm không được điểm ấy, liền không nên suy nghĩ nhiều!”"),t("br"),t("br"),n._v("“Bảy cỗ chân khí!”"),t("br"),t("br"),n._v("Trương Huyền nhớ lại mới vừa mới nhìn đến Phong Thánh giải."),t("br"),t("br"),n._v("Mặc dù Tằm Phong cảnh có thể ngưng tụ nguyên thai, nhưng muốn ngưng tụ cấp bậc cao hơn, cũng không phải là một cái Hóa Phàm cửu trọng có thể hoàn thành."),t("br"),t("br"),n._v("Khó mà làm được không nói, coi như có thể thành công, cũng bởi vì làm lực lượng chưa tới, không nắm được, từ đó làm cho sụp đổ."),t("br"),t("br"),n._v("Phong Thánh giải chính là biết chuyện này, đem Tằm Phong cảnh nguyên thai kỳ, cứ thế mà kéo dài, chỉ cần không có đi đến Thánh vực, đều có thể ngưng tụ, nói cách khác, liền xem như Bán Thánh cùng Tòng Thánh cấp bậc, cũng có thể đối nguyên thai tiến hành ôn dưỡng, để hắn không ngừng gia tăng."),t("br"),t("br"),n._v("Năm đó Khổng sư hẳn là như vậy, Bán Thánh, Tòng Thánh tích lũy hùng hậu, đi đến Thánh vực thời điểm, một lần hành động đột phá, nổi tiếng thiên hạ, ngay cả trời cao cũng vì đó công nhận."),t("br"),t("br"),n._v("Ngưng tụ nguyên thai, là đem chân khí hội tụ vào một chỗ, ngưng kết thành cầu, sau đó đuổi tầng mài giũa, theo giả đan đến chân đan, thực đan, cuối cùng mới là Kim Đan!"),t("br"),t("br"),n._v("Đối phương nói tới bảy cỗ chân khí, trên thực tế là cấu thành giả đan cơ sở."),t("br"),t("br"),n._v("Chân khí là khí thể, dễ dàng tán loạn, Phong Thánh giải là đem chân khí ngưng tụ thành tơ, lấy đặc thù khung phương thức, dựng cùng một chỗ."),t("br"),t("br"),n._v("Thật giống như xây nhà, không có có nhất định phương pháp và trình tự, tất cả gạch đá chất thành một đống, khó mà vững chắc không nói, càng không khả năng hình thành lộng lẫy kiến trúc."),t("br"),t("br"),n._v("Phong Thánh giải chính là đem bảy cỗ chân khí, lấy đặc thù khung ngưng tập hợp một chỗ, hình thành một đơn vị, chỉ cần cái này bảy cỗ vững chắc, đằng sau từng cái tích tụ là đủ."),t("br"),t("br"),n._v("Dường như gạch, đá, xi măng, nước, thoạt nhìn lẫn nhau không thể làm chung, một khi dung hợp lại cùng nhau, liền có thể kiến tạo ra một tòa lại một tòa hùng uy lầu bỏ."),t("br"),t("br"),n._v("đọc ngantruyen.com/"),t("br"),n._v("Nguyên lý không khó, mấu chốt là, như thế nào cô đọng chân khí, để nó biến phải cùng dây nhỏ đồng dạng, không tán loạn, còn có thể lấy loại kết cấu này ngưng tụ, cái này mới là trọng yếu nhất."),t("br"),t("br"),n._v("Chẳng những cần phải cường đại hồn lực, còn muốn đối chân khí có tuyệt đối khống chế mới có thể làm đến."),t("br"),t("br"),n._v("“Không sai, ngươi bây giờ đọc sách đi, chỉ cần trong ba ngày hoàn thành, ta sẽ nói cho ngươi biết, ta quá trình tu luyện, để ngươi thiếu đi đường quanh co, không đến mức giống như ta người mù sờ voi, cuối cùng tiêu hao mấy trăm năm, cũng không thành công!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi nói."),t("br"),t("br"),n._v("“Đọc sách... Trước không cần, ngươi nói bảy cỗ chân khí vững chắc là thế này phải không?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, ngón tay một chút, một đạo chân khí, thẳng tắp hiện lên ở trước mắt, đang muốn tiếp tục nữa, liền xem một bên Mục sư khôi lỗi con mắt trợn tròn, dường như dọa đến sắp ngất đi, thanh âm phát run."),t("br"),t("br"),n._v("“Ngươi có thế để cho... Chân khí ly thể? Hơn nữa còn duy trì có khống chế trạng thái?”"),t("br"),t("br"),n._v("“Cái này có cái gì kỳ quái, ta mới vừa tu luyện không lâu liền có thể làm được, lại không đại sự gì...”"),t("br"),t("br"),n._v("Thấy hắn một bộ ngạc nhiên, không có thấy qua việc đời bộ dạng, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Còn khai phái tổ sư, điểm ấy kiến thức bình thường đều không có."),t("br"),t("br"),n._v("Chân khí ly thể, cũng không phải việc khó gì, hắn mới tu luyện không lâu, liền đem chân khí, rót vào trong cơ thể người khác, khơi thông nhỏ bé trong kinh mạch tắc nghẽn, khống chế thành dây nhỏ mà thôi, võ giả thời điểm cũng có thể làm đến, hiện tại đạt đến Hóa Phàm cửu trọng, lại làm không được, còn không bằng đập đầu chết được rồi!"),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Nhìn thấy đối phương khinh bỉ thị lực, Mục sư khôi lỗi suýt chút nữa không có tại chỗ hộc máu."),t("br"),t("br"),n._v("Chân khí xuất thể rất dễ dàng, nhưng xuất thể về sau, còn có thể ngưng tụ thành tơ, dường như dây nhỏ, liền khó khăn, muốn làm đến điểm ấy, ít nhất phải hai cái điều kiện tất yếu."),t("br"),t("br"),n._v("Thứ nhất, chân khí đủ rất tinh khiết, ít nhất đi đến thượng tam phẩm tình trạng!"),t("br"),t("br"),n._v("Thứ hai, hồn lực đủ mạnh, thậm chí có thể đi đến xuất thể, nếu không, không có khả năng khống chế chân khí rời khỏi thân thể về sau, còn như khu cánh tay, tự do đi xuyên."),t("br"),t("br"),n._v("Có thể nói, trước mắt vị này làm được như vậy nhẹ nhõm, đối chân khí khống chế mức độ, quả thực nghe rợn cả người, coi như lúc trước hắn đi đến Thánh vực, cũng làm không được!"),t("br"),t("br"),n._v("Trong lòng đang chấn sợ nói không ra lời, liền thấy cách đó không xa thanh niên, ngón tay liên tục hướng về phía trước điểm ra."),t("br"),t("br"),n._v("Hô hô hô hô hô!"),t("br"),t("br"),n._v("Đạo thứ hai, đạo thứ ba... Rất nhanh bảy đạo chân khí, từ ngón tay tuôn ra, trên không trung lơ lửng, linh như rắn không ngừng chạy, từng đầu xen kẽ cùng một chỗ."),t("br"),t("br"),n._v("“Hắn... Không chỉ có thể khống chế một cái chân khí, còn có thể khống chế bảy cái?”"),t("br"),t("br"),n._v("Nuốt miệng cũng không tồn tại nước bọt, Mục sư khôi lỗi thanh âm khàn khàn."),t("br"),n._v("Đem chân khí ngưng tụ thành tơ, ly thể khống chế, chính là rất nhiều cường giả Thánh vực đều làm không được, cái tên này chẳng những thành công, còn một cái làm ra bảy cái... Cái này cũng quá cường đại a?"),t("br"),t("br"),n._v("Ngươi thật chỉ có Tằm Phong cảnh sơ kỳ?"),t("br"),t("br"),n._v("Không phải là Thánh vực cường giả đỉnh phong, hoặc là Khổng sư cái nào vị đệ tử, đặc biệt tới dạo chơi nhân gian a!"),t("br"),t("br"),n._v("Rất nhanh, bảy đạo chân khí, đã hoàn mỹ dung hợp lại cùng nhau, tạo thành một cái to bằng hạt vừng chân khí viên cầu, trên không trung nhẹ nhàng xoay tròn, tụ mà không tiêu tan."),t("br"),t("br"),n._v("“Ngươi ý tứ, trong đan điền, dùng chân khí xen kẽ thành như vậy tiểu cầu, sau đó từng đạo tổ hợp lại với nhau, liền có thể ngưng tụ thành giả đan đúng không?”"),t("br"),t("br"),n._v("Làm xong những thứ này, Trương Huyền ngẩng đầu nhìn tới."),t("br"),t("br"),n._v("“Đúng...”"),t("br"),t("br"),n._v("Thở ra một hơi, đem khiếp sợ trong lòng ngăn chặn, Mục sư khôi lỗi nhẹ gật đầu."),t("br"),t("br"),n._v("Phong Thánh giải, là đem chân khí ngưng tụ thành tơ, sau đó chậm rãi hội tụ, hình thành nguyên một đám dạng này tiểu cầu, cuối cùng dung hợp lại cùng nhau, dường như nước đọng thành sông, tích sa thành tháp, lợi dụng mài nước công phu, ngưng tụ Kim Đan."),t("br"),t("br"),n._v("Như vậy ngưng tụ ra, cơ sở vững chắc, lực lượng vững chắc, so bình thường tu luyện cường đại hơn rất nhiều, chỉ là cần tiêu hao công phu cũng thực sự quá lớn."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, hắn mới tiêu hao mấy trăm năm có thể thành công."),t("br"),t("br"),n._v("“Rất đơn giản ah, không dùng đến ba ngày làm được ah...”"),t("br"),t("br"),n._v("Còn cho là mình lý giải sai, suy nghĩ cả nửa ngày, đơn giản như vậy, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Liền dùng chân khí, làm cái cái đồ chơi này, cũng muốn dùng ba ngày mới có thể hoàn thành, còn gọi cái gì thiên tài? Còn không bằng tìm đậu phụ trực tiếp đâm chết được rồi."),t("br"),t("br"),n._v("“Ngươi... Khả năng tu luyện qua ngưng tụ chân khí phương pháp, thiên phú cùng những người khác khác biệt...”"),t("br"),t("br"),n._v("Thấy đối phương như vậy hời hợt, Mục sư khôi lỗi xấu hổ cười một tiếng, nói."),t("br"),t("br"),n._v("Vốn ý định làm khó đối phương, muốn nhìn một chút ngộ tính của hắn, kết quả người ta cái gì đều không có cảm thấy, ngược lại lại cảm thấy đơn giản không tưởng nổi, để hắn đều có chút không biết trả lời như thế nào..."),t("br"),t("br"),n._v("Từ khi lưu lại tàn hồn, canh giữ nơi này, gần vạn năm qua, thấy qua vô số thiên tài, có thể... Biến thái như vậy, còn là lần đầu tiên gặp được!"),t("br"),t("br"),n._v("Thậm chí cũng không thể gọi biến thái, quả thực cũng không phải là người!"),t("br"),t("br"),n._v("“Có lẽ đi!” Trương Huyền không tại nhiều nói."),t("br"),t("br"),n._v("Chuyên môn ngưng tụ chân khí phương pháp, hắn không có tu luyện qua, có điều, Thiên Đạo chân khí tinh thuần vô cùng, hơn xa cái gọi là thượng tam phẩm chân khí, đối với những người khác rất khó làm được sự tình, đối với hắn mà nói, ngược lại cùng ăn cơm uống nước đồng dạng đơn giản."),t("br"),t("br"),n._v("Còn làm như vậy, còn cần hồn lực mạnh, thân là vu hồn sư, hồn phách ly thể dễ như trở bàn tay."),t("br"),t("br"),n._v("“Đã có thể làm được điểm ấy, liền không cần tốn hao công phu, tiếp tục nghiên cứu, hiện tại liền có thể thử tu luyện Phong Thánh giải, nhìn xem lấy thực lực trước mắt có thể làm được mấy giải, mà không sụp đổ!”"),t("br"),t("br"),n._v("Mục sư khôi lỗi tiếp tục nói."),t("br"),t("br"),n._v("Đối phương chân khí ly thể cũng có thể làm đến, tiếp tục nghiên cứu cũng liền không có ý nghĩa gì, không bằng thử tu luyện, xem xem rốt cục có thể đi tới một bước nào."),t("br"),t("br"),n._v("Cái gọi là “Giải”, chính là phong sư giải bên trong bước trọng yếu nhất cùng phương pháp tu luyện, ngưng tụ bảy đạo chân khí cùng một chỗ, vì một hạt, bảy hạt vì một đoàn, bảy đám vì một giải!"),t("br"),t("br"),n._v("Nói cách khác, muốn ngưng tụ ba trăm bốn mươi ba đạo chân khí, không phạm sai lầm dưới tình huống, mới có thể hình thành một giải."),t("br"),t("br"),n._v("Lúc trước hắn tại Tằm Phong cảnh thời điểm, ròng rã khung lục giải, mới khống chế không nổi, bị bức ép rơi vào đường cùng, đột phá Bán Thánh."),t("br"),t("br"),n._v("Thiên phú không cao, linh hồn không mạnh, tại cấp bậc này, đừng nói lục giải, khả năng liền một đoàn đều làm không được."),t("br"),t("br"),n._v("Đối phương mặc dù đối chân khí lực khống chế rất mạnh, nhưng tu luyện qua phong sư giải, biết trong đó độ khó, nương theo chân khí ngưng tụ càng ngày càng nhiều, nguy hiểm cũng liền càng lúc càng lớn, cũng liền càng ngày càng khó khống chế."),t("br"),t("br"),n._v("Thật giống như xếp gỗ đồng dạng, mở một hai cái rất đơn giản, bày càng nhiều, liền càng nguy hiểm, hơi có chút sai lầm, đều sẽ khiến sụp đổ."),t("br"),t("br"),n._v("Ngưng tụ một hạt, chỉ cần chịu tốn tốn thời gian, chỉ cần chịu rèn luyện chân khí, rất nhiều người cũng có thể làm đến, nhưng mà ngưng tụ một đoàn, liền đối lập muốn phiền phức rất nhiều, còn ngưng tụ một giải, không có cực mạnh thiên phú, tinh nhỏ như tóc lực khống chế, cùng vĩnh cửu kiên nhẫn, căn bản không thể nào làm được."),t("br"),t("br"),n._v("Đối phương thiên phú mặc dù rất mạnh, dù sao tuổi trẻ, cộng thêm tu vi, chỉ có Tằm Phong cảnh sơ kỳ, có thể ngưng tụ một giải, hắn thấy, cũng rất không tệ."),t("br"),t("br"),n._v("“Ngưng tụ mấy giải?” Trương Huyền chần chờ một chút: “Ta thử một chút đi!”"),t("br"),t("br"),n._v("Có thể ngưng tụ mấy giải, không có thí nghiệm qua, thật không dám xác định, bất quá bây giờ ngã là có thể thử một chút."),t("br"),t("br"),n._v("Tiếng nói kết thúc, mười ngón tay xòe ra, đồng thời hướng về phía trước bắt ra ngoài."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Một hồi chân khí xao động, một trong nháy mắt, mấy trăm đạo chân khí theo đầu ngón tay của hắn, phun ra ngoài, như cùng một cái đầu mảnh rắn, lại hình như từng đầu tiểu long, không ngừng lăn mình."),t("br"),t("br"),n._v("“Đồng thời ngưng tụ ra nhiều như vậy chân khí?” Mục sư khôi lỗi đang đang suy đoán, trước mắt vị này muốn tu luyện bao lâu thời gian, liền nhìn thấy màn này, suýt chút nữa không có tại chỗ dọa ngất đi."),t("br"),t("br"),n._v("Đồng thời khống chế mấy trăm đạo chân khí, biến thành tơ mỏng, khắp nơi du tán, không tốn sức chút nào, linh hồn này cái kia cường đại cỡ nào?"),t("br"),t("br"),n._v("Đừng nói hiện tại, coi như hắn thời khắc đỉnh cao nhất, cũng làm không được điểm ấy."),t("br"),t("br"),n._v("Khiếp sợ còn chưa kết thúc, chỉ thấy cách đó không xa thanh niên, ngón tay nhất câu bắn ra, trước mắt mấy trăm đạo chân khí, lập tức quấn quýt lấy nhau, tạo thành một cái chừng hạt gạo chân khí đoàn."),t("br"),t("br"),n._v("Lập tức liền thấy đối phương nhìn lại, trong mắt mang theo nghi ngờ: “Đây chính là một giải a?”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-921"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-923"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);