(window.webpackJsonp=window.webpackJsonp||[]).push([[2218],{2573:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-919-tham-quan-khoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-919-tham-quan-khoc"}},[n._v("#")]),n._v(" Chương 919: Thẩm Quân khóc")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 919: Thẩm Quân khóc"),t("br"),t("br"),n._v("Thời gian trở lại kế vị đại điển kết thúc."),t("br"),t("br"),n._v("Nhìn thấy như vậy rung động lòng người kế vị đại điển, Thẩm Vạn Đình, Liễu Tại Ngôn hai vị tộc trưởng mặt mũi kích động cùng cảm khái."),t("br"),t("br"),n._v("Nhất là Thẩm Vạn Đình, với tư cách Hồng Viễn thành rất mọi người tộc thực tế người cầm quyền, biết Trương sư hôm nay náo ra động tĩnh lớn như vậy, đại biểu ý nghĩa."),t("br"),t("br"),n._v("Để chỗ có danh sư, đều hoàn toàn thừa nhận; Vân Vụ lĩnh tất cả linh thú, đều xưng hô chủ nhân... Quyền thế bao lớn, lực hiệu triệu mạnh, Hồng Viễn thành lại không người có thể so."),t("br"),t("br"),n._v("Coi như Ngọc Thần Thanh bệ hạ, đều chênh lệch cách xa vạn dặm, không tại cùng một cái cấp bậc."),t("br"),t("br"),n._v("Tới quan hệ thân thiết, về sau Thẩm gia lên như diều gặp gió, càng ngày càng mạnh, ở trong tầm tay."),t("br"),t("br"),n._v("Đang định về đến gia tộc, cùng tất cả trưởng lão thương nghị một chút, như thế nào đả thông quan hệ, liền thấy mình vị này bình thường, rất tán thưởng con trai, đầy người trọng thương bị người mang vào."),t("br"),t("br"),n._v("“Đây là thế nào?” Thẩm Vạn Đình sầm mặt lại."),t("br"),t("br"),n._v("Hồng Viễn thành tứ đại gia tộc đứng đầu, Thẩm gia có vô cùng sức uy hiếp mạnh mẽ, con của hắn, đường đường thiếu tộc trưởng, bị đánh thành dạng này, quả thực vô pháp vô thiên, không có đem Thẩm gia để vào mắt."),t("br"),t("br"),n._v("“Phụ thân, nhất định phải làm chủ cho ta...” Thấy hắn đi tới, Thẩm Quân nước mắt tứ lan tràn, một tiếng vù vù."),t("br"),t("br"),n._v("“Ai ra tay, dám khiêu khích Thẩm gia, coi như hắn là vương công quý tộc, hoàng thân quốc thích, đồng dạng thay ngươi làm chủ, để hắn cho ta một câu trả lời!”"),t("br"),t("br"),n._v("Híp mắt lại, Thẩm Vạn Đình thanh âm bên trong mang theo khí phách, cùng không thể nghi ngờ uy quyền."),t("br"),t("br"),n._v("Thân là đệ nhất gia tộc tộc trưởng, có năng lực như thế, cũng có cái này quyết đoán."),t("br"),t("br"),n._v("“Là danh sư học viện một cái không biết tên tiểu danh sư... Hắn hèn hạ vô sỉ, làm ra bẫy rập, ỷ nhiều người, đem ta cùng Liễu Tuyền, đánh thành dạng này!”"),t("br"),t("br"),n._v("Thẩm Quân cắn răng."),t("br"),t("br"),n._v("Hắn chỉ biết là đối phương người đông thế mạnh, cụ thể có bao nhiêu người cũng không rõ ràng, bị che lại đầu, chỉ cảm thấy phô thiên cái địa công kích, đổ ập xuống đập xuống đến, sau đó, sau đó... Cứ như vậy!"),t("br"),t("br"),n._v("“Không biết tên tiểu danh sư? Người đông thế mạnh?”"),t("br"),t("br"),n._v("Thẩm Vạn Đình lông mày vừa nhấc: “Ăn gan hùm mật gấu, dám tìm phiền toái?”"),t("br"),t("br"),n._v("đọc ngantruyen.com/ Thẩm gia mặc dù chỉ là cái đại gia tộc, thế lực lại là trải rộng các ngành các nghề, Hồng Viễn thành Danh Sư đường, liền có bọn họ bảy vị lục tinh danh sư cấp bậc cung phụng; Gia tộc tử đệ bên trong, cũng có mấy vị, đạt đến lục tinh danh sư cấp bậc."),t("br"),t("br"),n._v("Bởi vậy, chỉ là một cái bình thường danh sư lời nói, không tạo thành bất cứ uy hiếp gì, thuyết giáo dạy bảo sẽ dạy, mặc kệ Danh Sư đường, vẫn là danh sư học viện, chút mặt mũi này vẫn có thể cho."),t("br"),t("br"),n._v("“Hắn là mấy tinh danh sư?” Mặc dù tiểu danh sư, không tính là gì, Thẩm Vạn Đình vẫn là cẩn thận hỏi thăm thoáng cái."),t("br"),t("br"),n._v("“Tứ tinh!”"),t("br"),t("br"),n._v("Thẩm Quân vội vàng nói."),t("br"),t("br"),n._v("Nho nhỏ tứ tinh danh sư mà thôi, cha muốn động thủ, còn không thoải mái thêm vui vẻ, vài phút giết chết?"),t("br"),t("br"),n._v("“Loại cấp bậc này gia hỏa, cũng dám khi dễ đến trên đầu chúng ta?” Híp mắt lại, Thẩm Vạn Đình bàn tay lớn vẫy một cái: “Tên gọi là gì? Ta vậy thì phái người đi danh sư học viện bắt tới, thật tốt để ngươi trút giận!”"),t("br"),t("br"),n._v("“Hắn gọi Trương Huyền, là cái không biết trời cao đất rộng người ngông cuồng...”"),t("br"),t("br"),n._v("Thẩm Quân cắn răng, trong lòng đang mặc sức tưởng tượng lấy, cha ra tay, đối phương tất nhiên thê vô cùng, chỉ thấy cách đó không xa từ trước tới nay bình tĩnh trung niên nhân, toàn thân run rẩy, vẻ mặt thoáng cái trở nên trắng bệch vô cùng."),t("br"),t("br"),n._v("“Cha, ngươi thế nào?” Thẩm Quân nhịn không được hỏi."),t("br"),t("br"),n._v("Lời còn chưa dứt, chỉ thấy Thẩm Vạn Đình đạp một cước, tức giận đến sợi râu run rẩy: “Ta thao ngươi bà ngoại, con mẹ nó ngươi muốn tìm cái chết, đừng kéo lên Thẩm gia!”"),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Không nghĩ tới cha sẽ ra tay với hắn, không có chút nào phòng bị phía dưới, Thẩm Quân bị một chân đá đến trên tường, từng ngụm từng ngụm hộc máu, suýt chút nữa không có tại chỗ treo rồi."),t("br"),t("br"),n._v("Đồng thời một mặt mộng bức... Phụ thân, ta mới là bị đánh, không giúp ta hả giận ngược lại cũng thôi, lại là mắng người lại là động thủ, muốn làm gì?"),t("br"),t("br"),n._v("Còn thao ta bà ngoại, cho ngươi mười cái lá gan, ngươi dám không..."),t("br"),t("br"),n._v("“Phụ thân, ngươi nếu là không muốn nhúng tay, ta tự mình giải quyết, ta thân là Thẩm gia đại công tử, đối phó một cái tứ tinh danh sư còn có thể làm được...”"),t("br"),t("br"),n._v("Gào lên một tiếng, Thẩm Quân cắn răng."),t("br"),t("br"),n._v("Bất kể nói thế nào, hắn tại triều đình đều có công danh trên người, chiến công hiển hách, bây giờ bị đánh thành dạng này, không thay trút giận liền cũng là thôi, ngược lại đánh người..."),t("br"),t("br"),n._v("Thực sự không được, liền vận dụng chính mình quan hệ, trong quân đội có thể lăn lộn đến địa vị như vậy, sao lại đơn giản?"),t("br"),t("br"),n._v("Dạy bảo một cái cấp bốn danh sư, chắc hẳn còn hết sức dễ dàng."),t("br"),t("br"),n._v("“Con mẹ nó ngươi còn muốn tự mình ra tay, có tin ta hay không hiện tại liền chặt ngươi...”"),t("br"),t("br"),n._v("Nghe được cái này nghịch tử lời nói, Thẩm Vạn Đình suýt chút nữa không có hộc máu, bàn tay lớn vẫy một cái: “Người tới, đem cái tên này cho ta trói, chiếu chết bên trong đánh, không thể nương tay!”"),t("br"),t("br"),n._v("“Cái này...” Nghe được phân phó, xông tới một đám hạ nhân, toàn đều có chút luống cuống."),t("br"),t("br"),n._v("Chẳng qua đã ra lệnh, cũng không dám phản bác, đồng loạt tiến lên đem Thẩm Quân trói lại, nhưng không có động thủ."),t("br"),t("br"),n._v("“Vì cái gì?” Nhìn thấy phụ thân là thật sự tức giận, không giống trò đùa, Thẩm Quân mặt mũi không thể tin được."),t("br"),t("br"),n._v("“Còn dám nói với ta vì cái gì, ngươi cũng đã biết hôm nay danh sư học viện kế nhiệm viện trưởng là ai?” Thẩm Vạn Đình khí run rẩy."),t("br"),t("br"),n._v("“Viện trưởng? Ta mới vừa tới trường học không bao lâu, liền bị người đánh, cũng không không có tham gia kế vị đại điển...” Thẩm Quân xấu hổ."),t("br"),t("br"),n._v("Mặc dù cùng cha cùng đi xem việc đời, nhưng mà kế vị đại điển còn chưa bắt đầu, liền bị đánh đến hoàn toàn thay đổi, để thuộc hạ nâng về gia tộc."),t("br"),t("br"),n._v("Cái gì đều không có gặp, tự nhiên không biết mới viện trưởng là ai, càng không biết náo động lên nhiều động tĩnh lớn."),t("br"),t("br"),n._v("“Mới viện trưởng chính là Trương Huyền!”"),t("br"),n._v("Thẩm Vạn Đình nổi giận đùng đùng, đối tất cả hạ nhân vẫy tay một cái: “Mang theo cái này nghịch tử, đi danh sư học viện xin lỗi, a, không, trước đi hoàng cung, ta muốn đi gặp Ngọc Thần Thanh bệ hạ!”"),t("br"),t("br"),n._v("Cái này nghịch tử làm ra như vậy đại nghịch bất đạo sự tình, tiến đến nói xin lỗi, chỉ sợ liền viện trưởng bóng người đều không thấy được, chỉ có thể gửi hi vọng ở bệ hạ thể diện!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đồng dạng một màn còn phát sinh ở Liễu gia."),t("br"),t("br"),n._v("Liễu Tuyền cũng một mặt mộng bức bị phụ thân hắn cuồng đánh một trận, mấu chốt nhất là, trước đó tổn thương, còn không cho thoa thuốc... Dẫn đến rất nhiều nơi, thối rữa càng ngày càng nghiêm trọng, đau đớn không chịu nổi, không nỡ nhìn."),t("br"),t("br"),n._v("Nếu như nói trước đó còn không hiểu, giờ phút này nhìn thấy Trương Huyền ngồi tại chủ vị, mấy câu liền để Hoài Vương gia, cảm ơn rơi nước mắt, suýt chút nữa quỳ xuống, càng làm cho Ngọc Thần Thanh bệ hạ, cầu ông nội bảo bà nội, để hắn nhận lấy hạt Bồ Đề..."),t("br"),t("br"),n._v("Ngu nữa cũng rõ ràng, người trước mắt này địa vị."),t("br"),t("br"),n._v("“Ai làm nấy chịu, là ta muốn đối phó Trương viện trưởng, còn xin viện trưởng, đừng giận lây sang chúng ta Thẩm gia...”"),t("br"),t("br"),n._v("Trọng thương Thẩm Quân cắn răng một cái, nói."),t("br"),t("br"),n._v("“Còn có ta, mấy người kia là ta tìm, cùng Liễu gia ta không quan hệ, cha ta căn bản cũng không hiểu rõ tình hình...”"),t("br"),t("br"),n._v("Liễu Tuyền cũng vội vàng nói."),t("br"),t("br"),n._v("Nghe một hồi, Trương Huyền giờ mới hiểu được tới, đến cùng chuyện gì xảy ra, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Ngươi chạy đến danh sư học viện, muốn giáo huấn ta... Đây không phải tìm kích động ư?"),t("br"),t("br"),n._v("Coi như lúc ấy mọi người không biết hắn viện trưởng thân phận, có thể Huyền Huyền hội ở nơi đó bày đặt... Không có bị tại chỗ đánh chết, đã coi như là hạ thủ lưu tình."),t("br"),t("br"),n._v("Mặt mũi bất đắc dĩ, đang muốn cự tuyệt đối phương đền bù, dù sao hắn cũng không có tổn thương... Linh quang lóe lên, đem nhẫn nhận lấy."),t("br"),t("br"),n._v("Hắn địa vị bây giờ đã không phải là trước kia bình thường học viên, mà là danh sư học viện viện trưởng, đối phương ngang nhiên tìm bản thân phiền phức, không xử lý lời nói, đến một lần đối học viện danh tiếng có ảnh hưởng rất lớn; Thứ hai, hai gia tộc này tất nhiên ăn ngủ không yên, tại Hồng Viễn thành, sống không nổi nữa."),t("br"),t("br"),n._v("Không nói cái khác, nhưng bằng sức ảnh hưởng của mình, nếu như bị người khác biết, bọn họ từng đắc tội qua bản thân, ai còn còn dám cùng bọn hắn cùng một chỗ làm ăn?"),t("br"),t("br"),n._v("Tránh chi chỉ sợ không kịp!"),t("br"),t("br"),n._v("Địa vị đại biểu nói chuyện hành động, mọi cử động muốn phù hợp thân phận mới được, là cái này... Xã hội!"),t("br"),t("br"),n._v("Tiếp nhận nhẫn, tinh thần ở bên trong nhìn lướt qua, lập tức nhìn thấy vô số bảo vật."),t("br"),t("br"),n._v("Chỉ riêng linh thạch thượng phẩm, liền có gần năm trăm viên nhiều."),t("br"),t("br"),n._v("“Hai gia tộc này, thật hào phóng!” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Nhiều như vậy linh thạch thượng phẩm, cũng không phải số lượng nhỏ, coi như Hồng Viễn hoàng thất lấy ra, chỉ sợ đều muốn thương cân động cốt, hòa hoãn một đoạn thời gian, hai gia tộc này nhẹ nhõm đem ra, đủ thấy nội tình thâm hậu."),t("br"),t("br"),n._v("“Thẩm gia thương hội trải rộng toàn bộ Hồng Viễn đế quốc, lại thêm bọn họ rất nhiều buôn bán, đều là cùng Thanh Nguyên phong hào đế quốc làm... Tài sản khá là phong phú, muốn nói vốn lưu động, sợ là chúng ta hoàng thất, cũng không có có nhiều như vậy...”"),t("br"),t("br"),n._v("Nhìn ra hắn khiếp sợ, một bên Ngọc Thần Thanh lặng lẽ truyền âm tới."),t("br"),t("br"),n._v("“So hoàng thất còn giàu có?” Trương Huyền tắc lưỡi."),t("br"),t("br"),n._v("Khó trách lần thứ nhất nhìn thấy vị này Thẩm Quân, liền trực tiếp lấy ra hai cái linh thạch thượng phẩm xem như lễ gặp mặt, đây chính là Hình Viễn, Ngọc Phi Nhi đều làm không được."),t("br"),t("br"),n._v("Nguyên tới nhà có tiền như vậy!"),t("br"),t("br"),n._v("“Đã bọn họ làm ăn, lại cùng Thanh Nguyên đế quốc có giao dịch, hoàn toàn có thể để cho bọn họ hỗ trợ...” Đem vừa rồi ý nghĩ, tại trong óc chuyển một lần, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Hắn hiện tại thiếu tiền, muốn đi tìm linh thạch thượng phẩm mà không thể được, đã đối phương làm ăn, lại có Thanh Nguyên phong hào đế quốc phương pháp, chắc hẳn bán ra vài thứ, mười phần đơn giản."),t("br"),t("br"),n._v("Dù sao so với hắn ra tay, dễ dàng hơn nhiều."),t("br"),t("br"),n._v("Đã như vậy, vì cái gì không dùng một chút?"),t("br"),t("br"),n._v("“Đồ vật ta liền nhận!” Muốn đến nơi này, Trương Huyền nhẹ gật đầu, nhìn qua: “Vừa vặn ta còn có chút sự tình khả năng còn muốn làm phiền các ngươi!”"),t("br"),t("br"),n._v("“Viện trưởng nói là nơi nào nói, chúng ta Thẩm gia cùng Liễu gia, chỉ cần có thể làm được, nhất định không chối từ...”"),t("br"),t("br"),n._v("Thẩm Vạn Đình, Liễu Tại Ngôn ánh mắt sáng lên, đồng thời ôm quyền."),t("br"),t("br"),n._v("Hiện tại sợ nhất là đối phương vô dục vô cầu, có chuyện xảy ra, mới có thể lôi kéo quan hệ, càng ngày càng tốt."),t("br"),t("br"),n._v("“Kế vị đại điển lúc tình huống, các ngươi đều thấy được, Vân Vụ lĩnh tất cả thú sủng, vì lấy ta vui lòng, đưa rất nhiều lễ vật, những vật này ta cũng không cần đến, đã các ngươi hai nhà đều là làm ăn, có thể hay không thay bán ra?”"),t("br"),t("br"),n._v("Trương Huyền nói ra mục đích."),t("br"),t("br"),n._v("“Cái này... Chúng ta cầu còn không được!”"),t("br"),t("br"),n._v("Nghe được lại là yêu cầu này, Thẩm Vạn Đình, Liễu Tại Ngôn lập tức đại hỉ."),t("br"),t("br"),n._v("Liên quan lợi ích, liền rốt cuộc dứt bỏ không ra, đối phương đem thứ này cho bọn hắn đi làm, nói rõ đã vứt bỏ hiềm khích lúc trước, tín nhiệm rất nhiều."),t("br"),t("br"),n._v("“Vậy là tốt rồi...” Trương Huyền không tại nhiều nói, lấy ra một viên trữ vật giới chỉ đưa tới."),t("br"),t("br"),n._v("Vân Vụ lĩnh tất cả Thánh thú đưa tặng bảo vật, toàn ở bên trong."),t("br"),t("br"),n._v("Những vật này, mặc dù trân quý, nhưng đối với hắn mà nói tác dụng không lớn, còn không bằng đổi lấy linh thạch thượng phẩm, dùng tới tu luyện."),t("br"),t("br"),n._v("Còn đối phương cơ sở tại đây, khẳng định cũng không dám quét thủ đoạn gì."),t("br"),t("br"),n._v("“Những vật này, bất kể tại Hồng Viễn thành vẫn là đế quốc khác, đều cực kỳ quý hiếm... Viện trưởng yên tâm, giao cho chúng ta, cam đoan trong hai tháng, liền giúp ngươi toàn bộ tiêu thụ ra đi!”"),t("br"),t("br"),n._v("Thẩm Vạn Đình liên tục gật đầu."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-927"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-929"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);