(window.webpackJsonp=window.webpackJsonp||[]).push([[2219],{2574:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-920-lai-quay-ve-van-quoc-lien-minh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-920-lai-quay-ve-van-quoc-lien-minh"}},[n._v("#")]),n._v(" Chương 920: Lại quay về Vạn Quốc liên minh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 920: Lại quay về Vạn Quốc liên minh"),t("br"),t("br"),n._v("Vân Vụ lĩnh tất cả bảo vật, khoáng thạch, muốn nói trân quý nhất khẳng định là Vân Vụ hoa."),t("br"),t("br"),n._v("Thứ này chỉ có nơi này mới có thể sản xuất, thưa thớt vô cùng, coi như Hồng Viễn hoàng thất đều không bỏ ra nổi tới bao nhiêu, thoáng cái nhiều như vậy tấn... Nhất định là cái giá trên trời."),t("br"),t("br"),n._v("Có thể được đến những vật này bán ra, đến một lần có thể hiển lộ rõ ràng bọn họ Thẩm gia, Liễu gia cùng mới viện trưởng quan hệ, khiến người khác kính sợ; Thứ hai, còn khả năng khai thác càng lớn thị trường, để càng nhiều người biết hai đại gia tộc."),t("br"),t("br"),n._v("Có thể nói, coi như hỗ trợ bán ra, không thu một phần chỗ tốt, không kiếm một phân tiền, đều kiếm bộn rồi."),t("br"),t("br"),n._v("“Vậy làm phiền!”"),t("br"),t("br"),n._v("Rõ ràng đối phương cân nhắc, Trương Huyền cũng không nhiều lời, ôm quyền nhẹ gật đầu."),t("br"),t("br"),n._v("Lại trò chuyện vài câu, xác định như thế nào bán ra, bốn người liền thức thời đứng dậy cáo từ."),t("br"),t("br"),n._v("Mấy người rời khỏi, Mi trưởng lão đi đến."),t("br"),t("br"),n._v("“Viện trưởng, ta đã an bài, viện trưởng nếu như bây giờ khảo hạch lục tinh danh sư lời nói, chỉ cần lựa chọn một cái phong hào vương quốc, trong vòng mười năm để hắn tấn thăng thành Đế quốc, coi như thông qua!”"),t("br"),t("br"),n._v("“Mười năm?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lúc này quá lâu, hắn muốn khảo hạch, tự nhiên là càng nhanh càng tốt."),t("br"),t("br"),n._v("“Phong hào vương quốc... Liền Hiên Viên phong hào vương quốc đi!” Trương Huyền nói."),t("br"),t("br"),n._v("Hắn đi qua nhất đẳng vương quốc, liền cái này một cái, lúc trước Hiên Viên vương thất bị hắn giết chết, xác lập Mạc Vũ làm quốc vương bệ hạ, hiện tại hơn nửa năm, cũng không biết thế nào, vừa vặn cũng trở về đi xem một chút."),t("br"),t("br"),n._v("“Được!” Mi trưởng lão nhẹ gật đầu: “Ta hiện tại liền đưa tin cho tổng bộ, để bọn hắn phái người đi Hiên Viên vương quốc!”"),t("br"),t("br"),n._v("Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Xem qua tất cả thư tịch, đối tấn thăng đế quốc cái này lục tinh khảo hạch, đã hoàn toàn lý giải."),t("br"),t("br"),n._v("Danh sư học viện mặc dù có giao phó lục tinh danh sư tư cách, lại không tư cách vào đi loại cấp bậc này khảo thí, nhất định phải lên báo cấp bậc cao hơn Danh Sư đường, để hắn phái người tới."),t("br"),t("br"),n._v("Bọn họ trước đối lại trước vương quốc tiến hành hạch định, xác định đẳng cấp về sau, khảo hạch người lại đi qua, nghĩ biện pháp tấn thăng... Một khi thành công, tổng bộ sẽ lần nữa phái người xác nhận."),t("br"),t("br"),n._v("Dạng này liền tránh khỏi gian lận khả năng."),t("br"),t("br"),n._v("“Báo tin tổng bộ, bọn họ phái người hạch định xong, đại khái cần thời gian mười ngày!” Mi trưởng lão tính toán một chút, nói tiếp: “Mười ngày sau, viện trưởng liền có thể đứng dậy đi Hiên Viên phong hào vương quốc!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Dù sao hắn còn có nhiều như vậy Thiên Đạo công pháp, không có học tập, danh sư học viện, còn có tất cả nghề nghiệp sách, không có quan sát, vừa vặn thừa dịp này mười ngày công phu, cùng một chỗ hoàn thành."),t("br"),t("br"),n._v("Mấy ngày kế tiếp, Trương Huyền không có khắp nơi đi loạn, không làm gì liền đi tất cả học viện Tàng Thư khố đọc sách."),t("br"),t("br"),n._v("Rất nhanh, Thuần Thú viện, Vũ Kỹ viện, thiên công viện, Thư Họa viện... Những này trước đó không nhìn lại qua Tàng Thư khố, đều bị hắn đi dạo một lần, trong đó thư tịch, cũng tất cả đều ghi vào trong óc."),t("br"),t("br"),n._v("Loại trừ đọc sách, chính là bế quan học tập, ngày thứ chín thời điểm, rốt cục đem những ngày này xem sách toàn bộ học được một lần."),t("br"),t("br"),n._v("Đối với kiếm pháp, thương pháp, quyền pháp, thân pháp... Cùng tất cả võ kỹ, cũng có càng sâu lý giải."),t("br"),t("br"),n._v("Lĩnh ngộ nhiều như vậy tri thức, học tập nhiều ngày như vậy đạo bí tịch, lực lượng mặc dù không có bất luận cái gì tăng trưởng, nhưng sức chiến đấu lại lần nữa bạo tăng."),t("br"),t("br"),n._v("Nếu như cho hắn hiện tại gặp được Mục sư khôi lỗi, căn bản không cần nhiều ít chiêu số, chỉ tay liền có thể giải quyết."),t("br"),t("br"),n._v("Bán Thánh sơ kỳ tu vi, Thánh vực nhất trọng sơ kỳ, đều có thể nhẹ nhõm đánh bại, truyền đi, tuyệt đối có thể đem người dọa chết tươi."),t("br"),t("br"),n._v("Cái này đã không riêng gì tu vi thâm hậu vấn đề, đối võ kỹ lý giải, cùng chiến đấu lĩnh ngộ, đều đạt đến cực cao trình độ."),t("br"),t("br"),n._v("Làm xong những này, ngày cuối cùng, đem Vương Dĩnh, Trịnh Dương, Ngụy Như Yên đám người kêu tới, bắt đầu giảng bài."),t("br"),t("br"),n._v("Mấy người kia mặc dù đối từng người võ kỹ, đều có rất sâu sắc lý giải, thực lực cũng tại cùng độ tuổi, tài năng xuất chúng, nhưng đối với hắn mà nói, vẫn là quá yếu."),t("br"),t("br"),n._v("Đem mới vừa lĩnh ngộ thương pháp, kiếm pháp, quyền pháp, thân pháp, lần nữa truyền thụ, để hắn khổ tu, lúc này mới dặn dò một tiếng, Trương Huyền đạp vào Tử Dương thú sống lưng, thẳng tắp hướng Hiên Viên phong hào vương quốc phương hướng bay đi."),t("br"),t("br"),n._v("Lần này là đi khảo hạch, không cần thiết đem những đệ tử này mang theo, chậm trễ bọn họ thời gian tu luyện."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mây trắng thong thả, gió nhẹ phơ phất."),t("br"),t("br"),n._v("Tử Dương thú thẳng tắp hướng Hiên Viên vương quốc phương hướng bay đi, Trương Huyền thì lắc người một cái, đi vào Thiên Nghĩ Phong tổ."),t("br"),t("br"),n._v("Thánh vực tứ trọng ong mẫu tinh huyết, cho Thiên Nghĩ Phong Mẫu về sau, thực lực của nó, mấy ngày ngắn ngủi liền tấn cấp đến Thánh vực tam trọng, so với Tử Dương thú đều muốn mạnh mẽ hơn không ít."),t("br"),t("br"),n._v("Thực lực như thế, lại thêm các loại linh thạch bảo vật liên tục không ngừng cung cấp, tổ ong bên trong không gian, đường kính đã vượt qua hơn ba trăm mét, như cùng một quảng trường khổng lồ."),t("br"),t("br"),n._v("Gặp tổ ong phạm vi càng lúc càng lớn, Trương Huyền đem Vân Vụ lĩnh bị hắn thuần phục chín đại vương giả, cũng thu vào, sau đó lại hướng Ngọc Thần Thanh muốn rất nhiều gạch đá, để mấy tên động thủ, xây dựng một tòa phủ đệ."),t("br"),t("br"),n._v("Cho dù không bằng trước đó cái kia xinh đẹp, nhưng cũng phong cảnh lịch sự tao nhã, cư trú trong đó, mười phần hài lòng."),t("br"),t("br"),n._v("Bất quá, Trương Huyền đi vào, cũng không phải là xem phủ đệ, mấy bước đi tới ở giữa nhất sân nhỏ, nhìn về phía trên mặt đất, một viên xanh nhạt cành mầm."),t("br"),t("br"),n._v("Hạt Bồ Đề!"),t("br"),t("br"),n._v("Thứ này vừa được đến liền trồng xuống đến, cho tới hôm nay, mới phát ra chồi non."),t("br"),t("br"),n._v("“Nhìn xem có thể hay không để cho nó lớn lên mau mau...”"),t("br"),t("br"),n._v("Trước đó chỉ là hạt giống, có thủ đoạn cũng vô dụng, giờ phút này đâm chồi, nhẹ nhàng cười một tiếng, Trương Huyền ngón tay một chút, từng đạo Thiên Đạo chân khí, hỗn tạp địa mạch linh dịch, mạnh vọt qua."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Một hồi giòn vang, chồi non dường như sinh mệnh, từng ngụm từng ngụm hấp thu linh dịch cùng chân khí, mắt trần có thể thấy tăng trưởng."),t("br"),t("br"),n._v("Địa mạch linh dịch cùng Thiên Đạo chân khí hỗn hợp, không chỉ đối linh dược hữu dụng, đối Thánh dược tác dụng cũng rất lớn."),t("br"),n._v("Không đến nửa canh giờ, liền lớn trọn vẹn hai, cao ba mét, thân cây cũng có to bằng cánh tay trẻ con."),t("br"),t("br"),n._v("Mặc dù cùng hoàng thất cái kia so, chênh lệch có chút lớn, nhưng có thể khẳng định, nương theo thời gian trôi qua, truyền vào linh dịch cùng chân khí càng nhiều, nhất định có thể càng lúc càng lớn, vượt xa đối phương."),t("br"),t("br"),n._v("Thu hồi chân khí, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Hắn hiện tại không có Hóa Phàm cửu trọng Thiên Đạo Vu hồn, có điều, có cái này Bồ Đề thụ, cùng Thanh Giác Long thú hồn phách, có lẽ có thể lần nữa đột phá, đạt tới cấp bậc cao hơn."),t("br"),t("br"),n._v("Đang muốn Vu hồn ly thể, tiến hành tu luyện, liền nghe đến cách đó không xa Thiên Nghĩ Phong Mẫu tiếng âm vang lên."),t("br"),t("br"),n._v("“Chủ nhân, Tử Dương thú trên không trung ngừng lại, hình như có người ngăn cản đường đi!”"),t("br"),t("br"),n._v("“Cản đường?” Trương Huyền sững sờ, lại không tiếp tục, thân thể nhoáng một cái, theo tổ ong đi ra."),t("br"),t("br"),n._v("Tổ ong là chồng chất không gian, lấy thực lực của hắn bây giờ, tiến vào bên trong, còn không thể nhận ra cảm giác tình huống bên ngoài, có điều, ong mẫu làm tổ ong người sáng lập, lại có thể thời gian thực giám sát chung quanh, tiến hành báo cáo."),t("br"),t("br"),n._v("“Trương sư, chúng ta vui lòng cùng đi với ngươi Hiên Viên vương quốc, giúp ngươi một tay...”"),t("br"),t("br"),n._v("Đi tới trước cửa sổ, hướng nhìn ra ngoài, lập tức nhìn thấy Lạc Thất Thất, Ngọc Phi Nhi, Hồ Yêu Yêu đám người, chặn ở phía trước."),t("br"),t("br"),n._v("“Các ngươi làm sao đuổi theo tới?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Tử Dương thú cũng bay hơn nửa canh giờ, lấy tốc độ của nó, mấy người này ngồi bình thường linh thú, làm sao có thể đuổi theo kịp tới?"),t("br"),t("br"),n._v("“Biết viện trưởng lựa chọn tấn thăng đế quốc khảo hạch lục tinh danh sư, chúng ta trước thời hạn liền ở chỗ này chờ lấy, dạng này ngươi liền không có cách nào đem chúng ta đuổi đi...”"),t("br"),t("br"),n._v("Hồ Yêu Yêu cười nói."),t("br"),t("br"),n._v("Nhờ vào nàng yêu nghiệt chiếu cố lớn lên thân phận, cùng dung nhan tuyệt thế, lại thêm Bán Thánh thực lực, hỏi thăm ra loại chuyện nhỏ nhặt này, vẫn là rất đơn giản."),t("br"),t("br"),n._v("“Ây... Tốt a!”"),t("br"),t("br"),n._v("Thấy đối phương khăng khăng muốn theo tới, Trương Huyền cũng không có cự tuyệt."),t("br"),t("br"),n._v("Khảo hạch lục tinh danh sư, là chính hắn sự tình, mặc dù cái khác danh sư không thể hỗ trợ, nhưng chỉ cần không dính dáng đại cục, một chút việc vặt, còn có thể xử lý."),t("br"),t("br"),n._v("Dạng này cũng có thể tăng nhanh tiến độ, để phong hào vương quốc, tấn thăng tốc độ càng nhanh một chút."),t("br"),t("br"),n._v("“Cùng đi đi!”"),t("br"),t("br"),n._v("Trương Huyền cũng không xoắn xuýt, nói một tiếng, Lạc Thất Thất đám người một mặt mừng rỡ đi tới Tử Dương thú lưng thú bên trên."),t("br"),t("br"),n._v("Mấy người nhanh chóng hướng Hiên Viên vương quốc phương hướng bay đi."),t("br"),t("br"),n._v("Lần này tới chờ lấy, tổng cộng bốn người, theo thứ tự là Lạc Thất Thất, Ngọc Phi Nhi, Hồ Yêu Yêu, Đổng Hân."),t("br"),t("br"),n._v("Bốn vị đều là mỹ nữ, để cả phòng như bông hoa mở ra, hoạt sắc sinh hương, chỉ tiếc, Trương Huyền là cái đầu gỗ, con mắt dường như mù, loại trừ tu luyện chính là tu luyện, cùng đối phương nói chuyện, cũng là chỉ điểm."),t("br"),t("br"),n._v("Để các nàng tất cả đều tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Bắt đầu giao lưu có chút không thú vị, nhưng Trương Huyền ánh mắt cùng kiến thức, là các nàng không thể so được, lại thêm nhiều thủ đoạn, mới phi hành hai, ba ngày, liền liên tiếp đột phá."),t("br"),t("br"),n._v("Nhất là Lạc Thất Thất cùng Ngọc Phi Nhi, đã đạt đến Đạp Hư cảnh sơ kỳ, tiến bộ nhanh chóng, khiến cho người tắc lưỡi."),t("br"),t("br"),n._v("Hồ Yêu Yêu nhận được Trương Huyền tận tâm chỉ điểm, cũng tiến bộ rõ ràng, mặc dù vẫn như cũ là Bán Thánh sơ kỳ, sức chiến đấu cùng đối võ kỹ lĩnh ngộ, tăng gấp bội, sức chiến đấu so trước mấy ngày, tăng lên chí ít gấp đôi!"),t("br"),t("br"),n._v("Có thể đoán được, đồng dạng tu vi Tiết Chân Dương, Long Thương Nguyệt đám người liên hợp, đều chưa hẳn có thể là đối thủ."),t("br"),t("br"),n._v("Thậm chí trước đó vị kia Bán Thánh cấp bậc Lộc Thành, đều có thể không rơi vào thế hạ phong."),t("br"),t("br"),n._v("Đương nhiên, muốn vượt qua đối phương, khẳng định còn cần càng nhiều thời gian tu luyện cùng củng cố, nhưng mấy ngày ngắn ngủi, liền có thực lực như thế, có thể nói, đã nghe rợn cả người."),t("br"),t("br"),n._v("Còn Đổng Hân, Trương Huyền cũng chỉ điểm một chút, tiến bộ rõ ràng không có Hồ Yêu Yêu lớn, nhưng cũng sức chiến đấu bạo tăng."),t("br"),t("br"),n._v("Bốn người vốn chỉ là muốn cùng Trương sư cùng một chỗ, kinh nghiệm thoáng cái tấn thăng đế quốc ầm ầm sóng dậy, không nghĩ tới nhận được to lớn như vậy chỗ tốt, nguyên một đám tràn đầy kích động."),t("br"),t("br"),n._v("Không để ý tới các nàng kích động, thừa dịp cái này mấy ngày, Trương Huyền cũng đem tu vi dung hội quán thông."),t("br"),t("br"),n._v("Hắn tu luyện động tĩnh thực sự quá lớn, ở đây không tiện thi triển, cũng liền không có xung kích cảnh giới cao hơn, mấy ngày nay thuận tiện đem thư hoạ, thiên công, võ kỹ lại nghiên cứu một lần, cả người tinh khí thần cũng biến thành không đồng dạng."),t("br"),t("br"),n._v("Nhất là thư hoạ, để cho người ta tâm cảnh càng càng bao la thản nhiên."),t("br"),t("br"),n._v("Hắn giờ phút này, chỉ bằng vào thư hoạ, đã đạt đến thất tinh trình độ."),t("br"),t("br"),n._v("Khái niệm gì?"),t("br"),t("br"),n._v("Chỉ cần chân khí trong cơ thể đầy đủ, chỉ bằng vào một cây bút, tùy tiện vẽ tranh, liền có thể vẽ ra ngàn vạn linh thú, vô số binh sĩ, giết chết một cái phong hào vương quốc, cực kỳ dễ dàng."),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("Bất kể loại nào phụ tu, chỉ cần có thể vượt qua lục tinh, tất nhiên đều có lực lượng kinh thiên động địa, để cho người ta kính sợ."),t("br"),t("br"),n._v("“Phía trước là Vạn Quốc liên minh, Trương sư, có muốn hay không đi xuống xem một chút?”"),t("br"),t("br"),n._v("Nửa tháng sau."),t("br"),t("br"),n._v("Lạc Thất Thất đứng tại bên cửa sổ bên trên, cúi đầu nhìn một cái, nói."),t("br"),t("br"),n._v("“Vạn Quốc liên minh?”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng: “Xem một chút đi!”"),t("br"),t("br"),n._v("Hắn theo Vạn Quốc liên minh rời khỏi, đến bây giờ đã hơn bốn tháng, đã đi ngang qua, tự nhiên muốn đi qua nhìn một chút."),t("br"),t("br"),n._v("Dù sao, là ở đây thông qua được tuyển chọn, mới có tư cách tiến vào danh sư học viện, mới có bây giờ địa vị cùng thân phận."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-928"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-930"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);