(window.webpackJsonp=window.webpackJsonp||[]).push([[2221],{2576:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-921-truong-huyen-đo-so-anh-tu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-921-truong-huyen-đo-so-anh-tu"}},[n._v("#")]),n._v(" Chương 921: Trương Huyền đồ sộ anh tư")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 921: Trương Huyền đồ sộ anh tư "),t("br"),t("br"),n._v("Nghe được phân phó, Tử Dương thú thân thể nhoáng một cái thẳng tắp hướng phía dưới lao xuống, trong nháy mắt liền đi tới liên minh trước mặt ngoài sân rộng vây."),t("br"),t("br"),n._v("Lúc trước từng tại quảng trường này bên trên tiến hành thi đấu, cùng hơn hai mươi chỗ nhất đẳng vương quốc thế lực thiên tài, đoạt được đi vào danh sư học viện tư cách, mặc dù quá khứ chỉ có ngắn ngủi thời gian mấy tháng, nhưng giờ phút này nhớ tới, dường như đã có mấy đời."),t("br"),t("br"),n._v("Khi đó thiếu niên khí phách, phóng khoáng tự do, khí thế bay thẳng hoàn vũ, không ai bằng... Hiện tại, rõ ràng muốn trầm ổn hơn nhiều."),t("br"),t("br"),n._v("“Lão sư chính là từ nơi này đi ra?” Nhìn về phía trước mắt to lớn quảng trường, Lạc Thất Thất một mặt tò mò."),t("br"),t("br"),n._v("Trong mắt hắn, lão sư là cái thiên tư tuyệt đỉnh nhân vật, liền xem như Thánh Nhân quý tộc thế hệ trẻ tuổi có thể cùng sánh vai, cũng ít lại càng ít."),t("br"),t("br"),n._v("Nhân vật như vậy thế mà không phải đại gia tộc xuất thân, mà là loại địa phương nhỏ này đi ra, thật là khiến người khó có thể tin."),t("br"),t("br"),n._v("Quảng trường này thoạt nhìn rộng lớn, cùng chân chính Thánh Nhân quý tộc Thánh sơn, Linh địa so, vẫn là kém quá nhiều, cùng vốn không tại một cái cấp bậc."),t("br"),t("br"),n._v("Không nói cái khác, nơi này nồng độ linh khí liền thực sự quá kém, vô cùng mỏng manh, tại danh sư học viện một tháng tích lũy, chỉ sợ cũng muốn vượt qua cái này tu luyện một năm."),t("br"),t("br"),n._v("Biết ý nghĩ của đối phương, Trương Huyền cười cười, không có trả lời."),t("br"),t("br"),n._v("Nếu như không có Thiên Đạo thư viện, hắn khả năng đồng dạng sẽ tầm thường vô vi, tại Thiên Huyền vương quốc cuối đời một đời."),t("br"),t("br"),n._v("Nhưng... Không có nếu như, vận mệnh bọn họ thay đổi, cũng chỉ có thể từng bước một tiến về phía trước đi đến, không cách nào lùi bước."),t("br"),t("br"),n._v("Để Tử Dương thú đến không bên trong chờ đợi, Trương Huyền mấy người dọc theo đường đi hướng quảng trường đi đến."),t("br"),t("br"),n._v("Lúc này quảng trường người ta tấp nập, vô số tu luyện giả đứng tại chỗ, đồng loạt nhìn về phía ngay phía trước, một cái to lớn tượng đá đang đang chậm rãi dựng thẳng lên."),t("br"),t("br"),n._v("“Làm cái gì vậy?” Trương Huyền tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Quảng trường này tới gần Vạn Quốc liên minh minh chủ phủ cùng Danh Sư đường, từ trước tới nay mười phần thanh tĩnh, làm sao chất đống nhiều người như vậy? Cái này lại đang làm cái gì?"),t("br"),t("br"),n._v("“Mau nhìn, cái kia chính là Trương sư!”"),t("br"),t("br"),n._v("“Quả nhiên phong thái tuấn lãng, khí độ bất phàm!”"),t("br"),t("br"),n._v("“Cái kia là tự nhiên, Trương sư có thể là thần tượng của ta, ta một đời mục tiêu lớn nhất chính là cái kia đạt tới hắn một phần vạn.”"),t("br"),t("br"),n._v("“Không riêng gì ngươi, tại tất cả mọi người ở đây đều lấy Trương sư làm thần tượng đi!”"),t("br"),t("br"),n._v("“Đúng vậy a! Nghe nói Trương sư đi danh sư học viện, cũng không biết lúc nào, trở về lại cho chúng ta bên trên một tiết khóa...”"),t("br"),t("br"),n._v("“Có thể nghe được lớp của hắn, là bao nhiêu người tha thiết ước mơ sự tình, ngươi cũng đừng nghĩ, ai bảo ngươi lần trước không đến đâu?”"),t("br"),t("br"),n._v("“Nói liền cùng ngươi đã đến đồng dạng... Ngươi cũng không không có ở hiện trường?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Còn chưa tới đến quảng trường trước mặt, liền nghe đến đám người, nghị luận ầm ĩ, vô số người trẻ tuổi, có nam có nữ nhìn về phía trong sân rộng ở giữa, nguyên một đám ánh mắt kích động, tràn đầy bái phục."),t("br"),t("br"),n._v("“Xin hỏi... Trong miệng các ngươi nói cái này Trương sư là ai?”"),t("br"),t("br"),n._v("Nghe được mọi người thảo luận, lại thêm danh sư học viện loại hình chữ mấu chốt mắt, Lạc Thất Thất có chút nhịn không được, đi lên phía trước, nhìn một thanh niên, hỏi."),t("br"),t("br"),n._v("“Liền Trương sư cũng không biết, làm sao như thế kiến thức nông cạn...”"),t("br"),t("br"),n._v("Nghe được có người liền Trương sư họ tên, cũng không biết, thanh niên đang muốn trách mắng một câu, nhìn thấy Lạc Thất Thất dung mạo, lập tức dừng lại, vẻ mặt xấu hổ màu đỏ bừng, có chút chân tay luống cuống: “Là ngươi muốn hỏi ah, Trương sư, tên một chữ một cái ‘Huyền’ chữ, là chúng ta Vạn Quốc liên minh từ trước tới nay vĩ đại nhất danh sư...”"),t("br"),t("br"),n._v("Lạc Thất Thất dung mạo, tại bất kỳ địa phương nào cũng có thể làm cho vô số nam tử vì đó ái mộ, đột nhiên nhìn thấy, còn có thể nói ra lời, nói rõ người thanh niên này định lực, tính toán là không tệ."),t("br"),t("br"),n._v("“Huyền? Treo cổ cái kia ‘Huyền’ ?”"),t("br"),t("br"),n._v("Nghe được đối phương thảo luận lại là bản thân, Trương Huyền khắp khuôn mặt là cổ quái, đợi đối phương xác nhận về sau, nhịn không được nói: “Trương Huyền, hắn... Lúc nào thành thần tượng của các ngươi?”"),t("br"),t("br"),n._v("Chính mình cũng rời khỏi hơn mấy tháng, thảo luận bản thân làm gì?"),t("br"),t("br"),n._v("Còn thần tượng, vĩ đại nhất danh sư... Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Láo xược! Cũng dám gọi thẳng Trương sư họ tên, mà lại nói ra lớn như thế bất kính lời nói, ai cho ngươi lá gan?”"),t("br"),t("br"),n._v("Đang muốn tiếp tục hỏi thăm, hiểu rõ đến cùng chuyện gì xảy ra, liền nghe đến một cái hét lớn vang lên."),t("br"),t("br"),n._v("Lập tức liền thấy một cái kiêu ngạo thiếu niên đi tới."),t("br"),t("br"),n._v("Mười sáu mười bảy tuổi diện mạo, một thân lộng lẫy quần áo, lúc hành tẩu, mang theo cực mạnh khí thế, vừa nhìn liền biết địa vị không thấp."),t("br"),t("br"),n._v("Hắn vừa xuất hiện, lại thêm thanh âm mới vừa rồi rất lớn, lập tức đưa tới rất nhiều người chú ý, nhao nhao nhìn lại, chỉ nhìn Trương Huyền một cái, liền bị bên cạnh hắn bốn vị mỹ nữ thu hút."),t("br"),t("br"),n._v("Ngọc Phi Nhi, Lạc Thất Thất, Hồ Yêu Yêu, Đổng Hân... Mỗi một vị đều là nghiêng nước nghiêng thành tồn tại, thoáng cái xuất hiện bốn vị, hơn nữa cùng một thanh niên cùng một chỗ, đổi lại ai đều sẽ cảm giác đến kỳ quái."),t("br"),t("br"),n._v("“Bát điện hạ từ trước tới nay phong lưu, nhìn thấy nhiều như vậy mỹ nữ, khẳng định là muốn ở trước mặt các nàng triển lộ uy phong!”"),t("br"),t("br"),n._v("“Muốn tìm phiền toái đều không tìm được, đây không phải hướng trên họng súng gặp ư? Dám gọi thẳng Trương sư họ tên... Lá gan có thể thật là lớn!”"),t("br"),t("br"),n._v("“Trương sư tại Vạn Quốc liên minh là vô số người trẻ tuổi sùng bái thần tượng, nhất là vị này Bát điện hạ, càng là lấy vì chung thân phấn đấu mục tiêu... Thế mà còn ‘Treo cổ’ cái kia ‘Huyền’... Treo em gái ngươi ah! Quá không biết nói chuyện, đáng đời xui xẻo!”"),t("br"),t("br"),n._v("“Bản thân tự tìm cái chết, trách không được người khác!”"),t("br"),t("br"),n._v("Biết sự tình từ đầu đến cuối, tất cả đều lộ ra cười lạnh, chờ lấy xem náo nhiệt."),t("br"),t("br"),n._v("Vị thanh niên này, là Vạn Quốc liên minh Triệu minh chủ con trai thứ tám, Triệu Phi Vũ thân đệ đệ, chỉ có mười sáu tuổi."),t("br"),t("br"),n._v("Mặc dù tuổi không lớn lắm, danh tiếng lại không phải quá tốt."),t("br"),t("br"),n._v("Ham sắc đẹp, cuồng vọng ngỗ ngược, cậy tài khinh người, hành vi quá quắt..."),t("br"),n._v(" Bất quá, mấy tháng trước hành vi có đổi mới, nghe nói bị tỷ tỷ giáo huấn một trận, thề muốn lấy Trương sư làm gương, cố gắng tu luyện, tranh thủ trở thành hắn đồng dạng cường giả... Bởi vì tôn sùng, cho nên ghét nhất người khác nói hắn nói xấu."),t("br"),t("br"),n._v("Cái tên này, trực tiếp hỏi có phải hay không ‘Treo cổ’ ‘Huyền’... Quả thực chính là không tôn trọng Trương sư, khó trách cái tên này tức giận, lại thêm bốn phía nhiều mỹ nữ như vậy, có thể cho sắc mặt tốt mới là lạ."),t("br"),t("br"),n._v("“Không đúng... Các ngươi có cảm giác hay không, vị thanh niên này... Có chút quen mặt?”"),t("br"),t("br"),n._v("Có người thảo luận, cũng có người cảm thấy có chút kỳ quái, tứ đại mỹ nữ quay chung quanh thanh niên, thoạt nhìn có chút hiền hòa, thật giống như ở nơi nào gặp qua đồng dạng."),t("br"),t("br"),n._v("“Ta cũng cảm thấy có chút quen mặt, chính là nhất thời nhớ không ra thì sao...”"),t("br"),t("br"),n._v("Vừa nói như vậy, trong đám người cũng đã nhận được ứng hòa, không ít người nhíu mày."),t("br"),t("br"),n._v("“Đại bất kính? Làm sao đại bất kính? Chẳng lẽ ‘Trương Huyền’ cái tên này, còn gọi không được?” Trương Huyền yên lặng, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Bản thân hô tên của mình, thế mà cũng thành kiêng kị, để hắn dở khóc dở cười."),t("br"),t("br"),n._v("“Đương nhiên gọi không được! Đổi lại bình thường, ngươi vô lễ một chút, thì cũng thôi đi, ngươi có biết hôm nay là ngày gì?” Thiếu niên tròng mắt hơi híp."),t("br"),t("br"),n._v("“Ngày gì?” Trương Huyền càng thêm kỳ quái."),t("br"),t("br"),n._v("Không thể kêu tên của mình ngược lại cũng thôi, thế mà thời gian cũng có coi trọng."),t("br"),t("br"),n._v("“Hôm nay là Trương sư pho tượng đứng sừng sững thời gian, thân là Vạn Quốc liên minh con dân thế mà không biết, còn thể thống gì?”"),t("br"),t("br"),n._v("P/S: * đứng sừng sững là một cái cách diễn đạt, hàm nghĩa là đồ sộ mà thẳng tắp dựng đứng, cao ngất. Trọng điểm tại thẳng, thẳng mà cao điểm đứng thẳng chi ý. Trung tính từ, bình thường dùng cho vật, không dùng cho người. *"),t("br"),t("br"),n._v("Nghe được hắn liền hôm nay là ngày nào con, cũng không biết, thiếu niên tại chỗ xù lông, nổi giận đùng đùng."),t("br"),t("br"),n._v("Những người khác cũng đầy là nghi ngờ, coi như không biết thời gian, trên quảng trường nhiều người như vậy cũng có thể nhìn thấy đi! Liên minh như vậy thịnh hội, lại còn nói không biết, cái này là cố ý xem thường, vẫn là đối với liên minh không chậm?"),t("br"),t("br"),n._v("“Trương sư pho tượng đứng sừng sững?”"),t("br"),t("br"),n._v("Trương Huyền ngẩn ngơ, lập tức hướng vừa rồi nhìn thấy pho tượng kia nhìn lại."),t("br"),t("br"),n._v("Trước đó chỉ nhìn lướt qua cũng không có chú ý, nhìn kỹ, chỉ gặp pho tượng người mặc danh sư phục, một con tay vắt chéo sau lưng, một cái tay cầm bút lông, ánh mắt nhìn về phía trời xanh, hình như muốn xuyên thủng cổ kim, vượt qua ngàn năm, toàn thân trên dưới để lộ ra làm cho người không cách nào nhìn thẳng hạo nhiên chi khí."),t("br"),t("br"),n._v("Dưới thân thì là một con Phượng Hoàng, vượt ngang cưỡi lên, đôi cầm bày ra, cho người ta một loại bất cứ lúc nào cũng sẽ phá không mà đi, bay lượn cửu thiên cảm giác."),t("br"),t("br"),n._v("“Đây là ta?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy lên, khóe miệng giật một cái, da mặt lay động: “Cũng mẹ nó... Xấu đi!”"),t("br"),t("br"),n._v("Trong sân rộng ở giữa pho tượng, thân hình cao lớn, chạm trổ chính xác, dùng tài liệu cũng mười phần hiếm có, bất kể từ chỗ nào điểm xem, đều mười phần không sai, vừa nhìn liền biết xuất từ điêu khắc đại sư tay..."),t("br"),t("br"),n._v("Chỉ là cùng dung mạo của hắn có chút nhỏ bé chênh lệch, nhất là khí chất bên trên, quá mức vĩ đại, phong thần tuấn lãng, khí tức hiên ngang, ngược lại khiến người ta cảm thấy không quá giống."),t("br"),t("br"),n._v("Còn có... Phượng Hoàng, bản thân lúc nào, ngồi qua cái đồ chơi này? Cũng không có thuần phục qua loại này bộ dáng linh thú, Thánh thú a?"),t("br"),t("br"),n._v("Thuần nhiều như vậy man thú, linh thú, Thánh thú, rất giống cũng liền Thanh Ưng Thú, chỉ các loại ưng tương tự mà thôi, cùng Phượng Hoàng không có nửa điểm liên quan nha!"),t("br"),t("br"),n._v("Xếp bằng ở Phượng Hoàng sống lưng, phóng lên tận trời, như thế vĩ đại, như thế cợt nhả, thật chính là mình?"),t("br"),t("br"),n._v("Làm sao cảm giác như thế không thể tin được đâu?"),t("br"),t("br"),n._v("Đối phương nếu như không nói là hắn, coi như vừa ý một ngày đều không tưởng tượng nổi."),t("br"),t("br"),n._v("Lúc nào trở nên cao lớn như vậy, như thế có khí phách?"),t("br"),t("br"),n._v("Cái này là dựa theo Khổng sư tiêu chuẩn, tới điêu khắc a?"),t("br"),t("br"),n._v("Nếu không trong mắt những cái kia trách trời thương dân, hiền lành có thích... Cái quỷ gì?"),t("br"),t("br"),n._v("Không riêng gì hắn, Lạc Thất Thất đám người nhìn thấy pho tượng, cũng đều mí mắt nhảy loạn, nhất là Hồ Yêu Yêu, người khác đối vị này Trương sư kính trọng rất nhiều, nàng thế nhưng là tự mình lĩnh hội qua lợi hại."),t("br"),t("br"),n._v("Hố chết người không đền mạng hạng người, Vạn Quốc liên minh thế mà điêu khắc ra cao to như vậy pho tượng, mấu chốt khí chất cùng phong độ cùng bản nhân kém cũng quá xa đi!"),t("br"),t("br"),n._v("“Theo ta được biết, vị này Trương sư hẳn là còn chưa có chết đi, làm sao lại dựng thẳng lên pho tượng?”"),t("br"),t("br"),n._v("Đem buồn bực trong lòng dứt bỏ, Trương Huyền nói."),t("br"),t("br"),n._v("Đứng sừng sững pho tượng, dựng nên từ đường đây đều là sau khi chết mới làm, bản thân còn rất tốt còn sống, làm cái đồ chơi này làm gì?"),t("br"),t("br"),n._v("“Ngươi dám nguyền rủa Trương sư?”"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe xong thiếu niên tức giận đến mức cả người run run: “Trương sư giảng bài, Vạn Quốc liên minh trăm vạn người đồng thời được lợi, cùng tôn xưng hắn là lão sư... Vì hắn đứng sừng sững pho tượng, thời khắc kính bái, có gì không ổn?”"),t("br"),t("br"),n._v("“Giảng bài?”"),t("br"),t("br"),n._v("Trương Huyền ngẩn ngơ, lúc này mới nhớ tới, Vạn Quốc liên minh thi đấu về sau, hắn từng nói qua một bài giảng, cuối cùng còn lấy ra bút lông, vẽ lên một con Phượng Hoàng, ngồi ở tại bên trên, phi hành mà đi..."),t("br"),t("br"),n._v("Chẳng lẽ điêu khắc chính là thời điểm đó cảnh tượng?"),t("br"),t("br"),n._v("Nếu là như vậy, ngược lại thật sự là ngồi qua Phượng Hoàng, chỉ bất quá khi đó vì rạng rỡ, cường tráng tăng thanh thế, không nghĩ tới người trong liên minh tưởng thật, lại đem bản thân điêu khắc thành dạng này, thời khắc kính bái..."),t("br"),t("br"),n._v("Đây cũng quá trò đùa a?"),t("br"),t("br"),n._v("Trương Huyền mặt mũi xoắn xuýt."),t("br"),t("br"),n._v("Nói cho những người này, không có cao như vậy lớn... Tương đương đánh mặt; Không nói đi, lại cảm thấy có chút hổ thẹn."),t("br"),t("br"),n._v("Suy nghĩ một chút thật đúng là để cho người ta khó mà lựa chọn!"),t("br"),t("br"),n._v("(0 giờ bộc phát năm canh, đây là canh thứ nhất, mọi người chờ, đằng sau còn có.)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-929"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-931"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);