(window.webpackJsonp=window.webpackJsonp||[]).push([[2222],{2577:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-922-gap-lai-trieu-phi-vu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-922-gap-lai-trieu-phi-vu"}},[n._v("#")]),n._v(" Chương 922: Gặp lại Triệu Phi Vũ")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 922: Gặp lại Triệu Phi Vũ "),i("br"),i("br"),n._v("Chần chờ một chút, Trương Huyền quyết định vẫn là không tính nói."),i("br"),i("br"),n._v("Danh sư không chỉ chú trọng giảng bài, “Danh” cũng rất trọng yếu, đã lần trước giảng bài, lưu lại lớn như thế ảnh hưởng, đang dễ dàng dựa vào danh tiếng của mình chấn nhiếp khắp nơi, tránh khỏi phiền toái không cần thiết cùng tranh chấp, cũng có trợ giúp nhân vật thống nhất."),i("br"),i("br"),n._v("Đương nhiên, thân là học viện viện trưởng, có dạng này danh khí tự nhiên tốt nhất, đối khảo hạch lục tinh danh sư cũng có trợ giúp rất lớn."),i("br"),i("br"),n._v("“Cái tên này đối Trương sư bất kính, ta hoài nghi là thế lực khác gian tế, người tới! Đem hắn áp đi xuống, thật tốt thẩm vấn!”"),i("br"),i("br"),n._v("Gặp Trương Huyền đối cách đó không xa pho tượng chẳng những không có bất luận cái gì tôn kính, còn lộ ra xoắn xuýt biểu lộ, thiếu niên càng nghĩ càng giận, xoay người phân phó."),i("br"),i("br"),n._v("“Rõ!” Sau lưng đi ra mấy tên hộ vệ, đồng loạt vây quanh."),i("br"),i("br"),n._v("“Ta cũng không có bất kính...”"),i("br"),i("br"),n._v("Biết giải thích không thông, không để ý tới chung quanh hộ vệ, Trương Huyền lắc đầu, hai bước đi tới thiếu niên trước mặt, xoa đầu của hắn, một mặt vui vẻ: “Ngươi Triệu Phi Vũ tỷ tỷ có đó không? Để nàng đi ra gặp ta...”"),i("br"),i("br"),n._v("“Ngươi...” Thiếu niên sắp nổ tung."),i("br"),i("br"),n._v("Cái tên này là đầu óc có vấn đề vẫn là phản ứng không kịp? Bản thân thế nhưng là Vạn Quốc liên minh đường đường Bát hoàng tử, muốn bắt hắn, thẩm vấn hắn, không những không sợ, còn tới vò đầu của mình... Càng là gọi thẳng tỷ tỷ họ tên, để nàng đi ra gặp... Quả thực tội không thể tha!"),i("br"),i("br"),n._v("“Đáng ghét, dám gọi thẳng đại minh chủ họ tên, quả thực tội ác tày trời...” Lên tiếng gào thét, thiếu niên một quyền liền đối Trương Huyền đánh tới."),i("br"),i("br"),n._v("Cũng không tránh né, Trương Huyền sờ lấy đối phương đầu bàn tay, nhẹ nhàng đẩy một cái, nhờ vào thân cao, liền đủ không đến bản thân, tùy ý thiếu niên gió táp mưa rào, hắn tự vị nhưng bất động."),i("br"),i("br"),n._v("“Các ngươi chơi cái gì, còn không nhanh lên?”"),i("br"),i("br"),n._v("Gặp công kích của mình, rơi không đến trên người đối phương, ngược lại làm cho giống như thằng hề, thiếu niên tức giận sắp điên mất, một tiếng la lên."),i("br"),i("br"),n._v("Mấy tên hộ vệ cái này mới phản ứng được, đang muốn xông lại hỗ trợ, lại phát hiện thân thể giống như là lâm vào vũng bùn, căn bản là không có cách động đậy."),i("br"),i("br"),n._v("Xuất thủ chính là Hồ Yêu Yêu mấy người."),i("br"),i("br"),n._v("Lấy thực lực của các nàng, thu dọn mấy cái liền tông sư đều không có đi đến hộ vệ, dễ như trở bàn tay, hơn nữa, còn có thể bảo đảm, ngoại nhân ai cũng nhìn không ra."),i("br"),i("br"),n._v("“Triệu Phi Vũ làm đại minh chủ?”"),i("br"),i("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng, không để ý tới tất cả hộ vệ, lần nữa nhìn về phía trước mắt cánh tay không ngừng vung vẩy, nhưng thủy chung đủ không đến bản thân thiếu niên."),i("br"),i("br"),n._v("Trong ấn tượng, Triệu Phi Vũ là cái tri thức uyên bác, nhưng lại bởi vì thể chất không cách nào tu luyện cô gái, lúc nào biến thành đại minh chủ?"),i("br"),i("br"),n._v("Mặc dù tại trước khi đi, hỗ trợ giải quyết không thể vấn đề tu luyện, cũng có nhất định thực lực, có thể khoảng cách quản lý một cái Vạn Quốc liên minh còn kém rất xa ah!"),i("br"),i("br"),n._v("Vạn Quốc liên minh không có Hóa Phàm trở lên thực lực, đừng nói đại minh chủ, khả năng liền một cái có quyền thế nhất phẩm đại quan đều làm không lên."),i("br"),i("br"),n._v("“Thả ta, nếu không ta nhất định giết ngươi...”"),i("br"),i("br"),n._v("Không có trả lời hắn, thiếu niên ngược lại nghiến răng nghiến lợi."),i("br"),i("br"),n._v("“Quên đi, hỏi ngươi cũng nói không rõ ràng... Vẫn là chính ta hỏi nàng đi!”"),i("br"),i("br"),n._v("Biết ở đây càng dây dưa càng phiền phức, lười phải tiếp tục hỏi thăm, Trương Huyền ngẩng đầu lên nhìn về phía minh chủ phủ phương hướng, thanh âm nhàn nhạt, vang lên: “Phi Vũ công chúa, bạn cũ đến đây, mong rằng gặp một lần!”"),i("br"),i("br"),n._v("Thanh âm không lớn, nhưng bằng mượn hắn Bán Thánh cấp bậc thực lực, trong nháy mắt truyền vang toàn bộ quảng trường, thậm chí toàn bộ Vạn Quốc thành đều rõ ràng có thể nghe."),i("br"),i("br"),n._v("“Trương sư?”"),i("br"),i("br"),n._v("Nghe được thanh âm này, những người khác cũng không có cảm thấy cái gì, minh chủ phủ một cái thanh âm hưng phấn, vang lên, lập tức mọi người liền thấy, to lớn linh thú phi hành sống lưng bên trên, Triệu Phi Vũ đại minh chủ, tràn đầy hưng phấn hướng cái này bay tới."),i("br"),i("br"),n._v("Linh thú còn chưa tới đến trước mặt, cô gái liền từ không trung nhảy xuống tới, dường như muốn bổ nhào vào thanh niên trong lòng, đi tới trước mặt, cuối cùng ngừng lại, liền ôm quyền: “Phi Vũ bái kiến Trương sư!”"),i("br"),i("br"),n._v("“Trương sư?”"),i("br"),i("br"),n._v("“Có thể được Phi Vũ công chúa như vậy kính trọng, chẳng lẽ... Vị này chính là Trương Huyền Trương sư?”"),i("br"),i("br"),n._v("“Ngươi vừa nói như vậy, dường như cùng pho tượng, thật rất giống...”"),i("br"),i("br"),n._v("“Không phải giống như, chính là một người... Trương sư đến rồi, chúng ta thế mà không nhận ra được!”"),i("br"),i("br"),n._v("“Người thật so pho tượng thoạt nhìn muốn hèn... Vĩ đại nhiều lắm!”"),i("br"),i("br"),n._v("“Đúng vậy a, là vĩ đại...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được Triệu Phi Vũ lời nói, nhìn thấy động tác của nàng, toàn bộ quảng trường lập tức xôn xao."),i("br"),i("br"),n._v("Tất cả mọi người nguyên một đám con mắt sáng lên hướng ở giữa thanh niên nhìn tới."),i("br"),i("br"),n._v("Trước đó tuyên bố muốn đem Trương Huyền bắt lấy Bát hoàng tử, càng là mặt mũi ngẩn người, giống như là muốn điên rồi."),i("br"),i("br"),n._v("Hắn một mực lấy đối phương làm thần tượng, kết quả tự mình nhìn thấy, nhưng không nhận ra được, ngược lại vô lễ như thế, càng nghĩ càng thấy đến xấu hổ, hận không thể có một cái lỗ, chui vào."),i("br"),i("br"),n._v("“Đi minh chủ phủ đi!”"),i("br"),i("br"),n._v("Thấy mọi người lửa nóng ánh mắt, Triệu Phi Vũ biết nơi này không phải chỗ nói chuyện, nói một tiếng."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, Trương Huyền mang theo Hồ Yêu Yêu đám người, đạp vào linh thú phi hành sống lưng, thẳng tắp hướng minh chủ phủ bay đi."),i("br"),n._v("Mấy người vừa đi, toàn bộ quảng trường lập tức xôn xao, sắp nổ tung."),i("br"),i("br"),n._v("“Nguyên lai đây chính là Trương sư...”"),i("br"),i("br"),n._v("“Thật có lễ phép cùng kềm chế, nếu như có thể gả cho hắn, để cho ta lập tức chết đi, cũng vui lòng!”"),i("br"),i("br"),n._v("“Gả cho hắn, ngươi suy nghĩ nhiều, ngươi xem bên cạnh hắn mấy vị kia, bao quát Triệu Phi Vũ đại minh chủ, cái nào không phải nghiêng nước nghiêng thành? Ngươi cái bộ dáng này, thật muốn đi qua, đoán chừng liền làm nha hoàn tư cách đều không có...”"),i("br"),i("br"),n._v("“Làm không được nha hoàn, coi như có thể có một buổi vui thích, cũng là tốt...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Trên quảng trường tích tụ mọi người, có tôn sùng, có hâm mộ, nguyên một đám hưng phấn mà con mắt đỏ bừng."),i("br"),i("br"),n._v("Trương Huyền ở đây sáng tạo ra quá nhiều kỳ tích, để một mực chịu đến chèn ép Vạn Quốc liên minh mở mày mở mặt không nói, càng làm cho trăm vạn cường giả nắm giữ nửa sư tình nghĩa, như vậy thiên kiêu nhân vật, đột nhiên xuất hiện, lập tức để liên minh muôn người đều đổ xô ra đường, tất cả mọi người muốn tận mắt nhìn xem, vị này siêu cấp thiên tài, đến cùng là dáng dấp ra sao."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Quảng trường rối loạn, Trương Huyền không quản được, cũng lười đi để ý tới, lúc này đang ngồi ở rộng lớn đại điện phía trong, nhìn cách đó không xa Triệu Phi Vũ, mang trên mặt nụ cười thản nhiên."),i("br"),i("br"),n._v("Bốn, năm tháng không thấy, Triệu Phi Vũ thực lực, có tiến bộ rất lớn, đã đạt đến Chí Tôn cảnh, mặc dù khoảng cách Hóa Phàm, còn cách một đoạn, nhưng trong thời gian ngắn có thể đạt tới loại cảnh giới này, tất nhiên hạ quá lớn khổ công."),i("br"),i("br"),n._v("“Trương sư trước khi đi lưu lại cho ta tu luyện công pháp bí tịch, lại thêm Vạn Quốc liên minh cùng Lạc Sa tông, Hàn Khí tông, Lưu Vân tông... Những tông môn này vô điều kiện giúp đỡ, tiến bộ tự nhiên không chậm!”"),i("br"),i("br"),n._v("Nhìn ra ý nghĩ của hắn, Triệu Phi Vũ giải thích nói."),i("br"),i("br"),n._v("“Lạc Sa tông, Hàn Khí tông, Lưu Vân tông... Những tông môn này, vô điều kiện giúp đỡ?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("Những tông môn này, cùng Vạn Quốc liên minh nắm giữ tương đồng địa vị, lúc trước đem liên minh bức bách lợi hại, làm sao điên cuồng giúp đỡ nàng?"),i("br"),i("br"),n._v("“Đúng vậy a, Vạn Quốc liên minh xung quanh 27 chỗ thế lực, chỉ có một cái yêu cầu, chỉ cần ta làm minh chủ, liền dốc sức ủng hộ, cái khác ai đều không được...” Triệu Phi Vũ giải thích một lần."),i("br"),i("br"),n._v("Nghe xong giải thích, Trương Huyền hiểu được, cười khổ lắc đầu."),i("br"),i("br"),n._v("Lúc đó ngụy trang “Dương sư” xuất hiện, một sách đập chết Bạch Dương tông tông chủ Bạch Khải Chi, tạo thành lực uy hiếp thực sự quá lớn, lại thêm lấy được được danh sư thi đấu quán quân, thuận lợi đi vào danh sư học viện!"),i("br"),i("br"),n._v("Coi như học viện cách nơi này xa, rất nhiều tin tức không có truyền tới, nhưng trước đó làm ra đa số, cũng khiến người khác hoàn toàn sợ hãi."),i("br"),i("br"),n._v("Bản thân cùng Triệu Phi Vũ, quan hệ không tầm thường, những người này ngu nữa, cũng biết cái kia lấy lòng ai, nịnh bợ ai."),i("br"),i("br"),n._v("“Đã những tông môn này, thế lực, vui lòng kết giao, ngươi bây giờ liền báo tin bọn họ, ta hiện tại muốn đi Hiên Viên phong hào vương quốc, ở nơi đó đợi một đoạn thời gian, đồng thời sẽ ở nơi đó mở đường giảng bài, nếu có vui lòng tới, có thể đi qua!”"),i("br"),i("br"),n._v("Chần chờ một chút, Trương Huyền nói."),i("br"),i("br"),n._v("Đã những thế lực này, ý định thông qua nâng đỡ Triệu Phi Vũ phương thức tới kết giao bản thân, cũng không có gì khách khí."),i("br"),i("br"),n._v("Để phong hào vương quốc tấn cấp đế quốc, kém là nội tình, loại vật này, muốn trong thời gian ngắn hoàn thành, chỉ dựa vào hắn một người, khẳng định là không được!"),i("br"),i("br"),n._v("Chỉ có dẫn tới càng nhiều cường giả, tới giao lưu, mang đến cùng nhiều vật tư, mới có thể để cho người nhanh chóng tiến bộ, quốc lực đại tăng."),i("br"),i("br"),n._v("“Được!”"),i("br"),i("br"),n._v("Biết hắn phải làm những gì, Triệu Phi Vũ nhẹ gật đầu, nhẹ nhàng cười một tiếng: “Ta cũng sẽ báo cho liên minh tất cả mọi người, hiện tại toàn bộ liên minh, tôn sùng người của ngươi rất nhiều, một khi biết ngươi muốn đi Hiên Viên vương quốc, sợ toàn bộ Hiên Viên vương thành đều sẽ bị đạp phá...”"),i("br"),i("br"),n._v("Nghe nói như thế, Trương Huyền cười cười."),i("br"),i("br"),n._v("Giờ phút này cũng biết, đối phương đứng sừng sững bản thân pho tượng nguyên nhân."),i("br"),i("br"),n._v("Có hắn trấn áp, xung quanh 27 chỗ thế lực, mới không dám láo xược, tự nhiên muốn quá nhiều tuyên dương."),i("br"),i("br"),n._v("“Trương sư, ta muốn cùng ngươi cùng đi Hiên Viên vương quốc... Không biết được hay không?”"),i("br"),i("br"),n._v("Lại trò chuyện một hồi, đối Vạn Quốc liên minh tình hình gần đây biết đến nhiều, Trương Huyền thở phào nhẹ nhõm, đang muốn đứng dậy cáo từ, chỉ thấy Triệu Phi Vũ tràn đầy trông đợi ánh mắt nhìn qua."),i("br"),i("br"),n._v("“Ngươi không phải liên minh đại minh chủ ư? Có thể đi?” Trương Huyền nói."),i("br"),i("br"),n._v("“Ta cái này đại minh chủ, chỉ là treo cái tên tuổi mà thôi, chân chính chưởng quản Vạn Quốc liên minh vẫn là cha ta...” Triệu Phi Vũ nói."),i("br"),i("br"),n._v("“Muốn đi theo liền theo, vừa vặn cũng giúp ngươi tăng lên chút thực lực, ngắn ngủi mấy tháng liền đạt tới Chí Tôn cảnh, đối với ngươi mà nói, mặc dù tiến bộ rất nhanh, nhưng là muốn chấn nhiếp liên minh cùng xung quanh nhiều như vậy thế lực, còn còn thiếu rất nhiều! Ta sẽ nghĩ biện pháp đem thực lực của ngươi, trong thời gian ngắn tăng lên tới Hóa Phàm ngũ trọng hoặc là lục trọng!”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Vạn Quốc liên minh xung quanh, muốn làm được nhất thống thiên hạ, không ai dám phản bác, thực lực ít nhất phải đạt tới Hóa Phàm tứ trọng Trọc Thanh cảnh, mà muốn vô địch chân chính, thì phải nắm giữ ngũ trọng Hợp Linh cảnh hoặc là lục trọng Kiều Thiên cảnh sức chiến đấu."),i("br"),i("br"),n._v("Trước kia hắn không có năng lực này, hiện tại đạt tới Bán Thánh, trong tay càng có chư nhiều bảo vật cùng đan dược, giúp Triệu Phi Vũ trong thời gian ngắn tăng lên tới loại này cấp bậc, cũng không phức tạp."),i("br"),i("br"),n._v("“Đa tạ Trương sư!”"),i("br"),i("br"),n._v("Nghe được muốn trợ giúp bản thân tăng thực lực lên, Triệu Phi Vũ hai mắt tỏa ánh sáng."),i("br"),i("br"),n._v("Cho dù đối với quyền thế, không có hứng thú quá lớn, nhưng đối phương có thể trợ giúp bản thân, đại biểu đối nàng coi trọng, vẫn là để nàng vô cùng vui vẻ."),i("br"),i("br"),n._v("Lại thêm, Trương sư càng đi càng xa, thực lực chênh lệch quá lớn, gặp nhau sẽ càng lúc càng lớn, đến lúc lại không liên lạc."),i("br"),i("br"),n._v("Có thể tăng thực lực lên, nói rõ cùng hắn từng bước tới gần, coi như không có khả năng đuổi theo, chí ít chênh lệch không có lớn như vậy."),i("br"),i("br"),n._v("Đem liên minh sự tình giao phó xong, lại dựa theo Trương Huyền lời nói, đem tin tức truyền ra ngoài, Triệu Phi Vũ lúc này mới cùng mọi người cùng một chỗ bước lên Tử Dương thú sống lưng, thẳng tắp hướng Hiên Viên vương quốc phương hướng bay đi."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-930"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-932"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);